window.QDATA['wangjingzhai'] = [{"sura":1,"aya":1,"text":"奉普慈特慈安拉之名","translator":"王静斋"},{"sura":1,"aya":2,"text":"赞颂安拉；众世界的养主。","translator":"王静斋"},{"sura":1,"aya":3,"text":"普慈特慈的。","translator":"王静斋"},{"sura":1,"aya":4,"text":"执掌还报日的。","translator":"王静斋"},{"sura":1,"aya":5,"text":"我们惟独拜你，惟独求你相助。","translator":"王静斋"},{"sura":1,"aya":6,"text":"求你引领我们正道；","translator":"王静斋"},{"sura":1,"aya":7,"text":"就是那一切人的道你曾对他们施恩，不是被怒的，也不是迷误的。","translator":"王静斋"},{"sura":2,"aya":1,"text":"阿里甫、俩模、密默。","translator":"王静斋"},{"sura":2,"aya":2,"text":"这是其中无有疑惑的经典。 引导一般敬慎的人。","translator":"王静斋"},{"sura":2,"aya":3,"text":"那般人皆信目不能见的。他们立拜功，并就着我所赐给他们的作使用。","translator":"王静斋"},{"sura":2,"aya":4,"text":"那般人皆信所降给你的，和在你以前所降的。他们坚信后世。","translator":"王静斋"},{"sura":2,"aya":5,"text":"他们是守其养主正道的，他们均是得意的。","translator":"王静斋"},{"sura":2,"aya":6,"text":"不信的那些人，你警告或不警告，在他们一样地不归信。","translator":"王静斋"},{"sura":2,"aya":7,"text":"安拉封闭他们的心和听官了；他们的视官上有一种遮幔。他们应遭大的刑罚。","translator":"王静斋"},{"sura":2,"aya":8,"text":"有的人自称：（归信安拉和末日了 ）他们本不是归信的。","translator":"王静斋"},{"sura":2,"aya":9,"text":"他们欺骗安拉和众穆民，他们只是自欺而不觉悟。","translator":"王静斋"},{"sura":2,"aya":10,"text":"他们的心里有病，安拉复增给他们病症了。他们因着说谎应遭痛刑。","translator":"王静斋"},{"sura":2,"aya":11,"text":"有时候，人对他们说：（你们不要在地面上造恶，）他们就说：（我们只是调解的。）","translator":"王静斋"},{"sura":2,"aya":12,"text":"须知他们均是造恶的；但是自不觉悟。","translator":"王静斋"},{"sura":2,"aya":13,"text":"有时候，人对他们说：（你们归信，就像众人归信了吧：）他们就说：我们岂可归信，就像无识的人归信吗?）须知他们就是无识的人，只是自不知道。","translator":"王静斋"},{"sura":2,"aya":14,"text":"有时候，他们遇见归信的人了，就说：（我们已然归信了。）他们独遇到同类的恶魔时候，就说：我们和你们同类，我们是嘲弄的。","translator":"王静斋"},{"sura":2,"aya":15,"text":"安拉将以其嘲弄还诸其人。放任他们在迷误中徘徊着。","translator":"王静斋"},{"sura":2,"aya":16,"text":"这般人是以正道换取迷误的，他们的营业未获利，他们不是得指导的。","translator":"王静斋"},{"sura":2,"aya":17,"text":"他们的情形如同是燃火的人，在火光照耀四周的时候，安拉就夺去他们的光，把他们弃在重重黑暗里，他们看不见。","translator":"王静斋"},{"sura":2,"aya":18,"text":"他们是聋的、哑的、瞎的，所以不能转回。","translator":"王静斋"},{"sura":2,"aya":19,"text":"或是如同遇天雨的人，其中含有重重黑暗和雷电；他们因为怕死，畏惧雷呜，置指于耳中。安拉尽知众不信的人。","translator":"王静斋"},{"sura":2,"aya":20,"text":"闪电几至夺去他们的眼。那电向他们发光的时候，他们就乘光迈步；及至遮黑的时侯，他们就站住。设若安拉意欲……了，他便夺去他们的听官，视官。安拉是全能于万事的。","translator":"王静斋"},{"sura":2,"aya":21,"text":"众人哪！你们当敬拜养主，那造化你们和你们众前人的主，冀使你们敬慎。","translator":"王静斋"},{"sura":2,"aya":22,"text":"那真主，他为你们以地怍铺毯，以天作覆盖，并自上空降水，而为供给你们，用它发出种种果实。你们不得明知着而为安拉举同等的。","translator":"王静斋"},{"sura":2,"aya":23,"text":"如果你们对于我降给自己仆人的有所怀疑了，你们就举出和它相似的一章来。如果你们是实言的，你们就抛开安拉而唤来一般助己的。","translator":"王静斋"},{"sura":2,"aya":24,"text":"若是你们未能实作；你们决对不能实作——你们就堤防其燃料是人与石的火狱吧，那火狱已为不信的人们备下了。","translator":"王静斋"},{"sura":2,"aya":25,"text":"你给众归信并作善举的人报喜信吧，他们必获诸河流于其下的天园。不论何时，他们由那里蒙赐果实的给养了，他们就说：“（这是从前赐过我们的。）” 曾把那相似的赐给他们了。 他们在天园中获得清洁的配偶，永居在那里。","translator":"王静斋"},{"sura":2,"aya":26,"text":"安拉不耻于发一个比喻——蚊子或比它更小的。在归信的人们，皆知道那是出自养主的真理。不信的人们就说：（安拉用这个比喻作什么?）他用它使多的人迷误；并用它引导多的人。他惟用它使一般作恶的人迷误。","translator":"王静斋"},{"sura":2,"aya":27,"text":"那般人在结约以后，破坏安拉的约，并断绝安拉命以接合的，更在地上造恶。这般人均是伤折的。","translator":"王静斋"},{"sura":2,"aya":28,"text":"你们怎么不信安拉呢?你们原是死的，而后他教你们生，又教你们死，而后又教你们生，然后你们均被归返于他。","translator":"王静斋"},{"sura":2,"aya":29,"text":"他那真主，为你们造化地面上一切所有的，他且曾倾向造天而使其成为整齐的七天。他是深知万事的。","translator":"王静斋"},{"sura":2,"aya":30,"text":"昔时，你的养主对众天使说：（我必欲在地上造一代位的。）他们说：（有我们以赞你称道清净颂扬圣洁着，你岂可在地上造化作恶流血的人呢?（主说：（我实知道你们不知道的。）","translator":"王静斋"},{"sura":2,"aya":31,"text":"他把一切名称统教给阿丹了，他又把它们现给众天使，说：如果你们是说实话的，你们就把这一切的名称宣告给我。）","translator":"王静斋"},{"sura":2,"aya":32,"text":"他们说：（颂你清净：除却你所教给我们的，在我们并无知识。 你实是深知的，明哲的。）","translator":"王静斋"},{"sura":2,"aya":33,"text":"真主呼阿丹说：（你把这万物的名称宣告给他们吧，）及至他把万物的名称宣告他们的时候，真主就说：（我岂未对你们说过吗?我知道天地的隐秘，我知道你们公开的和隐匿的。）","translator":"王静斋"},{"sura":2,"aya":34,"text":"昔时，我告诉众天使向阿丹下拜；他们遂就下拜了；惟有以卜里嘶拒绝，高傲，他本属于逆徒。","translator":"王静斋"},{"sura":2,"aya":35,"text":"我呼阿丹说：（你和你的妻居园林吧！你俩由那里随意多食，不要接近这树，以免成为背义的。）","translator":"王静斋"},{"sura":2,"aya":36,"text":"但是恶魔诱他俩失足了，使他俩出离原有的地位。我说：（你们下去吧！你们彼此一个是一个的仇敌，你们在地上获一个定处，并享受到一个时期。）","translator":"王静斋"},{"sura":2,"aya":37,"text":"而后，阿丹接受养主的几句话，主随就允他悔罪了，主实是允许悔罪的，特慈的。","translator":"王静斋"},{"sura":2,"aya":38,"text":"我说：（你们一同从这里下去吧！若是我的指示达至你们了，那从我指示的人，在他们无所惧忧。","translator":"王静斋"},{"sura":2,"aya":39,"text":"隐昧，并不信我表征的人，均是永居火狱的。）","translator":"王静斋"},{"sura":2,"aya":40,"text":"以斯拉衣来的子孙哪！你们当记念我对你们施过的恩典，你们当全我的约，我就全你们的约。你们要惧怕我。","translator":"王静斋"},{"sura":2,"aya":41,"text":"我颂降的，是为证实你们所守的那个，你们要得信它。 你们不可首先隐昧它。你们不得以我的表征换稍微的代价。你们要畏惧我。","translator":"王静斋"},{"sura":2,"aya":42,"text":"你们不要明知着以假混真，也不要隐匿真理。","translator":"王静斋"},{"sura":2,"aya":43,"text":"你们举拜功：散天课：你们当和鞠躬的人们一同鞠躬。","translator":"王静斋"},{"sura":2,"aya":44,"text":"你们读经着能肯命人为善而忘却自身吗?你们怎不了解呢?","translator":"王静斋"},{"sura":2,"aya":45,"text":"你们当以（忍耐）和（拜功）求助。礼拜确是一件繁难的事；惟在谦逊的人上则不然。","translator":"王静斋"},{"sura":2,"aya":46,"text":"那般人切知自己是将遇见养主的，是将必归返于他的·","translator":"王静斋"},{"sura":2,"aya":47,"text":"以斯拉衣来的子孙哪！你们当记念我对你们所施的恩典并记念我教你们优越同一时代的人们。","translator":"王静斋"},{"sura":2,"aya":48,"text":"你们当堤防那么一天：人不能代人避免一事，不允人的告赦，不纳人的救赎，也无人相助他们。","translator":"王静斋"},{"sura":2,"aya":49,"text":"昔时，我教你们脱离匪喇傲的党徒。他们叫你们尝酷刑；他们宰死你们的男儿，留下你们的女儿。此中有出自养主的一大试证。","translator":"王静斋"},{"sura":2,"aya":50,"text":"昔时，我为你们分海；我解救你们，而淹毙匪喇傲的党徒，你们曾经看见。","translator":"王静斋"},{"sura":2,"aya":51,"text":"昔时，我和母撒相约四十夜，你们在他以后背义着拜牛犊了。","translator":"王静斋"},{"sura":2,"aya":52,"text":"在这以后，我又放赦你们了，好教你们感念。","translator":"王静斋"},{"sura":2,"aya":53,"text":"昔时，我赐给母撒经典 和区别的，好教你们得正道。","translator":"王静斋"},{"sura":2,"aya":54,"text":"昔时，母撤对他的族人说：（我的族人哪，你们因着拜牛犊自负已身了，你们当向造物主悔罪，你们当杀死同类，这在造物主视为于你们至好的。）而后主允你们悔罪了，真主实是允许悔罪的，特慈的。","translator":"王静斋"},{"sura":2,"aya":55,"text":"昔时，你们呼母撒说：（我们非明明地看见安拉以后，绝不信你， ）而后，你们遭天火了，你们曾经看见。","translator":"王静斋"},{"sura":2,"aya":56,"text":"在你们死去以后，我又教你们复起，好教你们感念。","translator":"王静斋"},{"sura":2,"aya":57,"text":"我用（白云）给你们作遮蔽，并降给你们（吗哪）和（鹑鸟；）（我吩咐）你们就着我所供给的各项美品吃吧！他们未负我，只是自负己身了。","translator":"王静斋"},{"sura":2,"aya":58,"text":"昔时，我说：（你们进这城池吧，你们由那里随意多吃，你们俯首入门，你们说（痕大！）若然，我就饶恕你们的差错，我即将增给为善的人。）","translator":"王静斋"},{"sura":2,"aya":59,"text":"而后，一般背义的人变更对他们所说过的话了。而后，我因那些背义的人作恶降下天灾了。","translator":"王静斋"},{"sura":2,"aya":60,"text":"昔时，母撒为自己的族人求水；我命以手杖击石；于是十二泉自那里涌出，众人各自认识取水的所在。（我说：）你们就着安拉的供给吃吧！饮吧！你们不要在地上意图造恶着犯罪。","translator":"王静斋"},{"sura":2,"aya":61,"text":"昔时，你们呼母撒说：叫我们绝难忍于一色食物；你为我们求你的养主：他给我们发观地上生长的——蔬菜，王瓜、麦子、扁豆、玉葱。 ）母撒说：（你们用最好的求改换最低的吗? 你们进城去吧！你们可获得自己所要求的。 ）他们遭受「侮辱和」贫乏了，他们遭受安拉的怒恼着转来，这是因他们不信安拉的表征，和他们无故杀害圣人。这是因他们的背逆和过当。","translator":"王静斋"},{"sura":2,"aya":62,"text":"的确，一般归信者、犹太教徒、基督教徒，沙便人，其信安拉和末日，并作善举的人，可在养主御前获得自己报酬，在他们无惧无忧。","translator":"王静斋"},{"sura":2,"aya":63,"text":"昔时，我取得你们的约；我起山于你们上面。（我说：）你们坚守我赐给你们的；你们谨记内中所载的，冀使你们敬慎。","translator":"王静斋"},{"sura":2,"aya":64,"text":"在这以后，你们复又违背了。设非安拉对你们的特恩和慈惠， 一定你们就归为伤折的了。","translator":"王静斋"},{"sura":2,"aya":65,"text":"你们确曾知道同类之中在安息日过当的人，我曾语他们变（若）卑贱的猴。","translator":"王静斋"},{"sura":2,"aya":66,"text":"我把这事作为儆惕它以前以后的人，并劝化敬慎的人们。","translator":"王静斋"},{"sura":2,"aya":67,"text":"昔时，母撒对自己的族人说：（安拉命你们宰一头母牛。）他们说：（你拿我们当作可戏的吗?）他说：（求安拉护佑我，莫作愚人。 ）","translator":"王静斋"},{"sura":2,"aya":68,"text":"他们说：（你为我们祈你的养主；他对我们表明：那是一头什么牛?）他说：（真主说了；那是一头不老不幼，适当的中年牛，你们奉行命令吧！）","translator":"王静斋"},{"sura":2,"aya":69,"text":"他们说：（你为我们祈你的养主；他对我们解明：那牛是个什么毛色?）","translator":"王静斋"},{"sura":2,"aya":70,"text":"他们说：（你为我们祈你的养主；他对我们表明：那是一头什么牛?这牛在我们实在辩不清楚。若是安拉意欲……了，我们是一定能够得到的。）","translator":"王静斋"},{"sura":2,"aya":71,"text":"他说：（真主说了！它是一头不善耕地、灌田，健全而且无杂色的牛。）他们说：（这时候你发表真话了。）于是他们宰了那头牛，他们原未想实作。","translator":"王静斋"},{"sura":2,"aya":72,"text":"昔时，你们杀了一个人，而为他互相争论。安拉现露你们所隐匿的，——","translator":"王静斋"},{"sura":2,"aya":73,"text":"我说：（你们用牛的一部分打他！）安拉如此复活死去的，并把自己的表征现给你们，好教你们了解。","translator":"王静斋"},{"sura":2,"aya":74,"text":"在这以后你们心硬如石，或者更硬。石头里有诸河自它上涌出的； 石头里有崩裂而由内中出水的；石头里有因畏惧安拉滚下来的。安拉必不忽于你们的作为。","translator":"王静斋"},{"sura":2,"aya":75,"text":"你们盼望他们信你们吗?他们之中的一伙曾听得安拉的话，而在了解它以后故意变更。","translator":"王静斋"},{"sura":2,"aya":76,"text":"他们遇见众穆民的时候，就说：（我们归信了。）他们彼此独自相遇的时候，就说(你们把安拉启示你们的述说给他们，而为的是他们在养主御前对你们争论吗?你们怎不了解呢?）","translator":"王静斋"},{"sura":2,"aya":77,"text":"他们岂不知道安拉知他们隐匿的和公开的吗?","translator":"王静斋"},{"sura":2,"aya":78,"text":"他们 之中有些失教的人，不知经典，只是妄想，他们不过是揣度。","translator":"王静斋"},{"sura":2,"aya":79,"text":"可叹那般人！他们亲手写经； 且自称：（这是由安拉上来的，）为的是用它换取些微的代价。他们因亲手所写的遭祸了！他们因自所作过的遭祸了！","translator":"王静斋"},{"sura":2,"aya":80,"text":"他们说：（火接触我们不过有数的几日。） 你说！（你们在安拉御前立下约了吗?安拉绝不爽约；或是你们往安拉上说自己不知道的吗?）","translator":"王静斋"},{"sura":2,"aya":81,"text":"不是的，凡是作恶且被差错所包围的人，均是永居火狱的。","translator":"王静斋"},{"sura":2,"aya":82,"text":"归信并作善举的人，均是永居天园的。","translator":"王静斋"},{"sura":2,"aya":83,"text":"昔时，我取得以斯拉衣来子孙的约：（你们不可拜安拉以外的；你们要善待父母、亲属、孤儿、贫人；你们要对人说好话；你们要立拜功，施天课。然后，除少数人以外，你们违背了。你们是惯于远避的。","translator":"王静斋"},{"sura":2,"aya":84,"text":"昔时，我取得你们的约：（不得互相残杀，不得把同类的人逐出自己的住宅）随后你们承认了，你们曾经自作见证。","translator":"王静斋"},{"sura":2,"aya":85,"text":"而后你们这般人自残同类，并 把一部分同人逐出自己的住宅，你们以（罪恶）和（仇恨）彼此相辅地压迫他们。他们若被俘着来到你们这里，你们就救赎他们。——其时驱逐他们，也是在你们不应该的。——你们信一部分经典，隐昧一部分吗?你们之中行这事的人的报应，只是在今生受侮辱，在后世归到最烈的刑罚。安拉必不忽于你们的作为；","translator":"王静斋"},{"sura":2,"aya":86,"text":"这般人是弃后世换今生的。不为他们减轻刑罚，也不相助他们。","translator":"王静斋"},{"sura":2,"aya":87,"text":"我确曾赐给母撒经典，并在他以后续差了列使，我赐给马尔焉的子尔撒各项异迹；并以（洁灵）援助他。每有一使者把你们心所不欲的示给你们了，你们就倔强起来；你们不信一部分，杀死一部分吗? 88、他们说：（我们的心是被遮的。）不然，是安拉因他们的不信怒恼他们了；所以他们绝少归信。","translator":"王静斋"},{"sura":2,"aya":88,"text":"他们说：（我们的心是被遮的。）不然，是安拉因他们的不信怒恼他们了；所以他们绝少归信。","translator":"王静斋"},{"sura":2,"aya":89,"text":"当出自安拉御前证实他们所守的经典达到的时候，（他们不肯承认；）从前他们求相助，以对付不信的人，及至他们得到所认识的了，便就不信它·不信的人应遭安拉的怒恼。","translator":"王静斋"},{"sura":2,"aya":90,"text":"他们用作出卖自身的太恶了！那就是隐昧安拉所降的，由于嫉忌安拉依其特恩降给自己所意欲的仆人；所以他们应当怒上遭怒着转来，不信的人们应受辱刑。","translator":"王静斋"},{"sura":2,"aya":91,"text":"一旦有人对他们说：（你们信安拉，降下的吧！）他们就说：（我们信所降给我们的。） 他们不信除它以外的。其实，那是证实他们所守的真理。你说：（如果你们是归信的，从前你们为了什么杀死安拉的列圣呢?）","translator":"王静斋"},{"sura":2,"aya":92,"text":"的确，母撤曾把种种异迹现给你们， 后来你们在他以后背义着拜牛犊了。","translator":"王静斋"},{"sura":2,"aya":93,"text":"昔时，我取得你们的约；我起山于你们上面，（说：）（你们坚守我曾赐给你们的吧！你们应当听从。）他们说：（我们听到了，我们违背了，）他们由于不信而醉心牛犊了。你说：（如果你们是有信仰的，你们的（信仰）命你们所做的事情太恶了。","translator":"王静斋"},{"sura":2,"aya":94,"text":"你说：（如果在安拉所掌的后世不归别人，特属你们的了，你们就盼望死去。你们若是实言的了，……）","translator":"王静斋"},{"sura":2,"aya":95,"text":"他们因亲手作下的 永不盼死，安拉深知背义的人。","translator":"王静斋"},{"sura":2,"aya":96,"text":"你觉得他们是人类最贪生的，且比多神教徒更是贪生。 他们有人希望教他享寿千年；给他寿限那不就是教他脱离刑罚的。安拉看得见他们的作为。","translator":"王静斋"},{"sura":2,"aya":97,"text":"你说，仇恨直布勒来的人，（可任其忿死。）的确，他奉安拉的命令把它降在你的心上，那（古兰）是证实在它以前的，并引导与给众归信的人报喜信的。","translator":"王静斋"},{"sura":2,"aya":98,"text":"仇恨安拉和他的众天使诸钦差、直布勒来、米克来的人，安拉一定是仇恨众逆徒的。","translator":"王静斋"},{"sura":2,"aya":99,"text":"我向你降下显着的明证了。隐昧它的，只是一般作恶的人。他们岂可（不信它？）","translator":"王静斋"},{"sura":2,"aya":100,"text":"他们每逢结约的时候，其中的一伙人抛弃它，他们大多数只是不归信。","translator":"王静斋"},{"sura":2,"aya":101,"text":"一位使者证实他们所守的，来自安拉那里的时候，一伙有经的人，就把天经弃在背后，好像他们不知道。","translator":"王静斋"},{"sura":2,"aya":102,"text":"他们追随恶魔在苏来茫作王时代所读的。苏来茫未曾背逆，只是恶魔们背逆了；他们教给人邪术，并追随在巴比伦。所降给二天使——哈鲁特、马鲁特 的。他俩必先说：（我们只是一种磨难，你不要背逆。 ）而后始肯教给任何一个人。他们从他俩上习学用以离间夫妇的。他们除非藉安拉的判定，绝不能用它伤一个人。他们习学于自己有害无利的。他们确切知道，那习邪术的人，在后世不得一点福分。他们用作出卖自身的太恶了，","translator":"王静斋"},{"sura":2,"aya":103,"text":"设若他们知道了，……。假使他们归信了，敬慎了，……。出自安拉御前的善赏是最好的。设若他们知道了，……。","translator":"王静斋"},{"sura":2,"aya":104,"text":"众归信的人哪，你们莫说（啦尔哪！）你们要说（温祖尔哪！）你们应当听命。众不信者应受痛刑。","translator":"王静斋"},{"sura":2,"aya":105,"text":"有经人中的不信者，以及多神教徒，不愿意把出自养主的福利降给你们。安拉特慈其意欲……的人，安拉是有大恩的。","translator":"王静斋"},{"sura":2,"aya":106,"text":"我停止不论哪节经文，或教人忘记它，我就赐给比它更好的，或是与它一样的。你岂不知道安拉是全能于万事的吗?","translator":"王静斋"},{"sura":2,"aya":107,"text":"你岂不知道安拉掌天地的权柄吗?你们舍去安拉，得不到一爱护的，相助的。","translator":"王静斋"},{"sura":2,"aya":108,"text":"你们要求自己的使者如同从前有人要求母撒吗?凡是以（正信）改换（不信）的，他却是失迷正路了。","translator":"王静斋"},{"sura":2,"aya":109,"text":"许多有经的人，盼望你们在归信以后仍作逆徒。这是在他们明白真理以后，出自私心的嫉妒你们当宽宥，当容让，待至安拉发下他的命令。安拉实是全能于万事的。","translator":"王静斋"},{"sura":2,"aya":110,"text":"你们当立拜功，施天课，你们为自身作下的善事，将在安拉那里获得它。安拉看得见你们的作为。","translator":"王静斋"},{"sura":2,"aya":111,"text":"他们说：（进天园的只是犹太人，或基督徒。 ）这是他们的幻想。你说（如果你们是实言的了，要你们举出凭据来。","translator":"王静斋"},{"sura":2,"aya":112,"text":"不然，凡是为善着，诚心倾向安拉的人，可在养主御前获得报偿在他们无所惧忧。","translator":"王静斋"},{"sura":2,"aya":113,"text":"犹太人说：（基督教徒空虚无为，）基督徒说：（犹太人空虚无为， ）他们均在读经着。无知的人们也说和他们一类的话。安拉将在复生日对他们所争议的从中判断。","translator":"王静斋"},{"sura":2,"aya":114,"text":"阻挡人们在拜安拉的地方记念他的尊名，并尽力摧毁礼拜堂，谁是比这类人越发背义的? 这般人本应畏惧着进礼拜堂。他们在今世受辱，在后世应遭大的刑罚。","translator":"王静斋"},{"sura":2,"aya":115,"text":"东方西方惟安拉执掌，你们面向不拘哪里，安拉就在面前。 安拉是宽恩的，深知的。","translator":"王静斋"},{"sura":2,"aya":116,"text":"他们说：（安拉立子了。）颂主清净，不然，天地间所有的，惟他执掌。统是顺服他的。","translator":"王静斋"},{"sura":2,"aya":117,"text":"造化天地的主，他判定一事的时候，仅对它说：（有，）它立刻就有。","translator":"王静斋"},{"sura":2,"aya":118,"text":"无知的人们说：（为什么安拉不对我们交谈，或有一异迹达至我们。 ）他们以前的诸人也说和他们一样的话，他们的心彼此相似。","translator":"王静斋"},{"sura":2,"aya":119,"text":"我为有定心的群众解明了各项表征。我确因着真理差你，报喜信，传警告着，你对居火狱的人不负责任。","translator":"王静斋"},{"sura":2,"aya":120,"text":"犹太人和基督徒必须你随从他们的宗教，始肯喜爱你。你说：（安拉的引导却是引导。）如果你在得到知识以后，跟随他们的私见了，你就得不到一个抗安拉作爱护与相助的。","translator":"王静斋"},{"sura":2,"aya":121,"text":"蒙我赐给经典的人，切实地读它、全都信它。不信它的均是伤折的人。","translator":"王静斋"},{"sura":2,"aya":122,"text":"以斯拉衣来的子孙哪！你们当记念我对你们施过的恩典，并记念我教你们优越同一时代的人。","translator":"王静斋"},{"sura":2,"aya":123,"text":"你们当堤防那么一日。人不能替人避免一事，不接受他的救赎，告赦于他无济，也无人相助他们。","translator":"王静斋"},{"sura":2,"aya":124,"text":"昔时养主以几句话试验易卜拉欣，他遂就完全奉行了。主说：（我使你作众人的教长，）他说：（也使我底一部分子孙。）主说：（我的约达不到背义的人上。）","translator":"王静斋"},{"sura":2,"aya":125,"text":"昔时，我设置此殿，为作世人的归所，与平安的地方。你们就着易卜拉欣站立的地方取礼拜的处所：我曾与易卜拉欣、易司马衣结约：你们俩为一般巡游的常住的、鞠躬叩头的人，洁净我的殿。","translator":"王静斋"},{"sura":2,"aya":126,"text":"昔时，易卜拉欣说：（我的养主啊！你把这地方作为平安的城池，你把一部分果实赐给它的居民，——其中归信安拉和末日的人。 ）主说：（并赐给不信的人享受些微的，然后再把他驱入火刑。居所太恶了！）","translator":"王静斋"},{"sura":2,"aya":127,"text":"昔时，易卜拉欣和易司马衣起高了殿基，说：我们的养主啊！求你予以悦纳，你实是能闻的，深知的。","translator":"王静斋"},{"sura":2,"aya":128,"text":"我们的养主啊！祈你使我们作顺服你的人，并使我们的一部分子孙作顺服你的群众！你把作功的地方现给，我们！你允承我们的悔罪吧！你实是允许悔罪的，特慈的。","translator":"王静斋"},{"sura":2,"aya":129,"text":"我们的养主啊！你把他们之中一位使者遣在那里；对他们读你的表征，并教给他们经典和学识，更要清洁他们。你实是优胜的，明哲的。","translator":"王静斋"},{"sura":2,"aya":130,"text":"除却不识自身的人，谁肯不守易卜拉欣的道。我确在今世选择他了，他在后世定属于义人。","translator":"王静斋"},{"sura":2,"aya":131,"text":"昔时，养主对他说：（你顺服了吧！）他说：（我顺服调养众世界的主了。）","translator":"王静斋"},{"sura":2,"aya":132,"text":"易卜拉欣曾以这话嘱咐他的儿子们，叶而孤白也是这样，（说：）（我的儿子们哪！安拉确为你们选择了这个教门；你们要顺服着至死！）","translator":"王静斋"},{"sura":2,"aya":133,"text":"当叶而孤白濒死的时候，你们在场吗?那时候，他对自己的儿子们说：（你们在我以后拜什么呢?）他们说：（我们拜你的主，也拜你列祖——易卜拉欣、易司马衣、易司哈各的主——独一的主。我们是惟独顺服他的。）","translator":"王静斋"},{"sura":2,"aya":134,"text":"这是那已经过去的群众，他们有他们的作为，你们有你们的作为，你们对他们的作为不负责任。","translator":"王静斋"},{"sura":2,"aya":135,"text":"他们说：（你们应作犹太人或作基督徒，你们就得正道了。） 你说吧！（不，我们只追随正直的易卜拉欣之道，他本不是拜偶像的。 ）","translator":"王静斋"},{"sura":2,"aya":136,"text":"你们说：（我们信安拉和降给我们的，和降给易卜拉欣，易司马衣、易司哈各、叶而孤白暨众子孙的； 和赐给母撒、尔撒的，和由养主上赐给列圣的。我们对于他们任何一位不作区别。我们是惟独顺服他的。）","translator":"王静斋"},{"sura":2,"aya":137,"text":"如果他们信如你们所信的，他们就得正道了。若是他们不肯了，他们就是在反抗中。安拉即将替你来对待他们。 他是能闻的，深知的。","translator":"王静斋"},{"sura":2,"aya":138,"text":"安拉的浸染。谁是比安拉善于浸染的?我们是惟独敬拜他的。","translator":"王静斋"},{"sura":2,"aya":139,"text":"你说：（你们因为安拉对我们争辩吗?他是我们的养主，也是你们的养主，在我们有我们的作为，在你们有你们的作为。我们只是对他虔敬的。）","translator":"王静斋"},{"sura":2,"aya":140,"text":"你们肯说：（易卜拉欣、易司马衣、易司哈各、叶而孤白暨众子孙，全是犹太人，或是基督徒吗?）你说：（你们至知?或是安拉呢?）谁是不义更甚于隐匿安拉寄他跟前见证的人?安啦必不忽于你们所作的。）","translator":"王静斋"},{"sura":2,"aya":141,"text":"这是那已经过去的群众，他们有他们的作为，你们有你们的作为；你们对于他们的作为不负责任。","translator":"王静斋"},{"sura":2,"aya":142,"text":"天才的人们将要好说：（什么事情使他们转过原守的朝堂呢？）你说：（东方西方惟属安拉执掌。他任便把人引至正道。","translator":"王静斋"},{"sura":2,"aya":143,"text":"我如此着，使你们成为中庸的一伙，好教你们是见证众人的，使者是见证你们的，我把你原守的作为朝堂，只为知道谁是追随使者的，谁是逆转的。那却是一件重大的事情；惟有安拉引导的人不然。安拉绝不湮没你们的信仰，安拉是必怜悯、慈悯众人的。","translator":"王静斋"},{"sura":2,"aya":144,"text":"我却见你面向着上空。我必教你转向你所喜爱的朝堂；你就面向禁寺方向吧！你们处在不论哪里，你们就应面向那一面！一般有经的人却知道：那是由养主上确当的，安拉必不忽于他们的作为。","translator":"王静斋"},{"sura":2,"aya":145,"text":"如果你把各项表徽现给有经的人了，他们不随从你的朝堂；你也不必随从他们的朝堂；他们的部分也不随部分的朝堂。如果在你得到知识以后，随从他们的私见了。那时侯你必定归为不义的人了。","translator":"王静斋"},{"sura":2,"aya":146,"text":"蒙我赐给有经典的人认识他，就象认识自己的儿子一样。他们之中的一伙人，明知着而隐匿真的。","translator":"王静斋"},{"sura":2,"aya":147,"text":"真的出自养主，你莫怀疑。","translator":"王静斋"},{"sura":2,"aya":148,"text":"每一伙人有其面向的朝堂。你们要忙行种种善事。你们处在不论哪里，安拉将把你们一同拿到。安拉是全能于万事的。","translator":"王静斋"},{"sura":2,"aya":149,"text":"你去到不论什么地方，就当面向禁寺的方面。这是出自养主的真理。安拉必不忽于你们的作为","translator":"王静斋"},{"sura":2,"aya":150,"text":"你去到不论什么地方，你就当面向禁寺方面吧！你们住到不论那里，你们就应该面向那一面，以免众人———除却他们其中一般不义的人———质问你们。你们不要惧他们应当惧我；并为我全满对你们的恩典；更为你们得正道；","translator":"王静斋"},{"sura":2,"aya":151,"text":"就如我在你们中间差来一个同类的使者。他把我的经典读给你们；他使你们清洁，他把经典和智慧教给你们；他把你们所不知道的教给你们。","translator":"王静斋"},{"sura":2,"aya":152,"text":"你们当记念我，我就记念你们；你们应当感念我，你们不要对我忘恩。","translator":"王静斋"},{"sura":2,"aya":153,"text":"众归信的人那！你们当以忍耐和拜功求襄助。安拉实是和忍耐的人同在的。","translator":"王静斋"},{"sura":2,"aya":154,"text":"你们对于在安拉之道上被杀者，莫说：（他们是死人。）确是活的。但是你们不觉得。","translator":"王静斋"},{"sura":2,"aya":155,"text":"我必用些微的——骇怕、饥馑、损折财命、果实，试证你们。你当给忍耐的人报喜信。","translator":"王静斋"},{"sura":2,"aya":156,"text":"那般人当遇难的时候，就说：（我们是安拉所掌的，我们是将必归于他的。）","translator":"王静斋"},{"sura":2,"aya":157,"text":"这般人可获其养主的怜爱和慈悯，这般人就是得正道的。","translator":"王静斋"},{"sura":2,"aya":158,"text":"的确，索法和墨尔袜是属安拉的徽迹，奔赴天房，或做例外巡礼的人，不妨游此二山。格外做善的人，安拉一定是颂善的，深知的。","translator":"王静斋"},{"sura":2,"aya":159,"text":"的确，我在经典中向世人解释自所降下的明证和标准以后， 一切隐匿它的人，均遭安拉的怒恼，并遭一切咒诅者的咒诅；","translator":"王静斋"},{"sura":2,"aya":160,"text":"除却那般悔罪自修并且表明的人。这般人我允他们的悔罪。我是允受悔罪的，特慈的。","translator":"王静斋"},{"sura":2,"aya":161,"text":"的确，","translator":"王静斋"},{"sura":2,"aya":162,"text":"一般不信，并在不信着死去的人，他们永遭安拉和众天使与世人一概的怒恼，他们不获减轻罪刑，且不获姑容。","translator":"王静斋"},{"sura":2,"aya":163,"text":"你们的主是唯一受拜的。除他普慈特慈的以外，再无有主。","translator":"王静斋"},{"sura":2,"aya":164,"text":"在天地的造化、夜昼的不同、为利于众人行驶在海中的船、和安拉自上空降下来而用它活既死之地的水，与那散播在地上的一切动物，和变换风，暨天地间被制的云，内中确对于一般了解的人含有种种表征。","translator":"王静斋"},{"sura":2,"aya":165,"text":"有的人撇开安拉取同等的。 他们爱它们，如爱安拉。众归信的人是爱安拉较甚的。设使背义的人，在亲见罪刑的时候看见能力统属安拉的，安拉是掌烈刑的，……。","translator":"王静斋"},{"sura":2,"aya":166,"text":"彼时，一般领袖 回避一般追随者。他们亲见刑罪了，种种关系已经断绝了。","translator":"王静斋"},{"sura":2,"aya":167,"text":"一般追随的人说：（但盼我们有一复反；我们和他们无干。如同他们和我们无干。安拉如此着把他们的作为显给他们，使他们懊丧着；他们绝出不去火狱。","translator":"王静斋"},{"sura":2,"aya":168,"text":"众人哪！你们吃地上所有清洁合法的！你们莫要追随恶魔的脚步。它实是你们的明敌。","translator":"王静斋"},{"sura":2,"aya":169,"text":"它惟命你们以罪恶与丑事；并命你们把自所不知的说在安拉上。","translator":"王静斋"},{"sura":2,"aya":170,"text":"一旦有人对他们说：（你们当追从安拉所降的，）他们就说：（不，我们只是追从曾见自己祖先所守的。） 设若他们的祖先不了解一事，不得正道，也可吗?","translator":"王静斋"},{"sura":2,"aya":171,"text":"一般不信的人的情形，就像呵叱只闻叫喊的人的情形。（他们）是聋子、哑子、瞎子，所以不能了解。","translator":"王静斋"},{"sura":2,"aya":172,"text":"众归信的人哪！你们食我所供给你们种种清洁的！你们当感念安拉。如果你们惟独拜他了，……。","translator":"王静斋"},{"sura":2,"aya":173,"text":"他只是对你们禁忌自死的、 血、猪肉、与宰割时不以安拉的名高呼的。其非背叛，也非越范，被迫无奈的人，于他无罪。安拉实是多恕的，特慈的。","translator":"王静斋"},{"sura":2,"aya":174,"text":"的确，一般不信安拉颁降的经典，并用它换取些微代价的人，他们只是把火吃在腹内。在复生日安拉不与交言，不使清洁，他们应遭痛刑。","translator":"王静斋"},{"sura":2,"aya":175,"text":"这般人是弃正道换取迷误，弃饶恕换取罪刑的。他们太勇于抵抗火狱了，","translator":"王静斋"},{"sura":2,"aya":176,"text":"这是由于安拉因为真理降下经典。对于经典起争议的人确是在遥远的反抗中。","translator":"王静斋"},{"sura":2,"aya":177,"text":"你们的面向东方西方不为真善。真善只是那归信安拉、末日、天使、经典、列圣，并把自爱的财物赠给亲属、孤儿、贫人、旅客、乞讨的、用作赎身的、立拜功、施天课，当结约时全其所约的人。一切在痛苦患难时，在出战时、忍耐的人太好了！这般人是忠实的，这般人均是敬慎的。","translator":"王静斋"},{"sura":2,"aya":178,"text":"众归信的人哪！已对你们规定了给被杀的人抵偿；良人以良人，奴隶以奴隶，女子以女子。但是其弟兄的一事经过赦免的人，可从定例，并欣然交付给他。 这是出自养主的减轻和慈惠。在这以后过当的人，应遭痛刑。","translator":"王静斋"},{"sura":2,"aya":179,"text":"有才识的人哪！你们于抵偿上可获一种生存，冀使你们敬慎。","translator":"王静斋"},{"sura":2,"aya":180,"text":"你们有人临终的时候，如果遗下财产了，可因为父母和近亲照例作遗嘱，这是对你们规定的。这是在一般敬慎的人上所应行的。","translator":"王静斋"},{"sura":2,"aya":181,"text":"在听到以后而又变更的人，惟独变更它的人有罪。安拉实是看见的，深知的。","translator":"王静斋"},{"sura":2,"aya":182,"text":"恐怕作遗嘱的人有些偏颇或罪恶，而从中调整的人，当然于他无罪。安拉是多恕的，特慈的。","translator":"王静斋"},{"sura":2,"aya":183,"text":"众归信的人哪！已对你们制定斋戒，就如对你们以前的众人制定它似的，冀使你们敬慎。","translator":"王静斋"},{"sura":2,"aya":184,"text":"限定的几日。你们之中染病，或旅行的人，则别几日。不能封斋的人，则罚赎——待一个贫人。 格外为善的人，是于他最好的。你们的封斋是于你们至好的，如果你们知道了……。","translator":"王静斋"},{"sura":2,"aya":185,"text":"（来默臧）月，是那于中颁降古兰的，是引导世人，并属于正道和判别的一切明证·你们之中适逢此月的人，当令他们封斋。染病或旅行的人则别几日。安拉对你们要容易，不对你们要困难；好教你们全满数目；并赞安拉伟大，因其指示你们正道；冀使你们感念。","translator":"王静斋"},{"sura":2,"aya":186,"text":"我的仆人向你问我的时候，我实是接近的。我应允祈者的祈求，当他求我的时候。当令他们应允我；当令他们归信我，好教他们得正道。","translator":"王静斋"},{"sura":2,"aya":187,"text":"在封斋夜内与妻交合是于你们合法的。她们是你们的衣服，你们是她们的衣服。安拉原知道你们自欺了，而后他允你们悔罪，他放赦你们了。现下你们和她们接触吧！你们寻安拉为你们写定的吧！任你们吃饮，直至黎明的白线自黑线里发现出来。而后你们完满斋戒，直到晚间。你们在礼拜堂里静坐的时候，不得与她们接触。这是安拉的定律。你们不要触犯它；安拉如此对众人解明他的律例，好教他们敬慎。","translator":"王静斋"},{"sura":2,"aya":188,"text":"你们不得妄吞同人的财产，把它投给法官，为的是明知着而以罪恶吞人家一部分财产也属不可。","translator":"王静斋"},{"sura":2,"aya":189,"text":"他们向你问新月。你说（新月是为世人和朝观人规定的标准。）真善不是由房后入室；真善是那敬畏的人。你们要由房门入室，你们当敬畏安拉，冀使你们得意。","translator":"王静斋"},{"sura":2,"aya":190,"text":"你们在安拉之道上对那和你们格斗的人格斗，你们不要过当。安拉实不爱过当的人。","translator":"王静斋"},{"sura":2,"aya":191,"text":"你们在不论哪里遇得他们，就当杀死他们，要从他们逐出你们的地方逐出他们去。祸患是较甚于格杀的。非至他们在禁寺左近对你们格杀的时候，你们不要在那里和他们格杀。如果他们和你们格杀了，你们就当对他们格杀。报复逆徒们就是这样。","translator":"王静斋"},{"sura":2,"aya":192,"text":"若是他们停止了。安拉实是多恕的，特慈的，","translator":"王静斋"},{"sura":2,"aya":193,"text":"你们当对他们格杀，直到祸患消灭，教门是因为安拉。如果他们停止了，只可敌视背义的人。","translator":"王静斋"},{"sura":2,"aya":194,"text":"禁月抵禁月。一切应该遵守的（事情）是有抵补的。压迫你们的人，你们要本着他的压迫去压迫他。你们当敬畏安拉，你们当知道安拉实是和敬畏的人们同在的。","translator":"王静斋"},{"sura":2,"aya":195,"text":"你们要使用在安拉的道上；不得自投灭亡。你们应当为善，安拉实爱喜为善的人。","translator":"王静斋"},{"sura":2,"aya":196,"text":"你们要因为安拉完满正朝，与副朝。如果你们被困了，则献一容易作到的祭物。你们不要剃头直至祭物达到它的处所而后可。你们之中染病或有头疾的人，则罚赎——斋戒，或施济，或献祭。如果你们获平安了，那借着副朝享受到正朝的人，则献一容易作到的祭物。未能得到的人，可于正朝中封三天斋；在你们返回的时候，封七天斋。这是完全的十天。这指的是不在禁寺住家的人。你们当敬畏安拉，当知道安拉是掌烈刑的。","translator":"王静斋"},{"sura":2,"aya":197,"text":"正朝是著名的几个月。在这些月里决意作正朝的人，则于朝觐中不得妄谈，作恶，纷争。你们举行的善事，安拉知道它。你们当预备路费。的确，最优的路费是虔敬。有才识的人哪！你们当敦畏我！","translator":"王静斋"},{"sura":2,"aya":198,"text":"你们不妨寻求养主的特恩。你们从尔里法提拥下来的时候，要在买士阿雷哈兰的跟前记念安拉。你们当记念主，依照他所指示你们的。你们在他以前确属茫无所知的。","translator":"王静斋"},{"sura":2,"aya":199,"text":"而后你们从众人拥下的地方拥下来！你们当祈安拉饶恕。安拉实是多恕的，特慈的。","translator":"王静斋"},{"sura":2,"aya":200,"text":"你们完结功课的时候，应当记念安拉，就像记念自己的列祖，或者记念的更甚。人类中有的说：（我们的养主啊！你在今世赐给我们吧！）其人在后世不获一分。","translator":"王静斋"},{"sura":2,"aya":201,"text":"他们之中有的说： （我们的养主啊！你把今世的好，并把后世的好，赐给我们吧！你保护我们脱离火刑吧！）","translator":"王静斋"},{"sura":2,"aya":202,"text":"这般人于自己所作的上获得一分。安拉是审算神速的。","translator":"王静斋"},{"sura":2,"aya":203,"text":"你们当在限定的几天里记念安拉！在两日内从速的人自属无罪。延迟的人于他无罪。（这）限于敬畏的人。你们当敬畏安拉，并当知自己是将被集至他的。","translator":"王静斋"},{"sura":2,"aya":204,"text":"人类中有的人，他对于今生说的话使你爱慕。他教安拉见证其心所怀的，他是最历害的仇敌。","translator":"王静斋"},{"sura":2,"aya":205,"text":"他退转的时候，就为着在地上造恶，并毁坏田产和牲畜而去努力。安拉不喜为恶。","translator":"王静斋"},{"sura":2,"aya":206,"text":"一旦有人对他说：（你敬畏安拉吧！）狂傲把他驱至罪恶。火狱是使他满足的铺垫太恶了！","translator":"王静斋"},{"sura":2,"aya":207,"text":"人类中有的人。为讨安拉的喜爱而卖出自身。安拉是怜恤众仆的。","translator":"王静斋"},{"sura":2,"aya":208,"text":"众归信的人哪！你们要投入整个的伊斯兰；不得追从恶魔的脚步。它实是你们的明敌。","translator":"王静斋"},{"sura":2,"aya":209,"text":"如果你们在种种明证达到以后失足了，你们当知道安拉实是优胜的，明哲的。","translator":"王静斋"},{"sura":2,"aya":210,"text":"他们岂可等待安拉和众天使乘着云影来到，事情已告终结?诸事惟归于安拉。","translator":"王静斋"},{"sura":2,"aya":211,"text":"你问以斯拉衣来人的子孙！我给过他们多少明迹? 在得到安拉的恩典以后加以变换的人，安拉实是掌烈刑的。","translator":"王静斋"},{"sura":2,"aya":212,"text":"因为逆徒们装饰今生了。他们嘲弄众穆民。敦畏的人，当复生日，是在他们上面。安拉无算地供给自所意欲……的。","translator":"王静斋"},{"sura":2,"aya":213,"text":"人类原是一伙，而后安拉派来报喜信，传警告的列圣；并连带地因为真理颁降了经典，俾便在众人中对他们所争议的加以判断。在各项明证达到以后，因为嫉忌起见，对它起争议的，只有蒙赐经典的人。安拉依其旨意向众穆民指示他们所对它争议的真理。安拉把自所意欲（指导）的人导至正路。","translator":"王静斋"},{"sura":2,"aya":214,"text":"你们的先人遭遇了痛苦和患难，他们曾受颠簸，而致使者和随他归信的人们说：（安拉的相助是在何时?）须知（安拉的相助是临近的，）像这样情形尚未达至你们，你们就以为将进天园吗?","translator":"王静斋"},{"sura":2,"aya":215,"text":"他们问你：可使用什么? 你说：（你们使用的财物，可归父母、近戚、孤儿、贫人、旅客。你们行下的善，安拉是深知它的。）","translator":"王静斋"},{"sura":2,"aya":216,"text":"曾责成你们格杀了，那是你们所厌恶的。恐怕你们厌恶一事，而那事本于你们有利；恐怕你们喜爱一事，而那事本于你们有害。安拉知道，你们不知道。","translator":"王静斋"},{"sura":2,"aya":217,"text":"他们对于在禁月里格杀上问你；你说：（在那月里格杀是大罪。阻碍安拉的道，不信主，阻碍禁寺，把寺中人逐出寺外，在安拉认为更是大罪。祸患是更大于格杀的。）他们时常对你们战争，为的是若得到能够的时候，就使你们转教。你们之中转教，至死不信的人，其功修在今世后世归为无效了；他们是永居火狱的。","translator":"王静斋"},{"sura":2,"aya":218,"text":"的确，众归信的人，及一切迁居，并在安拉的道上格斗的人，均盼望安拉的恩惠。安拉是多恕的，特慈的。","translator":"王静斋"},{"sura":2,"aya":219,"text":"他们对于酒和赌博上问；你说！（其中含有大罪，并有对人的利益。它俩的害处大过其利益。）他们问你，可使用什么? 你说！（盈余的。）安拉如此为你们解明一切表征，好教你们参悟今世与后世。","translator":"王静斋"},{"sura":2,"aya":220,"text":"他们对于孤儿上问你；你说！（为他们公平处理是最善的。如果你们和他们搀混了，（他们）就是你们的弟兄。安拉于调整的人上知道破坏的人。设使安拉意欲……了，他必教你们感觉困难，安拉实是优胜的，明哲的。","translator":"王静斋"},{"sura":2,"aya":221,"text":"你们不要娶多神教女子，待至他们归信而后可；多神教女子虽为你们所爱慕吧，究不如有正信的婢女。你们不要聘给多神教徒，待至他们归信而后可，多神教男子虽为你们所爱慕吧，究不如有正信的奴隶。这般人引人至火狱。安拉依自己的旨意导人于天园和饶恕。真主为世人解明他的表征，好教他们受劝。","translator":"王静斋"},{"sura":2,"aya":222,"text":"他们对于月经上问你，你说！（月经是一种污秽。 ）女子正在行经的时候，你们要远离，不要接近，待至她们洁净而后可。当她们洁净的时候，你们要依安拉所命的接近她们。安拉实爱喜悔罪的人，并喜爱清洁的人。","translator":"王静斋"},{"sura":2,"aya":223,"text":"你们的妇人是你们的田地，你们可任便来至自己的田地。你们当为自身送到前面，你们当敬畏安拉，当知自己是将遇见他的，你要给众穆民报喜信！","translator":"王静斋"},{"sura":2,"aya":224,"text":"你们莫以安拉作发誓：（不再行善，敬畏，并于人中调和，）的盾牌。安拉是听到的，深知的。","translator":"王静斋"},{"sura":2,"aya":225,"text":"安拉不以你们无意的发誓责罚你们；他只是以你们心所趋向的责罚你们。安拉是多恕的，仁厚的。","translator":"王静斋"},{"sura":2,"aya":226,"text":"发誓不与己妻交合的人，应等待四个月。如果他们反复了，安拉实是多恕的。特慈的。","translator":"王静斋"},{"sura":2,"aya":227,"text":"若是他们坚决离异了，安拉实是听到的，深知的 。","translator":"王静斋"},{"sura":2,"aya":228,"text":"离异的女子可自行等待三次月经。她们若信安拉和末日了，则不可隐讳安拉造在她们子宫之中的。她们的丈夫是立该在这期内收回他们的，若是他们意欲和睦的时候。 他们依照自所应负的得到享受。男子高出女子一等。安拉是优胜的，明哲的。","translator":"王静斋"},{"sura":2,"aya":229,"text":"休弃是两次，而后依常理留住，或善意地使去。你们所给了她们的，不可索回一物，除非他俩恐怕不能守安拉的定律时候。如果你们恐怕他俩不能守安拉的定律了，那女子用作赎身的，于他俩无罪。 这是安拉的定律，你们不得逾越它。越过安拉定律的人，均是背义的。","translator":"王静斋"},{"sura":2,"aya":230,"text":"如果他离弃她了。此后，非至她改嫁别的丈夫，则于他不相宜。若是他休弃了她，如果他俩认为能守安拉的律例了，在他二人不妨转圈。这是安拉的定律。他把它解明给有知识的一伙人。","translator":"王静斋"},{"sura":2,"aya":231,"text":"你们休了妻而后她们达到限期的时候，你们要依常理留下她们，或依常理使去。不得因为妨害而留下，为的是施行压迫。行这事的人，却自亏己身了。你们不要以安拉的表征当作可戏的。 你们当记念安拉对你们的恩典，暨他降给你们用以劝你们的经典和智慧。你们当敬畏安拉，并当知道安拉实是深悉万事的。","translator":"王静斋"},{"sura":2,"aya":232,"text":"你们休了妻，而后她们达到期限的时候，他们和后夫彼此循规情愿，这时候你们不得拘止她们嫁夫。这是用它劝你们之中归信安拉和末日的人。这是最使你们清白，最使清洁的。安拉知道。你们不知道。","translator":"王静斋"},{"sura":2,"aya":233,"text":"生母乳她们的婴儿完全的二年，限于那意欲完满乳期的人，父亲应照例供给她们吃穿。不得强人所难。不要因子妨害生母，也不要因子妨害生父。像这类事是继承人所应负的。如果他俩以彼此的情愿和协商意欲离乳，在他俩不为有罪。若是你们欲雇人哺乳自己的婴儿了，在你们欣然付给自所欲给的时候，于你们无罪。你们当敬畏安拉；当知道安拉看得见你们的作为。","translator":"王静斋"},{"sura":2,"aya":234,"text":"你们之中死去而遗下妻室的人，她们自应等待四个月零十日。当她们达到一定的期限时候，她们循规对自己所行的，于你们无罪。安拉尽知你们的作为。","translator":"王静斋"},{"sura":2,"aya":235,"text":"你们用作暗示娶妻或隐在心中的，于你们无罪。安拉知道你们即将向她们提出。但是你们不得和她们相约秘密；除非你们说合理的话。你们不要坚持婚姻的纽结，须至规定的期限 达到终了而后可。你们当悉安拉知道你们心中所怀的。你们要畏惧他，当知道安拉实是多恕的，仁厚的。","translator":"王静斋"},{"sura":2,"aya":236,"text":"如果你们尚未和妻接近，或是尚未为她们规定聘仪的时候，就把她们休弃了，那于你们无罪。 你们当送给她们一点物品。富的穷的，各量其所能，照例赠给一点物品；在为善的人上所应当的。","translator":"王静斋"},{"sura":2,"aya":237,"text":"如果你们在未与交合以前休弃她们了，彼时，你们已给她们规定了聘仪，则应给你们规定过的半数。除非她们蠲免，或是主婚人蠲免的时候。你们的蠲免是最近于公义的。你们莫忘掉彼此间的恩情，安拉实看得见你们的作为。","translator":"王静斋"},{"sura":2,"aya":238,"text":"你们当谨守拜功，（尤其是）至中的拜功。你们当为安拉顺命着站立。","translator":"王静斋"},{"sura":2,"aya":239,"text":"如果你们骇惧了 ，则步行或乘骑。你们得到平安的时候，就要记念安拉，依照他把你们所不知者教给你们的。","translator":"王静斋"},{"sura":2,"aya":240,"text":"你们之中死去而遗下妻室的人，当为其妻嘱咐享受到一年，不得逐出。若是她们外出了，她们对自身所行的光明事，于你们无罪。安拉是优胜的，明哲的。","translator":"王静斋"},{"sura":2,"aya":241,"text":"离异的女子可照例享受；在敬慎的人上所应当的。","translator":"王静斋"},{"sura":2,"aya":242,"text":"安拉如此为你们解明他的表征，好教你们了解。","translator":"王静斋"},{"sura":2,"aya":243,"text":"你岂未见那因为怕死出离自己居室的几千人吗? 安拉吩咐他们死去，复又使他们苏醒。安拉实是有恩于众人的，但是世人的大半不知感念。","translator":"王静斋"},{"sura":2,"aya":244,"text":"你们当在安拉的道上格杀，你们当知道安拉实是看见的，深知的。","translator":"王静斋"},{"sura":2,"aya":245,"text":"谁是善为贷予安拉的?果然，他就增加给他多倍。安拉掌收缩和开展。你们将被归返于他。","translator":"王静斋"},{"sura":2,"aya":246,"text":"你岂未见母撒以后以斯拉衣来子孙的一伙伟人吗?彼时，他们对自己的圣人说：（你为我们立一个王，我们在安拉的道上格杀。 ） 他说：（若是责成你们格杀了，你们就要不去格杀吧，）他们说： （我们怎会不在安拉的道上格杀呢?我们确被人逐出自己的住宅和子女了。）及至责成他们格杀的时候，他们便就退转了；只余他们其中的少数。安拉深知背义的人。","translator":"王静斋"},{"sura":2,"aya":247,"text":"他们的圣人对他们说：（安拉确为你们立大路特作王了。）他们说：（他怎可作我们的王呢?我们本是比他宜于作王的。他未蒙赐给宽裕的资财。）他说：（安拉确在你们上选择他了，他把在知识和体质上的宽展加增给他了。）安拉把自己权柄给他意欲（赐给）的人。安拉是宽恩的，深知的。","translator":"王静斋"},{"sura":2,"aya":248,"text":"他们的圣人对他们说：（他作王的预兆，是你们得到一个柜，内中有出自养主的（色克奈，）并有母撒家属和哈伦家属所遗下的余物，由众天使负荷着。其中确有对你们的表征。如果你们是归信的，……。","translator":"王静斋"},{"sura":2,"aya":249,"text":"当大路特率兵离去的时候，他就说：安拉用一道河试证你们；凡就着它饮水的，那不是我的人。未曾尝它的，便是我的人；除却那用手捧一捧的。 而后，他们就着那河饮水了，只余他们其中的少数。当他和随他归信的人渡过河去的时侯，他们说：（今日我们不能抵抗迦路特和他的军兵。）一切认为将看见安拉的人说：（很有少数的一伙，依安拉的旨意胜过多数的一伙。安拉是和忍耐的人同在的。）","translator":"王静斋"},{"sura":2,"aya":250,"text":"当他们向迦路特暨其军兵出战的时候，就说： （我们的养主啊！你倾给我们忍耐吧，你稳定我们的脚步吧！你相助我们抵抗一般逆民吧！）","translator":"王静斋"},{"sura":2,"aya":251,"text":"而后他们依着安拉的旨意战败逆徒了。达伍德杀了迦路特，安拉把作王和知识赐给达伍德了。他把自所意欲（教授）的教给他了。设不是安拉教世人彼此相抵禁，一定地面就坏了。但是安拉是有恩于世人的。","translator":"王静斋"},{"sura":2,"aya":252,"text":"这是安拉的表征，我据实把他读给你，你实属于被差的。","translator":"王静斋"},{"sura":2,"aya":253,"text":"这般使者我教他们的一部分优越一部分。他们之中有安拉与他交言的。他提升他们一部分的品级。我赐给马尔焉的子尔撒各项异迹，我以「洁灵」扶助他。设若安拉意欲……了，以后的人们，在种种明证达到以后不相格斗；但是他们起争议了，所以他们内中有信的，有不信的。设若安拉意欲（制止）了，他们不曾格斗。但是安拉行其所欲的。","translator":"王静斋"},{"sura":2,"aya":254,"text":"众归信的人哪！你们在无交易，无友谊，无告赦的那一日到来以前，就着我所供给的使用吧！不信的人均是背义的。","translator":"王静斋"},{"sura":2,"aya":255,"text":"安拉除他永生支撑的以外，再无有主。他不被打盹与睡眠所制。天地间所有的惟他执掌。除去奉他的命令，谁是在他御前告赦的?他知道他们前面与后面的。除去他所意欲（使知）的以外，他们不深悉他的一些知识。他的尊威包容了天地，保护它俩在他不觉疲乏。他是尊大的，玄高的。","translator":"王静斋"},{"sura":2,"aya":256,"text":"宗教无强迫。正道与迷路确已分明了。隐昧大欧特，归信安拉的人，他却抓住坚固无裂痕的把柄了。安拉是看见的，深知的。","translator":"王静斋"},{"sura":2,"aya":257,"text":"安拉是爱护众穆民的；他使他们从黑暗里出至光明。众逆徒的爱友是大欧特，使他们从光明里出至黑暗。这般人是永居火狱的。","translator":"王静斋"},{"sura":2,"aya":258,"text":"你岂未见有人依仗安拉赐给他权柄而因为养主与易卜拉欣起争辞吗?彼时，易卜拉欣说：（我的养主是那掌生死的主。）他说：（我，不掌生死。）易卜拉欣说：（安拉自东方发现太阳，你自西方发现它），由于是那隐昧的人惊疑了。安拉不引导背义的民众。","translator":"王静斋"},{"sura":2,"aya":259,"text":"或像那么一个人，它经过一个城池，已在屋背倒塌着；它说： （安拉在它消灭以后怎能繁荣它呢?）于是安拉使它死去百年，随后又使它苏醒了。他说：（你迟留几时了?）它说：（我迟留了一日，或是半日吧！）他说：（不然，你已停留百年了。你看自己的食饮，它尚未变色。你看自己的驴！我把你置作对于世人的表征。你再看骨骸，我怎样地并合它，而再给它穿上肉。）及至它已明白的时候，就说：（我知道安拉实是全能于万事的。）","translator":"王静斋"},{"sura":2,"aya":260,"text":"昔时，易卜拉欣说：（我的养主啊！你令我亲见你怎样复活已死的?）主说：（你未曾信吗?）他说：（是的，虽然只为我心宁定。）主说：（你取来四禽！把它们集至你跟前！你再把它们分置在各山上，你再唤它们；它们就赶忙来到你面前。你当知道安拉实是优胜的，明哲的。）","translator":"王静斋"},{"sura":2,"aya":261,"text":"把自己的资财用在安拉的道上那般人的情形，有如一粒生长七穗，每穗有百粒的情形。安拉加倍的增给自所意欲（增给）的人。安拉是宽恩的，深知的。","translator":"王静斋"},{"sura":2,"aya":262,"text":"把自己的资财用在安拉之道上，在使用后不复加以示惠，与侮辱的人，可在养主御前活动赏赐。在他们无所惧忧，","translator":"王静斋"},{"sura":2,"aya":263,"text":"温语与宽恕胜于含有侮辱的施济。安拉是无求的，仁厚的。","translator":"王静斋"},{"sura":2,"aya":264,"text":"众归信的人哪！你们不要以示惠侮辱破坏自己的施济；就像那为显弄给人，使用自己财物，并不信安拉与末日的人他的情形如同是上面有土的光滑石，大雨到来，而弃它为光硬的。他们不能作一事。安拉不引导背逆的民众。","translator":"王静斋"},{"sura":2,"aya":265,"text":"一般为讨安拉的喜，并以得自本心的坚定施用资财的人的情形，如同是在高阜的一座园林的情形。大雨到来，它结两倍果实。若大雨未到，则有露水。安拉看得见你们的作为。","translator":"王静斋"},{"sura":2,"aya":266,"text":"你们有人喜爱得到一座下有河流的枣子与葡萄园吗?其人在那里享受各样果实，老迈既至，在他有些无能的子孙；而后那园林遭遇含火的暴风，遂就被焚了。安拉如此为你们解明种种表征，好教你们参悟。","translator":"王静斋"},{"sura":2,"aya":267,"text":"众归信的人哪！你们就着自所赚来的美品，并就着我为你们由地上现出来的作使用吧！你们不要取那次等的作使用。你们除非是含混时，必不收取下等的。你们当知道安拉实是无求的，受赞的。","translator":"王静斋"},{"sura":2,"aya":268,"text":"恶魔以穷恫吓你们。他命你们以丑恶。安拉对你们许给自己的饶恕与特恩。安拉是宽恩的，深知的。","translator":"王静斋"},{"sura":2,"aya":269,"text":"他把智慧赐给自所意欲(赐给）的人。蒙赐智慧的人确已蒙赐多福了，惟独有才识的人接受教戒。","translator":"王静斋"},{"sura":2,"aya":270,"text":"你们所施舍的无论哪项财物，或是所举的无论哪项意念，安拉一定知道它。背义的人不得一相助的。","translator":"王静斋"},{"sura":2,"aya":271,"text":"若是你们公开出散济物，那是很好的了。如果你们暗中把它给穷人，那于你们更好了。他且给你们遮蔽一部分罪恶。安拉尽知你们的作为。","translator":"王静斋"},{"sura":2,"aya":272,"text":"你不是必得引导他们的，只是安拉引导自所意欲〔引导〕的人。你们所使用的资财有益你们自身。你们只可因为讨安拉的爱喜作使用。你们所使用的资财，将完全还给你们，并不亏损。","translator":"王静斋"},{"sura":2,"aya":273,"text":"应施给一般被困在安拉之道的穷人；他们不能在地上旅行， 不知道的人，以其廉洁而误认为他们是富有的。你以形迹认识他们，他们不纠缠地求人，你们使用的资财，安拉确是深知道它的。","translator":"王静斋"},{"sura":2,"aya":274,"text":"一般在夜昼暗着明着使用自己资财的人，可在其养主御前获得报酬，在他们无惧无忧。","translator":"王静斋"},{"sura":2,"aya":275,"text":"一般吃利息的人，将因疯狂站起，就像被恶魔摔倒的人站起。 这是由于他们声称：（贸易如同利息；）安拉许可贸易，严禁利息。奉到养主的劝化，遂就停止的人，可获其以往所得到的。他的事情付诸安拉。复反的人，是永居火狱的。","translator":"王静斋"},{"sura":2,"aya":276,"text":"安拉消灭利息，增长施济物。安拉不喜爱一切作恶忘恩的人。","translator":"王静斋"},{"sura":2,"aya":277,"text":"的确，一般归信，并作善举、立拜功、施天课的人，可在其养主御前获得报偿，在他们无惧无忧。","translator":"王静斋"},{"sura":2,"aya":278,"text":"众归信的人哪！如果你们是有正信的，你们就当敬畏安拉，放弃所余的利息。","translator":"王静斋"},{"sura":2,"aya":279,"text":"如果你们未实行，你们要晓得安拉并其使者的出征。若是你们悔罪了，你们就获得自己的原本。你们莫行亏，也莫受亏。","translator":"王静斋"},{"sura":2,"aya":280,"text":"若是遇到穷人了，则候至宽裕的时候；你们的施舍，是于你们最好的。若果你们知道了，……。","translator":"王静斋"},{"sura":2,"aya":281,"text":"你们当提防那么一日！你们在是日归返到安拉，随后每一个人完全得到所作的，并不受亏损。","translator":"王静斋"},{"sura":2,"aya":282,"text":"众归信的人哪！你们以定期的债务交易的时候，当要记下它。当令记帐者秉公记下，记帐人不要拒绝依照安拉所教的作记帐。当令他记帐；当令债户口述，当令他敬畏安拉、自己的养主，不得减少其中的一事。如果债户是个无知识的，或是无能力的，或是自己不能口述，当令他的被托人秉公口述。你们当觅同人中两个证人作见证。如果他两不是两个男子，则须你们所满意的证人中一男二女；以备他俩有一个忘记，而一个提醒给别一个。见证人遇人请求的时候，不要拒绝。你们不论巨细，要一一记帐，直到它的限期，不得厌恶。这是在安拉视为至公的，至能支持见证的，至近於你們不疑的。除非那是你們彼此交換的當面貿易；在你們無妨不記帳。你們交易的時候，要求人作見證。不要難為記帳人，也莫難為見證人。如果你們實作，那是你們的罪惡。你們當敬畏安拉。安拉教給你們。安拉是深知萬事的。","translator":"王静斋"},{"sura":2,"aya":283,"text":"如果你们是在旅行中，未遇到记帐人，只可是被取的质物。若是你们彼此相信了，当令债户付其债务。当令其敬畏安拉；自己的养主。你们不要隐匿见证。隐匿见证的人，却是其心作罪了。安拉深知你们的作为。","translator":"王静斋"},{"sura":2,"aya":284,"text":"天地间所有的惟安拉执掌。若是你们暴露心中所怀的，或是隐匿它，安拉将因为那事对你们清算。安拉随便饶恕人，随便惩治人。安拉是全能于万事的。","translator":"王静斋"},{"sura":2,"aya":285,"text":"使者信其养主所降给他的。众穆民亦然。统都信安拉，和他的天使，与他的经典，和他的列使。我们对于他的任何一个使者不作区别。他们说：（我们听到了，我们顺从了。〔盼望〕你的饶恕。我们的养主啊！归惟归于你。） 安拉不课人能力以外的。个人所作的于自己有益；其所作的于个人有害。","translator":"王静斋"},{"sura":2,"aya":286,"text":"我们的养主啊！若是我们忘记了，或是我们错误了，你莫处罚我们。我们的养主啊！你莫令我们担负重担，就像令我们以前的人担负重担似的。我们的养主啊！你莫教我们担负自不能担当的；你放赦我们吧，你饶恕我们吧，你慈悯我们吧！你是我们的爱护者。求你相助我们，抵抗一般逆民。","translator":"王静斋"},{"sura":3,"aya":1,"text":"阿里甫、俩模、密默。","translator":"王静斋"},{"sura":3,"aya":2,"text":"安拉，除他永生支撑的以外，再无有主。","translator":"王静斋"},{"sura":3,"aya":3,"text":"他因真理降给你经典，证实在它以前的。 从前曾降下讨忒喇、引支勒 指导世人，并降下福尔刚。","translator":"王静斋"},{"sura":3,"aya":4,"text":"不信安拉表征的人应遭烈刑·安拉是优胜的，掌报复的，","translator":"王静斋"},{"sura":3,"aya":5,"text":"地上天上实无一事可瞒安拉，","translator":"王静斋"},{"sura":3,"aya":6,"text":"他是那任便使你们在子宫里成形的主。除他优胜明哲的主以外，再无有受拜的。","translator":"王静斋"},{"sura":3,"aya":7,"text":"他是降给你经典的主。其中有坚固的文词。那是经的根本，另有需要解释的。内心偏斜的人为求扰乱并寻曲解起见，追从那含义不一的，知道解释它的只是安拉。一般在知识上有基础的人说：（我们信它了；统是出自养主的。） 惟独有才识的人受劝。","translator":"王静斋"},{"sura":3,"aya":8,"text":"我们的养主啊！你在指导我们以后，莫使我们心偏。你把出于自己的慈惠赏给我们吧！你实是厚施的。","translator":"王静斋"},{"sura":3,"aya":9,"text":"我们的养主啊！你实是在无疑的那一日，集合众人的。安拉绝不爽约。","translator":"王静斋"},{"sura":3,"aya":10,"text":"的确，不信的人，他们的资财和子女，绝不能代为避免安拉的一事。他们均是火狱的燃料。","translator":"王静斋"},{"sura":3,"aya":11,"text":"就像匪喇傲的党徒，及他们众前人的惯例；他们不信我的表征，而后安拉因他们的罪恶施以惩治了。安拉是掌烈刑的。","translator":"王静斋"},{"sura":3,"aya":12,"text":"你对一般不信的人说：（你们即将战败。你们且被集至火狱。铺垫太恶了！）","translator":"王静斋"},{"sura":3,"aya":13,"text":"两伙人遭遇，内中确含有对你们的一种表征；一伙在安拉的道上格杀；另一伙逆徒，众穆民亲眼看见他们是自己的两倍。安拉以自己的相助助他意欲〔相助〕的人。其中对于有见解的人确含着一种警惕。","translator":"王静斋"},{"sura":3,"aya":14,"text":"为世人装饰了爱欲——妇女、子嗣、贮藏的金银库、盖印子的马匹，家畜、田地，这是今生的亨受。在安拉御前有美好的归所。","translator":"王静斋"},{"sura":3,"aya":15,"text":"你说：（我把强于这个的告诉你们，可以吗？敬畏的人在养主御前可永获诸河流于其下的天园；并有清洁的女子和安拉的爱喜。安拉是看得见众仆的。）","translator":"王静斋"},{"sura":3,"aya":16,"text":"那般人均说：（我们的养主啊！我们实在归信了，求你饶恕我们的罪恶，求你保护我们脱离火刑。）","translator":"王静斋"},{"sura":3,"aya":17,"text":"一切忍耐的人，诚实的人，顺命的人施舍的人，和一切在黎明求恕的人很好了，","translator":"王静斋"},{"sura":3,"aya":18,"text":"安拉、众天使、及一切有知识的人秉公作见证；除他优胜明哲的主以外，再无有受拜的。","translator":"王静斋"},{"sura":3,"aya":19,"text":"的确，在安拉所重视的教门就是伊斯兰有经的人起争议，只在知识达到后，以他们自间的嫉忌。不信安拉表征的人，安拉实是速于清算的。","translator":"王静斋"},{"sura":3,"aya":20,"text":"如果他们对你起争辩了，你就说：（我和随从我的人绝对顺服安拉了。）你对一般有经的人和失教的人，說：（你们肯入伊斯兰吗?）如果他们归入伊斯兰他们必得正道。若是他们不肯了，你惟负责宣达罢了。安拉是看得见众仆的。","translator":"王静斋"},{"sura":3,"aya":21,"text":"的确，一般不信安拉的表征，背义地残杀诸圣，并残杀主持公道的人者，你当预告他们以痛刑！","translator":"王静斋"},{"sura":3,"aya":22,"text":"他们的功修 在今世后世失效了，无有相助他们的。","translator":"王静斋"},{"sura":3,"aya":23,"text":"你岂未看见那般蒙赐一部分经典的人吗？有人唤他们倾向安拉的经典，好叫他们在自己间用它判断；他们的一伙人复又拒绝。他们是惯于远避的。","translator":"王静斋"},{"sura":3,"aya":24,"text":"这 是因他们说：（火接触我们不过有数的几日。）他们被教中所造作的欺骗了。","translator":"王静斋"},{"sura":3,"aya":25,"text":"我把他们集在无疑的日子；对每一个人完全还给他们所作的，并不亏损，到那时候，是个什么情形呢?","translator":"王静斋"},{"sura":3,"aya":26,"text":"你说：（安拉！掌权的主啊！你任意赐人权柄、你任意夺人权柄，你任意把人抬高，你任意把人降低。好是在你的掌握。你实是全能于万事的。","translator":"王静斋"},{"sura":3,"aya":27,"text":"你入夜于昼，入昼于夜；你出生于死的，出死于生的；你无算地随意供人给养。）","translator":"王静斋"},{"sura":3,"aya":28,"text":"众穆民不要撇开穆民而结交不信者。行这事的人，对于安拉不属有为。除去你们对他们畏惧一种可怖的时候。安拉教你们畏惧他自己，归惟归于安拉。","translator":"王静斋"},{"sura":3,"aya":29,"text":"你说：（如果你们隐匿心中所怀的，或把它发布出来，安拉知道那个，他知道天地间所有的。安拉是全能于万事的。）","translator":"王静斋"},{"sura":3,"aya":30,"text":"当那一日，每一个人看见自己所行的善事是在当面；所行的恶事，他盼望自己和那恶事中间有遥远的距离。安拉教你们畏惧他自己。安拉是怜悯众仆的。","translator":"王静斋"},{"sura":3,"aya":31,"text":"你说：（如果你们喜爱安拉，你们就当追随我，安拉就喜爱你们，并饶恕你们的罪恶。安拉是多恕的，特慈的。）","translator":"王静斋"},{"sura":3,"aya":32,"text":"你说：（你们当顺从安拉和使者。）如果他们不肯了，安拉绝不喜爱背逆的人。","translator":"王静斋"},{"sura":3,"aya":33,"text":"的确，安拉在普世界上选择了阿丹、努海，易卜拉欣的家眷、尔母兰的家眷——","translator":"王静斋"},{"sura":3,"aya":34,"text":"一部分出自一部分的子孙。安拉是能听的，深知的。","translator":"王静斋"},{"sura":3,"aya":35,"text":"昔时，尔母兰的妇人说：（我的养主啊！我实为你许一产。我腹中的，被解放着；你应允我吧！你实是听得的，深知的。）","translator":"王静斋"},{"sura":3,"aya":36,"text":"及她产下一个女孩的时候，就说：（我的养主啊！我实产下了这一女孩）——安拉是至知她产下的——男和女不同。我给她命名为马尔焉。我祈你保佑她，和她的子孙，脱离被逐的恶魔。","translator":"王静斋"},{"sura":3,"aya":37,"text":"而后调养它的主以优美的接受悦纳她了；并教她生长，良好的生长。翟克赁亚顾照她了。翟克赁亚每逢由内殿来至她，他就看见她跟前有食品；他呼马尔焉说：（你由那里得到这个呢?）她说：（这是由安拉上来的；安拉实无算地任便供人给养。）","translator":"王静斋"},{"sura":3,"aya":38,"text":"当那时候，翟克赁亚呼求自己的养主，说：（我的养主啊！你亲自把一个廉洁的子嗣赏给我吧！你实是听得呼求的。）","translator":"王静斋"},{"sura":3,"aya":39,"text":"而后有天使呼唤他，当时他正在内殿站着礼拜：（安拉确以一个耶哈亚给你报喜信；证实安拉一语的，作领袖的，操守的，廉洁之类的一位圣人。）","translator":"王静斋"},{"sura":3,"aya":40,"text":"他说：（我的养主啊！我从哪里能获一个儿童呢?我已达至年迈，我的妇人是已经绝产的了）主说：（就是这样。安拉行所欲行。）","translator":"王静斋"},{"sura":3,"aya":41,"text":"他说：（我的养主啊！你观我一个预兆，）主说：（你的预兆是你除非示意外，三天不能和人接谈；）你当多记念自己的养主！你当朝夕称颂清净。","translator":"王静斋"},{"sura":3,"aya":42,"text":"昔时，众天使呼马尔焉说：（安拉确选择了你，使你清洁了。他在世界的女子上选择你了。","translator":"王静斋"},{"sura":3,"aya":43,"text":"马尔焉哪！你当顺从养主。你当叩头，你当和鞠躬的人们一同鞠躬，","translator":"王静斋"},{"sura":3,"aya":44,"text":"这是未曾听过的消息，我把它告谕你；当他们投签，谁照顾马尔焉的时候，你未在他们跟前；当他们互相争持的时候，你未在他们跟前。","translator":"王静斋"},{"sura":3,"aya":45,"text":"彼时，有天使呼马尔焉说：（的确，安拉以出自他的一句话给你报喜信。其名为埋希哈尔撒，马尔焉的子，在今世后世有地位的，属于接近一类的。","translator":"王静斋"},{"sura":3,"aya":46,"text":"他在摇蓝中并在成年时代对人叙话；且属于清廉一类的。）","translator":"王静斋"},{"sura":3,"aya":47,"text":"马尔焉说：（我的养主啊！未曾有人接近我，我从哪里有子呢?）主说：（就是这样。）安拉随便造化人。当他决定一事的时候，只对他说：（有，）它立刻就有。","translator":"王静斋"},{"sura":3,"aya":48,"text":"他教给他经典、智慧、讨喇忒、引支勒，","translator":"王静斋"},{"sura":3,"aya":49,"text":"被差至以斯拉衣来子孙的。我必把出自养主的奇迹现给你们；我将为你们用泥制造一个像鸟形的，我往它上吹气，它遂就依安拉的旨意成为一鸟。我依安拉的旨意治愈胎瞽，患大麻疯的；我能复活死人，我把你们吃的和你们家里所贮藏的历述给你们。 此中确含有对你们的表征。如果你们是信从的了，……","translator":"王静斋"},{"sura":3,"aya":50,"text":"、〔我来〕证实在我以前的讨喇忒，并为你们解除曾对你们严禁的一部分。我把出自养主的异迹现给你们。你们当敬畏安拉，当顺从我。","translator":"王静斋"},{"sura":3,"aya":51,"text":"的确，安拉是我的养主，亦是你们的养主。你们应当拜他。这是正道。","translator":"王静斋"},{"sura":3,"aya":52,"text":"当尔撒切知他们不信的时候，他就说：（谁是倾向安拉相助我的?）众门徒说：（我们是相助安拉的，我们归信安拉了。求你见证我们是顺服的。","translator":"王静斋"},{"sura":3,"aya":53,"text":"我们的养主啊！我们信你所降的了，我们追随这使者了。你把我们和一切证人共同记下吧！）","translator":"王静斋"},{"sura":3,"aya":54,"text":"他们施计谋；安拉报复计谋；安拉是最善于报复计谋的。","translator":"王静斋"},{"sura":3,"aya":55,"text":"彼时，安拉呼尔撒说：（我是必把你取去，教你升至我的；并使你清洁而远离不信的人们；更教那追随你的人超过逆徒们，直到复生日。遂后你们惟归返于我，我对你们所争议的从中判断。","translator":"王静斋"},{"sura":3,"aya":56,"text":"论到不信的人，我将在今世后世以烈刑惩治他们，在他们无一个相助的。","translator":"王静斋"},{"sura":3,"aya":57,"text":"论到归信并作善举的人，主将把他们的善赏完全还给他们。安拉不喜背义的人。","translator":"王静斋"},{"sura":3,"aya":58,"text":"这是我述给你的表征。和坚固的教戒。）","translator":"王静斋"},{"sura":3,"aya":59,"text":"的确，尔撒的情形在安拉视若阿丹的情形。主从土上造化了他，随后对它说：（有！）它立刻就有。","translator":"王静斋"},{"sura":3,"aya":60,"text":"真话出自你的养主，你莫怀疑。","translator":"王静斋"},{"sura":3,"aya":61,"text":"那在你得到知识以后对你起争论的人，你就说：（来呀！我们唤到彼此的子女、妇人，以及我们彼此个人。我们互相咒诅。我们求安拉把怒恼归到撒谎的人。）","translator":"王静斋"},{"sura":3,"aya":62,"text":"这是切实的叙述。除去安拉再无一受拜的。安拉确是优胜的，明哲的。","translator":"王静斋"},{"sura":3,"aya":63,"text":"如果他们不肯了，安拉深知造恶的人。","translator":"王静斋"},{"sura":3,"aya":64,"text":"你说：（有经的人哪！要你们归纳到彼此间一句共同的话，那就是我们只拜安拉，不举一事给他作匹偶，我们彼此不舍去安拉另举一主。）如果他们不肯了，你们就说：（要你们见证我们是顺服的。）","translator":"王静斋"},{"sura":3,"aya":65,"text":"有经的人哪！你们为什么因为易卜拉欣互起争论呢? 讨喇忒，引支勒只是在他以后被降的，你们怎不了解呢?","translator":"王静斋"},{"sura":3,"aya":66,"text":"咳！你们这般人为着自所知道的起争辩，而为什么对于自所不知道的也起争辩呢?安拉知道，你们不知道。","translator":"王静斋"},{"sura":3,"aya":67,"text":"易卜拉欣不是犹太教人，也不是基督教人，他只是一位守正的穆思林。他本不是举匹偶一类的。","translator":"王静斋"},{"sura":3,"aya":68,"text":"的确，人们最接近易卜拉欣的，是那追随他的人，与这个圣人，和众穆民安拉是爱护众穆民的。","translator":"王静斋"},{"sura":3,"aya":69,"text":"一部分有经的人希望诱你们迷误，他们只是自迷而不觉知。","translator":"王静斋"},{"sura":3,"aya":70,"text":"有经的人哪！你们既已作见证而为什么不信安拉的表征呢?","translator":"王静斋"},{"sura":3,"aya":71,"text":"有经的人哪！你们为什么明知着以假混真，隐匿真理呢?","translator":"王静斋"},{"sura":3,"aya":72,"text":"一部分有经的人说：（你们在早晨信那降给众穆民的，你们在晚间，就不要信：好教他们反回。","translator":"王静斋"},{"sura":3,"aya":73,"text":"你们仅可信那随从本教的人。）你说：（的确，引导是安拉的引导。（你们莫信）把类似给你们的，给予任何一个人；或是他们在养主那里对你们起争辩。）你说）特恩确在安拉的掌握。","translator":"王静斋"},{"sura":3,"aya":74,"text":"他任意把它赐给人。安拉是宽恩的，深知的，他随意特别赐给哪一个人。安拉是有大恩的。","translator":"王静斋"},{"sura":3,"aya":75,"text":"有经人之中，有的人你若把大宗财物寄托给他了，他终要把它付给你。他们之中有的人，你若把一枚钱寄托给他了，除非你时常守着他，他不付给你；这是由于他们说：（我们对于失教的人们不负什么罪过。）他们明知着，往安拉上说谎话。","translator":"王静斋"},{"sura":3,"aya":76,"text":"不然，那全满自己的约言，并且敬慎的人，安拉一定喜爱敬慎的人们。","translator":"王静斋"},{"sura":3,"aya":77,"text":"的确，一般以安拉的约，并以自己的发誓换取些微代价的人，他们在后世获不到一分。在复生日安拉不与交谈，不看他们，使他们清洁；他们应遭痛刑。","translator":"王静斋"},{"sura":3,"aya":78,"text":"的确，他们之中有一伙人，窜改业经写定的，好教你们认它为经典，那本不属于经典。他们自称那是出自安拉；它原非出自安拉。他们明知着。往安拉上说谎话。","translator":"王静斋"},{"sura":3,"aya":79,"text":"任何一个人，既蒙安拉赐给经典、智慧、圣职，就不可再对人说：（你们舍去安拉为我作奴吧！）只可（说：）你们由于教经，读经，是应该言行正直的。","translator":"王静斋"},{"sura":3,"aya":80,"text":"人也不可命你们以天使和列圣当养主。在你们是顺服的以后，他肯命你们不信吗?","translator":"王静斋"},{"sura":3,"aya":81,"text":"昔时，安拉取得列圣的约：（我赐给你们的经典和智慧，又有证实你们所守的一位使者 到来，你们必须信从他，扶助他）主说：（你们已承认并对这事取得我的约了吗?）他们说：（我们承认了。）主说：你们应作见证，我是随同你们作见证的。","translator":"王静斋"},{"sura":3,"aya":82,"text":"在这以后背逆的人，均是作恶的。","translator":"王静斋"},{"sura":3,"aya":83,"text":"他们觅安拉之教以外的吗? 天地里所有的惟顺服委曲着服从他；他们将被归返于他。","translator":"王静斋"},{"sura":3,"aya":84,"text":"你说，我们信安拉和降给我们的，与降给易卜拉欣、易司马仪、易司哈各、叶而孤白、及众子孙的；及由养主上赐给母撒、尔撒和列圣的。我们不在他们任何一个中间作区别。我们是惟独顺服他的。","translator":"王静斋"},{"sura":3,"aya":85,"text":"那觅除却伊斯兰教的人必得不到允纳，其人在后世归为伤折的。","translator":"王静斋"},{"sura":3,"aya":86,"text":"在归信并见证使者是真的，且在接奉种种明证以后，复又不信的那伙人，怎能得到安拉的领导呢? 安拉不引导逆民。","translator":"王静斋"},{"sura":3,"aya":87,"text":"这些人的报应，是永遭安拉和众天使和世人一概的怒恼；","translator":"王静斋"},{"sura":3,"aya":88,"text":"他们永不获减刑，且不获姑容，","translator":"王静斋"},{"sura":3,"aya":89,"text":"除非是那般在此以后悔罪自修的人。安拉是多恕的，特慈的。","translator":"王静斋"},{"sura":3,"aya":90,"text":"的确，一般归信复又不信，以后益发不信的人他们的悔罪必不获允纳。这般人均是迷误的。","translator":"王静斋"},{"sura":3,"aya":91,"text":"的确，一般不信且至死不信的人，纵然他们有人用充满地面的金子作赎资，亦绝获不到允纳；这般人应遭痛刑，无一相助他们的。","translator":"王静斋"},{"sura":3,"aya":92,"text":"你们非至使用自所喜爱的以后，必达不到真善，你们使用的无论何物，安拉是深知它的。","translator":"王静斋"},{"sura":3,"aya":93,"text":"各项食品统是为以斯拉衣来人的子孙列为合法的，除却以斯拉衣来 在讨喇忒颁降以前自忌的。","translator":"王静斋"},{"sura":3,"aya":94,"text":"你说：（如果你们所言属实，你们就取来讨喇忒读它。）在这以后往安拉上造作谎言的人，均是背义的。","translator":"王静斋"},{"sura":3,"aya":95,"text":"你说：（安拉说实言了。你们当追从端正的易卜拉欣之道；他本不是信匹偶的。）","translator":"王静斋"},{"sura":3,"aya":96,"text":"的确，为着世人设置的第一殿，就是那吉庆的并指导世人的颁克；","translator":"王静斋"},{"sura":3,"aya":97,"text":"其中有一切明迹……易卜拉欣的站处。进这殿的人可获平安。凡有川资与代步的人们都应当因为安拉去朝天房。不信的人，安拉确是无求众世界的。","translator":"王静斋"},{"sura":3,"aya":98,"text":"你说：（有经的人哪！你们为了什么不信安拉的表征?安拉见证你们的作为。）","translator":"王静斋"},{"sura":3,"aya":99,"text":"你说：（有经的人哪！你们为了什么自作见证着阻碍归信的人接近安拉之道，给它觅曲折。安拉必不忽于你们的作为。）","translator":"王静斋"},{"sura":3,"aya":100,"text":"众归信的人哪！若是你们顺从一部分有经的人了，他们就令你们在归信以后仍复不信。","translator":"王静斋"},{"sura":3,"aya":101,"text":"有人把安拉的表征读给你们。你们之中有他的使者，你们怎可不信呢?拉住安拉的人，却被引至正道了。","translator":"王静斋"},{"sura":3,"aya":102,"text":"众归信的人哪！你们当敬畏安拉，切实地敬畏他。你们当至死谨守伊斯兰。","translator":"王静斋"},{"sura":3,"aya":103,"text":"你们要一同拉住安拉的绳索，不得分伙；你们要记念安拉对你们的恩典。那时候，你们是互相仇视的，主在你们心中联合了。你们借着他的恩典结为弟兄。你们原是在火壕的边岸上，他自那里解救你们了。安拉如此为你们解明自己的表征，好教你们得正道。","translator":"王静斋"},{"sura":3,"aya":104,"text":"你们之中当有一般导人于善，命以合法的，禁止非理的，这类人均是得意的。","translator":"王静斋"},{"sura":3,"aya":105,"text":"你们不要像那一般在种种明证达到以后，分伙争议的人；","translator":"王静斋"},{"sura":3,"aya":106,"text":"他们在面白面黑的那一日遭受大刑。论到面黑的人，（将来有的对他们说：）这些人们在归信以后，复又不信了么?你们由于不信尝刑罚吧！","translator":"王静斋"},{"sura":3,"aya":107,"text":"论到面白的人。（他们）是永在安拉的慈惠中。","translator":"王静斋"},{"sura":3,"aya":108,"text":"这是安拉的表征，我把它据实读给你，安拉不欲亏负世人。","translator":"王静斋"},{"sura":3,"aya":109,"text":"天地间所有的惟归安拉执掌，诸事惟被归于安拉。","translator":"王静斋"},{"sura":3,"aya":110,"text":"你们是因为世人被选的至优的群众，你们命以合法的，禁止非理的，你们归信安拉。设若有经的人归信了，那实是于他们至好的。他们之中有些归信的，其大多数是作恶的。","translator":"王静斋"},{"sura":3,"aya":111,"text":"他们伤你们不过些微的伤害。他们若对你们格杀了，他们就掉过背去，不复得到相助。","translator":"王静斋"},{"sura":3,"aya":112,"text":"他们处在无拘哪里，便遭侮辱；除非拉住安拉的绳索和众人的绳索。他们终至遭受安拉的怒恨，并受困厄。这是由于他们不信安拉的表征，并背义地残杀诸圣；这是由于他们违逆和过当。","translator":"王静斋"},{"sura":3,"aya":113,"text":"他们本不相等。有经人之中有一伙端人；他们主夜间诵安拉的表征，并且叩头。","translator":"王静斋"},{"sura":3,"aya":114,"text":"他们信安拉和末日，命以合法的，禁止非理的，奔波种种善事。他们属于义人之类。","translator":"王静斋"},{"sura":3,"aya":115,"text":"他们行下的善事，绝不受湮没。安拉深知一切敬慎的人。","translator":"王静斋"},{"sura":3,"aya":116,"text":"的确，逆徒们的资财子女，不能代为避免安拉的一事。他们是永居火狱的。","translator":"王静斋"},{"sura":3,"aya":117,"text":"他们所使用在这今生的情形，有如寒风吹到一伙自亏己身的人的禾田，致被摧毁的情形。安拉未亏负他们，但是他们自亏己身了。","translator":"王静斋"},{"sura":3,"aya":118,"text":"众归信的人哪！你们不要于同人之外另结密友。他们尽力的对你们为恶，他们盼你们遭难，怒恨确己发诸他们的口了，他们胸中所怀的更是厉害。我为你们解明了各项表征；如果你们了解了……","translator":"王静斋"},{"sura":3,"aya":119,"text":"咳！你们这般人爱他们，他们不爱你们。你们信各项天经。他们遇见你们的时候，就说我们归信了。他们独自相处的时候，就由于恨，（你们）自咬指头——你说：（任你们愤死，）安拉实是深知胸中所怀的。","translator":"王静斋"},{"sura":3,"aya":120,"text":"若是你们受福了，那就贻他们忧愁；若是你们遭祸了，他们就因它快乐。若是你们忍耐，敬慎了，他们的计谋不能害你们一事。安拉尽悉他们的作为。","translator":"王静斋"},{"sura":3,"aya":121,"text":"彼时，你早晨由自己家里出去，而因为格斗把众穆民安置在各据点。安拉是听得的，深知的。","translator":"王静斋"},{"sura":3,"aya":122,"text":"彼时，你们两部分人似乎就要胆怯。——安拉是维护他们的——当令众穆民仰赖安拉。","translator":"王静斋"},{"sura":3,"aya":123,"text":"安拉确在百德里相助你们了。那时候，你们是单薄的。你们当敬畏安拉，冀使你们感念。","translator":"王静斋"},{"sura":3,"aya":124,"text":"彼时，你对众穆民说：（养主以遣下来的三千天使援助你们，这事还不使你们满足吗?）","translator":"王静斋"},{"sura":3,"aya":125,"text":"是的，若是你们忍耐、敬畏，他们立时来到你们面前，养主就以五千有标识的天使援助你们。","translator":"王静斋"},{"sura":3,"aya":126,"text":"安拉只是以它 作为对你们的喜信，并为你们的心藉它得到安静。相助惟出自优胜明哲的安拉。","translator":"王静斋"},{"sura":3,"aya":127,"text":"（他这样做）原为消灭一部分不信的人，或颠复他们而致归为失望的。","translator":"王静斋"},{"sura":3,"aya":128,"text":"这事于你毫不相干，或是允他们悔罪，或是惩治他们；因为他们是背议的人。","translator":"王静斋"},{"sura":3,"aya":129,"text":"在天的在地的惟安拉所掌。他任便恕饶人，他任便罪刑人。安拉是多恕的，特慈的。","translator":"王静斋"},{"sura":3,"aya":130,"text":"众归信的人哪！你们不要层层加倍地吃利息，你们当敬畏安拉，冀使你们得意。","translator":"王静斋"},{"sura":3,"aya":131,"text":"你们当堤防为不信之人备下的火。","translator":"王静斋"},{"sura":3,"aya":132,"text":"你们当顺从安拉和使者，冀使你们受怜恤。","translator":"王静斋"},{"sura":3,"aya":133,"text":"你们当趋向养主的饶恕，和那宽若天地，为着敬慎的人们备下的天园；","translator":"王静斋"},{"sura":3,"aya":134,"text":"那般在富有与困窘的时候施舍的，且是抑怒饶人的。安拉爱喜为善的人。","translator":"王静斋"},{"sura":3,"aya":135,"text":"那般人每做丑恶的事情，或自负己身的时候，便就记念安拉，并为自己的罪求恕——除去安拉谁能恕罪，他们未曾明知着而不改其行为。","translator":"王静斋"},{"sura":3,"aya":136,"text":"这些人的答报是养主的饶恕，与永居在诸河流于其下的天园。 一般功修者的报偿太好了！","translator":"王静斋"},{"sura":3,"aya":137,"text":"各种常例确在你们以前过去了。你们当在地上游行而观察一般不信者的结果如何?","translator":"王静斋"},{"sura":3,"aya":138,"text":"这是宣告给世人，并引导与劝化一般敬慎的人。","translator":"王静斋"},{"sura":3,"aya":139,"text":"你们不要松懈，不要忧虑。你们是至获胜利的。如果你们是归信的人了，……。","translator":"王静斋"},{"sura":3,"aya":140,"text":"若是你们遇到损害了，那一伙人确己同样地遇到损害了。这些时日，我把它轮流在人间，以便世人受劝，并为安拉藉以分别归信的，并从你们之中取得见证人。安拉不喜背义的人。","translator":"王静斋"},{"sura":3,"aya":141,"text":"安拉且藉此洁净归信的人，并消灭众逆徒。","translator":"王静斋"},{"sura":3,"aya":142,"text":"安拉尚未分别你们之中出战的，尚未分别一般忍耐的！你们就自以为将进天园吗?","translator":"王静斋"},{"sura":3,"aya":143,"text":"你们未进死亡以前原盼望一死。（现下）你们亲眼看见死了。","translator":"王静斋"},{"sura":3,"aya":144,"text":"穆罕默德只是一位使者，列使在他以前过世了。若是他死去或被杀了，你们能肯退转吗? 退转的人必妨碍不了安拉一事。安拉即将赏赐感念的人。","translator":"王静斋"},{"sura":3,"aya":145,"text":"人非依安拉的命令，必不会死。有限期的规定。欲得今世善赏的人，我就由这里给他；欲得后世善赏的人，我就在那世给他；我即将赏赐感念的人。","translator":"王静斋"},{"sura":3,"aya":146,"text":"许多圣人，有若干重修养的学者随他出征；他们未尝因为在安拉的道上遭难而表示松懈；他们未示弱，也未屈服。安拉爱喜忍耐的人。","translator":"王静斋"},{"sura":3,"aya":147,"text":"他们的话不外是说：（我们的养主啊！你饶恕我们的罪恶，和我们在事情上的妄为：你稳定我们的脚步！你相助我们抵抗一般逆民！）","translator":"王静斋"},{"sura":3,"aya":148,"text":"而后安拉赐给他们今世的赏赐，和后世的善赏。安拉喜爱为善的人。","translator":"王静斋"},{"sura":3,"aya":149,"text":"众归信的人哪！你们若顺从逆徒了，他们就使你们退转，你们终归为沦落的，","translator":"王静斋"},{"sura":3,"aya":150,"text":"不可，安拉是爱护你们的，他是最上的相助者。","translator":"王静斋"},{"sura":3,"aya":151,"text":"我即将把畏怯投在逆徒的心中，只因他们把未曾降给权力的。举为安拉的匹偶。他们的归处是火狱。背义者的居所太恶了！","translator":"王静斋"},{"sura":3,"aya":152,"text":"的确，安拉己对你们实现自己的约了。那时候，你们藉主的相助诛戮他们。待至你们胆怯，对事相争，在看见自所爱喜的以后违命的时候，……。你们之中有要今世的，有要后世的。终的，他使你们退转了，为的是试证你们。安拉确己放赦你们了。安拉是有恩于众穆民的。","translator":"王静斋"},{"sura":3,"aya":153,"text":"那时候，你们远扬，不顾一人，使者在后面唤你们。而后真主以双层的悲痛报复你们了；好教你们因着自所失掉的，和自所遭遇的致忧。安拉尽知你们的作为。","translator":"王静斋"},{"sura":3,"aya":154,"text":"在悲痛以后，主又降给你们安静，那遮护你们一部分人的假眠。另有一部分仅只忧虑自身的。他们对安拉作不正当的愚昧揣度，说：（这事于我们毫不相干。）你说：（事情统归安拉执掌。）他们心中暗怀着不对你发表的。他们说：（假使这事略于我们有关。我们不至于在那里被杀。）你说：（设使你们是在自己家里，那应该被杀的人必定去到就戮的地方。）安拉原为试证你们胸中所怀的，并涤净你们心中的。安拉深知胸中所怀的。","translator":"王静斋"},{"sura":3,"aya":155,"text":"的确，在两伙人相遇的那天，你们之中退转的人，就是恶魔以他们所做的一部分诱他们失足了。安拉确已饶恕他们。安拉实是多恕的，仁厚的。","translator":"王静斋"},{"sura":3,"aya":156,"text":"众归信的人哪！你们不要像逆徒似的，在弟兄们旅行或为战士的时候，指他们说：（设若他们和我们同处了，就不会死掉，也不致被杀，）终至安拉把这话置为你们心中的悔恨。安拉使生使死，安拉看得见你们的作为。","translator":"王静斋"},{"sura":3,"aya":157,"text":"若是你们被杀在安拉之道上，或是你们死掉了，一定安拉的饶恕和慈惠是优越他们所积下的。","translator":"王静斋"},{"sura":3,"aya":158,"text":"若是你们死掉了，或是被杀了，你们必被集至安拉。","translator":"王静斋"},{"sura":3,"aya":159,"text":"你借着安拉的慈惠对他们温和了。设使你是性暴的，心硬的了，他们必自你的四周散去。你要宽容他们为他们求恕；你要对于事情上和他们商议。如果你意志坚决了，你就仰赖安拉吧！安拉实喜爱仰赖的人。","translator":"王静斋"},{"sura":3,"aya":160,"text":"若是安拉相助你们了，就无有胜过你们的；他若抛弃你们了，在那以后，谁是相助你们的。当令众穆民仰赖安拉。","translator":"王静斋"},{"sura":3,"aya":161,"text":"圣人向不舞弊。 舞弊的人在复生日他那舞弊有害自身。终将各人所作的完全还给他，并不亏损。","translator":"王静斋"},{"sura":3,"aya":162,"text":"追随安拉的喜爱者，岂可像那遭安拉的怒恼而返的人呢?他的归所是火狱，归所太恶了！","translator":"王静斋"},{"sura":3,"aya":163,"text":"他们在安拉御前获有种种阶级。安拉看得见他们的作为。","translator":"王静斋"},{"sura":3,"aya":164,"text":"安拉确施惠于众穆民了。那时候，他在他们之中派遣了一个同类的使者，把主的表征读给他们，使他们清洁，并教给他们经典，和智慧。在这以前他们确是在显著的迷误中。","translator":"王静斋"},{"sura":3,"aya":165,"text":"你们遭遇时，确已得到同样的两倍了，你们肯说：（这是由哪里来的）吗?你说：（那是从你们自身上来的。安拉实是全能于万事的。","translator":"王静斋"},{"sura":3,"aya":166,"text":"在两伙人相遇的那天，你们所遭遇的是依安拉的判定，而致真主分别众穆民，并分别假信士。（这般假信士）有人对他们说：（你们来在安拉的道上格斗！或是作抵御。）他们就说：（设若我们知道格斗了，我们必定追随你们。","translator":"王静斋"},{"sura":3,"aya":167,"text":"在那一日，他们接近不信，较甚于接近归信。他们口说心里没有的，安拉至知他们所隐讳的。","translator":"王静斋"},{"sura":3,"aya":168,"text":"那般人退缩不前，且指他们弟兄说：（设若他们顺从我们了， 则不致被杀。）你说：（如果你们所言属实了，就应给自己避免一死。","translator":"王静斋"},{"sura":3,"aya":169,"text":"你不要以为在安拉之道上被杀的人们是死的，而是生存在养主御前，蒙赐给养的；","translator":"王静斋"},{"sura":3,"aya":170,"text":"藉安拉所赐的特恩欢笑着。他们对于后面尚未继续到来的人们表示喜悦；只因它们无惧无忧。","translator":"王静斋"},{"sura":3,"aya":171,"text":"他们满意安拉的惠泽和特恩；并满意安拉必不湮没众穆民的报偿。","translator":"王静斋"},{"sura":3,"aya":172,"text":"那般人在遭难以后，应承安拉与使者。他们之中为善，敬畏的人可获大赏。","translator":"王静斋"},{"sura":3,"aya":173,"text":"那般人，有的对他们说：（众人确为你们结集起来了，你们自当畏惧他们，）这话更增加给他们信仰了，他们说：（安拉是使我们满足的。管理的主太好了！）","translator":"王静斋"},{"sura":3,"aya":174,"text":"他们蒙安拉的惠泽和特恩着归返了，并未遭受痛苦。他们随从安拉的喜悦。安拉是有大恩的。","translator":"王静斋"},{"sura":3,"aya":175,"text":"这只是恶魔恫吓自己的众友。如果你们是归信的了，你们不要怕他们，当要怕我，","translator":"王静斋"},{"sura":3,"aya":176,"text":"你不要忧虑一般为着不信努力奔走的人。他们绝不能妨碍安拉一事，安拉不欲赐给他们后世的福分，他们应受大刑。","translator":"王静斋"},{"sura":3,"aya":177,"text":"的确，一般用正信换取隐昧的人，绝不能妨碍安拉一事。他们应受痛刑。","translator":"王静斋"},{"sura":3,"aya":178,"text":"不信的人莫以为我的姑容于他们有利，我的姑容只是为他们增加罪恶；他们应受辱刑。","translator":"王静斋"},{"sura":3,"aya":179,"text":"安拉绝不把众穆民弃在你们 所处的地位，终至教污浊的离开清洁的。安拉绝不容你们照见隐秘。但是安拉由其列使中自由选人。你们当归信安拉和他的列使。若是你们归信了，敬畏了，你们可获重赏。","translator":"王静斋"},{"sura":3,"aya":180,"text":"一般吝惜安拉所赐的特恩的人，切莫以为那于他们有利，而是于他们有害。即将在复生日把他们所吝惜的当作他们的项圈。天地的遗产惟归安拉所有。安拉尽知你们的作为。","translator":"王静斋"},{"sura":3,"aya":181,"text":"安拉确闻到一般自称：（安拉穷困，我们富有的人所言了。我就要写下他们所说的，及他们背义地杀害诸圣；我说：（你们尝焚刑吧！","translator":"王静斋"},{"sura":3,"aya":182,"text":"是由于你们先前亲手做下的，并由于安拉不是亏负众仆的。","translator":"王静斋"},{"sura":3,"aya":183,"text":"有一般人自称：（安拉确己与我们结约，非至使者现给我们被火吃去的祭品以后，必不信他。）你就说吧！（列使在我以前观给你们种种明证，和你们所说的那事；但是你们为何杀害他们呢?","translator":"王静斋"},{"sura":3,"aya":184,"text":"如果你们的所言属实了，……）如果他们不信你了……因为在你以前的列使确遭人不信服了。他们现过种种表征，箴言、和灿烂的经典。","translator":"王静斋"},{"sura":3,"aya":185,"text":"每个有性命的，是该尝试一死的。惟在复生日把你们的报酬完全赐给你们。离开火狱得进天园的人却已得意了。今生不外是欺人的什物。","translator":"王静斋"},{"sura":3,"aya":186,"text":"将必借着财物和生命试证你们。你们必由先前蒙赐经典的人上，并由多神教徒上听到许多毁损。若是你们忍耐了敬畏了，这实是意志坚绝的事情。","translator":"王静斋"},{"sura":3,"aya":187,"text":"昔时，安拉取得有经人的约：（你们该把天经解明给众人，不得隐匿它。）但是他们把它弃在背后，而用作换取些微的代价。他们换来的太恶了，","translator":"王静斋"},{"sura":3,"aya":188,"text":"一般爱喜自所发动的，并欲人以他们未曾做过的加以赞扬的人，你切莫以为他们是脱开罪刑的。他们应遭痛刑。","translator":"王静斋"},{"sura":3,"aya":189,"text":"天地的权柄惟安拉执掌，安拉是全能于万事的。","translator":"王静斋"},{"sura":3,"aya":190,"text":"的确，造化天地和夜昼的不同里，对于一般有识者确含有种种表征，","translator":"王静斋"},{"sura":3,"aya":191,"text":"那般人在站坐侧卧着记念安拉，并参悟天地的造化。（说：）我们的养主啊！你未从然造化这个。 颂你清洁，你庇佑我们脱离火刑吧！","translator":"王静斋"},{"sura":3,"aya":192,"text":"我们的养主啊！被你使入火狱的人实是你羞辱他了。背义的人不得一个相助的。","translator":"王静斋"},{"sura":3,"aya":193,"text":"我们的养主啊！我们实听得一个晓谕的往正信上晓谕、你们归信自己的养主吧——我们遂就归信了。我们的养主啊！你饶恕我们的罪恶吧，你遮蔽我们的过错吧，你教我们和义人同死。","translator":"王静斋"},{"sura":3,"aya":194,"text":"我们的养主啊！你把借着列使所许下的赐给我们吧，当复生日，你莫使我们现丑，你必不爽约。","translator":"王静斋"},{"sura":3,"aya":195,"text":"而后养主允承他们：我不湮没你们男女功修人的功修，你们的一部分类似一部分，一般迁居，被人逐出居宅，在我的道上遭受痛苦，与人格斗，遭受诛戮的，我必给他们遮蔽种种过错，我必令他们进入诸河流于其下的天园，出自安拉的报酬。安拉据有佳赏。","translator":"王静斋"},{"sura":3,"aya":196,"text":"不信的人们于各城里往来，在你可勿被其诱惑。","translator":"王静斋"},{"sura":3,"aya":197,"text":"些微的享受；终的，他们的归处是火狱，铺垫太恶了！","translator":"王静斋"},{"sura":3,"aya":198,"text":"只是敬畏养主的人，可永获诸河流于其下的天园。出自安拉御前的路餐。在安拉所据有的，于一般好人太美了，","translator":"王静斋"},{"sura":3,"aya":199,"text":"的确，有经人中有的信安拉和降给你们的，与降给他们的，在畏惧安拉着。他们不用安拉的表征换 些微的代价。这般人可在养主御煎获得自己的报偿。安拉实是清算神速的。","translator":"王静斋"},{"sura":3,"aya":200,"text":"归信的人哪！你们当坚忍、耐战，你们当准备战争，你们当敬畏安拉，冀使你们得意。","translator":"王静斋"},{"sura":4,"aya":1,"text":"众人哪！你们当敬畏养主。他由一个人上造化了你们。并由它上造化它的妻，并由他两个上繁衍了许多男女。你们当敬畏安拉，藉他相祈求的主。 (你们当避免断绝）血亲。安拉实是监察你们的。","translator":"王静斋"},{"sura":4,"aya":2,"text":"你们当退还孤儿的财产，不要用清洁的换取污浊的。不要把彼此的财产并在一处共食。那实是大罪。","translator":"王静斋"},{"sura":4,"aya":3,"text":"若是你们恐怕对于孤女不公了，你们就娶自所爱慕的女子；——两个两个的，三个三个的，四个四个的。若是你们恐怕不能公平待遇了，就(娶）一个，或是自己手辖的。是最近于你们不致失去公允的。","translator":"王静斋"},{"sura":4,"aya":4,"text":"你们要无代价的把聘仪赠给女子们。如果她们情愿放弃一部分了，你们就舒适愉快地吃它吧！","translator":"王静斋"},{"sura":4,"aya":5,"text":"安拉置为支持你们（生活）的资财，不得付给无知识的人，你们要就着那财产供给他们吃穿；并对他们说好话。","translator":"王静斋"},{"sura":4,"aya":6,"text":"你们要试验孤儿，直到他们达至婚期为止。若是你们看出他们智力充足了，当然要把财产还给他们；不要恐怕他们长大而浪费和忽忙地侵吞它。富有的人当有操守。穷困的人，可任其循例的食用。已可满足了。你们交付他们财产的时候，要叫人作见证。安拉作清算的已可满足了。","translator":"王静斋"},{"sura":4,"aya":7,"text":"男子可获父母和近戚们遗下的分额。女子可莸父母和近戚们遗下的分额，无论多少——规定的分额。","translator":"王静斋"},{"sura":4,"aya":8,"text":"有亲属关系者、孤儿、贫人，临分到场的时候，你们要赠给他们一些；并对他们说好话。","translator":"王静斋"},{"sura":4,"aya":9,"text":"有一般人，设若他们身后遗下弱嗣，则为它们骇怕，这类人，当令其（为孤儿们）恐惧，当令其敬畏安拉！当令他们说正言！","translator":"王静斋"},{"sura":4,"aya":10,"text":"的确，一般强吞孤儿财产的人，只是把火吃在腹里。他们即将入烈火。","translator":"王静斋"},{"sura":4,"aya":11,"text":"安拉为你们的子女嘱咐你们！一男获若二女的分额。她们若是两个以上的女子了，则获他遗下的三分之二。若是仅她一人了，则获半数。他的父母各获他遗下的六分之一，如果他有子嗣的时候。他若无有子嗣，他的父母继承他着，他母亲获得三分之一。如果他有弟兄，他母亲获得六分之一。这在用它作遗嘱或还付欠债以后。你们的父和子，你们不知哪是在利益上最近于你们的——由安拉规定的。安拉实是深知的，明哲的。","translator":"王静斋"},{"sura":4,"aya":12,"text":"你们获得妻室所遗的半数，若是她们无有子嗣的时候。如果她们有子嗣了，你们就获得她们所遗的四分之一，在她们用它作遗嘱或还欠以后。她们获得你们所遗的四分之一，若是你们无有子嗣的时候。如果你们有子嗣了，她们就获得你们所遗的八分之一；在你们用它作遗嘱或还欠以后。如果被继承的男子或女子，是一个上无父母，下无儿女的，在他有一弟（兄）或一姊（妹）他俩各获所遗的六分之一。如果他们是多过这个的，他们就于三分之一上共同。这在用它作遗嘱或还欠以后，不得有妨害。安拉的嘱告。安拉是深知的、仁厚的。","translator":"王静斋"},{"sura":4,"aya":13,"text":"这是安拉的定律。服从安拉和他的使者的人，真主教他进入诸河流于其下的天园，永久居在那里。这是大得意。","translator":"王静斋"},{"sura":4,"aya":14,"text":"那违逆安拉和他的使者，并越出他的定律者，真主将教他进火狱，永居在那里，遭受辱刑。","translator":"王静斋"},{"sura":4,"aya":15,"text":"你们的女子中发现丑行的人，当求你们同类的四个人作见证。若是他们作见证了，当把她们拘在家中，直到死期已至，或是安拉为她们现一个途径。","translator":"王静斋"},{"sura":4,"aya":16,"text":"你们之中两个发现这事的人则当毁他两个。如果他俩悔罪自修了，则当宽免。安拉实是允受悔罪的，特慈的。","translator":"王静斋"},{"sura":4,"aya":17,"text":"允受悔罪在安拉惟对于一般无知著作恶，不久复又悔罪的人。这般人可得安拉允其悔罪。安拉是深知的，公断的。","translator":"王静斋"},{"sura":4,"aya":18,"text":"有的人们作下种种罪恶，迨至临死的时侯，就说：（而今我实在悔罪了；）又有的人们不信着死去；这两项人的悔罪，均不受悦纳。我已为他们备下痛刑了。","translator":"王静斋"},{"sura":4,"aya":19,"text":"众归信的人哪！你们不可强勉继承女子。你们不要为着索回给出去的一部分拘制她们！除非她们发现显著的丑行的时候。你们当对她们和平相处。如果你们厌恶她们了，恐怕你们厌恶一事，安拉把多的好置在其中。","translator":"王静斋"},{"sura":4,"aya":20,"text":"如果你们欲去掉一妻，另换一妻了，其时你们已然把许多财物给了她们一个人，你们就不可向她们索回一物。你们肯用强迫和明显的罪恶索回它吗?","translator":"王静斋"},{"sura":4,"aya":21,"text":"你们彼此已经同房，她们已从你们上取得重约了，怎可索回它来呢?","translator":"王静斋"},{"sura":4,"aya":22,"text":"你们不要娶父亲娶过的女子，除却那以往的。那是可耻可怒的事，其道太恶了，","translator":"王静斋"},{"sura":4,"aya":23,"text":"对你们所严禁的是（娶）母辈、女儿、姊妹、姑母、姨母、蛭女、甥女、乳母，同乳的姊（妹），岳母，与出自你们同过房的妇人，在你们家中养育起来的女子，如果你们未和她们同过房，在你们不为有罪。和你们亲生子的妻室，和你们在姊妹间共聚；除却那以往的。安拉实是多恕的，特慈的。","translator":"王静斋"},{"sura":4,"aya":24,"text":"与一般有夫之妇，除却你们手辖的，安拉对你们决定的。除此以外的于你们为合法；那就是你们操守不涉奸淫着用财寻求。你们所借着她们得到享受的，当然要把已经规定的聘仪送给她们。规定后你们自相情愿的不为有罪。安拉实是深知的，明哲的。","translator":"王静斋"},{"sura":4,"aya":25,"text":"你们之中无财力能娶穆民良女的人，可娶你们手辖的穆民奴婢。安拉是至知你们的信仰的。你们的一部分出自一部分，你们娶她们，要得以其家主的许可。要照例给这般操守不涉奸淫，也不秘密结友的人聘仪。她们已然结了婚的时候，如果发现丑行了，则当受良女们所受的刑罚一半。这是指你们之中那畏惧痛苦的人。你们的坚忍是于你们至好的了。安拉是多恕的，特慈的。","translator":"王静斋"},{"sura":4,"aya":26,"text":"安拉欲表明给你们，并指给你们先人的道路，更允你们悔罪，安拉是深知的，明哲的。","translator":"王静斋"},{"sura":4,"aya":27,"text":"安拉欲允你们悔罪。随从欲望的人要你们偏而且大偏。","translator":"王静斋"},{"sura":4,"aya":28,"text":"安拉欲给你们减轻。人类原是在薄弱着被造的。","translator":"王静斋"},{"sura":4,"aya":29,"text":"众归信的人哪！除却彼此相情愿的商业外，你们不得妄吞同人的财物。你们不要自杀。安拉实是特慈你们的。","translator":"王静斋"},{"sura":4,"aya":30,"text":"那以仇视和背义肯行这事的人，不久的，我就教他进火狱。这是在安拉上容易的。","translator":"王静斋"},{"sura":4,"aya":31,"text":"若是你们远离所禁止的大罪了，我就遮蔽你们的过错。我教你们进高贵的地方。","translator":"王静斋"},{"sura":4,"aya":32,"text":"安拉用以使你们一部分优越一部分的，你们不要盼望。男子们可以其所行得到一分。女子们可以其所行得到一分。你们当向安拉讨其特恩，安拉实是深知万事的。","translator":"王静斋"},{"sura":4,"aya":33,"text":"我为每个人，安置了继承父母、近亲和盟友们所遗下的。安拉实是照临万事的。","translator":"王静斋"},{"sura":4,"aya":34,"text":"男子是管束女子的；借着安拉使他们的一部分优越一部分，并借着他们所使用的资财。廉节的女子是服从的。借着安拉的保佑操守忠贞的。你们恐怕她们反抗的那些女子，当然要规劝她们；不和她们同眠；责打她们。如果她们服从你们，那就不要对她们寻一途径了。安拉实是尊大的，玄高的。","translator":"王静斋"},{"sura":4,"aya":35,"text":"若是你们 恐怕他俩彼此决裂了，你们就由他的家属里，并由她的象属里，各举一个公正人；他俩若欲调解了，安拉就在他夫妇间融和。安拉实是深知的，尽知的。","translator":"王静斋"},{"sura":4,"aya":36,"text":"你们当拜安拉，不要给他妄拟一事为同伴。当对父母行孝，善待近亲，孤儿、贫人、近邻、远邻、旅客、同伴、男女奴仆。安拉不爱骄傲自矜的人！","translator":"王静斋"},{"sura":4,"aya":37,"text":"他们悭吝且命人悭吝，并瞒昧安拉所赐的特恩。我已为着昧恩的人们备下辱刑了。","translator":"王静斋"},{"sura":4,"aya":38,"text":"更有一般人，他们施舍财物，为的是炫耀于人；他们不信安拉，也不信末日。伴同恶魔的人，那伴侣太恶了，","translator":"王静斋"},{"sura":4,"aya":39,"text":"设若他们归信安拉和末日了，并施舍安拉所赐给的，那于他们有何妨碍呢?安拉是深知他们的。","translator":"王静斋"},{"sura":4,"aya":40,"text":"安拉绝不亏人一尘之重的。他那所作的事如果是善举，真主就加倍地赐给他，亲自赐给重赏。","translator":"王静斋"},{"sura":4,"aya":41,"text":"当我自每伙人中提出一个见证者，并召你见证这般人的时候，是个什么情形?","translator":"王静斋"},{"sura":4,"aya":42,"text":"在那一日，不信并违反使者的人，盼望教他们与地相平。他们不能对安拉隐瞒一句话。","translator":"王静斋"},{"sura":4,"aya":43,"text":"众归信的人哪！你们不得醉著作礼拜，待至知道自己所说的而后可。当失却大净的时候，非至作罢大净，也是不可的；除非是过路的。如果你们有病，或在旅途中，或是你们的一个人来自厕所，或是你们接触妇女而遇不到水，你们就奔向净土，而用它摸面与手。安拉实是赦免的，多恕的。","translator":"王静斋"},{"sura":4,"aya":44,"text":"你岂未见那蒙赐一分经典的人吗?他们换得迷误。更欲你们迷路。","translator":"王静斋"},{"sura":4,"aya":45,"text":"安拉至知你们的仇敌。安拉作爱护的已可满足了，安拉作相助的已可满足了。","translator":"王静斋"},{"sura":4,"aya":46,"text":"犹太人中有的人变更经句。口称：（我们业已听到了）（我们未服从）（你听不到地听吧！）并（说）拉尔哪！所因绕舌（骂人）并毁谤宗教。设使他们说：（我们听得了，我们顺从了，你听吧！你看顾我们吧！那确是于他们最好的，最正当的。但是安拉因他们的不信驱远他们了，所以他们只是略微的归信。","translator":"王静斋"},{"sura":4,"aya":47,"text":"有经的人们哪！我所颁降的是为证实你们所守的。 你们要得信它，勿待我涂抹面目，且把它转到脑后，或我驱远他们了，就如驱远守安息日的人似的。安拉的命令是必须实现的。","translator":"王静斋"},{"sura":4,"aya":48,"text":"安拉绝不饶恕给他举匹偶；除此以外的，他随便饶恕。给安拉举匹偶的人，他却造下大罪了。","translator":"王静斋"},{"sura":4,"aya":49,"text":"你岂未见一切自称洁净的人吗? 实是安拉随意使人清净。他们不受纤微的亏损。","translator":"王静斋"},{"sura":4,"aya":50,"text":"看哪！他们怎样地往安拉上造作谎言。这样显明的罪恶已竟足以了。","translator":"王静斋"},{"sura":4,"aya":51,"text":"你岂未见蒙赐一部分经典的人吗？他们信滞布特和大欧特。他们指着不信的人说：（这些人比众穆民得到正路，）","translator":"王静斋"},{"sura":4,"aya":52,"text":"他们是遭安拉驱远的。遭安拉驱远的人，你不见有相助他的。","translator":"王静斋"},{"sura":4,"aya":53,"text":"他们可握有一部分权柄吗?果然是那样，他们必不给人一丝；","translator":"王静斋"},{"sura":4,"aya":54,"text":"不然，是他们对于安拉把自己的特恩赐给别人，而生嫉忌吗? 我确把经典和智慧赐给易卜拉欣的子孙了，我赐给他们大权了。","translator":"王静斋"},{"sura":4,"aya":55,"text":"而后他们之中有信他的；有阻止的。燃着的火狱已可满足了，","translator":"王静斋"},{"sura":4,"aya":56,"text":"的确，一切不信我表征的人，不久我就教他们进火狱。每至他们的皮烤熟的时候，我就给他们换上另一层皮，好教他们尝刑罚。安拉实是优胜的，明哲的。","translator":"王静斋"},{"sura":4,"aya":57,"text":"众归信并行义举的人，我即将教他们永久进到诸河流于其下的天园；他们在那里获有清洁的配偶。我教他们入在延长的影下·","translator":"王静斋"},{"sura":4,"aya":58,"text":"的确，安拉命你们把寄托物付还原主，并命你们在人间判断的时候，要秉公判断。的确，安拉用以劝你们的事情很好了。安拉实是听得的，能见的。","translator":"王静斋"},{"sura":4,"aya":59,"text":"众归信的人哪！你们当顺从安拉，并顺从使者，和你们其中司命令的人。如果你们遇事相争了，你们就把它付诸安拉和使者：若是你们信安拉与末日了，……。这是至好的，结果至佳的。","translator":"王静斋"},{"sura":4,"aya":60,"text":"你岂未见自称归信降给你的，和在你以前降下的人们，欲诉诸大欧特；这时候已经命他们隐昧它，恶魔欲诱他们遥远地迷误。","translator":"王静斋"},{"sura":4,"aya":61,"text":"一旦有人告诉他们归到安拉所降的，并归到使者上，你就看见伪信士们躲避你，切实地躲避了。","translator":"王静斋"},{"sura":4,"aya":62,"text":"当他们以亲手作下的，遭逢患难，既而到你跟前指着安拉发誓：（我们只是意欲为善与调解，）这时候是个什么情形呢?","translator":"王静斋"},{"sura":4,"aya":63,"text":"这一般人，安拉知道他们心中所怀的。你当远避他们，你当劝化他们，你当把感动人的话。说到他们心里！","translator":"王静斋"},{"sura":4,"aya":64,"text":"我遣任何一个使者，只为人借着安拉的判定服从他。设若他们在自负己身的时候，到你跟前求安拉饶恕，使者也为他们求恕，他们必得到安拉是允从悔罪的。","translator":"王静斋"},{"sura":4,"aya":65,"text":"不是的，指着你的养主为誓：他们信你，必得是你先给他们解纷，心中对你所判决的不感觉窄狭，且服从绝对地服从。","translator":"王静斋"},{"sura":4,"aya":66,"text":"设若我责成他们杀身，或出离自己的住宅，他们只有少数人实行这事。设若他们施行所劝告他们的，一定那是于他们至好的，更使坚定的了。","translator":"王静斋"},{"sura":4,"aya":67,"text":"那时候，我必亲自赐给他们大赏，","translator":"王静斋"},{"sura":4,"aya":68,"text":"我必指示他们正道。","translator":"王静斋"},{"sura":4,"aya":69,"text":"顺从安拉和使者的人，全是和一般蒙安拉施恩的——列圣，诚恳的，殉教的，清廉人同在的。这些人作伴侣很好了！","translator":"王静斋"},{"sura":4,"aya":70,"text":"这是安拉的特恩，安拉为深知的已可满足了！","translator":"王静斋"},{"sura":4,"aya":71,"text":"众归信的人哪！你们当操起自己的兵器！你们当分组出战，或共同出战！","translator":"王静斋"},{"sura":4,"aya":72,"text":"的确，你们之中有迟缓的。若是你们遭难了，他就说：（安拉对我施恩了；那时候，我未和他们同在一处。）","translator":"王静斋"},{"sura":4,"aya":73,"text":"若是你们得到安拉特恩了，他必说：好像你们彼此间无友谊——（哎！宁愿我和他们处在一起而致得到大成功。）","translator":"王静斋"},{"sura":4,"aya":74,"text":"当令一切为着后世卖掉今生的人，在安拉的道上格斗。在安拉的道上格斗而至被杀，或战胜的人，不久我就赐给他大赏，","translator":"王静斋"},{"sura":4,"aya":75,"text":"你们为什么不在安拉的道上并（因着解救）一般无能为的男女儿童用武。那般人全说： （我们的养主啊！你教我们出了这居民不义的城池吧！你亲自为我们安置一个保护者，你亲自为我们安置一个相助者，）","translator":"王静斋"},{"sura":4,"aya":76,"text":"众穆民在安拉的道上格斗。众不信的人在魔道上格斗。 你们当与恶魔的爱友格斗，恶魔的诡计实是薄弱的。","translator":"王静斋"},{"sura":4,"aya":77,"text":"你岂未见那般人吗?有人告诉他们停手，并立拜功，施天课；及至责成他们格斗了，他们的一部分突然怕起人来，如同骇怕安拉，或者怕得更甚。他们说：（我们的养主啊！为什么你责成我们格斗呢?为什么你不容我们延至相近的期限呢?）你说：（今生的享受是些微的，后世是在敬畏的人上至好的；并不亏负你们一丝。","translator":"王静斋"},{"sura":4,"aya":78,"text":"你们处在不论什么地方，纵令住在高宫里，死必临到你们上。）便无有什么可畏惧的。无有畏惧，自然要视死如归了。他们若是享福了，就说：（这是从安拉上来的。）他们若是遭难了，就说：（这是由你上所起。）你说：（统是从安拉上来的。）这些人们几至不解一语，这是什么情形呢?","translator":"王静斋"},{"sura":4,"aya":79,"text":"你所得到的好，出自安拉，你所遭的歹，由你自身。我为众人遣你为使。安拉作见证的已可满足了。","translator":"王静斋"},{"sura":4,"aya":80,"text":"顺从使者的人，他确顺从安拉了。违背的人，我未差你保护他们。","translator":"王静斋"},{"sura":4,"aya":81,"text":"他们口称顺服；及至自你跟前出去了，他们的一部分人入夜谋划变更你所说的。安拉记录他们入夜所谋划的。你当远避他们，你当仰赖安拉！安拉作经管的已可满足了。","translator":"王静斋"},{"sura":4,"aya":82,"text":"他们怎不参悟古兰呢? 设若它不是出自安拉，他们必在内中见得多的相反。","translator":"王静斋"},{"sura":4,"aya":83,"text":"他们每逢得到一项平安或可骇的事情了，便就把它传播出去。设使他们把它付诸使者，并付诸他们其中司命令的人，那其中善于察究的人们必定知道它。设非安拉对你们的特恩和慈惠了，除少数人以外，你们必就追随恶魔了。","translator":"王静斋"},{"sura":4,"aya":84,"text":"你在安拉的道上格斗吧！负责任的惟你自身。你当催促众穆民。安拉将要制止众逆徒的战事了。安拉是在战争上最烈的，在惩戒上至严的。","translator":"王静斋"},{"sura":4,"aya":85,"text":"作善意介绍的人可因它获得一分。作恶意介绍的人，可因它获得一分。安拉是大能于万事的。","translator":"王静斋"},{"sura":4,"aya":86,"text":"有人以一种敬祝向你们致敬的时候，你们要用比它还好的作致敬，或是作同样的回答。安拉实是审算万事的。","translator":"王静斋"},{"sura":4,"aya":87,"text":"安拉，除他以外再无有主。他将把你们集至无疑的复生日。谁是比安拉更是言语诚实的。","translator":"王静斋"},{"sura":4,"aya":88,"text":"你们对一切伪信士，怎么分成两伙呢? 安拉因他们的作为颠覆他们了。你们欲引导安拉造成迷误的人吗?被安拉造成迷误的人，你绝不能为他得一个途径。","translator":"王静斋"},{"sura":4,"aya":89,"text":"他们希望你们不信，就像他们不信，而致彼此相等。非至他们在安拉的道上迁居后，你们不得认他们为友。如果他们不肯了，你们就捕获他们，随处杀死；不要拿他们当爱护的，相助的。","translator":"王静斋"},{"sura":4,"aya":90,"text":"另有一般人在外，他们集合到彼此间结过约的人，或是来至你们，其时以对你们格斗或对本族的人格斗心中感觉窄狭。设若安拉意欲……了，他必教他们压制你们而必对你们格斗。如果他们离开你们未与格斗，你们已接受和平，安拉就不给你们对他们的途径。","translator":"王静斋"},{"sura":4,"aya":91,"text":"你们即将遇到另一伙人，他们欲得你们保护，并得同族保护。每逢有人召他们作乱，他们就投到其间。如果他们，未离开你们，也未表示和平，也未停手，你们就随处捕杀他们。这般人，我给你们对他们显著的明证了。","translator":"王静斋"},{"sura":4,"aya":92,"text":"穆民不可杀穆民，除非错误着，误杀一穆民者，则解放一个穆民奴仆，和付给他家属的赎金；除非他们放弃的时候。如果他是你们仇敌同族的一个穆民，则解放一个有正信的奴仆。他若是和你们彼此间有约的同族，则付给他家属的赎金，并解放一个穆民奴仆。得不到的人，则接连封两个月的斋，安拉准许悔罪。安拉是深知的，明哲的。","translator":"王静斋"},{"sura":4,"aya":93,"text":"故意杀穆民的人，他的报应，是永居火狱。安拉怒恼他，驱远他。并为他备下大刑了。","translator":"王静斋"},{"sura":4,"aya":94,"text":"众归信的人哪！你们在安拉之道上旅行的时候，要得访查明白。不可在讨今生的利益着，对那表示服从的人说：（你们不是穆民。）安拉据有多的战利品。从前你们是这样。而后安拉对你们施恩了。你们须要访查明白。安拉尽知你们的作为。","translator":"王静斋"},{"sura":4,"aya":95,"text":"无有灾害 而故意退后的穆民，和一般以财命在安拉之道上奋斗的人不相等。安拉教一切以财产生命奋斗的人，在品级上超越一般退后的人。安拉许给每一个人福利了。安拉用大赏。","translator":"王静斋"},{"sura":4,"aya":96,"text":"就是出自他的一切品级，与饶恕和怜恤，使一般奋斗的人优越退后的人们。安拉是多恕的，特慈的。","translator":"王静斋"},{"sura":4,"aya":97,"text":"的确，一切正在自亏己身着被天使取去性命的人，天使们说：（你们原处在什么情况里?）他们就说：（我们原是在地面上无能为的。）天使们说：（安拉的地岂不是宽大的，任你们在地上迁移吗?）这些人的居处是火狱。归所太恶了！","translator":"王静斋"},{"sura":4,"aya":98,"text":"除非是一切（真正）无能为的男女儿童，他们不能设法，也得不到路子。","translator":"王静斋"},{"sura":4,"aya":99,"text":"这些人安拉即将放赦他们。安拉是宽宥的，多恕的。","translator":"王静斋"},{"sura":4,"aya":100,"text":"在安拉之道上迁居的人，可在地上得到多的居所和宽满。凡出离自己家室，迁赴安拉和他的使者，随后遇到丧亡的人，在安拉必定担负他的善赏。安拉是多恕的，特慈的。","translator":"王静斋"},{"sura":4,"aya":101,"text":"你们在地上旅行的时候，如果恐被逆徒们所害了，不妨缩短拜功。 众逆徒确是你们的明敌。","translator":"王静斋"},{"sura":4,"aya":102,"text":"你在他们里面率众礼拜的时候，要教他们的一伙人随你站起；当教他们操起兵器；当他们叩罢头的时候，可令他们站在你们后面。令那尚未礼拜的另一伙人，来随你礼拜；令他们操起自己的防械，和兵器。众逆徒盼望你们疏忽自己的兵器和行装，而扑你们一度猛扑。如果你们遇上雨灾或染病了，不妨放下自己的兵器。你们当操起自己的防械。安拉已为众逆徒备下辱刑了。","translator":"王静斋"},{"sura":4,"aya":103,"text":"你们礼罢拜的时候，要站着，坐着，侧卧着记念安拉。你们得到安定的时候，要得礼拜。的确，拜功是在众穆民上有一定时间的天命。","translator":"王静斋"},{"sura":4,"aya":104,"text":"你们搜索敌众，莫要放松。若是你们痛苦了，他们也像你们痛苦似的感觉痛苦，你们向安拉盼望他们不能盼望的。安拉是深知的，明哲的。","translator":"王静斋"},{"sura":4,"aya":105,"text":"的确，我因着真理把经典降给你，好教你依照安拉显给你的、于人中判断。你不要为背信的人分辩。","translator":"王静斋"},{"sura":4,"aya":106,"text":"你当求安拉饶恕，安拉实是多恕的，特慈的。","translator":"王静斋"},{"sura":4,"aya":107,"text":"你不可替那自欺的人分辩。安拉实不爱那欺骗，作罪的人。他们能瞒过人，不能瞒过安拉。他本是和他们同在的。","translator":"王静斋"},{"sura":4,"aya":108,"text":"那时候，他们夜间谋划不为真主所喜的话。安拉尽悉他们的作为。","translator":"王静斋"},{"sura":4,"aya":109,"text":"咳！你们在今生替这些人们争辩；谁在复生日替他们向安拉争辩?不然，谁是经管他们的?","translator":"王静斋"},{"sura":4,"aya":110,"text":"作恶事或自亏已身，随后求安拉饶恕的人，他就得到安拉是多恕的，特慈的。","translator":"王静斋"},{"sura":4,"aya":111,"text":"作罪的人，那只是害他自身作罪。安拉是深知的，明哲的。","translator":"王静斋"},{"sura":4,"aya":112,"text":"作下差错和罪恶，复又以它诬蔑好人者，他确担负说谎和明显的罪恶了。","translator":"王静斋"},{"sura":4,"aya":113,"text":"设非安拉对你的特恩与慈惠了，他们之中的一伙人必欲诱你错误。他们只是自误己身。他们妨害不了你一事。安拉降给你经典和智慧，并教给你原不知道的。安拉对你的特恩是大的。","translator":"王静斋"},{"sura":4,"aya":114,"text":"除却命人施济，或为善，或在人中调解的以外，他们的许多密谈里没有好处。为着讨安拉的喜，行这事的人，不久我就赐给他大赏。","translator":"王静斋"},{"sura":4,"aya":115,"text":"在明白正道以后，反抗使者，并随从不是穆民之道的人，我就教他趋向他所爱的。我就教他进火狱。归所太恶了！","translator":"王静斋"},{"sura":4,"aya":116,"text":"的确，安拉不饶恕给他举匹偶；他随便恕此以外的。给安拉举匹偶的人，确已深远地迷误了。","translator":"王静斋"},{"sura":4,"aya":117,"text":"他们舍开他只拜些女像。他们惟拜违命的恶魔，","translator":"王静斋"},{"sura":4,"aya":118,"text":"安拉驱远他了。恶魔说：（我必从你的众仆上提出规定的一分。","translator":"王静斋"},{"sura":4,"aya":119,"text":"我必诱他们迷误！我必令他们-妄想。我必命他们劐牲畜的耳。我必命他们变更安拉的造化）舍开安拉以恶魔为爱友的人，他确明明地伤折了。","translator":"王静斋"},{"sura":4,"aya":120,"text":"他许给他们，使他们妄想。恶魔许给他们只是欺骗。","translator":"王静斋"},{"sura":4,"aya":121,"text":"这些人的归所是火狱，不得从那里逃开。","translator":"王静斋"},{"sura":4,"aya":122,"text":"一切归信并作善举的人，我即将教他们永久进入诸河流于其下的天园，这是安拉的实约。谁是比安拉还要说话诚实的。","translator":"王静斋"},{"sura":4,"aya":123,"text":"事情不是依你们盼想的，也不是依有经人盼想的。作恶的人，就以那个还报他。那人舍开安拉得不到一个爱护的，相助的。","translator":"王静斋"},{"sura":4,"aya":124,"text":"作善举的男女穆民，均将进天园，并不蒙丝毫的亏损。","translator":"王静斋"},{"sura":4,"aya":125,"text":"谁是在宗教上较优于趋向安拉为善，并端正着追随易卜拉欣之道的? 安拉把易卜拉欣当为至友了。","translator":"王静斋"},{"sura":4,"aya":126,"text":"在天的在地的惟属安拉执掌。安拉是洞悉万事的。","translator":"王静斋"},{"sura":4,"aya":127,"text":"他们为着女子求你批判。你就说吧！（安拉为她们给你们作批判。天经上关于你们未给她们所规定的，且贪图娶他们那些孤女，与关于一切无能为的儿童，并关于你们为着孤儿主持公道所送给你们的亦然。你们行下的善，安拉是深知它的。）","translator":"王静斋"},{"sura":4,"aya":128,"text":"若有一个女人恐怕本夫执拗，或回避，不妨自行和睦。和睦是至好的。人类是受着悭吝支配的。若是你们为善，敬慎了，安拉尽悉你们的作为。","translator":"王静斋"},{"sura":4,"aya":129,"text":"你们在妻室之间，虽抱着企望，而绝不可能；但是你们不要过于偏爱，而把另一个弃若无着落的人。若是你们和平处置，并且敬慎了，安拉是多恕的，特慈的。","translator":"王静斋"},{"sura":4,"aya":130,"text":"若是他俩离异了，安拉就教各人借着自己的特长得到无求。安拉是宽恩的，明哲的。","translator":"王静斋"},{"sura":4,"aya":131,"text":"在天的在地的惟属安拉执掌。我确嘱告先你们蒙赐经典的人，和你们敬畏安拉。若果你们不信了，","translator":"王静斋"},{"sura":4,"aya":132,"text":"在天的在地的实属安拉执掌。安拉是无求的，受赞的。在天的在地的惟属安拉执掌。安拉作经管的已可满足了。","translator":"王静斋"},{"sura":4,"aya":133,"text":"众人哪！他若意欲（去掉）了，他就去掉你们，拿来另一伙。安拉是对此全能的。","translator":"王静斋"},{"sura":4,"aya":134,"text":"讨今世善赏的人，在安拉那里据有今世和后世的善赏。安拉是听得的，能见的。","translator":"王静斋"},{"sura":4,"aya":135,"text":"众归信的人哪！你们当为安拉作见证主持公道，纵令妨害自身，或父母，或近戚。不论他是富的穷的，安拉是最和他俩相宜的。你们不要随从偏见而丢升公道。如果你们屈舌，抑或迥避了，安拉尽知你们的作为。","translator":"王静斋"},{"sura":4,"aya":136,"text":"众归信的人哪！你们当信安拉，与他的使者；和降给自己使者的经典，和先前所降下的经典。隐昧安拉和他的天使，和他的各经典，和他的列使，以及末日的人，他确深远地迷误了。","translator":"王静斋"},{"sura":4,"aya":137,"text":"的确，一切归信，复又不信，然后归信，又复不信，而且益发不信的人，安拉绝不饶恕他们，也不指给他们一条途径。","translator":"王静斋"},{"sura":4,"aya":138,"text":"你报告伪信士们，他们必遭痛刑。","translator":"王静斋"},{"sura":4,"aya":139,"text":"那般人于众穆民之外另和逆徒们结友，他们可在那些人们跟前讨尊荣吗?尊荣统属安拉的。","translator":"王静斋"},{"sura":4,"aya":140,"text":"他在经典中确降给你们这话：（你们听到有人隐昧安拉的表征，并嘲笑它的时候，非至他们入到别的话以后，不要和他们同坐。果然是那样，你们就是和他们一样的。）安拉必把伪信士和众逆徒一同集在火狱中。","translator":"王静斋"},{"sura":4,"aya":141,"text":"那般（伪信士）等侯你们；如果你们得到安拉的相助，他们就说：（我们岂未和你们同在一起吗?）若是逆徒们获得一分了，他们就说：（我们未能优越你们，并替你们制止众穆民吗?安拉将在复生日从中公判。安拉绝不把对付众穆民的途径给不信的人们。","translator":"王静斋"},{"sura":4,"aya":142,"text":"伪信士们欺骗安拉。主是惩罚他们的欺骗的。他们站起礼拜的时候，懒怠站起。他们炫耀于人，他们记念安拉不过稍微的。","translator":"王静斋"},{"sura":4,"aya":143,"text":"徘徊在两者之间；不归这一伙，也不归那一伙。安拉造成迷误的人，你绝不能为他得一个途径。","translator":"王静斋"},{"sura":4,"aya":144,"text":"众归信的人哪！你们不要于众穆民之外，另把逆徒当作朋友。你们肯把不利于自己的明证给了安拉吗?","translator":"王静斋"},{"sura":4,"aya":145,"text":"的确，伪信士们是在火狱最低的一层。你得不到一个相助他们的。","translator":"王静斋"},{"sura":4,"aya":146,"text":"除非一切悔罪，自修，并抓住安拉，且因为真主虔守教门的。这些人是和众穆民同在的。不久的，安拉就赐给众穆民大赏。","translator":"王静斋"},{"sura":4,"aya":147,"text":"若是你们感念了，归信了，安拉以罪刑你们干什么?安拉是颁赏的，深知的。","translator":"王静斋"},{"sura":4,"aya":148,"text":"安拉不喜宣扬恶语，受迫害的人在外。安拉是能听的，深知的。","translator":"王静斋"},{"sura":4,"aya":149,"text":"若是你们公开为善，或暗中为善，或赦免罪恶了，安拉确是宽宥的，全能的。","translator":"王静斋"},{"sura":4,"aya":150,"text":"的确，一切不信安拉和他的列使，欲在安拉和他的使者中分离，且说：（我们信一部分，否信一部分）并欲从中取一途径，","translator":"王静斋"},{"sura":4,"aya":151,"text":"这般人均是真不信的。我为众逆徒备下辱刑了。","translator":"王静斋"},{"sura":4,"aya":152,"text":"一切归信安拉和他的使者，并未在他们一个中间作区别的人，不久的，他就赐给他们善赏。安拉是多恕的，特慈的。","translator":"王静斋"},{"sura":4,"aya":153,"text":"有经的人求你把经典从天上降给他们。他们向母撒求过比这个还要重大的，说：（你教我们明明地看见安拉，）它们遂就因着背义遭天火了。它们在接到种种明证以后，复又拜牛犊。而后我赦免那个了。我赐给母撒明显的权柄了，","translator":"王静斋"},{"sura":4,"aya":154,"text":"我因为和他结约把山起在上面。我告诉他们俯首入门；并告诉他们不要在安息日越范。我向他们取得重约了。（我罪刑他们）","translator":"王静斋"},{"sura":4,"aya":155,"text":"只因他们破约不信安拉的表征，背义地杀诸圣；与他们说：（我们的心是被遮的；）实是安拉因他们不信封闭其心了，所以他们只有少数人归信——","translator":"王静斋"},{"sura":4,"aya":156,"text":"并因他们的不信，和他们往马而焉上说出大的诬蔑，","translator":"王静斋"},{"sura":4,"aya":157,"text":"与他们说：（我们确杀死马而焉的子埋希哈•尔撒，安拉的使者了。他们原未杀死他，也未钉起他来；只是他们未看清楚。一切因他起争议的人，实是在对他怀疑中。他们知道它，不过是依循揣测。他们实在未曾杀死他；","translator":"王静斋"},{"sura":4,"aya":158,"text":"确是安拉教他升至自己了。安拉是优胜的，明哲的。","translator":"王静斋"},{"sura":4,"aya":159,"text":"凡属有经人，无一不在他死去以前信他。在复生日，他是见证他们的。","translator":"王静斋"},{"sura":4,"aya":160,"text":"只因犹太人背义，并因他们多方阻碍安拉的道，","translator":"王静斋"},{"sura":4,"aya":161,"text":"与他们的违禁吃利息，与他们的侵占人家的财物，我己严禁他们用那可用的美品。我为他们其中的不信者备下痛刑了。","translator":"王静斋"},{"sura":4,"aya":162,"text":"但是他们之中一般在知识上充足的和众穆民信从降给你的，和在你以先降下的。一切立拜功，散天课，信安拉和末日的人太好了！这一般，我即将赐给他们大赏。","translator":"王静斋"},{"sura":4,"aya":163,"text":"的确，我默谕你，就像默谕努海暨他以后的列圣。我己默谕易卜拉欣，易司马衣、易司哈各、叶而孤白、众子孙、尔撒、按忧伯，郁奴司、哈伦，苏来茫，我把翟布尔赐给达伍德。","translator":"王静斋"},{"sura":4,"aya":164,"text":"有些使者，从前我向你叙述了；有些使者，我未曾向你叙述。安拉曾对母撒交言了。","translator":"王静斋"},{"sura":4,"aya":165,"text":"并差遣了列使，报喜信，传警告着；免得世人在列使以后对安拉有所置辩。安拉是优胜的，明哲的。","translator":"王静斋"},{"sura":4,"aya":166,"text":"虽然，安拉见证所降给你的。他依自己的知识下降了它。众天使皆作见证着。安拉作见证己可满足了。","translator":"王静斋"},{"sura":4,"aya":167,"text":"的确，隐昧 并阻碍安拉之道的人，确己深远地迷误了。","translator":"王静斋"},{"sura":4,"aya":168,"text":"隐昧并且背义的人，安拉绝不饶恕他们，也不指示他们一条途径；","translator":"王静斋"},{"sura":4,"aya":169,"text":"只有火狱的途径。他们永久居住其间，这是在安拉上容易的。","translator":"王静斋"},{"sura":4,"aya":170,"text":"众人哪！使者自养主那里确把真理现给你们了，你们好好地归信吧！若是你们不信了，天地间所有的惟归安拉执掌。安拉本是深知的，明哲的。","translator":"王静斋"},{"sura":4,"aya":171,"text":"有经的人哪！你们对于宗教上不要过分，你们只应往安拉上说真的。马而焉的子埋希哈•尔撒只是安拉的使者，是他投给马而焉的一语，属于他的灵魂。你们当归信安拉和他的列使。你们不要称三位，当要停止，〔应说〕于你们至好的。惟安拉是独一受拜的。真主清净，不求有子，天地间的一切惟他执掌。安拉作维护的己可满足了。","translator":"王静斋"},{"sura":4,"aya":172,"text":"埋希哈绝不耻于给安拉作奴。众接近〔真主〕的天使亦不耻于给他作奴。而且顽强的人，真主就要把他们一同集结至自己。","translator":"王静斋"},{"sura":4,"aya":173,"text":"论到归信并作善举的人，它将完全赐给他们报酬。并增给自己的特恩。论到羞渐，顽强的人，真主将惩治他们痛刑。他们舍去安拉得不到一爱护的，也无一相助的。","translator":"王静斋"},{"sura":4,"aya":174,"text":"众人哪！出自养主的明证确己达至你们了，我己降给你们明光了。","translator":"王静斋"},{"sura":4,"aya":175,"text":"论到归信安拉并拉住他的人，他即将教他们入到自己的慈惠和特恩中。他且向他们指示达至他的正道。","translator":"王静斋"},{"sura":4,"aya":176,"text":"他们求你批判，你就说，安拉为着孤单的人给你们作批判。若有一个人死了，无儿子，仅有一姊（妹），她可获其人所遗下的半数。 若是她无儿子了，他就继承她。她俩若（只），是姊妹二人了，这二人继承他所遗下的三分之二。他们若是些男女弟兄了，一男可获若二女的分额。安拉解明给你们，以免你们忽略。安拉是深知万事的。","translator":"王静斋"},{"sura":5,"aya":1,"text":"众归信的人哪！你们应当全约，四足的家畜是为你们列为合法的；除非那所诵给你们的。你们既己受戒时，勿以行猎为合法，安拉随意判定。","translator":"王静斋"},{"sura":5,"aya":2,"text":"众归信的人哪：你们勿以妨碍安拉的（征迹，）禁月，祭物，和挂项饰的为合法；也勿以妨碍在讨养主的特恩和喜爱着奔赴禁殿的人为合法。你们开了戒的时候，可去行猎。恼恨一伙人因其阻止你们入禁寺，可勿贻你们趋至过当。你们要在真善和敬慎上相扶助；不得在罪恶和悖谬上相扶助。你们当敬畏安拉。安拉实是掌烈刑的。","translator":"王静斋"},{"sura":5,"aya":3,"text":"对你们列为禁品的，是自死的，血，猪肉，未经朗诵安拉的尊名宰死的。以及掐死的，打死的，摔死的，碰死的，毒兽吃剩下的，你们所宰割的不在禁例，在石上被宰的与你们用签子分的。这是背谬。今日，不信的人们不复希望破坏你们的教门了。你们不要怕他们，当要怕我。今日！我已为你们完成你们的教门，我已对你们全满我的恩典了。我为你们喜爱伊斯兰教了。在饥饿的时候，非偏于作罪，迫不得已的人，安拉实是多恕","translator":"王静斋"},{"sura":5,"aya":4,"text":"他们问你：为他们列为合法的是什么?你说：（己为你们列种种美品为合法的。你们教猎的。人所训练的猎物，你们用安拉教给的来训练它们，它们给你们保留的，你们就可吃它。你们当在那上记念安拉的尊名。你们当敬畏安拉。安拉实是清算神速的。）","translator":"王静斋"},{"sura":5,"aya":5,"text":"今日，已为你们列种种美品为合法的。有经人的食品是于你们合法的；你们的食品是于他们合法的。再有一切操守的穆民女子，和属于先你们有经人的操守女子，当你们正式结婚不涉奸淫也不秘密结友着，给她们聘仪的时候，（也是于你们合法的。）拒绝正信的人，他的善功无效了。他在后世是属于伤折的。","translator":"王静斋"},{"sura":5,"aya":6,"text":"众归信的人哪！你们站起，礼拜的时候，要洗面与手，直到胳肘；要摸头，洗脚，直到两踝骨。若是你们身染重污了，则当洗全身。你们若是染病的，或在旅途中，或是你们有人采自厕所，或是你们接近了妇女而未得到水，你们就奔向净土，用它摸面与手。安拉不欲给你们加上一种困难；他只欲使你们清洁，并完成对你们的恩典，好教你们感念。","translator":"王静斋"},{"sura":5,"aya":7,"text":"你们当记念安拉对你们的恩典，和他对你们所结的约，那时候；你们说：（我们闻而顺从了。）你们当敬畏安拉。安拉深知胸中听怀的。","translator":"王静斋"},{"sura":5,"aya":8,"text":"众归信的人哪！你们当为安拉主持（正义），秉公作见证。不要被恼恨一伙人贻你们趋至不公。你们要行公道，公道是最近于义的。你们当敬畏安拉。安拉尽知你们的作为。","translator":"王静斋"},{"sura":5,"aya":9,"text":"安拉约许众归信，并作善举的人；他们必获饶恕和大赏，","translator":"王静斋"},{"sura":5,"aya":10,"text":"隐昧，且不信我表征的人是居火狱的。","translator":"王静斋"},{"sura":5,"aya":11,"text":"众归信的人哪！你们当记念安拉对你们的恩典。那时候，有一伙人欲向你们伸手，而真主替你们制止他们的手了，你们当敬畏安拉。当令众穆民仰赖安拉！","translator":"王静斋"},{"sura":5,"aya":12,"text":"安拉确取得以斯拉衣来子孙的约了。我由他们之中派定十二个巡察。安拉说：（我实是偕同你们的。）若是你们立拜功，施天课，归信我的列使，援助他们，并好自贷予安拉了，我必给你们遮蔽种种罪恶，我必教你们进入诸河流于其下的天园。你们之中在这以后隐昧的人，他确失迷正路了。","translator":"王静斋"},{"sura":5,"aya":13,"text":"只因他们的破约，我驱远他们了，我使他们心硬了。他们窜改经句，忘掉了所规劝他们的一部分。除却他们少数人以外，你时常发现他们的一种背信。你要宽宥他们！你要容让，安拉实喜爱为善的人。","translator":"王静斋"},{"sura":5,"aya":14,"text":"我从一般自称为基督徒的人上取得约会了，但是他们忘掉所用以劝他们的一分。而后我使仇视与怒恨胶着在他们之间，直到复生日。不久的，安拉就向他们宣告他们所作的。","translator":"王静斋"},{"sura":5,"aya":15,"text":"有经的人哪！我的使者来对你们解明经上所隐匿的许多，他且放弃许多。光辉和灿烂的经典，确从安拉上达至你们了。","translator":"王静斋"},{"sura":5,"aya":16,"text":"安拉用它引导那从他所欲的人于平安之道；并依自己的旨意教他们由黑暗出至光明，且把他们领至正道。","translator":"王静斋"},{"sura":5,"aya":17,"text":"声称安拉就是马而焉的子埋希哈的人确切背逆了。你说：（若是安拉欲毁灭埋希哈、马而焉母子，和地上所有的一切，谁能掌他的一事? ）天地万物的权柄惟安拉执掌，他随便造化。安拉是全能于万事的。","translator":"王静斋"},{"sura":5,"aya":18,"text":"犹太人基督徒说：（我们是安拉的儿子，且是他的爱友。）你说：（然而他怎么又因你们的罪恶惩治你们呢?）不然，你们是他造化的人类。他恕其意欲（恕过）的人，惩其意欲（罪刑）的人。天地万物的权柄惟安拉执掌，归惟归至他。","translator":"王静斋"},{"sura":5,"aya":19,"text":"有经的人哪！我的使者确在列使间断的时代来对你们讲解，免得你们说：（未曾有一报喜信传警告的人来至我们。）报喜信传警告的人确己来至你们了。安拉是全能于万事的。","translator":"王静斋"},{"sura":5,"aya":20,"text":"那时侯，母撒对自己的族人说：（我的族人哪！你们当记念安拉对你们的恩典；那时候，他在你们之。中发现了列圣，他教你们作王，他把未给过世界一人的赐给你们了。","translator":"王静斋"},{"sura":5,"aya":21,"text":"我的族人哪！你们进安拉为你们规定的这个圣地！你们不要退转，而致归为伤折的。）","translator":"王静斋"},{"sura":5,"aya":22,"text":"他们呼母撒说：（在那里有一伙子暴徒，他们不先退出，我们绝不进去。如果他们从那里出去了，我们一定进去。）","translator":"王静斋"},{"sura":5,"aya":23,"text":"有两个蒙安拉施恩的怕主的人说：（你们冲他们入门吧！如果你们进到那里，你们一定获胜利。你们若是归信的，你们就当仰赖安拉。）","translator":"王静斋"},{"sura":5,"aya":24,"text":"他们呼母撒说：（它们在那里一日不走，我们一定永不进去。你和你的养主前去格斗吧！我们是坐在这里的。）","translator":"王静斋"},{"sura":5,"aya":25,"text":"母撒说：（我的养主啊！我只能管我自身和我弟兄；求你教我们和作恶的群众彼此分离吧！）","translator":"王静斋"},{"sura":5,"aya":26,"text":"主说：（那地方应禁固四十年，不准他们进去，他们在地上漂泊着。你对于恶民不要悲伤。）","translator":"王静斋"},{"sura":5,"aya":27,"text":"你把阿丹二子的消息据实诵给他们，那时候，他两个宰牲献祭。安拉悦纳这一个的；未悦纳那一个的。它说：（我必杀你。）他说：（安拉只是悦纳虔恪之人的。","translator":"王静斋"},{"sura":5,"aya":28,"text":"如果你必伸手杀我了，我绝不伸手杀你；我实畏惧安拉—— 调养众世界界的主。","translator":"王静斋"},{"sura":5,"aya":29,"text":"我实欲你带去我的罪，和你的罪，你就归为居火狱的。）这是还报背义的人。","translator":"王静斋"},{"sura":5,"aya":30,"text":"而后，它心里认为杀自己的弟兄，不是一件难事。它就把他杀死。遂就归为伤折的人了。","translator":"王静斋"},{"sura":5,"aya":31,"text":"而后安拉遣一只乌鸦掘地，为的是现给它：怎样地掩埋它弟兄的尸体。它说：（哎悲苦啊！我怎么就不如这个乌鸦似的，掩葬我弟兄的尸体，而致归为懊悔的人呢?）","translator":"王静斋"},{"sura":5,"aya":32,"text":"只因为这事，我对以斯拉衣来的子孙规定：非为抵命，或非因在地上发现罪恶而杀人的，那如同是杀死全人类了；救活一人的，如同是救活全人类了。我的列使确把种种异迹现给他们了。但是他们许多人复又在地上妄为。","translator":"王静斋"},{"sura":5,"aya":33,"text":"一般对安拉和他的使者作战，并在地上努力破坏的人，他们的报应只是被杀，或被挂，或被相异着断去手脚，或被逐出该地。这是他们在今世受辱，他们在后世应受大刑。","translator":"王静斋"},{"sura":5,"aya":34,"text":"除非一切在你们未对他们得手以前悔罪的人。你们当知道安拉是多恕的，特慈的。","translator":"王静斋"},{"sura":5,"aya":35,"text":"众归信的人哪！你们当敬畏安拉，求接近他，并在他的道上奋斗，冀使你们得意。","translator":"王静斋"},{"sura":5,"aya":36,"text":"设使地上所有的一切，并有类是的一倍归为不信的人执掌，而用他赎复生日的刑罚，在他们必得不到允纳，且受痛刑。","translator":"王静斋"},{"sura":5,"aya":37,"text":"他们欲出离火狱，而绝出不去那里，自应遭受长久的罪刑。","translator":"王静斋"},{"sura":5,"aya":38,"text":"男女窃贼，你们要割他俩的手，以惩其所行。这是出自女拉的儆诫。安拉是优胜的，明哲的。","translator":"王静斋"},{"sura":5,"aya":39,"text":"那在背义以后悔罪自修的人，安拉必允他的悔罪。安拉实是多恕的，特慈的·","translator":"王静斋"},{"sura":5,"aya":40,"text":"你岂不知安拉掌天地的权柄吗?他惩其意欲（罪刑）的人，恕其意欲（饶恕）的人。安拉是全能于万事的。","translator":"王静斋"},{"sura":5,"aya":41,"text":"使者呀！不要因为一般尽力隐昧口称归信，而心未归信的、人有所悲伤。犹太人中有一般好听谎言的，为着未到你这里来的另一伙人听话的，他们窜改经句，说：（若给你们这样判断了，你们就接受；若不给你们这样判断了，你们可要防备。）安拉意欲磨难的人，你不能为他掌安拉的一事；他们是安拉不欲洁净其心的人。他们在今世受辱；在后世且遭大刑。","translator":"王静斋"},{"sura":5,"aya":42,"text":"是些好听谎言，好吃非法所得的。如果他们来到你这里，你就在他们中间判断，或是远避他们·你若远避他们，他们绝对不能妨你一事；你若判断了，你麓在他们中间秉公判断，安拉实喜爱主持公道的人，","translator":"王静斋"},{"sura":5,"aya":43,"text":"在他们那里本有记载安拉律法的讨喇忒，他们怎可求你判断，而又不服呢?这般人不是真信者。","translator":"王静斋"},{"sura":5,"aya":44,"text":"我确降下了讨喇忒，内含有引导和光辉。顺命的列圣为着犹太人们依它作判断。一般有修养的学者，和众博士，依照寄托给他们的天经作判断，他们本是见证它的，你们不要怕人，当要怕我。你们不要以我的表征换取些微的代价。那未依着安拉所降的作判断者，统是不信的人。","translator":"王静斋"},{"sura":5,"aya":45,"text":"我在讨喇忒上为他们规定：（命抵命，眼抵眼，鼻抵鼻，耳抵耳，牙抵牙，种种伤害是有赔偿的。弃而不究的人，那是赎自己的罪恶；那未依安拉降下的作判断者，均是背义的人。）","translator":"王静斋"},{"sura":5,"aya":46,"text":"我在他们以后续差了马而焉的子尔撒，证实他以前的讨喇忒着。我把引支勒赐给他了；内含有引导与光辉；证实在他以前的讨喇忒着，并引导与劝化敬慎的人。","translator":"王静斋"},{"sura":5,"aya":47,"text":"我曾说过：）（让有引支勒的人，依照安拉在它里面所降的作判断吧！）那未依安拉降下的作判断者，均是为恶的人。","translator":"王静斋"},{"sura":5,"aya":48,"text":"我因为真理降给你经典，证实在它以前的经典，并保障它。你就依照安拉所降的从中判断吧！你莫离开那达至你的真理，而追从他们的私见。我为你们之中的每伙人指定一种教律与明路。设若安拉意欲（统一）了，他必教你们成为一族；但是（他令你们分伙）为的是对于所赐给你们的加以试证。你们忙行种种善举吧！你们统都归到安拉上，而后他对你们宣告你们所争议的。","translator":"王静斋"},{"sura":5,"aya":49,"text":"你当依着安拉所降的在他们中间作判断；不要追从他们的私见。你要防备他们对于安拉所降的一部分上磨难你。如果他们不肯了，你当知道：安拉将要教他们因着一部分罪恶遭祸。许多的人实是作恶的。","translator":"王静斋"},{"sura":5,"aya":50,"text":"他们讨茫昧时代的判断吗?谁是比安拉最善于为着一般有定信的人作判断的?","translator":"王静斋"},{"sura":5,"aya":51,"text":"众归信的人哪！你们不要和犹太人与基督徒结友；他们彼此是朋友。你们之中和他们结友的人，便是他们的同类。安拉绝不引导背义的群众。","translator":"王静斋"},{"sura":5,"aya":52,"text":"你看那般心里有病的人奔驰在他们中间；他们说：（我们恐怕遭难。）安拉即将发现相助，或是出自他的一项事情，以致他们悔恨自心所怀的。","translator":"王静斋"},{"sura":5,"aya":53,"text":"众归信的人说：这些人是那尽力指安拉发誓！和你们共同的吗?他们的功修失效了，终至归为伤折的人。","translator":"王静斋"},{"sura":5,"aya":54,"text":"众归信的人哪！你们之中叛教的人，不久的安拉就拿来一伙人，主爱他们，他们爱主。对众穆民是谦逊的。对不信者是刚毅的。他们在安拉的道上奋斗，不怕责备者的责备，这是安拉的特恩。他把它赐给自所意欲（赐给）的人。安拉是宽恩的，深知的。","translator":"王静斋"},{"sura":5,"aya":55,"text":"爱护你们的，只是安拉和他的使者，与畏惧着立拜功，施天课的众穆民。","translator":"王静斋"},{"sura":5,"aya":56,"text":"喜爱安拉和他的使者，以及众穆民的人，（将得到真主相助，）安拉一派的人实是获胜的。","translator":"王静斋"},{"sura":5,"aya":57,"text":"众归信的人哪！不要把一般耍戏你们宗教的，先你们有经的人和不信的人，当作朋友。如果你们是归信的了，当要敬畏安拉。","translator":"王静斋"},{"sura":5,"aya":58,"text":"每逢你们唤人礼拜的时候，他们就拿它当游戏，这是由于他们为一伙子不了解的人。","translator":"王静斋"},{"sura":5,"aya":59,"text":"你呼有经的人说：（你们痛恶我们，只以我们归信安拉，和降给我们的，和先前所降的；且以你们的大半是作恶的人。）","translator":"王静斋"},{"sura":5,"aya":60,"text":"你说：（以论遭受安拉的还报，尚有比这个更恶的，我可以告诉你们吗?被安拉驱远，惹他怒恨，使他们变猪变猴的人，和拜假主的人，均是地位至劣的，是至失迷正道的。）","translator":"王静斋"},{"sura":5,"aya":61,"text":"他们来到你们这里的时候，就说：（我们归信了。）他们违逆着进来，确又违逆着出去。安拉至知他们所遮瞒的。","translator":"王静斋"},{"sura":5,"aya":62,"text":"你看他们许多人奔驰在作恶，仇视，和吃非法所得的；他们所作的实在太恶了，","translator":"王静斋"},{"sura":5,"aya":63,"text":"一般有修养的学者，和众博士为什么不禁止他们说谎言，吃非法所得的呢?他们的行为太恶了！","translator":"王静斋"},{"sura":5,"aya":64,"text":"犹太人说：（安拉的手是被缚的。）实是他们的手被缚了！他们由于所说的话遭受驱远了！主的两手实是开展的；他随便使用。由养主上降给你的，加增给他们多数人背谬和不信了。我投仇视和恼恨于他们中间，直到复生日。 每逢他们燃起战火来，安拉就熄灭了它。他们在地上为着造恶而奔波。安拉不喜造恶的人。","translator":"王静斋"},{"sura":5,"aya":65,"text":"设使有经的人归信，敬慎了，我必给他们遮蔽种种罪恶，我必教他们进乐园。","translator":"王静斋"},{"sura":5,"aya":66,"text":"设若他们奉行讨喇忒、引支勒，并其它降自养主的，他们定可自其头上脚下取食。他们之内有一伙守中道的。他们多数人的行为太恶了，","translator":"王静斋"},{"sura":5,"aya":67,"text":"使者，你传达自你养主上降给你的吧！你若未实作，那是你未宣达他的使命。安拉保佑你不受人害。安拉必不指导逆民。","translator":"王静斋"},{"sura":5,"aya":68,"text":"你语有经的人说：（你们非至奉行讨喇忒、引支勒，并其它降自养主的以后，方属有为。）降自养主的，确把悖逆和不信增给他们许多人了。你对于逆民不要悲伤。","translator":"王静斋"},{"sura":5,"aya":69,"text":"一般归信的人，犹太教徒基督教徒，其信安拉，末日、并作善举的人，在他们无惧无忧。沙便人也是这样。","translator":"王静斋"},{"sura":5,"aya":70,"text":"的确，我取得以斯拉衣来子孙的约了；我向他们派去列使。使者把他们心所不欲的现给他们的时候，他们不信一部分杀死一部分。","translator":"王静斋"},{"sura":5,"aya":71,"text":"他们自以为无有祸患。他们眼瞎了，耳聋了。后来安拉允他们悔罪了。而后，他们许多人又眼瞎耳聋了，安拉看得见他们的行为。","translator":"王静斋"},{"sura":5,"aya":72,"text":"声称安拉就是马而焉的子埋希哈的人们确已背逆了。埋希哈尝唤以斯拉衣来的子孙说：你们当拜安拉——我的养主，也是你们的养主。凡给安拉举匹偶的人，安拉就不准他进天园。他的归所是火狱，背义的人不获一个相助者。","translator":"王静斋"},{"sura":5,"aya":73,"text":"一般自称安拉就是三位的一位者实已背逆了。除却独一主以外，再无有主。他们若不停止所说的，其中的不信者必遭一种痛刑。","translator":"王静斋"},{"sura":5,"aya":74,"text":"他们怎么不向安拉悔罪，并向他求恕呢?安拉是多恕的，特慈的。","translator":"王静斋"},{"sura":5,"aya":75,"text":"马而焉的儿子埋希哈只是一位使者，列使在他以前过去了。他母亲是一个诚挚人。他母子全吃食品。看哪！我怎么样对他们解明种种表征；你再看哪！他们转变到那里?","translator":"王静斋"},{"sura":5,"aya":76,"text":"你说：（你们舍去安拉拜那不为你们掌利害的吗?）安拉即是能听的，深知的。","translator":"王静斋"},{"sura":5,"aya":77,"text":"你唤有经的人说：（你们对于自己的教门不得背义地过分。你们不要随从那伙人的私见：他们先是自误；并使多的人迷误，他们失迷了正路。","translator":"王静斋"},{"sura":5,"aya":78,"text":"以斯拉衣来子孙中的逆徒，在达伍德与马而焉的子尔撒的口舌上遭咒诅了，这是由于他们背逆与过当。","translator":"王静斋"},{"sura":5,"aya":79,"text":"他们不禁忌所作的罪恶，他们的行为太恶了，","translator":"王静斋"},{"sura":5,"aya":80,"text":"你看！他们许多人和不信的人结友。他们先为本身作下的太恶了；那就是他们遭安拉的怒恨，永在刑罚中。","translator":"王静斋"},{"sura":5,"aya":81,"text":"设若他们信安拉和圣人，和降给他的，他们就未尝和他们结友了，但是他们多数人是作恶的。","translator":"王静斋"},{"sura":5,"aya":82,"text":"你必感觉人们在仇视归信的人上最烈的，是犹太人和拜偶像的；你必感觉人们在感情上最接近穆民的是一切自称基督徒的人，这是因他们内中有些学者，和修道士，并因他们不高傲。","translator":"王静斋"},{"sura":5,"aya":83,"text":"他们听到降给使者的时候，你看见他们因为所识的真理眼里流泪着说：主啊！我们归信了。你把我们和众见证人一同写下吧！","translator":"王静斋"},{"sura":5,"aya":84,"text":"我们哪能不信安拉和所来至我们的真理呢?我们希望养主教我们和廉洁的群众同进天园。","translator":"王静斋"},{"sura":5,"aya":85,"text":"于是安拉因着他们所说的话，把诸河流于其下的天园赏给他们，永居其中着。这是报偿为善的人们。","translator":"王静斋"},{"sura":5,"aya":86,"text":"隐昧且不信我表微的人，均是居火狱的。","translator":"王静斋"},{"sura":5,"aya":87,"text":"众归信的人哪！你们不要自忌安拉为你们列为合法的美品。你们不要过当，安拉绝不喜过当的人。","translator":"王静斋"},{"sura":5,"aya":88,"text":"你们吃安拉所赐的清洁合法的，你们当敬畏自所归信的安拉！","translator":"王静斋"},{"sura":5,"aya":89,"text":"安拉不以你们无意中的发誓责罚你们；他只是以你们有意的发誓责罚你们。补偿它，是款待十个贫人…，用那给家眷吃的中等的，或给他们穿的，或是解放一个奴。得不到的人，则封三天斋。在你们发誓，的时候，这就是罚你们的誓。你们当守自己的誓言，安拉如此对你们解明他的表征，好教你们感念。","translator":"王静斋"},{"sura":5,"aya":90,"text":"众归信的人哪！酒、赌博、偶像、抽签只是秽物，是恶魔的作为。你们要远避它，冀使你们达到所欲。","translator":"王静斋"},{"sura":5,"aya":91,"text":"恶魔欲在酒和赌里把怒恨投在你们之间，且阻碍你们记念安拉，并（阻碍）礼拜，你们可能禁忌吗?","translator":"王静斋"},{"sura":5,"aya":92,"text":"你们当顺从安拉，顺从使者，并当隄防。 如果你们不肯了，你们要得知道我的使者只负责公开的宣达罢了。","translator":"王静斋"},{"sura":5,"aya":93,"text":"归信并作善举的人，当他们敬慎，归信并作善举，越发敬慎归信，更加敬慎为善，到那时候，他们曾经吃过的不为有罪。安拉喜爱为善的人。","translator":"王静斋"},{"sura":5,"aya":94,"text":"众归信的人哪！安拉必用你们的手与枪得到的一些猎物试证你们，安拉原为知道，那来亲见而骇怕他的人。凡在这以后过当的，其人应遭痛刑。","translator":"王静斋"},{"sura":5,"aya":95,"text":"众归信的人哪！你们受着戒的时候不要杀猎物。你们之中故意杀猎物的人，应罚牲畜中类如他杀死的，同人中两公正人作判断！（就是赔补）直达克而白的祭品，或是补偿、款待些个贫人，或与那个相抵的斋戒，以便他尝自己事情的恶果，安拉赦免他以往的。复犯的人，安拉必惩罚他。安拉是优胜的，掌刑罚的。","translator":"王静斋"},{"sura":5,"aya":96,"text":"捕海中的猎物和其中的食品是对你们列成合法的，为的是利于你们和游行者。你们受着戒的时候，不得猎捕陆地的牲。你们当敬畏安拉——那把你们集至他的主。","translator":"王静斋"},{"sura":5,"aya":97,"text":"安拉把克尔白——禁殿和禁月，祭物，挂项饰的驼，置作支持众人的。这是教你们晓得安拉知道在天在地的，并晓得安拉是深知万事的。","translator":"王静斋"},{"sura":5,"aya":98,"text":"你们当知安拉是掌烈刑的；并知道安拉是多恕的特慈的。","translator":"王静斋"},{"sura":5,"aya":99,"text":"使者所负的只是宣达罢了。安拉知道你们公开的隐匿的。","translator":"王静斋"},{"sura":5,"aya":100,"text":"你说：（污浊的和清洁的不相等，纵令污浊的多使你爱慕。有才识的人哪！你们当敬畏安拉，以便你们达到所欲。）","translator":"王静斋"},{"sura":5,"aya":101,"text":"众归信的人哪！你们不可询问的事情，若把它宣告你们，则贻你们忧愁，在降古兰的时候，你们若问那些事了，就把它宣告你们。安拉放赦那事了。安拉是多恕的，仁厚的。","translator":"王静斋"},{"sura":5,"aya":102,"text":"你们以前的群众确问过它。但是他们因此归为背逆的人了。","translator":"王静斋"},{"sura":5,"aya":103,"text":"安拉原未设置白黑赖，沙一拜，窝随赖，哈目，但是悖逆的人们往安拉上造谎言，他们的大半并不了解，","translator":"王静斋"},{"sura":5,"aya":104,"text":"一旦有人告诉他们来至安拉所降的，并来至使者，他们就说：（我们得到列祖所守的已使我们满足了。）设若他们的列祖不知一事，也得不到正路，在他们也可以为满足吗?","translator":"王静斋"},{"sura":5,"aya":105,"text":"众归信的人哪！你们要自保其身。你们已然得着正道的时候，迷误的人妨害不了你们。你们统都归返安拉，他将对你们宣告你们以往所作的。","translator":"王静斋"},{"sura":5,"aya":106,"text":"众归信的人哪！你们有人临死作遗嘱的时候，其中的见证，是同类中两个公正人或是两个外人。若是你们在地上旅行而遇到死亡的时候——你们在拜后留止他两个，他俩指着安拉发誓；如果你们怀疑了，（我们不以此换代价，纵令他是近戚。我们不隐匿安拉的见证。果然是那样，我们定属于不公的人了。）","translator":"王静斋"},{"sura":5,"aya":107,"text":"如果有人发现他俩负罪了，则丧主之中两个最接近（死者）的人代替他俩的地位；指着安拉发誓：（我们的见证比较他俩的见证切实，我们未过当。果然是那样，我们便是背义的人。）","translator":"王静斋"},{"sura":5,"aya":108,"text":"这是最近于依着正理举行见证，或是恐怕在发誓以后被人驳斥誓言的。你们当敬畏安拉，你们当听从，安拉不引导恶民。","translator":"王静斋"},{"sura":5,"aya":109,"text":"将有那么一日！安拉集起列使来，说：（你们所得到的答复是什么?）他们说：（我们不知道。你确深知目不能见的。）","translator":"王静斋"},{"sura":5,"aya":110,"text":"那时候，安拉唤马尔焉的子尔撒说：你当记念我对你并对你母的恩典。那时候，我以一洁灵扶助你，你在摇篮中并在成年的时代对人接谈。那时候；我把经典，智慧、讨喇忒、引支勒教给你。那时候，你依我的判定从泥上制造一个像鸟形的，往它上吹气，它就依我的判定化为飞鸟。你依我的判定治愈胎瞽，患大麻疯的。那时候，你依我的判定现出死的。那时候；我代你制止伊斯拉衣来的子孙。那时候；你现给他们各样异迹。他们之中不信的人说：（这不外是明明的邪术。）","translator":"王静斋"},{"sura":5,"aya":111,"text":"那时候，我默谕众门徒：（你们信我和我的使者。）他们就说：（我们信了，求你见证我们确是顺服的。）","translator":"王静斋"},{"sura":5,"aya":112,"text":"那时候，众门徒唤马尔焉的子尔撒说：（你的养主能从天上降给我们餐桌吗?）尔撒说：（你们若是归信的，你们就当敬畏安拉。）","translator":"王静斋"},{"sura":5,"aya":113,"text":"他们说：（我们欲就着那餐桌吃，并为着我们的心得到安定，且晓得你对我们说了实言，我们为它作见证。）","translator":"王静斋"},{"sura":5,"aya":114,"text":"马尔焉的子尔撒说：（主啊！我们的养主啊！求你从天上把餐桌降给我们，以作我们前人后人的节期，并作为你的表征！你赐我们给养吧！你是无上的供给者）","translator":"王静斋"},{"sura":5,"aya":115,"text":"安拉说：（我必把它降给你们。你们之中在此以后不信的人，我将必用那未曾惩过世界一人的刑罚来惩治他。）","translator":"王静斋"},{"sura":5,"aya":116,"text":"那时候，安拉呼马尔焉的子尔撒说：（你对众人说：你们撇开安拉，把我和我母亲当作两个主吗?）尔撒说：赞你清净！我不该说那于我不相宜的。如果我说了，你必知道。你知道我心中的，我不知道你心中的。你确深知目不能见的。","translator":"王静斋"},{"sura":5,"aya":117,"text":"我只对他们说你所命令我的，就是：你们当拜安拉——我的养主，也是你们的养主。我是至死监视他们的。在你取去我的时候，你是巡察他们的。你是监视万事的。","translator":"王静斋"},{"sura":5,"aya":118,"text":"你若惩罚他们了，他们是你的奴仆：你若恕过他们了，你是优胜的，明哲的。","translator":"王静斋"},{"sura":5,"aya":119,"text":"安拉说：（这是实言有利于实言者的那一日。他们享受诸河流于其下的天园，永久居住在那里。安拉满意他们，他们也满意他了。这是大成功。","translator":"王静斋"},{"sura":5,"aya":120,"text":"天地万有的权柄惟安拉执掌。他是全能于万事的。）","translator":"王静斋"},{"sura":6,"aya":1,"text":"赞颂安拉，那造化天地，发生黑暗和光明的主。不信的人们尚且给其养主举对象。","translator":"王静斋"},{"sura":6,"aya":2,"text":"他是从泥上造化了你们，并且判定死期的主。一定的限期是他执掌，你们尚复怀疑。","translator":"王静斋"},{"sura":6,"aya":3,"text":"他是在天地间受拜的主！他知道你们的秘密和显扬，并知道你们所作的。","translator":"王静斋"},{"sura":6,"aya":4,"text":"他们每逢得到养主各项表征中的一种表征时，只是远避它。","translator":"王静斋"},{"sura":6,"aya":5,"text":"他们在真理达到时，确曾不信它了。不久他们所嘲弄的那事情的种种消息到来。","translator":"王静斋"},{"sura":6,"aya":6,"text":"他们岂未看见我在他们以前灭绝若干代吗?我教他们住在未曾令你们住过的地面，我向他们降下连绵的大雨，我使诸河流于他们下面，我因种种罪恶灭绝他们。在他们以后我又发现另一代人。","translator":"王静斋"},{"sura":6,"aya":7,"text":"设若我把写在纸上的降给你，他们亲手换它了，不信的人们必说：（这只是明显的邪术。）","translator":"王静斋"},{"sura":6,"aya":8,"text":"他们说：（为何不往他上降天使呢?）设若我降天使，事必告终了，他们不再受到姑容。","translator":"王静斋"},{"sura":6,"aya":9,"text":"设我委派天使了，我必教他化为人形。（设我教他化为人形）我就教他们混乱起来了。","translator":"王静斋"},{"sura":6,"aya":10,"text":"在你以前的列使确切遭受嘲弄了，而后那般嘲弄者因自己的嘲弄遇到报应了。","translator":"王静斋"},{"sura":6,"aya":11,"text":"你说：（你们去在地上旅行，而观察不信的人结果如何?）","translator":"王静斋"},{"sura":6,"aya":12,"text":"你说！（天地间的为谁执掌?）你就说：（安拉执掌。）安拉自行负起慈惠了。他必把你们集合到无可疑的复生日。自负己身的人均不归信。","translator":"王静斋"},{"sura":6,"aya":13,"text":"所蕴于夜昼的惟主执掌，他是能听的，深知的。","translator":"王静斋"},{"sura":6,"aya":14,"text":"你说：（我可把除过造化天地的安拉当作爱友吗?他赐给食物，自不受食。）你说：（我确已奉命为首先顺服的，不准作多神教徒。）","translator":"王静斋"},{"sura":6,"aya":15,"text":"你说：我若违犯养主了，我实畏惧大日的刑罚。","translator":"王静斋"},{"sura":6,"aya":16,"text":"在那一日，蒙得移转（刑罚）的人，确蒙真主慈惠了。这是明显的成功。","translator":"王静斋"},{"sura":6,"aya":17,"text":"若是安拉降给你祸了，除他以外无有释去的；他若降给你福了，他是全能与万事的。","translator":"王静斋"},{"sura":6,"aya":18,"text":"他是在其众仆之上有权威者。他是明哲的，尽知的。","translator":"王静斋"},{"sura":6,"aya":19,"text":"你说：（什么事为极大的见证?）你就说：（安拉是在我和你们中间作见证的。我蒙告谕这古兰，以便用它警告你们和那接到的人。你们肯见证：有另些受拜的和安拉共同吗?）你说：（我不敢作见证）你说：（他只是独一受拜的。我是对你们所拟的匹偶无干的。）","translator":"王静斋"},{"sura":6,"aya":20,"text":"蒙我赐给经典的那般人认识他，如同认识自己的儿子一样。一切自负己身的人均不信任。","translator":"王静斋"},{"sura":6,"aya":21,"text":"谁是背义较甚于往安拉上造谎言或是不信他的表征的?背义的人绝难得意，","translator":"王静斋"},{"sura":6,"aya":22,"text":"当那一日，我把他们一并集起，然后我对举匹偶的人们说：（你们自所妄拟的那些匹偶是在哪里?）","translator":"王静斋"},{"sura":6,"aya":23,"text":"随后他们的托词不外是说：（指着安拉）我们的养主为誓：我们不是举匹偶的。","translator":"王静斋"},{"sura":6,"aya":24,"text":"看哪！他们怎样地自造谎言?他们所造作的，业已不在了。","translator":"王静斋"},{"sura":6,"aya":25,"text":"他们之中有的人向你侧耳。我在他们的心上置一幔帐，而致不能解悟，并使他们重听。若是他们看见各项异迹了，则不信它。待至他们到你面前争论的时候，不信的人就说：（这不过是前人的故事。）","translator":"王静斋"},{"sura":6,"aya":26,"text":"他们阻止它，远避它。他们惟害自身，并不觉悟，","translator":"王静斋"},{"sura":6,"aya":27,"text":"在教他们站在火狱上，自谓：（哎宁愿我们复返，不再否信养主的表征，而归到穆民一流）的时候，设若你看见了……","translator":"王静斋"},{"sura":6,"aya":28,"text":"不然，从前他们所掩讳的已然现露。设若教他们复返了，他们仍蹈那所禁止他们的。他们实是谎言的人。","translator":"王静斋"},{"sura":6,"aya":29,"text":"他们说：（生只是我们今世的生，我们不再复起。）","translator":"王静斋"},{"sura":6,"aya":30,"text":"他们站在养主的御前，主说：（这不是真的吗?）他们就说：（指着我们养主发誓！是的。）主说：（你们由于不信尝刑罚吧！）","translator":"王静斋"},{"sura":6,"aya":31,"text":"到那时候，设若你看见了，……。不信遇见安拉的人，确已伤折了。待至复生日忽然来到的时候，他们就说：（哎！可惜我们在它里所怠慢的）他们负荷着自己的重担，须知他们所负荷的太恶了！","translator":"王静斋"},{"sura":6,"aya":32,"text":"今生不外是玩乐与荒嬉；后世是于一般敬慎的人最好的。你们怎不了解呢?","translator":"王静斋"},{"sura":6,"aya":33,"text":"我确知道他们说的那话贻你悲伤，他们实在不信你。背义的人只是不承认安拉的表征，","translator":"王静斋"},{"sura":6,"aya":34,"text":"在你以前的列使确遭人不信服了。但是他们忍受人家的不信和扰害。终至得到我的相助。 无有变更安拉之言的，你确得到列使的一部分消息了。","translator":"王静斋"},{"sura":6,"aya":35,"text":"如果你以他们的执拗为重大了，你若能在地上觅一孔隙，或得一天梯，而对他们举出一项异迹来……。设若安拉意欲（集合）了，他必把他们集合在正道上? 你不要无知。","translator":"王静斋"},{"sura":6,"aya":36,"text":"只有侧耳的人应承，死人 将被安拉复起，然后使他们复返于他。","translator":"王静斋"},{"sura":6,"aya":37,"text":"他们说：（何不把他养主的一项表征降给他呢?）你说：（安拉实是能于降下异迹的。但是他们的多半不知道。","translator":"王静斋"},{"sura":6,"aya":38,"text":"地上所有的动物和那用双翼翱翔的飞鸟，无一不是和你们一样的群众。）我在经典中未尝遗漏一事。随后他们惟被集至自己的养主。","translator":"王静斋"},{"sura":6,"aya":39,"text":"一切不信我表征的人是聋的哑的，处在种种黑暗中。安拉随意造人为迷误的。真主随意把人置在正道上。","translator":"王静斋"},{"sura":6,"aya":40,"text":"你说：（你们看出来了吗?设若安拉的罪刑临到你们上，或是复生日临到你们上了，你们肯呼吁安拉以外的吗?如果你们所言属实了……。","translator":"王静斋"},{"sura":6,"aya":41,"text":"不然，你们只是呼吁他。他若意欲（应允）了，他就释去你们所向他呼求的，你们忘掉自己所妄拟定的匹偶。","translator":"王静斋"},{"sura":6,"aya":42,"text":"我确向你以前的群众遣使了。（他们不信他）而后我以贫困和灾病惩治他们，冀使他们表示微贱。","translator":"王静斋"},{"sura":6,"aya":43,"text":"在我的刑罚来到的时候，他们怎不表示微贱呢? 但是，他们的心硬了。恶魔已为他们装饰了他们所作的。","translator":"王静斋"},{"sura":6,"aya":44,"text":"当他们忘却所用作教训的时候，我就为他们开放诸事的门。直至他们借着所赐给的狂喜时候，我突然罪刑他们，他们是立时绝望的。","translator":"王静斋"},{"sura":6,"aya":45,"text":"于是一般背义的民众的后代断绝了。赞颂安拉，调养众世的主。","translator":"王静斋"},{"sura":6,"aya":46,"text":"你说：（你们看出来了吗?若是安拉夺去你们的耳、眼，封闭了你们的心，谁是安拉以外的主，能把它拿给你们?）看哪！我如何重复各项表征，他们尚复远避?","translator":"王静斋"},{"sura":6,"aya":47,"text":"你说：（你们看出来了吗?若是安拉的罪刑突然或显然而至……·除却背义的群众以外，有遭灭绝的吗?）","translator":"王静斋"},{"sura":6,"aya":48,"text":"我差遣的列使不外是报喜信，传警告的。凡是归信并且自修的人，在他们无惧无忧。","translator":"王静斋"},{"sura":6,"aya":49,"text":"不信我表征的人，将因作恶遭受罪刑。","translator":"王静斋"},{"sura":6,"aya":50,"text":"你说，我不对你们说：（我据有安拉的仓库；）我也不自称知道目不能见的。也不对你们说：（我实是一个天使）我只是服从所默谕我的。 你说：（瞎子和有限的不一样，你们还不参悟吗?）","translator":"王静斋"},{"sura":6,"aya":51,"text":"你用它警告恐怕将被集至养主，舍开他不得一爱护的，告赦的那般人，好教他们敬慎。","translator":"王静斋"},{"sura":6,"aya":52,"text":"你不要驱逐那般朝夕祈求养主，讨他喜爱的人。你未负责对他们清算一些，他们也未负责对你清算一些，你何必驱逐他们，自为行亏的人呢?","translator":"王静斋"},{"sura":6,"aya":53,"text":"我就这样地用一部分试证一部分，终至他们说：（安拉于我们之中对这般人施恩了吗?） 安拉岂非至知感念的人?","translator":"王静斋"},{"sura":6,"aya":54,"text":"信我表征的人，一旦到你这里来了，你就说：（祝你们平安，）你们的养主已钦定慈惠了。那就是：你们之中凡是因着无知而作恶，嗣后复又悔罪自修的人，真主是多恕的，特慈的。","translator":"王静斋"},{"sura":6,"aya":55,"text":"我如此分析各项表征，（冀使真理发现）并使恶人的行径得以明显。","translator":"王静斋"},{"sura":6,"aya":56,"text":"你说：（我是被禁不许拜你们舍去安拉所拜的。）你说：（我不追随你们的私见。果然那样，我确迷误，我就不是得正道的了。）","translator":"王静斋"},{"sura":6,"aya":57,"text":"你说：（我必守养主的明证。你们已不信实它了，我不掌你们所求速现的。惟有安拉势掌判断。他述说真理。他是最优的判断者。）","translator":"王静斋"},{"sura":6,"aya":58,"text":"你说：设我势掌你们所求速现的，我和你们之间的事情一定告终了。安拉至知背义的人。 惟真主掌管（哀卜）的锁钥。","translator":"王静斋"},{"sura":6,"aya":59,"text":"惟有真主知道它。他且知道陆海之中的。所落下来的树叶，主全知道；地上黑暗里的子粒，也无一不知。所有湿的干的，均载在明典中。","translator":"王静斋"},{"sura":6,"aya":60,"text":"他是在夜间取去你们灵魂的。他知道你们在白昼所干的，他又教你们在白昼醒来，终至一定的限期得到完满。随后你们归返于他，他再把你们所作的宣告你们。","translator":"王静斋"},{"sura":6,"aya":61,"text":"他是在其众仆之上有权威的。他给你们差来保护的天使。待至你们有人将死的时候，我的列使不怠慢地取去他的性命。","translator":"王静斋"},{"sura":6,"aya":62,"text":"而后他们归到安拉——他们的真主宰。须知惟他掌判断，他是清算极为神速的。","translator":"王静斋"},{"sura":6,"aya":63,"text":"你说：适值你们表示微贱暗自呼吁他着，谁自陆海的黑暗上解救你们?（彼时他们说：）他若由这里解救了我们，我们必定感念。","translator":"王静斋"},{"sura":6,"aya":64,"text":"你说：（安拉由这患难上并由一切痛苦上解救你们，你们尚复举定匹偶。）","translator":"王静斋"},{"sura":6,"aya":65,"text":"你说：（真主是能从你们上面和脚下施刑的；或教你们一伙一伙地相混起来；并教你们一部分尝试一部分的格斗。）你看！我如何重复各项表征，好教他们解悟。","translator":"王静斋"},{"sura":6,"aya":66,"text":"那本是确实的，而你的族人不信它。你说：（我不是保障你们的。","translator":"王静斋"},{"sura":6,"aya":67,"text":"在每项消息有一定的时期。不久你们就知道。）","translator":"王静斋"},{"sura":6,"aya":68,"text":"你看见一般人曾在从事嘲弄我的表征时，你要迥避他们，待至他们入到别的话里。若是恶魔使你忘记了，你在想起以后，不要和这般背义的人同坐。","translator":"王静斋"},{"sura":6,"aya":69,"text":"敬慎的人本不负责对他们清算一事，只有善劝，好教他们敬畏。","translator":"王静斋"},{"sura":6,"aya":70,"text":"那些把自己的宗教当作可耍可戏的人，你任他们随便吧！他们被今生欺骗了。你就用它劝戒（他们）吧！以免各人因其所作的陷入灭亡。各人舍去安拉无一爱护的，也无一告赦的。如果他出以充足的罚金，则不获接受。这一般是因其所赚下的归为堕落的。他们因着不信而饮沸汤，受痛刑。","translator":"王静斋"},{"sura":6,"aya":71,"text":"你说：（我们岂肯舍去安拉拜那于我们无利无损的，并在得到安拉的指导以后复又退转；就像恶魔们在地上教他茫然堕落的人，在他上有些同伴唤他往正道上来。）你说：安拉的引导就是引导。我奉命顺服调养众世界的主；","translator":"王静斋"},{"sura":6,"aya":72,"text":"并奉命立拜功，并敬畏他。他是你们将被集至他的那个主。","translator":"王静斋"},{"sura":6,"aya":73,"text":"他是因真理造化天地的主。当那一日，主吩咐：（有！它立刻就有。他的话是切实的。在吹起号筒那一天，惟他掌权。知道目不能见的和显见的主，他是明哲的尽知的。）","translator":"王静斋"},{"sura":6,"aya":74,"text":"昔时，易卜拉欣对他的父阿则而，说：（你以偶像当作受拜的吗?我看你和你的族人确是在明显的迷误中）——","translator":"王静斋"},{"sura":6,"aya":75,"text":"我以此教易卜拉欣看见天地的大国，俾他坚信不疑，","translator":"王静斋"},{"sura":6,"aya":76,"text":"在他被夜遮黑的时候，他就看见星辰了，说：（这是我的养主。）当星辰没落的时候，他就说：（我不爱没落的）","translator":"王静斋"},{"sura":6,"aya":77,"text":"在他见月亮上升的时候，就说：（这是我的养主）当它没落的时候，他就说：（安拉若不引导我，我必是迷误一伙的人了。）","translator":"王静斋"},{"sura":6,"aya":78,"text":"在他看见太阳上升的时候，就说：（这是我的养主，这是伟大的）当它没落的时候，他就说：（我的同族啊！我实是对你们所拟的匹偶无干的；","translator":"王静斋"},{"sura":6,"aya":79,"text":"我必端然倾向那造化天地的主，我不是信匹偶的。）","translator":"王静斋"},{"sura":6,"aya":80,"text":"他的同族对他起争辩。他说：你们因为安拉对我起争辩吗?他确已引导我了。我不怕你们给主所拟的匹偶。除非我的养主意欲一事的时候。我的养主在知识上包容万物。你们怎不参想呢?","translator":"王静斋"},{"sura":6,"aya":81,"text":"你们不怕给安拉举那未降给它权力的为匹偶，我能怕你们所拟的匹偶吗? 这两部分人谁是最应得到平安的，如果你们知道了，（则请告诉我）。","translator":"王静斋"},{"sura":6,"aya":82,"text":"一般归信，并未以背义搀混自己信仰的人，可获平安。他们是得正道的。","translator":"王静斋"},{"sura":6,"aya":83,"text":"这是我赐给易卜拉欣对他同族的凭据。我随便教人升高种种品级，你的养主实是明哲的深知的。","translator":"王静斋"},{"sura":6,"aya":84,"text":"我曾赏给他易司哈各，叶而孤白，我引导了每一个。从前我曾引导了努海，和他子孙中的达伍德、苏来茫、按忧伯、郁素福、母撒、哈伦。我如此报偿为善的人。","translator":"王静斋"},{"sura":6,"aya":85,"text":"翟克赁亚、耶哈亚、尔撒、伊洛押司。统属义人。","translator":"王静斋"},{"sura":6,"aya":86,"text":"易司马衣、业赛亚、郁奴司、鲁脱。我教每一个优越同一时代的人，","translator":"王静斋"},{"sura":6,"aya":87,"text":"并有他们的一部分列祖、子孙、兄弟。我选择了他们，并引他们于正道。","translator":"王静斋"},{"sura":6,"aya":88,"text":"那是安拉的南针。他用它指示自所意欲（指示）的仆人。设若他们举匹偶了，他们所作的 一定失效。","translator":"王静斋"},{"sura":6,"aya":89,"text":"这一般是我赐给经典，智慧，与圣职的人。如果那般人不信了，我就把它委托一伙信它的人。","translator":"王静斋"},{"sura":6,"aya":90,"text":"这些人是受了安拉引导的。你就跟随他们的引导吧！你说：（我不为此向你们求报酬。它只是劝化普世界的人。）","translator":"王静斋"},{"sura":6,"aya":91,"text":"他们估计安拉，未至切当地估计他；那时候，他们说：（安拉从未向一人降过一事。）你说：（母撒显示的明光和引导世人的经典，你们把它置在纸面上公布它，并隐匿许多的，你们被教以自身和列祖所不知道的，那是谁降的?）你说：（安拉。）你且任他们在从事亵渎中嬉戏着，","translator":"王静斋"},{"sura":6,"aya":92,"text":"这是我降的吉庆的经典，证实在它以前的，好教你警告世界首府暨其四周的人。信后世的人信它。他们均在保守拜功着。","translator":"王静斋"},{"sura":6,"aya":93,"text":"往安拉上造作谎言，或自称：（我受默示了，）他原未受过一点默示；与那自称：（我即将降下类似安拉所降的，）谁是比这样人尤为背义的? 背义的人一旦处在临死的痛苦中，天使向他们伸手取命〔说：〕（今日将因你们往安拉上说不相当的，并因你们强项不承认他的表征，而报你们以辱刑，）到那时候，设使你看见了……","translator":"王静斋"},{"sura":6,"aya":94,"text":"你们将只身着到我这里来，就像我初次造化你们一样。你们把我所赐给的弃置背后；你们妄称：（自己间有些个匹偶，为你们代求赦罪。）我未见它们随你们同来。你们的联络确已中断了，你们所造作的消灭了。","translator":"王静斋"},{"sura":6,"aya":95,"text":"的确，安拉是剖开子粒与枣核的，他从死的里现出活的，且从活的里现出死的。这是安拉。你们转变到哪里?","translator":"王静斋"},{"sura":6,"aya":96,"text":"是发现破晓的。他以夜作安定的时间，以日月作计算的标准。这是优胜深知的土的规定。","translator":"王静斋"},{"sura":6,"aya":97,"text":"他那真主为你们造群星，以便你们在陆海的黑暗中得到明路。我已为着有知识的群众分析了种种表征，","translator":"王静斋"},{"sura":6,"aya":98,"text":"他那真主由一人上造化了你们。既而稳定和贮藏。我确为有解悟的群众分析了种种表征。","translator":"王静斋"},{"sura":6,"aya":99,"text":"他那真主由上空降下雨来，而用它发现各物的萌芽，再由它上发现绿叶；植物由它上发现累累的粒谷，枣树的叶鞘上，有一束一束下垂的；并（发现）葡萄园，与橄榄，与相似和不相似的石榴。在树木结果子的时候，你们观看它的果实和它的成熟吧，此中对于有信心的群众，确含有种种表征。","translator":"王静斋"},{"sura":6,"aya":100,"text":"他们给安拉设置一切匹偶——镇呢？他本造化了它们。他们无知着为他造作了些个子女，赞他清净，他玄高于他们所称述的，","translator":"王静斋"},{"sura":6,"aya":101,"text":"造化天地的主，在他无女伴，他何从有子嗣他造化了万物，他是深知万事的。","translator":"王静斋"},{"sura":6,"aya":102,"text":"这是安拉——调养你们的主。除他以外，再无一受拜的。造化万物的主，你们当要拜他，他是管理万事的。","translator":"王静斋"},{"sura":6,"aya":103,"text":"他不是众目可得的。他独得到众目了。他是妙知的，尽知的。","translator":"王静斋"},{"sura":6,"aya":104,"text":"养主的明证确已达至你们了。注目的人利他自身，盲目的人害他自身。我不是保护你们的，","translator":"王静斋"},{"sura":6,"aya":105,"text":"我如此重复各项表征，以免他们说：（你曾读过了，）并为把它解明给有知识的群众。","translator":"王静斋"},{"sura":6,"aya":106,"text":"你当随从得自养主的启示。除他以外，再无一受拜的，你要远避多神教徒。","translator":"王静斋"},{"sura":6,"aya":107,"text":"设若安拉意欲（制止）了，他们未尝举匹偶。我未委你保护他们，你不是管理他们的。","translator":"王静斋"},{"sura":6,"aya":108,"text":"你们不要骂一般呼求安拉以外的。若然，他们就无知着仇骂安拉。我如此因为每伙人装饰他们的作为，而后他们惟归于养主。他对他们宣告他们所作的。","translator":"王静斋"},{"sura":6,"aya":109,"text":"他们尽力地指着安拉发誓：（若有一项异迹达至他们了，他们一定信它。）你说：（一切异迹惟操自安拉。你们从何知道当异迹来到的时候，他们不信呢?）","translator":"王静斋"},{"sura":6,"aya":110,"text":"我转变他们的心、眼，就如他们前次未信似的。我任他们在迷路中徘徊着，","translator":"王静斋"},{"sura":6,"aya":111,"text":"设若我向他们差天使，死人能对他们叙话，并把各物一伙一伙地结集在他们那里，他们绝不归信；除非安拉意欲……的时候。 但是他们的多半无有知识。","translator":"王静斋"},{"sura":6,"aya":112,"text":"我如此为着每位圣人发现一般仇敌，人类和镇呢的恶魔。他们的一部分为着欺骗把假话暗示给一部分。设若你的养主意欲……了，他们就不作这事。你任他们随便造作去吧！","translator":"王静斋"},{"sura":6,"aya":113,"text":"、（他们暗传诡话）为的是一般不信后世的人心偏重它，喜爱它，以便他们干自所能干的。","translator":"王静斋"},{"sura":6,"aya":114,"text":"你说：（我寻安拉以外的作判断吗? 他向你们降下分明的经典。）蒙我赐给经典的人们全知道那是依着真理降自养主的，所以你是不可怀疑的。","translator":"王静斋"},{"sura":6,"aya":115,"text":"你养主的话，充分切实地完成了。无有变更他的言语者，他是能听的深知的。","translator":"王静斋"},{"sura":6,"aya":116,"text":"如果你顺从地上大多数人了，他们就教你错过安拉之道。他们只是依从揣测，他们不外是造作。","translator":"王静斋"},{"sura":6,"aya":117,"text":"的确，你的养主是至知错过其道的。他是至知得正道的。","translator":"王静斋"},{"sura":6,"aya":118,"text":"如果你们信安拉的表征，你们就应吃那临宰时记念过他的尊名的。","translator":"王静斋"},{"sura":6,"aya":119,"text":"你们为何不吃临宰时记念过安拉尊名的。真主确已对你们分析禁不可食的了。迫不得已的在外。许多人无知识，而依个人的私见诱人迷误。的确，你的养主至知过当的人。","translator":"王静斋"},{"sura":6,"aya":120,"text":"你们当弃掉明显的和暗昧的罪恶。的确，作恶的人们即将因着所作的罪恶遭受还报。","translator":"王静斋"},{"sura":6,"aya":121,"text":"你们不要吃那临宰时未记念过安拉之名的。那实是罪恶。恶魔们暗中嗾使自己的爱友。为的是对你们起争论。如果你们顺从他们，你们一定就是拜匹偶的人。","translator":"王静斋"},{"sura":6,"aya":122,"text":"那死而经我复生且蒙我赐给光辉乘它行在人间者，可像他的情形是在种种黑暗中不得外出的人吗?有的如此着为不信的人装饰了他们的作为。","translator":"王静斋"},{"sura":6,"aya":123,"text":"我如此着于每一个城市里发现一般罪魁，在那里施计谋。他们只是对自己施计谋，并不觉悟。","translator":"王静斋"},{"sura":6,"aya":124,"text":"每逢有一异迹达至他们，他们就说：非依照赐给安拉的列使的赐给我们以后，我们绝不归信。安拉至知置其委任的地方。为恶的人即将因着施计谋遭受安拉的侮辱和烈刑。","translator":"王静斋"},{"sura":6,"aya":125,"text":"安拉意欲引导的人，他就为着伊斯兰开展他的胸怀。真主欲造为迷误的人，他就使他的胸怀十分窄狭，有如登天一般。安拉如此着把秽物投在一般不信的人上这是你养主的正路。","translator":"王静斋"},{"sura":6,"aya":126,"text":"我确为着纳劝的群众分析各项表征了。","translator":"王静斋"},{"sura":6,"aya":127,"text":"他们在养主御前获得平安的第宅。他是因他们的作为爱护他们的。","translator":"王静斋"},{"sura":6,"aya":128,"text":"当那一日，真主把他们一同集合起来，说：（镇呢大众啊！你们确引诱多的入迷误了。）他们属于人类方面的朋友说：（我们的养主啊！我们曾彼此相利用；我们已达到你为我们限定的期限了。）主说：（火狱是你们的住处，永居其中；除非安拉意欲……的时侯。你的养主实是明哲的，深知的。）","translator":"王静斋"},{"sura":6,"aya":129,"text":"我因为背义的人们所行的，如此着教他们的一部分压制一部分。","translator":"王静斋"},{"sura":6,"aya":130,"text":"镇呢和人类大众啊！你们同族的列使未来向你们诵我的表征，并向你们警告遇见今日吗?他们说：（我们就作不利于自己的见证吧！）他们曾被今生欺骗，他们自行见证不信了","translator":"王静斋"},{"sura":6,"aya":131,"text":"这是由于你的养主绝不是乘着市民昏愦，因着背义而灭绝各城的。","translator":"王静斋"},{"sura":6,"aya":132,"text":"各人依其所作的获种种阶级。你的养主必不忽于他们的作为。","translator":"王静斋"},{"sura":6,"aya":133,"text":"你的养主是无求的，有慈惠的。他若意欲……了，便就拿去你们，在你们以后召采他意欲……的人作替代的；就如由另一伙人的子孙里发现你们。","translator":"王静斋"},{"sura":6,"aya":134,"text":"对你们所约的，定要实现。你们是不能抵抗的。","translator":"王静斋"},{"sura":6,"aya":135,"text":"你说：（我的族人哪！你们尽力去作吧！我也是工作的。不久的，你们就知道谁得今世的善果。的确背义的人不能得意。）","translator":"王静斋"},{"sura":6,"aya":136,"text":"他们为安拉由他所造的田产和家畜上规定一分，妄称：（这一分归到安拉，这一分归到我们的偶像。）那归到他们偶像的达不到安拉上。那归到安拉的可能达到他们的偶像。他们判定的太恶了！","translator":"王静斋"},{"sura":6,"aya":137,"text":"他们的同伙如此着为许多拜偶像的人装饰，杀自己的子女，原为残害他们 并扰乱他们的宗教。设若安拉意欲……了，他们不行这事，你任他们造作去吧，","translator":"王静斋"},{"sura":6,"aya":138,"text":"他们妄称：（这是禁不可用的家畜和田产，除却我们许可的以外，不得吃它、是不准骑在它背上的家畜；）并有些家畜，宰时不往那上诵安拉之名。往真主上造作。主即将依他们的造作施以还报。","translator":"王静斋"},{"sura":6,"aya":139,"text":"他们说：（这些家畜腹中的是于我们的男子纯洁的；在我们的女子禁不可用。）那若是自死的，他们就共同的用它。真主即将还报他们的称道。主实是明哲的，深知的。","translator":"王静斋"},{"sura":6,"aya":140,"text":"一般无才无识着残害自己儿女，并禁忌安拉所赐给的人确已伤折了。往安拉上造作谎言。他们确已迷误，未得正道。","translator":"王静斋"},{"sura":6,"aya":141,"text":"他那真主造化了架起与未架起的园林、枣树，和那果实不一的农产，橄榄以及相似与不相似的石榴。待它结果子的时候，你们就吃它的果实吧！在收割它的时候，你们要散给所应提出的一部分，你们不可滥费。真主实不喜滥费的人。","translator":"王静斋"},{"sura":6,"aya":142,"text":"并由家畜中发现负重的伏地的。你们就着安拉所供给的吃吧！不要追从恶魔的脚步。他实是你们的明敌。","translator":"王静斋"},{"sura":6,"aya":143,"text":"(他发现了）八只：一对绵羊，一对山羊；你说：（他曾禁用二牡或二牝吗?或二牝的子宫所孕的呢?）你们要明白告诉我——如果你们所言属实的时候。","translator":"王静斋"},{"sura":6,"aya":144,"text":"一对驼，一对牛，你说：（他曾禁用二牡或二牝吗?或二牝的子宫所孕的呢?不然，在安拉以这个命令你们的时候，你们在场吗?）谁是背义更甚于因着吞知诱入迷误而往安拉上追谎的?安拉必不引导背义的民众。","translator":"王静斋"},{"sura":6,"aya":145,"text":"你说：（对于食者禁不可食的，依我所得到的默示，不外是自死的或外溢的血，或猪肉——因为那是秽物或是临宰时未朗诵安拉之名的非法的，其非背叛，也非越范被迫无奈的人，你的养主实是多恕的，特慈的。）","translator":"王静斋"},{"sura":6,"aya":146,"text":"我禁止犹太人用一切有爪的。我严禁他们用牛油羊油；除非其脊背，或肠子上所粘附的，或连在骨上的。这是我因他们的罪恶罚办他们。我是诚实的。","translator":"王静斋"},{"sura":6,"aya":147,"text":"若是他们不信服你了，你就说！（你们的养主是有宽慈的。他那对于罪民的刑罚不受阻止。）","translator":"王静斋"},{"sura":6,"aya":148,"text":"拜偶像的人们即将说：设使安拉意欲（制止）了，我们和列祖未尝拜偶像，我们也未尝忌食一物。他们的众先人就是这样地不信服，而致尝试我的罪刑了。你说：（你们具有什么知识，你们就把它现给我。你们不外是依从揣度。）你们只是妄造。","translator":"王静斋"},{"sura":6,"aya":149,"text":"你说：（安拉据有充分的凭据。设若他意欲[引导]了，他必引导你们全体。）","translator":"王静斋"},{"sura":6,"aya":150,"text":"你说：（你们举出一般证人来，见证安拉曾严禁这个了。）如果他们作见证了，你不要和他们同作见证。你不要追随不信我的表征，不信后世，且为其养主拟对象的那般人的私见。","translator":"王静斋"},{"sura":6,"aya":151,"text":"你说：（你们来读养主对你们所严禁的；莫给真主举一事作匹偶，孝敬父母；不要因着怕穷残杀自己的儿女，——惟我赐予你们，也赐予他们给养；不要接近显著和暗昧的丑恶； 不要杀那业经安拉禁不可杀的人，除非因为正义。这是真主以此嘱告你们，好教你们了解。","translator":"王静斋"},{"sura":6,"aya":152,"text":"你们不要接近孤儿的财产，除非是用最善的手续，直到他达至壮岁。你们要秉公给足了量的称的。我不课人能力以外的。你们说话的时候，纵令那人是亲属，也得主持公道。你们要完成安拉的约，","translator":"王静斋"},{"sura":6,"aya":153,"text":"这是真主以此嘱告你们 冀使你们受教。这确是我底直道；你们当追从它，不要追从别的道路，致使你们分伙而离开主道。这是真主以此嘱告你们，好教你们敬慎。","translator":"王静斋"},{"sura":6,"aya":154,"text":"我且曾把经典赐给母撒， 原为完满对于行善者的恩典，并分析万事，且为着引导和慈惠，好教他们信看见自己的养主。","translator":"王静斋"},{"sura":6,"aya":155,"text":"这是我降的吉庆的经典；你们当追从它，并当敬慎，好教你们获得怜恤；","translator":"王静斋"},{"sura":6,"aya":156,"text":"免去你们说：（惟我们先前的两伙人，得被降给经典了，我们实是不明白他们诵读的；）","translator":"王静斋"},{"sura":6,"aya":157,"text":"或是说：（设使降给我们经典了，一定我们比他们更能得正道。）你们确已得到养主的明证和引导与慈惠了。谁是比不信安拉的表征，并对它加以阻止的人更是不义的?我即严惩一般阻人信我表征的，只因他们的加以阻止。","translator":"王静斋"},{"sura":6,"aya":158,"text":"他们只等待天使来到，或你养主来到，或你养主的一部分异迹来到。在你养主的一部分异迹来到的日子，那先未归信，或是在归信的时候未作过好事的人，他的归信于他无济。你说，（你们等待吧！我确也在等待着。）","translator":"王静斋"},{"sura":6,"aya":159,"text":"一切割裂宗教而成派系的人，你对他们未负着什么责任；他们的事情惟归到安拉上，而后他对他们宣告他们所作的。","translator":"王静斋"},{"sura":6,"aya":160,"text":"举行一件善事的人。可获类似它的十倍；举行一件恶事的人，仅只照样还报，并不亏损。","translator":"王静斋"},{"sura":6,"aya":161,"text":"你说：（养主确把我导至正道，即正教，正直的易卜拉欣之道。他本不是信偶像的。","translator":"王静斋"},{"sura":6,"aya":162,"text":"你说：（我的礼拜、作祭、生死，纯为安拉——调养众世界的主。","translator":"王静斋"},{"sura":6,"aya":163,"text":"无一和他共同的。我已奉到这样命令了。我是首先顺服的。）","translator":"王静斋"},{"sura":6,"aya":164,"text":"你说：（我拜安拉以外的养主吗?他本是调养万物的。）每个人所作的惟害他自身。负重责的不负别个人的重责，而后你们惟归返自己的养主。他对你们宣告你们所争议的。","translator":"王静斋"},{"sura":6,"aya":165,"text":"他是教你们作地上替代的；他教你们的一部分在种种品级上高超一部分，终至以所赐给的试证你们。你的养主确是忙施刑的。他实是多恕的，特慈的。","translator":"王静斋"},{"sura":7,"aya":1,"text":"阿里甫、俩模、密默、撒得。","translator":"王静斋"},{"sura":7,"aya":2,"text":"这是降给你的经典，你不要因它胸窄；为的是你用它作警告，并教戒众穆民。","translator":"王静斋"},{"sura":7,"aya":3,"text":"你们当追从那由养主上降颁的。不要撇开他另行追随爱友。你们绝少受教。","translator":"王静斋"},{"sura":7,"aya":4,"text":"我灭绝了许多城池。我的刑罚在晚间或当他们午眠的时候突然而至。","translator":"王静斋"},{"sura":7,"aya":5,"text":"我的刑罚发现的时候，他们的呼吁不外是说：（我们实是自亏的了。）","translator":"王静斋"},{"sura":7,"aya":6,"text":"我将必质问一般曾经见过使者的人，我也必质问列使。","translator":"王静斋"},{"sura":7,"aya":7,"text":"我必借着知识叙述给他们。我不是不在的。","translator":"王静斋"},{"sura":7,"aya":8,"text":"当那一天，称衡是确实的。天秤重的人，均是得意的；","translator":"王静斋"},{"sura":7,"aya":9,"text":"天秤轻的人，均是由于不信我的表征自折己身的。","translator":"王静斋"},{"sura":7,"aya":10,"text":"我确教你们安居在地上，并为你们在地上发现生活的路子。你们绝少感念。","translator":"王静斋"},{"sura":7,"aya":11,"text":"我确造化了你们，复又给你们成形。我吩咐众天使向阿丹下拜。除以卜里厮以外，他们逐就下拜了。他原不是下拜一类的。","translator":"王静斋"},{"sura":7,"aya":12,"text":"主说：（我命令你叩头的时候，你被什么阻止了呢?）他说：（我比他优越；你从火上造了我，你从泥上造了他。）","translator":"王静斋"},{"sura":7,"aya":13,"text":"主说：（你从这里下去吧！你不该在这里高傲，你出去吧！你定属卑贱的。）","translator":"王静斋"},{"sura":7,"aya":14,"text":"他说：（求你姑容我，直到复起他们的那一日。）","translator":"王静斋"},{"sura":7,"aya":15,"text":"主说：（你必获得姑容，）","translator":"王静斋"},{"sura":7,"aya":16,"text":"以卜里厮说：（只以你贬我为迷误的，我必为他们坐在你的正道上。","translator":"王静斋"},{"sura":7,"aya":17,"text":"随后，我必自他们前后右左来至他们；你得不到他们多半是感念的。）","translator":"王静斋"},{"sura":7,"aya":18,"text":"主说：（你受贬被逐着自这里下去吧！凡是追随你的人我必以你们一同充满火狱。）","translator":"王静斋"},{"sura":7,"aya":19,"text":"〔我说：）（阿丹哪！你和你的妻居园林吧！你俩随便多食！不要近此树，以免归为自亏的。 ）","translator":"王静斋"},{"sura":7,"aya":20,"text":"而后，恶魔怂恿他俩，卒使他俩被掩的羞体外露了。他说：（养主禁止你俩近这树，只恐你俩成为天使或为永居的。）","translator":"王静斋"},{"sura":7,"aya":21,"text":"他对他俩发誓：（我实是向你俩进忠告的。）","translator":"王静斋"},{"sura":7,"aya":22,"text":"于是，他就骗他两个堕落了。当他俩尝得树的时候，他俩的羞体现露了，他俩开始往身上粘天园的树叶。养主晓谕他俩：（我岂未禁止你俩接近此树吗?我未对你俩说；恶魔是你俩的明敌吗?）","translator":"王静斋"},{"sura":7,"aya":23,"text":"他俩说：（我们的养主啊！我们确曾自亏己身了；你若不饶恕我们，不怜恤我们，我们必属于伤折的了。）","translator":"王静斋"},{"sura":7,"aya":24,"text":"主说：（你们下去吧！你们的一部分是一部分的仇敌。你们在地上获一个居所，并享受到一个时期。）","translator":"王静斋"},{"sura":7,"aya":25,"text":"主说：（你们生在它里，死在它里，并且出自它里。）","translator":"王静斋"},{"sura":7,"aya":26,"text":"阿丹的子孙哪！我已为你们造下遮掩羞体的衣服，和美丽的衣服。敬畏的衣服是最优的。这是安拉的表征，冀使他们受教。","translator":"王静斋"},{"sura":7,"aya":27,"text":"阿丹的子孙哪！你们莫被恶魔所祸，就像他逐你们两个祖先出离园林似的。那时候，他自他两个上夺取衣服，至使他俩的羞体现露了。他和他的同类，于你们看不见他们的地方，能可看见你们。我以众恶魔作一般不信者的爱友。","translator":"王静斋"},{"sura":7,"aya":28,"text":"他们行了丑恶的时候，就说：（我们见到自己的祖先就是这样。安拉以此命令我们了。）你说：（安拉绝不命以丑恶。）你们往安拉上说自所不知的吗?","translator":"王静斋"},{"sura":7,"aya":29,"text":"你说：（我的养主命以公道了。你们礼拜的时候，要正自己的面。你们要竭诚顺服着拜他。你们将依照他造化你们似的而再复返。","translator":"王静斋"},{"sura":7,"aya":30,"text":"他引导了一伙，更有一伙必定迷误的。他们舍去安拉以恶魔们为友，且自以为是得正道的。）","translator":"王静斋"},{"sura":7,"aya":31,"text":"阿丹的子孙哪！你们每逢礼拜的时候，要取自己的装饰。你们吃吧！饮吧！不要滥费。真主实不爱滥费的人。","translator":"王静斋"},{"sura":7,"aya":32,"text":"你说：（安拉为他的众仆发现的装饰品，与清洁的食品，谁严禁它了?）你说：（这是为着一般在今生归信的人；在复生日独享着。）我如此着为一般有知识的人分析各项表征。","translator":"王静斋"},{"sura":7,"aya":33,"text":"你说：（我的养主惟严禁显著的暗昧的丑恶，罪孽，和背义的悖谬；并严禁你们把未给它降过权力的为安拉举匹偶；并严禁你们往安拉上说自所不知的。）","translator":"王静斋"},{"sura":7,"aya":34,"text":"每一伙人都有一定的时期；当他们的时期已至的时候，既不能退后一时，也不能争先。","translator":"王静斋"},{"sura":7,"aya":35,"text":"阿丹的子孙哪！你们同类的列使若来对你们述我的表征了，那敬慎并且自修的人，可得无惧无忧。","translator":"王静斋"},{"sura":7,"aya":36,"text":"不信我的表征而且对它倔强的人，均是永居火狱的。","translator":"王静斋"},{"sura":7,"aya":37,"text":"谁是背义较甚于往安拉上造作谎言，和不信他的表征的人?这般人得到决定的一分，直至我的使者来取命的时候，他们就说：（你们超过安拉所拜的是在哪里?）他们说：（它们离开我们不在了。）他们自证己身原是不信的。","translator":"王静斋"},{"sura":7,"aya":38,"text":"主说：（你们参加在一般先你们过去的人类和镇呢中进火狱吧！）每有一伙进去了，它就咒骂其同类的。迨至他们一同入火狱了，他们后一伙指前一伙说：（我们的养主啊！这般人诱我们迷误了，你给他们加倍的火刑吧！）主说：（各遭加倍的；但是你们不知道。）","translator":"王静斋"},{"sura":7,"aya":39,"text":"他们的前一伙对后一伙说：（你们并不优越我们。你们以自所干过的尝刑罚吧！）","translator":"王静斋"},{"sura":7,"aya":40,"text":"的确，不信我的表征，并对它高傲的人，必不为他们开放天门；非至驼入针眼的时候，他们不得入天园。我如此着还报为恶的人。","translator":"王静斋"},{"sura":7,"aya":41,"text":"他们获得火狱的铺垫，上面且有遮覆的。我如此着还报背义的人。","translator":"王静斋"},{"sura":7,"aya":42,"text":"归信并作善举的人—— 我不课人以其能力以外的，均是永居天园的。","translator":"王静斋"},{"sura":7,"aya":43,"text":"我释去他们胸中的隐恨。诸河流于其下，他们说：（赞颂安拉，那把我们引到这里的主。设不是安拉引导我们了，我们必不能得正道。我们养主的列使确已发布真理了。）（当时）有的晓谕他们：（这是依你们的作为而赐的天园。）","translator":"王静斋"},{"sura":7,"aya":44,"text":"天园的人呼喊火狱的人：（我们已得到养主的约是确实的了；你们可得到养主所约的也是确实的吗?）他们说：（是的）随后，有一宣告者在他们中间宣告：（背义的人当遭安拉的怒恼，","translator":"王静斋"},{"sura":7,"aya":45,"text":"那般人阻碍安拉的道，并给它寻曲折。他们是些不信后世的。","translator":"王静斋"},{"sura":7,"aya":46,"text":"两者之间有一隔障。高位上有些人，他们认识每一伙以其形迹。他们呼喊天园的人：（祝你们平安！）那时候，他们尚未进天园，正在希望着。","translator":"王静斋"},{"sura":7,"aya":47,"text":"他们转眼顾及居火狱的人方面时候，就说：（我们的养主啊！你不要教我们和背义的群众同处！）","translator":"王静斋"},{"sura":7,"aya":48,"text":"高位上的人呼唤能以形迹认识的那一伙人（说：）（你们的集合与倔强不能代为避免。 ）","translator":"王静斋"},{"sura":7,"aya":49,"text":"这般人是你们已曾发誓：（他们是得不到安拉的慈惠）的人吗?你们进天园吧！你们无所惧忧。","translator":"王静斋"},{"sura":7,"aya":50,"text":"火狱的人呼喊天园的人（说：）（请你们把水或把安拉赐给你们的 倾给我们吧！）他们就说：（一般拿着教门当可耍可戏，且被今生所骗的不信者，在安拉绝不准其用这两项了。）","translator":"王静斋"},{"sura":7,"aya":51,"text":"今日，我忘掉他们了，就如他们忘了遇见自己的今日，并否认我的表征。","translator":"王静斋"},{"sura":7,"aya":52,"text":"我确已示给他们经典，我曾依着知识分析它，以作引导并慈惠一般信民。","translator":"王静斋"},{"sura":7,"aya":53,"text":"他们惟等侯它的结果。在它的结果来到的那天，那些先前忘怀的人说：（我们养主的列使确已发布真理了，可有些为我们代求赦罪的，来为我们代求赦罪，或（容）我们复返，而作我们未作过的吗?）他们确已自伤己身了，他们所造作的不在了。","translator":"王静斋"},{"sura":7,"aya":54,"text":"你们的养主，是那在六日间造化了天地，随后操纵[阿勒世]的主，他用夜覆昼，夜在忽忙追昼着，并造化了日月群星，一切奉主的命令受制着。须和惟他掌造化与命令。安拉——调养主众世界的玄高了。","translator":"王静斋"},{"sura":7,"aya":55,"text":"你们当卑谦着暗中呼求自己的养主。真主实不喜过当的人。","translator":"王静斋"},{"sura":7,"aya":56,"text":"在调整地面后，你们不得在它上造恶，你们要骇怕希冀着呼求他。安拉的慈惠实是接近善人的。","translator":"王静斋"},{"sura":7,"aya":57,"text":"他那真主，在其慈悯以前驱动作预告的风。待至风驾重云的时候，我就把它驱至死城。我以云降水，而用它发现各项果实。我如此着现出已死的来，冀使你们受教。","translator":"王静斋"},{"sura":7,"aya":58,"text":"清洁的城池，它那田苗奉其养主的命令发现。污浊的仅只发现些微的。我如此着因为一般感念的人重复各项表征。","translator":"王静斋"},{"sura":7,"aya":59,"text":"我确遣努海往赴他的族人，说：（我的族人哪！你们当拜安拉。在你们无有除过他的主。我实为你们骇怕大日的刑罚。）","translator":"王静斋"},{"sura":7,"aya":60,"text":"他的族人中一般伟人说：（我们看你实是在显著的迷误中。）","translator":"王静斋"},{"sura":7,"aya":61,"text":"他说：（我的族人哪！我未染一些迷误，我只是由众世界的养主上差来的。","translator":"王静斋"},{"sura":7,"aya":62,"text":"我向你们宣达养主的种种消息。我向你们进忠告。我从安拉上知道你们不知道的。","translator":"王静斋"},{"sura":7,"aya":63,"text":"你们养主的教戒达至同类的一个人，好教他警告你们，并为你们敬慎，冀使你们获怜恤，你们对于这事可曾奇异吗?）","translator":"王静斋"},{"sura":7,"aya":64,"text":"但是他们不信他了。于是我解救他，并和他同舟的人们。我淹没了一般不信我的表征的人。他们实是一伙子失目的。","translator":"王静斋"},{"sura":7,"aya":65,"text":"并向阿代遣去他们的弟兄——扈代。他说：（他的族人哪！你们当拜安拉。在你们无有除过他的主。你们怎不敬慎呢?）","translator":"王静斋"},{"sura":7,"aya":66,"text":"他的族人中一般背逆的伟人说：（我们看你实在无有知识，我们揣度你是谎言的。）","translator":"王静斋"},{"sura":7,"aya":67,"text":"他说：（我的族人哪！我未染受无知，我只是由众世界的养主上差来的。","translator":"王静斋"},{"sura":7,"aya":68,"text":"我向你们宣达养主的消息；我是为你们诚实忠告的。","translator":"王静斋"},{"sura":7,"aya":69,"text":"你们养主的教戒，借着一个同类的人达至你们，好教他警告你们，你们对于这事可曾奇异吗?你们当记念那时候，主委你们在努海的族人以后作继替的。他增给你们在体态上的魁悟。你们当记念安拉的恩典，以便你们得到所欲。）","translator":"王静斋"},{"sura":7,"aya":70,"text":"他们说：（你来，为教我们拜独一的安拉而放弃自己列祖所拜的吗?如果你的所言属实，你就向我们实现你的所约！）","translator":"王静斋"},{"sura":7,"aya":71,"text":"他说：（养土的罪行和怒恨已对你们决定了。你们为着自己和列祖所称道的名字对我起争辩吗?安拉原未降给他权力。你们等待吧！我是随同你们等待的。）","translator":"王静斋"},{"sura":7,"aya":72,"text":"随后我以自己的慈惠解救了他，和随同他的人。我断绝一般不信我表征者的后辈。他们不是归信的。","translator":"王静斋"},{"sura":7,"aya":73,"text":"并向赛母待遣他们的弟兄——沙里哈；他说：（我的族人哪！你们当拜安拉。在你们无一除过他的主。养主的明证确已达至你们了。这是安拉的牝驼；对你们的一项表征。你们要任它在安拉的地上吃，不得虐待它，而致自遭痛刑。）","translator":"王静斋"},{"sura":7,"aya":74,"text":"你们回想：那时候，他委你们在阿代以后作替代的。他把你们安插在地上。你们就着软地造宫殿，并就山斫室。你们当记念安拉的恩典；不要在地上意图造恶着妄为。","translator":"王静斋"},{"sura":7,"aya":75,"text":"他族人中一般高傲的伟人，对那一切被列为无能的；其中的归信者说：你们知道沙里哈是被他养主差来的吗?他们说：（我们必信他所负的使命。）","translator":"王静斋"},{"sura":7,"aya":76,"text":"一般高傲的人说：（我们实在否认你们所信的。）","translator":"王静斋"},{"sura":7,"aya":77,"text":"于是他们宰了那只牝驼；他们违背养主的命令了。他们呼沙里哈说：（如果你是被差的，你就向我们实现你的约言，）","translator":"王静斋"},{"sura":7,"aya":78,"text":"于是他们遭遇地震。次晨他们在自己家内伏地而死了。","translator":"王静斋"},{"sura":7,"aya":79,"text":"随后沙里哈离开他们，说：（我的族人哪！我确已向你们宣达我养主的消息了，我对你们进过忠告了，但是你们不喜进忠告的人。）","translator":"王静斋"},{"sura":7,"aya":80,"text":"鲁脱，那时候，他对自己的族人说：（你们发现举世无一人先你们作过的—丑恶吗?","translator":"王静斋"},{"sura":7,"aya":81,"text":"你们为着性欲撇开女子而亲近男子；你们实是妄为的一伙人。）","translator":"王静斋"},{"sura":7,"aya":82,"text":"他族人的答复不外是说：（你们把他们逐出本村：他们实是清白的人。）","translator":"王静斋"},{"sura":7,"aya":83,"text":"于是，我解救他和他的家属了；只有他的妻，归为落后的。","translator":"王静斋"},{"sura":7,"aya":84,"text":"我给他们下（石）雨。看哪！一般为恶的人结果如何?","translator":"王静斋"},{"sura":7,"aya":85,"text":"并向买大严遣去他们的弟兄—书尔布。他说：（我的族人哪！你们当拜安拉。在你们无有除过他的主。养主的明证确已达至你们了。你们当给足了量的称的，不要少给人们一事。 在调整地面以后，你们不要在它上造恶。如果你们是归信的，这是于你们最好的了。","translator":"王静斋"},{"sura":7,"aya":86,"text":"你们不要坐在各路上恫吓人；阻碍信安拉的人接近他的道，并给它寻曲折。你们当记念，那时候你们是少数的，随后他给你们增多了。看哪！一般造恶者的结果如何?","translator":"王静斋"},{"sura":7,"aya":87,"text":"若是你们同人中有一伙人信我所负的使命，又有一伙人未信，你们应当忍耐，待至安拉在我们中间公断。他是至上的公判者。","translator":"王静斋"},{"sura":7,"aya":88,"text":"他的族人中一般高傲的伟人说：（书尔布啊，我们必把你和随你归信的人们逐出城外；或是你们复反我们的教门。）（他说：）设若我们憎恶，也得（复反）吗?","translator":"王静斋"},{"sura":7,"aya":89,"text":"在安拉由那上解救我们以后，如果我们复反你们的教门了，我们确是往安拉上造谎。 除非安拉——调养我们的主意欲……的时候，我们不该复反。我们的养主在知识上包容万事，我们惟仰赖安拉。我们的养主啊！你在我们和我们的族人中间以真理作判断吧！你是最优的判断者。","translator":"王静斋"},{"sura":7,"aya":90,"text":"他的族人中一般不信的伟人说：（你们若随从书尔布了，那时候，你们确是伤折的。）","translator":"王静斋"},{"sura":7,"aya":91,"text":"于是他们遭遇地震了。次晨他们在自己家里伏地而死。","translator":"王静斋"},{"sura":7,"aya":92,"text":"不信书尔布的人，好像是他们未在那里住过。不信书尔布的人，均是伤折的。","translator":"王静斋"},{"sura":7,"aya":93,"text":"而后他离开他们转去了，说：（我的族人哪！我确向你们传达我养主的敕令了；我向你们进忠告了。我何必为着不信的族人伤感呢?","translator":"王静斋"},{"sura":7,"aya":94,"text":"每逢我往一村里遣去一位圣人，我就以痛苦和患难惩治那地方的住民，好叫他们表示卑谦！","translator":"王静斋"},{"sura":7,"aya":95,"text":"我且变灾区为福乐，迨至他们繁殖起来，且说：（我们的列祖确曾遇到痛苦和愉快了。）于是我突然惩治他们，他们尚不知觉着。","translator":"王静斋"},{"sura":7,"aya":96,"text":"设若村里的人归信了，敬畏了，我必为他们开放天地的福祉。但是他们不信了，所以我因他们所作的惩治他们。","translator":"王静斋"},{"sura":7,"aya":97,"text":"村人不怕我的罪刑适值他们熟睡的时候，夤夜而至吗?","translator":"王静斋"},{"sura":7,"aya":98,"text":"村人不怕我的罪刑当他们玩耍着，在早晨到达吗?","translator":"王静斋"},{"sura":7,"aya":99,"text":"他们岂可对于安拉的罪刑安然呢?对于安拉的罪刑安然的，不外是伤折的群众。","translator":"王静斋"},{"sura":7,"aya":100,"text":"在他的居民以后继承地方的人们岂未明白！设使我意欲（毁灭）了，我就因他们的罪恶毁灭他们；我就封闭他们的心，而致听不到。","translator":"王静斋"},{"sura":7,"aya":101,"text":"那些村落，我把他的一部分消息述说给你。列使确把种种表征现给他们了。但是他们绝不信从前所不信的。","translator":"王静斋"},{"sura":7,"aya":102,"text":"安拉如此着封闭众不信者的心。我未见得他们大多数人全约；我曾见得他们大半是作恶的。","translator":"王静斋"},{"sura":7,"aya":103,"text":"在他们以后，我又派母撒藉我的异迹去到匪喇傲和他的伟人那里，但是他们辜负他了。看哪！一般造恶者的结果如何?","translator":"王静斋"},{"sura":7,"aya":104,"text":"母撒唤匪喇傲说：（我实是由调养众世界的主上差来的使者；","translator":"王静斋"},{"sura":7,"aya":105,"text":"是应当只往安拉上说正言的。我确向你们现出养主的异迹了。你释放以斯拉衣来的子孙随我同去！）","translator":"王静斋"},{"sura":7,"aya":106,"text":"匪喇傲说：（如果你能现出一个异迹，你若是实言的了，你就把它现出！）","translator":"王静斋"},{"sura":7,"aya":107,"text":"母撒掷出自己的木杖，那木杖忽然是一条明明的巨蛇。","translator":"王静斋"},{"sura":7,"aya":108,"text":"他拔出手来，那手忽然在一般的眼里看着是洁白的。","translator":"王静斋"},{"sura":7,"aya":109,"text":"匪喇傲党徒中的伟人们说：（这人一定是一个老练的术士；","translator":"王静斋"},{"sura":7,"aya":110,"text":"他要把你们逐出本土去。 你们作何主张）","translator":"王静斋"},{"sura":7,"aya":111,"text":"他们说：（你对他和他弟兄务要迟缓。你把众召募者派到各城里，","translator":"王静斋"},{"sura":7,"aya":112,"text":"他们能把一切老练的术士带到你这里。）","translator":"王静斋"},{"sura":7,"aya":113,"text":"众术士来至匪喇傲说：（若是我们获胜了，我们可得报酬吗?）","translator":"王静斋"},{"sura":7,"aya":114,"text":"他说：（是的，你们且必是接近的。）","translator":"王静斋"},{"sura":7,"aya":115,"text":"众术士呼母撒说：（或你[先]掷，或我们[先]掷?）","translator":"王静斋"},{"sura":7,"aya":116,"text":"他说：（你们掷出吧！）当他们掷出的时候，他们就炫惑众人的眼目，使他们骇惧，现出大的法术了。","translator":"王静斋"},{"sura":7,"aya":117,"text":"我默谕母撒掷出木杖，忽然间它就吞下他们所造作的。","translator":"王静斋"},{"sura":7,"aya":118,"text":"真的决定了，他们所作的归为消失了。","translator":"王静斋"},{"sura":7,"aya":119,"text":"他们就在那里失败而归为卑贱的了。","translator":"王静斋"},{"sura":7,"aya":120,"text":"众术士倒身下拜，","translator":"王静斋"},{"sura":7,"aya":121,"text":"说：（我们归信调养众世界的主，","translator":"王静斋"},{"sura":7,"aya":122,"text":"调养母撒，哈伦的主。）","translator":"王静斋"},{"sura":7,"aya":123,"text":"匪喇傲说：（你们尚未得我许可，就肯归信他吗?这必是你们在城中谋画的诡计，为的是由这里逐出住民去。不久你们就知道！","translator":"王静斋"},{"sura":7,"aya":124,"text":"我必相异着断去你们的手脚，我必把你们一同挂起来。）","translator":"王静斋"},{"sura":7,"aya":125,"text":"他们说：（我们一定是将归到养主的。","translator":"王静斋"},{"sura":7,"aya":126,"text":"你痛恶我们，只因养主的异迹达到的时候，我们信它了。我们的养主啊！你倾给我们忍耐吧！你教我们至死谨守伊斯兰吧！）","translator":"王静斋"},{"sura":7,"aya":127,"text":"匪喇傲党徒中一般伟人说：（你容母撒和他的族人在地面上造恶，并抛弃你和你所拜的吗?）他说：（我必杀死他们的男儿，留下他们的女儿，我实是在他们以上有权威的。）","translator":"王静斋"},{"sura":7,"aya":128,"text":"母撒向自己的族人说：（你们求安拉相助吧！你们忍耐吧！地面确是安拉所掌，他教自所愿意……的仆人继承它。敬畏的人可获善果。）","translator":"王静斋"},{"sura":7,"aya":129,"text":"他们说：（我们在你未到以前，并在你来到以后受扰害了。）母撒说：（养主即将灭绝你们的仇敌，他教你们在地上继位；他看你们怎样去作?）","translator":"王静斋"},{"sura":7,"aya":130,"text":"我确以歉年和减收果实，惩治匪喇敞的党徒，冀使他们受劝。","translator":"王静斋"},{"sura":7,"aya":131,"text":"他们蒙福的时候，就说：（这是我们应该享受的；）他们若遭患难了，就归为母澈暨其同人的不祥。须知他们的不幸只是操自安拉；但是他们的大半不知道。","translator":"王静斋"},{"sura":7,"aya":132,"text":"他们说：（你为炫惑我们，所发观的无论什么异迹，我们绝不信你。）","translator":"王静斋"},{"sura":7,"aya":133,"text":"而后，我往他们上降洪水、蝗虫、扁虱、蛙、血、种种分明的异迹；但是他们高傲了，他们本是一伙罪民。","translator":"王静斋"},{"sura":7,"aya":134,"text":"当他们遇到天灾的时候，就呼母撒说：（你以养主所约许你的，为我们呼求他吧！如果你给我们释去灾害了，我们必定信你；必定释放以斯拉衣来子孙随你同去。）","translator":"王静斋"},{"sura":7,"aya":135,"text":"而后，当我给他们释去灾害，直至他们所达到的期限时候，他们辄就破约了。","translator":"王静斋"},{"sura":7,"aya":136,"text":"于是我报复他们，我把他们淹在深海里，只因他们不信我的表征，并对它忽视。","translator":"王静斋"},{"sura":7,"aya":137,"text":"我教无能为的一伙人继承我曾降福的东西各方面。你养主的善言在以嘶拉衣来的子孙上，只因他们的忍耐已经实现了。我灭绝了匪喇傲和他的党徒所造作的， 与他们所架起的。","translator":"王静斋"},{"sura":7,"aya":138,"text":"我教以斯拉衣来的子孙渡过海去，而到达一伙子坚守偶像的人；他们呼母撒说：（求你为我们安置一个受拜的，就像它们有些受拜的。）母撒说：（你们实是一伙子愚民。）","translator":"王静斋"},{"sura":7,"aya":139,"text":"这般人其所守的一定是将要败坏的。 他们的功修归为失效。","translator":"王静斋"},{"sura":7,"aya":140,"text":"母撤说：（我给你们寻觅安拉以外的主吗?他本教你们优越世人了。）","translator":"王静斋"},{"sura":7,"aya":141,"text":"那时候，我救你们脱离匪喇傲的党徒；他们对你们施恶刑，杀死你们的男儿，留下你们的女儿；其中含有出自养主一大试验。","translator":"王静斋"},{"sura":7,"aya":142,"text":"我与母撒相约三十夜；我以十夜完成了它。他养主的约期在四十夜上告终了。母撒对自己的弟兄——哈伦说：你在族人中替代我：你要整理，不得随从一般造恶的人的道路。","translator":"王静斋"},{"sura":7,"aya":143,"text":"在母撤如期来到，养主对他交言的时候，他就说：（我的养主啊！求你向我显现而能看见你！）主说：（你绝看不见我，虽然你观山吧！如果它隐定在原处，不久你就看见我。）当养主向山显观的时候，就把山粉碎；母撒昏晕着跌倒了。待他苏醒过来的时候，就说：（赞你清净！我向你悔罪了，我是首先归信的。）","translator":"王静斋"},{"sura":7,"aya":144,"text":"主呼母撒说：（我以自己的敕令并以亲自交言撇弃众人而选择你了。你接受我赐给你的，你当列入感念的人一流，）","translator":"王静斋"},{"sura":7,"aya":145,"text":"我把各项事务——劝化和分析诸事，为他记在版片上；我命以坚守它；并吩咐自己的族人，守其最优者。我即将教你们居在恶人的第宅。","translator":"王静斋"},{"sura":7,"aya":146,"text":"我即将教那在地上妄自高傲的人们离开我的表征。他们若看见各项表征了，则不信它。他们若看见正道了，则不肯拿它当道路。他们若看见迷途了，就拿它当道路。这是因为他们不信我的表征，并对它忽视。","translator":"王静斋"},{"sura":7,"aya":147,"text":"一切不信我的表征与不信见后世的人，他们的功修失效了，他们只因着所作的遭还报。","translator":"王静斋"},{"sura":7,"aya":148,"text":"母撒的族人在他（去）后，用自己的饰品制一牛犊，就是一个发声的躯体。他们岂未见它不能和他们交谈，亦不能向他们指示一个途径吗?他们供奉它，他们本是自亏的。","translator":"王静斋"},{"sura":7,"aya":149,"text":"当他们备极懊丧，亲见自己迷误的时候，就说：（养主若不加怜恤和饶恕，我们必归为伤折的人了。）","translator":"王静斋"},{"sura":7,"aya":150,"text":"当母撒震怒忧愁着回到自己的族人时候，他就说：（你们在我以后，所代行的太恶了！ 你们对养主的命令何至提前呢?）他就摔了版片，抓住他弟兄的头（发，）把他扭至自己。他弟兄说：（母亲的儿子啊！族人们视我无能为；他们几乎杀了我。你莫拿我使仇人称快；你不要把我列在背义的族人一流。）","translator":"王静斋"},{"sura":7,"aya":151,"text":"母撒说：（我的养主啊！你饶恕我和我的弟兄，你教我们人在你的怜恤中，你是慈中最慈者，）","translator":"王静斋"},{"sura":7,"aya":152,"text":"的确，拜牛犊的人，即将遭到养主的怒恼和今生的侮辱。我如此还报造作的人心。","translator":"王静斋"},{"sura":7,"aya":153,"text":"一般作了过错，嗣后，复又悔罪，归信的人，在这以后，你的养主，一定是多恕的，特慈的。","translator":"王静斋"},{"sura":7,"aya":154,"text":"当母撒怒恨已息的时候，他就拾起版片，那上所记的，有引导和怜恤一般畏惧养主的人。","translator":"王静斋"},{"sura":7,"aya":155,"text":"母撒届时由自己的族人中选择七十人。而后，当他们遭地震的时候，母撒就说：（我们的养主啊！设若你意欲（灭绝）了，你就在这以前把他们和我灭绝了。你因我们之中一般无识者的行为灭绝我们吗?这只是你的试证。你以这事随便造人迷误；你随便引人正道。你是爱护我们的。你饶恕我们吧！你慈悯我们吧！你是无上的饶恕者。","translator":"王静斋"},{"sura":7,"aya":156,"text":"你在今世并在后世为我们制定幸福吧！我们确归向你了。主说：我把刑罚施行在自所意欲（罪刑）的人上；我的慈惠包容万物。我即将为那般敬慎。施天课，并信我表征的人制定它。","translator":"王静斋"},{"sura":7,"aya":157,"text":"那般人追从不文的使者圣人；他们得见他被记在自守的讨喇忒、引支勒上。他对他们命以合法的，禁止非理的；他许可他们用清洁的。严禁他们用不洁的。他给他们卸下重负，与他们所带的桎梏。一般信他，尊敬他，相助他，并追从所随他降下的光辉者，均是得意的人。","translator":"王静斋"},{"sura":7,"aya":158,"text":"你告诉众人说：我是安拉向你们大众差来的。天地的权柄惟他执掌。除他以外再无有主。他使生使死。你们当归信安拉和他的使者，就是那信安拉并信他所言的那位不文的圣人。你们当追随他，冀使你们得正道。","translator":"王静斋"},{"sura":7,"aya":159,"text":"母撤的族人中有一伙子人指示真理，并依真理作判断。","translator":"王静斋"},{"sura":7,"aya":160,"text":"我把他们分为十二族——一伙一伙的。我曾款示母撤！那时候他的族人向他求水；（你用自己的木杖击石吧！）于是十二泉源自那里涌出来；诸人各识其用水的地方。我以白云给他们作遮蔽。我降给他们甘露蜜与鹑鸟（我说：）（你们吃我所赐给你们清洁的，）他们来亏负我；只是自亏己身了。","translator":"王静斋"},{"sura":7,"aya":161,"text":"那时候，有人对他们说：（你们进此城吧！你们由那里随意多食；你们说（痕大）你们俯首入门！我就恕过你们的差错。","translator":"王静斋"},{"sura":7,"aya":162,"text":"我即将增加给为善的人。而后他们之中一般背义的人，改换对他们所说的话。我因他们的背义给他们降下天灾了。","translator":"王静斋"},{"sura":7,"aya":163,"text":"你向他们问那沿海的村落，那时候，他们在安息日过当。那时候，他们的鱼在安息日浮水而来，在非安息的那天，鱼就不来。我以他们的作恶，如此着试证他们。","translator":"王静斋"},{"sura":7,"aya":164,"text":"那时候，他们的一伙人说：（为什么你们劝那将遭安拉的灭绝或受他严惩的一伙人呢?）他们就说：（原为告无罪于养主，并俾便于他们敬滇。）","translator":"王静斋"},{"sura":7,"aya":165,"text":"当他们忘却所用作劝他们的时候，我解救一般禁止作恶的人，我对一般背义的人，因为他们作恶而施以严刑。","translator":"王静斋"},{"sura":7,"aya":166,"text":"而后当他们违背禁令时，我语他们变成了卑贱的猴。","translator":"王静斋"},{"sura":7,"aya":167,"text":"那时候，你的养主宣示：必向他们派去使其尝试烈刑的人，直到复生日为止。你的养主实是惩罚神速的，他实是多恕的，特慈的。","translator":"王静斋"},{"sura":7,"aya":168,"text":"我就教他们在地上分为一伙一伙的。他们之中有义人；他们之中有除此以外的。我以种种福祸试证他们，好教他们反回。","translator":"王静斋"},{"sura":7,"aya":169,"text":"后来的人在他们以后继替了。他们继承经典，他们取今世的利益；且说：（我们即将得到饶恕。他们若得到和它一样的利益了，他们就接受它。岂未向他们取得经上的约，就是莫往安拉上言真理以外的吗?他们已读过那经上所记的。后世最益于一般敬慎的人。你们怎不了解呢?","translator":"王静斋"},{"sura":7,"aya":170,"text":"且是（最益）于一般抓住经典且立拜功的人。我绝不湮没一般正直的人的报酬。","translator":"王静斋"},{"sura":7,"aya":171,"text":"那时候，我起山于他们上面，好象它是一个遮盖；他们认为那是将落到自己上的。（我说：）你们尽力接受我所赐给的经典，你们要记念内中所载的，冀使你们敬慎。","translator":"王静斋"},{"sura":7,"aya":172,"text":"那时候，你的养主自阿丹的子孙的脊背上，取出他们的后代来；真主命他们自作见证，（说：）（我不是你们的养主吗?）他们说：（是的，我们见证了。）（我行那事，免得）你们在复生日说：（我们对于这个上忽略了；）","translator":"王静斋"},{"sura":7,"aya":173,"text":"或是说：（惟有我们以前的列祖信匹偶了，我们是他们以后的子孙；你肯因为一般虚浮的人所作的灭绝我们吗?）","translator":"王静斋"},{"sura":7,"aya":174,"text":"我如此分析种种表征，冀使他们反回。","translator":"王静斋"},{"sura":7,"aya":175,"text":"你对他们 诵那么一个人的故事！我赐给他表征，而后他摆脱了它，恶魔追随他，他终归为迷误的人了。","translator":"王静斋"},{"sura":7,"aya":176,"text":"设若我意欲……了，我必藉此 把他抬高。但是他趋向低处，随从自己的偏好了。他的情形如同狗的情形；你若驱逐它，它就垂下舌来；你若弃置它，它也垂下舌来。这是不信我表征的那一伙人的情形。你述说故事吧！冀使他们参悟。","translator":"王静斋"},{"sura":7,"aya":177,"text":"不信我表征并自负己身的那伙人，在情况上太恶了！","translator":"王静斋"},{"sura":7,"aya":178,"text":"得到安拉引导的人，就是获正道的。被造为迷误的人，均是伤折的。","translator":"王静斋"},{"sura":7,"aya":179,"text":"我为火狱造化了许多镇呢和人类。他们有心而不用它解，有眼而不用它看，有耳而不用它听。这般人如同是畜类，且比较地更是迷误。这般人均是昏愦的。","translator":"王静斋"},{"sura":7,"aya":180,"text":"安拉具有极美的名称。你们就用它称呼他吧！你们丢开一般妄拟他的尊名的人。即将对他们报以他们所作的。","translator":"王静斋"},{"sura":7,"aya":181,"text":"我造化的人类中有一伙人，导以真理，并依真理作公判。","translator":"王静斋"},{"sura":7,"aya":182,"text":"不信我表征的人，我即将于他们不知道的地方，教他们渐渐地堕落下去，","translator":"王静斋"},{"sura":7,"aya":183,"text":"我姑容他们，我的惩罚实是坚强的。","translator":"王静斋"},{"sura":7,"aya":184,"text":"他们岂未参悟：（自己的同人并无疯癫）吗?他只是一个公开传警告的。","translator":"王静斋"},{"sura":7,"aya":185,"text":"他们岂未观察天地的大国，与安拉所造的物类吗?岂未参悟自己的大限，即要临近了吗?他们信它以外的什么话呢?","translator":"王静斋"},{"sura":7,"aya":186,"text":"安拉造成迷误的，无人引导他。他放任他们在迷路中徘徊着。","translator":"王静斋"},{"sura":7,"aya":187,"text":"他们对于复生日上问你：它的决定是在何时? 你说：（惟我的养主知道它。惟有真主届时发现它。那在天地间重大了！它只是骤然而至。）他们问你，好像你是深知道它的。你说：（惟有安拉知道它。但是大多数人不晓得。）","translator":"王静斋"},{"sura":7,"aya":188,"text":"你说：除却安拉意欲……的以外，我不能为自身主利害。设若我知道目不能见的，我就广聚利益，我也不至遭患难了。我只是一个向归信的群众传警告报喜信的。","translator":"王静斋"},{"sura":7,"aya":189,"text":"他那真主，从一个人上造化了你们， 并自他上发现他的妻，以便他和她安居。当他与她交合的时候，他就怀上轻孕而负它往返。待她感觉沉重的时候，他俩就呼求安拉调养他俩的主：（如果你赐给我们一个貌美的儿子，我们必定感念。）","translator":"王静斋"},{"sura":7,"aya":190,"text":"当他把一个貌美的赐给他俩了，他俩就以为有与真主共同赐给他俩的。安拉玄高于他们所举的匹偶。","translator":"王静斋"},{"sura":7,"aya":191,"text":"他们肯举那不能造一物而是被造的为匹偶吗?","translator":"王静斋"},{"sura":7,"aya":192,"text":"偶像不能相助他们，也不能自助。","translator":"王静斋"},{"sura":7,"aya":193,"text":"你们若导他们于正道了，它们不随从你们，勿论你们唤他们，或对他们缄默。","translator":"王静斋"},{"sura":7,"aya":194,"text":"的确，你们超过安拉所呼求的那一切是你们同类的仆辈。如果你们的所言属实了，你们就呼求它们，令它们应承你们。","translator":"王静斋"},{"sura":7,"aya":195,"text":"它们有藉资步行的脚吗?不然，它-们有藉资运用的手吗?不然，它们有藉资观看的眼吗?不然，它们有藉资闻听的耳吗?你说：任你们呼自己的同伙，然后你们对我。施计谋，不要姑容我。","translator":"王静斋"},{"sura":7,"aya":196,"text":"爱护我的，确就是那降经的安拉，他爱一切义人。","translator":"王静斋"},{"sura":7,"aya":197,"text":"你们舍去真主所拜的那一切，既不能相助你们，也不能自助。","translator":"王静斋"},{"sura":7,"aya":198,"text":"你若把它们唤至正道了，它们听不到。你看它们注视你，它们无所见。","translator":"王静斋"},{"sura":7,"aya":199,"text":"你当保持容让！你要命以合理的。你当远避一般无知识的人。","translator":"王静斋"},{"sura":7,"aya":200,"text":"若是出自恶魔的诱惑鼓动你了，你就求安拉保佑吧！他实是能听的，深知的。","translator":"王静斋"},{"sura":7,"aya":201,"text":"的确，敬畏的人，一旦遇到恶魔的诱惑，他们就想起来了。他们立刻是明见的。","translator":"王静斋"},{"sura":7,"aya":202,"text":"他们的弟兄助长他们迷误，且不怠慢。","translator":"王静斋"},{"sura":7,"aya":203,"text":"你未把异迹现给他们的时候，他们说：（你何不自选异迹呢?）你说：（我只追从养主所告谕我的。）这是出自养主的明证。并引导与慈惠一伙归信的人！","translator":"王静斋"},{"sura":7,"aya":204,"text":"有人诵古兰的时候，你们要向它侧耳静默，冀使你们蒙慈惠。","translator":"王静斋"},{"sura":7,"aya":205,"text":"你在自己心里朝夕表示卑谦、恐惧、语声勿高着，记念自己的养主。你不要昏愦 。","translator":"王静斋"},{"sura":7,"aya":206,"text":"的确，在你养主御前的那一切人，他们对于拜主上不高傲；他们颂他清净，他们惟向他叩头。","translator":"王静斋"},{"sura":8,"aya":1,"text":"他们对于战利品上问你，你就说：战利品归属安拉与使者。你们当畏惧安拉，当规正同人的情况。你们当顺从安拉与他的钦差。如果你们是归信的了……·","translator":"王静斋"},{"sura":8,"aya":2,"text":"众穆民就是：有人记念安拉时，其心骇惧，有人向其诵主的表征时，益增归信，并且依赖养主的那一切人；","translator":"王静斋"},{"sura":8,"aya":3,"text":"他们立拜功，并就着我所供给的作使用。","translator":"王静斋"},{"sura":8,"aya":4,"text":"他们均是真穆民。他们在其养主御前获得阶级、饶恕、与高贵的给养。","translator":"王静斋"},{"sura":8,"aya":5,"text":"就如你的养主因着正义教你出离自己家中。确有一部分穆民是表示厌恶的。","translator":"王静斋"},{"sura":8,"aya":6,"text":"他们在明白以后对于正义上和你起争辩；好像是他们眼看着被驱至死亡。","translator":"王静斋"},{"sura":8,"aya":7,"text":"那时候，安拉向你们许下两伙的一伙，属于你们控制。你们盼望无有武备的归你们控制。安拉欲以自己的命令奠定正教，并根绝不信者的后辈，","translator":"王静斋"},{"sura":8,"aya":8,"text":"为的是巩固真教，破坏虚伪，纵令为恶的人们厌恶。","translator":"王静斋"},{"sura":8,"aya":9,"text":"那时候，你们求养主的救助，他遂就应承你们！我必以一千有接应的天使援助你们。","translator":"王静斋"},{"sura":8,"aya":10,"text":"安拉就以此作为喜信，并为你们的心藉此得到安定。相助只是由安拉那里。安拉实是优胜的明哲的。","translator":"王静斋"},{"sura":8,"aya":11,"text":"那时候，他以假眠——就是所赐的安静遮覆你们，并自上空往你们上降水，为的是用它洁净你们，并自你们上去掉恶魔的污秽，更为助长你们的心，并用它稳定脚步。","translator":"王静斋"},{"sura":8,"aya":12,"text":"那时候，你养主告谕众天使：（我是和你们同在的，你们要安定众穆民。我即将把畏怯投在不信者的心中，要你们打在脖子上面，要你们打他们的各个手指。","translator":"王静斋"},{"sura":8,"aya":13,"text":"这是因他们反抗安拉和他的使者。反抗安拉和他使者的人，安拉实是掌烈刑的。","translator":"王静斋"},{"sura":8,"aya":14,"text":"这个，以及逆徒们遭火刑（是主的判定，）你们自应尝试它。","translator":"王静斋"},{"sura":8,"aya":15,"text":"众归信的人哪！你们遇见逆徒蠕动的时候，不得掉过背去。","translator":"王静斋"},{"sura":8,"aya":16,"text":"是日，除非为着格杀匿在一傍，或会合同伙外，凡是掉过背去的人，他确遭受安拉的恼恨着转回。他的居处是火狱，归所太恶了！","translator":"王静斋"},{"sura":8,"aya":17,"text":"你们未曾杀他们，只是安拉杀他们了。在你射击的时候，你未射击，只是安拉射击了。真主为的是亲自赠给众穆民以优上的赏赐，安拉实是能听的，深知的。","translator":"王静斋"},{"sura":8,"aya":18,"text":"就是这样。安拉是使逆徒的诡计归为薄弱的。","translator":"王静斋"},{"sura":8,"aya":19,"text":"若是你们求相助，你们已然得到相助了。若是你们停止，那是于你们至好的。若是你们复反了，我就复反。你们的同伙虽多，而绝不能代你们避免一事。安拉是和众穆民同在的。","translator":"王静斋"},{"sura":8,"aya":20,"text":"众归信的人哪！你们当顺从安拉和他的使者。你们既已听到的时候，就不要离开他了。","translator":"王静斋"},{"sura":8,"aya":21,"text":"你们不可类如自称听见，并不侧耳的那般人。","translator":"王静斋"},{"sura":8,"aya":22,"text":"的确，动物中最恶劣的，在安拉认为是那无有理解的聋子，哑子。设若安拉知他们有好处了，他必教他们听得。","translator":"王静斋"},{"sura":8,"aya":23,"text":"设若他教他们听到，他们一定远避而扭转了。","translator":"王静斋"},{"sura":8,"aya":24,"text":"众归信的人哪！你们当应承安拉并应承使者！当他唤你们趋向使你们灵动的事情的时候， 你们当知安拉隔于人与他的心间，并当知你们将被集至他。","translator":"王静斋"},{"sura":8,"aya":25,"text":"你们当畏惧那不仅临到背义的人上的祸患。你们当知道安拉是掌烈刑的。","translator":"王静斋"},{"sura":8,"aya":26,"text":"你们当记念那时候！你们是在地上无能为的少数，你们恐怕众人劫掠你们，但是他安插你们，他以自己的相助援救你们，并把一部分美品赐给你们，好教你们感念。","translator":"王静斋"},{"sura":8,"aya":27,"text":"众归信的人哪！你们不得与安拉和使者背信；你们也不得明知着瞒昧彼此间的信托。","translator":"王静斋"},{"sura":8,"aya":28,"text":"你们当知道资财儿女只是一种试证；并当知道安拉具有重赏。","translator":"王静斋"},{"sura":8,"aya":29,"text":"众归信的人哪！若是你们敬畏安拉了，他就为你们发现一种判别力，并为你们遮蔽种种罪恶，并且饶恕你们。安拉是有大恩的。","translator":"王静斋"},{"sura":8,"aya":30,"text":"那时候，逆徒们对你施计谋，为的是拘禁你，或杀害你，或驱逐你。他们弄计谋，安拉报复计谋。安拉最善于报复计谋。","translator":"王静斋"},{"sura":8,"aya":31,"text":"有人向他们诵我的表征时候，他们就说：（我们却已听到了，设若我们意欲（说出）了，我们定能说出跟这一样的。这只是前人的故事。）","translator":"王静斋"},{"sura":8,"aya":32,"text":"那时候，他们说：（主啊！如果这是出你跟前确实的，你就向我们雨天上的石头，或是把痛刑现给我们。）","translator":"王静斋"},{"sura":8,"aya":33,"text":"有你在他们中间，安拉绝不罪刑他们；他们正在求恕着，安拉不罪刑他们。","translator":"王静斋"},{"sura":8,"aya":34,"text":"他们阻碍入禁寺，安拉焉能不罪刑他们呢?他们不是相宜它的，相宜它的，只有一般敬慎的人，但是他们的大半不知道。","translator":"王静斋"},{"sura":8,"aya":35,"text":"他们在天房跟前的礼拜，只是吹哨与鼓掌。你们由于不信尝试刑罚吧！","translator":"王静斋"},{"sura":8,"aya":36,"text":"的确，一切不信的人施舍自己的资财，只为阻碍安拉的道，他们即将使用它。但是那是贻他们懊丧的，他们终归失败。不信的人将被集至火狱，","translator":"王静斋"},{"sura":8,"aya":37,"text":"而致安拉使污浊的从清洁的上离开，他把污浊的彼此集合起来，统通堆起，而置在火狱中；这一般均是伤折的。","translator":"王静斋"},{"sura":8,"aya":38,"text":"你对不信的人说：（若是他们停止了，则就饶恕他们那以往的；若是他们复反了，前人的常例确已过去。）","translator":"王静斋"},{"sura":8,"aya":39,"text":"你们当对他们格杀，直到祸患消灭，教门是因为安拉。若是他们停止了，安拉看得见他们的作为。","translator":"王静斋"},{"sura":8,"aya":40,"text":"若是他们不肯了，你们当知道安拉是你们的主宰这主宰太好了！这相助者太好了！","translator":"王静斋"},{"sura":8,"aya":41,"text":"你们当知道自所掳获的不拘何物，它的五分之一，归安拉，归使者，归亲属、孤儿、贫人、旅客。若是你们归信安拉，与我在判别之日，……两伙人遭遇之日所降给自己奴仆的了……。安拉是全能于万事的。","translator":"王静斋"},{"sura":8,"aya":42,"text":"那时候，你们是在近处，他们是在远方，驼队是在你们下面。设若你们彼此约定了， 你们一定爽约。但是，安拉为的是完成必须实现的事情；为的是灭亡的在经见明证以后灭亡。 生存的在经见明证以后生存。安拉实是能听的深知的。","translator":"王静斋"},{"sura":8,"aya":43,"text":"那时候，安拉教你在睡梦中看见他们是少数的。设若他教你瞧着他们是众多的了，你们一定胆怯。并对于事情上相争持了。但是安拉曾赐以安静了。主确深知胸中所怀的。","translator":"王静斋"},{"sura":8,"aya":44,"text":"那时候，就是你们相遇的时候，真主教你们在自己眼里看见他们是少数的；并教你们在他们眼里看成少数的。终至安拉完成必须实现的事情，凡是惟被归到安拉上。","translator":"王静斋"},{"sura":8,"aya":45,"text":"众归信的人哪！你们遇见一伙人的时候，要得稳定，要多记念安拉，冀使你们达到所欲。","translator":"王静斋"},{"sura":8,"aya":46,"text":"你们当顺从安拉和他的使者。你们不要纷争，而致发生怯弱，失掉自己的威风。你们要忍耐，安拉实是与忍耐的人同在的。","translator":"王静斋"},{"sura":8,"aya":47,"text":"你们不要像那骄狂，并卖弄于人，阻碍安拉之道着出离居宅的人。安拉尽悉他们的作为。","translator":"王静斋"},{"sura":8,"aya":48,"text":"那时候，恶魔装饰他们的作为，说：（今日，无人能胜过你们；我是一定援救你们的。）及至两伙人相见的时候，他就退转了；说：（我是与你们无干的，我实看见你们看不见的。我确骇怕安拉。安拉是掌烈刑的。）","translator":"王静斋"},{"sura":8,"aya":49,"text":"那时侯，众伪信士对心里有病的人说：（这般人被自己的教门所误了。）仰赖安拉的人，安拉实是优胜的明哲的。","translator":"王静斋"},{"sura":8,"aya":50,"text":"众天使殴打逆徒的面部与脊背着取去性命，并教他们尝焚刑","translator":"王静斋"},{"sura":8,"aya":51,"text":"（说：）这是由于你们先前亲手作下的，并由于安拉绝不迫害众仆，到那时候，如果你看见了，……。","translator":"王静斋"},{"sura":8,"aya":52,"text":"就像匪喇傲的党徒与他们以前众人的惯例。他们不信安拉的表征，而后安拉因他们的罪恶惩治他们了。安拉实是大能的，掌烈刑的。","translator":"王静斋"},{"sura":8,"aya":53,"text":"这是由于安拉对于任何一伙人，非至他们变更自己的态度后，始肯变更对他们的恩典。安拉实是能听的深知的。","translator":"王静斋"},{"sura":8,"aya":54,"text":"就像匪喇傲的党徒，与他们以前众人的惯例。他们不信养主的表征，而后我因他们的罪恶灭绝他们了，我淹没匪喇傲的党徒了。统都是背义的。","translator":"王静斋"},{"sura":8,"aya":55,"text":"的确，动物中最下等的，在安拉认为是一般不信的人，所以他们不归信。","translator":"王静斋"},{"sura":8,"aya":56,"text":"那就是你与他们结约，而后他们迭次违约，并不敬慎的那般人。","translator":"王静斋"},{"sura":8,"aya":57,"text":"你若在战场上遇到他们了，当以他们冲散其后面的人！冀使他们受教。","translator":"王静斋"},{"sura":8,"aya":58,"text":"如果你对于一伙人恐有欺诈了，你就一律地对他们解约；安拉实不喜欺诈的人。","translator":"王静斋"},{"sura":8,"aya":59,"text":"逆徒们勿疑为自己得到超越了，他们必不能作抵抗。","translator":"王静斋"},{"sura":8,"aya":60,"text":"你们要对他们预备所能的实力，与拴系的马匹；你们用它威吓安拉的仇敌，也是你们的仇敌；并有他们以外的另一伙人，你们不知道他们。安拉知道。你们用在安拉之道的不拘何物，将完全还给你们，并不亏损。","translator":"王静斋"},{"sura":8,"aya":61,"text":"若是他们偏于讲和了，你就偏于讲和，你当仰赖安拉，他实是能听的深知的。","translator":"王静斋"},{"sura":8,"aya":62,"text":"他们若欲欺骗你了，安拉实是使你满足的。他是以自己的相助，并借着众穆民相助你，并使他们心中融洽的。","translator":"王静斋"},{"sura":8,"aya":63,"text":"设若你用尽地上所有的一切了，你不能使他们心中融洽，但是安拉使他们内部融洽了。真主实是优胜的明哲的。","translator":"王静斋"},{"sura":8,"aya":64,"text":"圣人哪！安拉和追随你的众穆民是使你满足的。","translator":"王静斋"},{"sura":8,"aya":65,"text":"圣人哪！你要鼓励众穆民格斗！你们之中若有二十个坚忍的，他们就战胜二百。你们之中若有一百，他们就战胜千个逆徒。只因他们是一伙不解悟的。","translator":"王静斋"},{"sura":8,"aya":66,"text":"而今，安拉给你们减轻了。 他知道你们单薄。 你们之中若有一百坚忍的，他们就战胜二百。你们之中若有一千，他们就藉安拉的相助战胜二千。安拉是和忍耐的人同在的。","translator":"王静斋"},{"sura":8,"aya":67,"text":"圣人非至在地面上鏖战以后，不应享受俘囚。你们爱今生的福分。安拉爱后世。安拉是优胜的明哲的。","translator":"王静斋"},{"sura":8,"aya":68,"text":"设不是由安拉上先前规定了，你们必因着曾所取得的遭受巨刑了。","translator":"王静斋"},{"sura":8,"aya":69,"text":"你们就着所获的战利品吃清洁合法的吧！你们当敬畏安拉。安拉实是多恕的，特慈的。","translator":"王静斋"},{"sura":8,"aya":70,"text":"圣人哪！你对手内被俘的人说：（若是安拉知道你们心中有好了，他就从你们上拿了去再把较好的赐给你们，并要饶恕你们，安拉是多恕的，特慈的。）","translator":"王静斋"},{"sura":8,"aya":71,"text":"若是他们欲对你背信了，从前他们确对安拉背过信。而后他教你胜过他们。安拉是深知的明哲的。","translator":"王静斋"},{"sura":8,"aya":72,"text":"的确，一般归信、迁居、用自己的财产生命，在安拉之道上奋斗的人，和一般安排住所，并予以扶助的人，他们是彼此一个维护一个的。一般归信而未迁移的人，非至他们迁移以后，你们不要维护他们一事。若是他们为着教门求你们相助了，你们自当相助：除非对于彼此之间有约的那一伙人。安拉看得见你们所作的。","translator":"王静斋"},{"sura":8,"aya":73,"text":"逆徒们是彼此一个维护一个的。若是你们不实行去作，地上就发生巨祸与大坏了。","translator":"王静斋"},{"sura":8,"aya":74,"text":"一般归信、迁居、并在安拉之道上奋斗的人，和一般安排住居，并予以扶助的人，均是真归信的。他们可获饶恕与高贵的给养。","translator":"王静斋"},{"sura":8,"aya":75,"text":"一般在此以后归信、迁居、并随同你们奋斗的人，均属你们一类的。有血亲关系的人，在安拉的经典上是彼此一部分最宜于一部分的。安拉实是深知万事的。","translator":"王静斋"},{"sura":9,"aya":1,"text":"这是安拉和他的使者向你们相与结约之拜偶像的人们断绝关系。","translator":"王静斋"},{"sura":9,"aya":2,"text":"你们在地上游行四个月吧！须知你们不是能抵抗安拉的。（须知）安拉是使众逆徒现丑的。","translator":"王静斋"},{"sura":9,"aya":3,"text":"（这）是安拉和他的使者在最大的朝觐日， 向世人昭示：安拉和他的使者是与拜偶像的人们断绝关系的。如果你们悔过，那是于你们至好的了。若是你们不肯了，须知你们不是能抗安拉的。你把痛刑报告给逆徒们，","translator":"王静斋"},{"sura":9,"aya":4,"text":"除却你们相与结约，他们不复稍有破坏，也未协助一人对付你们的那般拜偶像的人。你们当对他们全约，直到期满为止。安拉实爱喜敬慎的人！","translator":"王静斋"},{"sura":9,"aya":5,"text":"禁月告终的时候，你们当随地杀死一般拜偶像的，当捕捉而拘系他们，并为他们坐在各路口。如果他们忏悔，并举拜功散天课了，你们就放开他们的路途。 安拉实是多恕的特慈的。","translator":"王静斋"},{"sura":9,"aya":6,"text":"若是一个拜偶像的人求你保护了，你就保护他，待他闻得安拉的话。而后，你就把他解送到平安的地方。这是因他们为一伙子无知的人。","translator":"王静斋"},{"sura":9,"aya":7,"text":"除却你们在禁寺跟前相与结约的众人以外，拜偶像的人们哪能守安拉和他使者的约呢?他们对你们守正的时候，你们要对他们守正。安拉实爱喜敬慎的人。","translator":"王静斋"},{"sura":9,"aya":8,"text":"焉能呢? 他们若战胜你们了，则对你们不顾戚谊，也不顾约言；他们口称爱喜你们，其心不肯。他们的大半是作恶的。","translator":"王静斋"},{"sura":9,"aya":9,"text":"他们借着安拉的表征换取稍微的代价。他们阻碍主道。的确他们所作的太恶了！","translator":"王静斋"},{"sura":9,"aya":10,"text":"他们对于穆民不顾戚谊，也不顾约言。这般人均是过当的。","translator":"王静斋"},{"sura":9,"aya":11,"text":"若是他们悔罪并立拜功散天课了，便就是你们的教胞。我为一般有知识的人分析种种表征。","translator":"王静斋"},{"sura":9,"aya":12,"text":"如果他们在结约以后破约，并毁谤你们的教门了，你们就当对那不信的袖领格杀！为的是他们停止。他们实无条约。","translator":"王静斋"},{"sura":9,"aya":13,"text":"破约并欲逐出使者，且首先向你们发动的人，你们何不与他们格杀呢? 你们怕他们吗?如果你们是归信的了，安拉是你们应当惧怕的。","translator":"王静斋"},{"sura":9,"aya":14,"text":"你们当对他们格杀，安拉就藉你们的手来罪刑他们，使他们现丑。相助你们对付他们；并医治穆民大众的胸，","translator":"王静斋"},{"sura":9,"aya":15,"text":"去掉它们心中的忧虑。安拉允他意欲……的人悔罪。安拉是深知的明哲的。","translator":"王静斋"},{"sura":9,"aya":16,"text":"安拉尚未分清你们之中（为教）奋斗，且未撇开安拉和使者与众穆民结秘友的人，你们就居然以为不负责任了吗? 安拉尽知你们的作为。","translator":"王静斋"},{"sura":9,"aya":17,"text":"自证不信着，拜偶像的人，他们的修葺拜主的处所，不为相宜。这般人的功修归为失效了。他们永居在火狱里。","translator":"王静斋"},{"sura":9,"aya":18,"text":"惟有归信安拉与末日，并立拜功施天课，惟独惧怕安拉的人，修葺拜主的处所，这般人可望属于得正道的。","translator":"王静斋"},{"sura":9,"aya":19,"text":"你们以供给朝觐者饮料，并修葺禁寺当若归信安拉与末日，并在安拉之道上奋斗的人吗?他们在安拉御前本不相等。安拉不引导背义的民众。","translator":"王静斋"},{"sura":9,"aya":20,"text":"那般归信、迁居、并以财产生命在安拉之道上奋斗的人，在安拉御前是阶级最大的；他们均是将得到所欲的。","translator":"王静斋"},{"sura":9,"aya":21,"text":"养主以自己的慈惠、喜悦、与内有常久的快乐、","translator":"王静斋"},{"sura":9,"aya":22,"text":"永远居住的天园，给他们报喜信。在安拉确据有大赏。","translator":"王静斋"},{"sura":9,"aya":23,"text":"众归信的人哪！你们的父兄如果舍去正信而选择不信了，则不要爱喜他们。你们之中爱喜他们的人，均是背义的。","translator":"王静斋"},{"sura":9,"aya":24,"text":"你说！（若是你们的父子、弟兄、妻室、近亲、以及自己所赚的钱财与恐其滞销的营业，与自所喜爱的住所，在你们认为比安拉和他的使者，与在他之道上奋斗，更觉可爱，你们就侯安拉发布自己的命令吧！安拉不引导一般恶民。）","translator":"王静斋"},{"sura":9,"aya":25,"text":"安拉确在许多地方，并在侯乃尼那天相助你们了。那时候，你们夸耀自己人数众多，但是众多并位代你们避免一事。地面虽系宽阔，而在你们感觉褊狭。","translator":"王静斋"},{"sura":9,"aya":26,"text":"终的，你们失败着退转了。安拉复把沉静降给自己的使者，并降给众穆民；更降下你们看不见的军兵，罪刑一般逆徒。这是报复不信的人们。","translator":"王静斋"},{"sura":9,"aya":27,"text":"在这以后，安拉复允自所意欲（许可）的人悔罪。安拉是多恕的特慈的。","translator":"王静斋"},{"sura":9,"aya":28,"text":"众归信的人哪！信偶像的人们只是秽物，所以在本年以后，莫容他们入禁寺。你们若怕受穷了，若是安拉意欲(赐给)的时候，不久的，他就教你们借着他的特恩富有。安拉实是深知的明哲的。","translator":"王静斋"},{"sura":9,"aya":29,"text":"有经的人中一般不信安拉与末日，并不禁忌安拉和他的使者所严禁的，更不服从真教的这些人，非至卑贱着实时付给[直支业，]则当对他们格杀。","translator":"王静斋"},{"sura":9,"aya":30,"text":"犹太人说：（欧宰尔是主的儿子，）基督徒说：（埋希哈是主的儿子，）这是他们口称。恰似从前一般逆徒所说的。安拉驱远他们了！他们转变到哪里?","translator":"王静斋"},{"sura":9,"aya":31,"text":"他们舍去安拉以学者与修士当养主。更以马尔焉的子埋希哈（当主。）他们原是奉命拜独一主的。除他以外再无有主。真主清净，不染他们所举的匹偶。","translator":"王静斋"},{"sura":9,"aya":32,"text":"他们意欲口熄安拉的光，安拉不可，惟欲自己的光完满，","translator":"王静斋"},{"sura":9,"aya":33,"text":"纵令迷徒们厌恶。他那真主用南针与正教遣来自己的使者，为的是使它优越各教，纵令信匹偶的人们厌恶。","translator":"王静斋"},{"sura":9,"aya":34,"text":"众归信的人哪！的确，许多学者与修士妄吞人家的资财，并阻碍安拉之道。积下金银并不把它用在安拉之道的人，你就把痛刑预告给他们吧！","translator":"王静斋"},{"sura":9,"aya":35,"text":"当那一日，在火狱的火上烧烤那金银，而用它烙他们的额头、两肋、脊背（有的对他们说：）（这是你们曾为自身积下的；你们就尝试自所积下的吧！）","translator":"王静斋"},{"sura":9,"aya":36,"text":"的确，在天经上依安拉认定的月数，自造化天地那天起，就是十二个月。内有四个禁月。这是正教。你们在这四个月内不要自亏己身。你们要共同对多神教徒格杀，就像他们共同对你们格杀。你们当知安拉是与敬畏的人同在的","translator":"王静斋"},{"sura":9,"aya":37,"text":"推延只是增加不信。逆徒们因此被归到迷误上。他们弛禁它一年，严禁它一年，为的是符合安拉所严禁的数目，而弛放安拉所严禁的。有的为他们装饰恶行为了。安拉不引导逆民。","translator":"王静斋"},{"sura":9,"aya":38,"text":"众归信的人哪！有人告诉你们在安拉之道上出战的时候，为什么你们坐地不起，你们弃后世而爱今世吗?今生的享受，比在后世上不过是些微的。","translator":"王静斋"},{"sura":9,"aya":39,"text":"若是你们不肯出战，真主就惩治你们痛刑，改换除过你们的一伙，你们伤不了他一事。安拉是全能于万事的。","translator":"王静斋"},{"sura":9,"aya":40,"text":"若是你们不相助他，安拉确曾相助他了。那时候，逆徒们逐出二人之一的他去。那时候，他俩是在山洞里；彼时，他对自己的同伴说：（你不要忧虑！安拉实是和我们同在的。）安拉降给他沉静，并以你们看不见的军兵援助他，而置逆徒的话为至卑下的。安拉的话就是至高尚的。安拉是优胜的明哲的。","translator":"王静斋"},{"sura":9,"aya":41,"text":"你们当负轻载重着出战！你们要以财产生命在安拉之道上奋斗。如果你们知道了，这是于你们最好的。","translator":"王静斋"},{"sura":9,"aya":42,"text":"设若那是眼前的利益，中程的旅行了，他们一定追随你，但是路程远了。他们即将指着安拉发誓：（设若我们有能力了，一定要一同随你们出去。）他们自伤己身了。安拉知道他们实是谎言的。","translator":"王静斋"},{"sura":9,"aya":43,"text":"安拉宽恕你了。你为什么准许他们呢? （他为何不教他们等侯，）直到你明白那些说实言的人，并知道一切说谎言的人。","translator":"王静斋"},{"sura":9,"aya":44,"text":"归信安拉与末日的人，对于以财产生命奋斗上不向你要求许可。安拉深知一般敬畏。","translator":"王静斋"},{"sura":9,"aya":45,"text":"向你求许可的，只是那般不信安拉与末日，心怀疑惑，且在疑惑中徘徊不定的人。","translator":"王静斋"},{"sura":9,"aya":46,"text":"设若他们意欲出去了，必就为它预备用品。但是安拉厌恶他们出去，所以使他们迟滞了。 有的说：（你们和坐的人一同坐下吧！）","translator":"王静斋"},{"sura":9,"aya":47,"text":"设若他们随你们出去了，他们只是给你们增加坏处，他们必在你们之中努力离间。给你们寻觅祸乱。你们之中有为他们侧耳的。安拉深知一般背义的人。","translator":"王静斋"},{"sura":9,"aya":48,"text":"从前他们确曾寻觅祸乱，他们为你颠倒些个事情，迄至真的到来，安拉的事情发现了。彼时他们是厌恶的。","translator":"王静斋"},{"sura":9,"aya":49,"text":"他们之中有的人说：（你准许我吧！勿陷我于祸患中。）须知他们已自陷入祸患了。的确火狱是包围众逆徒的。","translator":"王静斋"},{"sura":9,"aya":50,"text":"你若得福，他们就忧愁起来；你若遭患，他们就说：（事前我们已然虑到了，）他们愉快着转去。","translator":"王静斋"},{"sura":9,"aya":51,"text":"你说：（我们所遭遇的绝不外是安拉为我们规定的。他是我们的主宰，当令众穆民惟独仰赖安拉。）","translator":"王静斋"},{"sura":9,"aya":52,"text":"你说：（你们对我们所等侯的，不外两件好事的一件。我们为你们等侯安拉亲自或藉我们的手罪刑你们，你们等待吧！我们是随同你们等待的。）","translator":"王静斋"},{"sura":9,"aya":53,"text":"你说：（你们顺服或委曲着施舍吧！你们绝得不到悦纳。你们实是一伙作恶的。","translator":"王静斋"},{"sura":9,"aya":54,"text":"他们的使用被阻不受悦纳，只因其不信安拉和他的使者，与他们只是懒惰着礼拜，他们只是厌恶着使用。","translator":"王静斋"},{"sura":9,"aya":55,"text":"你不要羡慕他们的资财与子女。安拉只是欲藉此在今生罪刑他们，并欲他们不信着性命出体。","translator":"王静斋"},{"sura":9,"aya":56,"text":"他们指着安拉发誓：（自属与你们同类，）他们本不是你们一类的。他们只是一伙恐惧的。","translator":"王静斋"},{"sura":9,"aya":57,"text":"设若他们得到一个隐避的地方，或一个山洞，或一个地穴，他们必向那里急忙奔去。","translator":"王静斋"},{"sura":9,"aya":58,"text":"他们有的人对于分配施济物上毁谤你。若从那个上给他们了，他们就满意；若不从那个上给他们，他们立刻发怒。","translator":"王静斋"},{"sura":9,"aya":59,"text":"设若他们满意安拉与其使者所赐的，且说：安拉使我们满足了，安拉和他的使者即将以他的特恩赐给我们，我们实是仰望安拉的。","translator":"王静斋"},{"sura":9,"aya":60,"text":"施济品惟归给穷困的、清贫的、为此工作的、其心可得契合的、用在赎身的、拖欠外债的、并用在安拉之道上、用到旅客上。由安拉决定的。安拉是深知的，明哲的。","translator":"王静斋"},{"sura":9,"aya":61,"text":"他们之中有一般人扰害圣人，说：（他是个耳朵。）你说：（他是你们的好耳朵。他归信安拉，他信任众穆民，并怜恤你们之中归信的人。）扰害安拉的使者，其人应遭痛刑。","translator":"王静斋"},{"sura":9,"aya":62,"text":"他们指着安拉对你们发誓，为的是讨你们喜欢。如果他们是归信的了，安拉和他的使者最宜他们讨其喜欢的。","translator":"王静斋"},{"sura":9,"aya":63,"text":"他们岂未晓得违抗安拉和他使者，其人必永居火狱。这是绝大的侮辱。","translator":"王静斋"},{"sura":9,"aya":64,"text":"伪信士们恐怕降下一章天经，宣布他们心中所怀的。你说：（你们嘲弄吧！安拉必将暴露你们所防备的。）","translator":"王静斋"},{"sura":9,"aya":65,"text":"你若问他们，他们必说：（我们只是诙谐，打趣罢了。）你说：（你们嘲弄安拉和他的表征，与他的使者吗?","translator":"王静斋"},{"sura":9,"aya":66,"text":"你们不要托词。你们实是归信后复又不信了。）如果我放赦你们之中的一伙，我就罪刑（另）一伙，只因他们是为恶的人。","translator":"王静斋"},{"sura":9,"aya":67,"text":"男女伪信士，其一部分出自一部分。他们命以非法的，禁止合理的。他们拘紧自己的手。他们忘记安拉，他也忘掉他们了。的确伪信的人均是作恶的。","translator":"王静斋"},{"sura":9,"aya":68,"text":"安拉已对男女伪信士和逆徒们预许以火狱的火，永居其中。那火狱使他们满足了。他们遭受安拉的驱远，应受长期的罪刑。","translator":"王静斋"},{"sura":9,"aya":69,"text":"就像你们以前的众人似的；他们原比你们力强，也在资财子女上较多。他们曾享受自己的福分。你们也曾享受自己的福分了，如同你们以前的人享受自己的福分。你们诙谐，就像他们诙谐了。这般人，他们关于今世与后世的功修无效了，这般人均是伤折的。","translator":"王静斋"},{"sura":9,"aya":70,"text":"他们岂未得到众前人——努海的族人、阿代、赛母待、易卜拉欣的族人，买大严与被颠覆人的消息吗? 列使现给他们种种明证了。安拉绝未亏负他们，但是他们自亏己身了。","translator":"王静斋"},{"sura":9,"aya":71,"text":"众穆民男女彼此一个是一个的爱友。他们命以合法的，禁止非理的。他们立拜功，施天课。他们顺从安拉和他的使者。这般人即将蒙安拉的慈悯。安拉实是优胜的明哲的。","translator":"王静斋"},{"sura":9,"aya":72,"text":"安拉已对众穆民男女许下诸河流于其下永居的园林，与阿甸园中清洁的居所。安拉的喜爱是绝大的。这就是大成功。","translator":"王静斋"},{"sura":9,"aya":73,"text":"圣人哪！你对逆徒和伪信士们奋斗！你要严厉地对待他们。他们的住所是火狱。归处太恶了！","translator":"王静斋"},{"sura":9,"aya":74,"text":"他们指着安拉发誓：（未说）他们确说出不信的话了，他们在归顺以后复又隐昧了，他们希望不可得到的。他们怀恨，不过是因安拉和他的使者以自己的特恩教他们富有。若是他们悔罪了，那是于他们至好的。若是他们不肯了，安拉将在今世后世惩他们以痛刑。在地上无一爱护与相助他们的。","translator":"王静斋"},{"sura":9,"aya":75,"text":"他们之中有的人对安拉结约：（如果真主赐给我们特恩，我们必定施散，我们定是义人一类的。）","translator":"王静斋"},{"sura":9,"aya":76,"text":"及至真主就着自己的特恩赐给他们了，他们就坚吝了，违背了。他们是惯于违命的。","translator":"王静斋"},{"sura":9,"aya":77,"text":"只因他们对主爽约并因他们说谎，安拉曾使“伪信”遗在他们心中，直到见主的那一天。","translator":"王静斋"},{"sura":9,"aya":78,"text":"他们岂不知安拉晓得他们的内幕，与他们的密谈吗? （他们岂不知）安拉是能知目不能见的吗?","translator":"王静斋"},{"sura":9,"aya":79,"text":"那般人薄贬一般额外作施济的穆民。并薄贬一切仅得到力之所逮的。且对他们加以嘲笑，安拉将惩罚他们的嘲笑，他们应受痛刑。","translator":"王静斋"},{"sura":9,"aya":80,"text":"你为他们求恕也罢，不为他们求恕也罢。如果你为他们求恕七十遍了，安拉绝不晓恕他们，这是因他们不信安拉和他的使者。安拉不引导恶民。","translator":"王静斋"},{"sura":9,"aya":81,"text":"退后的人们以在安拉的使者后面坐着为乐，他们厌恶以财产和生命在安拉之道上奋斗。他们说：（你们在热的时候不要出战。）你说：（火狱的火更热，设若他们解悟了……。）","translator":"王静斋"},{"sura":9,"aya":82,"text":"他们应当少笑多哭，这是因他们所作的施以报应。","translator":"王静斋"},{"sura":9,"aya":83,"text":"若是安拉令你回到他们其中的一伙人，他们求你许可出去，你就说：（你们绝对永不随我出去，你们绝不随我对敌格杀。前次，你们就喜欢坐着，你们就与退后的人一同坐着吧！）","translator":"王静斋"},{"sura":9,"aya":84,"text":"你永莫给他们死去的一个人作殡仪的祈祷，你也莫站在他的墓傍，他们确不信安拉和他的使者了。他们在作恶着死去了。","translator":"王静斋"},{"sura":9,"aya":85,"text":"你不要羡慕他们的资财和子女。安拉只欲在今世因着那事罪刑他们；他们正在不信着性命出体。","translator":"王静斋"},{"sura":9,"aya":86,"text":"每逢降下一章天经，命他们信安拉并随同他的使者去奋斗，他们之中有能力的人就向你要求许可，说：（你容我们与坐的人同坐吧！）","translator":"王静斋"},{"sura":9,"aya":87,"text":"他们愿意和退后的人同处。他们的心已被封闭了，所以他们不解悟。","translator":"王静斋"},{"sura":9,"aya":88,"text":"只有使者和追随他的众穆民，以财产和生命去奋斗。这般人可获种种幸福，且均是得意的。","translator":"王静斋"},{"sura":9,"aya":89,"text":"安拉已为他们预备诸河流于其下永居的天园，这是大成功。","translator":"王静斋"},{"sura":9,"aya":90,"text":"一般居郊野的托词者，来要求准许他们。一般对于安拉和他底使者说谎的人坐下了。他们之中的一般逆徒即将遭受痛刑。","translator":"王静斋"},{"sura":9,"aya":91,"text":"无能为力的，患病的，得不到费用的，这些人们对安拉和他的使者忠诚的时候，无什么罪恶。对于为善的人们当无途径。安拉是多恕的特慈的。","translator":"王静斋"},{"sura":9,"aya":92,"text":"对那一般人也无有途径：他们到你跟前要求代步。你说：（我得不到给你们作代步的，）他们就因为忧虑得不到费用，而两眼流泪着转回了。","translator":"王静斋"},{"sura":9,"aya":93,"text":"途径惟对那般求你准许的富有的人。他们愿意和退后的人们同处。安拉已封闭他们的心了，所以他们不知道。","translator":"王静斋"},{"sura":9,"aya":94,"text":"当你们转回的时候，他们向你们要求原谅。 你说：（你们无须要求原谅，我们绝不信你们。安拉已把你们的消息告诉我们了。安拉和他的使者将要观看你们的作为，然后，教你们返回知道目不能见的与显见的主，他将对你们宣告你们已往所作的。）","translator":"王静斋"},{"sura":9,"aya":95,"text":"当你们转来的时候，他们指着安拉对你们发誓，为的是你们不要斥责他们。你们就容让他们吧！因为他们实是秽物。他们的归处是火狱，依他们所作的施以还报。","translator":"王静斋"},{"sura":9,"aya":96,"text":"他们对你们发誓，为的是你们爱喜他们。如果你们爱喜他们了，安拉绝不爱喜恶民。","translator":"王静斋"},{"sura":9,"aya":97,"text":"居郊野的人是在迷信和伪信上最烈的；是最近于不知道安拉降给自己使者的律例的。安拉是深知的，明哲的。","translator":"王静斋"},{"sura":9,"aya":98,"text":"居郊野的人中有一类人；把自己所使用的当作一种损失，并等侯你们遭逢种种的灾害。他们应遭恶报！安拉是能听的，深知的。","translator":"王静斋"},{"sura":9,"aya":99,"text":"居郊野的人中又有一类人！他信安拉和末日，并以自所使用的当作接近安拉，并得到使者祝福的原由。须知那是一定使他们接近的。安拉即将教他们入在自己的慈惠中。安拉实是多恕的，特慈的。","translator":"王静斋"},{"sura":9,"aya":100,"text":"一般领袖先进——若迁士和辅士们，以及追随他们为善的人，安拉爱喜他们，他们爱喜他。他为他们预备诸河流于其下的天园，永久住在那里，这是大成功。","translator":"王静斋"},{"sura":9,"aya":101,"text":"你们四周居郊野的人有些伪信士，默底纳人里也有；他们习于外实内虚。你不知道他们，我本知道，我即将罪刑他们两次，再教他们归入重刑。","translator":"王静斋"},{"sura":9,"aya":102,"text":"另有一伙承认罪恶的人，他们在善恶的行为上参半。安拉即将允他们悔罪。安拉实是多恕的，特慈的。","translator":"王静斋"},{"sura":9,"aya":103,"text":"你由他们的资财里取得济物！用它洁净他们，清洁他们着。你为他们祈福吧！你的祈福定可安慰他们。安拉是能听的，深知的。","translator":"王静斋"},{"sura":9,"aya":104,"text":"他们岂未知道安拉允其众仆的悔罪，并接受一切济物吗?且未知道安拉即是允承悔罪的，特慈的吗?","translator":"王静斋"},{"sura":9,"aya":105,"text":"你说：你们去作吧！安拉和他的使者和众穆民即将观看你们的作为；即将教你们归到知目不能见的和显见的主。他对你们宣告你们已往所作的！","translator":"王静斋"},{"sura":9,"aya":106,"text":"另有一伙退后的人等侯安拉的命令，或是真主罪刑他们，或允他们悔罪，安拉是深知的，明哲的。","translator":"王静斋"},{"sura":9,"aya":107,"text":"更有一般人！他们创建礼拜堂，为的是妨害，为着背逆，为在穆民中分伙，并为等侯从前对安拉和他的使者征战的人。他们发誓：（我们只是要好）安拉本见证他们实是谎言的。","translator":"王静斋"},{"sura":9,"aya":108,"text":"你永不可站在那里。由开始那天依着虔恪所创建的礼拜堂，是最宜于你站在那里的。内中有一般好洁者。安拉本喜清洁的人。","translator":"王静斋"},{"sura":9,"aya":109,"text":"为着敬畏安拉与（讨他的）喜悦而立建筑的根基的人是更好的?或是把根基创建在将塌的堤岸而自陷于火狱的那种人呢? 安拉不引导背义的民众。","translator":"王静斋"},{"sura":9,"aya":110,"text":"他们所创的建筑永久是他们心疑的（原由）除非他们的心粉碎的时候。安拉是深知的，明哲的。","translator":"王静斋"},{"sura":9,"aya":111,"text":"安拉确已收买众穆民的生命财产；以他们得到天园为代价。他们在安拉的道上格斗，而致杀人被杀，这是真主在讨喇忒、引支勒、和古兰上决定的约许。完满自己对安拉所约的人，你们就以自所用作交接的贸易表示喜悦吧！这就是大成功。","translator":"王静斋"},{"sura":9,"aya":112,"text":"一切悔罪的，拜主的，赞颂的，斋戒的，鞠躬叩头的，命以合法禁止非理的，谨守安拉的律例的；你就给这般穆民报佳音吧！","translator":"王静斋"},{"sura":9,"aya":113,"text":"圣人和众穆民对于拜偶像的人纵属近戚，而在明知他们是居火狱的人以后，也不得为他们求恕。","translator":"王静斋"},{"sura":9,"aya":114,"text":"易卜拉欣为他父亲求恕，只是因为对他父亲结下的约。当他明知父亲是安拉的仇敌了，他就对它断绝关系。易卜拉欣实是哀矜的，大度的","translator":"王静斋"},{"sura":9,"aya":115,"text":"安拉在引导一伙人以后，非至对他们解明所应敬慎的（而他们不实行敬慎）以后，绝不任其迷误。","translator":"王静斋"},{"sura":9,"aya":116,"text":"安拉确掌天地的权柄，他使生使死。在你们除过安拉以外无一爱护的，也无一相助的。","translator":"王静斋"},{"sura":9,"aya":117,"text":"安拉确允承圣人和迁士、辅士们的忏悔了。那般人在困苦的时候追随他，此在他们的一伙人心几至偏斜，他复允他们忏悔以后。主实是怜恤慈悯他们的。","translator":"王静斋"},{"sura":9,"aya":118,"text":"另有三个人， 他们迟延到地虽宽阔，而在他们感觉窄狭的时候；其心忧虑，自知除却倾向安拉，别无一个隐伏的地方。而后真主允他们悔罪了，好教他们复反。安拉实是允承忏悔的，特慈的。","translator":"王静斋"},{"sura":9,"aya":119,"text":"众归信的人哪！你们当畏惧安拉！当与说实言的人取连系，","translator":"王静斋"},{"sura":9,"aya":120,"text":"默底纳和其四周居郊野的人，不得退到安拉的使者后面，且不可只知顾己而异掉圣躬。 这是由于他们每逢在主道上遭受干渴与痛楚，并踏那激动逆徒忿怒的地方，且遭敌人的一种伤害时，无不为他们记下（善功。）安拉实不湮没为善者的报酬！","translator":"王静斋"},{"sura":9,"aya":121,"text":"他们每逢出舍稍微的和大宗的财物，每逢经过一个山谷，就有的为他们记下；卒至安拉以他们作下的最好的赏给他们。","translator":"王静斋"},{"sura":9,"aya":122,"text":"众穆民不可一同出动。他们的每伙人里何不有一部分人出动，为的是习学宗教上的知识，并为转来的时候警告自己的族人，以便他们防备。","translator":"王静斋"},{"sura":9,"aya":123,"text":"众归信的人哪！你们当与靠近你们的逆徒格斗。当令他们感觉你们的刚毅。你们当知道安拉是和敬慎的人同在的。","translator":"王静斋"},{"sura":9,"aya":124,"text":"每逢降下一章天经的时候，他们就有人说：（这能增加你们哪一个的信仰呢?）论到归信的人，那益增他们信仰，皆在表示喜悦着。","translator":"王静斋"},{"sura":9,"aya":125,"text":"至于心中有病的人，那就给他们污秽上加增污秽。他们至死不信。","translator":"王静斋"},{"sura":9,"aya":126,"text":"他们岂未看见历年磨难他们一次或两次，他们不复悔罪，也不受劝吗?","translator":"王静斋"},{"sura":9,"aya":127,"text":"每逢降下一章天经，他们就彼此相观，（说：）（有人看见你们吗?）而后转回。安拉拨转他们的心了，只因他们是不解悟的一伙人。","translator":"王静斋"},{"sura":9,"aya":128,"text":"你们同人中的一个使者确曾来到你们这里；你们的遭难在他感觉痛苦他是企图引导你们的，怜恤慈悯众穆民的。","translator":"王静斋"},{"sura":9,"aya":129,"text":"如果他们不肯了，你就说：（安拉使我满足了。除他以外再无一受拜的。我惟仰赖他。他是掌伟大的“阿勒世”的主。）","translator":"王静斋"},{"sura":10,"aya":1,"text":"（阿里甫、俩模、拉。）这是固典的文词。","translator":"王静斋"},{"sura":10,"aya":2,"text":"人们岂可诧异，（我告谕他们其中的一个人！警告世人，并对众穆民预报他们必在养主御前获得高尚的成绩。）众不信的人说：（这只是一个明明的术士。）","translator":"王静斋"},{"sura":10,"aya":3,"text":"的确，你们的养主是那在六日间造化天地的。而后镇摄[阿勒世]治理事情的安拉。每一个代求的均须经过他的许可。这是安拉——调养你们的主。你们当敬拜他，你们怎不受劝呢?","translator":"王静斋"},{"sura":10,"aya":4,"text":"你们统惟归于他。这是安拉决定的约会。的确，他造化世人而又恢复他，为的是秉公赏赐一切归信并作善举的人。逆徒们应因为不信而饮滚水受痛刑。","translator":"王静斋"},{"sura":10,"aya":5,"text":"他那真主造日为光，造目为明，并为它规定站道；为教你们知道年数和计算。安拉只因真理造化了这个。他为着有知识的群众分析种种的表征。","translator":"王静斋"},{"sura":10,"aya":6,"text":"的确，在夜昼的不同，与安拉在天地间所造的，对于一般敬慎的人确含有种种表征。","translator":"王静斋"},{"sura":10,"aya":7,"text":"的确，一般不盼望见我，喜爱今生，依它安定的人，并忽于我的种种表征的人，","translator":"王静斋"},{"sura":10,"aya":8,"text":"他们的归处是火狱，由于他们以往所作的。","translator":"王静斋"},{"sura":10,"aya":9,"text":"众归信并作善举的人，养主必因为他们的归信而加以指导。乐园中的诸河流于其下。","translator":"王静斋"},{"sura":10,"aya":10,"text":"他们在那里的呼吁是：（赞你清净！哎主啊！）他们在那里的庆祝是平安。他们末后的呼吁是：赞颂安拉——调养众世界的主。","translator":"王静斋"},{"sura":10,"aya":11,"text":"设若安拉为世人忙于发现祸患，就像他们的忙于求福，则必对他们结束期限了。我放任一般不盼望见我的人在迷路中徘徊着。","translator":"王静斋"},{"sura":10,"aya":12,"text":"人若遭难的时候，他就侧卧着或站着坐着呼求我。及我给他释去患难的时候，他依然如故，好像是他未曾求我释去他遭受的患难。有的如此着因为一般妄为的人装饰他们的作为。","translator":"王静斋"},{"sura":10,"aya":13,"text":"你们以前的若干代，当其背义的时候，我已灭绝他们了。列使把种种明证现给他们。（但是）他们一定不信。我如此着还报一般为恶的人。","translator":"王静斋"},{"sura":10,"aya":14,"text":"而后我教你们作他们以后在地上替代的，藉观你们如何行事·","translator":"王静斋"},{"sura":10,"aya":15,"text":"每逢有人把我明显的表征诵给他们了，那般不盼见我的人就说：（你把除此以外的古兰拿给我们，或你变更它！）你说：（我不该私自变更它，我惟追从所告谕我的，我若违犯养主了，我实骇怕大日的刑罚。）","translator":"王静斋"},{"sura":10,"aya":16,"text":"你说：若是安拉意欲（不诵）了，我就不把它诵给你们；他也不教你们知道它。先前，我确在你们内中迟留一世了。你们怎不了解呢?","translator":"王静斋"},{"sura":10,"aya":17,"text":"往安拉上造谎或不信服他的表征，谁比这类人更是背义的?为恶的人绝不能得意。","translator":"王静斋"},{"sura":10,"aya":18,"text":"他们抛开安拉拜那于自己无害无利的。他们说：这一切是在安拉御前给我们代求赦免的。你说：你们向安拉宣告天地间非他所知道的吗？真主清净！他玄高于他们所拟的匹偶。","translator":"王静斋"},{"sura":10,"aya":19,"text":"世人原只是一族，而后他们起争议了。设非由你养主先前的话，一定就对于他们所争议的从中判定了。","translator":"王静斋"},{"sura":10,"aya":20,"text":"他们说：（为何不把出自养主的一项异迹降给他?）你说：（目不能见的惟属于他[知道。 ]你们等待吧！我是随同你们等待的。）","translator":"王静斋"},{"sura":10,"aya":21,"text":"在人们遭遇患难以后，我教他们尝得慈惠的时候，他们辄就对我的表征施行计谋。你说，（安拉很快的惩罚计谋。）我的天使记录你们所计谋的。","translator":"王静斋"},{"sura":10,"aya":22,"text":"他那真主教你们在陆海上行走；待至你们登船，船乘着和风航行，他们以此感觉快乐的时候，暴风突至，洪涛来自各方，他们认为一定遭难；他们在竭诚顺服着呼求安拉。（如果你从这个上解救我们了，我们一定感念。）","translator":"王静斋"},{"sura":10,"aya":23,"text":"及至真主解救他们了，他们立刻就在地上背义着造恶。众人哪！你们的悖逆惟害自身。只不过今生的享受，而后你们惟归于我，我对你们宣告你们以往所作的。","translator":"王静斋"},{"sura":10,"aya":24,"text":"今生的情形，如同是我自天空上降下来的水，地上的植物，若人类和牲畜所吃的，借着它丛杂起来；待地得到它的华美受了装饰，那地方的居民认为自己是能〔把握〕它的时候，我的命令在夜间。或白天到来；我使它化为己被收割的，似乎它在昨天原就无有。我如此着因为有参悟的群众分析各项表征。","translator":"王静斋"},{"sura":10,"aya":25,"text":"安拉唤人于平安的第宅，导其意欲（得道）的人于正道。","translator":"王静斋"},{"sura":10,"aya":26,"text":"为善的人获善赏，且获增加。他们的面目不为灰暗和卑贱所遮。这般人是永居天园的。","translator":"王静斋"},{"sura":10,"aya":27,"text":"为恶的人们遭遇和它一样地恶报，且现出一种丑形；无一个抗安拉护佑他们的。恰像他们的面目被一部黑夜遮覆。他们是永居火狱的。","translator":"王静斋"},{"sura":10,"aya":28,"text":"当那一日，我把他们一同集合起来，然后，我对拜偶像的人说：（你们和自己的匹偶站在本位！）于是我在他们中间隔离。他们的匹偶说：（你们原未拜我们。","translator":"王静斋"},{"sura":10,"aya":29,"text":"安拉在我们和你们中间作见证已可满足了。我们实是忽略你们的敬拜的。）","translator":"王静斋"},{"sura":10,"aya":30,"text":"在那时候，每一个人尝试自所送于前的。均被归到安拉——他们的真宰。他们所造作的消灭了。","translator":"王静斋"},{"sura":10,"aya":31,"text":"你说：（谁就着天地供你们给养?）不然，谁掌听官与视官? 谁出生于死的，出死于生的?谁治理事情?他们即将说：（安拉）你说：（你们怎不禁忌呢?","translator":"王静斋"},{"sura":10,"aya":32,"text":"这是安拉——调养你们的真主。真的以外，除却迷误，尚有什么?你们转变到哪里?）如此着，","translator":"王静斋"},{"sura":10,"aya":33,"text":"你的养主对于一般为恶者的话——他们必不归信，已然决定了。","translator":"王静斋"},{"sura":10,"aya":34,"text":"你说：（你们的匹偶中有那造化世人而又恢复他的吗?）你说：安拉造化世人而又恢复他，你们转变到哪里?","translator":"王静斋"},{"sura":10,"aya":35,"text":"你说：（你们的匹偶中有那导至真理的吗?）你说：（安拉导至真理。）那导至真理者是最应跟从的?或是非受过引导而不能得正道的呢?你们怎么啦?你们如何判断的?","translator":"王静斋"},{"sura":10,"aya":36,"text":"他们的大半只追随揣测。揣测，实不能替代一些真的。安拉深知他们的行为。","translator":"王静斋"},{"sura":10,"aya":37,"text":"这古兰不是有人撒开安拉私造的，而是证实它以前的，并分析无可疑惑出自众世界养主的经典。","translator":"王静斋"},{"sura":10,"aya":38,"text":"他们说：（他造作了它）吗?你说：（如果你们的所言属实，你们就举出和它同样的一章来；你们就撇开安拉呼求你们所能〔求〕的人。）","translator":"王静斋"},{"sura":10,"aya":39,"text":"不然，他们不信那未深切知道它，且未得到它的结果的。在他们以前的众人如此不信实了。看哪！一切背义的人结果如何?","translator":"王静斋"},{"sura":10,"aya":40,"text":"他们之中有信它的，有不信它的。你的养主至知一般造恶的人。","translator":"王静斋"},{"sura":10,"aya":41,"text":"若是他们不信你了，你就说！我有我的作为，你们有你们的作为；你们是于我的作为无干的。我是于你们的作为无干的。","translator":"王静斋"},{"sura":10,"aya":42,"text":"他们之中有的人向你侧耳。聋子虽不了解，你能教他们听到吗?","translator":"王静斋"},{"sura":10,"aya":43,"text":"他们之中有的人看见你。你与失目的人虽其看不见，而你也能引导他们吗？","translator":"王静斋"},{"sura":10,"aya":44,"text":"安拉绝不亏人一事，但是世人自亏己身了。","translator":"王静斋"},{"sura":10,"aya":45,"text":"当哪一日，他集合他们，好象他们只迟留白昼的片时，彼此相识着，不信看见安拉的人却已伤折了，他们未得正道。","translator":"王静斋"},{"sura":10,"aya":46,"text":"如果我把自所预许他们的一部分现给你，（哪是可能的）如果我取去你的性命，他们惟独归于我，安拉且是见证他们的行为的。","translator":"王静斋"},{"sura":10,"aya":47,"text":"在每一伙人有一位使者，当使者到达的时候，就在他们中间公判，并不亏负。","translator":"王静斋"},{"sura":10,"aya":48,"text":"他们说：（此约是在什么时候？如果你们的所言属实了——）","translator":"王静斋"},{"sura":10,"aya":49,"text":"你说：（除却安拉所意欲的以外，我不能为自身掌利害。）在每伙人有个一定的时期，当他们的时期已至之际，既不能退后一时，也不能提前。","translator":"王静斋"},{"sura":10,"aya":50,"text":"你说：（你们看出来了吗？若是他的刑法在夜间，或在白昼临到你们上，这些为恶的人，尚有什么要求从速实现的呢？）","translator":"王静斋"},{"sura":10,"aya":51,"text":"终的，刑罚实现的时候，你们归信他吗？在这时候岂得能够？你们却曾要求急速发现它了。","translator":"王静斋"},{"sura":10,"aya":52,"text":"随后有人对一般背义的人说：（你们尝试长久的刑罚吧！仅依你们以往的作为还报你们。）","translator":"王静斋"},{"sura":10,"aya":53,"text":"他们要你明白说出：（哪是确实的吗？）你说：（是的，指着我的养主发誓：哪是一定确实的，你们是不能抵抗的。）","translator":"王静斋"},{"sura":10,"aya":54,"text":"设若地上的一切，是属每个背义的人所有了，他必定用它作赎资。他们看见罪刑的时候，暗自懊悔。在他们中间秉公判断，并不亏负。","translator":"王静斋"},{"sura":10,"aya":55,"text":"须知天地间所有的惟归安拉执掌。须知安拉的约确实；但是大多数人不知道。","translator":"王静斋"},{"sura":10,"aya":56,"text":"他使生使死，你们将被返回于他 。","translator":"王静斋"},{"sura":10,"aya":57,"text":"众人哪！劝化和医治胸中的，并对于众穆民的指导与怜恤，已由养主上达至你们了！","translator":"王静斋"},{"sura":10,"aya":58,"text":"你说：（让他们借着安拉的特恩与他的慈惠快乐吧！这是优于他们所聚集的。）","translator":"王静斋"},{"sura":10,"aya":59,"text":"你说：（要你们告诉我！安拉降给你们什么给养了，你们于中安排合法与不合法的?）你说！（安拉特许你们吗?不然，是你们往安拉上造作呢?）","translator":"王静斋"},{"sura":10,"aya":60,"text":"一般往安拉上造作谎言的人，对于复生的那一天作什么揣度? 安拉实是有恩于众人的，但是他们的大半不知感念。","translator":"王静斋"},{"sura":10,"aya":61,"text":"你原未处在一种事务上。你末读过他的一些古兰。你们每逢举行一种工作，在你们深入其事的时候，我是监察你们的。天地间无有一蚁重的，离去你的养主；所有比这个较小较大的统记在明典中。","translator":"王静斋"},{"sura":10,"aya":62,"text":"须知安拉所爱护的人一定无惧无忧。","translator":"王静斋"},{"sura":10,"aya":63,"text":"归信并且敬慎的那般人，","translator":"王静斋"},{"sura":10,"aya":64,"text":"均在今生并在后世获得喜信。安拉的话无有变更。这是大成功。","translator":"王静斋"},{"sura":10,"aya":65,"text":"你不要因他们所谈的而有所忧虑。荣耀统属安拉的；他是能听的，深知的。","translator":"王静斋"},{"sura":10,"aya":66,"text":"须知在天的在地的惟安拉执掌。撇开安拉作呼吁的那般人，并未追随一切匹偶。他们只是追随揣度，他们不外是造作。","translator":"王静斋"},{"sura":10,"aya":67,"text":"他那真主为你们造夜，好教你们在它里休息；并造化光明的白昼。此中对于侧耳的群众确含有种种表征。","translator":"王静斋"},{"sura":10,"aya":68,"text":"他们说：（安拉立子了。）真主清净！他是无求的。在天的在地的惟他执掌。你们对于这个上并无所持的明证。你们往安拉上说自所不知的吗?","translator":"王静斋"},{"sura":10,"aya":69,"text":"你说：（往安拉上造作谎言的人必不得意。）","translator":"王静斋"},{"sura":10,"aya":70,"text":"今世上的一点享受，而后他们返回于我。我再因他们的不信使其尝烈刑。","translator":"王静斋"},{"sura":10,"aya":71,"text":"你把努海的往事读给他们！那时候，他对自己族人说：我的族人哪！若是我的站立和我借着安拉的表征来规劝，在你们感觉痛苦了，我惟仰赖安拉；你们就联合自己的匹偶，决定自己的事情！以后你们的事情不要隐瞒。 以后任你们向我实施。不要姑容我。","translator":"王静斋"},{"sura":10,"aya":72,"text":"如果你们不肯了，我未向你们讨些报酬。我的报酬惟在安拉。我奉命归为顺从的。","translator":"王静斋"},{"sura":10,"aya":73,"text":"但是他们不信服他，而后我解救他与随他乘舟的人。我把他们置作替代的。我淹没不信我表征的人。看哪！一般受过警告的人结果如何?","translator":"王静斋"},{"sura":10,"aya":74,"text":"在他以后，我又把列使遣至他们的族人，而现给它们种种明证。但是它们以从前的不信、绝不归信。我如此着封闭一般过当的人心。","translator":"王静斋"},{"sura":10,"aya":75,"text":"在他们以后，我又把母撤、哈伦持我的表征。派到匪喇傲和他的党徒那里。但是他们高傲了，他们原是一般恶民。","translator":"王静斋"},{"sura":10,"aya":76,"text":"当出我御前的真理达到的时候，他们就说：（这确是明明的邪术。）","translator":"王静斋"},{"sura":10,"aya":77,"text":"母撒说：在真理达到你们的时候，你们肯指着它说；[这是邪术]吗?行邪术的人不能得意。","translator":"王静斋"},{"sura":10,"aya":78,"text":"他们说：（你来为的是制止我们远离曾见自己祖先所守的，你俩在此地作领袖吗?我们必不信你二人。）","translator":"王静斋"},{"sura":10,"aya":79,"text":"匪喇傲说：（你们把一般老练的术士给我带来！）","translator":"王静斋"},{"sura":10,"aya":80,"text":"及术士来到的时候，母撤就对他们说：（你们掷出所欲掷出的！）","translator":"王静斋"},{"sura":10,"aya":81,"text":"当他们郑出的时候，母撒就说：（你们所发现的是邪术。安拉一定即将破坏它。安拉绝不成全造恶的人的行为。）","translator":"王静斋"},{"sura":10,"aya":82,"text":"安拉以自己的话奠定真理，纵令为恶的人憎嫌。","translator":"王静斋"},{"sura":10,"aya":83,"text":"而后信母撒的只是他的族人中一般子孙，在畏惧匪喇傲暨其党徒惩治着。匪喇傲实是在地上高傲的，他实属于妄动的。","translator":"王静斋"},{"sura":10,"aya":84,"text":"母撒说：（族人们哪！若是你们归信安拉了，你们就当仰赖他。如果你们是顺服的了，）","translator":"王静斋"},{"sura":10,"aya":85,"text":"他们说：（我们惟仰赖安拉了。我们的养主啊！你不容暴民蹂躏我们！","translator":"王静斋"},{"sura":10,"aya":86,"text":"你以自己的怜悯教我们脱开逆民吧！）","translator":"王静斋"},{"sura":10,"aya":87,"text":"我默谕母撒和他的弟兄！你俩为自己的族人在埃及住家！你们就以住房作朝堂！你们当立拜功！你给众穆民报喜信！","translator":"王静斋"},{"sura":10,"aya":88,"text":"母撒说：（我们的养主啊！你确把今生的装饰和资财赐给匪喇敖和他的党徒了；我们的养主啊！为的是他们错过你的道路吗?我们的养主啊！你抹去他们的资财！你封琐他们的心而致他们不信，直到看见痛刑为止。）","translator":"王静斋"},{"sura":10,"aya":89,"text":"主说：（你俩的祈求必获允纳了，你俩务须守正！不要追随一般无知的人的道路。）","translator":"王静斋"},{"sura":10,"aya":90,"text":"我令以斯拉衣来的子孙渡海，匪喇傲和他的军兵在妨害与压迫着追赶他们。及至他被淹的时候，他就说：我归信：（除却以斯拉衣采的子孙所信的主以外，再无一受拜的，我是属于顺服的。）","translator":"王静斋"},{"sura":10,"aya":91,"text":"现时吗? 从前你违犯了，你原是造恶的人。","translator":"王静斋"},{"sura":10,"aya":92,"text":"今日，我解救你连带你的躯体，俾作后人的表征。许多人确是疏忽我的表征的。","translator":"王静斋"},{"sura":10,"aya":93,"text":"我确使以斯拉衣来的子孙居在可爱的地方。我把一部分美品供给他们。他们起争议。只是在得到知识以后。你的养主将在复生日对于他们所争议的从中判定。","translator":"王静斋"},{"sura":10,"aya":94,"text":"如果你对于我所颁降的有所怀疑了，你就问那在你以前读经的人。的确，真理自你的养主上来到了，你莫怀疑。","translator":"王静斋"},{"sura":10,"aya":95,"text":"你莫不信安拉的表征而致归为折本的。","translator":"王静斋"},{"sura":10,"aya":96,"text":"经你养主的话所决定的那般人，","translator":"王静斋"},{"sura":10,"aya":97,"text":"纵使得到各项异迹，则非亲见痛刑必定不信。","translator":"王静斋"},{"sura":10,"aya":98,"text":"除却郁奴司的族人以外，从无有一个城池归信，他的归信遂就于它有济。当他们归信的时候，我给他们释去今生的辱刑；我教他们享受，直到一个时期。","translator":"王静斋"},{"sura":10,"aya":99,"text":"设若你的养主意欲……了，在地上一切所有的人统都归信。你强迫世人成为穆民吗？","translator":"王静斋"},{"sura":10,"aya":100,"text":"人非借着真主的许可，必不能归信。真主将把污秽置在一切无理智的人上。","translator":"王静斋"},{"sura":10,"aya":101,"text":"你说：（你们看哪！在天地间的是些什么? 种种表征和传警告的，不利于背信的民众。","translator":"王静斋"},{"sura":10,"aya":102,"text":"他们只等待类如以前过去的那般人的时日。你说：（你们等待吧！我实是随同你们等待的。）","translator":"王静斋"},{"sura":10,"aya":103,"text":"我解救列使和众归信的人。 我如此着决定解救众穆民。","translator":"王静斋"},{"sura":10,"aya":104,"text":"你说：（众人哪！如果你们对于我的教门有怀疑了——。我不拜你们撇开安拉所拜的，我只是拜那取去你们性命的安拉。我受命为归信者。","translator":"王静斋"},{"sura":10,"aya":105,"text":"你当倾向正教，莫作信匹偶的，","translator":"王静斋"},{"sura":10,"aya":106,"text":"且莫撇开安拉拜那于你无利无害的。如果你实作了，那时候，你必归为背义的人了。","translator":"王静斋"},{"sura":10,"aya":107,"text":"若是安拉降给你患难了，除他以外无一释去它的。若是他欲对你施恩了，无一阻其特恩的。他使它达到自所意欲（达到）的众仆，他是多恕的，特慈的。","translator":"王静斋"},{"sura":10,"aya":108,"text":"你说：（众人哪！出自养主的真理确已达至你们了；得正道的人惟利他自身得正道，迷误的人惟害他自身迷误。我不是保护你们的。）","translator":"王静斋"},{"sura":10,"aya":109,"text":"你跟从所告谕你的吧！你当忍耐，直至安拉施行判断。他是最优的判断者。","translator":"王静斋"},{"sura":11,"aya":1,"text":"（阿里甫，俩模，拉。）这经典是文词坚固的，且由精明尽知的主上分析清楚的；","translator":"王静斋"},{"sura":11,"aya":2,"text":"为是你们莫拜安拉以外的。我实是代他向你们传警告报喜信的。","translator":"王静斋"},{"sura":11,"aya":3,"text":"并为你们求养主的饶恕，更要你们向他降罪。果然，他就教你们享受，美满地享受，直到一定的时期。他把自己的特恩赐给每个有功绩的人。如果你们不肯了，我实替你们恐惧大日的刑罚。","translator":"王静斋"},{"sura":11,"aya":4,"text":"你们惟归于安拉。他是全能于万事的须知他们胸怀怨恨，为的是对主隐瞒。","translator":"王静斋"},{"sura":11,"aya":5,"text":"须知他们用衣服遮掩的时候，他知道他们的瞎昧和明扬。他深知胸中所怀的。","translator":"王静斋"},{"sura":11,"aya":6,"text":"地上一切动物，无不由安拉负担它的给养。他知道它的稳定和贮藏。统是在明典中。","translator":"王静斋"},{"sura":11,"aya":7,"text":"他那真主在六日内造化了天地·他的[阿勒世]原设在水上，卒至他试证你们的哪一个是在作为上最善的。你若说：（你们将在死后复活）不信的人们必说：（这只是明显的邪术。）","translator":"王静斋"},{"sura":11,"aya":8,"text":"我若对他们延迟刑罚，直到一定的时期了，他们必说：（它被什么制止了?）须知刑罚来到的那一日，不再退转，他们被素所嘲笑的包围了。","translator":"王静斋"},{"sura":11,"aya":9,"text":"我要教人尝到我的慈惠而又剥去的时候，他便是绝望的，忘恩的。","translator":"王静斋"},{"sura":11,"aya":10,"text":"我若教他在遭难以后尝着恩典了，他必说：（种种患难自我上去掉了。）他必定是狂喜自矜的。","translator":"王静斋"},{"sura":11,"aya":11,"text":"惟有忍耐为善的人不然。这般人应获饶恕和巨赏。","translator":"王静斋"},{"sura":11,"aya":12,"text":"恐怕你撇掉所告谕你的一部分；你因着他们说：（为何不降给他宝物或有一天使随他同来。）而心觉窄狭，你只是一个传警告者。安拉是保护万事的。","translator":"王静斋"},{"sura":11,"aya":13,"text":"不然，他们说：（他造作它）吗? 你说：（如果你们的所言属实，你们就现出和它相似的自撰的十章来，你们就撇开安拉求你们所能（唤到）的人。","translator":"王静斋"},{"sura":11,"aya":14,"text":"他们若未应承你们，你们当知道那是藉安拉的知识被降者。当知除他以外再无有主。你们可是顺服的吗?","translator":"王静斋"},{"sura":11,"aya":15,"text":"讨今生及其装饰的人我就在今世把他们所作的完全还给他们，并不少给。","translator":"王静斋"},{"sura":11,"aya":16,"text":"这般人在后世，除却火狱别无所获。他们所作的在那里无效了。其所作的乌有了。","translator":"王静斋"},{"sura":11,"aya":17,"text":"守他养主的明证，且有主的见证者追随他，在它以前更有母撒的经典，在被追随和慈惠着，这类人可（若不是如此的人）吗?这般人均信它。各派系之中不信它的人，火狱就是对他约许的地方。你不要对它怀疑。那确是出自养主的真理，但是世人的大半不信。","translator":"王静斋"},{"sura":11,"aya":18,"text":"谁是比往安拉上造谎的人更为不义的?这般人被带到养主上。众见证者说；这般人是那往养主上说谎话的·须知背义的人应遭安拉的驱远。","translator":"王静斋"},{"sura":11,"aya":19,"text":"那些人阻碍安拉的道，并给它觅曲折。他们且不信后世。","translator":"王静斋"},{"sura":11,"aya":20,"text":"这般人不是在地上能作抵抗的。他们撇开安拉得不到爱护的，均将遭受加倍的刑罚。他们不能听，且也看不见。","translator":"王静斋"},{"sura":11,"aya":21,"text":"这一般是自负己身的。其所造作的已竟消失了。","translator":"王静斋"},{"sura":11,"aya":22,"text":"无疑他们在后世均是最伤折的。","translator":"王静斋"},{"sura":11,"aya":23,"text":"归信并作善举且安心敬拜养主的人，均是永居天园的。","translator":"王静斋"},{"sura":11,"aya":24,"text":"这两伙人的情形，如同失目聋耳的人，和能视能听的。他俩的情形相等吗? 你们怎不受劝呢?","translator":"王静斋"},{"sura":11,"aya":25,"text":"我确把努海遣至他的族人说：叼我实是对你们公开警告：","translator":"王静斋"},{"sura":11,"aya":26,"text":"莫拜安拉以外的；我实为你们骇怕苦日的刑罚。","translator":"王静斋"},{"sura":11,"aya":27,"text":"他族人中一般背逆的伟人说：（我们看你只是和我们一样的人。我们在表面上看出，那追从你的全是我们之中的下等人。我们看不出你们优越我们。我们实揣度你们是说谎的。）","translator":"王静斋"},{"sura":11,"aya":28,"text":"努海说：我的族人哪！你们看出来了吗?我若守我养主的明证，他把自己的慈惠赏给我，你们茫无所知，其时你们厌恶它着，我能因它强迫你们吗?","translator":"王静斋"},{"sura":11,"aya":29,"text":"我的族人哪！我不为此向你们求财物，我的报酬惟在安拉上。我必不驱逐众归信的人，以他们是将遇见养主的。我看你们只是一伙愚民。","translator":"王静斋"},{"sura":11,"aya":30,"text":"我的族人哪！我若驱逐他们了，谁来对付安拉相助我？你们怎不受劝呢?","translator":"王静斋"},{"sura":11,"aya":31,"text":"我不向你们自称：据有安拉的仓库，并知道目所不见的，我也不说自己确是天使，我更不指你们目所轻视的人说：安拉绝不赐给他们好处。安拉至知他们心中所怀的。果然那样，我必属于迫害的人。","translator":"王静斋"},{"sura":11,"aya":32,"text":"他们说：（努海！你对我们争辩且过于争辩了。如果你的话属实，你就对我们现出自所许下的。）","translator":"王静斋"},{"sura":11,"aya":33,"text":"他说：（若是安拉意欲[现出]了，他就把刑罚观给你们，你们本不是能抵抗的。","translator":"王静斋"},{"sura":11,"aya":34,"text":"我欲对你们进忠告的时候，若是安拉要教你们迷路了，我的忠告不利于你们。他是你们的养主，你们惟被归于他。）","translator":"王静斋"},{"sura":11,"aya":35,"text":"不然，他们说：（他造作它了）吗? 你说：（如果我造作它了，我的作恶惟有伤我；我是对于你们的罪恶无干的。）","translator":"王静斋"},{"sura":11,"aya":36,"text":"努海奉到默示：你的族人仅有那已然归信的人归信；你不要因他们的行为有所忧虑。","translator":"王静斋"},{"sura":11,"aya":37,"text":"你在我当面奉我的命令造舟吧！你不要为着一般逆徒呼吁我，他们是一定被淹的。","translator":"王静斋"},{"sura":11,"aya":38,"text":"努海造舟，他族人中一切伟人每逢在那里经过，便就嘲笑他。他说：（若是你们嘲笑我了，我必依照你们的嘲笑来嘲笑你们。","translator":"王静斋"},{"sura":11,"aya":39,"text":"不久你们就知道：什么人遭辱刑，罹常刑。）","translator":"王静斋"},{"sura":11,"aya":40,"text":"迨至我的命令达到，炉子滚起的时候，我就说：（你从每类上把一对两性和你的家眷，除过（关于刑罚的）话已对他在先的；与归信的人载在船上！）随他归信的不过少数人。","translator":"王静斋"},{"sura":11,"aya":41,"text":"努海说：（你们在航行和停泊的时候，奉安拉之名乘舟吧！我的养主一定是多恕的，特慈的。）","translator":"王静斋"},{"sura":11,"aya":42,"text":"舟载着他们航行在如山的涛浪中，努海唤他儿子，其时他儿子正在远处、我的小儿啊！你随我们乘舟吧！你不要追随背逆的人们。","translator":"王静斋"},{"sura":11,"aya":43,"text":"他说：（我即将委身那护我不受永淹的山）努海说：（今日除却得安拉怜恤的人，并无一抗他的命令作护佑。）涛浪在他两个中间隔开，他遂就归为被淹的了。","translator":"王静斋"},{"sura":11,"aya":44,"text":"有的命地吞水，命天止雨，水就下渗，事已决定了。舟停泊在朱定。当时有的说：（背义的民众灭亡了！）","translator":"王静斋"},{"sura":11,"aya":45,"text":"努海呼吁他的养主说：（我的养主啊！我的儿子是我家的人，你的约是确定的。你是最善于判断者。）","translator":"王静斋"},{"sura":11,"aya":46,"text":"主呼努海说：（他不是你的眷属；他是一个行为不良的，你不要向我请求自所不知的。我劝你不要列入愚人。）","translator":"王静斋"},{"sura":11,"aya":47,"text":"他说：（养主啊！求你护佑我，莫向你请求自所不知的。若是你不饶恕我，不怜恤我，我就归为伤折的人了。）","translator":"王静斋"},{"sura":11,"aya":48,"text":"有的呼努海说：你乘着我赐的平安，和降给你并降给随同你的群众的福庆下去吧！另有一般群众 我即将教他们享受，而后他们遭受我的痛刑。","translator":"王静斋"},{"sura":11,"aya":49,"text":"这是秘密的消息，我把它默谕你，你和你的族人前此本不知道它。你当忍耐，敬慎的人必获善果。","translator":"王静斋"},{"sura":11,"aya":50,"text":"并向阿代差去他们的弟兄——扈代。他说：我的族人哪！你们当拜安拉。在你们无一除过他的主。你们只是造作的。","translator":"王静斋"},{"sura":11,"aya":51,"text":"我的族人哪！我不向你们讨报酬。我的报酬惟在那造化我的主上，你们怎不了解呢?","translator":"王静斋"},{"sura":11,"aya":52,"text":"我的族人哪！你们当求养主的饶恕，且当向他悔罪。果然，他就由云上降给你绵雨，并给你们力上增力。你们不要为恶着背逆。","translator":"王静斋"},{"sura":11,"aya":53,"text":"他们说：（扈代！你未曾对我们发现一项明证。我们必不因你的话放弃自己的受拜的，我们绝不信你。","translator":"王静斋"},{"sura":11,"aya":54,"text":"我们一旦发言，不兔就有自己的一部分受拜的使你遭患。他说：（我必求安拉作见证，你们也当见证！我是于你们抛开他所拟的匹偶上无干的。","translator":"王静斋"},{"sura":11,"aya":55,"text":"任你们一同对我施诡计，不再姑容我。","translator":"王静斋"},{"sura":11,"aya":56,"text":"我实仰赖安拉——我的养主，也是你们的养主。所有的动物，惟有真主能于制服它。我的养主实是在正道上。","translator":"王静斋"},{"sura":11,"aya":57,"text":"如果你们不肯了，我已向你们宣达我的使命了。我的养主召来另一伙人作替代，你们不能妨碍真主一事。我的养主实是监察万事的。","translator":"王静斋"},{"sura":11,"aya":58,"text":"当我的命令已至的时候。我就以自己的慈惠解救扈代和随他归信的人，我救他们脱离酷刑。","translator":"王静斋"},{"sura":11,"aya":59,"text":"这是阿代，他们不承认养主的表征，违抗他的列使。他们追从一切强梁顽梗者的命令。","translator":"王静斋"},{"sura":11,"aya":60,"text":"他们在今世并在复生日被咒诅追随着。须知阿代不信养主了。须知阿代——扈代的族人远离了！","translator":"王静斋"},{"sura":11,"aya":61,"text":"并向赛母待遣去他们的弟兄——沙里哈，他说：（我的族人哪！你们当拜安拉；在你们无一除过他的主。他从地上造化了你们，他教你们居住在地上。你们当求他饶恕，你们当向他悔罪。你的养主实是接近的，允承的。）","translator":"王静斋"},{"sura":11,"aya":62,"text":"他们呼沙里哈说：（前此你原是在我们之中有希望的，）","translator":"王静斋"},{"sura":11,"aya":63,"text":"他说：（我的族人哪！你们看出来了吗？我若守定养主的明证，他把自己的慈惠赏给我，如果我违犯他了，谁抗安拉相助我呢? 你们只增给我伤折罢了。","translator":"王静斋"},{"sura":11,"aya":64,"text":"我的族人哪！这是安拉的母驼；对你们的表征。你们任它在安拉的地上吃，不得加害它，至遭快要来到的刑罚。）","translator":"王静斋"},{"sura":11,"aya":65,"text":"于是他们宰死它了。他又说：（你们在自己宅内安逸三天吧，这是不假的期许。","translator":"王静斋"},{"sura":11,"aya":66,"text":"当我的命令既至的时候，我就以自己的慈惠，并从那日的现丑上，解救沙里哈和随他归信的人。你的养主就是大能的，优胜的。","translator":"王静斋"},{"sura":11,"aya":67,"text":"背义的人遭遇一声呜；次日，他们是在自己宅内伏地而死的，","translator":"王静斋"},{"sura":11,"aya":68,"text":"好像他们未曾在那里住过。须知赛母待确不信其养主了。须知赛母待离远了！","translator":"王静斋"},{"sura":11,"aya":69,"text":"我的列使确向易卜拉欣报喜信了。他们说：（平安！）他（亦）说：（平安！）他立时现上熟牛犊肉。","translator":"王静斋"},{"sura":11,"aya":70,"text":"当他见他们不往那上伸手的时候，他对他们认识不清，怀着一种畏惧。他们说：（你不要骇怕，我们实是被差至鲁脱族人的。）","translator":"王静斋"},{"sura":11,"aya":71,"text":"他的妇人正在站立着，遂就发笑了。我就以易司哈各并易司哈各后的叶而孤白给他报喜信。","translator":"王静斋"},{"sura":11,"aya":72,"text":"她说：（奇怪呀！我已年迈，这是我的老夫，我尚能产生吗?这实是一件奇事。）","translator":"王静斋"},{"sura":11,"aya":73,"text":"他们说：（你对于安拉的事情惊异吗?圣眷哪！安拉的怜恤和他的福庆是在你们上，他实是堪称赞的，有荣威的。）","translator":"王静斋"},{"sura":11,"aya":74,"text":"当易卜拉欣不再骇怕，得到喜信的时候，他就为着鲁脱的族人对我起辩论。","translator":"王静斋"},{"sura":11,"aya":75,"text":"易卜拉欣实是一个大度，哀矜，归向的。","translator":"王静斋"},{"sura":11,"aya":76,"text":"我说：（易卜拉欣！你不要这样。你养主的命令确已来到了，不可遏止的刑罚必定达至他们。）","translator":"王静斋"},{"sura":11,"aya":77,"text":"我的列使来至鲁脱的时候，他就因他们忧虑，因他们心窄了。他说：（这是一个难日）","translator":"王静斋"},{"sura":11,"aya":78,"text":"他的族人迅速到来。他们先前干了些个恶事。鲁脱说：（我的族人哪！我的这些女儿）她们是至使你们清洁的。你们当畏惧安拉，不要使我对我的来宾惭愧。你们之中无一个正经男儿吗?","translator":"王静斋"},{"sura":11,"aya":79,"text":"他们说：（你确知道！我们对你的女儿无什么要求，你实知道我们所意欲的。）","translator":"王静斋"},{"sura":11,"aya":80,"text":"他说：（宁愿我有一种对你们的力量。或我投奔一个有力的支柱。）","translator":"王静斋"},{"sura":11,"aya":81,"text":"他们说：（鲁脱！我们是你的养主差来的，他们不能贴近你。你在夜初携眷出走吧！你们任何一个人不得回顾。你的妻在外，她必遭遇他们所遭遇的。他们的时间是在清晨，清晨岂不是临近的吗?）","translator":"王静斋"},{"sura":11,"aya":82,"text":"当我的命令已至的时候，我就使那城池上下倒置。我把在你养主御前盖过印子的密集的泥石下到它上了，","translator":"王静斋"},{"sura":11,"aya":83,"text":"这是离着背义的人不远的。","translator":"王静斋"},{"sura":11,"aya":84,"text":"并向买大严差去他们的弟兄——书尔布。他说：（我的族人哪！你们当拜按拉，在你们无一除过他的主，你们不得减少量的称的。我确见你们是宽裕的。我实为你们骇怕包围的那一日的刑罚。","translator":"王静斋"},{"sura":11,"aya":85,"text":"我的族人哪！你们要秉公给足了量的称的，你们不要少给人东西；你们不要在地上意图造恶着过为。","translator":"王静斋"},{"sura":11,"aya":86,"text":"如果你们是归信的人了，安拉留下的是于你们至好的。我不是维护你们的。）","translator":"王静斋"},{"sura":11,"aya":87,"text":"他们说：（书尔布！你的拜功教你命我们放弃自己列祖所拜的，或是放弃对于自己的财产任意行事吗? 你是有涵养的获指导的。）","translator":"王静斋"},{"sura":11,"aya":88,"text":"他说：（我的族人哪！你们看出来了吗？我若守我养主的明证，他亲自赐给我好的给养。——我不欲反对你们而自蹈禁忌；我惟欲竭力的调整，我的顺适惟藉安拉。我惟托靠他。","translator":"王静斋"},{"sura":11,"aya":89,"text":"我惟归向他。我的族人哪！你们不要被反抗我贻你们遭遇像努海的族人，或扈代的族人，或沙里哈的族人所遭遇的。鲁脱的族人去你们不远。","translator":"王静斋"},{"sura":11,"aya":90,"text":"你们当求养主的饶恕，且当向他悔罪。我的养主实是特慈的，爱悦的。","translator":"王静斋"},{"sura":11,"aya":91,"text":"他们呼书尔布说：（你说的话，有我们很不明白的。我们看你是本族中无能为的。设不是你的同族了，我们必用石块击死你。你本不是超越我们有权威的）","translator":"王静斋"},{"sura":11,"aya":92,"text":"他说：（我的族人哪！我的同族在你们认为比安拉还优上吗?你们把真主当作被弃置背后的了，我的养主尽悉你们的作为。","translator":"王静斋"},{"sura":11,"aya":93,"text":"我的族人哪！你们尽力去作吧！我是一定工作的。不久你们就知道：谁是遭丑刑的，谁是说谎话的?你们等待吧！我是随同你们等待的。）","translator":"王静斋"},{"sura":11,"aya":94,"text":"当我的命令来到的时候，我以自己的慈惠解救书尔布暨同他归信的人。背义的人遭受一声呜。次晨，他们是在自己宅内伏地而死的；","translator":"王静斋"},{"sura":11,"aya":95,"text":"恰像他们未在那里住过。须知买大严离远，就如赛母待离远了，","translator":"王静斋"},{"sura":11,"aya":96,"text":"我确把母撒藉我的异迹和明证","translator":"王静斋"},{"sura":11,"aya":97,"text":"差至匪喇傲与他的伟人了，但是他们随从匪喇傲的命令。匪喇傲的命令实不正当。","translator":"王静斋"},{"sura":11,"aya":98,"text":"他在复生日走到他族人的前面，使他们进火狱。应进的地方太恶了！","translator":"王静斋"},{"sura":11,"aya":99,"text":"他们在这里并在复生日被咒诅追随着，所给的赠品太恶了！","translator":"王静斋"},{"sura":11,"aya":100,"text":"这是各城的消息，我把它述说给你；其中有存在的，有消失的。","translator":"王静斋"},{"sura":11,"aya":101,"text":"我未亏负他们，但是他们自亏己身了。你养主的命令到达的时候，他们撇开安拉所拜的各主，不能代为避免一事。它们只增给他们损折。","translator":"王静斋"},{"sura":11,"aya":102,"text":"你的养主惩治背义的城池时候，他的惩治也是这样。主的惩治确是严酷致痛的。","translator":"王静斋"},{"sura":11,"aya":103,"text":"此对于畏惧后世刑罚的人确含有一种表征。这是聚起众人的那一日，这是应该见到的那一日。","translator":"王静斋"},{"sura":11,"aya":104,"text":"我迟迟发现它，只因待至一定的期限。","translator":"王静斋"},{"sura":11,"aya":105,"text":"当它来到的那一日，人非奉主的命令，则不能发士气他们之中有厄运的和幸运的。","translator":"王静斋"},{"sura":11,"aya":106,"text":"厄运的人是在火狱中，他们在那里发出一呼一吸的吼声。","translator":"王静斋"},{"sura":11,"aya":107,"text":"天地存在一日，他们就永居在那里，除非你的养主意欲…时候。你的养主是为所欲为的。","translator":"王静斋"},{"sura":11,"aya":108,"text":"幸运的人是在天园中。天地存在一日，他们就永居在那里，除非你的养主意欲……的时候，这是无间断的恩赏。","translator":"王静斋"},{"sura":11,"aya":109,"text":"你对于这般人所拜的不要疑惑。他们只是拜若他们列祖先前所拜的。我必把他们应得的一分完全施给他们，并不减少。","translator":"王静斋"},{"sura":11,"aya":110,"text":"我确曾把经典赐给母撒。而后有人对它起争议了。设非出自养主先前的话。必就在他们中间判定了。他们实是在对它不安的怀疑中。","translator":"王静斋"},{"sura":11,"aya":111,"text":"的确，每一个人八当其复起的时候u你的养主是必定还给他们自所作下的。真主尽知他们的作为。","translator":"王静斋"},{"sura":11,"aya":112,"text":"你和随你忏悔的人要如命端正！不得过分。真主看得见你们的作为。","translator":"王静斋"},{"sura":11,"aya":113,"text":"你们不得偏于一般背义的人，而致与火接触。在你们抛开安拉无有爱护的，且不复获得相助。","translator":"王静斋"},{"sura":11,"aya":114,"text":"你要在白昼的两端，和夜内几个时间举拜功。种种善举可免除一切罪恶。这是提醒一般受劝的人。","translator":"王静斋"},{"sura":11,"aya":115,"text":"你当忍耐，安拉必不湮没为善者的报酬。","translator":"王静斋"},{"sura":11,"aya":116,"text":"他们以前历代的人，除却其中被我解救的少数人以外，有德性的人们为什么不阻止在地上为恶?背义的人随从所令他们享受的。他们本是为恶的人。","translator":"王静斋"},{"sura":11,"aya":117,"text":"当居民清正的时候，你的养主必不因为背义灭绝些个城池。","translator":"王静斋"},{"sura":11,"aya":118,"text":"设若你的养主意欲（集中）了，他必教世人成为一伙。","translator":"王静斋"},{"sura":11,"aya":119,"text":"他们时常起争议；蒙你养主慈惠的人在外。他就为这个造化了他们。","translator":"王静斋"},{"sura":11,"aya":120,"text":"你养主的话：（我必以镇呢和人类一同充满火狱）已然应验了。我用以安定你心的列使的消息，我统都述说给你，此中的真理、劝化、并提醒众穆民，已竟达至你了。","translator":"王静斋"},{"sura":11,"aya":121,"text":"你对一般不信的人说：（你们尽力去作吧！我是一定实作的。）","translator":"王静斋"},{"sura":11,"aya":122,"text":"你们等待吧！我实是等待的。","translator":"王静斋"},{"sura":11,"aya":123,"text":"天地的隐秘惟属安拉（知道）事情统被归于他。你当敬拜他，当要托靠他。你的养主必不忽于你们的作为。","translator":"王静斋"},{"sura":12,"aya":1,"text":"（阿里甫、俩模、拉。）这是明典的文词。","translator":"王静斋"},{"sura":12,"aya":2,"text":"我确降此阿拉伯文的古兰，好教你们了解。","translator":"王静斋"},{"sura":12,"aya":3,"text":"我借着向你启示这古兰，把最好的故事述说给你。","translator":"王静斋"},{"sura":12,"aya":4,"text":"从前你确属于忽略的。那时候，郁素福对他的父说：（父啊！我确梦见十一星和日月向我下拜。）","translator":"王静斋"},{"sura":12,"aya":5,"text":"他说：（我的小儿啊！）你不要把自己的梦象述说给你的弟兄们。若然，他们就对你施诡计，切实施诡计，恶魔确是人类的明敌。","translator":"王静斋"},{"sura":12,"aya":6,"text":"你的养主如此着选择你，教给你解梦，并对你和叶而孤拜的子孙完成自己的恩典；就如从前对你的两个祖先；易卜拉欣和易司哈各完成它一样。你的养主实是深知的，明哲的。","translator":"王静斋"},{"sura":12,"aya":7,"text":"的确，在郁素福和他兄弟们（往事）中含有对于究问的人们种种表征。","translator":"王静斋"},{"sura":12,"aya":8,"text":"那时候，他们说：（郁素福和他弟弟在父的跟前比我们这般雄壮的人更是可爱的。父亲实是在明显的错误中。","translator":"王静斋"},{"sura":12,"aya":9,"text":"你们杀死郁素福，或把他投在一个地方！父亲就仅只爱喜你们。你们在此以后归为义民，","translator":"王静斋"},{"sura":12,"aya":10,"text":"他们之中一个发言人说：（你们不要杀死郁素福，你们把他投在井底！他就被一部分旅客拾去。如果你们肯于实行了，……）","translator":"王静斋"},{"sura":12,"aya":11,"text":"他们说：（父啊！你怎么对于郁素福不放心我们呢?我们实是对他忠诚的。","translator":"王静斋"},{"sura":12,"aya":12,"text":"明日，你遣他和我们同去，吃鲜果，作游戏，我们是一定维护他的。）","translator":"王静斋"},{"sura":12,"aya":13,"text":"他说：（你们带了他去，这事实在教我忧虑。我恐怕他在你们顾及不到的时候，被狼吃去。）","translator":"王静斋"},{"sura":12,"aya":14,"text":"他们说：（有我们这伙雄壮的人，而任狼把他吃去，那时候，我们便是沦丧的人了。）","translator":"王静斋"},{"sura":12,"aya":15,"text":"当他们带了他去，决意把他投在井底的时候，……。我默谕他：（你将必对他们宣布这事，那时候他们并不觉悟。）","translator":"王静斋"},{"sura":12,"aya":16,"text":"晚上，他们哭着来至其父，","translator":"王静斋"},{"sura":12,"aya":17,"text":"说：（父啊！我们赛跑去，把郁素福置在什物的跟前，既而被狼吃去了。纵令我们是实言的，你必不相信。）","translator":"王静斋"},{"sura":12,"aya":18,"text":"他们献上抹以假血的衬衫。他说：（你们的心灵把事件当作平常了。惟有好自忍耐罢了。 安拉是于你们所称述者被求相助的。）","translator":"王静斋"},{"sura":12,"aya":19,"text":"旅客到达，他们派出自己寻水草的人参那人放下他的木桶，说：（呀喜信，这是一个男童，）他们把他当作货品着隐藏起来。安拉深知他们的作为。","translator":"王静斋"},{"sura":12,"aya":20,"text":"他们以廉价——有数的几个钱卖出他去了。他们本是不贪恋他的。","translator":"王静斋"},{"sura":12,"aya":21,"text":"那买他的埃及人，对自己的妇人说：（要你优待他！或许他将于我们有利，或者我们拿他当作儿子。）我如此着令郁素福住在此地。我把解梦教给他。安拉是操纵自己事情的。但是世人的大半不知道。","translator":"王静斋"},{"sura":12,"aya":22,"text":"当他达其壮年的时候，我就赐给他智慧与学识。我如此报酬一般为善的人。","translator":"王静斋"},{"sura":12,"aya":23,"text":"郁素福在她房内的那一女子闭上房门，调戏郁素福说：（来！）他说：（祈求安拉护佑！我的主人确切优待我了，背义的人必不能得到所愿。）","translator":"王静斋"},{"sura":12,"aya":24,"text":"她却倾慕他；他也倾慕她了，设非他看见养主的明证了，……。如此，只为由他上转移罪恶和丑污。他实是我的清白仆人。","translator":"王静斋"},{"sura":12,"aya":25,"text":"他二人往门上争奔，她由后面撕裂他的衬衣。他二人在门上撞见她的主子。她说：（那意欲对你眷属行恶事者的报应，不外是监禁或痛刑。","translator":"王静斋"},{"sura":12,"aya":26,"text":"郁素福说：（她调戏我了。）她家属中的一个证人作见证；他的衬衣若自前面撕裂，是她说实言了，他是说谎的。","translator":"王静斋"},{"sura":12,"aya":27,"text":"他的衬衣若自后面撕裂，是她撤谎，他是说实言的）","translator":"王静斋"},{"sura":12,"aya":28,"text":"及王看他的衬衣自后面撕裂的时候，就说：（这是你们的诡计，你们的诡计实在是大的）","translator":"王静斋"},{"sura":12,"aya":29,"text":"郁素福！你不要发表这事。不要对外宣扬。你为自己的罪求恕吧！你实是有过错的。","translator":"王静斋"},{"sura":12,"aya":30,"text":"城内一般妇女说：（王后调戏她的青年了；恋爱深入她的心膜了；我们看她实是在显著的错误中。）","translator":"王静斋"},{"sura":12,"aya":31,"text":"当她闻得她们背地谈论的时候，她就派人把她们约来，预备宴席，付给每人一把刀子，说：（出来至她们，）及至她们看见他的时候，她们就认为他是一个伟大的。她们〔误〕割自己的手，说：（真主清净！这不是人，这只是一位贵天仙。）","translator":"王静斋"},{"sura":12,"aya":32,"text":"她说：（这是你们因他责备我的那个人。我实在调戏他，他求护佑了。他若不履行我的命令，必被监禁；一定归为卑贱的。）","translator":"王静斋"},{"sura":12,"aya":33,"text":"他说：（我的养主啊！我宁愿坐监而不从她们的诱导；你若不为我转移她们的诡计，我就倾向她们，而归为愚人一流了。）","translator":"王静斋"},{"sura":12,"aya":34,"text":"于是，他的养主应承他，为他转移她们的诡计了。真主实是能听的，深知的。","translator":"王静斋"},{"sura":12,"aya":35,"text":"他们在亲见各项见证以后复又主张：（必须暂时把他监禁起来。）","translator":"王静斋"},{"sura":12,"aya":36,"text":"两个青年随他一同入监，一个说：（我梦见榨酒；）另一个说：（我梦见头上顶着一个面包，飞鸟就着它吃。）求你对我们明白解释。我们看你实属为善的人。","translator":"王静斋"},{"sura":12,"aya":37,"text":"郁素福说：（所供给你俩的食品在未到以前，我就对你俩明白解释那梦，这是养主所教给我的。我必撇弃一伙子不信安拉且不信后世者的教道。","translator":"王静斋"},{"sura":12,"aya":38,"text":"我跟从自己的祖先——易卜拉欣、易司哈各、叶而孤白的教道。我们不应奉一物给安拉作匹偶。这是安拉对我们并对众人的特恩，但是世人的大半不知感念。）","translator":"王静斋"},{"sura":12,"aya":39,"text":"（二位监友啊！杂乱的主是至优的，或是独一权威的安拉呢?","translator":"王静斋"},{"sura":12,"aya":40,"text":"你们撇去他所拜的，只是你们和你们列祖所命的名称，安拉并未因此降一明证。判断惟在安拉。他命你们惟独拜他。这是正教，但是，世人的大半不知道。","translator":"王静斋"},{"sura":12,"aya":41,"text":"二位监友啊！你俩有一个给主人斟酒；另一个被挂，而飞鸟自他的头上吃。你俩追问的事情已然判定了。）","translator":"王静斋"},{"sura":12,"aya":42,"text":"郁素福对那自知即将得救的人说：（要你在主人跟前提我一声！）而后，恶魔使他忘记提给他的主人了。郁素福就在监里留住几年。","translator":"王静斋"},{"sura":12,"aya":43,"text":"王说：（我梦见七头瘦牛吃七头肥牛；更有七颗青穗，并另外些个枯干的。众伟人哪！如果你们善于解梦了，就为我的梦象作解答。）","translator":"王静斋"},{"sura":12,"aya":44,"text":"他们说：（这是杂梦，我们不知圆杂梦。）","translator":"王静斋"},{"sura":12,"aya":45,"text":"二人之中已然得救，在一向之后想起的那个人说：（我能对你们明白解释这梦，你们派我前去，）","translator":"王静斋"},{"sura":12,"aya":46,"text":"（郁素福——诚实的人哪！请你为七头肥牛被七头瘦牛所吃，并为七颗绿穗并另外些个枯干的，给我们作解答，好教我返回诸人，俾便他们知道。）","translator":"王静斋"},{"sura":12,"aya":47,"text":"他说：（你们照例栽种七年，把所收割的留在谷穗中，仅余自食的少数。","translator":"王静斋"},{"sura":12,"aya":48,"text":"此后又经过七年亢旱，吃去你们先前贮下的，只留你们保存的一些。","translator":"王静斋"},{"sura":12,"aya":49,"text":"在那以后又有一年，人们在那年得到救济，并在那年压榨。）","translator":"王静斋"},{"sura":12,"aya":50,"text":"王说：（你们把他给我带来，）使者到他跟前的时候，他就说：（要你归问你的主人。那般自割己手的妇人究属什么情形?","translator":"王静斋"},{"sura":12,"aya":51,"text":"我的养主确是深知她们诡计的。）王说：（你们调戏郁素福的时候，究竟是怎么个情形呢?）她们说：（安拉清净！我们不知他有些须罪恶。）王后说：（现下真情毕露了，是我调戏他，他确是实言的。）","translator":"王静斋"},{"sura":12,"aya":52,"text":"（这是好教他知道我未在背后对他负义，并知道安拉不成全负义者的诡计。）","translator":"王静斋"},{"sura":12,"aya":53,"text":"我不自称清洁，欲性确是命人作罪恶的；除却我养主怜恤的人。我养主实是多恕的，特慈的。","translator":"王静斋"},{"sura":12,"aya":54,"text":"王说：（你们把他带到我这里，我特为自身选用。）及其对他 交谈的时候，它就说：（今日之下，你确是在我跟前有地位，且可信赖的。）","translator":"王静斋"},{"sura":12,"aya":55,"text":"他说：（你派我掌这地方的仓库吧！我实是一个有知识的守护者。）","translator":"王静斋"},{"sura":12,"aya":56,"text":"我就这样教郁素福住在那地方，他于那里随意居住，我任便教人得到我的慈惠，我不湮没为善者的报酬。","translator":"王静斋"},{"sura":12,"aya":57,"text":"后世的善赏最益于归信并且敬慎的人们。","translator":"王静斋"},{"sura":12,"aya":58,"text":"郁素福的弟兄们抵达至他，他认识他们，他们不认识他了。","translator":"王静斋"},{"sura":12,"aya":59,"text":"当他为他们备办什物的时候，就说：（你们把同父的弟弟给我带来，你们岂不见我给足量具吗?我是最善于待客的。","translator":"王静斋"},{"sura":12,"aya":60,"text":"如果你们不把他给我带来，你们在我这里就得不到所量的，你们不能接近我。）","translator":"王静斋"},{"sura":12,"aya":61,"text":"他们说：（我们必尽力央求父亲打发他来，我们一定实作。）","translator":"王静斋"},{"sura":12,"aya":62,"text":"郁素福对他的青年们说：（你们把他们的货物装在他们的行囊中，……以便他们回到家中的时候，认得它；好教他们转来。）","translator":"王静斋"},{"sura":12,"aya":63,"text":"当他们回到父亲那里的时候，就说：（父啊！我们被阻不得，籴粮了；你派我们的弟弟同去吧！我们能可余粮。我们必定保护他。）","translator":"王静斋"},{"sura":12,"aya":64,"text":"父说：（我因为他不放心你们，就如从前为他弟兄不放心你们一样。安拉是最善于保护的。他是慈中最慈的。）","translator":"王静斋"},{"sura":12,"aya":65,"text":"他们打开行囊的时候，看见自己的货物已被退还了。他们说：（父啊！我们还要求什么呢?这是我们的货物，已被退还我们了。我们给家属购买日用品。我们保护着弟弟，增加一个驼驮。这是稍微的一点粮食。）","translator":"王静斋"},{"sura":12,"aya":66,"text":"叶而孤白说：（我绝不撒开他和你们同去，待至你们指着安拉与我结约！必把他给我带回；除非你们一并遇难的时候）当他们与他结约的时候，他就说：（安拉是监察我们所说的。）","translator":"王静斋"},{"sura":12,"aya":67,"text":"又说：（我的儿们哪！你们不要由一个门进去；你们要从不同的门进去，我不能为你们避免安拉的一事。判定惟在安拉。我只仰赖他。当令仰赖者惟独仰赖他吧！）","translator":"王静斋"},{"sura":12,"aya":68,"text":"当他们依着父的命令进去的时候，除却暴露叶而弧白心中所怀的爱护外，并末代他们避免安拉一的一事。 他实知道我所教给他的，但是世人的大半不知道。","translator":"王静斋"},{"sura":12,"aya":69,"text":"当他们达至郁素福的时候，他就把他弟弟拥到自己（怀里，）说：（我实是你哥哥，你不要因着他们的作为，有所悲伤。）","translator":"王静斋"},{"sura":12,"aya":70,"text":"当他给他们备办什物的时候，他就把大杯装在他弟弟的行囊里。随后有一个呼唤者喊道： （客人们哪！你们确是盗贼。）","translator":"王静斋"},{"sura":12,"aya":71,"text":"他们就面向它们说：（你们失掉什么了?）","translator":"王静斋"},{"sura":12,"aya":72,"text":"它们说：（我们失掉王的大杯。拿它来的人可获一个驼驮。我是担保这事的。）","translator":"王静斋"},{"sura":12,"aya":73,"text":"他们说：（指着安拉为誓！你们确知道。我们来不是为在这地方上造恶，我们本不是窃贼，）","translator":"王静斋"},{"sura":12,"aya":74,"text":"他们说：（如果你们是谎言的，应如何罚办它呢?）","translator":"王静斋"},{"sura":12,"aya":75,"text":"他们说：发现自谁的行囊中，便就罚办谁，（贬为奴才，）这就是，罚办他。我就这样地罚办一般背义的人。","translator":"王静斋"},{"sura":12,"aya":76,"text":"他就在（搜）他兄弟的囊袋以前，开始搜他们的囊袋。于是他就从其弟弟的囊袋中发现出大杯来。我就这样地教郁素福设谋。他原未依照王法拿获自己的弟弟；除非安拉意欲……的时候。我教自所意欲……的人升高种种品级。每一个识者以上有识者。","translator":"王静斋"},{"sura":12,"aya":77,"text":"他们说：（若是他偷了，从前他哥哥却曾偷过。）郁素福把这话隐在心中，未对他们发表。他说：（你们是地位至恶的。安拉至知你们所称述的。）","translator":"王静斋"},{"sura":12,"aya":78,"text":"他们说：（王啊！他有一年迈的老父；你捕我们一个人，替代他吧。我们确见你是为善的。）","translator":"王静斋"},{"sura":12,"aya":79,"text":"郁素福说：（祈安拉护佑我们不要乱捕，除非在他跟前殳观我们物品的人。果然那样，我们却是背义的人了。）","translator":"王静斋"},{"sura":12,"aya":80,"text":"及至他们对他绝望的时候，他们就去到一旁暗自商谈。他们之中一个年长的人说：（你们不知道父亲曾指着安拉向你们结的约吗?从前你们对郁素福怠慢了。我永不商开此地，待至父亲准许我；或是安拉为我判断。他是至善于判断的。）","translator":"王静斋"},{"sura":12,"aya":81,"text":"你们回到父亲，就说：（父啊！你的儿子偷窃了；我们惟见证自所知道的。我们不保目所未见的。","translator":"王静斋"},{"sura":12,"aya":82,"text":"你打听我们住的那个城池，并打听我们偕他迸发的旅客。我们确是实言的。）","translator":"王静斋"},{"sura":12,"aya":83,"text":"他说：（不然！实是你们的心灵把事件当作平常了。好自忍耐罢了。安拉即将把他们一同拿给我。真主实是深知的明哲的。","translator":"王静斋"},{"sura":12,"aya":84,"text":"他离转他们，说：（暖呀！我好忧愁郁素福啊！）他的二目因忧变白了，他是很愤懑的。","translator":"王静斋"},{"sura":12,"aya":85,"text":"他们说：（指着安拉为誓：你时常思念郁素福，终至染病，或归为死亡的。）","translator":"王静斋"},{"sura":12,"aya":86,"text":"他说：（我惟向安拉陈述自己的烦闷与忧愁。我从安拉上知你们所不知的。","translator":"王静斋"},{"sura":12,"aya":87,"text":"我的儿们哪！你们去访查郁素福和他弟弟，不得对于安拉的慈悯绝望；对安拉的慈悯绝望的只是逆民。）","translator":"王静斋"},{"sura":12,"aya":88,"text":"当他们到他那里的时候，他们就说：（王啊！我们和我们的眷属遭受困难了，我们带来些微的货币，你为我们给足了粮食吧！你对我们施恩吧，安拉必善赏施恩的人。）","translator":"王静斋"},{"sura":12,"aya":89,"text":"他说：（你们在愚昧的时候，对郁素福和他弟弟所干的事，你们还知道吗?）","translator":"王静斋"},{"sura":12,"aya":90,"text":"他们说：（你必是郁素福吧！）他说：（我是郁素福，这是我的弟弟；安拉确对我们施恩了。的确，敬畏并且忍耐的人，安拉绝不湮没为善者的报酬。）","translator":"王静斋"},{"sura":12,"aya":91,"text":"他们说：（指着安拉为誓：安拉确教你优越我们了，我们实在是有罪的人。）","translator":"王静斋"},{"sura":12,"aya":92,"text":"郁紊福说：（今日之下，对你们无侮辱。安拉饶恕你们，他是慈中最慈的。","translator":"王静斋"},{"sura":12,"aya":93,"text":"你们带去我这件衬衣，把它搭在父的面上；他就转为能看的了。你们把家眷全部搬到我这里。）","translator":"王静斋"},{"sura":12,"aya":94,"text":"当旅客离开的时候，他们的父就说：（我确嗅得郁素福的气味。若不是你们以我为胡涂了，……。）","translator":"王静斋"},{"sura":12,"aya":95,"text":"他们说：（指着哎拉为誓：你依然是在旧日的错误中。）","translator":"王静斋"},{"sura":12,"aya":96,"text":"当报喜的人来到的时候，他就把衬衣搭在叶而孤白的面上，他遂就转为能看的了。他说：（我未对你们讲过吗?我从安拉上知道你们不知道的。）","translator":"王静斋"},{"sura":12,"aya":97,"text":"他们说：（父啊！你为我们恳求恕罪吧，我们实是有过错的人。）","translator":"王静斋"},{"sura":12,"aya":98,"text":"他说：（不久我就为你们求我的养主饶恕。主确是多恕的，特慈的。","translator":"王静斋"},{"sura":12,"aya":99,"text":"当他们抵达郁素福的时候，他就把他父母拥至自己，说：（若是安拉意欲…了，你们就安然进城吧！）","translator":"王静斋"},{"sura":12,"aya":100,"text":"他教自己的父母升上宝座；他们向他倒身下拜。他说：（父啊！这是解我先前的梦象。我的养主确使它实观了，他确对我施恩了。那时候，他教我出监。在恶魔于我和我弟兄们中间挑唆以后，真主教你们来自郊野。我的养主确是善处自所意欲…的。他是深知的，明哲的。","translator":"王静斋"},{"sura":12,"aya":101,"text":"我的养主啊！你确赐给我王权了，你教给我解梦了，造化天地的主啊！你是我的在今世与后世爱护者。你教我顺命着死去，你教我联合众义人吧！）","translator":"王静斋"},{"sura":12,"aya":102,"text":"这是隐秘的消息；我把它启示给你。当他们秘谋着议决事件的时候，你未在他们跟前。","translator":"王静斋"},{"sura":12,"aya":103,"text":"世人的大半，纵使你盼望，（也）必不归信。","translator":"王静斋"},{"sura":12,"aya":104,"text":"你未因此向他们讨工资。这不外是劝导众世界的人。","translator":"王静斋"},{"sura":12,"aya":105,"text":"天地间许多表征，他们经过它，而均不注意。","translator":"王静斋"},{"sura":12,"aya":106,"text":"他们的大半只是在拟匹偶着归信安拉。","translator":"王静斋"},{"sura":12,"aya":107,"text":"他们岂不畏惧出自安拉的一种笼罩的刑罚到来，或是复生在他们不知觉着突然到来吗？","translator":"王静斋"},{"sura":12,"aya":108,"text":"你们；这是我的道；我与随从我的人依着明证领导至安拉。安拉清净！我不是信匹偶一类的。","translator":"王静斋"},{"sura":12,"aya":109,"text":"我在你以前所差的不外是城市人中一般受我默谕的人。他们岂未在地上游行，而观察他们以前众人的结果如何吗?后世是在一般敬畏的人上最优的。你们怎不了解呢?","translator":"王静斋"},{"sura":12,"aya":110,"text":"（他们获姑容）直至列使绝望，料想确已遭人斥为撒谎的时候为止。我的相助达至他们，我解救自所意欲（解救）的人。无有给恶民制止我底刑罚的。","translator":"王静斋"},{"sura":12,"aya":111,"text":"他们的往事中确切含有儆诫有理性的人们。那不是造作的话，而是证实在它以前的，与分析万事，并引导与怜恤一般信民。","translator":"王静斋"},{"sura":13,"aya":1,"text":"（阿里甫、俩模、密默、拉。）这是经典的文词。那由你养主上降给你的是切实的，但是世人的多半不信实。","translator":"王静斋"},{"sura":13,"aya":2,"text":"安拉是那不用柱子悬起了你们所看见的天，且复镇摄“阿勒世”的主。他制服了日月；每一个运行一定的期限。他治理事情，他分析各项表征，好教你们坚信遇见自己的养主。","translator":"王静斋"},{"sura":13,"aya":3,"text":"他那真主：铺展了地，并在那里造化了山河，更在那里千各项果实上造化了一对两性，他以黑夜遮覆白昼。此中对于有参悟的民众确含有种种表征。","translator":"王静斋"},{"sura":13,"aya":4,"text":"地上有些毗连的区域，与葡萄园，禾谷，及同根与不同根的枣树；受一种水的灌溉；我教它在滋味上一部优越一部。此中对于有理性的民众确含有种种表征。","translator":"王静斋"},{"sura":13,"aya":5,"text":"如果你惊异了，他们说：（我们已然土化的时候可能重新被造吗?）是可惊异的。这一般人是那不信养主的，颈项戴枷的，永居火狱的。","translator":"王静斋"},{"sura":13,"aya":6,"text":"他们在福乐以前向你要求速现灾患。种种患难在他们以前确已过去了。你的养主对于世人，虽系他们背义，确有饶恕，你的养主实是有严刑的。","translator":"王静斋"},{"sura":13,"aya":7,"text":"众不信的人说： （因何不向他降一出自养主的异迹。）你只是一个传警告的。在每伙人有一个引导者。","translator":"王静斋"},{"sura":13,"aya":8,"text":"安拉知道每一卜妇人所孕的。与各子宫所减所增的。事事在他跟前皆有一定的限度。","translator":"王静斋"},{"sura":13,"aya":9,"text":"他是知道目不能见的与显见的，伟大玄高的主。","translator":"王静斋"},{"sura":13,"aya":10,"text":"你们之中那低声密语，与高声说话，以及在夜间隐遁的，与在白昼行路的，全都一样。","translator":"王静斋"},{"sura":13,"aya":11,"text":"人各有些天使往来在他的前后；奉安拉的命令保护他。安拉对于任何一伙人，非至他们变更自己的态度后，始肯变更对他们的恩典。安拉欲罪刑一伙人的时候，无有制止罪刑的。他们撇开他无一维护的。","translator":"王静斋"},{"sura":13,"aya":12,"text":"他那真主，为着使人骇怕与希望，把闪电现给你们。他发动重云雷；","translator":"王静斋"},{"sura":13,"aya":13,"text":"以赞他称颂清洁；众天使由于骇怕他也是这样。他且发出电火，而用它毁其所意欲（毁灭）的人，彼时，他们正在因为安拉起争辩着。他是掌严刑的。","translator":"王静斋"},{"sura":13,"aya":14,"text":"惟他接受正当的祈祷。他们撇开他所呼求的那一切全不能应承他们一事；就像往水上伸展双手，为着达至己口的人；那水本不能达至他。迷徒们的呼求只是在失效中。","translator":"王静斋"},{"sura":13,"aya":15,"text":"天地之间的人顺服与委曲着给安拉叩头；他们的阴影在朝夕也是这样。你说！（谁是天地的养主?）你说：（安拉）","translator":"王静斋"},{"sura":13,"aya":16,"text":"你说！（你们撇开他，取一般不能为自身掌利害的爱友吗?）你说：（瞎子与有眼的相等吗? 不然，黑暗和光明相等吗? 他们肯为安拉设置一般匹偶，它们造化就像真主造化而至所造的在他们辨别不清吗？）你说，安拉是造化万物的；他是独一的权威者。","translator":"王静斋"},{"sura":13,"aya":17,"text":"他自上空降水，各水道依其容量长流，猛水冲起浮沫。他们为着寻求装饰或器皿在火上所溶化的，有和它一样的渣滓。安拉就是这样地表明真的假的。渣滓被弃而去；那有利于人的，存留在地上。安拉就是这样地发表些个比喻。","translator":"王静斋"},{"sura":13,"aya":18,"text":"应承养土的人可获善赏。那未应承真主的人，设若地上所，有的一切，并类如它的一倍为他们所有了，少他们一定用它作赎资。这般人应遭恶劣的审算。他们的归处是火狱，铺垫太恶了；","translator":"王静斋"},{"sura":13,"aya":19,"text":"那知道真理由养主上降给你的人，可像是盲目的人吗? 惟独有才智的人受劝。","translator":"王静斋"},{"sura":13,"aya":20,"text":"那般人全满安拉的约，并不破约，","translator":"王静斋"},{"sura":13,"aya":21,"text":"那般人接合安拉所命以接合的，他们畏惧养主，并骇怕恶劣的审算；","translator":"王静斋"},{"sura":13,"aya":22,"text":"那般人他们坚忍，为的是讨养主的喜爱；他们立拜功，并暗昧公开的施用我所赐给的。他们以善避恶，这般人可获今世的善果——","translator":"王静斋"},{"sura":13,"aya":23,"text":"永居的园林；他们和其列祖与妻子中的清廉人进它。众天使从各门上达至他们，","translator":"王静斋"},{"sura":13,"aya":24,"text":"你们由于坚忍获得平安。今世的善果很好了！","translator":"王静斋"},{"sura":13,"aya":25,"text":"那般人在结约以后破坏安拉的约；他们断绝安拉所命以接合的；他们在地上造恶。这般人应遭抛弃。他们应遭那一世的痛苦。","translator":"王静斋"},{"sura":13,"aya":26,"text":"安拉随便伸缩人的给养，他们爱喜今生；今生比在后世上，不过是些微的享受。","translator":"王静斋"},{"sura":13,"aya":27,"text":"不信的人们说：（为什么不把出自养主的一项异迹降给他呢?）你说：（安拉随便造人迷误；","translator":"王静斋"},{"sura":13,"aya":28,"text":"把那归向的人导至自己。）那般人既已归信，其心且以记念安拉得到宁定。须知各个心以记念安拉得到宁定。","translator":"王静斋"},{"sura":13,"aya":29,"text":"归信并作善举的人，均享幸福与优美的归所。","translator":"王静斋"},{"sura":13,"aya":30,"text":"我如此着把你差在一伙人里；一伙一伙的在它以前过去了；为的是你把我所启示你的读给那般不信慈主的人。你说，他是我的养主。除他以外再无有主。我惟仰赖他，我惟归向他。","translator":"王静斋"},{"sura":13,"aya":31,"text":"设若用这古兰移动山，或用它断开地，或用它使死人发言（那有何不能?）不然事情统归安拉执掌。众归信的人岂不知道：设若安拉意欲……了，他必使世人一概得正道。不信的人时常因为自所作下的遭受刑罚；或是刑罚降在他们的住宅左近，直到安拉的约来到。安拉绝不爽约。","translator":"王静斋"},{"sura":13,"aya":32,"text":"在你以前的列使确被嘲笑了，但是鞭姑容那般不信者；而后我惩治他们了，我的惩治如何?","translator":"王静斋"},{"sura":13,"aya":33,"text":"那监察每一个人以其所作的，可（像不是这样的一般偶像）吗?他们为安拉设置些个匹偶。你说！（你们提出它们的名称来：）不然，你们把他在地上自所不知的，或把表面上的话，告诉他吗? 不然， 有的已为不信服的人们装饰他们的计谋，并阻止他们入正道了。安拉造为迷误的人—，无有引导他的。","translator":"王静斋"},{"sura":13,"aya":34,"text":"他们在今生遭罪刑。后世的刑罚是较苦的。无一抗安拉维护他们的。","translator":"王静斋"},{"sura":13,"aya":35,"text":"所许给众敬慎者的天园情形是：诸河流于其下，果实常时不断；它的阴影也是这样。这是一般敬畏者的结局。逆徒们的归宿是火狱。","translator":"王静斋"},{"sura":13,"aya":36,"text":"蒙我赐给经典的人，他们喜爱所降给你的。这几伙之中，有的人不承认它的一部分。你说：（我只奉命敬拜安拉，不得给他拟匹偶，我惟呼求他，我惟归向他。）","translator":"王静斋"},{"sura":13,"aya":37,"text":"我如此着降这阿拉伯文的判断。如果你在得到知识以后随从他们的偏见了，则无一抗安拉爱护你的，也无一庇佑的。","translator":"王静斋"},{"sura":13,"aya":38,"text":"我确曾在你以前差遣列使；我赐给他们妻子。使者非奉到安拉的命令，则不得现一异迹。 每一时期有一部（默示）书。","translator":"王静斋"},{"sura":13,"aya":39,"text":"安拉随便勾消与制定。在他据有经的根本。","translator":"王静斋"},{"sura":13,"aya":40,"text":"设若我把所许给他们的一部分现给你，或是我取去你的性命…。你仅担负宣达，惟我操纵审算。","translator":"王静斋"},{"sura":13,"aya":41,"text":"他们岂未看见我达至地面，我从它的各边区缩减它吗? 安拉亲自判定。无有拒绝他的判定者。他是审算神速的。","translator":"王静斋"},{"sura":13,"aya":42,"text":"在他们以前的人确曾弄计谋了。报复计谋统归安拉。他知道每一个人所作的。","translator":"王静斋"},{"sura":13,"aya":43,"text":"逆徒即将知道什么人得到世的善果。边徒们说：（你不是担负使命的）你说！（安拉与那具有天经知识的人在我和你们中间作见证已可满足了）","translator":"王静斋"},{"sura":14,"aya":1,"text":"阿里甫、俩模、拉。）这是〕我降给你的经典，为的是你奉养主的命令教世人由黑暗出至光明；就是出至优胜的，受赞的，","translator":"王静斋"},{"sura":14,"aya":2,"text":"也就是那掌天地间所有的安拉之道。可叹不信的人遭受","translator":"王静斋"},{"sura":14,"aya":3,"text":"那般人在后世上选择今生。他们阻碍安拉之道，给它觅曲折。 这般人是在遥远的迷误中。","translator":"王静斋"},{"sura":14,"aya":4,"text":"我每逢差一使者，必须依照他民众的方士气以便他对他们说明。而后安拉任便造人迷误，他任便引人正道。他是优胜的，明哲的。","translator":"王静斋"},{"sura":14,"aya":5,"text":"我确曾差母撒藉我的异迹为使，〔说：〕你使自己的族人由黑暗出至光明：你要以安拉的时日劝导他们！此中对于每一个忍耐，感谢的人，确含有种种表征。","translator":"王静斋"},{"sura":14,"aya":6,"text":"昔时，母撒对自己的族人说：（你们当记念安拉对你们的恩典。那时候，他解救你们脱离匪喇傲的党徒；他们教你们尝烈刑，他们宰死你们的男儿，留下你们的女儿。此中有出自养主的一大试证。）","translator":"王静斋"},{"sura":14,"aya":7,"text":"往时，你们的养主宣示：（如果你们感念了，我必增加给你们；若是你们忘恩了，我的刑罚一定是猛烈的。）","translator":"王静斋"},{"sura":14,"aya":8,"text":"母撒说：（若是你们和地上的人统都不信了，安拉一定是无求的，堪称赞的。）","translator":"王静斋"},{"sura":14,"aya":9,"text":"在你们以前的众人努海的族人、阿代、赛母待、与他们以后众人的消息，未曾达至你们吗?——知道他们的只有安拉——列使现给他们种种明证，但是他们把手伸在口里。他们说：（我们实不信你们的使命；我们对你所唤导的事情是在不安的疑惑中。）","translator":"王静斋"},{"sura":14,"aya":10,"text":"列使说：（岂可对安拉——造化天地的主怀疑?他唤你们，为的是饶恕你们的罪恶他姑容你们，直到一定的时期）。他们说：（你们只是和我们一样的人，你们欲阻止我们离开自己列祖所拜的吗?你们把显著的明证现给我们。）","translator":"王静斋"},{"sura":14,"aya":11,"text":"列使对他们说：（我们只是和你们一样的人。但是安拉任便对于自己的仆人施恩。我们非奉安拉的命令，必不能现给你们一项明证每 当令众归信的人仰赖安拉！","translator":"王静斋"},{"sura":14,"aya":12,"text":"我们怎可不仰赖安拉呢?他确指示我们正道了。我们必定忍受你们的伤害·当令众仰赖的人仰赖安拉！）","translator":"王静斋"},{"sura":14,"aya":13,"text":"不信的人们对列使说：（我们必把你们逐出本地，或者你们重反我们的教道。）养主默谕他们：叫我必灭绝一切背义的人。","translator":"王静斋"},{"sura":14,"aya":14,"text":"我必教你们在他们以后居住在该地，这是属于那骇惧站在我御前，并骇怕我惊吓的人。）","translator":"王静斋"},{"sura":14,"aya":15,"text":"他们求相助。一切强霸顽梗的人失望了。","translator":"王静斋"},{"sura":14,"aya":16,"text":"在他前面有火狱。有的教他饮脓水，他徐徐地下咽。他几乎不容易下咽。","translator":"王静斋"},{"sura":14,"aya":17,"text":"死由各方达至，他绝不会死，在他面前有重刑。","translator":"王静斋"},{"sura":14,"aya":18,"text":"不信养主的那般人的情形，其善举有如灰尘；在发暴风的那天被风猛烈地刮散。他们不能于自所作过的一事。这就是遥远的迷误。","translator":"王静斋"},{"sura":14,"aya":19,"text":"你岂未见安拉因着真理造化天地吗?若是他意欲……了，他就去掉你们，拿来新的造化，","translator":"王静斋"},{"sura":14,"aya":20,"text":"这在安拉并不困难。","translator":"王静斋"},{"sura":14,"aya":21,"text":"他们统出现于安拉，而后一般无能为的人对那一伙高傲的人说：『我们原是追从你们的。你们可能替我们避免安拉的些微罪刑吗?）他们说：（设若安拉引导我们了，我们一定领你们得正道。我们暴躁也罢，忍耐也罢，一样地在我们无有逃处。）","translator":"王静斋"},{"sura":14,"aya":22,"text":"在事已判明的时候，恶魔说：（安拉许给你们，切实地约许；我许给你们而对你们失信了。我除却唤导你们而得到应承以外，自无一点权柄。你们不要责备我；你们责备自身吧：我不能解救你们，你们也不能解救我。我实在不承认你们从前举我〔与真主〕同等。背义的人必遭痛刑。","translator":"王静斋"},{"sura":14,"aya":23,"text":"众归信并作善举的人，奉养主的命令得进诸河流于其下的天园，永居其中着。他们在天园中的灾况就是（平安。）","translator":"王静斋"},{"sura":14,"aya":24,"text":"你岂未看见安拉怎样地发比喻?清洁的话，如同清洁的树；它的根基是坚固的，它的枝干是在上空；","translator":"王静斋"},{"sura":14,"aya":25,"text":"它在每一个时期依其养主的旨意结它的果实。安拉为世人发些比喻，好教他们受劝。","translator":"王静斋"},{"sura":14,"aya":26,"text":"污浊的话如同污浊的树；被拔自地上，并不坚固。","translator":"王静斋"},{"sura":14,"aya":27,"text":"安拉在今生并在后世用坚决的话安定众归信的人。安拉造背义的人为迷误的，安拉任变行事。","translator":"王静斋"},{"sura":14,"aya":28,"text":"你岂未见有一般人，以妄信瞒昧安拉的恩典，并使自己的族人陷在沦落的第宅——","translator":"王静斋"},{"sura":14,"aya":29,"text":"火狱?他们将要进它，居所太恶了！","translator":"王静斋"},{"sura":14,"aya":30,"text":"他们为安拉设置一切同等的，为的是诱人错过他的道。（你说；（你们享受吧，你们一定归到火狱。）","translator":"王静斋"},{"sura":14,"aya":31,"text":"你对我那归信的众仆说：他们应当立拜功，并就着我所赐给他们的暗昧明扬着使用，须在无贸易无友谊的那日到来以前。","translator":"王静斋"},{"sura":14,"aya":32,"text":"安拉那真主造化天地；由天空降水，而为慈惠你们用它发观各项果实。并为你们制服船只，以便它依主的旨意航行在海中，为你们制服诸河。","translator":"王静斋"},{"sura":14,"aya":33,"text":"为你们制服日月，在循环着，为你们制服夜昼。","translator":"王静斋"},{"sura":14,"aya":34,"text":"并把你们所求的一切赐给你们。你们若历数安拉的恩典了，你们不能统计它。人类实是负义的，忘恩的。","translator":"王静斋"},{"sura":14,"aya":35,"text":"昔时，易卜拉欣说：我的养主啊！你把平安赐给这个城池！你教我和我的子孙远避拜偶像！","translator":"王静斋"},{"sura":14,"aya":36,"text":"我的养主啊！它们确诱多的人迷误了。追随我的人确属我一类的；违犯我的人，你一定是多恕的，特慈的。","translator":"王静斋"},{"sura":14,"aya":37,"text":"我们的养土啊！我确教自己的一部分子孙居住在无禾谷的山谷，靠近你的禁殿。","translator":"王静斋"},{"sura":14,"aya":38,"text":"我们的养主啊！冀使他们立拜功。你教一部分人心爱慕他们！你把一部分果实赐给他们！好教他们感念。我们的养主啊！你确知道我们隐匿的和显扬的。天地间无一事可能瞒住安拉。","translator":"王静斋"},{"sura":14,"aya":39,"text":"赞颂那安拉，他把易司马依和易司哈各赐给我年迈人。我的养主实在是听得呼求的。","translator":"王静斋"},{"sura":14,"aya":40,"text":"我的养主啊！你教我们和我的一部分子孙为立拜功的！我们的养主啊！你允承我的呼求吧！","translator":"王静斋"},{"sura":14,"aya":41,"text":"我们的养主啊，在审算成立的那一日，你饶恕我和我的父母，以及众穆民！","translator":"王静斋"},{"sura":14,"aya":42,"text":"你切莫以为安拉是忽于一般背义的人所作的。他姑容他们，只到众目凝视的那一日。","translator":"王静斋"},{"sura":14,"aya":43,"text":"均在仰首奔驰，目不转睛，其心空空着。","translator":"王静斋"},{"sura":14,"aya":44,"text":"你向众人警告那么一日：刑罚到来，而背义的人们说：（我们的养主啊！你姑容我们，直到相近的限期！我们就应承你的劝导，追随列使。）你们从前岂未发誓；（无有更移）吗?","translator":"王静斋"},{"sura":14,"aya":45,"text":"你们曾住在一般自负己身者的居所；你们已明白我怎样地对他们行事，并对你们发比喻。","translator":"王静斋"},{"sura":14,"aya":46,"text":"他们确已阴谋计划。报复他们的阴谋惟在安拉。他们的阴谋绝不是群山可囚它移动的。","translator":"王静斋"},{"sura":14,"aya":47,"text":"你莫以为安拉对他的列使爽约。安拉实是优胜的，报复的。","translator":"王静斋"},{"sura":14,"aya":48,"text":"将有那么一日，地被改成不是这个地。诸天也是这样。他们出现至独一权威的安拉。","translator":"王静斋"},{"sura":14,"aya":49,"text":"当那一日，你看见一般为恶的人是同被系在桎梏的。","translator":"王静斋"},{"sura":14,"aya":50,"text":"他们的大衣是柏油的，他们的面目被火遮覆。","translator":"王静斋"},{"sura":14,"aya":51,"text":"安拉对各个人·报以他所作的。安拉实是审算神速的。","translator":"王静斋"},{"sura":14,"aya":52,"text":"这是昭示世人的。为的是以此警告他们，并教他们知道：（惟他是独一主。）当令有才智的人们受劝！","translator":"王静斋"},{"sura":15,"aya":1,"text":"（阿里甫、俩模、拉。）这是经典与显明的古兰的文词。","translator":"王静斋"},{"sura":15,"aya":2,"text":"逆徒们迭次盼望！自己原是顺服的。","translator":"王静斋"},{"sura":15,"aya":3,"text":"你任他们吃吧！任他们享受吧！他们被希望所贻误。不久他们就知道。","translator":"王静斋"},{"sura":15,"aya":4,"text":"我灭绝任何一个城他，在它都有一定的期限。","translator":"王静斋"},{"sura":15,"aya":5,"text":"任何一个人不能争先他的期限，也不能退后。","translator":"王静斋"},{"sura":15,"aya":6,"text":"他们说：（蒙降给教戒的人哪！你却是一个疯子。","translator":"王静斋"},{"sura":15,"aya":7,"text":"如果你的话属实，你怎么不把天使拿给我们呢?）","translator":"王静斋"},{"sura":15,"aya":8,"text":"我降天使，只是因为真理。如果是那样，他们就不获姑容了。","translator":"王静斋"},{"sura":15,"aya":9,"text":"我确降下教戒了。我是一定保护它的。","translator":"王静斋"},{"sura":15,"aya":10,"text":"我在你以先，确曾遣使于前代各群众。","translator":"王静斋"},{"sura":15,"aya":11,"text":"每逢有一使者达至他们，他们就嘲笑他。","translator":"王静斋"},{"sura":15,"aya":12,"text":"我就是这样地把它 投在一般为恶的人心中。","translator":"王静斋"},{"sura":15,"aya":13,"text":"他们不信服他。众前代的惯例已然过去了。","translator":"王静斋"},{"sura":15,"aya":14,"text":"设若我给他们开放天门，能可上升，","translator":"王静斋"},{"sura":15,"aya":15,"text":"他们必说：（我们的眼已被堵塞了；我们且是受了眩惑的一伙人。","translator":"王静斋"},{"sura":15,"aya":16,"text":"我造宿宫于天上，我因为观众装饰了它。","translator":"王静斋"},{"sura":15,"aya":17,"text":"我从一切被逐的恶魔上保护它；","translator":"王静斋"},{"sura":15,"aya":18,"text":"除非那窃听而被明星驱逐的。","translator":"王静斋"},{"sura":15,"aya":19,"text":"我伸展地；我把大山置在它上。我在它上发长一切相称的东西。","translator":"王静斋"},{"sura":15,"aya":20,"text":"我在那里为你们造化生活之道，和你们不能供给生活的。）","translator":"王静斋"},{"sura":15,"aya":21,"text":"凡事皆由我掌它的仓库。我依一定的限变降下它。","translator":"王静斋"},{"sura":15,"aya":22,"text":"我驱动蕴雨的风，我由上空降水份供你们饮它。你们本不是能存积它的。","translator":"王静斋"},{"sura":15,"aya":23,"text":"我实使生使死，我是收留的。","translator":"王静斋"},{"sura":15,"aya":24,"text":"我确知道你们的一切先人，我确知道一切后人。","translator":"王静斋"},{"sura":15,"aya":25,"text":"你的养主将集合他们。他实是明哲的，深知的。","translator":"王静斋"},{"sura":15,"aya":26,"text":"我确曾由黑臭的干泥上造化人类。","translator":"王静斋"},{"sura":15,"aya":27,"text":"从前我曾由烈火上造化迦呢。","translator":"王静斋"},{"sura":15,"aya":28,"text":"那时候，你的养主对众天使说：（我将必由黑臭的干泥上造化人类。","translator":"王静斋"},{"sura":15,"aya":29,"text":"当我使他完备，往他上次入我〔造〕的灵魂时候，你们就向他伏身下拜。）","translator":"王静斋"},{"sura":15,"aya":30,"text":"众天使均一同下拜了；","translator":"王静斋"},{"sura":15,"aya":31,"text":"惟有以卜里厮拒绝随众一同下拜。","translator":"王静斋"},{"sura":15,"aya":32,"text":"主说：（以卜里厮！你为什么不随众一同下拜呢?）","translator":"王静斋"},{"sura":15,"aya":33,"text":"他说：（我不该向你由黑臭的干泥上造化的一人下拜。）","translator":"王静斋"},{"sura":15,"aya":34,"text":"主说：（你自这里出去吧，","translator":"王静斋"},{"sura":15,"aya":35,"text":"你是被驱逐的。你应遭驱远，直到复生日。）","translator":"王静斋"},{"sura":15,"aya":36,"text":"他说：（我的养主啊！你姑容我直到复活他们的那一日，）","translator":"王静斋"},{"sura":15,"aya":37,"text":"主说：（你定获姑容，","translator":"王静斋"},{"sura":15,"aya":38,"text":"直到一定的时日。）","translator":"王静斋"},{"sura":15,"aya":39,"text":"他说：（我的养主啊！以你的教我迷误为誓：我必在地上为他们装饰。我必诱他们一同迷误；","translator":"王静斋"},{"sura":15,"aya":40,"text":"除非他们之中你的清白仆人。）","translator":"王静斋"},{"sura":15,"aya":41,"text":"主说：这是达至我的正道。","translator":"王静斋"},{"sura":15,"aya":42,"text":"我的众仆，除却追随你的那般迷徒以外，在你绝无有对付他们的权力，","translator":"王静斋"},{"sura":15,"aya":43,"text":"火狱实是对他们全体约许的地方。","translator":"王静斋"},{"sura":15,"aya":44,"text":"火狱上有七道门；每道门上有他们其中被隔离的一部分。","translator":"王静斋"},{"sura":15,"aya":45,"text":"的确，一般敬慎的人是在园林与源泉。","translator":"王静斋"},{"sura":15,"aya":46,"text":"有的对他们说：（你们安然无恐着进去吧！","translator":"王静斋"},{"sura":15,"aya":47,"text":"我释去他们陶中的衔恨，互作弟兄着，相向坐在床上。","translator":"王静斋"},{"sura":15,"aya":48,"text":"他们在那里不感觉苦楚，绝不至从那里出去。","translator":"王静斋"},{"sura":15,"aya":49,"text":"你告诉我的众仆！我实是多恕的，特慈的。","translator":"王静斋"},{"sura":15,"aya":50,"text":"我的刑罚就是致痛的刑罚。","translator":"王静斋"},{"sura":15,"aya":51,"text":"你向他们述说易卜拉欣的宾客！","translator":"王静斋"},{"sura":15,"aya":52,"text":"那时候，他们来向他祝平安。他说：（我实是骇惧你们的。）","translator":"王静斋"},{"sura":15,"aya":53,"text":"他们说：（你不要骇惧；我们以一个有知识的童子给你报喜信。）","translator":"王静斋"},{"sura":15,"aya":54,"text":"他说：（你们给老迈已至的我报喜信吗?你们以什么报喜信呢?）","translator":"王静斋"},{"sura":15,"aya":55,"text":"他们说：（我们据实给你报喜信了，你不要绝望。）","translator":"王静斋"},{"sura":15,"aya":56,"text":"他说：（除却一般迷误的人，谁对其养主的慈惠绝望呢?）","translator":"王静斋"},{"sura":15,"aya":57,"text":"他说：（诸使啊！你们有什么重大事件?）","translator":"王静斋"},{"sura":15,"aya":58,"text":"他们说：（我们是被差至一伙恶民的；","translator":"王静斋"},{"sura":15,"aya":59,"text":"只有鲁脱的眷属，我们必一概地解救他们。","translator":"王静斋"},{"sura":15,"aya":60,"text":"他的妻室在外。我们预定她归为落后的。）","translator":"王静斋"},{"sura":15,"aya":61,"text":"当诸使来至鲁脱眷属的时候，","translator":"王静斋"},{"sura":15,"aya":62,"text":"鲁脱就说：（你们实是非常的一伙。）","translator":"王静斋"},{"sura":15,"aya":63,"text":"他们说：（不，我们把他们所对它怀疑的拿给你。","translator":"王静斋"},{"sura":15,"aya":64,"text":"我们把真的拿给你了。我们确是实言的。","translator":"王静斋"},{"sura":15,"aya":65,"text":"你在夜内一更天携眷出走吧!要你随在后面。你们任何一个人不得回颐。你们要去到所命令你们的地方，）","translator":"王静斋"},{"sura":15,"aya":66,"text":"我向他默示这事！此般人的后代是将在晨时被根绝的。","translator":"王静斋"},{"sura":15,"aya":67,"text":"城池的人嬉笑着来了。","translator":"王静斋"},{"sura":15,"aya":68,"text":"鲁脱说：（这一般确是我的宾客，你们不要使我蒙羞。","translator":"王静斋"},{"sura":15,"aya":69,"text":"你们当畏惧安拉；不要侮辱我。）","translator":"王静斋"},{"sura":15,"aya":70,"text":"他们说：（我们岂未禁止你接近外人吗?）","translator":"王静斋"},{"sura":15,"aya":71,"text":"他说：（这一般是我的女儿；如果你们有所欲为……。）","translator":"王静斋"},{"sura":15,"aya":72,"text":"指着你的寿限为誓：（他们确在迷误中徘徊着。）","translator":"王静斋"},{"sura":15,"aya":73,"text":"而后当日升的时候他们遭遇一声呜；","translator":"王静斋"},{"sura":15,"aya":74,"text":"我使那城池上下倒置；我向池们降下泥石雨。","translator":"王静斋"},{"sura":15,"aya":75,"text":"此中对于一般有见识的人确含有种种表征。","translator":"王静斋"},{"sura":15,"aya":76,"text":"那城池是在通行的大道上。","translator":"王静斋"},{"sura":15,"aya":77,"text":"此中对于众穆民确食有一种表征。","translator":"王静斋"},{"sura":15,"aya":78,"text":"哀凯人，确是背义的，我曾惩治他们了。","translator":"王静斋"},{"sura":15,"aya":79,"text":"这两城确是在显明的大道上。","translator":"王静斋"},{"sura":15,"aya":80,"text":"黑秩尔人，确不信实诸使了。","translator":"王静斋"},{"sura":15,"aya":81,"text":"我赐给他们种种异迹。但是他们不注视它。","translator":"王静斋"},{"sura":15,"aya":82,"text":"他们就山凿室，安居着。","translator":"王静斋"},{"sura":15,"aya":83,"text":"而后他们在晨时遭遇一声呜；","translator":"王静斋"},{"sura":15,"aya":84,"text":"他们所赚下的并末代为避免。","translator":"王静斋"},{"sura":15,"aya":85,"text":"我惟因着真理造化了天地万有。复生日一定实现，你要容让，好好地容让，","translator":"王静斋"},{"sura":15,"aya":86,"text":"你的养主实是造化的深知的。","translator":"王静斋"},{"sura":15,"aya":87,"text":"我确赐给你重读的七节，与伟大的古兰；","translator":"王静斋"},{"sura":15,"aya":88,"text":"你不要注视我使那儿伙人所享受的，你不要因他们忧虑；你要向众穆民表示谦逊。","translator":"王静斋"},{"sura":15,"aya":89,"text":"你说：（我确是一个公然传警告的。）","translator":"王静斋"},{"sura":15,"aya":90,"text":"就像我降给那般把古兰分为一部一部地分配者。","translator":"王静斋"},{"sura":15,"aya":91,"text":"就像我降给那般把古兰分为一部一部地分配者。","translator":"王静斋"},{"sura":15,"aya":92,"text":"指着你的养主为誓：","translator":"王静斋"},{"sura":15,"aya":93,"text":"我将必对他们所作的问他们大众。","translator":"王静斋"},{"sura":15,"aya":94,"text":"你宣扬所命令你的吧！你远避一般拜偶像的人。","translator":"王静斋"},{"sura":15,"aya":95,"text":"我必代你对付一般嘲笑的人。","translator":"王静斋"},{"sura":15,"aya":96,"text":"那些人举另一受拜的和安拉共同。不久他们就知道。","translator":"王静斋"},{"sura":15,"aya":97,"text":"我确知道你的胸怀，因他们所说的（话）感觉窄狭。","translator":"王静斋"},{"sura":15,"aya":98,"text":"你当以赞你的养主称颂清净！你当属于叩的头的！","translator":"王静斋"},{"sura":15,"aya":99,"text":"你当拜你的养主，直至死亡来到！","translator":"王静斋"},{"sura":16,"aya":1,"text":"安拉的命今、快到了，你们不要忙讨它。真主清净，他玄高于他们所拟的匹偶，","translator":"王静斋"},{"sura":16,"aya":2,"text":"他以属于自己命令的（鲁哈）把天使降在自所意欲……的奴仆上！你们应该这样传警告：除我以外，再无有受拜的。你们当畏惧我。","translator":"王静斋"},{"sura":16,"aya":3,"text":"他以真理造化了天地，他玄高于他们所拟的匹偶。","translator":"王静斋"},{"sura":16,"aya":4,"text":"他自一点精上造化了人类，忽然它是一个公然敌对的。","translator":"王静斋"},{"sura":16,"aya":5,"text":"他为你们造化了四蹄的；内中有温暖物，和各种利益，你们就着它吃。","translator":"王静斋"},{"sura":16,"aya":6,"text":"在晚间收回的时候，白天放出的时候，其中有你们的荣耀。","translator":"王静斋"},{"sura":16,"aya":7,"text":"那牲畜载你们的重负去到你们非经劳身而不能抵达的城池。你们的养主实是怜悯的，特慈的。","translator":"王静斋"},{"sura":16,"aya":8,"text":"并造化马、骡、驴，好教你们骑它，并且因为装饰。他更造化你们不知道的。","translator":"王静斋"},{"sura":16,"aya":9,"text":"正直的道操自安拉。有一部分道路是偏斜的。设若他意欲（引导）了，他就使你们一概得正道。","translator":"王静斋"},{"sura":16,"aya":10,"text":"他那真主从上空降水；其中有你们的饮料。草从它上生，你们在它里牧放。","translator":"王静斋"},{"sura":16,"aya":11,"text":"他为你们用水发生和谷、橄榄、枣树、葡萄及其它各种果实。此中对于有思维的民众确含有一种表征。","translator":"王静斋"},{"sura":16,"aya":12,"text":"他为你们制服夜昼与日月。群星是依他的命令被制服的。此中对于有理性的民众，确含有种种表征。","translator":"王静斋"},{"sura":16,"aya":13,"text":"并制服为你们在地上所造的色色不同的。此中对于受教的民众确含有一种表征。","translator":"王静斋"},{"sura":16,"aya":14,"text":"他那真主制服了海，以便你们从那里吃鲜肉。你们从那里发观所戴的装饰。你看船在那里破浪航行，为的是你们觅主的特恩，冀使你们感念。","translator":"王静斋"},{"sura":16,"aya":15,"text":"他把大山置在地上，以免摇动你们；并造化诸河与道路，冀使你们得正道。","translator":"王静斋"},{"sura":16,"aya":16,"text":"更造化各种标记。他们借着星辰获指导。","translator":"王静斋"},{"sura":16,"aya":17,"text":"遗物主可像那不能造化的吗? 你们怎不受劝呢?","translator":"王静斋"},{"sura":16,"aya":18,"text":"你们若历数安拉的恩典了，你们不能统计它，安拉实是多恕的，特慈的。","translator":"王静斋"},{"sura":16,"aya":19,"text":"安拉知道你们所隐所显的。","translator":"王静斋"},{"sura":16,"aya":20,"text":"他们舍去安拉所拜的那一切，均属被造，不能造化一事，","translator":"王静斋"},{"sura":16,"aya":21,"text":"且是一切无生命的死物；它们本不知道：何时复活他们。","translator":"王静斋"},{"sura":16,"aya":22,"text":"你们的主是独一主。不信后世的人，心昏而且高傲。","translator":"王静斋"},{"sura":16,"aya":23,"text":"无疑安拉知道他们所隐所显的。安拉实不爱顽强的人们。","translator":"王静斋"},{"sura":16,"aya":24,"text":"一旦有人对他们说：（你们的养主降下什么了?）他们就说：（前人的故事。）","translator":"王静斋"},{"sura":16,"aya":25,"text":"将来他们在复生日担负自己完全的重担，并担负被他们茫然引入迷误的那般人的重担。须知他们所负担的太恶了！","translator":"王静斋"},{"sura":16,"aya":26,"text":"他们以前的人们确曾弄计谋，而后安拉由根本上摧毁他们的建筑，而致屋顶从上塌下，刑罚突然而至！","translator":"王静斋"},{"sura":16,"aya":27,"text":"厥后当复生日，真主使他们观丑，且说：（你们给我举的那些偶像，你们曾因为它们作反抗的那一切，是在哪里?一般有知识的人说：一个信的人们应遭今日的现丑与困难；","translator":"王静斋"},{"sura":16,"aya":28,"text":"那般人正在自亏己身着被天使取去性命。他们表示顺服，〔说：〕我们未作过恶事。）不然，安拉深知你们的作为。","translator":"王静斋"},{"sura":16,"aya":29,"text":"你们进火狱的门，永住在那里吧！骄傲人的归所太恶了！","translator":"王静斋"},{"sura":16,"aya":30,"text":"有的对一般敬畏的人说：（你们的养主降下什么了?）他们就说：（好。）在今生为善的人可获善赏。后世实是更好的。敬慎者的第宅太好了！","translator":"王静斋"},{"sura":16,"aya":31,"text":"他们将进诸河流于其下，永久的天园，他们在那里享受臼所愿望的。安拉就是这样地善赏广般敬慎的人。","translator":"王静斋"},{"sura":16,"aya":32,"text":"那般人正在愉快着被天使取去性命?天使们说：祝你们平安，你们因着从前所作的进天园吧！","translator":"王静斋"},{"sura":16,"aya":33,"text":"他们惟等待众天使来到，或你养主的命令来到。在他们以前的人们就是这样的行事了。安拉未亏他们；但是他们自亏己身了。","translator":"王静斋"},{"sura":16,"aya":34,"text":"而后，他们遭受所作的一切恶报。他们被曾所嘲笑的包围了。","translator":"王静斋"},{"sura":16,"aya":35,"text":"信匹偶的人说：（设若安拉意欲……了，我们以及自己的祖先不撇开他另拜一物；我们也不超越他严禁一事。）在他们以前的人们就是这样行事。使者所负的只是公然的宣达。","translator":"王静斋"},{"sura":16,"aya":36,"text":"我确在每一族人里派去一个使者。〔说：〕你们当拜安拉，远避大欧特。而后他们之中有得安拉引导的；他们之中有一定迷误的。你们当去在地上游行而观察！一般不信服者的结果如何?","translator":"王静斋"},{"sura":16,"aya":37,"text":"你若企盼他们得正道了，安拉实不引导自所造为迷误的人，无有相助他们的。","translator":"王静斋"},{"sura":16,"aya":38,"text":"他们指着安拉发誓，尽力发誓：（安拉绝不复活死者。）不然，这是在他确定的期许；但是世人的大半不知道。","translator":"王静斋"},{"sura":16,"aya":39,"text":"（真主复活他们）为的是对他们解明其所为它起争议的，并为的是教不信的人们知道：自己实是说谎话的。","translator":"王静斋"},{"sura":16,"aya":40,"text":"我意欲一事的时候，仅对它说：（有！）它立刻就有。","translator":"王静斋"},{"sura":16,"aya":41,"text":"在受人迫害以后为着安拉迁居的人，我必把他们在今世安插在好的地方。后世的善赏是较大的。设若他们知道了……。","translator":"王静斋"},{"sura":16,"aya":42,"text":"那般人均已忍耐，且惟仰懒他们的养主。","translator":"王静斋"},{"sura":16,"aya":43,"text":"我在你以前所差的只是受我默谕的一般人。如果你们不知道了，你们就问有经的人；","translator":"王静斋"},{"sura":16,"aya":44,"text":"借着各项明证与典籍。我降给你教戒，以便你对众人解明所降给他们的，冀使他们有所思维。","translator":"王静斋"},{"sura":16,"aya":45,"text":"谋划种种罪恶的人，岂可不怕安拉以地陷下他们? 或是刑罚突然达到?","translator":"王静斋"},{"sura":16,"aya":46,"text":"或是他们在往来之间遭遇刑罚，他们不是能抵抗的?","translator":"王静斋"},{"sura":16,"aya":47,"text":"或是他们在渐渐消灭着遭遇刑罚? 你们的养主实是怜悯的，特慈的。","translator":"王静斋"},{"sura":16,"aya":48,"text":"他们岂未见安拉所造化的物类，它的阴影于左右传动，在微贱着向安拉下拜吗?","translator":"王静斋"},{"sura":16,"aya":49,"text":"天地间所有的动物惟向安拉下拜；众天使也是这样，并不高傲。","translator":"王静斋"},{"sura":16,"aya":50,"text":"他们 畏惧其上面的养主。他们奉行命令。","translator":"王静斋"},{"sura":16,"aya":51,"text":"安拉说：你们不要取两个主，他只是独一主，你们应当惧我。","translator":"王静斋"},{"sura":16,"aya":52,"text":"天地之间的一切惟他执掌，惟应时常的顺他。你们畏惧安拉以外的吗?","translator":"王静斋"},{"sura":16,"aya":53,"text":"你们所受的恩典是从安拉上一你们遭难的时候，惟向他衷号。","translator":"王静斋"},{"sura":16,"aya":54,"text":"迨至他给你们释去患难的时候，你们的一部分辄就给养主举匹偶；","translator":"王静斋"},{"sura":16,"aya":55,"text":"而致瞒昧我所赐给他们的。你们享受吧！不久的你们就知道。","translator":"王静斋"},{"sura":16,"aya":56,"text":"他们为着自所不知的，安置我曾赐给的一部分。指着安拉为誓：（将向你们质问你们所造作的。）","translator":"王静斋"},{"sura":16,"aya":57,"text":"他们为安拉安置一切女儿——真主清争！为本身安置自所想望的。","translator":"王静斋"},{"sura":16,"aya":58,"text":"有人以女儿给他们的一个人报喜信的时候，他的面貌就愤懑着变黑了。","translator":"王静斋"},{"sura":16,"aya":59,"text":"他因为所报告的不美，而对族人隐瞒。（自思）可是忍辱着留下她?我是把她掩埋在里呢? 须知他们所判定的太恶了，","translator":"王静斋"},{"sura":16,"aya":60,"text":"不信后世的人遭恶的比拟。惟安拉具有至高的比拟。他是优胜的，明哲的。","translator":"王静斋"},{"sura":16,"aya":61,"text":"设若安拉因为世人的背义施行惩治了，他就不把一个动物遗在地上；但是他姑容他们，直到一定的时期。当他们的期限已至的时候，既不能退后一时，也不能争先。","translator":"王静斋"},{"sura":16,"aya":62,"text":"他们为安拉安置自己所憎恶的。他们口述谎言：（自必获得幸福。）无疑的他们必遭火刑。他们是被迭到前面的。","translator":"王静斋"},{"sura":16,"aya":63,"text":"指着安拉为誓：我确实向你以前的各民众道使，但是恶魔为他们饰其作为。他是在今日助长他们的。他们应遭痛刑。","translator":"王静斋"},{"sura":16,"aya":64,"text":"我降给你经典，只为你向他们解明所对它起争议的，并引导与怜恤有信仰的民众。","translator":"王静斋"},{"sura":16,"aya":65,"text":"安拉由上空降水，而用它复活既死的地；此中对于侧耳的民众确含有一种表征。","translator":"王静斋"},{"sura":16,"aya":66,"text":"你们对于家畜上实获得一种警惕；我教你们从它的腹中饮那出自粪血之间的纯乳，使饮的人容易下咽着。","translator":"王静斋"},{"sura":16,"aya":67,"text":"并从枣树与葡萄树的果实上。你们从那上取得饮料与美食。此中对于有理性的民众确含有一种表征?","translator":"王静斋"},{"sura":16,"aya":68,"text":"你的养主感悟蜜蜂！你就山并就树，及他们所架的搭窝！","translator":"王静斋"},{"sura":16,"aya":69,"text":"你再就着各项果实去吃！你要行你养主的易行之道；色色不一内含医治世人的饮料，出自它的腹中。此中对于有思维的民众确含有一种表征。","translator":"王静斋"},{"sura":16,"aya":70,"text":"安拉造化你们，而后取去你们的性命。你们之、甲有反回最卑的年令的；为的是在知道以后不识一事。安拉实是深知的，全能的。","translator":"王静斋"},{"sura":16,"aya":71,"text":"安拉教你们在生活上彼此一部分优越一部分。居优势的；绝不会把自己的生活费让给手下的人，而致彼此是在给养上相等的。他们不承认安拉的恩典吗?","translator":"王静斋"},{"sura":16,"aya":72,"text":"安拉为你们造化同类的妻室；并为你们由妻室上造化了儿孙，更赐给你们各种美品。他们信假的而瞒昧安拉的恩典吗?","translator":"王静斋"},{"sura":16,"aya":73,"text":"他们抛开安拉而拜那不能执掌出自天地的一切给养且无能力的吗?","translator":"王静斋"},{"sura":16,"aya":74,"text":"你们莫为安拉发比喻。惟安拉知道，你们不知道。","translator":"王静斋"},{"sura":16,"aya":75,"text":"安拉发广个比喻：一个不能一事的被制的奴隶，与蒙我赐予优上的给养而暗着明着施用的人，他们能可相等吗? 赞颂安拉！——只是他们的大半不知道。","translator":"王静斋"},{"sura":16,"aya":76,"text":"安拉发一个比喻：两个人有一个是凡女，不能，只是赘累主人的哑巴！主人把他派到无论哪里，他也拿不来一件好事，这个人和那站在正路上命以正义的人相等吗?","translator":"王静斋"},{"sura":16,"aya":77,"text":"天地的隐秘惟安拉执掌。复生日的事情不过是一转瞬。不然，是较近的。安拉实是全能于万事的。","translator":"王静斋"},{"sura":16,"aya":78,"text":"安拉使你们出自母腹，你们本不知一事。他赐给你们耳眼心，只为你们感念。","translator":"王静斋"},{"sura":16,"aya":79,"text":"他们岂未见被制在天空的飞鸟吗?只有安拉保持它们。此中对于有信仰的民众。确含有种种表征。","translator":"王静斋"},{"sura":16,"aya":80,"text":"安拉教你们以自己的房舍作息所，为你们以兽皮作居室；你们在旅行与居家的日子以它感觉轻便；更以绵羊绒、驼毛、山羊绒，制造需用品，享受到一个时期。","translator":"王静斋"},{"sura":16,"aya":81,"text":"安拉为你们自其所造的物类上发现阴影。为你们就山造洞，为你们制造御热的衣，和防战的衣。他就是这样地完成对你们的恩典，冀使你们顺服。","translator":"王静斋"},{"sura":16,"aya":82,"text":"如果他们不肯了，你惟担负公开的宣达。","translator":"王静斋"},{"sura":16,"aya":83,"text":"他们认识安拉的恩典，而又不承认。他们的大半是忘恩的。","translator":"王静斋"},{"sura":16,"aya":84,"text":"当那一日，我自每一伙人里复活一个见证人，而后不准背逆的人们（托词，）且不容其讨喜悦。","translator":"王静斋"},{"sura":16,"aya":85,"text":"背义的人亲见罪刑的时候，不给他们减轻，也不姑容。","translator":"王静斋"},{"sura":16,"aya":86,"text":"拜偶像的人们亲见其所拜的匹偶时候，就说：（我们的养主啊！这一切是我们于你之外所呼求的匹偶。）于是它们回答他们：（你们实是谎言的。）","translator":"王静斋"},{"sura":16,"aya":87,"text":"当那一日，他们向安拉表示顺服。他们素所造作的不在了。","translator":"王静斋"},{"sura":16,"aya":88,"text":"不信并阻碍安拉之道的人，我就因为他们的作恶给他们在刑罚上增加刑罚。","translator":"王静斋"},{"sura":16,"aya":89,"text":"当那一日，我在每伙人中由他们同人里，复活一个见证人。我拿你来见证这般人。我曾降给你经典，解明万事，并为引导，慈惠，与给顺服的人们报喜信。","translator":"王静斋"},{"sura":16,"aya":90,"text":"的确，安拉命以公道、为善、赠给亲属；并禁止丑恶、非理、与背逆；他劝你们，冀使你们受教。","translator":"王静斋"},{"sura":16,"aya":91,"text":"你们结约的时候，要完成安拉的约；你们既以安拉为自己作保证了，就不可定约后破坏誓言。安拉确实知道你们的行为。","translator":"王静斋"},{"sura":16,"aya":92,"text":"你们不要像那一女子！她把自己的线在捻紧以后，破成一缕一抡的。你们把誓言作成自己间的阴谋，因为一伙是增过一伙的。安拉惟独以此试证你们。他必在复生日对你们表明你们已往所争议的。","translator":"王静斋"},{"sura":16,"aya":93,"text":"设若安拉意欲……了，他就教你们成为一伙。但是他任意造入迷误，任意造人得道。你们以往所作的事将必受质问。","translator":"王静斋"},{"sura":16,"aya":94,"text":"你们不得把誓言作成自己间的阴谋，而致脚步在稳定以后滑倒。并因阻止安拉的道尝灾害，且受重刑。","translator":"王静斋"},{"sura":16,"aya":95,"text":"你们不要以安拉的约换些微的代价。在安拉所据有的，实是于你们最好的。如果你们知道了……。","translator":"王静斋"},{"sura":16,"aya":96,"text":"你们所据有的将要消灭。安拉所据有的永存。我将必报给忍耐的人以善赏，本他们最优的作为。","translator":"王静斋"},{"sura":16,"aya":97,"text":"作善功的男女穆民，我将教他清洁着活；我必报给他们善赏，本他们最优的作为。","translator":"王静斋"},{"sura":16,"aya":98,"text":"你诵古兰的时候，要对于被逐的恶魔上祈安拉护佑。","translator":"王静斋"},{"sura":16,"aya":99,"text":"的确，他对于众归信并仰赖其养主的人无有权柄。","translator":"王静斋"},{"sura":16,"aya":100,"text":"他的权柄惟施诸一般爱喜他的人，和一般给真主举匹偶的人。","translator":"王静斋"},{"sura":16,"aya":101,"text":"我改换一节，以代一节的时候，——安拉至知自所颁降的；……他们说：（你只是一个编造者。）不然，实是他们的大多数不知道。","translator":"王静斋"},{"sura":16,"aya":102,"text":"你说：洁灵自你养主上据实颁降它了；只为安定众穆民并引导，与为众穆士林报喜信。","translator":"王静斋"},{"sura":16,"aya":103,"text":"我确知道他们说：（不过是有人教给他。）他们所指的那个人的乡音是外国语；这是显明的阿拉伯语。","translator":"王静斋"},{"sura":16,"aya":104,"text":"不信安拉表征的人，安拉必不引导他们，均应遭受痛刑。","translator":"王静斋"},{"sura":16,"aya":105,"text":"不信安拉表征的人，只是造作谎言；他们均是说谎的。","translator":"王静斋"},{"sura":16,"aya":106,"text":"在归信安拉以后，复又隐昧的人，（必遭真主的怒恼，）除非那被迫，其心借着归信得到安然的人。但是那以妄信开胸的人，应遭安拉的怒恼，并遭巨刑。","translator":"王静斋"},{"sura":16,"aya":107,"text":"这是由于他们弃后世而爱今生；并由于安拉不引导一般逆民，","translator":"王静斋"},{"sura":16,"aya":108,"text":"这一般是被安拉封闭其心与其耳眼的人。这一般均是昏愦的人。","translator":"王静斋"},{"sura":16,"aya":109,"text":"无疑他们在后世均是伤折的。","translator":"王静斋"},{"sura":16,"aya":110,"text":"你的养主且复〔多恕特慈〕一般在遭患以后迁居，复又奋斗坚忍的人；你的养主在这以后，一定是多恕的，特慈的。","translator":"王静斋"},{"sura":16,"aya":111,"text":"当那一日，每一个人来为自己辩论。各人得到完全还其所作的，并不受损。","translator":"王静斋"},{"sura":16,"aya":112,"text":"安拉发一个比喻： 一个平定 安宁 的城池，那城的给养很多的来自各方面；但是人民瞒昧安拉的恩典。于是安拉因它们所作的，使其尝试饥饿与畏惧的衣服。","translator":"王静斋"},{"sura":16,"aya":113,"text":"他们同人中的一位使者确曾来到，但是他们不信他。而后他们正在背义着遭受刑罚了。","translator":"王静斋"},{"sura":16,"aya":114,"text":"你们就着安拉所赐的吃清洁合法的吧，你们当感念安拉的恩典，如果你们惟独拜他了。","translator":"王静斋"},{"sura":16,"aya":115,"text":"他只严禁你们八吃一自死的、血、猪肉、与临宰时朗诵除过安拉的。其非背逆，也非越矩，被逼无奈的人，安拉实是多恕的，特慈的。","translator":"王静斋"},{"sura":16,"aya":116,"text":"你们不要为往安拉上造作谎言。信口称述：（这个合法，那个不合法。）的确，往安拉上造作谎言的人均不得意。","translator":"王静斋"},{"sura":16,"aya":117,"text":"只不过些微的享受。他们应遭痛刑。","translator":"王静斋"},{"sura":16,"aya":118,"text":"我对犹太人严禁先已述说给你的。我未亏负他们；但是他们自亏己身了。","translator":"王静斋"},{"sura":16,"aya":119,"text":"你的养主且复〔多恕特慈〕一般因愚作恶，而后复又忏悔自修的人。你的养主在此以后一定是多恕的，特慈的。","translator":"王静斋"},{"sura":16,"aya":120,"text":"易卜拉欣确是一位服从安拉命令，端正的楷模。他不是拜偶像的，","translator":"王静斋"},{"sura":16,"aya":121,"text":"实是感念真主之恩的。真主选择他，并导他于正道。","translator":"王静斋"},{"sura":16,"aya":122,"text":"我在今生赐给他幸福了；他在后世确是义人一类的。","translator":"王静斋"},{"sura":16,"aya":123,"text":"我且曾告谕你追从正直的易卜拉欣之道，他不是拜匹偶的。","translator":"王静斋"},{"sura":16,"aya":124,"text":"安息日是惟对一般因它起争议的人设置的。你的养主将在复生日，对他们所争议的从中判断。","translator":"王静斋"},{"sura":16,"aya":125,"text":"你当用智慧与善劝，导至养主的道；你当用至善的态度对他们辩论，你的养主确是至知错过他的道路的。他是至知一般得正道的。","translator":"王静斋"},{"sura":16,"aya":126,"text":"如果你们报复了，你们就可依照自受的残害似的施以报复。若是你们忍耐了，那是在忍耐的人上最好的。","translator":"王静斋"},{"sura":16,"aya":127,"text":"你当忍耐，你的忍耐惟借着安拉。你不要为他们忧虑。你对于他们的阴谋不要心窄。","translator":"王静斋"},{"sura":16,"aya":128,"text":"安拉是和一般敬畏与为善的人同在的。","translator":"王静斋"},{"sura":17,"aya":1,"text":"赞主清净！他教自己的仆人由禁寺夜赴我赐福于其四周的远寺，为的是把我的一部分表征现给他。主实是能听的。","translator":"王静斋"},{"sura":17,"aya":2,"text":"我曾赐给母撒经典，我委他引导以斯拉衣采的子孙，不要撇开鞭另取保护的。","translator":"王静斋"},{"sura":17,"aya":3,"text":"曾与努海同乘人的子孙们哪！他实是一个多感念的仆人。","translator":"王静斋"},{"sura":17,"aya":4,"text":"我在经典上告谕以斯拉衣来的子孙：你们将必在地上造恶两次，你们将必狂傲，很大地狂傲。","translator":"王静斋"},{"sura":17,"aya":5,"text":"当第一次约来到的时候，我就把自己有暴力的众仆派到你们那里；他们在各城里搜查。那是必须实现的约。","translator":"王静斋"},{"sura":17,"aya":6,"text":"而后，我又为你们恢复了强胜。我以资财儿女助长你们；我使你们在数目上较多。","translator":"王静斋"},{"sura":17,"aya":7,"text":"若是你们为善了，则为自身为善；若是你们作恶了，也是为着自身。在第二次约来到的时候，（我就派遣他们，）致使你们面观忧愁；并任他们进寺，如同前次进寺，更教他们捣毁自所征服的，","translator":"王静斋"},{"sura":17,"aya":8,"text":"切实地捣毁，养主就要怜恤你们 如果你们复反了， 我就复反。 我把火狱作成逆徒的监牢。","translator":"王静斋"},{"sura":17,"aya":9,"text":"的确，这古兰一导人于至正之道，并给一般作善举的穆民报喜信：（他们必受大赏，）","translator":"王静斋"},{"sura":17,"aya":10,"text":"并报告：（不信后世的人，我已为他们备下痛刑了。）","translator":"王静斋"},{"sura":17,"aya":11,"text":"人类求祸，就像他的求福；人类原是暴躁的。我把夜昼作为两个标识，但是我涂抹了夜的标识。","translator":"王静斋"},{"sura":17,"aya":12,"text":"我把白昼的标识造为明亮的，为的是你们寻求养主的特恩，并为知道年数与计算。我分析各项事情，切实地分析。","translator":"王静斋"},{"sura":17,"aya":13,"text":"我使每一个人所作过的，永不离开他的脖子。在复生日，我把文卷现给他，他亲见它伸展着。","translator":"王静斋"},{"sura":17,"aya":14,"text":"我对他说：你读自己的文卷吧！今一日，你自身对你清算已可满足了。","translator":"王静斋"},{"sura":17,"aya":15,"text":"得正道的人惟利自身得正道；迷误的人惟害自身迷误。负重担的人不负别个人的重担。我必先遣使而后施刑。","translator":"王静斋"},{"sura":17,"aya":16,"text":"我欲灭绝一个城池的时候，就令那地方上享受的人在那里作恶，而后八关于罪刑的u话对它实现了，我毁灭它，切实地毁灭。","translator":"王静斋"},{"sura":17,"aya":17,"text":"我在努海以后灭绝了若干代，你的养主尽知且见他众仆的罪恶已可满足了。","translator":"王静斋"},{"sura":17,"aya":18,"text":"讨现世（幸福）的人，我就在这里随意地赶快赐给自所意欲（赐给）的人，随后我为他安置火狱，他将被贬被逐着进它。","translator":"王静斋"},{"sura":17,"aya":19,"text":"那归信着讨后世并为它努力，切实努力的人，他们的努力是可获悦纳的。","translator":"王静斋"},{"sura":17,"aya":20,"text":"我助长每伙人——这一般和那一般；出自养主的赏赐。你养主的赏赐是不受制止的。","translator":"王静斋"},{"sura":17,"aya":21,"text":"看哪！我如何使他们的一部分优越一部分。一定后世是在阶级上至大的，是在优越上更大的。","translator":"王静斋"},{"sura":17,"aya":22,"text":"你不要举另一受拜的与安拉作同等妁，而致归为被贬被弃的。","translator":"王静斋"},{"sura":17,"aya":23,"text":"你的养主判定：你们不要敬拜除过他的；当对父母行孝；若是他俩的一个或两个统在你跟前达到年迈了，你不可对他俩说噫嘻！你不要呵叱他俩个，当对他俩说温语。","translator":"王静斋"},{"sura":17,"aya":24,"text":"当以敬爱对他俩表示谦卑；你说：（我的养主啊！求你对他俩行慈，一如他俩抚育我幼小时代似的。）","translator":"王静斋"},{"sura":17,"aya":25,"text":"养主至知你们心中所怀的。如果你们是义人了，他一定是多恕一般归向的。","translator":"王静斋"},{"sura":17,"aya":26,"text":"你要给亲属、贫人和旅客应得的利益。你不要滥费，不要滥费。","translator":"王静斋"},{"sura":17,"aya":27,"text":"滥费的人们确是恶魔的弟兄。恶魔是对他的养主忘恩的。","translator":"王静斋"},{"sura":17,"aya":28,"text":"如果你因等侯自所盼望的养主慈惠回避他们了，你就对他们说温和的话。","translator":"王静斋"},{"sura":17,"aya":29,"text":"你不要吝啬，也不要十分地铺展，而致遭受责备，感觉懊丧。","translator":"王静斋"},{"sura":17,"aya":30,"text":"你的养主为其意欲的人伸缩给养，他实是尽知且见自己众仆的。","translator":"王静斋"},{"sura":17,"aya":31,"text":"你们不要因为怕穷杀害自己的子女，惟我赐给他们和你们给养。 杀害他们实是一件大罪","translator":"王静斋"},{"sura":17,"aya":32,"text":"你们不得接近奸淫。那实是丑恶。其道太恶了，","translator":"王静斋"},{"sura":17,"aya":33,"text":"你们除非因着正义给人偿命外，不得杀那业经安拉严禁的性命。凡被屈杀的人，我确已赐给丧主权柄了。莫容他对于杀害上过分。他是该受援助的。","translator":"王静斋"},{"sura":17,"aya":34,"text":"你们不得接近孤儿的财产；除非用那较善的手续，待其达到壮年时为止。","translator":"王静斋"},{"sura":17,"aya":35,"text":"你们应当全约；约会确是将受质问的。你们量出去的时候，要给足了量具。你们要用公秤称给人家。这是较好的，是结果至善的。","translator":"王静斋"},{"sura":17,"aya":36,"text":"你不要追随自所不知的。的确，耳眼心，这一切统是负责的。","translator":"王静斋"},{"sura":17,"aya":37,"text":"你不要在地上狂行；你绝不能扯破地，也绝不能达到山那么高。","translator":"王静斋"},{"sura":17,"aya":38,"text":"这一切其罪恶在你的养主视为可憎的。","translator":"王静斋"},{"sura":17,"aya":39,"text":"这是你的养主所告谕你的学识。你不要举另一受拜的与安拉作同等。若然，你就受谴责，受驱逐着被投在火狱中。","translator":"王静斋"},{"sura":17,"aya":40,"text":"你们的养主把男儿让给你们，而自己由天使中选择女儿吗?你们确说出大话了。","translator":"王静斋"},{"sura":17,"aya":41,"text":"我在这古兰中反复申述，好教他们受劝。（但是）那话只增他们逃遁。","translator":"王静斋"},{"sura":17,"aya":42,"text":"你说，（设若尚有些个主与他同等，一如他们所说的，那时候，他们一定就向掌（阿勒世）的主寻一个途径了。）","translator":"王静斋"},{"sura":17,"aya":43,"text":"真主清净！他玄高于他们所说的，而且大玄高。","translator":"王静斋"},{"sura":17,"aya":44,"text":"七天与地与其它万有颂主清净！无一物不以赞他称颂清净，但是你们不解它们的称颂。主确是宽仁的，多恕的。","translator":"王静斋"},{"sura":17,"aya":45,"text":"你诵古兰的时候，我就在你和一般不信后世的人之间置一被垂的帐幔。","translator":"王静斋"},{"sura":17,"aya":46,"text":"我在他们心上置些个遮覆而致不能解陪，并教他们重听。你藉古兰记念调养你的独一主时候，他们逃遁着退转了。","translator":"王静斋"},{"sura":17,"aya":47,"text":"当他们向你侧耳并互相密语的时候，我是至知他们所因它闻听的。那时候，背义的人们说：（你们不过是追随一个受了幻惑的人。）","translator":"王静斋"},{"sura":17,"aya":48,"text":"看哪！他们是怎样地为你发比喻，而致迷误，竟不能得一途径。","translator":"王静斋"},{"sura":17,"aya":49,"text":"他们说：（我们既已化成了骨骸与朽肢的时候，定可重新被造吗?）","translator":"王静斋"},{"sura":17,"aya":50,"text":"你说（要你 们成为石铁，","translator":"王静斋"},{"sura":17,"aya":51,"text":"或你们胸中认为重大的另一被造者！）他们即将说：（谁复活我们呢?）你说！（那初次造化你们的主。）他们即将向你摇首，说：（那在何时呢?）你说，（恐怕它是相近的吧！）","translator":"王静斋"},{"sura":17,"aya":52,"text":"当那一日，真主呼唤你们，继而你们以赞他作应承。你们揣度：只迟留少时。","translator":"王静斋"},{"sura":17,"aya":53,"text":"你告诉我的众仆，说至善的话：的确，恶魔在他们中间挑拨。恶魔确是人类的明敌。","translator":"王静斋"},{"sura":17,"aya":54,"text":"养主是至知你们的。他若意欲（怜恤）了，他就怜恤你们；他若意欲〔罪刑〕了，他就罪刑你们。我未差你保护他们。","translator":"王静斋"},{"sura":17,"aya":55,"text":"你的养主至知天地间所有的。我确使一部分圣人优越一部分。我且赐给达伍德则逋尔。","translator":"王静斋"},{"sura":17,"aya":56,"text":"你说，你们呼求那超过他自所臆度的吧！他们不管给你们解难与转移。","translator":"王静斋"},{"sura":17,"aya":57,"text":"他们呼求的这一切，其最接近的向养主求接近，并盼他的慈惠，骇怕他的罪刑。养主的刑罚确实可骇。","translator":"王静斋"},{"sura":17,"aya":58,"text":"无一个城池不是我在复生日以前消灭它，或惩它烈刑的。这是在（被庇的牌）上写定的。","translator":"王静斋"},{"sura":17,"aya":59,"text":"我制止发异迹，只因先前的人们不信它。我曾把母驼给了赛母待作明证。但是他们迫害它了。我发各项异迹，只是为着惊吓。","translator":"王静斋"},{"sura":17,"aya":60,"text":"那时候，我对你说：你的养主确环绕众人了。我只以所现给你的梦象，与古兰上所记的被诅的树，作为试证世人的。我惊吓他们，但是我的惊吓益增他们大逆。","translator":"王静斋"},{"sura":17,"aya":61,"text":"昔时，我语众天使向阿丹下拜，他们遂就下拜了。只有以卜里厮（拒绝。）他说：（我向你以泥造化的人下拜吗?）","translator":"王静斋"},{"sura":17,"aya":62,"text":"他又说：（你教他优越我的这个人，求你告诉我！）你若姑容我直到复生日，我必根绝他的子孙，只余少数的。","translator":"王静斋"},{"sura":17,"aya":63,"text":"主说：（你去吧！他们其中追随你的人， 火狱一定你们的报应，充分地报应。","translator":"王静斋"},{"sura":17,"aya":64,"text":"你以自己的声音摇动你所能够的人吧！任你向他们催动自己的骑兵与步兵！你在资财子女上和他们相共！你许给他们吧！恶魔的约只是欺骗。","translator":"王静斋"},{"sura":17,"aya":65,"text":"你对我的仆人无有权柄。你的养主作保护已可满足了。）","translator":"王静斋"},{"sura":17,"aya":66,"text":"你们的养主是那为你们教船只行驶在海中，以便你们讨其特恩的。他实是特慈你们的。","translator":"王静斋"},{"sura":17,"aya":67,"text":"你们在海中遇难的时候，除去真主以外，你们素所呼求的已然消失了。及他解救你们达至陆地的时候，你们就扭转了。人类原是忘恩的。","translator":"王静斋"},{"sura":17,"aya":68,"text":"你们岂可不怕真主把你们陷在陆地，或往你们上发下砂石，你们不复得到一个维护的？","translator":"王静斋"},{"sura":17,"aya":69,"text":"不然，你们岂可不怕他另次教你们复返在海中，向你们驱暴风，至因你们的不信而淹没你们，不复得到一个抗我帮助你们的?","translator":"王静斋"},{"sura":17,"aya":70,"text":"我，确敬重了阿丹的子孙，我把他们载在陆海上。我把一部分美品赐给他们。我使他们优越我所造化的许多，切使优越。","translator":"王静斋"},{"sura":17,"aya":71,"text":"当那一日，我唤每伙人连带他们的首领。用右手接受文卷的人，他们读自己的文卷，并不受丝毫的亏损。","translator":"王静斋"},{"sura":17,"aya":72,"text":"在今生盲目的人，那就是在后世盲目的，且是最错走道路的。","translator":"王静斋"},{"sura":17,"aya":73,"text":"他们几乎以离开我所启示你的来陷害你，好教你往我上另行造作。如果是这样，他们必以你当契友了。","translator":"王静斋"},{"sura":17,"aya":74,"text":"设非我安定你，你几乎就略微偏于他们一事了。","translator":"王静斋"},{"sura":17,"aya":75,"text":"那时候，我必教你尝加倍的活〔刑，〕加倍的死（刑。）而后你得不到一个抗我相助你的人。","translator":"王静斋"},{"sura":17,"aya":76,"text":"他们几乎教你由地上跌倒，为的是把你从那里逐出。如果是那样，他们在你以后，只能迟留短期。","translator":"王静斋"},{"sura":17,"aya":77,"text":"这是我先你所差之列使的惯例。你不见封我的惯例上有变更。","translator":"王静斋"},{"sura":17,"aya":78,"text":"你当在日昃直到黑夜的时候奉行拜功。你当诵晓时的古兰。晓时的古兰确系得到莅临的。","translator":"王静斋"},{"sura":17,"aya":79,"text":"你要超过你的责任着在夜半不睡。你的养主即将把你派到可颂的地方。","translator":"王静斋"},{"sura":17,"aya":80,"text":"你说：（我的养主啊！你使我进入，欣悦地使入，你使我出去，欣悦地使出，你把一种辅助的力量亲自赐给我！）","translator":"王静斋"},{"sura":17,"aya":81,"text":"你说！（真的来了，假的去了，假的实是该去掉的。）","translator":"王静斋"},{"sura":17,"aya":82,"text":"我降下那医治与慈惠众穆民的古兰。那仅增给一般背义的人伤折。","translator":"王静斋"},{"sura":17,"aya":83,"text":"我对人施恩的时候，他就扭转而避在一旁。他遇难的时候，便就绝望。","translator":"王静斋"},{"sura":17,"aya":84,"text":"你说：（每一个人依着自己的途径去作。你们的养主至知那最获正道的人。）","translator":"王静斋"},{"sura":17,"aya":85,"text":"他们对于灵魂上问你。（灵魂属我养主的命令。所赐给你们的知识不过少微的。）","translator":"王静斋"},{"sura":17,"aya":86,"text":"我若意欲（收回）的时候，必就收回所告语你的，而后你得不到一个因此抗我维护你的。","translator":"王静斋"},{"sura":17,"aya":87,"text":"只是籍着你的养主的慈惠。他对于你的特恩实在是大的。","translator":"王静斋"},{"sura":17,"aya":88,"text":"你说：（人类与镇呢若共同举出象这古兰的，纵令他们彼此相辅，也不能举出和他一样的来。）","translator":"王静斋"},{"sura":17,"aya":89,"text":"我在这古兰上向世人反复解明各项比喻。但是大多数人不肯接受，只有隐昧而已。","translator":"王静斋"},{"sura":17,"aya":90,"text":"他们说：（我们绝不信你，待至你为我们从地里涌出源泉；","translator":"王静斋"},{"sura":17,"aya":91,"text":"获你拒有枣树与葡萄园，你使诸河在内长流；","translator":"王静斋"},{"sura":17,"aya":92,"text":"获依你所自称的把一块天落到我们上，或你当面举出安拉和天使；","translator":"王静斋"},{"sura":17,"aya":93,"text":"或是你具有金库，或你升上天去。非至你降下经典，由我们读它着，则绝不信你的升高。）你说：（颂我的养土清净，我只是一个被差的人。）","translator":"王静斋"},{"sura":17,"aya":94,"text":"在诸人得到引领的时候，阻止他们归信的，不外是说：（安拉能遣吓人为使吗?）","translator":"王静斋"},{"sura":17,"aya":95,"text":"你说：（设若在地上有天使安然着行走，我必从天上把天使降给他们为使。）","translator":"王静斋"},{"sura":17,"aya":96,"text":"你说：（安拉在我和你们中间作见证已可满足了。他实是尽知且见其众仆的。","translator":"王静斋"},{"sura":17,"aya":97,"text":"安拉引导的人，就是得正道的；他造为迷误的人，你绝见不到一个越他相助他们的。）当复生日，我教他们面目朝下，眼瞎、暗哑、耳聋着复起。他们的归处是火狱。每逢火狱熄灭的时候，我就给它们加上火。","translator":"王静斋"},{"sura":17,"aya":98,"text":"这是他们的报应；只因他们不信我的表征，并谓：（我们既成骨骸与朽肢的时候，定必重新被造吗?）","translator":"王静斋"},{"sura":17,"aya":99,"text":"他们岂未看见！那造化—大地的主是能于造化和他们同样的，并为他们造定不可疑的时期吗?背义的人不接受，只是隐昧而已。","translator":"王静斋"},{"sura":17,"aya":100,"text":"你说：（设若你们掌我养主的慈库了，那时候，你们就因恐怕使用而吝啬了。人类原是悭吝的。）","translator":"王静斋"},{"sura":17,"aya":101,"text":"在母撒来至以斯拉衣来子孙的时候，我确赐给他九项异迹了。你问他们吧！匪喇傲对他说：（母撒呀！我实揣度你是一个受了眩惑的。）","translator":"王静斋"},{"sura":17,"aya":102,"text":"他说：（你确知道降下这些明证的，只是调养天地的主；匪喇傲啊！我实揣度你是将灭亡的，）","translator":"王静斋"},{"sura":17,"aya":103,"text":"他要把他们逐出此地，而后我把他和随同他的人一并淹死。","translator":"王静斋"},{"sura":17,"aya":104,"text":"在他以后我命以斯拉衣来的子孙居住在该地。当后世的约期已到的时侯，我就把你们搀杂着拿到。","translator":"王静斋"},{"sura":17,"aya":105,"text":"我因真理下降它，含真理下降。我只是差你报喜信，传警告着。","translator":"王静斋"},{"sura":17,"aya":106,"text":"我分降古兰，以便你把它慢慢地诵给众人。我颁降它，切实地颁降。","translator":"王静斋"},{"sura":17,"aya":107,"text":"你说：（你们信与不信随便。）的确，先前蒙赐知识的人们，一旦有人把它诵给他们了，他们就下颏伏地叩头着，","translator":"王静斋"},{"sura":17,"aya":108,"text":"说：颂我们的养主清净！我们养主的约一定实现。","translator":"王静斋"},{"sura":17,"aya":109,"text":"他们下颏伏地着痛哭。那益增他们畏惧。","translator":"王静斋"},{"sura":17,"aya":110,"text":"你说：（你们称安拉，或称来哈曼。你们称无拘什么，惟他具有最优的名称。）你不要高声礼拜，也莫低声。须取一适中之道。","translator":"王静斋"},{"sura":17,"aya":111,"text":"你说：（赞颂真主！他未立子，无有在权柄上与他共，同的。在他也无一个对于卑贱上爱护的）你当颂他伟大，称赞伟大！","translator":"王静斋"},{"sura":18,"aya":1,"text":"赞颂真主，他把经典降给自己的仆人，并未在它上发观歪曲；","translator":"王静斋"},{"sura":18,"aya":2,"text":"严正的，为的是以出自他的烈刑作警告，并给一般作善举的谬民报喜音，（说：）他们必获善赏，","translator":"王静斋"},{"sura":18,"aya":3,"text":"时常居住在那里，","translator":"王静斋"},{"sura":18,"aya":4,"text":"并警告一般声称：（安拉立子）的人。","translator":"王静斋"},{"sura":18,"aya":5,"text":"他们及其祖先无有些须知识，那出自他们口中的话太大了！他们只是说谎言。","translator":"王静斋"},{"sura":18,"aya":6,"text":"若是他们不信这话了，恐怕你是随后因忧伤身的。","translator":"王静斋"},{"sura":18,"aya":7,"text":"我确把地上的一切置作它的装饰，为的是试证他们的哪一个是行为至善的。","translator":"王静斋"},{"sura":18,"aya":8,"text":"我将必把地上的一切化为不毛的平地。","translator":"王静斋"},{"sura":18,"aya":9,"text":"你可知道洞穴与「来根」的人们是属我的奇迹吗?","translator":"王静斋"},{"sura":18,"aya":10,"text":"那时候，一伙子青年遁入洞中，说：（我们的养主啊！你把出于自己的慈惠赐给我们…你为我们的事情预备指导！）","translator":"王静斋"},{"sura":18,"aya":11,"text":"我封闭他们的听官，居在洞内若干年；","translator":"王静斋"},{"sura":18,"aya":12,"text":"又教他们醒来，卒至我晓得 两伙人的哪一伙至知所迟留的期限。","translator":"王静斋"},{"sura":18,"aya":13,"text":"我把他们的消息据实述说给你；他们确是一伙信其养主的青年。 我加增引导他们了。","translator":"王静斋"},{"sura":18,"aya":14,"text":"我曾坚固他们的心。那时候，他们起立说：我们的养是调养天地的主。我们绝不于他之外另取一个受拜的。果然是那样，我们确说出过当的话了。","translator":"王静斋"},{"sura":18,"aya":15,"text":"这般人——我们的同族，他们于他之外另立些个受拜的。他们对于（拜）那般假主上怎不举出一个显明的凭据呢? 谁是比那往安拉上说谎的人更是不义的。","translator":"王静斋"},{"sura":18,"aya":16,"text":"那时候，你们远离他们，和他们撇开安拉所拜的。你们遁居洞穴吧！你们的养主就以自己的慈惠为你们开展，并为你们的事情安排利益。","translator":"王静斋"},{"sura":18,"aya":17,"text":"你看太阳上升的时候，它从洞穴往右斜；太阳落下的时候，它就经过他们往左偏；他们正是在洞内一个宽场里。这是安拉的奇迹。安拉引领的人，他就是得正道的。真主造为迷误的人，你绝得不到一个对他爱护的，指导的。","translator":"王静斋"},{"sura":18,"aya":18,"text":"你以为他们是醒的，他们本是睡的，我教他们翻右翻左。他们的狗是在洞门上张开它的两肘的。设使你望见他们了，你必从他们那里逃回，你必饱受惊骇。","translator":"王静斋"},{"sura":18,"aya":19,"text":"就是这样；我教他们醒来，好教他们彼此询问。其中一个发言人说：（你们迟留好多时了。）他们就说：（我们迟留一日或半日。）他们说：（养主至知你们所迟留的。你们打发一个人持这银币去到城里；教他参看，城内的哪一家是食品至洁的。令他就把那里的食物给你们带来。当令他温和，莫任一个人知道你们。","translator":"王静斋"},{"sura":18,"aya":20,"text":"如果他们望见你们，他们就用石块打死，或使你们归入他们教内。果然是那样，你们就永不能逃脱了。）","translator":"王静斋"},{"sura":18,"aya":21,"text":"我就这样地教人知道他们；只为他们晓得安拉的约是确实的；并晓得复生日是无可疑惑的。那时候，他们对那般人的事情彼此相争， 说：（你们在他们那里建筑一堵墙，）他们的养主是至知他们的。一般操纵他们的事情者说：我们必在他们那里建一个礼拜的地方。","translator":"王静斋"},{"sura":18,"aya":22,"text":"他们即将说：（是三个，连狗是四个）他们说：（是五个，连狗是六个；）对于未见的事悬揣着。他们说：（是七个，连狗是八个，）你说：我的养主至知他们的数目。知道他们的只有少数人；除却表面的辩论以外，你莫因为他们起争论。你不要为他们询问任何一个人。","translator":"王静斋"},{"sura":18,"aya":23,"text":"你对任何一事切不要说：（我要明天去作，）","translator":"王静斋"},{"sura":18,"aya":24,"text":"除非加上（安拉意欲……。）你忘记的时候，要得记念你的养主，说：（我的养主就要把我引至比这个更近的正道。）","translator":"王静斋"},{"sura":18,"aya":25,"text":"他们在洞内迟留三百零九年。","translator":"王静斋"},{"sura":18,"aya":26,"text":"你说：安拉是至知他们所迟留的。惟他知道天地间目不能见的。在他看得多么清！听得多么仔细啊！在他们除他以外，无一爱护的。他不容任何广个参与他的判断。","translator":"王静斋"},{"sura":18,"aya":27,"text":"你诵所默谕你的——养主的经典；无有变更其言的。你撇开他决得不到一个投奔的地方。","translator":"王静斋"},{"sura":18,"aya":28,"text":"你当随同一般讨主的喜悦着，朝夕拜其养、主的人而抑制自己。你一个可音心想今生的装饰着，而忽视他们。由我使其心昏不知记念我，而追随自己偏好的人，你不要服从他。他的事情是过当的。","translator":"王静斋"},{"sura":18,"aya":29,"text":"你说：（真的出自养主；谁欲相信，就任他相信；谁欲不信，就任他不信。）我已为一般背义的人预备火了。他们被火幔包围着。若是他们要求解救了，就以类似熔铜炙热面部的水解救他们。饮料太恶了！地位太恶了！","translator":"王静斋"},{"sura":18,"aya":30,"text":"归信并作善举的人，我必不湮没为善者的报赏。","translator":"王静斋"},{"sura":18,"aya":31,"text":"这般人可获诸河流于其下常住的天园；他们在那里得饰金钏，并着粗细绸缎衣。在那里靠在床位上。善赏太好了！地位很高了！","translator":"王静斋"},{"sura":18,"aya":32,"text":"你向他们发一个比喻：有两个人，我赐其一人两座葡萄园；我用枣树围绕这二园；并在其中间发观禾谷，","translator":"王静斋"},{"sura":18,"aya":33,"text":"两座园子各结其果实，并未减收一些。我使河（水）涌流其中，","translator":"王静斋"},{"sura":18,"aya":34,"text":"在他可获得些个果实。而后他对自己的同伴辩论道：（我是比你的资财较多的，同伙至贵的。）","translator":"王静斋"},{"sura":18,"aya":35,"text":"他就自负着进了他的园子，说：（我永不认为这园子有消灭；","translator":"王静斋"},{"sura":18,"aya":36,"text":"我不认为复生日是将成立的。如果我被返回养主了，我必得到一个比它更好的归所。）","translator":"王静斋"},{"sura":18,"aya":37,"text":"他的同伴对他辩论道：你不信真主吗?他造你于土上，再造一点精上，复使你成为一个完美的人。","translator":"王静斋"},{"sura":18,"aya":38,"text":"我只〔深信〕他是安拉。调养我的主；我不单任何一个给我的养主做匹偶。","translator":"王静斋"},{"sura":18,"aya":39,"text":"你进园子的时候，为什么不说：安拉欲有的，〔一定要有。 〕除去仰赖安拉，别无有力量。你若看我在资财子嗣上较少于你了，","translator":"王静斋"},{"sura":18,"aya":40,"text":"或许我的养主要把强过你的园子赐给我，他往你的园子上降天灾，致成光渭的平地；","translator":"王静斋"},{"sura":18,"aya":41,"text":"或是园子的水渗下去，你不复能于寻求它。","translator":"王静斋"},{"sura":18,"aya":42,"text":"它的果实遭了灾殃。次晨，他因为痛惜自所施用在其中的，反转自己的两手。其时，那园子是在屋顶倒塌着。他说：（哎，宁愿我未曾举任何一个为我的养主作匹偶。）","translator":"王静斋"},{"sura":18,"aya":43,"text":"在他无有一伙子人超过安拉相助他；他也不是自卫的。","translator":"王静斋"},{"sura":18,"aya":44,"text":"当那时候，爱护惟在真的安拉。他是最善于赏赐的，最善于报酬的。","translator":"王静斋"},{"sura":18,"aya":45,"text":"你对他们发一今生的比喻：就像是我自上空降下的水，地上的植物借着它丛杂起来，既而成为被风吹散的枯屑。安拉是大能于万事的。","translator":"王静斋"},{"sura":18,"aya":46,"text":"资财子嗣是今生的装饰。永存的善功，在你的养i视为在善赏上至好的；在归结上至优的。","translator":"王静斋"},{"sura":18,"aya":47,"text":"当那一日，我使山行动。你看见地是显明的，我集合了他们，我不遗漏他们其中的一个。","translator":"王静斋"},{"sura":18,"aya":48,"text":"他们排列着被现到你的养主。你们必定来至我，如同我初次造化你们似的。不然，你们自以为我绝不为你们规定一个约许的地方吗?","translator":"王静斋"},{"sura":18,"aya":49,"text":"递给文卷，而后你看见一切为恶的人是因着内中所记者骇惧的。他们说：（哎！我们遭祸了！怎么这文卷不遗漏一件小罪大罪只是一概记录它呢?）他们得见以往所作的是在面前。你的养主不亏负任何一个人。","translator":"王静斋"},{"sura":18,"aya":50,"text":"昔时，我对众天使说：（你们向啊丹下拜吧！）他们遂就下拜了；以卜里厮在外，他原属镇呢。而后他出离养主的命令了。你们撇开我，以他和他的子孙当爱友吗?他们本是你们的仇敌。背义者的更易太恶了！","translator":"王静斋"},{"sura":18,"aya":51,"text":"我未教他们亲临造化天地，与造化他们自身。我未把诱人迷误的当臂助。","translator":"王静斋"},{"sura":18,"aya":52,"text":"当那一日，主说：（你们唤自所妄称的匹偶，）他们遂就呼喊，但是那些匹偶未应答他们，我在他们中间置一沦落的地方。","translator":"王静斋"},{"sura":18,"aya":53,"text":"为恶的人们看见火了，他们深知自己是将落在其中，不得由那里移转的。","translator":"王静斋"},{"sura":18,"aya":54,"text":"我在这古兰中确为众人反复表明各项比喻。人是物类中辩论最多的。","translator":"王静斋"},{"sura":18,"aya":55,"text":"人类在得到指导的时候，所阻止他们归信与求其养主饶恕的，只是等待前人的往例，或遭遇种种刑罚。","translator":"王静斋"},{"sura":18,"aya":56,"text":"我遣列使不外是报喜信的，传警告的。不信的人妄自争论，只为藉此破坏真理，他们以我的各项表征与所警告他们的，当作可戏的。","translator":"王静斋"},{"sura":18,"aya":57,"text":"遇人劝以养主的经文而远避它，并忘其两手往前送的，谁比这类人更是背义?的确，我把遮幔置在他们的心上，（不欲）他们解悟它，并教他们重听！你若向正道上唤导他们，那时候，他们绝不能得正道。","translator":"王静斋"},{"sura":18,"aya":58,"text":"你的养主是多恕的，有慈惠的。设若他因他们所作的施以惩罚，他必立时向他们观出刑罚了。但是在他们有一个约定的时候，他们撇开他绝得不到一个避难所。","translator":"王静斋"},{"sura":18,"aya":59,"text":"这些城池，当人们背义的时候，我消灭他们了。我为消灭他们规定一个约期。","translator":"王静斋"},{"sura":18,"aya":60,"text":"昔时，母撒对他的青年说：（我常时旅行，而致达到两海交会的地方，或是我经过很久的时间。）","translator":"王静斋"},{"sura":18,"aya":61,"text":"当他二人走到两海交会的地方，他二人忘记鱼了。那鱼曾以海道当作了地穴。","translator":"王静斋"},{"sura":18,"aya":62,"text":"及至他俩走过的时候，母撒就对他的青年洗：（你把早饭拿给我们吧！我们在这次旅行中确遇到苦楚了。）","translator":"王静斋"},{"sura":18,"aya":63,"text":"他说：（你看见了吗? 当我们遁避岩石的时候，我忘却鱼了，使我忘记[提及]它的，只是亚心魔……他往海里取其可奇的道路了。）","translator":"王静斋"},{"sura":18,"aya":64,"text":"母撒说：（这是我们所寻求的，）而后他俩循着原路回去访查。","translator":"王静斋"},{"sura":18,"aya":65,"text":"他俩遂就得到我众仆中的一个仆人；我曾把自己的慈惠赐给他，并把自己的知识教与他了。","translator":"王静斋"},{"sura":18,"aya":66,"text":"母撒对他说：（我追随你，赖你把自所习学的一部分真理指示给我，可以吗?）","translator":"王静斋"},{"sura":18,"aya":67,"text":"他说：（你绝不能随我忍耐；","translator":"王静斋"},{"sura":18,"aya":68,"text":"你如何能忍于不深知道的事情呢?）","translator":"王静斋"},{"sura":18,"aya":69,"text":"母撒说：（若是安拉意欲……了，你就得到我是忍耐的；我不违背你的命令。）","translator":"王静斋"},{"sura":18,"aya":70,"text":"他说：（如果你欲追随我了，遇事非至我明白告诉你，你不要问。）","translator":"王静斋"},{"sura":18,"aya":71,"text":"他俩遂就行走。及至他俩乘上船的时候，那人就把船捣漏了。母撒说：（你捣漏这船，为的是淹死这船上的人吗?你确发现可奇的事情了。）","translator":"王静斋"},{"sura":18,"aya":72,"text":"他说：（我岂未说：你绝不能随我忍耐吗?）","translator":"王静斋"},{"sura":18,"aya":73,"text":"母撒说：（你不要因我的忘记而责备我；你对我的事不要强我所难。）","translator":"王静斋"},{"sura":18,"aya":74,"text":"他俩随又行走。及其遇见一个童子，他就把他杀死的时候，母撒说：（你非为给人抵命而杀死清白的人吗?你确发现可厌恶的事情了。）","translator":"王静斋"},{"sura":18,"aya":75,"text":"他说：（我岂为未对你说：你绝不能随 忍耐吗：）","translator":"王静斋"},{"sura":18,"aya":76,"text":"母撒说：[以后我若再问你一事了，你就不要半同我；（那时侯）你必得到我的原谅了。]","translator":"王静斋"},{"sura":18,"aya":77,"text":"他两遂又行走。几其达到一城的居民时候，他两就向那地方人讨食物；但是他们拒绝款待。既而他两在那里得见一堵墙，将要倾倒，他就把它扶正了。（设若你意欲（受酬）了，你必因它取工资。","translator":"王静斋"},{"sura":18,"aya":78,"text":"他说：[这是我们彼此离别（的时候。）]我就要向你明白解释你未能对它忍耐的，","translator":"王静斋"},{"sura":18,"aya":79,"text":"说到船只上，它本属在海上工作的几个贫人；我欲摧残它；在他们前面有一个王，强征所有的船只。","translator":"王静斋"},{"sura":18,"aya":80,"text":"说到童子上，他的父母是两个穆民；我们恐怕他强迫他两悖逆与忘恩。","translator":"王静斋"},{"sura":18,"aya":81,"text":"我们意欲养主把一个比他更清洁的，尤近于行孝的，抵换给他二人。","translator":"王静斋"},{"sura":18,"aya":82,"text":"说到墙上，它本是城内两个孤儿的；在那独墙下有他两的储藏；他两的父是个义人。你的养主欲他两达到壮岁，而自行发掘储藏。这是出自养主的慈惠！我作这事，并非于个人的主张。这是解释你未能对它忍耐的。","translator":"王静斋"},{"sura":18,"aya":83,"text":"他们对于租立格儿乃尼上问你；你说！（我即将把他的消息述说给你们。）","translator":"王静斋"},{"sura":18,"aya":84,"text":"我赐给他在地上的能力。我把各项事情的机缘赐给他了，","translator":"王静斋"},{"sura":18,"aya":85,"text":"他就随从机缘。","translator":"王静斋"},{"sura":18,"aya":86,"text":"及他达到日落之地的时候，他就得见太阳落在黑泥泉里；他在那里得见一伙人。我呼租立格儿乃尼说：（或你施行惩罚，或你善待他们。）","translator":"王静斋"},{"sura":18,"aya":87,"text":"他说：（论到背义的人，我将对他施刑；再使其返回他的养主，而惩他以烈刑","translator":"王静斋"},{"sura":18,"aya":88,"text":"论到归信并做善的人，他可获得善报。我即将对他说出我的容易举行的命令。）","translator":"王静斋"},{"sura":18,"aya":89,"text":"他又随从机缘。","translator":"王静斋"},{"sura":18,"aya":90,"text":"及其抵达日升之地的时候，他就得见太阳照耀一伙人，我未为他们在那里造一遮挡。","translator":"王静斋"},{"sura":18,"aya":91,"text":"就是这样。我尽知他所具有的。","translator":"王静斋"},{"sura":18,"aya":92,"text":"他复随从机缘。","translator":"王静斋"},{"sura":18,"aya":93,"text":"及其抵达两山之间的时候，条件在两山跟前得见一伙人，他们几乎不解一语。","translator":"王静斋"},{"sura":18,"aya":94,"text":"他们唤租立格儿乃尼说：（的确，耶朱哲、马朱哲在地上造恶的。我们献给你报酬，你在我们和他们当中筑一隔樟，可以吗：）","translator":"王静斋"},{"sura":18,"aya":95,"text":"他说：（我的养主赐给我的能力是较优的。你们助我以力吧！我在你们和他们中间筑一道坚固的隔樟；","translator":"王静斋"},{"sura":18,"aya":96,"text":"要你们把铁片供给我。）及至他在两山之间砌平的时候，他就说：（你们吹火吧！）及他化铁成火的时候，就说：（你们把溶铜供给我！我就灌到它上；","translator":"王静斋"},{"sura":18,"aya":97,"text":"嗣后，他们就不能登上它去，也不能钻透它。）","translator":"王静斋"},{"sura":18,"aya":98,"text":"他说：（这是我养主的慈惠。待至养主所约的来到时，他就把它粉碎了。养主的约是确实的。）","translator":"王静斋"},{"sura":18,"aya":99,"text":"那时侯，我教他们彼此搀混。吹起了号筒，我集合他们，切实地集合。","translator":"王静斋"},{"sura":18,"aya":100,"text":"我把火狱献给悖逆的人们，切实地现给。","translator":"王静斋"},{"sura":18,"aya":101,"text":"他们的眼被遮覆，看不到我的教诫；他们且不能听到。","translator":"王静斋"},{"sura":18,"aya":102,"text":"背义的人们自以为于我之外另以我的众仆当爱友了吗？我已为悖逆的人预备火狱做路餐了。","translator":"王静斋"},{"sura":18,"aya":103,"text":"你说！（我把一般在行为上至伤折的告诉你们，可以吗：）","translator":"王静斋"},{"sura":18,"aya":104,"text":"那般人，他们在今生的努力已然失效；他们尚以为做下好事了。","translator":"王静斋"},{"sura":18,"aya":105,"text":"这般人是不信养主的表征且不信见主的，所以他们的功修归为失效了。在复生日，我不为他们立砝码。","translator":"王静斋"},{"sura":18,"aya":106,"text":"这火狱是他们的报应，所因他们不信并把我的表征与我的列使当作可戏的。","translator":"王静斋"},{"sura":18,"aya":107,"text":"的确，归信并组善举的人。斐尔道司天园定是他们的路餐，","translator":"王静斋"},{"sura":18,"aya":108,"text":"永居在那里；他们不要求自那里移动。","translator":"王静斋"},{"sura":18,"aya":109,"text":"你说：（设若海是（录）我养主之言的墨（水）那海就在我养主的话未尽以前尽竭了。假如我取和它相等的来助长，——）","translator":"王静斋"},{"sura":18,"aya":110,"text":"你说：（我只不过和你们一样的人；我得到示谕：你们的主是独一主。凡盼望见其养主的人，当要令他做善举，对于敬拜其养主上不得举任何一个为共同的。）","translator":"王静斋"},{"sura":19,"aya":1,"text":"（克夫、哈、押、二尼、萨搭。）","translator":"王静斋"},{"sura":19,"aya":2,"text":"（这是）记你养主慈悯自己的仆人—甾克另亚。","translator":"王静斋"},{"sura":19,"aya":3,"text":"那时侯，他暗自呼吁他的养主，","translator":"王静斋"},{"sura":19,"aya":4,"text":"说：（我的养主啊！我的骨衰弱了，头上白发闪烁了。我的养主啊！我对于求你上未曾失望。","translator":"王静斋"},{"sura":19,"aya":5,"text":"我实害怕身后的近族们。我的妻已经断产了。求你自你卸前赏我一子，","translator":"王静斋"},{"sura":19,"aya":6,"text":"他继承我；并继承叶尔孤白的子孙。我的养主啊！你造他为可爱的！）","translator":"王静斋"},{"sura":19,"aya":7,"text":"甾克另亚啊！我以一个童子给你报喜信，他的名字是叶哈亚；从前我未造化过一个和他同名的。","translator":"王静斋"},{"sura":19,"aya":8,"text":"他说：（我的养主啊！我的妻是已经断产的，我已老迈达到十分衰弱，而怎能得一童子呢？）","translator":"王静斋"},{"sura":19,"aya":9,"text":"主说：就是这样）你的养主说：（这是在我上容易的。从前我造化你，你原不是以物。）","translator":"王静斋"},{"sura":19,"aya":10,"text":"他说：我的养主啊！你给我指定一个预兆！）主说：你的预兆是：你健康着三夜不能对人讲话）","translator":"王静斋"},{"sura":19,"aya":11,"text":"而后，他由内殿出至自己的族人，他指示他们：要朝夕颂主清净。","translator":"王静斋"},{"sura":19,"aya":12,"text":"（说）（叶哈亚！你尽力遵守经典吧！）在幼童时代，我赐给他智慧，","translator":"王静斋"},{"sura":19,"aya":13,"text":"和出我卸前的慈爱，与纯洁。他本是一个敬慎的，","translator":"王静斋"},{"sura":19,"aya":14,"text":"孝他父母的，他不是一个玩梗违拗的。","translator":"王静斋"},{"sura":19,"aya":15,"text":"当他降生的那一日，当他死去的那一日，当他活着复起的那一日，平安是在他上！","translator":"王静斋"},{"sura":19,"aya":16,"text":"你当记念上的马尔焉！那时侯她离开当地的人到东方。","translator":"王静斋"},{"sura":19,"aya":17,"text":"她在他们前面立一帐幔。我向她谴去我的{鲁哈}他就化形一个健全的人现给她了。","translator":"王静斋"},{"sura":19,"aya":18,"text":"她说：（我对你祈求慈主护佑。如果你是一个敬畏的——。）","translator":"王静斋"},{"sura":19,"aya":19,"text":"他说：（我只是你养主的一个使者，我为的是把一个纯洁的童子赏给你。）","translator":"王静斋"},{"sura":19,"aya":20,"text":"她说：曾无一个人接近我，我也不是失贞的，我怎能获一个童子呢？）","translator":"王静斋"},{"sura":19,"aya":21,"text":"他说：（就是这样。你的养主说：这是在我上容易的，我以此作为地世人的表征，并出自我的慈惠。那是已竞判定的事情。）","translator":"王静斋"},{"sura":19,"aya":22,"text":"于是她就妊他，怀他去到远处。","translator":"王静斋"},{"sura":19,"aya":23,"text":"产生的痛苦迫她来到枣树身下。","translator":"王静斋"},{"sura":19,"aya":24,"text":"她说：（哎！但愿我先前死掉了；我是一个无人注意的忘怀的。）他由下面呼喊她：（不要忧虑！你的养主确在你的下面发现一道河。","translator":"王静斋"},{"sura":19,"aya":25,"text":"你往怀里摇撼枣树身！那树便往你落下可摘的鲜枣。","translator":"王静斋"},{"sura":19,"aya":26,"text":"任你吃饮！人你愉快！如果你看见任何一个人了，你就说：我已为慈主许下斋戒了。今日我决不和任何一个人交谈。）","translator":"王静斋"},{"sura":19,"aya":27,"text":"她就怀抱着他来到自己的族人；他们呼马尔焉说：[你确发现丑事了哈仑的姊（妹）啊！","translator":"王静斋"},{"sura":19,"aya":28,"text":"他父不是歹人，你母也不是失贞的。]","translator":"王静斋"},{"sura":19,"aya":29,"text":"她就手指那个乳尔。他们说：（我们怎可和这摇篮里的小尔接谈呢？）","translator":"王静斋"},{"sura":19,"aya":30,"text":"他说：（我实是安拉的一个仆人，他赐给我经典，委我为圣，","translator":"王静斋"},{"sura":19,"aya":31,"text":"我住在不拘哪里，他就教我有福气。我活一天，他就嘱告我做礼拜，散天课；","translator":"王静斋"},{"sura":19,"aya":32,"text":"并嘱我孝敬我母亲。他未教我做一个玩梗违拗的。","translator":"王静斋"},{"sura":19,"aya":33,"text":"在我降生的那一日，在我死去的那一日，在我死去的那一日在我活着复起的那一日，平安是在我上！）","translator":"王静斋"},{"sura":19,"aya":34,"text":"这是马尔焉的子尔撒——他们所对他怀疑的那句真言。","translator":"王静斋"},{"sura":19,"aya":35,"text":"安拉无须立子。真主清净！他判定一事的时候，只对它说（有）它立刻就有。","translator":"王静斋"},{"sura":19,"aya":36,"text":"安拉确是我的养主，也是你们的养主。你们应当拜他。这是正道。","translator":"王静斋"},{"sura":19,"aya":37,"text":"而后，几个派别彼此间发生争议了。可叹不新的人们亲临大日！","translator":"王静斋"},{"sura":19,"aya":38,"text":"在他们道我卸前的那遗体，他们听的看的多么清楚啊！虽然，今日之下，背义的人们是在显著的迷误中——","translator":"王静斋"},{"sura":19,"aya":39,"text":"你对他们警告懊悔的日子，就是事情告终的时候——他们正在昏聩不信着。","translator":"王静斋"},{"sura":19,"aya":40,"text":"我必收留地，以及地上所有的人，他们惟被归于我。","translator":"王静斋"},{"sura":19,"aya":41,"text":"你记念这经上所记的易卜拉欣！他实是一个诚恳的圣人。","translator":"王静斋"},{"sura":19,"aya":42,"text":"那时侯他对他父亲说：（父啊！你为什么拜那听不见，看不见，也不能代你抵御一事的呢？","translator":"王静斋"},{"sura":19,"aya":43,"text":"父啊！未曾达至你的知识确已达至我了，你要追从我！我就把你领到正路上。","translator":"王静斋"},{"sura":19,"aya":44,"text":"父啊！你不要拜恶魔。恶魔是违犯慈主的。","translator":"王静斋"},{"sura":19,"aya":45,"text":"父啊！我实害怕你遭慈主的罪刑，而与恶魔伴同）","translator":"王静斋"},{"sura":19,"aya":46,"text":"他说：（易卜拉欣哪！你背叛我的众主么？如果你不停止了，我必用石击毙你。你常时间的离开我吧！","translator":"王静斋"},{"sura":19,"aya":47,"text":"易卜拉欣说：平安是在你上！我即将为你求我的养主饶恕；他实是很慈爱我的。","translator":"王静斋"},{"sura":19,"aya":48,"text":"我远避你们和你们撇开安拉所拜的。我拜我的养主；或许我不至因求养主而归为失望的。）","translator":"王静斋"},{"sura":19,"aya":49,"text":"及他远避他们和他们撇开安拉所拜的时候，我就把易司哈格和叶尔孤白赏给他了，我委每一个为圣人。","translator":"王静斋"},{"sura":19,"aya":50,"text":"我教着自己的慈惠恩赏他们。我为他们造成高尚的美誉。","translator":"王静斋"},{"sura":19,"aya":51,"text":"你记念这经上所记的母撒！他实是一个清白的钦差圣人。","translator":"王静斋"},{"sura":19,"aya":52,"text":"我从山的右面晓谕他；我教他来到卸前诉说机密。","translator":"王静斋"},{"sura":19,"aya":53,"text":"我就着自己的慈惠把他的弟兄——哈仑圣人赏给他了。","translator":"王静斋"},{"sura":19,"aya":54,"text":"你记念这经上所记的易司马义！他却是一个实践约会的钦差圣人。","translator":"王静斋"},{"sura":19,"aya":55,"text":"他命令自己的家眷做礼拜，散天课，他是在养主卸前可爱的。","translator":"王静斋"},{"sura":19,"aya":56,"text":"你记念这经上所记念的一得力司！他却是一个诚实的圣人。","translator":"王静斋"},{"sura":19,"aya":57,"text":"我教他升上高位。","translator":"王静斋"},{"sura":19,"aya":58,"text":"这些人是安拉对他们施恩的圣人；属于阿丹的子孙，属于与努海同舟的，并属于易卜拉欣，与易司拉以来的子孙；并属于我所领导，我所选择的人。每逢把慈主的表征读给他们了。他们就伏地叩头啼哭。","translator":"王静斋"},{"sura":19,"aya":59,"text":"不良的子孙在他们以后继替了；他们放弃拜功，追从欲望。不久，他们就遇见恶报。","translator":"王静斋"},{"sura":19,"aya":60,"text":"惟有悔罪归信并做善举的人，将进入天园，不蒙一些亏损。","translator":"王静斋"},{"sura":19,"aya":61,"text":"常住的天园，慈主已许给自己的众仆，他们尚未亲见着，他的约是一定的要来的。","translator":"王静斋"},{"sura":19,"aya":62,"text":"他们在哪里听不到无益的话，只有（平安！）他们在哪里朝夕得到自己的给养。","translator":"王静斋"},{"sura":19,"aya":63,"text":"那天园，我教自己众仆中敬慎的人承受它。","translator":"王静斋"},{"sura":19,"aya":64,"text":"我们下临，必得奉有养主的命令。惟他执掌我们前面后面的，和那中间的。你的养主不是忘事的。","translator":"王静斋"},{"sura":19,"aya":65,"text":"（他）是调养天地万有的。你当拜他，你当安心拜他。你知道有一个和他同名的吗？","translator":"王静斋"},{"sura":19,"aya":66,"text":"有的，人说：（我死了的时候，不久就当活着出观吗?）","translator":"王静斋"},{"sura":19,"aya":67,"text":"人类岂不记得：（从前我造化他，他原不是个什么。）","translator":"王静斋"},{"sura":19,"aya":68,"text":"指着你的养主为誓：我必集起他们连同众恶魔，然后我教他们屈膝着出现在火狱的四周。","translator":"王静斋"},{"sura":19,"aya":69,"text":"然后，我自每伙人里提出那般违抗慈主最烈的人。","translator":"王静斋"},{"sura":19,"aya":70,"text":"我且至知一般最应进火狱的人。","translator":"王静斋"},{"sura":19,"aya":71,"text":"你们无不达至它。这是经你养主决定的判断。","translator":"王静斋"},{"sura":19,"aya":72,"text":"而后我解救一般敬慎的人。我把背义的人屈膝着弃在火狱里。）","translator":"王静斋"},{"sura":19,"aya":73,"text":"有人向池们诵我显明的表征时候，逆徒们就指着众归信的人。说：（两伙人的哪一伙是地位至优的，是会场最佳的。）","translator":"王静斋"},{"sura":19,"aya":74,"text":"我在他们以前灭绝了若干代，他们是在饰品和外观上较好的。","translator":"王静斋"},{"sura":19,"aya":75,"text":"你说：（处在迷误的人，慈主助长他，切实地助长；直到他们亲见所约的时候，或是刑罚，或是复生日。）他们即将知道谁是地位至恶的，朋党最弱的。","translator":"王静斋"},{"sura":19,"aya":76,"text":"安拉增加引导一般得道的人。悠长的善举在你的养主御前，以论善赏是至好的，以论归宿是至优的。","translator":"王静斋"},{"sura":19,"aya":77,"text":"你曾看见那不信我的表征，自称（必定蒙赐资财子女）的人吗?","translator":"王静斋"},{"sura":19,"aya":78,"text":"他看到目不能见的吗? 不然，还是他在慈主御前取得约了呢?","translator":"王静斋"},{"sura":19,"aya":79,"text":"不是的，我即将汜录他所说的；我为他延长刑罚，切实地延长。","translator":"王静斋"},{"sura":19,"aya":80,"text":"我且给他收回他所说的。他将只身来至我。","translator":"王静斋"},{"sura":19,"aya":81,"text":"他们撇开安拉取一切受拜的，俾作于自己有利的荣耀。","translator":"王静斋"},{"sura":19,"aya":82,"text":"不是的，他们即将否认自己所拜的。那般受拜的是反抗他们的。","translator":"王静斋"},{"sura":19,"aya":83,"text":"你岂未见我把众恶魔驱至逆徒，它们鼓动他们，切实地鼓动吗?","translator":"王静斋"},{"sura":19,"aya":84,"text":"你对他们不要急切。我只是为他们统计，切实地统计。","translator":"王静斋"},{"sura":19,"aya":85,"text":"当那一日，我教一般敬畏的人乘骑着集至慈主；","translator":"王静斋"},{"sura":19,"aya":86,"text":"我把一般为恶的人干渴着驱至火狱。他们不能代求赦免，只有那在慈主御前取了约的人。","translator":"王静斋"},{"sura":19,"aya":87,"text":"他们说：（慈土立子了。）","translator":"王静斋"},{"sura":19,"aya":88,"text":"你们确发现丑事了。","translator":"王静斋"},{"sura":19,"aya":89,"text":"各天几乎因着这话分裂，","translator":"王静斋"},{"sura":19,"aya":90,"text":"地面崩开，群山倒塌，","translator":"王静斋"},{"sura":19,"aya":91,"text":"所因他们给慈主安排儿子。","translator":"王静斋"},{"sura":19,"aya":92,"text":"慈主本不是应该立子的。","translator":"王静斋"},{"sura":19,"aya":93,"text":"天地间所有的一切，只是来给慈主为仆的。","translator":"王静斋"},{"sura":19,"aya":94,"text":"他计数他们，切实地计数。","translator":"王静斋"},{"sura":19,"aya":95,"text":"他们的每一个在复生日，只身来至他。","translator":"王静斋"},{"sura":19,"aya":96,"text":"众归信并作善举的人，慈主即将为他们发观爱慕。","translator":"王静斋"},{"sura":19,"aya":97,"text":"我用你的乡音使它成为容易的，好教你用它给敬慎的人们报喜信，并用它警告一般逆民。","translator":"王静斋"},{"sura":19,"aya":98,"text":"我在他们以前灭绝了若干代。你看见他们内中的一个，抑或你听到他们的低语吗?","translator":"王静斋"},{"sura":20,"aya":1,"text":"（搭哈。）","translator":"王静斋"},{"sura":20,"aya":2,"text":"我降给你古兰，不是为你受痛苦；","translator":"王静斋"},{"sura":20,"aya":3,"text":"而是为你劝化畏惧的人。","translator":"王静斋"},{"sura":20,"aya":4,"text":"降自造地和高天的 主。","translator":"王静斋"},{"sura":20,"aya":5,"text":"这慈主操纵（阿勒世。）","translator":"王静斋"},{"sura":20,"aya":6,"text":"在天的，在地的，和它两个中间的，和地下的，惟他执掌。","translator":"王静斋"},{"sura":20,"aya":7,"text":"如果你高声说话了，他确知道秘密和至秘密。","translator":"王静斋"},{"sura":20,"aya":8,"text":"安拉，除他以外，再无有一受拜的。惟他具有至优的名称。","translator":"王静斋"},{"sura":20,"aya":9,"text":"母撒的消息，未曾达至你吗？","translator":"王静斋"},{"sura":20,"aya":10,"text":"他看见火的时候，就对自己的眷属说：（要你们迟侯，我确望见火了。或许我自那里拿给你们火把，或者我在火上得到引导的。）","translator":"王静斋"},{"sura":20,"aya":11,"text":"当他来到火跟前的时候，有的晓谕他：（母撒呀，","translator":"王静斋"},{"sura":20,"aya":12,"text":"我实是你的养主，你脱下自己的两鞋来！你确是主圣谷图哇。","translator":"王静斋"},{"sura":20,"aya":13,"text":"我选择你，你听启示吧！","translator":"王静斋"},{"sura":20,"aya":14,"text":"除我以外再无有主，你当拜我，你当为着记念我立拜功。","translator":"王静斋"},{"sura":20,"aya":15,"text":"的确，复生日是该到的。我殆密而不宣，——为的是对每个人所努力的施以报偿。","translator":"王静斋"},{"sura":20,"aya":16,"text":"你不要受那不信 它并随从自己私见的人阻遏，而致自陷毁灭。","translator":"王静斋"},{"sura":20,"aya":17,"text":"母撒呀：你右手的是什么?）","translator":"王静斋"},{"sura":20,"aya":18,"text":"他说：（它是我的手杖，我拄它，并用它给我的羊敲树叶，我借着它尚有别的需求。 ）","translator":"王静斋"},{"sura":20,"aya":19,"text":"主说：（母撒呀！你掷出木杖去！）","translator":"王静斋"},{"sura":20,"aya":20,"text":"及其掷出，那木杖忽然地就是一条活跃的巨蛇。","translator":"王静斋"},{"sura":20,"aya":21,"text":"主说：（你握住它，不要骇怕；我将教它恢复原状，","translator":"王静斋"},{"sura":20,"aya":22,"text":"你把自己的手夹在腋下！它就洁白无瑕地另一奇迹着发现；","translator":"王静斋"},{"sura":20,"aya":23,"text":"为的是把我的一大部分奇迹现给你。","translator":"王静斋"},{"sura":20,"aya":24,"text":"你往匪喇傲那里去吧！他实在过分了。）","translator":"王静斋"},{"sura":20,"aya":25,"text":"母撒说：我的养主啊！你为我展开我的胸：","translator":"王静斋"},{"sura":20,"aya":26,"text":"你教我容易作自己的事业。","translator":"王静斋"},{"sura":20,"aya":27,"text":"你解开我的舌结，","translator":"王静斋"},{"sura":20,"aya":28,"text":"以便他们解悟我的说话；","translator":"王静斋"},{"sura":20,"aya":29,"text":"你由我的眷属中指定我兄——","translator":"王静斋"},{"sura":20,"aya":30,"text":"哈伦给我作辅佐；","translator":"王静斋"},{"sura":20,"aya":31,"text":"你用他加强我的力量，","translator":"王静斋"},{"sura":20,"aya":32,"text":"你教他对我的事业上相共，","translator":"王静斋"},{"sura":20,"aya":33,"text":"好教我们多赞你。","translator":"王静斋"},{"sura":20,"aya":34,"text":"我们多记念你；","translator":"王静斋"},{"sura":20,"aya":35,"text":"你却是看见我们的。","translator":"王静斋"},{"sura":20,"aya":36,"text":"主说：（母撒呀！你却蒙赐给自所祈求的了。）","translator":"王静斋"},{"sura":20,"aya":37,"text":"我另一次对你施恩了。","translator":"王静斋"},{"sura":20,"aya":38,"text":"那时候，我向你母感悟所感悟的：","translator":"王静斋"},{"sura":20,"aya":39,"text":"（你把他投在柜里，而后把柜投在大河，再任大河把他投在海边，而致我的仇敌，亦是他的仇敌拾了他去，）我把出自我的喜爱投在你上，并使你在我的当面得到抚育。","translator":"王静斋"},{"sura":20,"aya":40,"text":"那时候，你姊走去，说：（我向你们指出一个看护这小儿的，可以吗?） 他就把你归给你母亲；好教他愉快无忧。你曾杀死一个人，而后我救你脱离忧愁。","translator":"王静斋"},{"sura":20,"aya":41,"text":"我磨难你，切实地磨难。你在买大严人那里住了几年，而后你依前定到采， 母撒呀—我为自己选择你了。","translator":"王静斋"},{"sura":20,"aya":42,"text":"你和你弟兄持我的奇迹前去！","translator":"王静斋"},{"sura":20,"aya":43,"text":"你俩于记念我上不要松懈。你俩去到匪喇傲！他实在过分了。","translator":"王静斋"},{"sura":20,"aya":44,"text":"你俩对他说温语！或许他能受劝或惧怕。","translator":"王静斋"},{"sura":20,"aya":45,"text":"他俩说：（我们的养主啊！我们实骇怕他立时惩治我们！或他过分。）","translator":"王静斋"},{"sura":20,"aya":46,"text":"主说：你俩不要骇怕，我实是和你俩同在的，我听得见，看得见。","translator":"王静斋"},{"sura":20,"aya":47,"text":"你俩到他那里去，就说：（我们实是养主的两个使者，要你释放以斯拉衣来的子孙随我我们同去，你不要惩治他们。我确把养主的表征现给你了。平安是在追随引导的人上！","translator":"王静斋"},{"sura":20,"aya":48,"text":"我们确已得到默谕了！不信而且拒绝的人应遭罪刑。）","translator":"王静斋"},{"sura":20,"aya":49,"text":"匪喇傲说：（母撒呀！你俩的养主是谁？）","translator":"王静斋"},{"sura":20,"aya":50,"text":"他说：（我们的养主是那赐给各物形态且加指导的。）","translator":"王静斋"},{"sura":20,"aya":51,"text":"匪喇傲说：（前代的人是什么情形?）","translator":"王静斋"},{"sura":20,"aya":52,"text":"他说：（知它是在我的养主，是记在舆章的。我的养主不昏不忘。","translator":"王静斋"},{"sura":20,"aya":53,"text":"那真主为你们置地为铺垫，为你们在那里畅通各道路，并上空降水，而后用它发长各种不同的植物。）","translator":"王静斋"},{"sura":20,"aya":54,"text":"你们吃吧！你们牧放自己的牲畜吧!此中对于一般有才识的人确、食有种种表征。","translator":"王静斋"},{"sura":20,"aya":55,"text":"我从地上造化你们，将把你们归到那里，再次从那里发现你们。","translator":"王静斋"},{"sura":20,"aya":56,"text":"我确把自己的异迹统现给他了；但是他不信，他拒绝了。","translator":"王静斋"},{"sura":20,"aya":57,"text":"匪喇傲说：（母撒呀！你来为的是用你的邪术把我们逐出、本地吗?","translator":"王静斋"},{"sura":20,"aya":58,"text":"我们必定对你发观和那个一样的邪术。要你于彼此间指定一个互相遵守的约， 一个适中的地点。）","translator":"王静斋"},{"sura":20,"aya":59,"text":"他说：（你们的约期是大会的那一天，在清晨集结众人。","translator":"王静斋"},{"sura":20,"aya":60,"text":"（匪喇做回去集合军兵，复又转来。","translator":"王静斋"},{"sura":20,"aya":61,"text":"母撒对他们说： （你们应当遭祸了！你们不该往安拉上造作谎言，致被刑罚所伤，造谎的人确已失望了。）","translator":"王静斋"},{"sura":20,"aya":62,"text":"而后他们对于自己的事情彼此间万一起争论了。 他们暗诉密语，","translator":"王静斋"},{"sura":20,"aya":63,"text":"说： （这两个 一定是两个术士；他俩想把你们用邪术逐出、本地，并去掉你们高尚的行径。","translator":"王静斋"},{"sura":20,"aya":64,"text":"你们要集起自己所计划的。你们列队前来！今日获胜的人一定适意了。）","translator":"王静斋"},{"sura":20,"aya":65,"text":"他们说： （母撒呀！你掷出；或我们首先掷出！）","translator":"王静斋"},{"sura":20,"aya":66,"text":"他说：（不，你们掷出吧！）忽然他们的绳杖借着邪术使母撒意其为活跃的。","translator":"王静斋"},{"sura":20,"aya":67,"text":"于是母撒心中怀着一种畏惧。","translator":"王静斋"},{"sura":20,"aya":68,"text":"我说：（你不要骇怕！你一定获胜利。","translator":"王静斋"},{"sura":20,"aya":69,"text":"你掷出右手所持的！它就吞没他们所造作的。他们所造作的是术士的诡计。术士子他所来的地方不能得意。）","translator":"王静斋"},{"sura":20,"aya":70,"text":"于是众术士伏身下拜，说：（我们归信哈伦和母撒的养主了。）","translator":"王静斋"},{"sura":20,"aya":71,"text":"他说：（我尚未准许你们，你们就归信他了吗?他必是教给你们邪术的一个伟人；我必相异着断去你们的手脚。我必把你们挂在枣树身上。你们将必知道我们的哪一个是刑罚至烈的，是至长久的。）","translator":"王静斋"},{"sura":20,"aya":72,"text":"他们说： （我们绝不离开自所得到的明证，和那造化我们的主而选择你。 你判决自欲判决的吧！你仅可在这今生判决。","translator":"王静斋"},{"sura":20,"aya":73,"text":"我们己然归信养主了，为的是求他饶恕我们的罪，并饶恕你逼迫我们（施行）的邪术。 安拉是至优上至长久的。 ）","translator":"王静斋"},{"sura":20,"aya":74,"text":"的确，凡为恶着来至养主的人， 一定遭受火狱；在那里不死不活。","translator":"王静斋"},{"sura":20,"aya":75,"text":"凡归信并作善举着来至他的人，均可获到一切高品——","translator":"王静斋"},{"sura":20,"aya":76,"text":"永居在诸河流于其下长久的天园中。这是报偿清洁的人。","translator":"王静斋"},{"sura":20,"aya":77,"text":"我曾告谕母撒：（你偕我的众仆夜行，为他们在海里打出一条干路！不要骇怕追赶。不要畏惧。）","translator":"王静斋"},{"sura":20,"aya":78,"text":"而后，匪喇傲带兵追赶他们。那从海上达到的，遮覆他们了。","translator":"王静斋"},{"sura":20,"aya":79,"text":"匪喇敏诱他的民众迷误了，位未曾施行引导。","translator":"王静斋"},{"sura":20,"aya":80,"text":"以斯拉衣来的子孙哪！我曾教你们脱开仇敌；我在山的右面与你们结约；我降给你们下甘露蜜乙和呷鹑鸟。（主吩咐：）","translator":"王静斋"},{"sura":20,"aya":81,"text":"你们就着我所赐给的一部分美品吃吧！你们不得过分，而致遭受我的怒恨。凡是遭我怒恨的必定堕落下去。","translator":"王静斋"},{"sura":20,"aya":82,"text":"必饶恕那悔罪，归信并作善举，而且谨守正道的人。母撒呀！","translator":"王静斋"},{"sura":20,"aya":83,"text":"你何至急切不顾族人啊，","translator":"王静斋"},{"sura":20,"aya":84,"text":"他说：（他们这般人是在我的后面，我匆忙来至你，我的养主啊！为的是你爱悦。）","translator":"王静斋"},{"sura":20,"aya":85,"text":"主说：（在你以后，我确曾磨难你的族人；一个沙迷赖人诱他们迷误了。","translator":"王静斋"},{"sura":20,"aya":86,"text":"母撒在怒恨忧愁着回到他的族人，说：（我的族人哪，养主未对你们结下好的约会吗？时间长久了吗?不然，你们欲遭养主的怒恨，违反我的约了吗?）","translator":"王静斋"},{"sura":20,"aya":87,"text":"他们说：（我们未曾自动地违犯你的约。只是我们已被责成对于族人的饰品担负重责了。我们投下它去；迷沙赖人也这样投入了。","translator":"王静斋"},{"sura":20,"aya":88,"text":"他就为他们观出一头牛犊—— 一个发声的躯体。他们说：这是你们的主，也是母撒的主；但是他忘记了。）","translator":"王静斋"},{"sura":20,"aya":89,"text":"他们岂未见：它不能回他们一句话，亦不为他们掌利害吗?","translator":"王静斋"},{"sura":20,"aya":90,"text":"先是哈伦对他们说：（我的族人哪，你们只是被他磨难了。你们的养主确是来哈曼。你们应当追随我，当服从我的命令。）","translator":"王静斋"},{"sura":20,"aya":91,"text":"他们说：（我们必长时守此牛犊，直到母撒转回为止。）","translator":"王静斋"},{"sura":20,"aya":92,"text":"母撒说：（哈伦哪！你看见他们迷误的时候，你被什么所阻，","translator":"王静斋"},{"sura":20,"aya":93,"text":"竟不追随我呢?你违犯我的命令吗?）","translator":"王静斋"},{"sura":20,"aya":94,"text":"哈伦说： [母亲的儿子啊！你不要捋我的须和我的头（发。）我实骇怕你说： 你在以斯拉衣来的子孙中割裂了；你未遂行我的话。]","translator":"王静斋"},{"sura":20,"aya":95,"text":"母撒说：（沙迷赖人哪！你是怎么一回事情?）","translator":"王静斋"},{"sura":20,"aya":96,"text":"他说：（我看见他们看不见的。我就着使者的迹印撮取了一握。我把它投进去我的心灵如此为我装饰了。","translator":"王静斋"},{"sura":20,"aya":97,"text":"（母撒说：（你去吧！你生平应说：（不要挨近）在你尚负着决不可失掉的约。你看自所守定的那受拜的；我们必焚毁它，我们再把它扬撒在深海中，切实地扬撒。","translator":"王静斋"},{"sura":20,"aya":98,"text":"你们的受拜的只是那安拉，除他以外再无一受拜的。他在知识上包容万物。","translator":"王静斋"},{"sura":20,"aya":99,"text":"我就这样地把以前的一部分消息述说给你。我确由自己御前赐给你教戒了。","translator":"王静斋"},{"sura":20,"aya":100,"text":"远避它的人，在复生日必定永负重担。","translator":"王静斋"},{"sura":20,"aya":101,"text":"在复生日，就是吹起号筒那一日，他们所担负的太恶了，","translator":"王静斋"},{"sura":20,"aya":102,"text":"当那一日，我集起一般为恶的人眼蓝着。","translator":"王静斋"},{"sura":20,"aya":103,"text":"他们彼此低声地说：[你们只停留十（昼夜）]","translator":"王静斋"},{"sura":20,"aya":104,"text":"我深知他们所说的。那时候，他们在理解上最充足的人说：（你们只停留一天。）","translator":"王静斋"},{"sura":20,"aya":105,"text":"他们对于山岳上问你，你说！（我的养主将扬撒它，切实地扬撒它。","translator":"王静斋"},{"sura":20,"aya":106,"text":"而后弃它为平坦的空场。）","translator":"王静斋"},{"sura":20,"aya":107,"text":"在那里看不出一个弯曲和高岗来。","translator":"王静斋"},{"sura":20,"aya":108,"text":"是日，他们一直的追随呼唤者。群声圈为慈主寂然。仅闻得低声。","translator":"王静斋"},{"sura":20,"aya":109,"text":"当那一日，告赦只利于得到慈主许可，并欲他发言的人。","translator":"王静斋"},{"sura":20,"aya":110,"text":"他知道在他们前面后面的。他们并不完全知道它。","translator":"王静斋"},{"sura":20,"aya":111,"text":"一般面貌已向永生掌理的主表现卑贱。负担背义的人确已失望了，","translator":"王静斋"},{"sura":20,"aya":112,"text":"作善功的穆民不怕亏损和减少。","translator":"王静斋"},{"sura":20,"aya":113,"text":"我就是这样地降这阿拉伯文的古兰；我在它里重复惊吓，好教他们敬畏，或对他们启发醒觉。","translator":"王静斋"},{"sura":20,"aya":114,"text":"安拉——真实的掌理者玄高了！在向你完结示谕以前，你不要忙诵古兰。你说，我的养主啊！求你增给我知识！","translator":"王静斋"},{"sura":20,"aya":115,"text":"从前我曾默示阿丹，而后他忘记了。我未见他有坚忍。","translator":"王静斋"},{"sura":20,"aya":116,"text":"昔时，我对众天使说：（你们向啊丹下拜！）他们遂就下拜，只有以卜里厮不肯。","translator":"王静斋"},{"sura":20,"aya":117,"text":"我说：阿丹哪！这是你的，也是你妻的仇敌；不要被他把你们俩逐出天园而致自遭痛苦。","translator":"王静斋"},{"sura":20,"aya":118,"text":"你在天园里不致饥饿和赤体；","translator":"王静斋"},{"sura":20,"aya":119,"text":"你在天园里不致干渴，也不受晒。","translator":"王静斋"},{"sura":20,"aya":120,"text":"而后恶魔暗唆他，说：（阿丹哪！我把你领到久远的树，和不朽的国里，可以吗？）","translator":"王静斋"},{"sura":20,"aya":121,"text":"他俩遂就由那树上吃了。从此羞体外露，只得往身上粘树叶。 —河丹违犯他的养主了，他迷误了，","translator":"王静斋"},{"sura":20,"aya":122,"text":"后来他的养主选择他，允他的悔罪，并加以指导了。","translator":"王静斋"},{"sura":20,"aya":123,"text":"主说：（你俩一同从这里下去吧！你们彼此一个是一个的仇敌。若是我的指导达至你们了，凡随我指导的人，必不迷误，也不受苦。）","translator":"王静斋"},{"sura":20,"aya":124,"text":"凡远避我的教戒者，必遭困难的生活。在审判日，我使他盲目着复起。）","translator":"王静斋"},{"sura":20,"aya":125,"text":"他说：（我的养主啊！你为什么把我盲目着复起，我原是有眼的。）","translator":"王静斋"},{"sura":20,"aya":126,"text":"主说：（就是这样。我的表征达至你，你忘记它了。今日，就如此着忘记你。","translator":"王静斋"},{"sura":20,"aya":127,"text":"我就是这样地还报妄为且不信他养主表征的人。后世的刑罚实是至烈的，最长久的。）","translator":"王静斋"},{"sura":20,"aya":128,"text":"我在他们以前灭绝了若干代，他们在那些人的居处行走，这事在他们尚不明白吗? 此中对于有才智的人确含有种种表征。","translator":"王静斋"},{"sura":20,"aya":129,"text":"设非自你养主以往的话和一定的期限了，那刑罚是必须粘附的。","translator":"王静斋"},{"sura":20,"aya":130,"text":"要你忍于他们所说的。你当在日出和日落之前以赞你的养主称颂清净。你在夜内一部分时候，并在白昼的边缘称颂清净，或者你能得到快乐。","translator":"王静斋"},{"sura":20,"aya":131,"text":"我使他们几伙人享受今世的华美，是为着用它磨难他们，你对于这个上不要注视！你养主的供给是最优至久的。","translator":"王静斋"},{"sura":20,"aya":132,"text":"你要命令自己的同人礼拜，你要为着礼拜忍耐，我不向你求给养，我本赐给你给养。敬慎的人可获善果。","translator":"王静斋"},{"sura":20,"aya":133,"text":"他们说：（养主的异迹怎么来不到我们这里呢?）他们未得到古籍中听表明的吗?","translator":"王静斋"},{"sura":20,"aya":134,"text":"设若我先以刑罚灭绝他们了，他们必说：（我们的养主啊！为什么你不向我们遣一个使者，而致我们在受辱与现丑以前，追从你的表征。）","translator":"王静斋"},{"sura":20,"aya":135,"text":"你说：（每一个人是等待的，你们等待吧！而后不久的你们就知道谁是正道上的人，谁得正道了。）","translator":"王静斋"},{"sura":21,"aya":1,"text":"众人正在昏愦中远避着，审算接近他们了。","translator":"王静斋"},{"sura":21,"aya":2,"text":"每至养主的一项新教戒达到的时候，他们就心地昏愦而嘲笑着听它。","translator":"王静斋"},{"sura":21,"aya":3,"text":"背义的人们秘密谈道；（这只是和你们一样的人。你们眼看着接受邪术吗?）","translator":"王静斋"},{"sura":21,"aya":4,"text":"他说：（我的养主知道天地间的言语。 他是能听的，深知的。）","translator":"王静斋"},{"sura":21,"aya":5,"text":"不然，他们就说：（是杂梦；）不然， (就说：)他造作它了；不然，（就说：）他是个诗人；当叫他像前人为使者似的，对我们举出异迹来。","translator":"王静斋"},{"sura":21,"aya":6,"text":"在他们以前所有未归信的城池，我曾灭绝它了， 他们能肯归信吗?","translator":"王静斋"},{"sura":21,"aya":7,"text":"我在你以前所差的全是一般受过我默谕的（人）。如你们不知道了，你们就问有经人。","translator":"王静斋"},{"sura":21,"aya":8,"text":"我未教他们作不吃食物的躯体，他们也不是永居的。","translator":"王静斋"},{"sura":21,"aya":9,"text":"而后我对他们实行所约。我解救他们和我意欲（解救）的人；我灭绝一般妄为的人。","translator":"王静斋"},{"sura":21,"aya":10,"text":"我确给你们降下含有教戒的经典，你们怎不了解呢?","translator":"王静斋"},{"sura":21,"aya":11,"text":"我摧毁了许多背义的城池，在它以后我重新造化另一般民众。","translator":"王静斋"},{"sura":21,"aya":12,"text":"他们望见我的刑罚时候，立即逃遁。","translator":"王静斋"},{"sura":21,"aya":13,"text":"你们不要逃遁；你们回到所享受的，和自己的住宅！以便供人询问。","translator":"王静斋"},{"sura":21,"aya":14,"text":"他们说：（唉！我们实是背义的人了。）","translator":"王静斋"},{"sura":21,"aya":15,"text":"那是他们长时的呼喊，至使他们成为被割而消灭的。","translator":"王静斋"},{"sura":21,"aya":16,"text":"我非戏而造化天地万有。","translator":"王静斋"},{"sura":21,"aya":17,"text":"设若我欲作游戏了，我若实行的时候，我必就着自己御前的取游戏。","translator":"王静斋"},{"sura":21,"aya":18,"text":"不然，我以真的射到假的上，就损毁它，那假的立刻消灭了。你们因自所称述的遭祸了。","translator":"王静斋"},{"sura":21,"aya":19,"text":"天地间所有的惟他执掌。在他御前的那一切，对于拜主上不高傲，也不疲乏。","translator":"王静斋"},{"sura":21,"aya":20,"text":"他们夜昼称颂清净，并不松懈。","translator":"王静斋"},{"sura":21,"aya":21,"text":"不然，他们从地上取些受拜的，它们能使复生吗?","translator":"王静斋"},{"sura":21,"aya":22,"text":"设若天地间有除过安拉一切受拜的，一定天地就归毁灭了，掌（阿勒世）的安拉清净，不染他们所称述的！","translator":"王静斋"},{"sura":21,"aya":23,"text":"真主所行的不受质问，他们均受质问，不然，他们撇开他另取一切受拜的吗?你说：你们羊当自己的明证来。这是教戒随同我的人，并教戒我以前的人。","translator":"王静斋"},{"sura":21,"aya":24,"text":"不然，他们的多半不知道真理，所以他们远避。","translator":"王静斋"},{"sura":21,"aya":25,"text":"我在你以前所遣的使者，均由我告谕他：（除我以外再无有主，你们当要拜我。）","translator":"王静斋"},{"sura":21,"aya":26,"text":"他们说：（慈主立子了；真主清净！不然，（他们）是一般高尚的奴仆。","translator":"王静斋"},{"sura":21,"aya":27,"text":"他们不先他发言；他们惟奉他的命令行事。","translator":"王静斋"},{"sura":21,"aya":28,"text":"他知道他们前面后面的，他们仅为他爱喜的人祈求赦免；他们是因骇怕他战栗的。","translator":"王静斋"},{"sura":21,"aya":29,"text":"他们内中自称：（我是他以外受拜的。）这人我将报他以火狱，我就这样地还报一般背义的人。","translator":"王静斋"},{"sura":21,"aya":30,"text":"逆徒们岂末看见天地原是固结的，而后我使其分离吗? 我由水上造化一切活物，他们怎不归信呢?","translator":"王静斋"},{"sura":21,"aya":31,"text":"我在地上发现定山，以免它摇动他们。我在那里发现些个宽阔的道路，以便他们得正道。","translator":"王静斋"},{"sura":21,"aya":32,"text":"我把天造成被庇的屋顶，他们不注意他的和和，表征。","translator":"王静斋"},{"sura":21,"aya":33,"text":"他是造化夜昼，和日月的主。统浮游在天体上。","translator":"王静斋"},{"sura":21,"aya":34,"text":"我未赐给你以前的人永久。若是你死了，他们能可永久吗?","translator":"王静斋"},{"sura":21,"aya":35,"text":"每一个有性命的是该尝死亡的，我因为磨难而用祸福试证你们，你们将被归于我。","translator":"王静斋"},{"sura":21,"aya":36,"text":"不信的人看见你的时候，惟有拿你当作可戏的。（这是那提到你们偶像的人吗?）他们本不承认慈主的教戒。","translator":"王静斋"},{"sura":21,"aya":37,"text":"人类原造自忙迫上。我即将把自己的表征现给你们。你们不要向我求速。","translator":"王静斋"},{"sura":21,"aya":38,"text":"他们说：（这约是在何时? 如果你们是实言的了，……。）","translator":"王静斋"},{"sura":21,"aya":39,"text":"设若不信的人们知道不能给自己的面和脊背止火，也不复得到相助的时候，……。","translator":"王静斋"},{"sura":21,"aya":40,"text":"不然，复生日将骤然临到，至使他们惊愕，而不能抵抗它，也不复得到姑容。","translator":"王静斋"},{"sura":21,"aya":41,"text":"在你以前的列使确遭人嘲弄；而后那些嘲弄的人遭遇自所嘲弄的了。","translator":"王静斋"},{"sura":21,"aya":42,"text":"你说，谁是在夜昼抵抗养主保护你们的。不然，他们是远避养主教戒的。","translator":"王静斋"},{"sura":21,"aya":43,"text":"不然，有除过我的主为他们制止吗? 他们不能自助，也无有抗我保护他们的。","translator":"王静斋"},{"sura":21,"aya":44,"text":"不然，我教这些人及其祖先享受，直到悠长的年令。他们岂未看见我来至地，缩小它的边境，他们是获胜的吗?","translator":"王静斋"},{"sura":21,"aya":45,"text":"你说：（我奉谕警告你们；聋子遇人警告的时候，听不到呼喊。）","translator":"王静斋"},{"sura":21,"aya":46,"text":"他们若遭你养主些微的刑罚了，他们必就说：（哎！我们悲苦，我们实是自亏的人。）","translator":"王静斋"},{"sura":21,"aya":47,"text":"我在复生日设置天秤，无一人遭受些须亏损。纵令那所作的是芥菜粒重，我必把它拿到。我作清算的已可满足了。","translator":"王静斋"},{"sura":21,"aya":48,"text":"我确曾赐给母撒和哈伦（规范、）与（光辉，）与对于敬慎者的（教戒。）","translator":"王静斋"},{"sura":21,"aya":49,"text":"那般人尚在未见着畏惧养主。他们是对于复生日战栗的。","translator":"王静斋"},{"sura":21,"aya":50,"text":"这是我降的吉庆的教戒。你们不承认它吗?","translator":"王静斋"},{"sura":21,"aya":51,"text":"的确，我先赐给易卜拉欣正道了，我是知道他的。","translator":"王静斋"},{"sura":21,"aya":52,"text":"那时候，他对他父亲和族人们说：（你们谨守的这些偶像是什么?","translator":"王静斋"},{"sura":21,"aya":53,"text":"他们说：（我们曾见列祖敬拜它。）","translator":"王静斋"},{"sura":21,"aya":54,"text":"他说：（你们和你们的 列祖确是在显著的迷误中。）","translator":"王静斋"},{"sura":21,"aya":55,"text":"他们说：（你对我们发表真实的了?或者你是嬉戏的呢?）","translator":"王静斋"},{"sura":21,"aya":56,"text":"他说：不然，你们的养主是那造化天地的主。我是对于这话作见证的。","translator":"王静斋"},{"sura":21,"aya":57,"text":"指着安拉为誓：在你们退转以后，我必对你们的偶像施计谋。","translator":"王静斋"},{"sura":21,"aya":58,"text":"他遂就捣毁了偶像，只留下它们一个大的，冀使他们回转于它。","translator":"王静斋"},{"sura":21,"aya":59,"text":"他们说：（什么人对我们的主干下这事了?他实属亏己的人。）","translator":"王静斋"},{"sura":21,"aya":60,"text":"他们说：（我们听说有一个人称为易卜拉欣的青年，提说那些偶像了。）","translator":"王静斋"},{"sura":21,"aya":61,"text":"他们说：（你们在众人的当面把他拿来，好教大家作见证。","translator":"王静斋"},{"sura":21,"aya":62,"text":"他们说：（易卜拉欣哪！你对我们的主、干下这事了吗?）","translator":"王静斋"},{"sura":21,"aya":63,"text":"他说；（不，实是它们这个大的干下这事。若是它们能发言了，你们就询问它们：）","translator":"王静斋"},{"sura":21,"aya":64,"text":"他们回心思索，遂就说：（你们实是自亏的。）","translator":"王静斋"},{"sura":21,"aya":65,"text":"后来他们又转回头来，（说：）你确知道这一切不能发言。","translator":"王静斋"},{"sura":21,"aya":66,"text":"易卜拉欣说：（你们撇开安拉，拜那不利你们一事，也于你们无害的吗?","translator":"王静斋"},{"sura":21,"aya":67,"text":"噫嘻，你们及你们撇开安拉所拜的，你们怎不理解呢?）","translator":"王静斋"},{"sura":21,"aya":68,"text":"他们说：（你们烧死他，你们相助自己的主！如果你们肯作了，…。）","translator":"王静斋"},{"sura":21,"aya":69,"text":"我说：（火，要你是对易卜拉欣凉爽和平的。）","translator":"王静斋"},{"sura":21,"aya":70,"text":"他们欲对他施计谋，我使他们归为至伤折的。","translator":"王静斋"},{"sura":21,"aya":71,"text":"我解救他并（解救）鲁脱往赴我在那里为世人降福之地。","translator":"王静斋"},{"sura":21,"aya":72,"text":"我赐给他易司哈各和另外的叶而孤白。我教每一个作了义人，","translator":"王静斋"},{"sura":21,"aya":73,"text":"我教他们作领袖，奉我的命令引导人。默示他们行好事，立拜功，散天课。他们是惟独拜我的。","translator":"王静斋"},{"sura":21,"aya":74,"text":"又有鲁脱，我曾赐给他智慧和学识；我解救他脱离那行污浊的城池。他们实是一伙作恶的罪民。","translator":"王静斋"},{"sura":21,"aya":75,"text":"我教他入到我的慈惠中。他实是义人一类的。","translator":"王静斋"},{"sura":21,"aya":76,"text":"又有从前的努海；那时候，他呼吁，我遂就应允他了。我解救他和他的眷属脱离大难；","translator":"王静斋"},{"sura":21,"aya":77,"text":"我相助他以抗那不信我表征的民众。他们实是恶民，而后均被我淹毙了。","translator":"王静斋"},{"sura":21,"aya":78,"text":"又有达伍德与苏来茫；那时候，他俩为着夜间在田地里随便吃草的一伙人的羊施行裁判。我是见证他们底裁判的。","translator":"王静斋"},{"sura":21,"aya":79,"text":"我教苏采茫解悟它了。我赐给每一个人圣职和学识。我制服群山和飞鸟，随同达伍德称颂。我是实作的。","translator":"王静斋"},{"sura":21,"aya":80,"text":"我教给他为你们制铠甲，为的是于战争上保卫你们。你们感谢吗?","translator":"王静斋"},{"sura":21,"aya":81,"text":"更为苏来茫制服暴风，风依主的命令吹到蒙我降福之地，我本是知道万事的。","translator":"王静斋"},{"sura":21,"aya":82,"text":"恶魔中有的为他潜水，并做除此以外的工作。我本是监察他们的。","translator":"王静斋"},{"sura":21,"aya":83,"text":"又有安优伯；那时候，他呼吁他的养主：（我实遭受痛苦了，你是慈中最慈的。","translator":"王静斋"},{"sura":21,"aya":84,"text":"于是我就应承他了。我释去他染受的痛苦，我以自己的慈惠，并为提醒敬拜的人，把他的眷属加倍地赐给他了。","translator":"王静斋"},{"sura":21,"aya":85,"text":"又有易司马衣，一德理师，宰洛祈福礼，统属于忍耐的。","translator":"王静斋"},{"sura":21,"aya":86,"text":"我教他们入到我的慈惠中，他们实是义人一类的。","translator":"王静斋"},{"sura":21,"aya":87,"text":"又有赞农；那时候，他忿怒着出走，他以为我不能于（对付）他了。他在重重黑暗中呼吁：（赞你清净，除你以外再无一受拜的。我实是自亏的人了。","translator":"王静斋"},{"sura":21,"aya":88,"text":"于是我就应允了他，我释去他的忧愁，我就这样地解救众穆民。","translator":"王静斋"},{"sura":21,"aya":89,"text":"又有翟克赁亚；那时候，他呼吁他的养主：（我的养主啊！你不要把我孤单着弃掉，你是最优的承受者。","translator":"王静斋"},{"sura":21,"aya":90,"text":"（于是我就应承他了。我把叶哈亚给了他，并为他调整他的妻他们努力于种种善举。他们在仰望畏惧着呼求我，他们是对我卑谦的。","translator":"王静斋"},{"sura":21,"aya":91,"text":"又有一贞节的女子；我藉我的（鲁哈）吹在她里，我为世人以她和她的儿子作表征。","translator":"王静斋"},{"sura":21,"aya":92,"text":"这确是你们的道，统一的道。我是你们的养主，你们应当拜我。","translator":"王静斋"},{"sura":21,"aya":93,"text":"他们彼此间割裂自己的事情了。统是归返于我的。","translator":"王静斋"},{"sura":21,"aya":94,"text":"凡归信著作善举的人，他的努力不受湮没。我是一定为他记录的","translator":"王静斋"},{"sura":21,"aya":95,"text":"我所灭绝的城池，不得复返。","translator":"王静斋"},{"sura":21,"aya":96,"text":"直到耶朱哲、马朱哲得被开放，均都奔驰自各方的时候。","translator":"王静斋"},{"sura":21,"aya":97,"text":"确实的约已竟临近了。不信的人是立时瞠目的。（说：）唉！我们确未注意这事。不然，我们是自亏的了。","translator":"王静斋"},{"sura":21,"aya":98,"text":"你们和你们撇开安拉所拜的是火狱的柴薪，你们是将进它的。","translator":"王静斋"},{"sura":21,"aya":99,"text":"设若这一切是真主了，他们不至于进它。统是在那里永居的。","translator":"王静斋"},{"sura":21,"aya":100,"text":"他们在那里发哀声，全都听不到。","translator":"王静斋"},{"sura":21,"aya":101,"text":"其先蒙我造定幸福的人们离它遥远。","translator":"王静斋"},{"sura":21,"aya":102,"text":"他们听不到它的声音，永远享受自心所想望的。","translator":"王静斋"},{"sura":21,"aya":103,"text":"他们不忧虑绝大的惊恐。众天使迎接他们，（说：）这是曾对你们约下的 那一日。","translator":"王静斋"},{"sura":21,"aya":104,"text":"当那一日，我卷起天，就像卷起写就的卷砷就像我初次造化似的将要恢复他。是我约定的，我是必将实作的。","translator":"王静斋"},{"sura":21,"aya":105,"text":"我在教戒 以后，确在则遗尔上写定：（地终归我的义仆继承；）","translator":"王静斋"},{"sura":21,"aya":106,"text":"这确使得一般敬拜的民众满足了。","translator":"王静斋"},{"sura":21,"aya":107,"text":"我遣你为使，只为普慈众世界，","translator":"王静斋"},{"sura":21,"aya":108,"text":"你说！（我所得到的启示是：你们的主只是独一主。）你们可是顺服的吗?","translator":"王静斋"},{"sura":21,"aya":109,"text":"如果他们不肯了，你就说：我概括的告诉你们了。我不知道所对你们约定的是切近的?还是遥远的呢?","translator":"王静斋"},{"sura":21,"aya":110,"text":"主确知道你们明白说出的，并知道你们所隐匿的。","translator":"王静斋"},{"sura":21,"aya":111,"text":"我不知道恐怕那是试证你们，并（教你们）享受到一定的时期。","translator":"王静斋"},{"sura":21,"aya":112,"text":"他说：（我的养主啊！你依正义判断吧！我们的养主是来哈曼，是对于你们所称述者被求以相助的。）","translator":"王静斋"},{"sura":22,"aya":1,"text":"众人哪！你们当敬畏养主。的确，复生日的震动是一重大事件。","translator":"王静斋"},{"sura":22,"aya":2,"text":"当你们看见它的那一日，每一个乳母顾不得自所哺乳的；每一个妊妇都要流产。你看众人是醉的，他们原是不醉；虽然安拉的刑罚是严厉的。","translator":"王静斋"},{"sura":22,"aya":3,"text":"有的人无知识着因为安拉起争辩，并追从一切顽强的恶魔。","translator":"王静斋"},{"sura":22,"aya":4,"text":"凡与他结友的，就被他引入迷误，并被他导至火刑。这是已然对他判定的。","translator":"王静斋"},{"sura":22,"aya":5,"text":"众人哪！如果你们对于复活上怀疑了，……。我确造你们于土上，又于一点精上，又于一块血上，又于成形与不成形的一块肉上；为的是表明给你们。我把自所意欲（保持）的留在子宫，直到一定的期限。然后再现出你们为乳儿，而后（赐给你们寿限，)为的是你们达到壮年。你们之中有死亡的。你们之中有被归到最卑的年令的，好教他在知道以后不知一事。你看见地面是干燥的；及至我往它上降水的时候，它就摇动，滋长，生发各种美丽的。","translator":"王静斋"},{"sura":22,"aya":6,"text":"这由于安拉是真的，他能使死的复活；他是全能于万事的，","translator":"王静斋"},{"sura":22,"aya":7,"text":"复生日无疑是该到的，安拉即将复起墓中人。","translator":"王静斋"},{"sura":22,"aya":8,"text":"有的人无知识，无标准，亦无光明的经典，","translator":"王静斋"},{"sura":22,"aya":9,"text":"傲慢着为安拉起争议，终至诱人失迷安拉之道。那人在今世受辱；在复生日，我教他尝焚刑。","translator":"王静斋"},{"sura":22,"aya":10,"text":"这是由于你的双手往前送的；并由于安拉绝不迫害众仆。","translator":"王静斋"},{"sura":22,"aya":11,"text":"有的人在边际上敬拜安拉，他若遇到好处了，便就藉它安定；他若遭逢祸患了，便就倒转，他伤折今世与后世了，这就是显著的伤折。","translator":"王静斋"},{"sura":22,"aya":12,"text":"他撇开安拉拜那于他无害无益的；这就是遥远的迷误。","translator":"王静斋"},{"sura":22,"aya":13,"text":"他呼那其害较近于其利的主宰实在太恶了，伴侣实在太恶了。","translator":"王静斋"},{"sura":22,"aya":14,"text":"安拉使归信并行善举的人，进入诸河流于其下的天园。安拉行其所欲的。","translator":"王静斋"},{"sura":22,"aya":15,"text":"凡是自揣安拉绝不在今世后世相助他的人，任他抓住绳子上天，任他气闭身死，令他观看，他的计谋能可释去自所怒恨的吗?","translator":"王静斋"},{"sura":22,"aya":16,"text":"我如此降下这明显的表征。安拉必引其意欲（导道）的人得正道。","translator":"王静斋"},{"sura":22,"aya":17,"text":"的确，众归信的人、犹太人、 （沙便人、）基督教徒、袄教徒、多神教徒，安拉将在复生日从中判断。安拉实是见证万事的。","translator":"王静斋"},{"sura":22,"aya":18,"text":"你岂未见在天的、在地的、日、 月、群星、各山、树木、 牲畜、许多的人， 并有许多必定受刑的人，给安拉叩头吗? 遭安拉侮辱的人，再无一敬重他的。安拉必行其所欲行的。","translator":"王静斋"},{"sura":22,"aya":19,"text":"这两个敌人为着养主作敌对。不信的人，他们的火衣已经裁妥。从头上倾下沸汤，","translator":"王静斋"},{"sura":22,"aya":20,"text":"用它熔化他们腹内的，与皮。","translator":"王静斋"},{"sura":22,"aya":21,"text":"他们且遭受铁鞭。","translator":"王静斋"},{"sura":22,"aya":22,"text":"每逢他们因为忧闷欲从那里出来的时候，就教他们复返。你们尝焚刑吧，","translator":"王静斋"},{"sura":22,"aya":23,"text":"安拉必教一般归信并作善举的人进入诸河流于其下的天园。他们在那里蒙饰以金钏和珍珠。他们在那里的衣服是锦缎。","translator":"王静斋"},{"sura":22,"aya":24,"text":"他们被导至洁言；被导至堪夸之道。","translator":"王静斋"},{"sura":22,"aya":25,"text":"的确，一切不信并阻碍安拉之道，及我为众人无分住民与客居的入所设置的禁寺者，……在那里欲以背义偏斜的人，我将教他尝痛刑。","translator":"王静斋"},{"sura":22,"aya":26,"text":"昔时，我为易卜拉欣指定天房的基址，（说： ）你不要给我举一事作匹偶。你要为一般巡游的，长居的、鞠躬叩头的人洁净我的殿。","translator":"王静斋"},{"sura":22,"aya":27,"text":"你要宣告世人朝觐，果然，他们就徒步，并骑那来自各远路的一般瘦驼着到你这里来；","translator":"王静斋"},{"sura":22,"aya":28,"text":"以便他们亲临自己的益处，并在照例的几日内往安拉所赐给他们的四足家畜上记念安拉的尊名。你们就着它吃：并款待穷苦无告的人。","translator":"王静斋"},{"sura":22,"aya":29,"text":"而再令他们涤净油泥，令他们全其许下的愿。当令他们环游古殿。","translator":"王静斋"},{"sura":22,"aya":30,"text":"就是这样。凡尊重安拉的戒律者，那是在其养主认为于他至好的了。家畜，除去所诵给你们的，全于你们合法。你们当离远一切污秽的偶像，并避忌谎言；","translator":"王静斋"},{"sura":22,"aya":31,"text":"为安拉守正不偏，不给他举匹偶。凡给安拉举匹偶的人，恰是从天上落下来，而被飞鸟夺去，或是被风抛到远方。","translator":"王静斋"},{"sura":22,"aya":32,"text":"就是这样。凡尊敬安拉的征迹者，那尊敬实出自心中的诚敬。","translator":"王静斋"},{"sura":22,"aya":33,"text":"你们于家畜上享受各种利益，直到一定的时期以后，宰牲的处所 是在古殿附近。","translator":"王静斋"},{"sura":22,"aya":34,"text":"我为每一族人设定一种祭礼，好教他们往安拉所赐给的四足家畜上记念他的尊名。你们的主是独一主。你们惟当顺服他，你当为一般卑谦的人报喜信。","translator":"王静斋"},{"sura":22,"aya":35,"text":"那些人有的记念安拉的时候，他们心中畏惧；并为一般忍于自所遭遇的，以及立拜功且就着我所赐的作使用的人。","translator":"王静斋"},{"sura":22,"aya":36,"text":"我为你们把祭祀驼作为安拉的征迹。你们于它上获一种利益。当站齐的时候，你们就往它上诵安拉之名，其肋落地的时候，你们就吃它，并款待穷不求人的，以及意欲求人的。我就这样地为你们降服它，好教你们感念。","translator":"王静斋"},{"sura":22,"aya":37,"text":"它的肉和它的血绝达不到安拉上，只有你们的诚敬能可达至他。真主就这样地为你们降服它了，好教你们赞安拉伟大，以(表感谢)他所指导你们的。你给为善的人们报喜信吧！安拉必代众穆民作抵御。","translator":"王静斋"},{"sura":22,"aya":38,"text":"安拉实不爱喜一切背信负恩的。","translator":"王静斋"},{"sura":22,"aya":39,"text":"遭逢格斗的人们，因受迫害而得到许可（抵御）安拉确能相助他们。","translator":"王静斋"},{"sura":22,"aya":40,"text":"那些人，无端的被人逐出家宅，只因声称：（我们的养主是安拉。）设非安拉教世人的一部分抵御一部分，则修道所，基督教堂，犹太教堂，与有人多在那里记念安拉之名的礼拜殿，必被捣毁了。安拉必助那相助他的人。安拉实是大力的，优胜的。","translator":"王静斋"},{"sura":22,"aya":41,"text":"那般人，我若教他们在地上得到能力了，他们就立拜功，散天课，命以合法的，阻止非理的。诸事的结果惟归安拉。","translator":"王静斋"},{"sura":22,"aya":42,"text":"若是他们不信你了，（不要忧虑，）因为在他们以前努海的族人、阿代、赛母待、","translator":"王静斋"},{"sura":22,"aya":43,"text":"易卜拉欣的族人，鲁脱的族人、","translator":"王静斋"},{"sura":22,"aya":44,"text":"买大严人，确曾不信服了；并有人不信服母撒。但是我姑容众逆徒，而后惩治他们了，我的惩罚如何?","translator":"王静斋"},{"sura":22,"aya":45,"text":"许多城池正在负义着被我灭绝，而致屋背倒塌。又有许多废井和倾圮的高官。","translator":"王静斋"},{"sura":22,"aya":46,"text":"他们岂未在地上游行，而用心理解，用耳闻听吗?确实眼不瞎，只是胸中的心瞎。","translator":"王静斋"},{"sura":22,"aya":47,"text":"他们要你速现刑罚。安拉绝不爽其所约。在你养主那里的一日，如同是你们计数的一千年。","translator":"王静斋"},{"sura":22,"aya":48,"text":"许多背义的城池，我姑容它，而后施以惩罚，归惟归于我。","translator":"王静斋"},{"sura":22,"aya":49,"text":"你说：众人哪！我只是一个向你们公开传警告的。","translator":"王静斋"},{"sura":22,"aya":50,"text":"归信并作善举的人可获饶恕，和优上的给养。","translator":"王静斋"},{"sura":22,"aya":51,"text":"一般对我的表征尽力抵抗的人，均是居火狱的。","translator":"王静斋"},{"sura":22,"aya":52,"text":"我在你以前每逢遣一使者（或）圣人，当他望想的时候，恶魔就投入在他所望想的里面。但是安拉消除恶魔所投的，安拉且坚固自己的表征。安拉是深知的，明哲的。","translator":"王静斋"},{"sura":22,"aya":53,"text":"为的是真主以恶魔所投入的置作磨难一般心里有病的，和伪心坚硬的。背义的人们， 实是在遥远的反抗中。","translator":"王静斋"},{"sura":22,"aya":54,"text":"并为的是一般蒙赐知识的人知道那是出自养主的真理，而致归信它，心且因它谦卑。安拉实是把众穆民引至正道的。","translator":"王静斋"},{"sura":22,"aya":55,"text":"不信的人时常对它怀疑，直到复生日忽然来到，或是断绝日的刑罚来临。","translator":"王静斋"},{"sura":22,"aya":56,"text":"当那日，权柄为安拉执掌。他在他们中间公判。归信并作善举的人是在乐园中。","translator":"王静斋"},{"sura":22,"aya":57,"text":"隐昧且不信我表征的人，均应遭受辱刑。","translator":"王静斋"},{"sura":22,"aya":58,"text":"一般在安拉的道上迁移而后被杀或死亡的人，安拉必赐给他们优上的给养，安拉实是最上的赐予给养者。","translator":"王静斋"},{"sura":22,"aya":59,"text":"他将教他们进那所爱喜的地方。安拉实是深知的，仁厚的。","translator":"王静斋"},{"sura":22,"aya":60,"text":"就是这样。凡依照所罹的扰害而施行报复，后又遭人压迫的，安拉将必相助他。安拉实是放赦的，多恕的。","translator":"王静斋"},{"sura":22,"aya":61,"text":"这是由于安拉纳夜于昼，纳昼于夜。安拉实是能听的，能见的。","translator":"王静斋"},{"sura":22,"aya":62,"text":"这是由于安拉确是真的，他们撇开他所拜者是假的。安拉就是尊大的，玄高的。","translator":"王静斋"},{"sura":22,"aya":63,"text":"你岂未看见安拉由上空降水而致地面成为绿色的吗?安拉是施惠的，尽知的。","translator":"王静斋"},{"sura":22,"aya":64,"text":"在天的在地的惟他执掌。安拉就是无求的，受赞的。","translator":"王静斋"},{"sura":22,"aya":65,"text":"你岂未看见安拉为你们降服地上所有的，并降服船只，依他的旨意航行在海中；且制止穹苍落地；奉到他的命令时在外。安拉一定是怜血慈悯众人的。","translator":"王静斋"},{"sura":22,"aya":66,"text":"他那真主教你们生，再教你们死，又教你们生。人类实是忘恩的。","translator":"王静斋"},{"sura":22,"aya":67,"text":"我为每一伙人置定一种道理，供他们奉行。他们对于这事上不得和你起纷争。你导至你的养主！你实是在正道上。","translator":"王静斋"},{"sura":22,"aya":68,"text":"若是他们对你起争论了，你就说：（安拉至知你们的作为。","translator":"王静斋"},{"sura":22,"aya":69,"text":"安拉将在复生日，对于你们所争议的从中判断。）","translator":"王静斋"},{"sura":22,"aya":70,"text":"你岂未悉安拉知道天地所有的吗？这是载在典章的，这实是在安拉上容易的。","translator":"王静斋"},{"sura":22,"aya":71,"text":"他们撇开安拉拜那未曾经他降给权柄的。在他们原不知道他。背义的人不得一相助的。","translator":"王静斋"},{"sura":22,"aya":72,"text":"有人把我的表征诵给他们的时候，你就能在那般逆徒的面上瞧出憎恶来。差不多他们就要猛扑那些向他们诵我表征的人。","translator":"王静斋"},{"sura":22,"aya":73,"text":"众人哪！有人发了一个比喻，你们来听它！的确，你们撇开安拉所拜的那一切纵令他们集合起来，亦不能造一个蝇子。若是蝇子由他们上夺去一物，他们不能从它上追回来。追寻的和被追寻的太薄弱了。","translator":"王静斋"},{"sura":22,"aya":74,"text":"他们尊敬安拉，不是正当地尊敬他。安拉实是有力的，优胜的。","translator":"王静斋"},{"sura":22,"aya":75,"text":"安拉于天使中并于人类中选择一切使者；安拉实是能听的，能见的。","translator":"王静斋"},{"sura":22,"aya":76,"text":"他知道在他们前面后面的。诸事惟归于安拉。","translator":"王静斋"},{"sura":22,"aya":77,"text":"众归信的人哪！你们鞠躬叩头吧！你们当拜自己的养主，并当为善，冀使你们得到所欲。","translator":"王静斋"},{"sura":22,"aya":78,"text":"你们当为安拉奋斗，切实地为他奋斗。他选择你们，他未在教门上给你们加以困难。（你们当追随）自己的祖先——易卜拉欣的教道。真主在从前，并在这里称你们为穆思林，终至使者是见证你们的；你们是见证众人的。你们当立拜功，散天课。你们当抓住安拉。他是爱护你们的。这爱护者太好了：这相助者太好了！","translator":"王静斋"},{"sura":23,"aya":1,"text":"众信者确已得意了；","translator":"王静斋"},{"sura":23,"aya":2,"text":"那些人是在拜中敬畏的；","translator":"王静斋"},{"sura":23,"aya":3,"text":"那些人是避忌无益的事情的；","translator":"王静斋"},{"sura":23,"aya":4,"text":"那些人是实行清洁的；","translator":"王静斋"},{"sura":23,"aya":5,"text":"那些人是遮掩羞体的，","translator":"王静斋"},{"sura":23,"aya":6,"text":"除去对自己的妻室或手下的，当不至遭谴责。","translator":"王静斋"},{"sura":23,"aya":7,"text":"觅此以外的人，均是违法的。","translator":"王静斋"},{"sura":23,"aya":8,"text":"那些人是谨守信托与约言的；","translator":"王静斋"},{"sura":23,"aya":9,"text":"那些人常守自己的拜功，","translator":"王静斋"},{"sura":23,"aya":10,"text":"他们是继承斐尔道司 的继承者。","translator":"王静斋"},{"sura":23,"aya":11,"text":"永居在那里。","translator":"王静斋"},{"sura":23,"aya":12,"text":"我确曾用泥精造化人类，","translator":"王静斋"},{"sura":23,"aya":13,"text":"而后使它成为一点精，藏在坚固的定所。","translator":"王静斋"},{"sura":23,"aya":14,"text":"我又把一点精造成一块血，又把一块血造成一块肉，而给一块肉造上骨，而给骨穿上肉；以后我造他另一次造化。 安拉——至上的造化者很玄妙了！","translator":"王静斋"},{"sura":23,"aya":15,"text":"在这以后你们必定是死的；","translator":"王静斋"},{"sura":23,"aya":16,"text":"以后在复生日你们必被复起。","translator":"王静斋"},{"sura":23,"aya":17,"text":"我确曾在你们上面造化了七重天；我于所造的上并不忽略。","translator":"王静斋"},{"sura":23,"aya":18,"text":"我依着限度从上空降下水来；我使它留在地里；我是定能除去它的。","translator":"王静斋"},{"sura":23,"aya":19,"text":"我为你们以水造化枣树和葡萄园；你们在那里享受多的果实；你们就着它吃。","translator":"王静斋"},{"sura":23,"aya":20,"text":"并造化出自西乃山，产油并为食者调味的树。","translator":"王静斋"},{"sura":23,"aya":21,"text":"家畜中含有对你们的一种儆诫。我教你们饮那出自腹中的。你们于中享受许多利益。","translator":"王静斋"},{"sura":23,"aya":22,"text":"并且就着它吃，更教你们乘在它上和船只上。","translator":"王静斋"},{"sura":23,"aya":23,"text":"我曾把努海差至他的族人，他说我的族人哪！你们当拜安拉；在你们无一除过他的主。你们怎不畏俱呢?","translator":"王静斋"},{"sura":23,"aya":24,"text":"他的族人中一般不信的领袖说：这不过是和你们一样的人；他想要超越你们。设若安拉意欲……了，他必降下天使。我们在自己前代列祖中未听到过这个。","translator":"王静斋"},{"sura":23,"aya":25,"text":"他只是一个有疯病的人你们等侯他，直到一个时期吧！）","translator":"王静斋"},{"sura":23,"aya":26,"text":"努海说：（我的养主啊！你因他们的不信相助我吧！）","translator":"王静斋"},{"sura":23,"aya":27,"text":"而后我默示他；（依我的相助，奉我的命令造船，）当我的命令既至，炉子弗腾的时候（我就命令！）你把每一类一对两性载在船上，并载上你的眷属，除却他们其中应该遭难的。你不要为背义的人呼吁我。他们是必定被淹的。","translator":"王静斋"},{"sura":23,"aya":28,"text":"你和随同你的人在船上坐稳的时候，当说：（赞颂安拉，那解救我们脱离背义的民众的主。 ）","translator":"王静斋"},{"sura":23,"aya":29,"text":"你说：我的养主啊！你教我居在吉庆的地方，你是最善于安插的。","translator":"王静斋"},{"sura":23,"aya":30,"text":"此中确含有种种表征，我确是试证的。","translator":"王静斋"},{"sura":23,"aya":31,"text":"在他们以后我又创造另一代人。","translator":"王静斋"},{"sura":23,"aya":32,"text":"我往他们那里差其同类的人为使者。（说：）你们当拜安拉！在你们无一除过他的主；你们怎不畏惧呢?","translator":"王静斋"},{"sura":23,"aya":33,"text":"他的族人中一般隐昧 且不信看见后世，我使其在今生享受的那般领袖说： （这不过是你们一类的人；他吃你们所吃的；他饮你们所饮的。","translator":"王静斋"},{"sura":23,"aya":34,"text":"如果你们服从那和你们一样的人了，那时候，你们一定是伤折的了。","translator":"王静斋"},{"sura":23,"aya":35,"text":"他对你们预许：（你们死去，化为土与骨骸的时候，定要复出）吗?。","translator":"王静斋"},{"sura":23,"aya":36,"text":"那对你们预许的太远了，太远了。","translator":"王静斋"},{"sura":23,"aya":37,"text":"生存就是今世的生存，我们有死有死。 我们不再复起。","translator":"王静斋"},{"sura":23,"aya":38,"text":"他不外是一个往安拉上造作谎言的人；我们绝不信他。 ）","translator":"王静斋"},{"sura":23,"aya":39,"text":"他 说：（我的养主啊！你因他们的不信相助我吧！）","translator":"王静斋"},{"sura":23,"aya":40,"text":"主说： （少时他们必定懊悔，）","translator":"王静斋"},{"sura":23,"aya":41,"text":"而后他们切实遭受一声鸣，我使他们化为碎屑。背义的民众相去太远了，","translator":"王静斋"},{"sura":23,"aya":42,"text":"在他们以后我复又创造另外若干代。","translator":"王静斋"},{"sura":23,"aya":43,"text":"任何一伙人不能争先自己的期限；也不能退后。","translator":"王静斋"},{"sura":23,"aya":44,"text":"然后我差去列使，接接连连着，每有一个使者达至一伙人的时候，就遭他们不信。我教他们的一部分追随一部分。我把他们作为一般谈料。不信的民众太远了！","translator":"王静斋"},{"sura":23,"aya":45,"text":"而后我把母撒和他的弟兄——哈伦，持我的异迹和明证遣至匪喇傲与他的众伟人；","translator":"王静斋"},{"sura":23,"aya":46,"text":"但是他们倔强了。他们本是一伙高傲的。","translator":"王静斋"},{"sura":23,"aya":47,"text":"他们说：（我们归信两个和我们一样的人吗?他俩的族人原是服从我们的。 ）","translator":"王静斋"},{"sura":23,"aya":48,"text":"于是他们不信他两个，他们归为灭绝的了。","translator":"王静斋"},{"sura":23,"aya":49,"text":"我确赐给母撒经典，好教他们得正道。","translator":"王静斋"},{"sura":23,"aya":50,"text":"我以马尔焉母子作一种表征；我使他俩居在安定并有活水的高地。","translator":"王静斋"},{"sura":23,"aya":51,"text":"列使啊！你们就着一切美品吃吧！你们应当作善举。我确深知你们的作为。","translator":"王静斋"},{"sura":23,"aya":52,"text":"这确是你们的道，统一的道。我是你们的养主，你们应当惧我。","translator":"王静斋"},{"sura":23,"aya":53,"text":"而后他们割裂自己间的事情为一段一段的。","translator":"王静斋"},{"sura":23,"aya":54,"text":"每一伙人是爱喜自己所守的。","translator":"王静斋"},{"sura":23,"aya":55,"text":"你任他们处在茫昧中直到一个时期，","translator":"王静斋"},{"sura":23,"aya":56,"text":"他们岂可猜度：我用以助长他们的资财和儿子，是我赶忙赐给他们的种种好处?不然，他们自不觉悟。","translator":"王静斋"},{"sura":23,"aya":57,"text":"的确，一般由于怕其养主而战栗的，","translator":"王静斋"},{"sura":23,"aya":58,"text":"信其养主各项表征的，","translator":"王静斋"},{"sura":23,"aya":59,"text":"不为其养主举匹偶的，","translator":"王静斋"},{"sura":23,"aya":60,"text":"与一般心在畏惧着给其所给的，以自己是将必归返养主的，","translator":"王静斋"},{"sura":23,"aya":61,"text":"这些人忙行种种善事，为它争先着。","translator":"王静斋"},{"sura":23,"aya":62,"text":"我不课人能力以外的。 在我卸前有述说实情的经典，他们并不受损。","translator":"王静斋"},{"sura":23,"aya":63,"text":"不然，他们的心不甚明白这事。在他们有除此以外自所做下的工作。","translator":"王静斋"},{"sura":23,"aya":64,"text":"直到我用刑罚惩治他们的富户时候，他们立就哀号。","translator":"王静斋"},{"sura":23,"aya":65,"text":"（我说）今日之下你们不要哀号。你们绝得不到我的相助。","translator":"王静斋"},{"sura":23,"aya":66,"text":"从前有人对你们诵我表征的时候，你们就高傲着退转；","translator":"王静斋"},{"sura":23,"aya":67,"text":"在禁地里夜间叙话，妄作批评着。","translator":"王静斋"},{"sura":23,"aya":68,"text":"他们岂未参悟这话吗？不然，他们的列祖未曾得到的已竟达至他们了吗?","translator":"王静斋"},{"sura":23,"aya":69,"text":"不然，他们的列祖未曾得到的竞达至他们了吗：不然，他们未识自己的使者，而致不承认他吗?","translator":"王静斋"},{"sura":23,"aya":70,"text":"不然，他们说！（他染有疯病吗?）不然，他把真理现给他们了，他们的大多数是厌恶真理的。","translator":"王静斋"},{"sura":23,"aya":71,"text":"设若真的追随他们的偏见，一定天地及其间所有的就毁灭了。不然，我赐给他们教戒了，他们是远避教戒的。","translator":"王静斋"},{"sura":23,"aya":72,"text":"不然，你向他们讨工资吗?你养主的工资是至上的。他是最善于赐给的。","translator":"王静斋"},{"sura":23,"aya":73,"text":"你确导他们于正路。","translator":"王静斋"},{"sura":23,"aya":74,"text":"不信后世的人是走到路傍的，","translator":"王静斋"},{"sura":23,"aya":75,"text":"设若我慈惠他们，我释去他们所遭的患难，他们一定要深入迷路中，徘徊着。","translator":"王静斋"},{"sura":23,"aya":76,"text":"确切我以刑罚惩治他们了，他们未问其养主屈服，也不表示谦卑。","translator":"王静斋"},{"sura":23,"aya":77,"text":"及至我给他们启开有烈刑的门的时候，他们立时就在那里绝望了。","translator":"王静斋"},{"sura":23,"aya":78,"text":"他是为你们造化耳、眼、心的主，你们绝少感念。","translator":"王静斋"},{"sura":23,"aya":79,"text":"他是把你们造在地上，将把你们集至他的主。","translator":"王静斋"},{"sura":23,"aya":80,"text":"他是使生使死且掌夜昼的不同的主。你们怎不了解呢?","translator":"王静斋"},{"sura":23,"aya":81,"text":"不然，他们说类如前人们所说的了。","translator":"王静斋"},{"sura":23,"aya":82,"text":"他们说：（一旦我们死去，化为土与骨骸的时侯，我们必定复起吗?","translator":"王静斋"},{"sura":23,"aya":83,"text":"从前确有人向我们和我们的列祖预许这事；这不外是前人们的故事。）","translator":"王静斋"},{"sura":23,"aya":84,"text":"你说!（地和在它以上的是谁执掌?如果你们知道了，……。 ）","translator":"王静斋"},{"sura":23,"aya":85,"text":"他们即将说：（安拉执掌。）你说！（你们怎不受劝呢?）","translator":"王静斋"},{"sura":23,"aya":86,"text":"你说！（谁是调养七天的主，调养伟大的「阿勒世」的主?）","translator":"王静斋"},{"sura":23,"aya":87,"text":"他们即将说：（安拉执掌。）你说！（你们怎不畏惧呢?）","translator":"王静斋"},{"sura":23,"aya":88,"text":"你说：（谁掌万物的权柄?他本救济人，人不救济他。如果你们知道了，……。）","translator":"王静斋"},{"sura":23,"aya":89,"text":"他们即将说：（安拉执掌。）你说：（你们怎可受骗呢?）","translator":"王静斋"},{"sura":23,"aya":90,"text":"不然，我把真的现给他们了。他们实是撒谎的。","translator":"王静斋"},{"sura":23,"aya":91,"text":"安拉并未立子，无有一个受拜的和他共同。果然是那样， 一定每一个主拿去他所造化的。他们的一部分抑制一部分。安拉清净不染他们所称述的！","translator":"王静斋"},{"sura":23,"aya":92,"text":"知道目不能见的与显见的主，他玄高于他们所举的匹偶。","translator":"王静斋"},{"sura":23,"aya":93,"text":"你说：（我的养主啊！你若把对他们所许下的显给我了，","translator":"王静斋"},{"sura":23,"aya":94,"text":"我的养主啊！你不要教我归入背义的民众里。）","translator":"王静斋"},{"sura":23,"aya":95,"text":"我定能把自所许给他们的现给你。","translator":"王静斋"},{"sura":23,"aya":96,"text":"你要以最善的行为免去罪恶！我深知他们所称述的。","translator":"王静斋"},{"sura":23,"aya":97,"text":"你说：（我的养主啊！我求你护佑，莫受恶魔的暗示。","translator":"王静斋"},{"sura":23,"aya":98,"text":"我的养主啊，我求你保佑，莫容他们挨近我）","translator":"王静斋"},{"sura":23,"aya":99,"text":"及至他们一个人临死的时候，他就说：（我的养主啊！你教我回去吧，","translator":"王静斋"},{"sura":23,"aya":100,"text":"或者我以抵补曾经放弃的去作善举。）不能。这是他自己所说的话。在他们的面前有一隔障，直到复活他们的那一日。","translator":"王静斋"},{"sura":23,"aya":101,"text":"当吹起号筒的时候，是日，他们彼此间无宗亲，也不相询问。","translator":"王静斋"},{"sura":23,"aya":102,"text":"天秤重大的人，就是得意的。","translator":"王静斋"},{"sura":23,"aya":103,"text":"天秤轻的人，是伤折己身的，永居火狱中。","translator":"王静斋"},{"sura":23,"aya":104,"text":"他们的面被火焚烧；他们是在那里吡牙裂嘴的。","translator":"王静斋"},{"sura":23,"aya":105,"text":"未曾有人向你们读我的表征么?但是你们不曾信它。","translator":"王静斋"},{"sura":23,"aya":106,"text":"他们说：（我们的养主啊！我们受厄运的压制了，我们原是一伙迷误的。","translator":"王静斋"},{"sura":23,"aya":107,"text":"我们的养主啊！你教我们从这里出去，如果我们复反了，我们定是背义的 ）","translator":"王静斋"},{"sura":23,"aya":108,"text":"主说：（你们缄默，不要对我发言。","translator":"王静斋"},{"sura":23,"aya":109,"text":"的确，我的仆人曾有一伙人说：「我们的养主啊！我们归信了，你饶恕我们吧?你慈悯我们吧，你是最上的慈悯者。","translator":"王静斋"},{"sura":23,"aya":110,"text":"但是你们拿他们当作可戏的。致使你们忘却记念我。你们曾嘲笑他们了，","translator":"王静斋"},{"sura":23,"aya":111,"text":"今日，我确因他们的忍耐赏赐他们，他们均是得意的人）","translator":"王静斋"},{"sura":23,"aya":112,"text":"主说：（你们在地上迟留几年了，）","translator":"王静斋"},{"sura":23,"aya":113,"text":"他们说：（我们迟留一日或一日的一部分了。）但是你问计算的（天使）吧！","translator":"王静斋"},{"sura":23,"aya":114,"text":"主说：（你们只留少时，设若你们知道了，……。）","translator":"王静斋"},{"sura":23,"aya":115,"text":"你们自以为我嬉戏着造化你们，你们不返回我吗？","translator":"王静斋"},{"sura":23,"aya":116,"text":"安拉—— 真实的主太玄高了！除他以外再无有主，他是掌尊贵的“阿勒世”者。","translator":"王静斋"},{"sura":23,"aya":117,"text":"凡是无明证的当着安拉呼求另一受拜的，惟有他的养主对他清算。的确，不信的人不能得意。","translator":"王静斋"},{"sura":23,"aya":118,"text":"你说，我的养主啊，你饶恕吧，你慈悯吧，你是至上的慈悯者。","translator":"王静斋"},{"sura":24,"aya":1,"text":"（这）是我颁降，我规定，我在它里降下明文的一章，冀使你们受教。","translator":"王静斋"},{"sura":24,"aya":2,"text":"淫妇奸夫，你们应鞭他俩每一个人一百鞭子。如果你们归信安拉与末日了，你们为着安拉的教门不要怜洫他俩，当令一伙子穆民亲临惩治他两个。","translator":"王静斋"},{"sura":24,"aya":3,"text":"奸夫只可娶淫妇，或多神教的女子。淫妇只有奸夫或多神教的男子娶她。这是在众穆民禁不可行的。","translator":"王静斋"},{"sura":24,"aya":4,"text":"诬蔑贞节女子并未举出四个证人者，你们要鞭他们八十鞭子。你们永勿接受他们的见证。这般人均是作恶的。","translator":"王静斋"},{"sura":24,"aya":5,"text":"在此以后悔罪自修的人们除外。安拉一定是多恕的，特慈的。","translator":"王静斋"},{"sura":24,"aya":6,"text":"诬蔑自己的妻室，除去自身别无证人者，其一人的见证，是指安拉做四次见证：他确是实言的。","translator":"王静斋"},{"sura":24,"aya":7,"text":"第五次是这样：如果他是谎言的，在他应遭安拉的驱远。","translator":"王静斋"},{"sura":24,"aya":8,"text":"她指着安拉举行四次见证：他实是谎言的；","translator":"王静斋"},{"sura":24,"aya":9,"text":"第五次是：如果他的所言属实，她就遭安拉的怒恼，这样便可为她兔去施刑。","translator":"王静斋"},{"sura":24,"aya":10,"text":"设非安拉对你们的特恩与他的慈惠，并安拉实是允许悔罪的，明哲的了……。","translator":"王静斋"},{"sura":24,"aya":11,"text":"的确，那般发动诬蔑的，就是你们本伙的人。你们不要以为这是你们的祸，而是你们的福。他们每一个人担负自所作下的罪恶。他们之中担负其大的，应受巨刑。","translator":"王静斋"},{"sura":24,"aya":12,"text":"在你们听到哪话的时候，穆民男女们为甚么对于同人不往好处揣测，说： （这是明显的谎话？）","translator":"王静斋"},{"sura":24,"aya":13,"text":"他们为何不因着这事举出四个证人来?他们既未能举出证人的时候，他们是在安拉视为谎言的。","translator":"王静斋"},{"sura":24,"aya":14,"text":"设非安拉在今世后世对你们的特恩和他的慈惠，你们必因为参与其间遭受巨刑了。","translator":"王静斋"},{"sura":24,"aya":15,"text":"那时候，你们互相传播，你们口称自所不知的。你们以为那是平常的。其实那在安拉视为重大的。","translator":"王静斋"},{"sura":24,"aya":16,"text":"你们听见这事的时候，为什么不说：（我们不该说这话。赞你清净；这是一大谗谤）","translator":"王静斋"},{"sura":24,"aya":17,"text":"安拉劝你们：如果你们是信士了，要永勿再行这类事。安拉为你们解明各项表征。","translator":"王静斋"},{"sura":24,"aya":18,"text":"安拉是深知的，明哲的。","translator":"王静斋"},{"sura":24,"aya":19,"text":"的确，一般喜欢丑恶在众穆民中播扬的人，应在今世与后世遭痛刑。安拉知道，你们不知道。","translator":"王静斋"},{"sura":24,"aya":20,"text":"设非安拉对你们的特恩与他的慈惠，并安拉是怜悯的特慈的了……。","translator":"王静斋"},{"sura":24,"aya":21,"text":"众归信的人哪！你们不要追随恶魔的脚步那追随恶魔脚步的人。恶魔必命以丑恶与非理。设非安拉对你们的特恩与他的慈惠了，他永不使你们之中的一个得到清洁；但是安拉使其意欲（清洁）的人清洁。安拉是能听的，深知的。","translator":"王静斋"},{"sura":24,"aya":22,"text":"你们之中有资望与多财的人，莫要誓不赠给近戚贫人，以及在安拉之上迁居的。当令他们赦宥，当令他们宽容；你们不欲安拉饶恕你们吗?安拉是多恕的，特慈的。","translator":"王静斋"},{"sura":24,"aya":23,"text":"的确，一般诬蔑有正信的，未曾介意的贞女者，必在今世与后世遭咒诅，且受巨刑。","translator":"王静斋"},{"sura":24,"aya":24,"text":"当那一日，他们的口舌与手脚对他们见证其所作的。","translator":"王静斋"},{"sura":24,"aya":25,"text":"当那一日，安拉对他们秉公施以充分的报应，他们知道安拉就是表明的，真实的。","translator":"王静斋"},{"sura":24,"aya":26,"text":"一切污浊的女子应配污浊的男子；一切污浊的男子；应配污浊的女子；一切清洁的女子应配清洁的男子；一切清洁的男子应配清洁的女子。这些人们是清白无染那般人所说的。他们可获饶恕和优上的给养。","translator":"王静斋"},{"sura":24,"aya":27,"text":"众归信的人哪！你们非至请求许可，并向房主道安以后，不得进别人家的住室，这是于你们至好的；但愿你们受劝。","translator":"王静斋"},{"sura":24,"aya":28,"text":"如果你们在那里未遇见一个人，你们非至得到许可以后不要进去。如果有人对你们说：（回去！）你们就回去，这是于你们至洁的。安拉深知你们的作为。","translator":"王静斋"},{"sura":24,"aya":29,"text":"无人居住的房舍，内有你们的货物，在你们不妨进入。安拉知道你们公开的和隐匿的。","translator":"王静斋"},{"sura":24,"aya":30,"text":"你当告诉众穆民男子；俯首下视，遮其羞体。这是于他们至洁的。安拉尽知他们的作为。","translator":"王静斋"},{"sura":24,"aya":31,"text":"你当告诉众穆民女子：俯首下视，遮其羞体，不得现露装饰，除非那外露的。当令她们把（头巾）垂在衣领上；不要露出装饰。除去当着丈夫，或生父，或公爹，或本夫的儿子，或自己的儿子，或自己的弟兄，或自己的侄男，或自己的甥男，或同类的妇女，或手下的奴才，或无性欲的男仆，或是未见过女阴的赤子；她们亦莫为着令人知遂自所隐匿的装饰而跺脚。众归信的人哪！你们当共同归向安拉，冀使你们得意。","translator":"王静斋"},{"sura":24,"aya":32,"text":"你们应给同人中的孤身者，与廉节的仆婢结婚姻！如果他们 是穷困的，安拉就依自己的特慈教他们富有。安拉是宽恩的，深知的。","translator":"王静斋"},{"sura":24,"aya":33,"text":"无力结婚的人应当操守；待至安拉以自己的特恩使他们富有。你们的奴才中那要……求预约赎身的人，如果你们知道他们相宜了，你们就和他们预约赎身吧！你们把安拉赐予你们的资财赠给他们。你们的使女如欲守贞了，不得为着寻得今生的些微福分，而强迫她们行淫。那强迫她们的人，在她们被迫以后安拉一定是多恕的，特慈的。","translator":"王静斋"},{"sura":24,"aya":34,"text":"我确切向你们降下明显表征，与先你们过世那般人的故事，与对于一般敬慎者的劝化。","translator":"王静斋"},{"sura":24,"aya":35,"text":"安拉是天地的光，他那光的情状，如同是有灯的壁龛；灯在玻璃中，那玻璃如同是光耀的星辰，燃自吉庆的树。就是不东不西的橄榄树；它的油虽未与火接触，而几乎发光。光在光上，安拉引导自所意欲……的人，达至的光辉，安拉为人发布种种比喻。安拉是深知万事的。","translator":"王静斋"},{"sura":24,"aya":36,"text":"安拉命令建高而任人在那里记念他的尊名的殿内，","translator":"王静斋"},{"sura":24,"aya":37,"text":"有些男子在那里朝夕称颂清净。那般人不被交易买卖所缠，而致顾不到记念安拉，与立拜功，施天课。他们骇怕心目转动的那一天；","translator":"王静斋"},{"sura":24,"aya":38,"text":"终而安拉依他们所作的至优者赏赐他们，并把自己的特恩增加给他们。安拉无算地赐给自所意欲（赐给）的人。","translator":"王静斋"},{"sura":24,"aya":39,"text":"不信的人们，其功修如同是平原的蜃气，干渴的人以为那是水。及至来到它的时候，则一无所得。他就得到安拉在他御前，对他结算。安拉是审箅神速的。","translator":"王静斋"},{"sura":24,"aya":40,"text":"或像深海中种种黑暗，那人被上面有云的重重涛浪所遮， 一切黑暗一部分是在一部分的上面。他伸出手的时候，几不见手。未蒙安拉赐给光辉的人，在他毫无光辉。","translator":"王静斋"},{"sura":24,"aya":41,"text":"你岂未见天地间的人与展翅的飞禽颂安拉清净吗?每一个确知道祈祷他，赞颂他。安拉深知他们的作为。","translator":"王静斋"},{"sura":24,"aya":42,"text":"天地的权柄惟安拉执掌。归惟归于安拉。","translator":"王静斋"},{"sura":24,"aya":43,"text":"你岂未见安拉驱云，而使其彼此连接，更把它罗起。你看见雨点出自其间，并从上边山上降下冰雹，而使它达到自所意欲(达到)的人；并为自所意欲（制止）的人制止它。他的电光几乎夺去视官。","translator":"王静斋"},{"sura":24,"aya":44,"text":"安拉变换夜昼。这内中确含有对于一般明鉴者的儆诫。","translator":"王静斋"},{"sura":24,"aya":45,"text":"安拉由水上造化各种动物。有的以腹行走；有的以双足行走；有的以四蹄行走。安拉造其所意欲（造化）的。安拉实是全能于万事的。","translator":"王静斋"},{"sura":24,"aya":46,"text":"我确降下明显的表征了。安拉导其意欲（得道）的人于正道。","translator":"王静斋"},{"sura":24,"aya":47,"text":"他们说：（我们归信安拉和使者了，我们顺从了。）在这以后他们的一伙人复又回避，他们绝不是归信的。","translator":"王静斋"},{"sura":24,"aya":48,"text":"一旦有人唤他们趋向安拉和他的使者，为的是在他们中间判断了，他们的一伙立刻远避。","translator":"王静斋"},{"sura":24,"aya":49,"text":"若是他们能获权利了，便就服从着去到他那里。","translator":"王静斋"},{"sura":24,"aya":50,"text":"他们心里有病吗? 不然，他们怀疑吗？不然，他们恐怕安拉和他的使者迫害他们吗?不然，这些人均是背义的。","translator":"王静斋"},{"sura":24,"aya":51,"text":"众归信的人被唤至安拉和他的使者，为的是在他们中间判断的时候，只是说：（我们听到了，我们顺从了。）这些均是得意的。","translator":"王静斋"},{"sura":24,"aya":52,"text":"凡顺从安拉和他的使者，并惧怕安拉，堤备他（的罪刑者，）均是达到所欲的。","translator":"王静斋"},{"sura":24,"aya":53,"text":"他们指着安拉矢重誓。如果你吩咐他们了，他们一定出击。你说：（你们不要发誓，（只求）合理的服从。安拉尽知你们的作为。 ）","translator":"王静斋"},{"sura":24,"aya":54,"text":"你说：（你们顺从安拉吧！你们顺从使者吧！）如果他们不肯了，他惟负着自所担当的。你们也负着自所担当的。你们若顺从他了，你们就得正道。使者所负的只是公开的的宣达。","translator":"王静斋"},{"sura":24,"aya":55,"text":"安拉预许你们其中归信并作善举的人：必定委他们在地上继替， 一如委他们以前的众人继替；必为他们巩固自所喜爱的教门。 他必给他们易「畏惧」为安定。 他们拜我，不给我举一事为匹偶。在这以后负恩的人，均是作恶的。","translator":"王静斋"},{"sura":24,"aya":56,"text":"你们当立拜功，施天课，顺从使者，冀使你们蒙慈惠。","translator":"王静斋"},{"sura":24,"aya":57,"text":"你莫以为不信的人们是在地上能做抵抗的， 他们的居处是火狱。归所太恶了！","translator":"王静斋"},{"sura":24,"aya":58,"text":"众归信的人哪！当令手下的人与尚未成丁的人三次，向你们请求许可；在「晨礼」以前，在「日昃」脱下衣服的时候，并在「宵礼」以后。（这是）你们的三个时间。在这些时候以后子你们彼此间无妨。你们是彼此互相往来的。安拉如此着为你们解明各项表征。安拉是深知的，明哲的。","translator":"王静斋"},{"sura":24,"aya":59,"text":"你们的儿童达到成年的时候，当令他们请求许可，一如从前的人们请求许可。安拉如此着为你们解明他的表征。安拉是深知的，明哲的。","translator":"王静斋"},{"sura":24,"aya":60,"text":"一般不复希望结婚的老妪，无妨脱下衣服，不露装饰。她们的拘束是较好的。安拉是能听的，深知的。","translator":"王静斋"},{"sura":24,"aya":61,"text":"瞎子、瘸子、染病的、以及同类的人，不妨就食于自己的家里，或父亲家里，或母亲家里，或弟兄家里，或姊妹家里，或伯叔家里，或姑母家里，或舅父家里，或姨母家里，或是掌其锁钥的家里，或朋友家里。你们不妨同处或分开着取食。你们入室的时候，要向同人祝安。这是出自安拉清洁多福的庆祝。安拉如此着为你们解明种种表征，好教你们了解。","translator":"王静斋"},{"sura":24,"aya":62,"text":"穆民们只是一般归信安拉和他的使者的；他们和他处在一件共同的事务时候，非至得到他的许可，则不得走去。的确，求你许可的人，均是归信安拉和他的使者的。他们为着个人一部分事情求你许可的时候，你就准许自所意欲……的人；你就为他们求安拉饶恕，安拉是多恕的，特慈的。","translator":"王静斋"},{"sura":24,"aya":63,"text":"你们莫以使者唤你们，当若彼此相呼唤。安拉确知道你们其中一般互作掩护，悄然而去的人。当令违背圣命的人们防备遭患难或遭痛刑。","translator":"王静斋"},{"sura":24,"aya":64,"text":"须知天地间所有的惟安拉执掌。他确知道你们所处的情况。当那一日，他们返回于他，他对他们宣告他们所作的。安拉是深知万事的。","translator":"王静斋"},{"sura":25,"aya":1,"text":"赞主尊大；他把规范降给自己的仆人，好教他警告众世界。","translator":"王静斋"},{"sura":25,"aya":2,"text":"那掌天地的权柄的主，他未曾立子，在他也无一个于权柄上共同的。他造化万事，他规定它，切实地规定。","translator":"王静斋"},{"sura":25,"aya":3,"text":"他们撇去他取多主它们被造着不能造一事。它们不能为自身掌利害，它们也不能掌死生与复活。","translator":"王静斋"},{"sura":25,"aya":4,"text":"众不信的人说：（这只是他自造谎言；另有一伙人在这上扶助他，）他们确发动背义和谎话了。","translator":"王静斋"},{"sura":25,"aya":5,"text":"他们说：（{这}是众前人的故事。他求人书录，而朝夕宣读给他。）","translator":"王静斋"},{"sura":25,"aya":6,"text":"你说：（那知道天地的机密的主降下它了。他是多恕的，特慈的。 ）","translator":"王静斋"},{"sura":25,"aya":7,"text":"他们说：（这是什么使者，他吃食物并在街上行走?为何不降给他天使，而偕同他传警告，","translator":"王静斋"},{"sura":25,"aya":8,"text":"或是投给他财宝，或是他有就食的园林?）不义的人们说：（你们只追随一个受了幻惑的人。）","translator":"王静斋"},{"sura":25,"aya":9,"text":"看哪！他们是怎样地对你发比喻?他们迷误了，他们不能得一个途径。","translator":"王静斋"},{"sura":25,"aya":10,"text":"那真主很尊大了。他若意欲……了，便就赐给你优越这个的。诸河流于其下的天园；并赐给你宫殿。","translator":"王静斋"},{"sura":25,"aya":11,"text":"不然，他们不信复生日了。我已为不信复生日的人预备烈火。","translator":"王静斋"},{"sura":25,"aya":12,"text":"火狱自远方接纳他们的时候，他们就听到它的怒声与呼声。","translator":"王静斋"},{"sura":25,"aya":13,"text":"有时候，他们手脚受绑着从那里被掷到狭隘的地方。他们就在那里呼喊（毁灭，）","translator":"王静斋"},{"sura":25,"aya":14,"text":"今日之下，你们不要呼喊一次毁灭，你们要呼多的毁灭吧！","translator":"王静斋"},{"sura":25,"aya":15,"text":"你说：（这是至优的?或是许给一般敬慎的人永居的天园呢?）那天园是他们的报答与归所。","translator":"王静斋"},{"sura":25,"aya":16,"text":"他们在那里永享自所想望的。这是对你养主可求的期待。","translator":"王静斋"},{"sura":25,"aya":17,"text":"当那一日，主集合他们及他们撇开安拉所拜的。主说：（你们诱我的这些仆人迷误了，或是他们自行迷路呢?）","translator":"王静斋"},{"sura":25,"aya":18,"text":"他们说：（赞你清净！我们不应撇开你，另取一般爱友；只是你教他们和他们的列祖享受，而致他们忘却教戒，成为一伙沦落的了。）","translator":"王静斋"},{"sura":25,"aya":19,"text":"它们不信服你们所声称的。你们不能制止，也不能自助。你们之中背义的人，我将令他尝大刑。","translator":"王静斋"},{"sura":25,"aya":20,"text":"我在你以前所差的列使无不吃食物，并在街上行走。我以你们的一部分磨难一部分，你们可能忍耐吗? 你的养主是看得见的。","translator":"王静斋"},{"sura":25,"aya":21,"text":"一般不盼望见我的人说：（为何不向我们遣使，或是我们亲见养主。）他们心中确切自觉伟大了；他们倔强，而且大倔强了。","translator":"王静斋"},{"sura":25,"aya":22,"text":"当他们亲见天使的那一日，彼时，对为恶的人无有喜信。他们说：（该止的制止！）","translator":"王静斋"},{"sura":25,"aya":23,"text":"我回转至他们所行的举动，我就把它化为被汤的微尘。","translator":"王静斋"},{"sura":25,"aya":24,"text":"是日，天园的人是居处较优的，是息所至美的。","translator":"王静斋"},{"sura":25,"aya":25,"text":"在天连同白云崩裂。天使降临，切实地降临的那一日，","translator":"王静斋"},{"sura":25,"aya":26,"text":"是日真实的权柄为慈主所掌。那是在逆徒上困难的日子。","translator":"王静斋"},{"sura":25,"aya":27,"text":"在一部分背义的人咬自己的两手的那一日，他说：（唉！宁愿我曾随同使者取一个途径。","translator":"王静斋"},{"sura":25,"aya":28,"text":"哎！宁愿我未把某人当朋友。","translator":"王静斋"},{"sura":25,"aya":29,"text":"在教戒达至我以后，他确诱我迷误了。恶魔原是弃人的。）","translator":"王静斋"},{"sura":25,"aya":30,"text":"使者说：我的养主啊！我的族人以这古兰为可弃的。","translator":"王静斋"},{"sura":25,"aya":31,"text":"我就是这样地为每一个圣人安置一个为恶的仇敌。你的养主作引导与相助的已可满足了。","translator":"王静斋"},{"sura":25,"aya":32,"text":"众不信的人说：（因何不把古兰一起降给他。就是这样。我为的是以此坚定你的心，我一段一段地消停着发布它。","translator":"王静斋"},{"sura":25,"aya":33,"text":"他们每逢对你发一个比喻，我就把切实的与在注释上最好的拿给你。","translator":"王静斋"},{"sura":25,"aya":34,"text":"一般伏地着被集到火狱的人，均是地位至恶劣的，是至失去途径的。","translator":"王静斋"},{"sura":25,"aya":35,"text":"我确曾降给母撒经典，并委他弟兄——哈伦随他为辅。","translator":"王静斋"},{"sura":25,"aya":36,"text":"我说：（你俩去到一般不信我表征的民众！）而后我毁灭他们，切实地毁灭了。","translator":"王静斋"},{"sura":25,"aya":37,"text":"（并毁灭了）努海的族人，当他们不信列使的时候，我淹没他们了。我把他们作为对于世人的表征。我已为背义的人们备下痛刑了。","translator":"王静斋"},{"sura":25,"aya":38,"text":"（并毁灭了）阿代、赛母待：滥斯人，更有其间的若干代。","translator":"王静斋"},{"sura":25,"aya":39,"text":"我为着每一伙人发表些个比喻，我毁灭了每一伙，切实地毁灭。","translator":"王静斋"},{"sura":25,"aya":40,"text":"他们确到过那些落过恶雨的城池；他们岂未见过它吗?不然，他们不盼望复生。","translator":"王静斋"},{"sura":25,"aya":41,"text":"他们看见你的时候，只把你当作可戏的（说：）（这就是安拉派为使者的那个人吗?","translator":"王静斋"},{"sura":25,"aya":42,"text":"设不是我们安守这一切主宰了，几乎他使我们离开它们归未迷误了。）不久他们在亲见刑罚的时候，就知道谁是较为迷路的。","translator":"王静斋"},{"sura":25,"aya":43,"text":"你可看见那以个人的私见当主宰的人吗? 你岂能是保护他的呢?","translator":"王静斋"},{"sura":25,"aya":44,"text":"不然，你以为他们的大半能可听到或能可理解吗?他们只像是家畜。不然，均是至迷路的。","translator":"王静斋"},{"sura":25,"aya":45,"text":"你岂未看见你的养主怎样地张影?——设若他意欲……了，他必使它稳定我且以太阳给它作标准。","translator":"王静斋"},{"sura":25,"aya":46,"text":"而后我收缩它，徐徐地收缩。","translator":"王静斋"},{"sura":25,"aya":47,"text":"他那真主为你们以[夜]作遮覆。以[睡]作休息，以「白昼」作复起。","translator":"王静斋"},{"sura":25,"aya":48,"text":"他那真主在自己的慈惠以前，发出报喜信的风。我由上空降下清洁的水，","translator":"王静斋"},{"sura":25,"aya":49,"text":"而用它复活死城。我使所造化的家畜，与许多人类饮它。","translator":"王静斋"},{"sura":25,"aya":50,"text":"我确切在他们之中重复它了，好教他们受劝。但是世人的多半不肯，只有负恩罢了。","translator":"王静斋"},{"sura":25,"aya":51,"text":"设我意欲……了，我必在各城里派定一个传警告的。","translator":"王静斋"},{"sura":25,"aya":52,"text":"你莫顺从不信的人。你要以此抵抗他们，并且大抵抗。","translator":"王静斋"},{"sura":25,"aya":53,"text":"他那真主放任两海，这是解渴的甜水，那是味苦的咸水。他在两者之间造一堤障，与一个遮蔽的阻隔。","translator":"王静斋"},{"sura":25,"aya":54,"text":"他那真主自水上造化人类，而后使其结为宗亲与姻戚。你的养主是全能的。","translator":"王静斋"},{"sura":25,"aya":55,"text":"他们撇开安拉拜那无益无损的。 不信的人是忤其养主作臂助的。","translator":"王静斋"},{"sura":25,"aya":56,"text":"我惟遣你报喜信传警告者。","translator":"王静斋"},{"sura":25,"aya":57,"text":"你说！（我不在这事上向你们讨些工资；那意欲向其养主取一途径的人除外。 ）","translator":"王静斋"},{"sura":25,"aya":58,"text":"你当仰赖长生不死的主，你以赞他称颂清净吧！真主尽知其众仆的罪恶已可满足了。","translator":"王静斋"},{"sura":25,"aya":59,"text":"就是那在六日造化天地万有随后操纵[阿勒世]的主。普慈的主。你问知道它的主吧！","translator":"王静斋"},{"sura":25,"aya":60,"text":"每逢有人对他们说：（你们当叩拜慈主，）他们就说：（什么是慈主?我们叩拜你所命令我们的吗?）这（话）反增他们逃遁了。","translator":"王静斋"},{"sura":25,"aya":61,"text":"真主尊大，他在天上造化宿宫， 并在天上造灯，与明月。","translator":"王静斋"},{"sura":25,"aya":62,"text":"他那真主；为意欲受劝，或意欲感念的人造夜昼为循环的。","translator":"王静斋"},{"sura":25,"aya":63,"text":"慈主的仆人是那般在地上安然行走，愚人对他们交谈的时候，他们就说：和平；","translator":"王静斋"},{"sura":25,"aya":64,"text":"也是那般为其养主叩头立站着度夜的；","translator":"王静斋"},{"sura":25,"aya":65,"text":"也是那般说：（我们的养主啊，你为我们移转火狱的刑罚；）火狱的刑罚实是连绵的。","translator":"王静斋"},{"sura":25,"aya":66,"text":"的确，这住处与居所太恶了！","translator":"王静斋"},{"sura":25,"aya":67,"text":"也是那般每逢使用的时候，既不浪费，亦不吝啬，他是居间折中的。","translator":"王静斋"},{"sura":25,"aya":68,"text":"也是那般不併同安拉呼求另一受拜的。他们除去因为正义外，不杀业经安拉禁（杀）的人命，他们也不行奸。凡行这事的人，将遇见恶报；","translator":"王静斋"},{"sura":25,"aya":69,"text":"在复生日加倍罪刑他，他且卑贱着永居在那里。","translator":"王静斋"},{"sura":25,"aya":70,"text":"除非那悔罪，归信并作善举的人。这一般，安拉将易他们的罪恶为善行。安拉是多恕的，特慈的。","translator":"王静斋"},{"sura":25,"aya":71,"text":"那悔罪并作善举的人，他确归向安拉，切实地归向。","translator":"王静斋"},{"sura":25,"aya":72,"text":"那般人，不作伪见证；每逢经过无益的时候，自尊着过去。","translator":"王静斋"},{"sura":25,"aya":73,"text":"也是一般遇人劝以养主的表征时，未曾聋着瞎着倒在那上的。","translator":"王静斋"},{"sura":25,"aya":74,"text":"也是那般人，他们说：（我们的养主啊：你把借着我们的妻子（所得的）愉快赏给我们吧！你委我们作一般敬慎者的首领吧！）","translator":"王静斋"},{"sura":25,"aya":75,"text":"这些人由于忍耐蒙赐高室，并教他们在那里遇见庆贺与祝安，","translator":"王静斋"},{"sura":25,"aya":76,"text":"永居在那里。那住处与居所太好了！","translator":"王静斋"},{"sura":25,"aya":77,"text":"你说：（设不是你们呼求了！我的养主必不重视你们。你们确是不信服了。不久刑罚是要粘附的。）","translator":"王静斋"},{"sura":26,"aya":1,"text":"（搭一、欺尼、密默。）","translator":"王静斋"},{"sura":26,"aya":2,"text":"这是表明的经典的文词。","translator":"王静斋"},{"sura":26,"aya":3,"text":"恐怕你是因他们不肯归信而伤己身的。","translator":"王静斋"},{"sura":26,"aya":4,"text":"我若意欲……了，我就从天上降给他们异迹，他们的脖子是服从它的。","translator":"王静斋"},{"sura":26,"aya":5,"text":"每逢有出自慈主的一项新的教戒到达时，他们就远避它。","translator":"王静斋"},{"sura":26,"aya":6,"text":"他们确实不信了。他们所嘲笑的种种消息即将来到。","translator":"王静斋"},{"sura":26,"aya":7,"text":"他们岂未看见地，我在那里发长各类贵物。","translator":"王静斋"},{"sura":26,"aya":8,"text":"此中确含有一种表征。他们的多半不是归信的。","translator":"王静斋"},{"sura":26,"aya":9,"text":"你的养主确是优胜的，特慈的。","translator":"王静斋"},{"sura":26,"aya":10,"text":"昔时，你的养主晓谕母撒：（你去到背义的民众，","translator":"王静斋"},{"sura":26,"aya":11,"text":"就是匪喇傲的民众那里，他们怎不畏惧呢?）","translator":"王静斋"},{"sura":26,"aya":12,"text":"母撒说：（我的养主啊！我实骇怕他们不信我，","translator":"王静斋"},{"sura":26,"aya":13,"text":"我心感觉窄狭。我的口舌不能发言。求你遣使去约哈伦。","translator":"王静斋"},{"sura":26,"aya":14,"text":"我负着对他们一件罪恶。恐怕，他们杀我。）","translator":"王静斋"},{"sura":26,"aya":15,"text":"主说：（不，你俩藉我的异迹前往，我是一定随同你们听到的。）","translator":"王静斋"},{"sura":26,"aya":16,"text":"他俩来到匪喇傲说：（我们是调养众世界的主差来的；","translator":"王静斋"},{"sura":26,"aya":17,"text":"要你解放以斯拉衣来的子孙随我们同去！","translator":"王静斋"},{"sura":26,"aya":18,"text":"匪喇傲说：（你作赤子的时候，未在我们这里受过抚育吗? 你生平在我们这里住过若干年，","translator":"王静斋"},{"sura":26,"aya":19,"text":"你行过所行的事情；你是忘恩一类的人。）","translator":"王静斋"},{"sura":26,"aya":20,"text":"母撒说：彼时，我行那事，正在无知着；","translator":"王静斋"},{"sura":26,"aya":21,"text":"嗣后我因为骇怕你们而逃走了。后来我的养主赏给我知识，委我作使者。","translator":"王静斋"},{"sura":26,"aya":22,"text":"你拿以斯拉衣来的子孙当奴才，这是你对我施恩吗？","translator":"王静斋"},{"sura":26,"aya":23,"text":"匪喇傲说：（什么是调养众世界的主?）","translator":"王静斋"},{"sura":26,"aya":24,"text":"母撒说：（就是调养天地万有的主。如果你们是坚信的了，……。）","translator":"王静斋"},{"sura":26,"aya":25,"text":"他对其左右的人说：（你们没听见吗?）","translator":"王静斋"},{"sura":26,"aya":26,"text":"母撒说：（调养你们的主，也是调养你们前代列祖的主。）","translator":"王静斋"},{"sura":26,"aya":27,"text":"他说：（你们这个被差的使者，一定是个疯子。）","translator":"王静斋"},{"sura":26,"aya":28,"text":"母撒说：（调养东方西方及两者之间的主，如果你们了解了，……。）","translator":"王静斋"},{"sura":26,"aya":29,"text":"匪喇傲说：（如果你以除过我的当主了，我必教你坐监）","translator":"王静斋"},{"sura":26,"aya":30,"text":"母撒说：（设若我把明显的事情现给你，你肯那样吗?）","translator":"王静斋"},{"sura":26,"aya":31,"text":"匪喇傲说：（如果你的所言属实，你就现出那事来。）","translator":"王静斋"},{"sura":26,"aya":32,"text":"母撤遂就掷出自己的木杖，那木杖立时化为明明的巨蛇。","translator":"王静斋"},{"sura":26,"aya":33,"text":"他伸出手来，那手立时在观众看着是洁白的，","translator":"王静斋"},{"sura":26,"aya":34,"text":"他对左右的伟人们说：（这人实是一个老练的术士；","translator":"王静斋"},{"sura":26,"aya":35,"text":"他要用其邪术把你们逐出本地，你们怎样地主张呢?）","translator":"王静斋"},{"sura":26,"aya":36,"text":"他们说：（你暂且教他和他的弟兄退去，你把招募者派在各城里，","translator":"王静斋"},{"sura":26,"aya":37,"text":"他们把一切老练的术士带到你跟前。）","translator":"王静斋"},{"sura":26,"aya":38,"text":"而后术士们在规定的日期集合起来。","translator":"王静斋"},{"sura":26,"aya":39,"text":"有的对众人说：（你们集合起来了吗?","translator":"王静斋"},{"sura":26,"aya":40,"text":"我们盼望追从众术士，如果他们获胜的时候。）","translator":"王静斋"},{"sura":26,"aya":41,"text":"在术士们来到的时侯，就对匪喇傲说：（如果我们得胜，可能获赏吗?）","translator":"王静斋"},{"sura":26,"aya":42,"text":"他说：（是的，到那时候，你们一定是接近的。","translator":"王静斋"},{"sura":26,"aya":43,"text":"母撒对他们说：（你们掷出所欲掷出的吧，）","translator":"王静斋"},{"sura":26,"aya":44,"text":"他们遂就掷出自己的绳杖，说：（指着匪喇傲的荣耀为誓：我们一定是获胜的。）","translator":"王静斋"},{"sura":26,"aya":45,"text":"母撒掷出他的手杖，那手杖倏然吞没他们所造作的。","translator":"王静斋"},{"sura":26,"aya":46,"text":"于是众术士倒身下拜，","translator":"王静斋"},{"sura":26,"aya":47,"text":"说：（我们归信调养众世界的主，","translator":"王静斋"},{"sura":26,"aya":48,"text":"就是调养母撒与哈伦的主。）","translator":"王静斋"},{"sura":26,"aya":49,"text":"匪喇傲说：（我尚未准许你们、你们就归信他吗?他必是教授你们邪术的一个伟人。一定不久的你们就知道。我必相异着断去你们的手脚；我必定把你们一同挂起，）","translator":"王静斋"},{"sura":26,"aya":50,"text":"他们说：（无妨，我们是将必返回养之的。","translator":"王静斋"},{"sura":26,"aya":51,"text":"我们实希望养主饶恕我们的差错，我们是首先归信的。）","translator":"王静斋"},{"sura":26,"aya":52,"text":"我曾默谕母撒：（你当偕我众仆夜行，你们定被追赶。）","translator":"王静斋"},{"sura":26,"aya":53,"text":"匪喇傲曾把招募者派在各城里。","translator":"王静斋"},{"sura":26,"aya":54,"text":"（说：）（这些人是少数的一伙；","translator":"王静斋"},{"sura":26,"aya":55,"text":"他们确实是惹我们怒恨的","translator":"王静斋"},{"sura":26,"aya":56,"text":"我们是一伙警觉的。）","translator":"王静斋"},{"sura":26,"aya":57,"text":"而后我把他们逐出园林、源泉、","translator":"王静斋"},{"sura":26,"aya":58,"text":"财宝、与高贵的所在。","translator":"王静斋"},{"sura":26,"aya":59,"text":"就是这样。我教以斯拉衣来的子孙继承它。","translator":"王静斋"},{"sura":26,"aya":60,"text":"他们就在日升的时候追上它们了。","translator":"王静斋"},{"sura":26,"aya":61,"text":"当两伙人相见的时候，母撒的同人们就说：（我们一定要被追及了。 ）","translator":"王静斋"},{"sura":26,"aya":62,"text":"母撤说：（不能，确有我的养主和我同在，他即将指导我。）","translator":"王静斋"},{"sura":26,"aya":63,"text":"我默谕母撒：（用你的手仗打海吧！）海遂就分开；每一段有如一座大山。","translator":"王静斋"},{"sura":26,"aya":64,"text":"我教另一伙人接近那里。","translator":"王静斋"},{"sura":26,"aya":65,"text":"我使母撒和他的同人均都得救了，","translator":"王静斋"},{"sura":26,"aya":66,"text":"而后我淹毙另一伙人。","translator":"王静斋"},{"sura":26,"aya":67,"text":"此中确含有一种表征。他们的大半不归信。","translator":"王静斋"},{"sura":26,"aya":68,"text":"你的养主实是优胜的，特慈的。","translator":"王静斋"},{"sura":26,"aya":69,"text":"你把易卜拉欣的消息诵给他们，","translator":"王静斋"},{"sura":26,"aya":70,"text":"那时候，他对他的父亲和族人们说：（你们拜什么?）","translator":"王静斋"},{"sura":26,"aya":71,"text":"他们就说：（我们拜偶像；我们是时常谨守它的。）","translator":"王静斋"},{"sura":26,"aya":72,"text":"他说：（你们呼求的时候，它们听得你们，","translator":"王静斋"},{"sura":26,"aya":73,"text":"或与你们有利有害吗?）","translator":"王静斋"},{"sura":26,"aya":74,"text":"他们说：（不，我们曾见自己列祖就是这样作。）","translator":"王静斋"},{"sura":26,"aya":75,"text":"易卜拉欣说：（你们曾看见自己和自己前代列祖所拜的吗?","translator":"王静斋"},{"sura":26,"aya":76,"text":".","translator":"王静斋"},{"sura":26,"aya":77,"text":"它们实是我的仇敌。惟有调养众世界的主，","translator":"王静斋"},{"sura":26,"aya":78,"text":"他造化我，而后引导我；","translator":"王静斋"},{"sura":26,"aya":79,"text":"那真主供我食，供我饮；","translator":"王静斋"},{"sura":26,"aya":80,"text":"我染病的时候，他医治我；","translator":"王静斋"},{"sura":26,"aya":81,"text":"那真主，使我死，复又使我生；","translator":"王静斋"},{"sura":26,"aya":82,"text":"那真主，我希望他在审判日饶恕我的差错。","translator":"王静斋"},{"sura":26,"aya":83,"text":"我的养主啊，你把学识赏给我，你教我联合一般义人。","translator":"王静斋"},{"sura":26,"aya":84,"text":"你赐给我美誉留在后人之间。","translator":"王静斋"},{"sura":26,"aya":85,"text":"你把我列为继承乐园的。","translator":"王静斋"},{"sura":26,"aya":86,"text":"你饶恕我的父吧，他实是有错误一类的人。）","translator":"王静斋"},{"sura":26,"aya":87,"text":"在复起他们的那一天，你莫教我受辱，","translator":"王静斋"},{"sura":26,"aya":88,"text":"当那一日，资财子嗣无济；","translator":"王静斋"},{"sura":26,"aya":89,"text":"除却那乘着纯洁的心来至安拉的人。","translator":"王静斋"},{"sura":26,"aya":90,"text":"天园接近敬慎的人；","translator":"王静斋"},{"sura":26,"aya":91,"text":"火狱被现给迷误的人。","translator":"王静斋"},{"sura":26,"aya":92,"text":"有的对他们说：（你们舍去安拉所拜的哪里去了?","translator":"王静斋"},{"sura":26,"aya":93,"text":"他们能可相助你们，或是能可自卫吗?）","translator":"王静斋"},{"sura":26,"aya":94,"text":"他们和迷误的人，与以卜里厮的军兵，统被投在火狱中。","translator":"王静斋"},{"sura":26,"aya":95,"text":".","translator":"王静斋"},{"sura":26,"aya":96,"text":"他们在那里，彼此争持着说：","translator":"王静斋"},{"sura":26,"aya":97,"text":"（指着安拉为誓：我们实是在显著的迷误中。）","translator":"王静斋"},{"sura":26,"aya":98,"text":"那时候，我们列你们与调养众世界的主同等。","translator":"王静斋"},{"sura":26,"aya":99,"text":"诱我们迷误的，只是一般为恶的。","translator":"王静斋"},{"sura":26,"aya":100,"text":"在我们无有代求赦免的；","translator":"王静斋"},{"sura":26,"aya":101,"text":"也无有一个恳挚的朋友。","translator":"王静斋"},{"sura":26,"aya":102,"text":"但盼我们有一次复返，而属于归信的。","translator":"王静斋"},{"sura":26,"aya":103,"text":"此中确含有一种表征。他们的多半不归信。","translator":"王静斋"},{"sura":26,"aya":104,"text":"你的养主，实是优胜的，特慈的。","translator":"王静斋"},{"sura":26,"aya":105,"text":"努海的族人曾不信服众使了，","translator":"王静斋"},{"sura":26,"aya":106,"text":"那时候，他们的弟兄——努海，对他们说：（你们怎不畏惧呢?","translator":"王静斋"},{"sura":26,"aya":107,"text":"我实是你们忠诚的使者。","translator":"王静斋"},{"sura":26,"aya":108,"text":"你们当畏惧安拉。当服从我。","translator":"王静斋"},{"sura":26,"aya":109,"text":"我不以此向你们讨工资；我的工资惟在调养众世界的主上，","translator":"王静斋"},{"sura":26,"aya":110,"text":"所以你们当畏惧安拉，当服从我。 ）","translator":"王静斋"},{"sura":26,"aya":111,"text":"他们说：（有这些卑贱的人追随着你，我们能肯信你吗?）","translator":"王静斋"},{"sura":26,"aya":112,"text":"他说：（我不知他们所作的。","translator":"王静斋"},{"sura":26,"aya":113,"text":"对他们审算惟在调养我的主，设若你们觉悟了……。","translator":"王静斋"},{"sura":26,"aya":114,"text":"我绝不是驱逐众穆民的。","translator":"王静斋"},{"sura":26,"aya":115,"text":"我只是一个公开传警告的。）","translator":"王静斋"},{"sura":26,"aya":116,"text":"他们说：（努海啊！如果你不停止了，你就必被乱石击死。）","translator":"王静斋"},{"sura":26,"aya":117,"text":"他说：（我的养主啊！我的族人确不信服我了，","translator":"王静斋"},{"sura":26,"aya":118,"text":"你在我们彼此间判断，切实地判断吧！你解救我和追随我的众穆民：）","translator":"王静斋"},{"sura":26,"aya":119,"text":"于是我解救他和随他同乘满载的船者。","translator":"王静斋"},{"sura":26,"aya":120,"text":"在这以后我又淹没其余的人。","translator":"王静斋"},{"sura":26,"aya":121,"text":"此中确含有一种表征，他们的多半不信。","translator":"王静斋"},{"sura":26,"aya":122,"text":"你的养主实是优胜的，特慈的。","translator":"王静斋"},{"sura":26,"aya":123,"text":"阿代曾不信服列使了。","translator":"王静斋"},{"sura":26,"aya":124,"text":"那时候，他们的弟兄——扈代对他们说：（你们怎不畏惧呢?","translator":"王静斋"},{"sura":26,"aya":125,"text":"我实是你们忠诚的使者；","translator":"王静斋"},{"sura":26,"aya":126,"text":"你们当畏惧安拉，当服从我。","translator":"王静斋"},{"sura":26,"aya":127,"text":"我不向你们讨工资；我的工资惟在调养众世界的主上。","translator":"王静斋"},{"sura":26,"aya":128,"text":"你们岂可在各高地上建筑标记，作娱乐，","translator":"王静斋"},{"sura":26,"aya":129,"text":"并修造水沟，希望久居，","translator":"王静斋"},{"sura":26,"aya":130,"text":"你们伸手的时候，便就残暴着伸手？","translator":"王静斋"},{"sura":26,"aya":131,"text":"你们当畏惧安拉，当服从我。","translator":"王静斋"},{"sura":26,"aya":132,"text":"你们当畏惧那真主，他以你们所知道的相助你们………","translator":"王静斋"},{"sura":26,"aya":133,"text":"他助你们以家畜，子嗣、","translator":"王静斋"},{"sura":26,"aya":134,"text":"园林、源泉。","translator":"王静斋"},{"sura":26,"aya":135,"text":"我实为你们骇怕大日的刑罚。","translator":"王静斋"},{"sura":26,"aya":136,"text":"他们说：（你劝与不劝，于我们相等。","translator":"王静斋"},{"sura":26,"aya":137,"text":"这不过是前人的习惯。","translator":"王静斋"},{"sura":26,"aya":138,"text":"我们必不会遭受罪刑的。）","translator":"王静斋"},{"sura":26,"aya":139,"text":"于是他们不信他而后我灭绝他们了。此中确含有一种表征，他们的多半不归信。","translator":"王静斋"},{"sura":26,"aya":140,"text":"调养你的主实是优胜的，特慈的。","translator":"王静斋"},{"sura":26,"aya":141,"text":"赛母待曾不信服列使了。","translator":"王静斋"},{"sura":26,"aya":142,"text":"那时候，他们的弟兄——沙里哈，对他们说：（你们怎不畏惧呢?","translator":"王静斋"},{"sura":26,"aya":143,"text":"我实是你们忠诚的使者。","translator":"王静斋"},{"sura":26,"aya":144,"text":"你们当畏惧安拉，当服从我。","translator":"王静斋"},{"sura":26,"aya":145,"text":"我不向你们讨工资，我的工资惟在调养众世界的土上。","translator":"王静斋"},{"sura":26,"aya":146,"text":"你们可能安然着被弃在这里，","translator":"王静斋"},{"sura":26,"aya":147,"text":"就是园林、源泉、","translator":"王静斋"},{"sura":26,"aya":148,"text":"禾谷与那叶鞘细嫩的枣树里吗?","translator":"王静斋"},{"sura":26,"aya":149,"text":"你们精巧地就山斫室。","translator":"王静斋"},{"sura":26,"aya":150,"text":"你们当畏惧安拉，当服从我。","translator":"王静斋"},{"sura":26,"aya":151,"text":"不得服从那般妄为者的命令，","translator":"王静斋"},{"sura":26,"aya":152,"text":"就是那在地上造恶并不规正的人。）","translator":"王静斋"},{"sura":26,"aya":153,"text":"他们说：（你只是受了幻惑的，","translator":"王静斋"},{"sura":26,"aya":154,"text":"你只是和我们一样的人。如果你的所言属实，你就现出一个异迹来，）","translator":"王静斋"},{"sura":26,"aya":155,"text":"他说：（这是一只母驼，在它应获一分饮料，在你们有一定日期的饮料，","translator":"王静斋"},{"sura":26,"aya":156,"text":"你们不要加害它，而致自遭大日的刑罚。）","translator":"王静斋"},{"sura":26,"aya":157,"text":"而后他们宰死它了，他们遂就追悔，","translator":"王静斋"},{"sura":26,"aya":158,"text":"而致遭受刑罚了。此中确含有一种表征，他们的多半不信。","translator":"王静斋"},{"sura":26,"aya":159,"text":"调养你的主，实是优胜的，特慈的。","translator":"王静斋"},{"sura":26,"aya":160,"text":"鲁脱的族人曾不信服列使了。","translator":"王静斋"},{"sura":26,"aya":161,"text":"那时候，他们的弟兄——鲁脱对他们说：（你们怎不畏惧呢?","translator":"王静斋"},{"sura":26,"aya":162,"text":"我实是你们忠诚的使者。","translator":"王静斋"},{"sura":26,"aya":163,"text":"你们当畏惧安拉，当服从我。","translator":"王静斋"},{"sura":26,"aya":164,"text":"我不以此向你们讨工资，我的工资惟在调养众世界的主上。","translator":"王静斋"},{"sura":26,"aya":165,"text":"你们岂可亲近世上的男子，","translator":"王静斋"},{"sura":26,"aya":166,"text":"而放弃养主为你们造化的女子? 你们实是一伙越范的人。）","translator":"王静斋"},{"sura":26,"aya":167,"text":"他们说：（鲁脱啊！如果你不停止，你必归为被逐的。）","translator":"王静斋"},{"sura":26,"aya":168,"text":"鲁脱说：（我确实怒恨你们的行为。","translator":"王静斋"},{"sura":26,"aya":169,"text":"我的养主啊，你解救我和我的眷属脱离他们的作为吧！）","translator":"王静斋"},{"sura":26,"aya":170,"text":"而后我解救他和他的眷属们全体；","translator":"王静斋"},{"sura":26,"aya":171,"text":"只有一个老媪，列在下余的以内，","translator":"王静斋"},{"sura":26,"aya":172,"text":"而后，我毁灭另一伙人。","translator":"王静斋"},{"sura":26,"aya":173,"text":"我给他们落石雨。受过警告的人的雨太恶了，","translator":"王静斋"},{"sura":26,"aya":174,"text":"此中确含有一种表征，他们的多半不信。","translator":"王静斋"},{"sura":26,"aya":175,"text":"你的养主实是优胜的，特慈的。","translator":"王静斋"},{"sura":26,"aya":176,"text":"哀凯人曾不信服列使了。","translator":"王静斋"},{"sura":26,"aya":177,"text":"那时候，书尔布对他们说：（你们怎不畏惧呢?","translator":"王静斋"},{"sura":26,"aya":178,"text":"我实是你们忠诚的使者。","translator":"王静斋"},{"sura":26,"aya":179,"text":"你们当畏惧安拉，当服从我。","translator":"王静斋"},{"sura":26,"aya":180,"text":"我不以此向你们讨工资，我的工资惟在调养众世界的主上。","translator":"王静斋"},{"sura":26,"aya":181,"text":"你们当给足量具，不得减少。","translator":"王静斋"},{"sura":26,"aya":182,"text":"你们要称以公秤，","translator":"王静斋"},{"sura":26,"aya":183,"text":"你们莫少给人东西。你们不得在地上造恶着过为。","translator":"王静斋"},{"sura":26,"aya":184,"text":"你们当畏惧那造化你们与众前代的主。）","translator":"王静斋"},{"sura":26,"aya":185,"text":"他们说：（你不外是受了幻惑的；","translator":"王静斋"},{"sura":26,"aya":186,"text":"你不过是和我们一样的人。我们揣度你一定是谎言的。","translator":"王静斋"},{"sura":26,"aya":187,"text":"如果你是实言的，你就把一块天落到我们上。）","translator":"王静斋"},{"sura":26,"aya":188,"text":"他说：（我的养主至知你们的作为。）","translator":"王静斋"},{"sura":26,"aya":189,"text":"但是他们不信他。而后，他们遭遇云影日的刑罚了。那确是大日的刑罚。","translator":"王静斋"},{"sura":26,"aya":190,"text":"此中确含有一种表征。他们的多半不信。","translator":"王静斋"},{"sura":26,"aya":191,"text":"你的养主实足优胜的，特慈的。","translator":"王静斋"},{"sura":26,"aya":192,"text":"的确，它是调养众世界的主降下的。","translator":"王静斋"},{"sura":26,"aya":193,"text":"忠实的「鲁哈」借着显明的阿拉伯语把它降在你的心上，","translator":"王静斋"},{"sura":26,"aya":194,"text":"冀使你归为传警告的。","translator":"王静斋"},{"sura":26,"aya":195,"text":".","translator":"王静斋"},{"sura":26,"aya":196,"text":"的确，它是载在众前人册籍的。","translator":"王静斋"},{"sura":26,"aya":197,"text":"以斯拉衣来子孙中的学者们知道它，这岂非是对他们的一种明证吗?","translator":"王静斋"},{"sura":26,"aya":198,"text":"设若我把它降给一部分外邦人，","translator":"王静斋"},{"sura":26,"aya":199,"text":"将它诵给他们，他们绝不信它。","translator":"王静斋"},{"sura":26,"aya":200,"text":"我就这样地使不信实输入到一般为恶者的心中。","translator":"王静斋"},{"sura":26,"aya":201,"text":"他们非至亲见痛刑，必不归信它。","translator":"王静斋"},{"sura":26,"aya":202,"text":"而后，当他们不觉悟的时候，刑罚突然而至。","translator":"王静斋"},{"sura":26,"aya":203,"text":"他们就说：（我们可能得到姑客吗?）","translator":"王静斋"},{"sura":26,"aya":204,"text":"他们岂可要求立时发现刑罚呢？","translator":"王静斋"},{"sura":26,"aya":205,"text":"要你告诉我： 如果我教他们享受多年，","translator":"王静斋"},{"sura":26,"aya":206,"text":"而后他们遭遇所惊吓的，","translator":"王静斋"},{"sura":26,"aya":207,"text":"他们所享受的不能代为避免。","translator":"王静斋"},{"sura":26,"aya":208,"text":"我每逢灭绝一个城池，必先有人因着劝化而作警告，","translator":"王静斋"},{"sura":26,"aya":209,"text":"我不是施行不义的。","translator":"王静斋"},{"sura":26,"aya":210,"text":"恶魔们未曾降它；","translator":"王静斋"},{"sura":26,"aya":211,"text":"那于他们不相宜；他们也不能作到。","translator":"王静斋"},{"sura":26,"aya":212,"text":"他们实是被点不得侧耳的。","translator":"王静斋"},{"sura":26,"aya":213,"text":"你不要并同安拉呼求另一受拜的，而致遭受刑罚。","translator":"王静斋"},{"sura":26,"aya":214,"text":"你警告自己的近族：","translator":"王静斋"},{"sura":26,"aya":215,"text":"你当对于随从你的众穆民表示谦逊。","translator":"王静斋"},{"sura":26,"aya":216,"text":"如果他们违背你了，你就说！（我实是对于你们的作为无干的。）","translator":"王静斋"},{"sura":26,"aya":217,"text":"你当仰赖那优胜特慈的主。那个主。","translator":"王静斋"},{"sura":26,"aya":218,"text":"他在你站起的时候看见你，","translator":"王静斋"},{"sura":26,"aya":219,"text":"并见你在叩头的人中转动着。","translator":"王静斋"},{"sura":26,"aya":220,"text":"的确，他是能见的，深知的。","translator":"王静斋"},{"sura":26,"aya":221,"text":"我可以告诉你们；恶魔们下到什么人上吗?","translator":"王静斋"},{"sura":26,"aya":222,"text":"他下到一切说谎作罪的人上。","translator":"王静斋"},{"sura":26,"aya":223,"text":"他们侧耳。他们大多数是说谎话的。","translator":"王静斋"},{"sura":26,"aya":224,"text":"诗家只有迷路的人追随他们。","translator":"王静斋"},{"sura":26,"aya":225,"text":"你岂未见他们彷徨在各山谷里，","translator":"王静斋"},{"sura":26,"aya":226,"text":"他们说自所不干的吗?","translator":"王静斋"},{"sura":26,"aya":227,"text":"除非是一般归信并作善举，多记念安拉，且在受人迫害后取报复的人。行亏的人们即将知道，自己将要转到那个地方去?","translator":"王静斋"},{"sura":27,"aya":1,"text":"搭一、斯呢。 这是古兰与表明的经典的文词，","translator":"王静斋"},{"sura":27,"aya":2,"text":"引道并为众穆民报喜信。","translator":"王静斋"},{"sura":27,"aya":3,"text":"那般人，立拜功，施天课，并且坚信后世。","translator":"王静斋"},{"sura":27,"aya":4,"text":"不信后世的人，我已为他们饰其作为了，所以他们均在茫昧着。","translator":"王静斋"},{"sura":27,"aya":5,"text":"这些人应遭恶刑。在后世是至为伤折的。","translator":"王静斋"},{"sura":27,"aya":6,"text":"你确自明哲深知的真主上接受古兰。","translator":"王静斋"},{"sura":27,"aya":7,"text":"昔时，母撒对他的眷属说：（我确望见火了；我即将从那里把一种消息报告你们；或是我将燃着的「火把」拿给你们，以便你们取暖）","translator":"王静斋"},{"sura":27,"aya":8,"text":"及至母撒来到的时候，有的晓谕：（火中和其四周的人蒙幸福了，赞颂安拉 ——调养众世界的主清净！","translator":"王静斋"},{"sura":27,"aya":9,"text":"母撒呀!的确，我是安拉——优胜明哲的主。","translator":"王静斋"},{"sura":27,"aya":10,"text":"你掷出你的手杖去，）待他看见那手杖活跃如巨蛇的时候，他就退转不归了。母撒呀，不要骇怕。列使在我御前绝不畏惧。","translator":"王静斋"},{"sura":27,"aya":11,"text":"惟有那背义而在作恶以后复又改善的人，我是一定多恕的，特慈的。","translator":"王静斋"},{"sura":27,"aya":12,"text":"你把手插在衣领里，它就洁白无瑕着显现。列在九项异迹以内的。（你去到）匪喇傲及其民众。他们实是一伙恶民。","translator":"王静斋"},{"sura":27,"aya":13,"text":"当他们得到我显明的异迹时候，就说：（这是显著的邪术。）","translator":"王静斋"},{"sura":27,"aya":14,"text":"他们因着背义与高傲不肯承认，他们的心信它无疑。看哪！一般作恶者的结果如何?","translator":"王静斋"},{"sura":27,"aya":15,"text":"我确赐给达伍德与苏来茫知识了。他俩说：（赞颂安拉，他使我们优越自己的许多穆民仆人。）","translator":"王静斋"},{"sura":27,"aya":16,"text":"苏来茫继承达伍德，苏来茫说：（众人哪！我们已蒙教给禽语，并蒙赐以各物，这确是显著的特恩。）","translator":"王静斋"},{"sura":27,"aya":17,"text":"苏来茫的军兵——镇呢、人类与飞禽为他集结，均被排列起来。","translator":"王静斋"},{"sura":27,"aya":18,"text":"待至他们抵达蚂蚁谷的时候，有一个蚂蚁就说：（蚂蚁们哪！你们进窝去吧！勿被苏来茫和他的众兵于无意中损害你们。）","translator":"王静斋"},{"sura":27,"aya":19,"text":"苏来茫对它的话微笑了一笑，说：（我的养主啊！求你感悟我，谢你对我并对我父母的恩典，并作你所喜爱的善举。求你依自己的慈惠把我列在你的义仆以内）","translator":"王静斋"},{"sura":27,"aya":20,"text":"他检阅飞禽，说：（我怎么看不见呼哒呼哒呢? 它是不在的吗?","translator":"王静斋"},{"sura":27,"aya":21,"text":"我必严惩它，或是屠宰它，或是它对我现出一种明证。","translator":"王静斋"},{"sura":27,"aya":22,"text":"它停留不远，遂就说：（我望见你不能望见的，我把塞白的一个确实的消息报告给你。","translator":"王静斋"},{"sura":27,"aya":23,"text":"我得见一个女子，给他们为王；她曾蒙赐各类物资。在她有一大宝座。","translator":"王静斋"},{"sura":27,"aya":24,"text":"我曾见她的民众撇去安拉朝拜太阳。恶魔为他们装饰他们的作为；并阻止他们走正路；所以他们不能得正道。","translator":"王静斋"},{"sura":27,"aya":25,"text":"他们怎不叩拜那发现天地间所蕴藏的，并知道你们所隐所显的主呢?","translator":"王静斋"},{"sura":27,"aya":26,"text":"安拉除他掌尊大的「阿勒世」的主以外，再无一个受拜的","translator":"王静斋"},{"sura":27,"aya":27,"text":"苏来茫说：（我们即将察看你说实言了，或者你是说谎的呢?","translator":"王静斋"},{"sura":27,"aya":28,"text":"你携去我这书信投给他们，你遂就回转，而看他们怎样答复?）","translator":"王静斋"},{"sura":27,"aya":29,"text":"女王说：（众伟人哪！我接到一件光荣的书，","translator":"王静斋"},{"sura":27,"aya":30,"text":"确是由苏来茫那里来的。那话是：奉普慈特慈安拉之名；","translator":"王静斋"},{"sura":27,"aya":31,"text":"你们不得对我傲慢，要你们顺服着来到我这里。）","translator":"王静斋"},{"sura":27,"aya":32,"text":"女王说：（众伟人哪！请你们为我的事作一个决定，非至你们亲临到我这里，我向来不决断一事。）","translator":"王静斋"},{"sura":27,"aya":33,"text":"他们说：（我们是有力量的，是勇猛善战的。事情归你主张，你看！应作何吩咐?）","translator":"王静斋"},{"sura":27,"aya":34,"text":"女王说；（帝王们每入一城的时候，他们就捣毁它；使那居民的上人成为下人。他们就是这样的作。","translator":"王静斋"},{"sura":27,"aya":35,"text":"我赠给他们大宗礼品，而看差役们带回什么来?）","translator":"王静斋"},{"sura":27,"aya":36,"text":"差人抵达苏来茫的时候，苏来茫就说：（你们用资财来扶助我吗? 安拉赐给我的，强过那赐给你们的；实是你们以自己的赠品狂喜。","translator":"王静斋"},{"sura":27,"aya":37,"text":"你返回他们吧！我必率无敌的军兵前去，我必把他们自那里卑贱蒙辱着逐出。 ）","translator":"王静斋"},{"sura":27,"aya":38,"text":"他说：（众伟人！你们哪一个在他们未顺服着来我这里以前，给我取来她的宝座?）","translator":"王静斋"},{"sura":27,"aya":39,"text":"一个镇呢之类的污浊魔鬼说：（我在你未从本位站起以前把它取给你，我实是对于这事忠实有力的。）","translator":"王静斋"},{"sura":27,"aya":40,"text":"一个具有天经知识的说：（我能在你一转瞬之前，把它给你取到。）及其看见宝座稳定在自己面前的时候，他就说：（这是属我养主的特恩；为的是试验我，我感念，或是忘恩呢?感念的人，惟利他自身感念；忘恩的人，我的养主确是无求的，仁慈的。）","translator":"王静斋"},{"sura":27,"aya":41,"text":"他说：（你们变更她的宝座的形状，我们看她明白或不明白呢?）","translator":"王静斋"},{"sura":27,"aya":42,"text":"及至她来到的时候，有的说：（这是你的宝座吗?）她说：（好像是它，从前我们蒙赐知识了！我们原是顺服的。）","translator":"王静斋"},{"sura":27,"aya":43,"text":"她被那撇开安拉所拜的阻止了，她实属于逆民。","translator":"王静斋"},{"sura":27,"aya":44,"text":"有的对她说：（你进宫殿吧！）及她看见宫殿的时候，就以它为池塘，而由双胫上撂起了。苏来茫诜：（这确是光滑的玻璃宫。）她说：（我的养主啊，我曾亏负自身了；（而今）我随同苏来茫顺服安拉，调养众世界的主了。）","translator":"王静斋"},{"sura":27,"aya":45,"text":"我确向赛母待遣去他们的弟兄——沙里哈。（他说： ）（你们当拜安拉，）他们立刻是两伙互相争论的人。","translator":"王静斋"},{"sura":27,"aya":46,"text":"他说：（我的族人哪，你们为什么在幸福以前忙讨祸患呢?你们为什么不求安拉饶恕，冀使你们蒙得慈惠。 ）","translator":"王静斋"},{"sura":27,"aya":47,"text":"他们说：「我们由于你和随同你的人遭逢不吉了。」他说：「你们的不吉祥操自安拉。你们实是一伙遭受磨难的。」","translator":"王静斋"},{"sura":27,"aya":48,"text":"城內有九个人在地上造恶，并不守正。","translator":"王静斋"},{"sura":27,"aya":49,"text":"他们指着安拉互相发誓，说：「我们必在夜內谋害他，和他的眷属。而后我们对他的亲属说：我们未曾亲去损害他的眷属；我们确是实言的。」","translator":"王静斋"},{"sura":27,"aya":50,"text":"他们施诡计，切实地施诡计。我乘他们不觉悟的时候，报复诡计切实地报复诡计。","translator":"王静斋"},{"sura":27,"aya":51,"text":"看哪，他们的诡计的结果如何？我确把他们和他们族人一併毀灭了。","translator":"王静斋"},{"sura":27,"aya":52,"text":"这是他们的居室，由于他们的背义已在倾圮着。此中确对于有知识的民众，含有一种表征。","translator":"王静斋"},{"sura":27,"aya":53,"text":"我解救一般归信并且敬慎的人。","translator":"王静斋"},{"sura":27,"aya":54,"text":"鲁脱，那时候他对自己的族人说：「你们相观着行丑事吗?","translator":"王静斋"},{"sura":27,"aya":55,"text":"你们确是为着性欲弃女子而亲近男子了。不然，你们是一伙愚民。」","translator":"王静斋"},{"sura":27,"aya":56,"text":"他族人的答复不外是说：「你们把鲁脱的一家人逐出本城去！他们实是一伙子好洁的人。」","translator":"王静斋"},{"sura":27,"aya":57,"text":"而后我解救他和他的眷属了，除去他的妻，我预定她归为下余的。","translator":"王静斋"},{"sura":27,"aya":58,"text":"我给他们降下恶雨。受过警告的人的雨太恶了，","translator":"王静斋"},{"sura":27,"aya":59,"text":"你说，赞颂安拉！平安是在他自选的众仆上。安拉是至优的。或是他们所信的匹偶呢?","translator":"王静斋"},{"sura":27,"aya":60,"text":"不然，是那造化天地，为你们由上空降水而用以发生美丽的园林的主呢? 那园里的树不是你们能使它生长的。可有一个受拜的和安拉共同吗?不然，他们是一伙举对象的。","translator":"王静斋"},{"sura":27,"aya":61,"text":"不然，谁是置地为居所，于中造河道，为地造定山，更在两海之间造隔障的主?可有一个受拜的和安拉共同吗?不然，他们的多半不知道。","translator":"王静斋"},{"sura":27,"aya":62,"text":"不然，当无奈何的人呼求的时候，谁能应承他，释去患难，并教你们作地上替代的? 可有一个受拜的和安拉共同吗?你们绝少受劝。","translator":"王静斋"},{"sura":27,"aya":63,"text":"不然，谁在陆海种种的黑暗中指导你们，谁在他的慈惠以前发出报喜信的风? 可有一个受拜的和安拉共同吗?安拉玄高于他们所举的匹偶了！","translator":"王静斋"},{"sura":27,"aya":64,"text":"不然，谁造化万有而又恢复它?又谁就着天地赐给你们给养?可有一个受拜的和安拉共同吗?你说！如果你们的所言属实了，你们就举出自己的明证来：","translator":"王静斋"},{"sura":27,"aya":65,"text":"你说：（除去安拉，天地间的人不知目不能见的，且不知道将在何时复起。）","translator":"王静斋"},{"sura":27,"aya":66,"text":"不然，他们对于后世的知识太少了。不然，他们对它疑惑。不然，他们是对它盲昧的。","translator":"王静斋"},{"sura":27,"aya":67,"text":"不信的人说：（我们和自己列祖既已土化的时候，必被现出吗?","translator":"王静斋"},{"sura":27,"aya":68,"text":"我们和自己列祖从前曾受过此约。这只是众前人的故事。）","translator":"王静斋"},{"sura":27,"aya":69,"text":"你说：（你们在地上游行，而观察一般为恶者的结果如何?）","translator":"王静斋"},{"sura":27,"aya":70,"text":"不要忧虑他们，你对于他们的计谋不要心窄。","translator":"王静斋"},{"sura":27,"aya":71,"text":"他们说：（这约是在何时?如果你们的所言属实了，……。）","translator":"王静斋"},{"sura":27,"aya":72,"text":"你说！（恐怕你们要求立时实现的一部分，快要来临了。 ）","translator":"王静斋"},{"sura":27,"aya":73,"text":"你的养主确是有恩于众人的，但是他们的多半不知感念。","translator":"王静斋"},{"sura":27,"aya":74,"text":"的确，你的养主知道他们胸中所隐匿的，和他们显扬的。","translator":"王静斋"},{"sura":27,"aya":75,"text":"天地间所有的隐秘，均记在明典中。","translator":"王静斋"},{"sura":27,"aya":76,"text":"这古兰对以嘶拉衣来的子孙分析他们所争议的许多。","translator":"王静斋"},{"sura":27,"aya":77,"text":"的确，它是引导与慈惠众穆民的。","translator":"王静斋"},{"sura":27,"aya":78,"text":"的确，你的养主必依自己的判断在他们中间判明。他是优胜的，深知的。","translator":"王静斋"},{"sura":27,"aya":79,"text":"你当仰赖安拉。你确是、在显著的正义上。","translator":"王静斋"},{"sura":27,"aya":80,"text":"的确，你不能使死者听到；在聋子退转的时候，你不能使他们听到呼喊。","translator":"王静斋"},{"sura":27,"aya":81,"text":"你绝不能引导瞎子脱开迷误。你只能教那信我表征的人听到；所以他们是顺服的。","translator":"王静斋"},{"sura":27,"aya":82,"text":"当他们遭遇刑罚的时候，我为他们在地上现出一个动物对他们发言；只因众人不深信我的表征。","translator":"王静斋"},{"sura":27,"aya":83,"text":"当那一日，我自每一伙人里复起一群不信我表征的人，均被排列起来。","translator":"王静斋"},{"sura":27,"aya":84,"text":"待至他们来到的时候，主说：（你们对我的表征不深知道，就可不信它吗?你们干下的是些什么?）","translator":"王静斋"},{"sura":27,"aya":85,"text":"他们由于背义定遭罪刑，而致不能发言。","translator":"王静斋"},{"sura":27,"aya":86,"text":"他们岂未看见：凝造夜，好教他们在它里安歇，并造化光明的白昼?此中对于有信仰的民众确含有种种表征。","translator":"王静斋"},{"sura":27,"aya":87,"text":"当那一日，吹起了号筒，既而在天的在地的震惊起来。安拉意欲……的人在外。每一个均卑贱着到他御前。","translator":"王静斋"},{"sura":27,"aya":88,"text":"你看山，你以为它是固定的，（其实）它过往就像云的过往。（这）是那坚固万事的安拉的造化，他尽知你们的行为。","translator":"王静斋"},{"sura":27,"aya":89,"text":"举行一项善事的人可因它获一件好处，当那一日，他们是安然无恐的。","translator":"王静斋"},{"sura":27,"aya":90,"text":"发动一件恶事的人，他们颠倒着被投在火狱中，你们惟被报以自所作过的。","translator":"王静斋"},{"sura":27,"aya":91,"text":"我惟奉命敬拜掌理这座禁城的主；万物惟他执掌；","translator":"王静斋"},{"sura":27,"aya":92,"text":"我且奉命为穆思林一类的，并奉命诵读古兰。得正道的人，惟利自身得正道。迷误的人，你就说：（我不过是传警告的。 ）","translator":"王静斋"},{"sura":27,"aya":93,"text":"你说：（赞颂安拉！他即将把自己的表征现给你们，你们终将认识它。你的养主必不忽于你们的作为。）","translator":"王静斋"},{"sura":28,"aya":1,"text":"（搭一、斯呢、密木。）","translator":"王静斋"},{"sura":28,"aya":2,"text":"这是表明的经典的文词。","translator":"王静斋"},{"sura":28,"aya":3,"text":"我因为有正信的民众把母撒与匪喇傲的消息据实读给你。","translator":"王静斋"},{"sura":28,"aya":4,"text":"匪喇傲确在地上强霸了；他把地方的人分为几派。他压制他们其中的一伙；他宰死他们的男儿，留下他们的女儿。他实属造恶的人。","translator":"王静斋"},{"sura":28,"aya":5,"text":"我欲施惠于地上被压制的人们，我使他们作领袖，并使他们作继承者。","translator":"王静斋"},{"sura":28,"aya":6,"text":"我予他们在地上的权力。我教匪喇傲、哈曼和他俩的军兵，看见他们所防备的。","translator":"王静斋"},{"sura":28,"aya":7,"text":"我曾感召母撒的生母；（要你哺乳他，你因他害怕的时候，就把他投在大河中。不要害怕，不要忧虑。我必把他归还给你，并教他为使者。）","translator":"王静斋"},{"sura":28,"aya":8,"text":"随后，匪喇傲的党徒拾了他去，终至成为他们的仇敌与忧虑。的确，匪喇傲、哈曼与他俩的军兵统是错误的。","translator":"王静斋"},{"sura":28,"aya":9,"text":"匪喇傲的妇人说：（这是我与你的愉快，你们不要杀他，恐怕他于我们有益，或是我们把他当作儿子。）那时候，他们并不觉悟。","translator":"王静斋"},{"sura":28,"aya":10,"text":"母撒的母亲，心是空空的。若不是我稳定她的心而归为信者，差不多她就要吐露实情了。","translator":"王静斋"},{"sura":28,"aya":11,"text":"她对母撒的姊说：（你追随他） 她就从远处遥望他。那时候，他们并不觉悟。","translator":"王静斋"},{"sura":28,"aya":12,"text":"我先使他拒绝各乳母。他的姊说：（我对你们指告一家人，能忠实地为你们抚育这个小儿，好吗?）","translator":"王静斋"},{"sura":28,"aya":13,"text":"于是我把他归还他母亲，好教她愉快无忧；并知道安拉的约确实；但是他们的多半不知道。","translator":"王静斋"},{"sura":28,"aya":14,"text":"当他达到壮年并且雄健的时候，我就赐给他智慧与学识了。我就这样地赏赐一般为善的人。","translator":"王静斋"},{"sura":28,"aya":15,"text":"他在居民不知觉的时候走进城市。他在那里得见两个人决斗；这一个是他的同伙， 那一个是他的仇敌。他那个同伙的人求他援救，以对付他那仇敌。母撒打了那人一拳，遂就把他结果了。 他说：（这属于恶魔的作为。他实是公开诱人迷误的仇敌。","translator":"王静斋"},{"sura":28,"aya":16,"text":"他说： （养主啊！我确亏负自身了，你饶恕我吧！）主遂就饶恕他了，主实是多恕的，特慈的。","translator":"王静斋"},{"sura":28,"aya":17,"text":"他说：（我的养主啊！只以你对我的施恩，我绝不臂助为恶的人。）","translator":"王静斋"},{"sura":28,"aya":18,"text":"次晨，他正在城内骇怕等侯着，忽然那在昨日求他援助的人向他呼救。母撒对他说：（你实是一个显然迷误的人。）","translator":"王静斋"},{"sura":28,"aya":19,"text":"当他欲扑他俩的仇敌时候，那人就说：（母撒呀！你像昨天杀人似的意欲杀我吗?你只欲在此地行霸道，你不欲作调和的人。）","translator":"王静斋"},{"sura":28,"aya":20,"text":"城内远方的一个人匆忙走来， 说：（母撒呀！众伟人正在会议杀你，你出走吧：我是对你进忠告的。）","translator":"王静斋"},{"sura":28,"aya":21,"text":"母撒就骇怕等侯着由那里出来了。他说：（我的养主啊！你解救我脱离不义的民众吧，）","translator":"王静斋"},{"sura":28,"aya":22,"text":"当他奔向买大严方面的时候，他就说：（我的养主即将把我引至正道。）","translator":"王静斋"},{"sura":28,"aya":23,"text":"当他抵达买大严水的时候，就在那里得见一伙人饮（羊）。他得见他们跟前有两个女子，在拦阻着。他说：（你俩是什么事情?）她俩说：我们的父亲是一年迈的老者；我们非至牧人们转回以后不去饮（羊）。","translator":"王静斋"},{"sura":28,"aya":24,"text":"于是他就为她二人饮（羊），而后复归影下。遂说：（我的养主啊！我需求你垂给我的好处。）","translator":"王静斋"},{"sura":28,"aya":25,"text":"二女有一个羞惭着走来，说：（我父唤你，只为把你为我们饮（羊）的工资报酬给你。）及至他来到那一老者的跟前，对他述说已往的事情了，老者就说；（你不要骇怕，你已竟脱开不义的人民了。）","translator":"王静斋"},{"sura":28,"aya":26,"text":"二女子有一个说：（父啊！你雇下他吧！的确，你雇的人最好是有能力的忠诚的。）","translator":"王静斋"},{"sura":28,"aya":27,"text":"老者说：（我欲把我这两个女儿聘给你一个，限你给我佣工八年；你若全满十年了，那就在乎你自己，我不欲难为你。若是真主意欲……了，你即将见我是义人一类的。）","translator":"王静斋"},{"sura":28,"aya":28,"text":"母撒说：（这是我你之间的。我完成两个期限无论哪一个，在我并无怨恨。安拉见证我们所说的。）","translator":"王静斋"},{"sura":28,"aya":29,"text":"当母撒全满期限，偕眷夜行的时候，他就由土尔（山）方面望见火了。他对自己的眷属说：（你们略微迟候，我实望见火了。或许我从那里将一项消息，或是一段火把拿给你们，以便你们取暖。）","translator":"王静斋"},{"sura":28,"aya":30,"text":"及其来至火跟前的时候，有的从福地之内，山谷的右面，由树那里晓谕：叫母撒呀！我实是安拉………调养众世界的主。","translator":"王静斋"},{"sura":28,"aya":31,"text":"你掷出自己的手杖去，）及其见它活跃如巨蛇的时候，他就退转未归。我说：（母撒呀！前进！不要害怕，你一定是平安的。","translator":"王静斋"},{"sura":28,"aya":32,"text":"你把手插在衣领里！他就洁白无暇着发现。你为着骇怕折起你的臂来。这是你养主对匪喇傲及其众伟人的两项明证。的确，他们是一伙恶人）。","translator":"王静斋"},{"sura":28,"aya":33,"text":"母撒说：（我的养主啊！我曾杀死他们的一个人，我恐怕他们杀我。","translator":"王静斋"},{"sura":28,"aya":34,"text":"我兄——哈伦比我善谈。你差他随我作辅佐，教人信实我。我实骇怕他们斥我为谎言的）。","translator":"王静斋"},{"sura":28,"aya":35,"text":"主说：（我即将以你弟兄助你的膀臂，我赐你二人威势，他们不能到你俩跟前。你俩藉我的异迹（前往。）你俩和追随你俩的人是将获胜利的）","translator":"王静斋"},{"sura":28,"aya":36,"text":"当母撤对他们举出我明显的异迹时候，他们就说：（这只是造作的邪木。我们在前代的列祖里未听见过这事）。","translator":"王静斋"},{"sura":28,"aya":37,"text":"母撒说：（我的养主至知自他御前取得南针的人，和可获今世善果的人。背义的人必不能到达所欲）。","translator":"王静斋"},{"sura":28,"aya":38,"text":"匪喇傲呼众伟人说：（我不知道你们有除我以外的主。哈曼哪！你为我在泥上燃火；你为我筑一高官，或许我能望见母撤的主；我实揣度他是谎言的。）","translator":"王静斋"},{"sura":28,"aya":39,"text":"他与他的众兵在地上背义地高傲。自以为不被返回我。","translator":"王静斋"},{"sura":28,"aya":40,"text":"而后我惩治他和他的众兵，我把他们投在深海中。看哪！一般背义的人结果如何?","translator":"王静斋"},{"sura":28,"aya":41,"text":"我把他们置为导至火狱的领袖。在复生日，他们得不到相助。","translator":"王静斋"},{"sura":28,"aya":42,"text":"在今世，我使咒诅追随他们；在复生日他们是被驱逐的。","translator":"王静斋"},{"sura":28,"aya":43,"text":"在灭绝前代以后，我曾赐给母撒经典，以作众人的明证，并引导与慈惠，好教他们受劝。","translator":"王静斋"},{"sura":28,"aya":44,"text":"当我向母撒发表事情的时候，你未曾在西边，你也未在当场。","translator":"王静斋"},{"sura":28,"aya":45,"text":"只是我创造若干代，时间悠长了。你未曾住在买大严人间，向他们诵我的表征；虽然，我是遣使者。","translator":"王静斋"},{"sura":28,"aya":46,"text":"当我晓谕的时候，你未在土尔（山）方面。虽然，出自养主的慈惠。好教你警告那一伙未曾先你得到警告的人，好教他们受劝，","translator":"王静斋"},{"sura":28,"aya":47,"text":"设非他们以亲手前送的遭患难，至说：（我们的养主啊！为何你不差一个使者，俾使我们追随你的表征，归为信服的呢——。）","translator":"王静斋"},{"sura":28,"aya":48,"text":"在出自我御前的真理达到他们时候，他们就说：（为什么不赐给他，就像赐给母撒似的。）他们岂未隐昧从前赐给母撒的吗?他们曾说：（是两种相辅的邪术！）并说：（我们一概不信。）","translator":"王静斋"},{"sura":28,"aya":49,"text":"你说：（你们举出发自安拉御前较这两个尤能引导人的经典，如果你们所言属实，我就服从它。）","translator":"王静斋"},{"sura":28,"aya":50,"text":"他们若未应许你，你要知道：那是他们随从自己的私见。谁是迷误更甚于未受安拉的引导而追从自己私见的人?安拉绝不引导背义的民众。","translator":"王静斋"},{"sura":28,"aya":51,"text":"我确把这话接连的告诉他们了，好教他们受劝。","translator":"王静斋"},{"sura":28,"aya":52,"text":"在这以前得我赐给经典的人们全都信它 。","translator":"王静斋"},{"sura":28,"aya":53,"text":"有人诵读给他们的时候，他们就说：（我们信它了，那确是来自养主的真理，我们在它以前原是顺服的。）","translator":"王静斋"},{"sura":28,"aya":54,"text":"这些人，由于忍耐蒙得两次报酬。他们以善御恶，并就着我所赐给的作使用。","translator":"王静斋"},{"sura":28,"aya":55,"text":"他们听到妄言的时候，就远避它，说：（我们有我们的作为；你们有你们的作为。祝你们平安，我们不寻觅一切愚人。）","translator":"王静斋"},{"sura":28,"aya":56,"text":"你必不能引导自所喜爱的人，只是安拉引导自所意欲……的人。他至知得正道的人。","translator":"王静斋"},{"sura":28,"aya":57,"text":"他们说：（若是我们随你信从正道了，我们就被人掳出本地。）我岂未教他们居住在平安的禁地，由我供给的各类果实贩运到那里，但是他们的多半不知道。","translator":"王静斋"},{"sura":28,"aya":58,"text":"我灭绝了许多在生活期内狂妄的城市 这是他们的居处，在他们以后仅有少数人住过它。我是收留的。","translator":"王静斋"},{"sura":28,"aya":59,"text":"你的养主灭绝些个城池，必须先往它的都会派去一个使者，向他们诵我的表征。我每逢灭一个城池，其时那城的居民正在背义着。","translator":"王静斋"},{"sura":28,"aya":60,"text":"所赐给你们的任何一物，乃是今生的享受，和它的装饰。在安拉御前的，是较优至久的。你们怎不了解呢?","translator":"王静斋"},{"sura":28,"aya":61,"text":"有的人蒙我许给他快乐而后遇到他了，在他可象那类人吗？我叫他享受今生的乐趣，终而在复生日被带到（火狱）里?","translator":"王静斋"},{"sura":28,"aya":62,"text":"当那一日，主晓谕他们，说：（你们所妄称的我那些匹偶哪里去了?）","translator":"王静斋"},{"sura":28,"aya":63,"text":"（关于罪刑的）话对他们决定的那般人说：（我们的养主啊！这是被我们诱惑的那些人；我们诱他们迷误，就如自己迷误了。我们向你表示无干；他们未曾拜我们。）","translator":"王静斋"},{"sura":28,"aya":64,"text":"有的说：（你们呼求自己的偶像吧，）他们遂就呼求它们；但是它们未曾应承。他们亲见刑罚了，希望自己曾得正道。","translator":"王静斋"},{"sura":28,"aya":65,"text":"当那一日，主晓谕他们，说：（你们怎样答应列使了。）","translator":"王静斋"},{"sura":28,"aya":66,"text":"是日，他们不明种种消息，而致不相询问。","translator":"王静斋"},{"sura":28,"aya":67,"text":"论到悔罪，归信并作善举的人，可望他是达到所欲的。","translator":"王静斋"},{"sura":28,"aya":68,"text":"你的养主任便造化与选择。在他们不得选择。安拉清净，他玄高于他们所拟的匹偶。","translator":"王静斋"},{"sura":28,"aya":69,"text":"你的养主知道他们胸中所隐匿的和他们所显扬的。","translator":"王静斋"},{"sura":28,"aya":70,"text":"他是安拉，除他以外再无有主。惟他应受今世后世的赞颂。判断惟他掌握，你们将被归于他。","translator":"王静斋"},{"sura":28,"aya":71,"text":"你说！（你们看出来了吗?若是安拉为你们造（夜），始终不变，直到复生日，谁是安拉以外的主，能把「光」赐给你们?你们怎不听从呢?）","translator":"王静斋"},{"sura":28,"aya":72,"text":"你说：（你们看出来了吗?若是安拉给你们造「昼，」始终不变，直到复生日， 谁是安拉以外的主，能赐给你们「夜，」可在它里安歇?你们怎不观察呢?）","translator":"王静斋"},{"sura":28,"aya":73,"text":"他依自己的慈惠心为你们造化夜昼，以便你们在夜间安歇，并讨他的特恩，冀使你们感念。","translator":"王静斋"},{"sura":28,"aya":74,"text":"当那一日，主晓谕他们，说：（你们所妄称的那些匹偶哪里去了?）","translator":"王静斋"},{"sura":28,"aya":75,"text":"我由每伙人里提出一个作见证的；我说： 要你们举出自己的明证来！）他们知道真理属于安拉。他们所造作的不在了。","translator":"王静斋"},{"sura":28,"aya":76,"text":"葛伦原属母撒的族人，但是他对他们傲慢了。我赐给他财宝，它的锁钥压倒一伙强壮的人。那时候，他的族人对他说：（你不要狂喜。安拉实不爱狂喜的人。","translator":"王静斋"},{"sura":28,"aya":77,"text":"你要借着安拉所赐给的，寻求后世。你不要忘却今生的福分。你要为善，就像安拉善待你似的。你不要在地上寻求作恶。安拉实不喜造恶的人。）","translator":"王静斋"},{"sura":28,"aya":78,"text":"他说：（我蒙赐财宝，惟依仗自己所具的知识。）——他岂不知道，安拉确在他以前灭绝比他力量雄壮，积蓄较多的若干代吗? 不向为恶的人们问其罪孽；","translator":"王静斋"},{"sura":28,"aya":79,"text":"他乘着装饰出现自己的族人，一般追求今生的人说：（宁愿我们获若所赐给葛伦的，他实在是有大福分的。）","translator":"王静斋"},{"sura":28,"aya":80,"text":"一切有知识的人说：（你们遭祸了，安拉的善赏是在归信并作善举的人上最好的。）接受这话的只是忍耐的人。","translator":"王静斋"},{"sura":28,"aya":81,"text":"而后我把他和他的住宅陷入在地里。在他并无有一伙超过安拉相助他的，他也不是自卫的。","translator":"王静斋"},{"sura":28,"aya":82,"text":"那昨日盼望他的地位的人们，在次晨说：（哎呀！安拉为他意欲……的众仆伸缩给养。设非安拉对我们施恩了，一定他就陷下我们了。哎呀！负恩的人不能得意。）","translator":"王静斋"},{"sura":28,"aya":83,"text":"这后世我为着不欲在地上高傲为恶的人安排它。敬慎的人可获善果。","translator":"王静斋"},{"sura":28,"aya":84,"text":"行一件善事的人，可获强于它的；行一件恶事的人，那作恶的人，惟依其所作的受还报。","translator":"王静斋"},{"sura":28,"aya":85,"text":"的确，那降给你古兰的主，是必使你回到一个归返之地的。（我的养主至知那得到引导的人，与那处在明明迷误的人。）","translator":"王静斋"},{"sura":28,"aya":86,"text":"你原来未盼望接奉经典，（降给你经）只是出你养主的慈惠罢了。你切不要臂助悖逆的人们。","translator":"王静斋"},{"sura":28,"aya":87,"text":"你蒙受安拉降给表征以后，莫容他们阻碍你读它。要你劝导至养主；你莫属于拜偶像的。","translator":"王静斋"},{"sura":28,"aya":88,"text":"你莫併同安拉呼求另一受拜的。除他以外再无有主，除去真主的本然以外，万物将归消灭。判断惟他掌握，你们将被归返于他。","translator":"王静斋"},{"sura":29,"aya":1,"text":"（阿里夫、俩摸、密摸）","translator":"王静斋"},{"sura":29,"aya":2,"text":"世人以为容他们口称归信而不受折磨吗？","translator":"王静斋"},{"sura":29,"aya":3,"text":"我确试证他们以前的众人了，安拉确知道一般说实言的人。","translator":"王静斋"},{"sura":29,"aya":4,"text":"作恶的人们自以为能够超越我吗? 他们所判断的太恶了：","translator":"王静斋"},{"sura":29,"aya":5,"text":"盼望遇见安拉的人，（就教他崛起奋斗吧！）安拉的期限一定是该到的。他是能听的，深知的。","translator":"王静斋"},{"sura":29,"aya":6,"text":"奋斗的人，那只是为他个人奋斗。安拉确是无求众世界的。","translator":"王静斋"},{"sura":29,"aya":7,"text":"归信并作善举的人，我必为他们遮蔽种种过错；我必依他们所作的把最优的（善赏）赠给他们。","translator":"王静斋"},{"sura":29,"aya":8,"text":"我嘱告人类孝敬他的父母。若是他俩迫你给我举自所不知道的为匹偶了，你不得顺从他俩。你们惟归于我，而后我把你们所作过的宣告给你们。","translator":"王静斋"},{"sura":29,"aya":9,"text":"归信并作善举的人，我将把他们列入义人以内。","translator":"王静斋"},{"sura":29,"aya":10,"text":"有的人声称：（我们归信安拉了。）但是一旦因着安拉蒙害了，他就以人的扰害当作安拉的罪刑。若是出自养主的相助到来了，他们必说：（我们实是和你们一样的）安拉岂非至知世人胸中所怀的吗?","translator":"王静斋"},{"sura":29,"aya":11,"text":"安拉实知道一般归信的人，实知道一般伪信士。","translator":"王静斋"},{"sura":29,"aya":12,"text":"众不信者对一般归信的人说：（你们随从我们的道路吧！让我们担负你们的罪恶吧！）他们本不能担负他们的些微罪恶；他们实是谎言的。","translator":"王静斋"},{"sura":29,"aya":13,"text":"让他们去负自己的重担，和其它加倍的重担吧！在复生日他们应对于自所造作的受质问。","translator":"王静斋"},{"sura":29,"aya":14,"text":"我确把努海遣至他的族人了，他在他们中间留住了一千年，除出五十载。他们正在背义着遭遇洪水了。","translator":"王静斋"},{"sura":29,"aya":15,"text":"于是我解救他和同船的人们。我把他置作世人的表征。","translator":"王静斋"},{"sura":29,"aya":16,"text":"易卜拉欣，那时候他对自己的族人说：（你们当拜安拉，并当畏惧他；那是于你们至好的；如果你们知道了，……。","translator":"王静斋"},{"sura":29,"aya":17,"text":"你们撇开安拉惟拜些偶像，并且造作谎言。 的确，你们撇开安拉所拜的那一切，均不能为你们掌给养，你们要在安拉那里讨给养，当要拜他，并感念他！你们将被归返于他。）","translator":"王静斋"},{"sura":29,"aya":18,"text":"若是你们不信了，在你们以前的各民众确曾不信。使者所负的仅止公开的宣达。","translator":"王静斋"},{"sura":29,"aya":19,"text":"他们岂未见安拉如何造化万有而又恢复它?这在安拉确是容易的。","translator":"王静斋"},{"sura":29,"aya":20,"text":"你说：（要你们在地上游行而观察，真主怎样地造化万有?安拉复再发现另一造化。安拉实是全能于万事的。","translator":"王静斋"},{"sura":29,"aya":21,"text":"主罪刑自所意欲（罪刑）的人，慈惠自所意欲（慈惠）的人。你们将被归转于他。","translator":"王静斋"},{"sura":29,"aya":22,"text":"你们不是能在地上，也不是能在天上作抵抗的。你们撇开安拉别无一爱护的，也无一相助的。","translator":"王静斋"},{"sura":29,"aya":23,"text":"不信安拉的表征，与（不信）见他的人们，均于我的慈惠上绝望了。这些人应遭痛刑。）","translator":"王静斋"},{"sura":29,"aya":24,"text":"他的族人的答复不外是说：（你们杀死他或烧死他。）而后安拉救他脱开火了。此中对于有正信的民众确含有种种表征。","translator":"王静斋"},{"sura":29,"aya":25,"text":"易卜拉欣说：（你们只是在今生撇开安拉而借着偶像彼此之间联合感情；今后在复生日，你们彼此不相承认，彼此互相咒诅。你们的居所是火狱，无有相助你们的。）","translator":"王静斋"},{"sura":29,"aya":26,"text":"而后鲁脱归信他了，他说：（我是一定迁至养主的。主实是优胜的，明哲的。 ）","translator":"王静斋"},{"sura":29,"aya":27,"text":"我赏给他易司哈各与叶而孤白；我把圣职与经典置在他的子孙之间。我在今世赐给他报酬，他在后世定属于义人一类的。","translator":"王静斋"},{"sura":29,"aya":28,"text":"鲁脱，那时候，他对自己的族人说：你们行举世从无一人先你们作过的那丑恶吗?","translator":"王静斋"},{"sura":29,"aya":29,"text":"你们亲近男子，断绝道路。你们在席面上发动非理的事情吗?）他族人的答复只是说：（如果你的所言属实了，你就把安拉的罪刑拿给我们）","translator":"王静斋"},{"sura":29,"aya":30,"text":"他说：（我的养主啊：你相助我，以对付造恶的族人吧！）","translator":"王静斋"},{"sura":29,"aya":31,"text":"当我的诸使给易卜拉欣报喜信的时候，他们就说：（我们必要灭绝这城的人。它的居民实是背义的。）","translator":"王静斋"},{"sura":29,"aya":32,"text":"易卜拉欣说：（那城里确有鲁脱。）他们说：（我们知道那里的人；我们一定救他和他的眷属；除非他的妻，她归为余下的。）","translator":"王静斋"},{"sura":29,"aya":33,"text":"当我的诸使达至鲁脱的时候，他就因着他们忧愁，心窄了。他们说：（你不要骇怕，不要忧愁；我们必要救你，与你的眷属；除非你的妻，她归为余下的。","translator":"王静斋"},{"sura":29,"aya":34,"text":"我们是将必因为这城市人的作恶，而往他们上降天灾的。","translator":"王静斋"},{"sura":29,"aya":35,"text":"我们必在那里为着有理智的民众遗下显明的标记。）","translator":"王静斋"},{"sura":29,"aya":36,"text":"并向买大严遣去他们的弟兄——书尔布。他说：（我的族人哪！你们当拜安拉，当盼望末日！你们不要在地上造恶着过为。）","translator":"王静斋"},{"sura":29,"aya":37,"text":"但是他们不信他。而后，他们遭遇地震；次晨，是在自己宅内伏地而死的。","translator":"王静斋"},{"sura":29,"aya":38,"text":"阿代、赛母待，他们的一部分居所，在你们已明明见过了。恶魔装饰他们的作为；他们眼见着被他所阻，离开正道。","translator":"王静斋"},{"sura":29,"aya":39,"text":"又有葛伦、匪喇傲、与哈曼。母撒确把种种明证现给他们了；但是他们在地上高傲，他们本不能超过。","translator":"王静斋"},{"sura":29,"aya":40,"text":"而后，我因为每一个人的罪恶施以惩罚。他们之中有我往他上驱动砂石的；他们之中有被暴声所惩的；他们之中有我以地陷下去的；他们之中有被我淹没的。安拉绝不亏负他们，但是他们自亏己身了。","translator":"王静斋"},{"sura":29,"aya":41,"text":"一般撇开安拉结友的人的情形，有如造室的蜘蛛的情形。住室的至松软的一定是蜘蛛室；设若他们知道了，……。","translator":"王静斋"},{"sura":29,"aya":42,"text":"安拉实知道他们撇开他所拜的物类。他是优胜的，明哲的。","translator":"王静斋"},{"sura":29,"aya":43,"text":"这些比喻，我为世人发表它；理解它的，只是一般有知识的人。","translator":"王静斋"},{"sura":29,"aya":44,"text":"安拉依着真理造化了天地。此中对于众归信的人确含有一种表征。","translator":"王静斋"},{"sura":29,"aya":45,"text":"你诵那所告谕你的经典， 你当立拜功：礼拜确能阻遏丑恶和非理的事。记念安拉更是伟大。安拉知道你们的作为。","translator":"王静斋"},{"sura":29,"aya":46,"text":"你们不要与有经人争辩，除非以至好的态度。他们之中背义的人在外。你们说：（我们归信降给我们的，和降给你们的。我们的主和你们的主是一。我们是惟独顺服他的。）","translator":"王静斋"},{"sura":29,"aya":47,"text":"我如此着把经典降给你。蒙我降给经典的人信它；这一切人里（也）有信它的。不承认我的表征的，只有一般逆徒。","translator":"王静斋"},{"sura":29,"aya":48,"text":"从前你未读过经典，你也未用右手写过它。果然是那样，一般虚浮的人定要怀疑了。","translator":"王静斋"},{"sura":29,"aya":49,"text":"不然，它是记在一般有识者胸中的明文。不承认我表征的，只是一般背义的人。","translator":"王静斋"},{"sura":29,"aya":50,"text":"他们说：（怎么不把出自养主的异迹降给他呢?）你说：（一切异迹惟操自安拉；我只是一个公开传警告的。）","translator":"王静斋"},{"sura":29,"aya":51,"text":"我降给你经典，读给他们，这事尚不使得他们满足吗?其中确含有慈惠和劝化一般有正信的民众。","translator":"王静斋"},{"sura":29,"aya":52,"text":"你说：（安拉在我和你们中间作见证已可满足了。他知道天地间所有的。信虚伪不信安拉的人，均是伤折的。）","translator":"王静斋"},{"sura":29,"aya":53,"text":"他们要求你立时现出刑罚。设非一定的时期。刑罚必然达至他们；当他们不知觉的时候，那刑罚一定突然来到。","translator":"王静斋"},{"sura":29,"aya":54,"text":"他们向你要求立时现出刑罚。火狱确是环绕众逆徒的。","translator":"王静斋"},{"sura":29,"aya":55,"text":"当那一日，他们被上面与脚下的刑罚所遮，主说：（你们尝自所作过的吧！）","translator":"王静斋"},{"sura":29,"aya":56,"text":"我那归信的众仆啊，我的地方确是宽大的。你们应当拜我。","translator":"王静斋"},{"sura":29,"aya":57,"text":"每一个有性命的是该尝死亡的。而后你们惟被归于我。","translator":"王静斋"},{"sura":29,"aya":58,"text":"归信并作善举的人，我将教他们永居在天园中，诸河流于其下的高楼上。工作者的报酬太好了，","translator":"王静斋"},{"sura":29,"aya":59,"text":"那般人，他们忍耐，并且仰赖养主。","translator":"王静斋"},{"sura":29,"aya":60,"text":"许多动物不能担负自己的给养；安拉赐给它和你们给养。他是能听的，深知的。","translator":"王静斋"},{"sura":29,"aya":61,"text":"你若问他们：（谁造化了天地，并降服了日月?）他们必说：（安拉。）他们转变到哪里?","translator":"王静斋"},{"sura":29,"aya":62,"text":"安拉为其意欲……的众仆伸缩给养。安拉实是深知万事的。","translator":"王静斋"},{"sura":29,"aya":63,"text":"你若问他们：（谁由上空降水而用它活地于既死以后?）他们必说：（安拉。）你说：（赞颂安拉！）他们的多半实不了解。","translator":"王静斋"},{"sura":29,"aya":64,"text":"这今生不外是玩耍与荒嬉；后世即是永生。设若他们知道了，……。","translator":"王静斋"},{"sura":29,"aya":65,"text":"他们乘在船上的时候，就呼求安拉，对他竭诚顺服着。及至真主解救他们达至陆地的时候，他们立刻就举定匹偶，","translator":"王静斋"},{"sura":29,"aya":66,"text":"为的是瞒昧我所赐给他们的，更为的是享受。不久他们就知道。","translator":"王静斋"},{"sura":29,"aya":67,"text":"他们岂未看见：我设置平安的禁地，其四周的人在遭受劫掠着。他们信假的，而不承认安拉的恩典吗?","translator":"王静斋"},{"sura":29,"aya":68,"text":"往安拉上造谎，或在真理达到的时候不曾信服它，谁比这类人更为不义?火狱里没有逆徒们的住所吗?","translator":"王静斋"},{"sura":29,"aya":69,"text":"为我奋斗的人，我必把他们领在我的道路上。 安拉是和一般为善的人同在的。","translator":"王静斋"},{"sura":30,"aya":1,"text":"（阿里甫、俩模、密默。 ）","translator":"王静斋"},{"sura":30,"aya":2,"text":"鲁密在最近之地战败了；","translator":"王静斋"},{"sura":30,"aya":3,"text":"他们在失败以后即将在几年以内获胜。","translator":"王静斋"},{"sura":30,"aya":4,"text":"前后的事情惟属安拉执掌。当那一日，众穆民借着安拉的相助快乐。","translator":"王静斋"},{"sura":30,"aya":5,"text":"真主相助自所意欲…的人。他是优胜的，特慈的。","translator":"王静斋"},{"sura":30,"aya":6,"text":"安拉约定的。安拉不爽其约。但是世人的多半不知道。","translator":"王静斋"},{"sura":30,"aya":7,"text":"他们知道今生的表面。对于后世茫无所知。","translator":"王静斋"},{"sura":30,"aya":8,"text":"他们心中岂未思考：安拉只以正义并依一定的期限造化天地万有吗? 许多人确是不信看见养主的，","translator":"王静斋"},{"sura":30,"aya":9,"text":"他们岂未在地上游行而观察他们以前众人的结果如何?那些人原比他们力量雄厚。他们耕地，并且繁荣它，多过他们所繁荣的。列使现给他们种种明证。安拉未亏负他们。但是他们自亏己身了。","translator":"王静斋"},{"sura":30,"aya":10,"text":"最不幸的是一般为恶者的结果；只因他们不信安拉的表征，并嘲弄它。","translator":"王静斋"},{"sura":30,"aya":11,"text":"安拉肇造世人，随又恢复它，而后你们将被归返于他。","translator":"王静斋"},{"sura":30,"aya":12,"text":"当复生成立的那一天，众作恶的人归为失望。","translator":"王静斋"},{"sura":30,"aya":13,"text":"他们的偶像不能代为求赦；他们且是不承认自己偶像的。","translator":"王静斋"},{"sura":30,"aya":14,"text":"当复生成立的那一天，是日，他们纷纷散开。","translator":"王静斋"},{"sura":30,"aya":15,"text":"归信并作善举的人，均在天园中得到快乐。","translator":"王静斋"},{"sura":30,"aya":16,"text":"论到隐昧，不信我的表征，和（不信）见后世的人，均被带到刑罚的。","translator":"王静斋"},{"sura":30,"aya":17,"text":"在到晚晌，到清晨的时候，颂安拉清净：","translator":"王静斋"},{"sura":30,"aya":18,"text":"惟他应受天地间的赞颂。并在下午与日昃的时候。","translator":"王静斋"},{"sura":30,"aya":19,"text":"他出生于死的，出死于生的，他活地于既死以后。你们就是这样地将被现出。","translator":"王静斋"},{"sura":30,"aya":20,"text":"真主由土上造化你们，而后你们忽然成为散布的人类，这是他的一项表征。","translator":"王静斋"},{"sura":30,"aya":21,"text":"他为你们由你们的同类上造化妻室；为的是你们和她安居，并在你们中间发生爱情和怜悯，这也是他的一项表征。此中对于有参悟的民众确含有种种显迹。","translator":"王静斋"},{"sura":30,"aya":22,"text":"天地的造化与你们的语言和肤色的不同。这也是他的一种表征。此中含有对于众世界人的种种显迹。","translator":"王静斋"},{"sura":30,"aya":23,"text":"你们在夜昼里睡眠，和你们寻求他的特恩，这也是他的一项表征。此中对于侧耳的民众确含有种种显迹。","translator":"王静斋"},{"sura":30,"aya":24,"text":"他因为恐吓并令人希望，把闪电现给你们，更由上空降水而用它复活已死的地，这也是他的一项表征。此中对于有解悟的民众确含有种种显迹。","translator":"王静斋"},{"sura":30,"aya":25,"text":"天地依循主的命令屹立，而后他自地上呼你们一呼的时候，你们辄就出现，这也是他的一项表征。","translator":"王静斋"},{"sura":30,"aya":26,"text":"天地间一切所有惟他执掌，统是顺服他的。","translator":"王静斋"},{"sura":30,"aya":27,"text":"他是那肇造万有，而后恢复它的主。这是在他上至容易的。惟他具有天地间至高的比拟他是优胜的，明哲的。","translator":"王静斋"},{"sura":30,"aya":28,"text":"主就着你们同人发出一个比喻：你们愿意和手下的人平等地共享我所赐给的，你们怕他们就像怕同人吗? 我就这样地为一般有理智的民众分析种种表征。","translator":"王静斋"},{"sura":30,"aya":29,"text":"一般背义的人实是无知识着随从自己的私见了。谁能引导被安拉造为迷误的人?在他们本无一个相助的。","translator":"王静斋"},{"sura":30,"aya":30,"text":"你当倾向正教。（应守）安拉所赋予人类的禀赋。安拉的造化上无有变换。这是正教；但是世人的多半不知道。","translator":"王静斋"},{"sura":30,"aya":31,"text":"归向于他着。你们当畏惧他，当立拜功，不要列入那般分宗教","translator":"王静斋"},{"sura":30,"aya":32,"text":"成派系的多神教徒。每一伙人是酷爱自己所守的。","translator":"王静斋"},{"sura":30,"aya":33,"text":"人类遇难的时候便就呼求养主，归向于他。以后真主教他们尝到慈惠的时候，其中的一伙忽然就给养主举匹偶，","translator":"王静斋"},{"sura":30,"aya":34,"text":"终至瞒昧我所赐给的。你们享受吧！以后不久你们就知道了。","translator":"王静斋"},{"sura":30,"aya":35,"text":"不然，我曾降给他们明证，那明证言及他们所信的匹偶吗？","translator":"王静斋"},{"sura":30,"aya":36,"text":"我教人尝到慈惠的时候，他们就因它狂喜；他们若因亲手往前送的遭遇不幸了，他们就立时绝望。","translator":"王静斋"},{"sura":30,"aya":37,"text":"他们岂未看见。安拉为其意欲…的人伸缩给养?此中对于有正信的民众，确含有种种表征。","translator":"王静斋"},{"sura":30,"aya":38,"text":"你当赠给近戚以其所应得的；并（当赠给）贫人和旅客；这是在一般讨安拉喜悦的人上至好的；这般人就是达到所欲的。","translator":"王静斋"},{"sura":30,"aya":39,"text":"你们为着藉人的资财增长而给出去的赠品，那在安拉认为不会发长。你们在讨安拉的喜悦着施出去的济物可获加倍的。","translator":"王静斋"},{"sura":30,"aya":40,"text":"安拉他造化了你们，赐给你们给养，而后使你们死，而又复活你们。你们的偶像里可有能作此一事的吗?赞主清净，他玄高于他们所拟的匹偶。","translator":"王静斋"},{"sura":30,"aya":41,"text":"因着众人亲手作下的，败坏在陆海上显现了，为的是使他们尝试自所作下的一部分，好教他们转回。","translator":"王静斋"},{"sura":30,"aya":42,"text":"你说：（你们在地上游行，而观察众前人的结果如何?）他们的多半原是信偶像的。","translator":"王静斋"},{"sura":30,"aya":43,"text":"你当在无转回的那一天，从安拉上来到以前，倾向正教：是日，他们纷纷散开。","translator":"王静斋"},{"sura":30,"aya":44,"text":"不信的人，他的不信惟害自己。作善功的人，均是为其自身展开，","translator":"王静斋"},{"sura":30,"aya":45,"text":"终而真主以其特恩赏赐一般归信并作善举的人，真主必不喜爱背逆的人。","translator":"王静斋"},{"sura":30,"aya":46,"text":"真主驱风报喜信，为教你们尝试他的慈惠，为船只依循他的命令行驶，为着你们讨他的特恩，并为你们感念，这是属于他的表征。","translator":"王静斋"},{"sura":30,"aya":47,"text":"我在你以前确把列使遣至他们的族人了；且对它们举出各项明证。而后我报复一般为恶的人。相助众穆民是经我决定的。","translator":"王静斋"},{"sura":30,"aya":48,"text":"安拉，他驱风而后激动云，随又任便使它展在上空，更使它分成一段一段的。你见雨点出自其间。待真主使其意欲…的众仆得到雨的时候，他们立刻表示喜悦。","translator":"王静斋"},{"sura":30,"aya":49,"text":"在未降雨以前，他们原是绝望的。","translator":"王静斋"},{"sura":30,"aya":50,"text":"你观看安拉的慈迹！他怎样地活地于既死以后?他是定能于复活死者的。他是全能于万事的。","translator":"王静斋"},{"sura":30,"aya":51,"text":"我若驱风，既而他们看见禾苗变黄了，嗣后他们立刻忘恩。","translator":"王静斋"},{"sura":30,"aya":52,"text":"你不能让死人听到；聋子当其退转的时候，你也不能让他听到呼喊。","translator":"王静斋"},{"sura":30,"aya":53,"text":"你不能领导瞎子离开他们的迷误。你只能让那信我表征的人听到；所以他们是顺服的。","translator":"王静斋"},{"sura":30,"aya":54,"text":"安拉他造化你们，从薄弱起；又在薄弱以后造化力量；在力量以后又造化薄弱与白发。他自由造化，他是深知的，全能的。","translator":"王静斋"},{"sura":30,"aya":55,"text":"当复生成立的那一日，众为恶的人发誓：（他们只停留了一时。）他们如此着受转变，","translator":"王静斋"},{"sura":30,"aya":56,"text":"一般得到知识与正信的人说：（你们依循安拉写定的，已竟迟留到复生日了。这就是复生日，但是你们不知道。）","translator":"王静斋"},{"sura":30,"aya":57,"text":"当那一日，背义的人们的托词不济于事，且不让他们讨喜悦。","translator":"王静斋"},{"sura":30,"aya":58,"text":"我在这古兰中为世人发表各项比喻了。你若向他们举出一项异迹来，不信的人们必说：（你们只是一般虚浮的人。）","translator":"王静斋"},{"sura":30,"aya":59,"text":"安拉就这样地封闭一般无知识的人心。","translator":"王静斋"},{"sura":30,"aya":60,"text":"你当忍耐。安拉的约委是切实的。莫容无定信的人们诱你轻浮。","translator":"王静斋"},{"sura":31,"aya":1,"text":"（阿里甫、俩模、密默。）","translator":"王静斋"},{"sura":31,"aya":2,"text":"这是固典的文词。","translator":"王静斋"},{"sura":31,"aya":3,"text":"引导与慈惠一般为善的人；","translator":"王静斋"},{"sura":31,"aya":4,"text":"就是一般立拜功，施天课，并且坚信后世的。","translator":"王静斋"},{"sura":31,"aya":5,"text":"他们是守其养主正道的，这般人均是得意的，","translator":"王静斋"},{"sura":31,"aya":6,"text":"人类中有的接受戏言，而致无知识地诱人错过安拉的道，并以它为可戏的；这般人应遭辱刑。","translator":"王静斋"},{"sura":31,"aya":7,"text":"一旦有人把我的表征读给他了，他就高傲着转去，好像他未听到，好像他的两耳重听。你就以痛刑报告他吧，","translator":"王静斋"},{"sura":31,"aya":8,"text":"归信并作善举的人，必得到乐园，永居其中。","translator":"王静斋"},{"sura":31,"aya":9,"text":"安拉决定的约。他是优胜的，明哲的。","translator":"王静斋"},{"sura":31,"aya":10,"text":"他造化诸天，并无你们看见的楹柱。他把定山置在地上，以免它摇动你们。他把各样动物散播在地上；并由上空降下水来，而用它发长各色美丽的。","translator":"王静斋"},{"sura":31,"aya":11,"text":"这是安拉的造化；你们告诉我：除他以外的那一切造化什么了? 背义的人实是在显著的迷误中。","translator":"王静斋"},{"sura":31,"aya":12,"text":"我确曾赐给鲁格曼卓识了，（说：）你当感念安拉，感念的人，那就是利其自身作感念。忘恩的人，安拉一定是无求的，受赞的。那时候，","translator":"王静斋"},{"sura":31,"aya":13,"text":"鲁格曼劝他儿子说：（我的小儿呀，你不要给安拉拟匹偶；举匹偶实是一大背义。","translator":"王静斋"},{"sura":31,"aya":14,"text":"我嘱告世人孝他父母——其母备极疲乏着怀他；他的离乳是在二年。你当感念我和你的父母。归惟归于我。","translator":"王静斋"},{"sura":31,"aya":15,"text":"如果他俩强你把自所不知的给我作匹偶，你不要服从他俩。在今世你要善待父母；你要追随那归向我的人的道路。而后你们惟归于我，我再把你们所作过的宣告你们——","translator":"王静斋"},{"sura":31,"aya":16,"text":"我的小儿呀！差错若是芥菜粒重，它是在盘石里，或在天，或在地，安拉即将拿它来。安拉是妙知的，尽知的。","translator":"王静斋"},{"sura":31,"aya":17,"text":"我的小儿呀，你当立拜功，当命以合法的，禁止非理的，并忍受自所遭遇的。这确属于意志坚决的事情。","translator":"王静斋"},{"sura":31,"aya":18,"text":"你不要对人骄傲：你不要在地上狂行：安拉实不喜骄傲自矜的人。","translator":"王静斋"},{"sura":31,"aya":19,"text":"你在步行上要得守中： 你要低声。的确，声音最可憎的实是驴声。","translator":"王静斋"},{"sura":31,"aya":20,"text":"你们岂未看见：安拉为你们降服在天在地的，并把自己内外的恩典完全施给你们吗?有的人无知识，无引导，也无光明的经典着，因为安拉起辩论。","translator":"王静斋"},{"sura":31,"aya":21,"text":"一旦有人对他们说：（你们追随安拉降下的吧！）他们就说：（不，我们追随曾见自己列祖所守的。）设若恶魔把他们唤至火刑，（他们也肯追随吗?）","translator":"王静斋"},{"sura":31,"aya":22,"text":"那在为善着倾向安拉的人，确已抓住至坚固的把柄了。诸事的归宿惟归于安拉。","translator":"王静斋"},{"sura":31,"aya":23,"text":"不信的人，其不信可莫贻你忧虑。他们惟独返回于我而后我把他们所干过的宣告给他们。安拉深知胸中所怀的。","translator":"王静斋"},{"sura":31,"aya":24,"text":"我教他们享受少时，而后我把他们迫至重刑。","translator":"王静斋"},{"sura":31,"aya":25,"text":"你若问他们：（谁造化了天地?）他们必说：（安拉。）你说：（赞颂安拉。）不然，他们的多半不知道。","translator":"王静斋"},{"sura":31,"aya":26,"text":"天地间所有的惟独安拉执掌。安拉实是无求的，受赞的。","translator":"王静斋"},{"sura":31,"aya":27,"text":"设若地上的树是些个笔，一海之外另有七海助长它，安拉的话未尝尽没。安拉实是优胜的，明哲的。","translator":"王静斋"},{"sura":31,"aya":28,"text":"造化你们和复活你们，只像（造化）一个人。安拉实是能听的，能见的。","translator":"王静斋"},{"sura":31,"aya":29,"text":"你岂未看见安拉纳夜于昼，纳昼于夜，并降服了日月，每一个行至一定的时期，且（未看见）安拉尽知你们的作为吗?","translator":"王静斋"},{"sura":31,"aya":30,"text":"这是由于安拉就是真的，他们撇开他所拜的是假的，安拉就是尊大玄高的。","translator":"王静斋"},{"sura":31,"aya":31,"text":"你岂未见船在海中借着安拉的恩惠航行，为的是教你们看见他的一部分表征?此中对于一切忍耐与感恩的人确含有种种表征。","translator":"王静斋"},{"sura":31,"aya":32,"text":"像阴影的浪涛遮覆他们的时候，他们就呼求安拉对他竭诚顺服着；当他解救他们达到陆地的时候，他们内里有持中的。不信我表征的，只是一切失信忘恩的人。","translator":"王静斋"},{"sura":31,"aya":33,"text":"众人哪，你们当畏惧养主；你们当骇怕那么一日。父不能替子，子不能替父料理一事。安拉的约是切实的。你们不要被今生所骗，也不要被欺骗的借着安拉来欺骗。","translator":"王静斋"},{"sura":31,"aya":34,"text":"的确，惟安拉自知复生；他降下雨来，他知道子宫以内的。人不知道自己在明天要作何事；人不知道自己将死在什么地方。安拉确是深知的，尽知的。","translator":"王静斋"},{"sura":32,"aya":1,"text":"（阿里甫、俩模、密默。）","translator":"王静斋"},{"sura":32,"aya":2,"text":"降下无可疑的经典，是从调养众世界的主上。","translator":"王静斋"},{"sura":32,"aya":3,"text":"不然，他们说：（他造作了它）吗? 不然，那是出自养主的真理，好教你警告一伙在你以前未得到警告的人，以便他们得正道。","translator":"王静斋"},{"sura":32,"aya":4,"text":"安拉在六日造化了天地万有，而后操纵「阿勒世」。在你们除他以外无一爱护的，也无一告赦的。你们怎不受劝呢?","translator":"王静斋"},{"sura":32,"aya":5,"text":"他治理事情，自天至地；而后那事件在它的限度是你们所计算的一千年的那一天升至真主。","translator":"王静斋"},{"sura":32,"aya":6,"text":"这是知目不能见的与显见的优胜特慈的主。","translator":"王静斋"},{"sura":32,"aya":7,"text":"那真主把自所造化的每一物造成完美的。他由泥上开始造化人类，","translator":"王静斋"},{"sura":32,"aya":8,"text":"并由微贱的清水上造化它的子孙。","translator":"王静斋"},{"sura":32,"aya":9,"text":"主且造它为齐整的，为它吹入灵魂。主为你们造化耳、眼、心，你们绝少感念。","translator":"王静斋"},{"sura":32,"aya":10,"text":"他们说：（我们消失在地中的时候，必须重新被造吗?）不然，他们是不信看见养主的。","translator":"王静斋"},{"sura":32,"aya":11,"text":"你说，（那负责经管你们的，掌理死亡的天使，取去你们的性命，然后你们惟被归到养主。）","translator":"王静斋"},{"sura":32,"aya":12,"text":"为恶的人在养主御前俯首说：（养主啊！我们看见了，听到了；你让我们转回去作善举，我们实是坚信的。）当这时候，设若你看见了，……","translator":"王静斋"},{"sura":32,"aya":13,"text":"设我意欲……了，我必赐给每一个人正道。虽然，我的话：（必以镇呢与人类一同充满火狱）已然决定了。","translator":"王静斋"},{"sura":32,"aya":14,"text":"你们由于忘掉看见今日而尝试吧：我忘却你们了，你们因自所作过的尝试永久的刑罚吧！","translator":"王静斋"},{"sura":32,"aya":15,"text":"信我的表征惟有一般遇人以这个劝他的时候，遂就倒身下拜，以赞其养主称颂清净，并不高傲的人。","translator":"王静斋"},{"sura":32,"aya":16,"text":"他们的两肋离开卧所，在骇怕希望着呼求养主；并就着我所赐与的作使用。","translator":"王静斋"},{"sura":32,"aya":17,"text":"人不知道已为他们隐伏的愉快。这是因着他们所作过的施以报偿。","translator":"王静斋"},{"sura":32,"aya":18,"text":"为穆民的安能像是作恶的人呢?他们本不一样。","translator":"王静斋"},{"sura":32,"aya":19,"text":"归信并作善举的人，可以其所作过的获得路筵般的长住的园林。","translator":"王静斋"},{"sura":32,"aya":20,"text":"作恶的人，其居所是火狱。有时他们欲从那里出去，复被投在其中；有的对他们说：（你们尝试曾所不信的火刑吧！）","translator":"王静斋"},{"sura":32,"aya":21,"text":"我必教他们尝试次于极大刑罚的最近的刑罚，好教他们转回。","translator":"王静斋"},{"sura":32,"aya":22,"text":"遇人劝以安拉的表征遂就远避，谁是比这类人更属背义的。我义报复作恶的人。","translator":"王静斋"},{"sura":32,"aya":23,"text":"我确把经典赐给母撒了。你对于见他上不要怀疑。我委他引导以斯拉衣来的子孙。","translator":"王静斋"},{"sura":32,"aya":24,"text":"我于他们之间发现一般教领，奉我的命令作引导，彼时他们忍耐， 他们坚信我的种种表征。","translator":"王静斋"},{"sura":32,"aya":25,"text":"你的养主将在复生日对他们所争议的从中判明。","translator":"王静斋"},{"sura":32,"aya":26,"text":"我在他们 以前灭绝了若干代，他们经过那般人的居处，这在他们尚未明白吗?此中确含有种种表征，他们怎不听从呢?","translator":"王静斋"},{"sura":32,"aya":27,"text":"他们岂未看见，我把水驱至不毛之地，而用它发生禾谷。他们的牲畜与个人就着它吃，他们怎不观察呢?","translator":"王静斋"},{"sura":32,"aya":28,"text":"他们说：（这判明是在何时? 如果你们所言属实了，……）","translator":"王静斋"},{"sura":32,"aya":29,"text":"你说：（在判断{实现}的那一天，逆徒们的归信无济，他们也得不到姑容。）","translator":"王静斋"},{"sura":32,"aya":30,"text":"你要远避他们。你等侯吧！他们也必是等侯的。","translator":"王静斋"},{"sura":33,"aya":1,"text":"圣人哪！你畏惧安拉吧！不要顺从不信者与伪信士。安拉是深知的，明哲的。","translator":"王静斋"},{"sura":33,"aya":2,"text":"你当追从那由养主上所告谕你的。安拉尽知你们的作为。","translator":"王静斋"},{"sura":33,"aya":3,"text":"你要仰赖安拉；安拉作保护的已可满足了。","translator":"王静斋"},{"sura":33,"aya":4,"text":"安拉未为一个人在他内里造化两颗心。他未把你们比为母背的妻子，置作你们的生母；他也未把你们的义子置为亲生子。这是你们的口称。安拉说真的，他指示途径。","translator":"王静斋"},{"sura":33,"aya":5,"text":"你们把他们归还给生父，这在安拉认为是至公平的。如果你们不知道他们的生父，则归为你们同教弟兄，归为你们所解放的。你们的错误不为有罪。只是你们的心所倾向的（有罪）。安拉是多恕的，特慈的。","translator":"王静斋"},{"sura":33,"aya":6,"text":"圣人对众穆民比他们自身更是接近的。他的妻是他们的母。一般血亲在安拉的写定之下，彼此比众穆民与辅士们更是接近的。你们的对于至友行善，不在此例。这是记录在典章的。","translator":"王静斋"},{"sura":33,"aya":7,"text":"昔时，我自列圣上取得他们的约了；并自你上，更自努海、易卜拉欣、母撒和马尔焉的子尔撒上。我自他们上取得重约，","translator":"王静斋"},{"sura":33,"aya":8,"text":"为的是向一般（保持）诚实的人询问他们的诚实。真主已为众不信的人备下痛刑了。","translator":"王静斋"},{"sura":33,"aya":9,"text":"众归信的人哪！你们当记念安拉对你们的恩典！当众兵来到的时候，我向他们驱风，和你们看不见的军兵，安拉看得见你们的作为。","translator":"王静斋"},{"sura":33,"aya":10,"text":"那时候，他们从你们上下来至你们。那时候，众目凝视，心达咽喉。你们对安拉揣度种种揣测。","translator":"王静斋"},{"sura":33,"aya":11,"text":"众穆民在那里经受试验，他们遭遇猛烈的震动了。","translator":"王静斋"},{"sura":33,"aya":12,"text":"那时候，伪信士们及心中有病的人，说：（安拉和他的使者许给我们的，只是一种欺骗）。","translator":"王静斋"},{"sura":33,"aya":13,"text":"当时，他们之中的一伙人说：（耶士雷布人哪，无有你们立足的地方了。你们退回吧！）他们一部分人求圣人许可，说：（我们的住房不严紧）。那住房并非不严紧，他们只是意欲逃跑罢了。","translator":"王静斋"},{"sura":33,"aya":14,"text":"设使有人由各方面来到他们这里，向他们要求扰乱，他们一定允许。他们仅止稍微的迟疑。","translator":"王静斋"},{"sura":33,"aya":15,"text":"从前他们确曾对安拉结约：（不掉过背去）。 安拉的约是该负责的。","translator":"王静斋"},{"sura":33,"aya":16,"text":"你说：（如果你们欲脱开死亡或被杀，那逃跑绝于你们无济。到那时候，你们只能得到少许的享受）。","translator":"王静斋"},{"sura":33,"aya":17,"text":"你说！（若是安拉意欲你们遭患难，谁是抵抗安拉护佑你们的；或是意欲加惠你们[谁能祸害你们。]）他们撇开安拉得不到一爱护的，也无一相助的。","translator":"王静斋"},{"sura":33,"aya":18,"text":"安拉确知道你们之中一般阻止的，与对自己弟兄们说到我们这里来）的，他们临战不过些微的。","translator":"王静斋"},{"sura":33,"aya":19,"text":"对你们悭吝着。一旦骇怕上来，你就瞧见他们看你；其目转动，有如因死而昏晕的人。直到骇怕已去的时候，他们就用利舌伤害你们；在好事上暴露吝啬着。这般人原未归信。所以安拉使他们的举动归为鸟有了。这是在安拉上容易的。","translator":"王静斋"},{"sura":33,"aya":20,"text":"他们猜度，联军尚未走去。如果联军复来，他们就盼望移到郊野，和居乡的人同住；探问你们的消息，设若他们参加你们之中了，他们不过些微的格杀。","translator":"王静斋"},{"sura":33,"aya":21,"text":"安拉的使者确是一般盼望安拉与末日并多记念安拉的人一个好的典型。","translator":"王静斋"},{"sura":33,"aya":22,"text":"当众穆民看见联军的时候，他们就说：（这是安拉与他的使者许给我们，安拉与他的使者实现所约的那个。）这只是益增他们归信与顺服。","translator":"王静斋"},{"sura":33,"aya":23,"text":"穆民之中有些人，实践对安拉所约定的；他们之中有死去的；他们之中有等待的。他们未曾予以变更，予以变更。","translator":"王静斋"},{"sura":33,"aya":24,"text":"终至安拉向一般（保持）诚实的人们，因其诚实而施以报偿。并惩罚伪信士们，若是他意欲——的时候；或是允他们悔罪。安拉实是多恕的，特慈的。","translator":"王静斋"},{"sura":33,"aya":25,"text":"安拉驱回众逆徒， 乘其忿恨，未曾得到好处着。安拉维护众穆民作格斗。 安拉是大力的，优胜的。","translator":"王静斋"},{"sura":33,"aya":26,"text":"他使有经人中臂助他们的人退下自己的堡垒。他把恐怖投在他们心中。你们杀死一部分，俘获一部分。","translator":"王静斋"},{"sura":33,"aya":27,"text":"他教你们继承他们的地方、住宅、财产、与你们未曾踏过的地方。安拉是全能于万事的。","translator":"王静斋"},{"sura":33,"aya":28,"text":"圣人哪！你对自己的妻室们说：（如果你们讨今生和它的装饰了，你们来，我送给你们赠品；我遣退你们，好好地遣退。","translator":"王静斋"},{"sura":33,"aya":29,"text":"若是你们要安拉和他的使者与后世了，安拉必因你们之中一般为善的人预备大赏。）","translator":"王静斋"},{"sura":33,"aya":30,"text":"圣宫们哪！你们之中发现显著的丑恶的人，则对她增 加两倍刑罚。这是在安拉上容易的。","translator":"王静斋"},{"sura":33,"aya":31,"text":"你们之中，听命于安拉和他的使者，并作善举的人，我就赐给她两次善赏。我已为她备下高尚的给养了。","translator":"王静斋"},{"sura":33,"aya":32,"text":"圣宫们哪！你们不比任何一个女子。如果你们畏惧了，你们在说话上不可过于谦和，以免心中有病的人怀着欲望。你们要说明朗的话。","translator":"王静斋"},{"sura":33,"aya":33,"text":"你们当居在自己家里；不要炫弄，一如从前愚昧时代的炫弄。你们当立拜功，施天课，顺从安拉和他的使者。圣眷哪！安拉惟欲给你们释去污秽；使你们清洁，切使清洁。","translator":"王静斋"},{"sura":33,"aya":34,"text":"你们当记念在自己家里所诵的——安拉的表征与学识。安拉实是妙知的，尽知的。","translator":"王静斋"},{"sura":33,"aya":35,"text":"的确，一般顺服的男女，归信的男女，听命的男女，诚实的男女，忍耐的男女，敬畏的男女，施济的男女，封斋的男女，以及遮掩羞体的男女，多记想安拉的男女，安拉已为他们预备饶恕与巨赏了。","translator":"王静斋"},{"sura":33,"aya":36,"text":"归信的男女，当安拉和他的使者判定一事的时候，在他们对自己的事不要随便。 违犯安拉和他使者的人，确已迷误，显著地迷误了。","translator":"王静斋"},{"sura":33,"aya":37,"text":"那时候，你告知那蒙安拉施恩，并蒙你施恩的人，说：（你要留下你的妻，你当畏惧安拉。）你心中隐匿安拉将要发布的事情，你怕人，安拉是至应该你怕他的。当宰笃对她结束需求了，我把她婚配给你，只为众穆民对于义子的妻，当他们彼此离异的时候，不至有些困难。安拉的事是通行的。","translator":"王静斋"},{"sura":33,"aya":38,"text":"圣人于安拉为他所规定的上无有困难。安拉对以前过去的众人的惯例。安拉的事情是已决的判定。","translator":"王静斋"},{"sura":33,"aya":39,"text":"那般人宣达安拉的敕令；他们怕他，不怕除过安拉的任何一个。安拉作审算的已可满足了。","translator":"王静斋"},{"sura":33,"aya":40,"text":"穆罕默德不是你们哪一个人的父，只是安拉的使者，且是列圣最后的一位。安拉是深知万事的。","translator":"王静斋"},{"sura":33,"aya":41,"text":"众归信的人哪，你们当记念安拉，多的记念；","translator":"王静斋"},{"sura":33,"aya":42,"text":"你们当朝夕颂他清净！","translator":"王静斋"},{"sura":33,"aya":43,"text":"他是降福于你们的主，他的众天使亦然。只为使你们从黑暗里出至光明。他是特慈众穆民的。","translator":"王静斋"},{"sura":33,"aya":44,"text":"他们的庆祝在见主的那日是道安宁。他已为他们预备上赏了。","translator":"王静斋"},{"sura":33,"aya":45,"text":"圣人哪！我确差你作见证，报喜信，传警告的，","translator":"王静斋"},{"sura":33,"aya":46,"text":"奉他的命令唤导至安拉的，且是光明的灯。","translator":"王静斋"},{"sura":33,"aya":47,"text":"你当报告众穆民！他们必获安拉的大恩。","translator":"王静斋"},{"sura":33,"aya":48,"text":"你不要顺从不信的人与伪信士。你不要介意他们的扰害；你当仰赖安拉：安拉作维护的已可满足了。","translator":"王静斋"},{"sura":33,"aya":49,"text":"众归信的人哪，你们娶得穆民女子，随后在未接触以前休弃的时候，她们不应该为你们守那规定的期限。你们应送给她们赠品，你们要遣退她们，好好地遣退。","translator":"王静斋"},{"sura":33,"aya":50,"text":"圣人哪！我因为你列为合法的，是你付给聘仪的妻；和你手下的人，就是安拉特为归给你的；与你伯叔的女儿，姑母的女儿，舅父的女儿，姨母的女儿 ，一般随你迁移的与穆民女子，若是她以身许给圣人，若是圣人意欲娶她的时候，超过众穆民，惟独归你，我确知道为他们的妻和他们手下人。规定的以免你感觉困难。安拉是多恕的，特慈的。","translator":"王静斋"},{"sura":33,"aya":51,"text":"你随便退散她们；你随便招致。你不妨寻回自所遗弃的。这是最近于她们愉快无忧，均都满意你所给她们的。安拉知道你们心怀的，安拉是深知的，仁厚的。","translator":"王静斋"},{"sura":33,"aya":52,"text":"在这以后的女子于你不相宜；你也不可以她们改换别的妻，纵令她们的貌美使你爱慕。你手辖的在外。安拉是监察万事的。","translator":"王静斋"},{"sura":33,"aya":53,"text":"众归信的人哪！你们不得进圣人的住室；除非准许你们就食的时候，不得等侯煮熟它；但是有人唤你们的时候，你们就进去，吃罢的时候，就要散开，不得为着谈话而留连，这事妨碍圣人。他对你们羞惭。安拉于正言上不羞惭。一旦你们向圣妻们寻求什物了，你们就从帐后向她们寻求。这是至洁净你们心和她们心的，你们不该妨害安拉的使者；也永不得在他以后娶他的妻。这在安拉视为大罪。","translator":"王静斋"},{"sura":33,"aya":54,"text":"如果你们表现一事，或隐匿一事了，安拉是深知万事的。","translator":"王静斋"},{"sura":33,"aya":55,"text":"她们不妨（会晤）自己的父亲、儿子、弟兄、侄男、甥男、同类的女子，以及手下的人。你们当畏惧安拉。安拉实是见证万事的。","translator":"王静斋"},{"sura":33,"aya":56,"text":"的确，安拉及他的众天使尊敬圣人。众归信的人哪！你们当赞他，并（为他）祝安，切实地祝安。","translator":"王静斋"},{"sura":33,"aya":57,"text":"的确，扰害安拉和他的使者的人，安拉在今世后世怒恼他们，并为他们预备辱刑。","translator":"王静斋"},{"sura":33,"aya":58,"text":"无端地扰害男女穆民的人，确担负毁谤与显明的罪了。","translator":"王静斋"},{"sura":33,"aya":59,"text":"圣人哪！你对自己的妻女，以及众穆民的妇女们说：（让她们身披长衫。 这是最近于被人辨认，不致于受扰害的。安拉是多恕的，特慈的。）","translator":"王静斋"},{"sura":33,"aya":60,"text":"伪信士，与心中有病的人，和在默底纳传播谣言的人，若不停止了，我必教你对付他们；而后他们在那里仅可短时间的和你为邻，均遭怒恼着。","translator":"王静斋"},{"sura":33,"aya":61,"text":"在不拘哪里遇到他们，就可捉获他们，杀死他们，切实地杀死。","translator":"王静斋"},{"sura":33,"aya":62,"text":"这是安拉对于从前过去那般人的惯例。你于安拉的惯例上绝不见得变换。","translator":"王静斋"},{"sura":33,"aya":63,"text":"人们向你询问复生日；你就说：（知它惟在安拉，）你从何知道：恐怕复生日相近了，","translator":"王静斋"},{"sura":33,"aya":64,"text":"安拉怒恼不信的人们，并为他们预备烈火了。","translator":"王静斋"},{"sura":33,"aya":65,"text":"他们永久居在那里，得不到一爱护的，与相助的。","translator":"王静斋"},{"sura":33,"aya":66,"text":"他们的面目在火中转动的那一日，他们就说：（哎，宁愿我们顺从安拉了，顺从使者了。）","translator":"王静斋"},{"sura":33,"aya":67,"text":"他们说：（养主啊！我们确顺从自己的首领与伟人了，他们诱我们迷路了。","translator":"王静斋"},{"sura":33,"aya":68,"text":"养主啊！你给他们加倍的刑罚吧！你怒恼他们，很大地怒恼吧！）","translator":"王静斋"},{"sura":33,"aya":69,"text":"众归信的人哪，你们不要像一般陷害母撒的人似的；他们所说的那个曾经安拉证明于他无干。他在安拉御前是有面分的。","translator":"王静斋"},{"sura":33,"aya":70,"text":"众归信的人哪！你们当畏惧安拉，当说正言，","translator":"王静斋"},{"sura":33,"aya":71,"text":"他就规正你们的作为，饶恕你们的罪恶。顺从安拉和他的使者的人，他确得到大的收获了。","translator":"王静斋"},{"sura":33,"aya":72,"text":"我把「信托」现给天地群山了，然而它们拒绝担负，对它骇怕。人类担负它了；——他是自亏的，愚昧的。","translator":"王静斋"},{"sura":33,"aya":73,"text":"终至安拉罪刑男女伪信士，与男女信偶像的；安拉允承男女穆民的悔罪。安拉是多恕的，特慈的。","translator":"王静斋"},{"sura":34,"aya":1,"text":"赞颂安拉惟他掌理在天在地的，惟他在后世受赞颂。他是明哲的，尽知的。","translator":"王静斋"},{"sura":34,"aya":2,"text":"他知道那入地出地的，和降自天上，升在天空的，他是特慈的，多恕的。","translator":"王静斋"},{"sura":34,"aya":3,"text":"不信的人们说：（复生日来不到我们上。）你说：（不是的。）指着我的养主…，知道目不能见的主为誓：它一定要来至你们。）在天地间无一尘之重的离远他。所有比这较小较大的，无一不记在明典中。","translator":"王静斋"},{"sura":34,"aya":4,"text":"为的是赏赐一般归信并作善举的人。这般人可获饶恕与高尚的给养。","translator":"王静斋"},{"sura":34,"aya":5,"text":"一般对我的表征努力抵抗的人， 均应遭受致痛的恶刑。","translator":"王静斋"},{"sura":34,"aya":6,"text":"蒙赐知识的人们，视那降自养主的即是真的。它引导至优胜受赞的主道。","translator":"王静斋"},{"sura":34,"aya":7,"text":"不信的人们说：（你们已被粉碎，完全粉碎的时候，定受重新造化的，（这话）我们指出一个人来告诉你们，可以吗?","translator":"王静斋"},{"sura":34,"aya":8,"text":"他往安拉上造作谎言呢?或是染有疯症呢?不然，不信后世的人是在刑罚与逗远的迷误中。","translator":"王静斋"},{"sura":34,"aya":9,"text":"他们岂未见其前面和后面的天地吗?我若意欲…了，我就以地陷下他们，或把一块天落到他们上。此中对于一切归向的仆人确含有一种表征。","translator":"王静斋"},{"sura":34,"aya":10,"text":"的确，我把自己的特恩赐给达伍德了，我说：山哪！你随他和谐吧！（我为他制服了）飞禽， 我为他化铁为柔软的。","translator":"王静斋"},{"sura":34,"aya":11,"text":"（说：）你制造长大的铠甲，你在编织上要守限度。你们当作善举！我实看得见你们的作为。","translator":"王静斋"},{"sura":34,"aya":12,"text":"并为苏来茫降服风，它的早程是一个月，它的晚程是一个月。我为他涌出铜泉来。镇呢中有奉养主的命令，在他面前工作的。凡是违犯我命令的，我就教他尝火刑。","translator":"王静斋"},{"sura":34,"aya":13,"text":"他们为他制造他所意欲——的，宫殿，形像，如池塘的大碑与稳定的锅。达伍德的后裔啊！你们因为感念而实行工作吧！我仆人中感念的人是少数的。","translator":"王静斋"},{"sura":34,"aya":14,"text":"我判他死去的时候，对他们指明他死去的，只是啮他拐杖的一个木虫。待他跌倒的时候，镇呢才明白！设若他们知道目不能见的了，则不致迟留在辱刑中。","translator":"王静斋"},{"sura":34,"aya":15,"text":"在塞白人住的地方，有一个表征，右面与左面的两座园林；（我说：）你们就着养主所供给的吃吧！你们应当感念他。清洁的城池，多恕的养主。","translator":"王静斋"},{"sura":34,"aya":16,"text":"而后他们违犯了。于是我向他们发下瀑布，我把他们的两座园林化成有酸果、柽柳、与几许少数的「席代雷」的二园。","translator":"王静斋"},{"sura":34,"aya":17,"text":"这是我因他们忘恩施以还报。我只是还报忘恩的人。","translator":"王静斋"},{"sura":34,"aya":18,"text":"我在他们和我降福的城池中间安置连亘的些个村落。我在那里规定行程，（说：）你们在它里夜昼安然着行走吧！","translator":"王静斋"},{"sura":34,"aya":19,"text":"他们说：（养主啊！求你隔远我们旅途的距离。）他们自负己身了，我使他们作为一般谈料。我粉碎了他们，完全地粉碎。此中对于一切忍耐感念的人，确含有种种表征。","translator":"王静斋"},{"sura":34,"aya":20,"text":"以卜里厮曾见对他们的揣度已经实现了。而后他们就追随他。一部分穆民在外。","translator":"王静斋"},{"sura":34,"aya":21,"text":"在他无有对他们的权柄。我只不过为着知道谁信后世，谁对它怀疑。你的养主是保护万事的。","translator":"王静斋"},{"sura":34,"aya":22,"text":"你说：（你们撇开安拉呼求自所妄揣的吧！）他们不能掌天地间一尘之重的。 他们并未参加造化天地。在真主也不需要他们臂助。","translator":"王静斋"},{"sura":34,"aya":23,"text":"在主御前告赦，惟利于那得到真主许可的人。他们心中的（恐怖）被释的时候，就说：（你们的养主说什么了?）他们说：（实言。）他是伟大的，玄高的。","translator":"王静斋"},{"sura":34,"aya":24,"text":"你说，（谁就着天地赐给你们给养?）你说：（安拉，我和你们谁在正道上?谁在显著的迷误上?）","translator":"王静斋"},{"sura":34,"aya":25,"text":"你说：（你们于我们所作的罪恶不负责任；我们于你们所作的不负责任。）","translator":"王静斋"},{"sura":34,"aya":26,"text":"你说：（养主将在我们中间集合，且在我们中间秉公判断。他是公断的，深知的，）","translator":"王静斋"},{"sura":34,"aya":27,"text":"你说：（你们把自给真主结为匹偶的那一切现给我。）不可呀！不然，他是优胜的，明哲的安拉。","translator":"王静斋"},{"sura":34,"aya":28,"text":"我只是为人类大众差你报喜信，传警告着；但是世人的多半不知道。","translator":"王静斋"},{"sura":34,"aya":29,"text":"他们说：（这约是在何时?如果你们是实言的了……）。","translator":"王静斋"},{"sura":34,"aya":30,"text":"你说，（在你们有一个日期，你们于那一日不能退后一时，也不能提前）","translator":"王静斋"},{"sura":34,"aya":31,"text":"不信的人说：（我们不信这古兰，也不信它以前的。）当背义的人们被拘在养主御前，彼此接谈的时候，设若你看见了。","translator":"王静斋"},{"sura":34,"aya":32,"text":"一般无能为的人对高傲的人说：若不是你们，我们一定是归信的了。 ） 高傲的人对无能为的人说：（在正道达至你们以后，我们阻止过你们吗?你们实是为恶的人。）","translator":"王静斋"},{"sura":34,"aya":33,"text":"一般无能为的人对高傲的人说：（不然，是夜昼的阴谋。那时候，你们命我们不信安拉，并为他拟同等的。）他们亲见刑罚的时候暗怀悔恨。我给逆徒的脖子带上火枷。惟依照他们所作的施以还报。","translator":"王静斋"},{"sura":34,"aya":34,"text":"每逢我把一个传警告的遣到一个城池，那地方的富户就说：（我们不信你们所负的使命，）","translator":"王静斋"},{"sura":34,"aya":35,"text":"且说：（我们是财产与子女至多的；我们不该受罪刑。）","translator":"王静斋"},{"sura":34,"aya":36,"text":"你说！（我的养主确为他意欲——的人伸缩给养。但是世人的多半不知道。）","translator":"王静斋"},{"sura":34,"aya":37,"text":"你们的财富与子女绝不是使你们接近我，切实接近的。归信并作善举的人在外。这般人因其所作的获到加倍的善赏，安居在高楼中。","translator":"王静斋"},{"sura":34,"aya":38,"text":"对我的表征努力抵抗的人，均是亲临到刑罚的。","translator":"王静斋"},{"sura":34,"aya":39,"text":"你说：我的养主必为其意欲——的众仆伸缩给养。你们所使用的无论何物，真主将补还给。他是无上的赐予给养者。","translator":"王静斋"},{"sura":34,"aya":40,"text":"当那一日，主把他们一同集起，随后对众天使说：（这些人曾拜过你们吗?）","translator":"王静斋"},{"sura":34,"aya":41,"text":"他们说：（赞你清净，你是我们所爱喜的，不是他们。不然，他们原拜镇呢了；他们统是信它们的。）","translator":"王静斋"},{"sura":34,"aya":42,"text":"今日，你们彼此一个不能为一个掌利害。我对一般背义的人说：（你们尝试曾经不信的火刑吧！）","translator":"王静斋"},{"sura":34,"aya":43,"text":"一旦有人把我显明的表征读给他们了，他们就说：（这是意欲阻止你们离开列祖所拜的那个人。）他们说：（这只是造作的谎言罢了）不信的人在得到真理的时候，就指着它说：（这不外是一种显著的邪术。）","translator":"王静斋"},{"sura":34,"aya":44,"text":"我原未赐过他们经典诵读。在你以前，我未向他们差过传警告的。","translator":"王静斋"},{"sura":34,"aya":45,"text":"他们以前的人们不信服。这般人尚未达到我赐给前人的十分之一咧。那些人们不信我的列使，我的惩罚如何?","translator":"王静斋"},{"sura":34,"aya":46,"text":"你说：我劝你们一句话，就是你们因为安拉两个两个，一个一个地站起，既而默思，你们的同伴无有一点疯症；他不外是在烈刑以前，向你们传警告的。","translator":"王静斋"},{"sura":34,"aya":47,"text":"你说，（我未向你们讨些微的工资，那是你们的所有。我的工资惟在安拉上；他是见证万事的）","translator":"王静斋"},{"sura":34,"aya":48,"text":"你说！（的确，我的养主输以真理；他是知道目不能见的。）","translator":"王静斋"},{"sura":34,"aya":49,"text":"你说：（真的来了；假的不能肇造，也不能恢复。）","translator":"王静斋"},{"sura":34,"aya":50,"text":"你说：（若是我迷误了，我只害我自身迷误；我若获正道了，那是藉我养主的启示。主确是能听的，切近的。）","translator":"王静斋"},{"sura":34,"aya":51,"text":"当他们骇怕的时候，设若你看见……。无有逃脱，均被提自近处；","translator":"王静斋"},{"sura":34,"aya":52,"text":"他们声称归信了，而怎能得自远处呢?","translator":"王静斋"},{"sura":34,"aya":53,"text":"从前他们确曾不信他；他们从远处加以揣度。","translator":"王静斋"},{"sura":34,"aya":54,"text":"他们和自所想望的彼此间已被隔离，一如对待他们以前的同类似的。的确，他们是在不安的怀疑中。","translator":"王静斋"},{"sura":35,"aya":1,"text":"赞颂安拉，造化天地的；造天使为有二翼、三翼、四翼的。他在造化上任便增加。安拉实是全能于万事的。","translator":"王静斋"},{"sura":35,"aya":2,"text":"安拉为世人开放不拘什么慈惠，无有制止它的，他所制止的，无有在它以后开放的。他是优胜的，明哲的。","translator":"王静斋"},{"sura":35,"aya":3,"text":"众人哪！你们当记念安拉对你们的恩典。可有除过安拉的造物主，能就着天地赐予你们给养吗?除他以外再无有主。你们转变到哪里?","translator":"王静斋"},{"sura":35,"aya":4,"text":"若是他们不信服你了。（不要忧虑；）在你以前的列使确曾遭人不信服了。诸事惟被归到安拉。","translator":"王静斋"},{"sura":35,"aya":5,"text":"众人哪！安拉的约确是实在的。你们不要被今生所骗，也莫容欺骗的借着安拉欺骗你们。","translator":"王静斋"},{"sura":35,"aya":6,"text":"恶魔确是你们的仇敌；你们当把他当作仇敌。他唤自己的同伙，好教他们归为火狱的人。","translator":"王静斋"},{"sura":35,"aya":7,"text":"不信的人应遭烈刑。众归信并作善举的人，可获饶恕与大赏。","translator":"王静斋"},{"sura":35,"aya":8,"text":"个人的恶行受了粉饰，而自视为美的人，可像那得到真主引导的人吗? 安拉造自所意欲——的人迷误，导自所意欲——的人得道。你不要因为忧虑他们而害及自身。安拉深知他们的作为。","translator":"王静斋"},{"sura":35,"aya":9,"text":"安拉，他降风，激云，既而把它驱至死城；从而用它活地于既死以后。复生也是这样，","translator":"王静斋"},{"sura":35,"aya":10,"text":"讨荣耀的人，荣耀统属安拉的。清洁的话上升至真主。善举只有真主把它提高。阴谋恶事的人，自应遭受烈刑。他们的阴谋即要消灭。","translator":"王静斋"},{"sura":35,"aya":11,"text":"安拉造你们由土上，再由一点精上，又使你们结成配偶。女子怀妊与生产，无一不经他知道。任何一个有寿命的，寿长寿短，全都载在典章上。这在安拉是容易的。","translator":"王静斋"},{"sura":35,"aya":12,"text":"两海不相等；这一边是极甜味甘，容易下咽的；那一边是苦咸的。你们于每一边吃鲜肉。你们发现装饰品，佩带它。你看船在海内破浪行驶；以便你们讨他的特恩，冀使你们感念。","translator":"王静斋"},{"sura":35,"aya":13,"text":"他纳夜于昼，纳昼于夜。他降服日月，每一个行到一定的时期。这是安拉：调养你们的主，惟他掌权柄。你们撇开他所呼求的那一切，他们不能掌丝毫。","translator":"王静斋"},{"sura":35,"aya":14,"text":"你们若呼求它们了，它们听不到你们的呼求，设若它们听到了，则不能应承你们。在复生日，它们不承认你们的信匹偶。无有像尽知的主告诉你的。","translator":"王静斋"},{"sura":35,"aya":15,"text":"众人哪！你们是求安拉的；安拉即是无求的，受赞的。","translator":"王静斋"},{"sura":35,"aya":16,"text":"他若意欲——了，他就去掉你们，而拿来新的造化。","translator":"王静斋"},{"sura":35,"aya":17,"text":"这在安拉并不困难。","translator":"王静斋"},{"sura":35,"aya":18,"text":"负重担的人?不负别个人的重担。若是负重的人唤人担负他的重担了，无人担负他一事，纵令他是近亲。你仅可警告那在未见着惧怕养主，并立拜功的人。清洁的人，只是利他自身清洁。归惟归到安拉。","translator":"王静斋"},{"sura":35,"aya":19,"text":"瞎子和有眼的不相等；","translator":"王静斋"},{"sura":35,"aya":20,"text":"黑暗与光明也不；","translator":"王静斋"},{"sura":35,"aya":21,"text":"阴影和炎热也不。","translator":"王静斋"},{"sura":35,"aya":22,"text":"活的和死的不相等；安拉确教自所意欲……的人听到；你不是教墓中人听到的。","translator":"王静斋"},{"sura":35,"aya":23,"text":"你不过是一个传警告的。","translator":"王静斋"},{"sura":35,"aya":24,"text":"我只因为真理差你报喜信传警告着。任何一伙人，全有一个传警告的过到那里了。","translator":"王静斋"},{"sura":35,"aya":25,"text":"若是他们不信服你，（不要忧虑），因为他们以前的人们确不信服了。列使把种种明证、册籍，与光明的经典拿给他们了。","translator":"王静斋"},{"sura":35,"aya":26,"text":"而后我惩治一般不信的人了；我的惩罚如何?","translator":"王静斋"},{"sura":35,"aya":27,"text":"你岂未看见安拉由上空降水，而用它发现色色不一的果实?山上有些颜色不一白的红的，与黑而且黑的走道；","translator":"王静斋"},{"sura":35,"aya":28,"text":"人类、动物、与家畜里，有如此颜色不同的。安拉的仆人中惟有一般学者惧怕他。安拉实是优胜的，多恕的。","translator":"王静斋"},{"sura":35,"aya":29,"text":"的确，一般诵天经，立拜功，并就着我所赐给的，暗昧、公开着使用的人，均盼望那绝无消沉的营业；","translator":"王静斋"},{"sura":35,"aya":30,"text":"为的是完全施给他们报酬，并增给他们特恩。他实是多恕的，颁赏的。","translator":"王静斋"},{"sura":35,"aya":31,"text":"我告谕你的经典就是真的；证实在它以前的着。安拉确是尽知，看见他的众仆的。而后我教自所选择的仆人承受经典。","translator":"王静斋"},{"sura":35,"aya":32,"text":"他们共中有自亏己身的；有持中的；有循安拉的旨意兢行种种善举的。这就是大恩。","translator":"王静斋"},{"sura":35,"aya":33,"text":"他们将进常住的天园，在那里得到金钏与珍珠的装饰。他们在那里的服装是绫锦。","translator":"王静斋"},{"sura":35,"aya":34,"text":"他们说：（赞颂那给我们释去忧愁的安拉。我们的养主确是多恕的，颁赏的。","translator":"王静斋"},{"sura":35,"aya":35,"text":"也就是那以自己的特恩教我们下到常居的、第宅的主。我们在那里见不到苦楚，也见不到疲乏。","translator":"王静斋"},{"sura":35,"aya":36,"text":"不信的人应遭火狱的火。他们不获解决，而怎能死去；且也不给他们减轻火刑。我就是这样地还报一切忘恩的人。","translator":"王静斋"},{"sura":35,"aya":37,"text":"他们在那里呼救！（养主啊，你容我们出去吧！我们就作善举，一反从前所作的。）我岂未教你们活到纳劝的人可能于中纳劝的工夫吗?传警告的人已然来到你们跟前了；你们尝试（刑罚）吧！背义的人不获一相助的。","translator":"王静斋"},{"sura":35,"aya":38,"text":"安拉知道天地的隐秘。他确深知胸中所怀的。","translator":"王静斋"},{"sura":35,"aya":39,"text":"他是把你们置为在地上继替的。忘恩的人，他的忘恩惟于个人有害。逆徒的不信，仅只增加养主的怒恨。逆徒的不信只增加自己的伤折。","translator":"王静斋"},{"sura":35,"aya":40,"text":"你说：（你们曾见自所超过安拉呼求的吗?你们现给我！他们由地上造化什么了?）不然，他们于（造）天上共同吗? 不然，我赐给他们经典。他们守其明证吗?不然，背义的人们彼此只以欺骗相期许。","translator":"王静斋"},{"sura":35,"aya":41,"text":"安拉保留天地不至更动。如果天地有了更动，在他以后无有一个能保留它俩的。他实是仁厚的，多恕的。","translator":"王静斋"},{"sura":35,"aya":42,"text":"他们指着安拉矢重誓：（如果有一个传警告的人到来，他们一定是比任何一族至能得正道的。）及至传警告的人到来的时候，那愈增他们逃遁了；","translator":"王静斋"},{"sura":35,"aya":43,"text":"只为在地上行高傲与弄恶谋。恶谋只是临到其人。他们只是等侯众前人的惯例。 你在安拉的惯例上绝不见得改换；你在安拉的惯例上绝不见得变更。","translator":"王静斋"},{"sura":35,"aya":44,"text":"他们岂未在地上旅行而观察；他们以前众人的结果如何吗?他们在力量上原是比这般人雄厚的。安拉，天地间无一事可能抵抗他。他实是深知的，全能的。","translator":"王静斋"},{"sura":35,"aya":45,"text":"设若安拉因着人们所作的而施以惩罚了，他就不把一个动物遗在地上；但是他姑容他们，直到一定的时期。当他们的时期既至的时候，安拉实是看得见自己的众仆的。","translator":"王静斋"},{"sura":36,"aya":1,"text":"（押歆。）","translator":"王静斋"},{"sura":36,"aya":2,"text":"试思含有奥义的古兰；","translator":"王静斋"},{"sura":36,"aya":3,"text":"你实是负着使命，","translator":"王静斋"},{"sura":36,"aya":4,"text":"守正路的。","translator":"王静斋"},{"sura":36,"aya":5,"text":"忧胜特慈的主降下的，","translator":"王静斋"},{"sura":36,"aya":6,"text":"好教你警告一伙人，他们的列祖未受过警告，至为昏溃的。","translator":"王静斋"},{"sura":36,"aya":7,"text":"话已对他们大多数人决定了。所以他们不归信。","translator":"王静斋"},{"sura":36,"aya":8,"text":"我给他们的脖子带上枷。枷至下颏，他们是仰首闭目的。","translator":"王静斋"},{"sura":36,"aya":9,"text":"我在他们前面安置一个隔障，并在他们后面安置一个隔障；我遮覆他们了，所以他们看不见。","translator":"王静斋"},{"sura":36,"aya":10,"text":"你警告或不警告，他们是一样地不归信。","translator":"王静斋"},{"sura":36,"aya":11,"text":"你只可警告追随教戒，并在未见着惧怕慈主的人。你以饶恕与上赏给他报喜信吧！","translator":"王静斋"},{"sura":36,"aya":12,"text":"的确，我复活死者；我写下他们前送的。和他们的足迹。一切的事情，我已把它记在明典上。","translator":"王静斋"},{"sura":36,"aya":13,"text":"你为他们发一个比喻。一个城市的人，诸使去到那里的时候，","translator":"王静斋"},{"sura":36,"aya":14,"text":"当时，我向他们差去二人；但是他们不信服他俩，我复辅以第三者。他们说： （我们是被差至你们的）","translator":"王静斋"},{"sura":36,"aya":15,"text":"他们说：（你们只是和我们一样的人；慈主并未降下一事，你们只是撒谎罢了。）","translator":"王静斋"},{"sura":36,"aya":16,"text":"他们说：养主知道；我们实是向你们差来的。","translator":"王静斋"},{"sura":36,"aya":17,"text":"我们应负的，只是公开的宣达。","translator":"王静斋"},{"sura":36,"aya":18,"text":"他们说：（我们以你们卜为不吉了。如果你们不肯停止，我们必用石子击死你们，你们将必尝我们的痛刑。）","translator":"王静斋"},{"sura":36,"aya":19,"text":"他们说：（你们的不吉是追随你们的。若是有人劝你们，也可这样吗? 不然，你们是一伙妄为的人。）","translator":"王静斋"},{"sura":36,"aya":20,"text":"有一个人自那城的远处匆忙到来，他说：我的族人哪！你们追随众使吧！","translator":"王静斋"},{"sura":36,"aya":21,"text":"你们追随不向你们索工资的人吧！他们本是得正道的。","translator":"王静斋"},{"sura":36,"aya":22,"text":"（我怎肯不拜那造化我，将把你们归返于他的主呢?","translator":"王静斋"},{"sura":36,"aya":23,"text":"我能撇开他另取多主吗?若是慈主意欲毁我了，它们的告赦不能代我免除一事；它们不能解救我。","translator":"王静斋"},{"sura":36,"aya":24,"text":"果然是那样，我确是在显著的迷误中。","translator":"王静斋"},{"sura":36,"aya":25,"text":"我实归信你们的养主了；你们听从我吧！）","translator":"王静斋"},{"sura":36,"aya":26,"text":"有的说：（你进天园吧，）他说：（哎，宁愿我的族人，知道","translator":"王静斋"},{"sura":36,"aya":27,"text":"养主因着什么饶恕我，并列我为受尊敬的人。）","translator":"王静斋"},{"sura":36,"aya":28,"text":"在他以后，我未向他的族人遣去一个天兵。我向未派遣过。","translator":"王静斋"},{"sura":36,"aya":29,"text":"那不过是一声呜。他们立时是消灭的。","translator":"王静斋"},{"sura":36,"aya":30,"text":"可叹众仆！每有一使者到达，他们无不嘲笑他。","translator":"王静斋"},{"sura":36,"aya":31,"text":"他们岂未看见（我在他们以前灭绝若干代，那般人不能转回他们?）","translator":"王静斋"},{"sura":36,"aya":32,"text":"统不外是集在我御前聚合的。","translator":"王静斋"},{"sura":36,"aya":33,"text":"死地，就是对他们的一种表征；我复活它，我从那里发现子粒，他们就着它吃。","translator":"王静斋"},{"sura":36,"aya":34,"text":"我在那里造化枣树与葡萄园，并在那里涌出源泉，","translator":"王静斋"},{"sura":36,"aya":35,"text":"好教他们吃它的果实，和他们手制的。他们怎不感念呢?","translator":"王静斋"},{"sura":36,"aya":36,"text":"赞主清净！他曾造化了各种匹偶，——若地里生长的，和属于他们同类的，与属于他们所不知道的。","translator":"王静斋"},{"sura":36,"aya":37,"text":"夜也是对他们的一种表征；我自它上剥脱白昼，他们忽然入到黑暗里。","translator":"王静斋"},{"sura":36,"aya":38,"text":"太阳运行到它的定所。这是优胜深知的主的规定。","translator":"王静斋"},{"sura":36,"aya":39,"text":"月我给它规定些个站道，而致缩若干枯的枣树枝。","translator":"王静斋"},{"sura":36,"aya":40,"text":"太阳不应追上月，夜不能争先白昼。统浮游在天体上。","translator":"王静斋"},{"sura":36,"aya":41,"text":"又一对他们的表征，我教他们的子孙乘上满载的船。","translator":"王静斋"},{"sura":36,"aya":42,"text":"我为他们造化类如船只的。","translator":"王静斋"},{"sura":36,"aya":43,"text":"我若意欲……了，我就淹没他们，无一拯救他们的。他们不能得救；","translator":"王静斋"},{"sura":36,"aya":44,"text":"除非是借着我的慈惠，享受到一个时期。","translator":"王静斋"},{"sura":36,"aya":45,"text":"一旦有人对他们说：（你们当畏惧自己前面后面的；好教你们蒙得慈惠，……）","translator":"王静斋"},{"sura":36,"aya":46,"text":"养主的表征，无论哪项表征达到的时候，他们就远避它！","translator":"王静斋"},{"sura":36,"aya":47,"text":"一旦有人对他们说：（你们就着安拉赐给你们的作使用吧！）不信的人就对众穆民说：（设若安拉意欲（给赐）了，他就赐给食物；这样人可用我们供给食物吗?你们只是在显著的迷误中。）","translator":"王静斋"},{"sura":36,"aya":48,"text":"他们说：（这约是在几时呢? 如果你们所言属实了，……）","translator":"王静斋"},{"sura":36,"aya":49,"text":"他们只等侯遭遇一声鸣，其时正在争辩着。","translator":"王静斋"},{"sura":36,"aya":50,"text":"他们不能嘱告，也不能返回自己的家中。","translator":"王静斋"},{"sura":36,"aya":51,"text":"吹起了号筒，他们突然从坟里忙奔至养主，","translator":"王静斋"},{"sura":36,"aya":52,"text":"说：（哎我们悲苦啊！谁把我们从睡处唤起来?）这是慈主所约定，列使所实说的。","translator":"王静斋"},{"sura":36,"aya":53,"text":"那只是一声鸣；他们突然是集在我御前聚合的。","translator":"王静斋"},{"sura":36,"aya":54,"text":"今日，不亏人一事，惟本着你们所作的施以还报。","translator":"王静斋"},{"sura":36,"aya":55,"text":"今日，天圆的人是在一种事务里欢乐的。","translator":"王静斋"},{"sura":36,"aya":56,"text":"他们和自己的妻室在阴影下，倚靠在床榻上。","translator":"王静斋"},{"sura":36,"aya":57,"text":"他们在那里享受鲜果，并享受自所想望的。","translator":"王静斋"},{"sura":36,"aya":58,"text":"平安是由特慈的养主上说出的。","translator":"王静斋"},{"sura":36,"aya":59,"text":"为恶的人哪：今日，你们离开吧！","translator":"王静斋"},{"sura":36,"aya":60,"text":"阿丹的子孙哪：我岂未向你们约定！不要拜恶魔！他实是你们的明敌。","translator":"王静斋"},{"sura":36,"aya":61,"text":"你们应当拜我，这是正路。","translator":"王静斋"},{"sura":36,"aya":62,"text":"他确诱你们多的人迷误了，你们尚未了解吗?","translator":"王静斋"},{"sura":36,"aya":63,"text":"这是曾对你们所约定的火狱；","translator":"王静斋"},{"sura":36,"aya":64,"text":"今日，你们由于以往的不信进它吧，","translator":"王静斋"},{"sura":36,"aya":65,"text":"今日，我封闭他们的口。他们的手对我接谈；他们的脚见证自己所作过的。","translator":"王静斋"},{"sura":36,"aya":66,"text":"设我意欲——了，我就涂去他们的眼。他们奔驰在道路上，他们焉能看得见?","translator":"王静斋"},{"sura":36,"aya":67,"text":"设我意欲……了，我就在原地方变化他们，他们不能前进后退。","translator":"王静斋"},{"sura":36,"aya":68,"text":"我赐给长寿的人，我就教他在原造上颠倒；他们怎不了解呢?","translator":"王静斋"},{"sura":36,"aya":69,"text":"我未教给他诗，那也不是他容易作到的。这不外是一种教戒，与显明的古兰，","translator":"王静斋"},{"sura":36,"aya":70,"text":"以便他警告活人，并为所说的话在不信的人上决定。","translator":"王静斋"},{"sura":36,"aya":71,"text":"他们岂来看见我为他们造化亲手所做的家畜，他们能于管制它吗?","translator":"王静斋"},{"sura":36,"aya":72,"text":"我教它服从他们，内中有他们乘骑的。他们就着它吃，","translator":"王静斋"},{"sura":36,"aya":73,"text":"且于它上获得一切利益和饮料，他们怎不感念呢?","translator":"王静斋"},{"sura":36,"aya":74,"text":"他们舍去安拉取一切受拜的；希望得它们相助。","translator":"王静斋"},{"sura":36,"aya":75,"text":"它们不能相助他们。它们是为他们亲临的军兵。","translator":"王静斋"},{"sura":36,"aya":76,"text":"你莫因他们的话感觉忧虑，我实知道他们所隐所显的。","translator":"王静斋"},{"sura":36,"aya":77,"text":"人类岂未看见我自一点精水上造化他，忽然他是一个显著的敌人?","translator":"王静斋"},{"sura":36,"aya":78,"text":"他为我举一个比喻，他忘却造化他了。他说：（谁能复活这朽骨?）","translator":"王静斋"},{"sura":36,"aya":79,"text":"你说：（那初次造化它的主复活它。他是深知万有的。","translator":"王静斋"},{"sura":36,"aya":80,"text":"那真主为你们从绿树上现出火来，你们立时用它燃火。）","translator":"王静斋"},{"sura":36,"aya":81,"text":"造化天地的主岂不能于造化像他们的吗?是的， 他是造化的，深知的，","translator":"王静斋"},{"sura":36,"aya":82,"text":"他意欲一事的时候，他的命令只是对它说：（有）它立刻就有。","translator":"王静斋"},{"sura":36,"aya":83,"text":"赞颂真主，万物的权柄惟在他的掌握，你们将被归返于他。","translator":"王静斋"},{"sura":37,"aya":1,"text":"试思一切列班，列班的，","translator":"王静斋"},{"sura":37,"aya":2,"text":"一般驱逐，驱逐的，","translator":"王静斋"},{"sura":37,"aya":3,"text":"一般诵读赞言的；","translator":"王静斋"},{"sura":37,"aya":4,"text":"你们的主确是独一的；","translator":"王静斋"},{"sura":37,"aya":5,"text":"调养天地万有的，调养日升各地的。","translator":"王静斋"},{"sura":37,"aya":6,"text":"我确用装饰——群星装饰了至近的天，","translator":"王静斋"},{"sura":37,"aya":7,"text":"并防备每一个背逆的恶魔。","translator":"王静斋"},{"sura":37,"aya":8,"text":"他们不能向最高的天使侧耳；他们因遭受驱逐，被射自各方面；","translator":"王静斋"},{"sura":37,"aya":9,"text":"遭受常久的刑罚；","translator":"王静斋"},{"sura":37,"aya":10,"text":"除非是一度窃听，而被光耀的流星追逐的。","translator":"王静斋"},{"sura":37,"aya":11,"text":"你问他们：他们在造化上是至坚固的?或是我所造化的呢? 我从粘泥上造化了他们。","translator":"王静斋"},{"sura":37,"aya":12,"text":"不然，你奇异，他们嘲笑。","translator":"王静斋"},{"sura":37,"aya":13,"text":"有人劝他们的时候，他们不受劝。","translator":"王静斋"},{"sura":37,"aya":14,"text":"他们看见一项表征的时候，便就戏谑，","translator":"王静斋"},{"sura":37,"aya":15,"text":"且说：（这只是一种显著的邪术。）","translator":"王静斋"},{"sura":37,"aya":16,"text":"（我们死去而化为土与骨的时候，","translator":"王静斋"},{"sura":37,"aya":17,"text":"我们和我们前代的列祖必将复活吗?）","translator":"王静斋"},{"sura":37,"aya":18,"text":"你说，（是的，那时候，你们是微贱的。）","translator":"王静斋"},{"sura":37,"aya":19,"text":"那只是一声呜， 他们立就（痴）视，","translator":"王静斋"},{"sura":37,"aya":20,"text":"说（哎，我们悲苦啊！）这是还报日，","translator":"王静斋"},{"sura":37,"aya":21,"text":"这是你们曾经不信它的那判明日。","translator":"王静斋"},{"sura":37,"aya":22,"text":"你们集合一般背义的人，与他们的同伙，及他们撇开安拉所拜的，","translator":"王静斋"},{"sura":37,"aya":23,"text":"你们把他们领到火狱的路上，","translator":"王静斋"},{"sura":37,"aya":24,"text":"拘留起来，他们应受质问。","translator":"王静斋"},{"sura":37,"aya":25,"text":"你们怎么不能互助呢?","translator":"王静斋"},{"sura":37,"aya":26,"text":"不然，他们在今日是服从的。","translator":"王静斋"},{"sura":37,"aya":27,"text":"他们的一部分面向一部分，相互询问着，","translator":"王静斋"},{"sura":37,"aya":28,"text":"说：（你们曾由右边来至我们。）","translator":"王静斋"},{"sura":37,"aya":29,"text":"他们说：（不然，你们原不是归信的。","translator":"王静斋"},{"sura":37,"aya":30,"text":"我们无有对你们的权力。不然，你们是一般逆民。","translator":"王静斋"},{"sura":37,"aya":31,"text":"养主的话已然决定了。","translator":"王静斋"},{"sura":37,"aya":32,"text":"我们是将必尝试的。我们诱你们迷误，只因我们是自迷的。）","translator":"王静斋"},{"sura":37,"aya":33,"text":"当那时候他们共同受刑。","translator":"王静斋"},{"sura":37,"aya":34,"text":"我就这样地对待一般为恶的人。","translator":"王静斋"},{"sura":37,"aya":35,"text":"每逢有人对他们说：（安拉以外再无有主，）","translator":"王静斋"},{"sura":37,"aya":36,"text":"他们必就高傲起来，说：（我们必须为着一个疯狂的诗人放弃自己受拜的吗?）","translator":"王静斋"},{"sura":37,"aya":37,"text":"不然。他举出真理并证实列使了。","translator":"王静斋"},{"sura":37,"aya":38,"text":"你们将必尝试痛刑——","translator":"王静斋"},{"sura":37,"aya":39,"text":"将本着你们所作的施以还报，","translator":"王静斋"},{"sura":37,"aya":40,"text":"除非安拉的清白仆人；","translator":"王静斋"},{"sura":37,"aya":41,"text":"他们享受一定的供养品。","translator":"王静斋"},{"sura":37,"aya":42,"text":"各种鲜果，","translator":"王静斋"},{"sura":37,"aya":43,"text":"他们是在乐园里享受尊荣的。","translator":"王静斋"},{"sura":37,"aya":44,"text":"相向在床榻上，","translator":"王静斋"},{"sura":37,"aya":45,"text":"给他们轮斟盛有洁白流动，在饮者感觉味美的酒杯。","translator":"王静斋"},{"sura":37,"aya":46,"text":".","translator":"王静斋"},{"sura":37,"aya":47,"text":"那饮料不含灾害，他们也不因它致醉。","translator":"王静斋"},{"sura":37,"aya":48,"text":"在他们跟前有一切宽目的不妄视的女子。","translator":"王静斋"},{"sura":37,"aya":49,"text":"她们好像是被遮覆的驼鸡卵，","translator":"王静斋"},{"sura":37,"aya":50,"text":"他们的一部分面向一部分彼此相询着。","translator":"王静斋"},{"sura":37,"aya":51,"text":"他们的发言人说：（从前我有一个同伴，","translator":"王静斋"},{"sura":37,"aya":52,"text":"说：你肯信吗?","translator":"王静斋"},{"sura":37,"aya":53,"text":"我们死去，已成土与骨骸的时候，定受还报吗?）","translator":"王静斋"},{"sura":37,"aya":54,"text":"他说：（你们肯去望看吗?）","translator":"王静斋"},{"sura":37,"aya":55,"text":"他去望看，而见那人是在火狱的中心。","translator":"王静斋"},{"sura":37,"aya":56,"text":"他说：（指着安拉为誓：你几乎把我毁掉。","translator":"王静斋"},{"sura":37,"aya":57,"text":"若不是我养主的恩典了，我必定是亲临的。","translator":"王静斋"},{"sura":37,"aya":58,"text":"我们除去初次死亡，还能再死吗?","translator":"王静斋"},{"sura":37,"aya":59,"text":"我们必不会受罪刑。","translator":"王静斋"},{"sura":37,"aya":60,"text":"这确是大成功，","translator":"王静斋"},{"sura":37,"aya":61,"text":"让工作的人为着像这个的工作吧！）","translator":"王静斋"},{"sura":37,"aya":62,"text":"那 是至上的路餐?或是（凿骨末）树呢?","translator":"王静斋"},{"sura":37,"aya":63,"text":"我用那树磨难一般背义的人。","translator":"王静斋"},{"sura":37,"aya":64,"text":"它是在火狱底下发出来的树。","translator":"王静斋"},{"sura":37,"aya":65,"text":"它的果实如同是恶魔的头颅。","translator":"王静斋"},{"sura":37,"aya":66,"text":"他们是就着它吃，且以它满腹的。","translator":"王静斋"},{"sura":37,"aya":67,"text":"随后他们在那上饮搀杂滚水的，","translator":"王静斋"},{"sura":37,"aya":68,"text":"终必归入火狱。","translator":"王静斋"},{"sura":37,"aya":69,"text":"他们曾见自己列祖迷误，","translator":"王静斋"},{"sura":37,"aya":70,"text":"他们追随在后面奔驰。","translator":"王静斋"},{"sura":37,"aya":71,"text":"许多的先人在他们以前迷误了。","translator":"王静斋"},{"sura":37,"aya":72,"text":"我曾往他们之间遣去传警告的。","translator":"王静斋"},{"sura":37,"aya":73,"text":"看哪：一般受过警告的人结果如何?","translator":"王静斋"},{"sura":37,"aya":74,"text":"安拉的清白仆人在外。","translator":"王静斋"},{"sura":37,"aya":75,"text":"努海确曾呼吁我，应承者太好了！","translator":"王静斋"},{"sura":37,"aya":76,"text":"我解救他和他的眷属脱离大难；","translator":"王静斋"},{"sura":37,"aya":77,"text":"我使他的子孙存在，","translator":"王静斋"},{"sura":37,"aya":78,"text":"我们（赞扬）他遗在后来的普世界人中。","translator":"王静斋"},{"sura":37,"aya":79,"text":"平安是在努海上！","translator":"王静斋"},{"sura":37,"aya":80,"text":"我就这样地赏赐一般为善的人。","translator":"王静斋"},{"sura":37,"aya":81,"text":"他实是我的有正信的仆人。","translator":"王静斋"},{"sura":37,"aya":82,"text":"随后我淹没另一伙人。","translator":"王静斋"},{"sura":37,"aya":83,"text":"他的同伙中确有易卜拉欣。","translator":"王静斋"},{"sura":37,"aya":84,"text":"那时候，他秉着纯洁的心来至养主。","translator":"王静斋"},{"sura":37,"aya":85,"text":"那时候，他对父亲和族人们说：你们拜的是什么?","translator":"王静斋"},{"sura":37,"aya":86,"text":"你们撇开安拉妄寻一切受拜的吗?","translator":"王静斋"},{"sura":37,"aya":87,"text":"你们对于调养众世界的主作什么揣度?","translator":"王静斋"},{"sura":37,"aya":88,"text":"他看了一看星辰，","translator":"王静斋"},{"sura":37,"aya":89,"text":"遂就说：我实是有病的。","translator":"王静斋"},{"sura":37,"aya":90,"text":"他们就离他退转了。","translator":"王静斋"},{"sura":37,"aya":91,"text":"他暗自去到他们假主那里，说：（你们为什么不吃?","translator":"王静斋"},{"sura":37,"aya":92,"text":"你们为什么不语?）","translator":"王静斋"},{"sura":37,"aya":93,"text":"他就趋前用右手打它们，","translator":"王静斋"},{"sura":37,"aya":94,"text":"那些人们急切奔向他来了。","translator":"王静斋"},{"sura":37,"aya":95,"text":"他说：你们拜自所斫成的吗?","translator":"王静斋"},{"sura":37,"aya":96,"text":"安拉本造化你们和你们所作的。","translator":"王静斋"},{"sura":37,"aya":97,"text":"他们说：（你们为他筑一堵墙。把他投在烈火里。）","translator":"王静斋"},{"sura":37,"aya":98,"text":"他们欲对他施诡计，我使他们归为至卑下的。","translator":"王静斋"},{"sura":37,"aya":99,"text":"他说：（我必往赴我的养主，他即将引导我。）","translator":"王静斋"},{"sura":37,"aya":100,"text":"我的养主啊！你把一个清廉的赏给我吧！","translator":"王静斋"},{"sura":37,"aya":101,"text":"我遂就以一个宽仁的童子给他报喜信。","translator":"王静斋"},{"sura":37,"aya":102,"text":"及那小儿达到随他奔驰的时候了，他就说：（我的小儿呀！我确梦见宰你；看你持何所见?）那小儿说：（父啊你奉命行事吧！若是安拉意欲——了，你就得见我是忍耐的。）","translator":"王静斋"},{"sura":37,"aya":103,"text":"当他父子顺服，他就使它额角倒地的时候，","translator":"王静斋"},{"sura":37,"aya":104,"text":"我晓谕他：（易卜拉欣：","translator":"王静斋"},{"sura":37,"aya":105,"text":"你已实践梦象了。）我就这样地善赏一般为善的人；","translator":"王静斋"},{"sura":37,"aya":106,"text":"这就是显著的试证。","translator":"王静斋"},{"sura":37,"aya":107,"text":"我用大的牺牲救赎他了。","translator":"王静斋"},{"sura":37,"aya":108,"text":"我把（赞扬）他遗在后人之间。","translator":"王静斋"},{"sura":37,"aya":109,"text":"平安是在易卜拉欣上，","translator":"王静斋"},{"sura":37,"aya":110,"text":"我就这样地赏赐一般为善的人。","translator":"王静斋"},{"sura":37,"aya":111,"text":"他实属我的有正信的仆人。","translator":"王静斋"},{"sura":37,"aya":112,"text":"我以廉节的易司哈各圣人给他报喜信。","translator":"王静斋"},{"sura":37,"aya":113,"text":"我赐福给他和易司哈各。他俩的子孙有为善的，有公然自亏己身的。","translator":"王静斋"},{"sura":37,"aya":114,"text":"我曾施恩于母撒和哈仑，","translator":"王静斋"},{"sura":37,"aya":115,"text":"并使他俩及其族人脱开大难了。","translator":"王静斋"},{"sura":37,"aya":116,"text":"我相助他们致为获胜的。","translator":"王静斋"},{"sura":37,"aya":117,"text":"我赐他二人显明的经典；","translator":"王静斋"},{"sura":37,"aya":118,"text":"我指示他俩正路。","translator":"王静斋"},{"sura":37,"aya":119,"text":"我使（赞扬）他俩遗在后人之中。","translator":"王静斋"},{"sura":37,"aya":120,"text":"平安是在母撒与哈伦上！","translator":"王静斋"},{"sura":37,"aya":121,"text":"我就这样地赏赐为善的人；","translator":"王静斋"},{"sura":37,"aya":122,"text":"他俩实属我的有正信的仆人。","translator":"王静斋"},{"sura":37,"aya":123,"text":"伊洛押司确属于被差的。","translator":"王静斋"},{"sura":37,"aya":124,"text":"那时候，他对自己的族人说：（你们怎不畏惧呢?","translator":"王静斋"},{"sura":37,"aya":125,"text":"你们岂可呼求巴拉，而放弃最优的造物主……，","translator":"王静斋"},{"sura":37,"aya":126,"text":"安拉，调养你们的主，也是调养你们前代列祖的主?）","translator":"王静斋"},{"sura":37,"aya":127,"text":"但是他们不信他，他们是一定亲临的。","translator":"王静斋"},{"sura":37,"aya":128,"text":"安拉的清白仆人在外。","translator":"王静斋"},{"sura":37,"aya":129,"text":"我使（赞扬）他遗在后人之中，","translator":"王静斋"},{"sura":37,"aya":130,"text":"平安是在伊洛押司上，","translator":"王静斋"},{"sura":37,"aya":131,"text":"我就这样地赏赐一般为善的人。","translator":"王静斋"},{"sura":37,"aya":132,"text":"他实属我的有正信的仆人。","translator":"王静斋"},{"sura":37,"aya":133,"text":"鲁脱确属被差的，","translator":"王静斋"},{"sura":37,"aya":134,"text":"那时候，我使他和他的眷属一概得救；","translator":"王静斋"},{"sura":37,"aya":135,"text":"除去一老妪是在落后的。","translator":"王静斋"},{"sura":37,"aya":136,"text":"而后我毁灭另一伙人。","translator":"王静斋"},{"sura":37,"aya":137,"text":"的确你们朝夕经过他们那里，","translator":"王静斋"},{"sura":37,"aya":138,"text":"你们怎不了解呢?","translator":"王静斋"},{"sura":37,"aya":139,"text":"郁奴司确属被差的。","translator":"王静斋"},{"sura":37,"aya":140,"text":"那时候；他逃到满载的船上，","translator":"王静斋"},{"sura":37,"aya":141,"text":"他抽签而归为失败的。","translator":"王静斋"},{"sura":37,"aya":142,"text":"鱼吞下他去，他是遭受谴责的。","translator":"王静斋"},{"sura":37,"aya":143,"text":"若非他属于赞颂的，","translator":"王静斋"},{"sura":37,"aya":144,"text":"他必留在鱼腹里，直到复生的那一日。","translator":"王静斋"},{"sura":37,"aya":145,"text":"而后我把他染病着弃在不毛之地。","translator":"王静斋"},{"sura":37,"aya":146,"text":"我在它的上面生长出葫芦树来。","translator":"王静斋"},{"sura":37,"aya":147,"text":"我把他打发到十万人，或者还多的，","translator":"王静斋"},{"sura":37,"aya":148,"text":"他们遂就归信了；我教他们享受到一定的时期。","translator":"王静斋"},{"sura":37,"aya":149,"text":"你问他们：（女儿是你养主的?男儿是他们的吗?","translator":"王静斋"},{"sura":37,"aya":150,"text":"不然，我造天使为女儿，他们在场吗?","translator":"王静斋"},{"sura":37,"aya":151,"text":"须知他们妄称（安拉生子了，）他们实是谎言的人。","translator":"王静斋"},{"sura":37,"aya":152,"text":".","translator":"王静斋"},{"sura":37,"aya":153,"text":"他可舍去男儿选择女儿吗?","translator":"王静斋"},{"sura":37,"aya":154,"text":"你们是怎样判断的?","translator":"王静斋"},{"sura":37,"aya":155,"text":"你们何不参想呢?","translator":"王静斋"},{"sura":37,"aya":156,"text":"不然，你们据有显著的明证吗?","translator":"王静斋"},{"sura":37,"aya":157,"text":"如果你们的所言属实，你们就现出自己的经典来。","translator":"王静斋"},{"sura":37,"aya":158,"text":"他们在他和天使之间拟定宗亲了。天使确知道他们， 一定是将要亲临的。","translator":"王静斋"},{"sura":37,"aya":159,"text":"安拉清净，不染他们所称述的。","translator":"王静斋"},{"sura":37,"aya":160,"text":"安拉的清白仆人不在其内。","translator":"王静斋"},{"sura":37,"aya":161,"text":"你们和你们所拜的，","translator":"王静斋"},{"sura":37,"aya":162,"text":"只是抗他磨难那将进火狱的人。","translator":"王静斋"},{"sura":37,"aya":163,"text":".","translator":"王静斋"},{"sura":37,"aya":164,"text":"我们无不各有一定的地位。","translator":"王静斋"},{"sura":37,"aya":165,"text":"我们确是列班的，","translator":"王静斋"},{"sura":37,"aya":166,"text":"我们确是称颂的。","translator":"王静斋"},{"sura":37,"aya":167,"text":"他们声称：","translator":"王静斋"},{"sura":37,"aya":168,"text":"设若我们据有前人的教戒了，","translator":"王静斋"},{"sura":37,"aya":169,"text":"我们一定是安拉的清白仆人）","translator":"王静斋"},{"sura":37,"aya":170,"text":"他们不信它。 不久他们就知道。","translator":"王静斋"},{"sura":37,"aya":171,"text":"我的对于负有使命的众仆所说……他们必获相助。","translator":"王静斋"},{"sura":37,"aya":172,"text":"确已在先了。","translator":"王静斋"},{"sura":37,"aya":173,"text":"我的军兵一定获胜。","translator":"王静斋"},{"sura":37,"aya":174,"text":"你回避他们，直到一个时期。","translator":"王静斋"},{"sura":37,"aya":175,"text":"你看吧！不久他们就看见。","translator":"王静斋"},{"sura":37,"aya":176,"text":"他们要求立时现出我的刑罚吗?","translator":"王静斋"},{"sura":37,"aya":177,"text":"待刑罚下到他们天井的时候，受过警告者的清晨，太恶了。","translator":"王静斋"},{"sura":37,"aya":178,"text":"你回避他们，直到一个时期，","translator":"王静斋"},{"sura":37,"aya":179,"text":"你看吧！不久他们就看见。","translator":"王静斋"},{"sura":37,"aya":180,"text":"你的有荣威的养主清净，不染他们所称述的。","translator":"王静斋"},{"sura":37,"aya":181,"text":"平安是在列使上！","translator":"王静斋"},{"sura":37,"aya":182,"text":"赞颂安拉——调养众世界的主。","translator":"王静斋"},{"sura":38,"aya":1,"text":"（刷德）试思有教戒的古兰；","translator":"王静斋"},{"sura":38,"aya":2,"text":"背逆的人实是在自矜与反抗中，","translator":"王静斋"},{"sura":38,"aya":3,"text":"我在他们以前灭绝若干代。他们呼喊；不是逃脱的时候了：","translator":"王静斋"},{"sura":38,"aya":4,"text":"他们奇异同人中有一个传警告的达至他们。不信的人说：（这是一个说谎的术士。","translator":"王静斋"},{"sura":38,"aya":5,"text":"他把一切主归纳为一个主吗?这实在是一件奇事。）","translator":"王静斋"},{"sura":38,"aya":6,"text":"他们的一般伟人走去，说：（你们前去安心谨守自己的各主吧！这确是一件要有的事情。","translator":"王静斋"},{"sura":38,"aya":7,"text":"我们在后来的宗教里未听见过这个。这不过是一种造作。","translator":"王静斋"},{"sura":38,"aya":8,"text":"可能把教戒降给我们之间的他吗?）他们只是对我的教戒怀疑了。不然，他们尚未尝试我的罪刑。","translator":"王静斋"},{"sura":38,"aya":9,"text":"不然，他们据有你的优胜厚施的养主慈库吗?","translator":"王静斋"},{"sura":38,"aya":10,"text":"不然，他们掌天地万有的权柄吗?任他们缘绳上升吧！","translator":"王静斋"},{"sura":38,"aya":11,"text":"（他们）是联军中在那里败退的一支军兵。","translator":"王静斋"},{"sura":38,"aya":12,"text":"努海的族人、阿代、有木橛的匪喇傲、","translator":"王静斋"},{"sura":38,"aya":13,"text":"赛母待、鲁脱的族人、哀凯人——这几伙人在他们以前不信服了、","translator":"王静斋"},{"sura":38,"aya":14,"text":"每一伙皆不信服使者，而后我的刑罚实现了。","translator":"王静斋"},{"sura":38,"aya":15,"text":"这般人只候不稍延缓的一声呜。","translator":"王静斋"},{"sura":38,"aya":16,"text":"他们说：（我们的养主啊！你在审算日以前，把我们应得的罪刑赶忙施给我们吧！）","translator":"王静斋"},{"sura":38,"aya":17,"text":"你当忍受他们所说的。你当记念我的仆人——有实力的达伍德！他确是归向的。","translator":"王静斋"},{"sura":38,"aya":18,"text":"我降服群山，在日没日升的时候，随他赞颂清净，","translator":"王静斋"},{"sura":38,"aya":19,"text":"被集的飞鸟亦然。每一个是服从他的命令的。","translator":"王静斋"},{"sura":38,"aya":20,"text":"我奠定他的王国，我赐给他智慧与明辨。","translator":"王静斋"},{"sura":38,"aya":21,"text":"敌对者的消息达至你了吗?那时候，他们登上内殿的墙；","translator":"王静斋"},{"sura":38,"aya":22,"text":"那时侯，他们抵至达伍德，他对他们感觉恐怖。他们说：（你不要骇怕，我们是两个敌人；我们的一个亏负一个了；你在我们之间秉公判断吧，你不要顺从私见。你把我们导至正道！","translator":"王静斋"},{"sura":38,"aya":23,"text":"这是我的弟兄，他有九十九只羊，我有一只羊。他说：你把它归我管理吧！他在谈判上胜过我了。）","translator":"王静斋"},{"sura":38,"aya":24,"text":"达伍德说：（他以要求把你的羊归并他的羊迫害你了。许多同伙的，确是彼此一个迫害一个；除非一般归信并作善举的人；他们是绝对少数的。）达伍德确知道我试证他了。于是他求他的养主饶恕，俯身倒下而归向了。","translator":"王静斋"},{"sura":38,"aya":25,"text":"而后我饶恕他那件事；他在我跟前确获接近与优美的归处。","translator":"王静斋"},{"sura":38,"aya":26,"text":"达伍德啊！我委你在地上作替代的，要你在众人间秉公判断。不得跟从私见，而致使你错过安拉之道。的确，错过安拉之道的人，均由于忘记审算日遭受烈刑。","translator":"王静斋"},{"sura":38,"aya":27,"text":"我未枉造天地万有，这是不信之人的揣度。可叹不信的人们遭受火狱，","translator":"王静斋"},{"sura":38,"aya":28,"text":"我把一般归信并作善举的人，置若在地上造恶的人吗?不然，我把敬慎的人当若作恶的人吗?","translator":"王静斋"},{"sura":38,"aya":29,"text":"（这古兰）是我降给你的吉庆的经典，好教他们参悟它的表征，并为一般有才识的人纳劝。","translator":"王静斋"},{"sura":38,"aya":30,"text":"我把苏来茫赏给达伍德了。这仆人太好了，他实是一个归向的。","translator":"王静斋"},{"sura":38,"aya":31,"text":"那时候，有人在晚间把快马献给他了，","translator":"王静斋"},{"sura":38,"aya":32,"text":"他就说：（我以爱马替代记念养主，迨至太阳隐入帐幔了，","translator":"王静斋"},{"sura":38,"aya":33,"text":"你们给我带回此马。）他就开始摩擦小腿与脖子。","translator":"王静斋"},{"sura":38,"aya":34,"text":"我确磨难苏来茫，我把一个驱体掷在他的宝座上，他遂就归向了。","translator":"王静斋"},{"sura":38,"aya":35,"text":"他说：（我的养主啊！你饶恕我吧！你把不宜于我以后任何一个人的权柄赏给我吧！你实是厚施的。）","translator":"王静斋"},{"sura":38,"aya":36,"text":"而后，我为他降服风，风奉其命令和缓着刮到他所欲到的地方；","translator":"王静斋"},{"sura":38,"aya":37,"text":"并降服恶魔……一切建筑者，潜水者。","translator":"王静斋"},{"sura":38,"aya":38,"text":"另有一般同被系在桎梏的。","translator":"王静斋"},{"sura":38,"aya":39,"text":"（我说：）这是我的馈赠；任你施恩或留止，自无审算。","translator":"王静斋"},{"sura":38,"aya":40,"text":"他在我御前确获接近，与优美的归处。","translator":"王静斋"},{"sura":38,"aya":41,"text":"你当记念我的仆人——安优伯。那时候，他呼吁他的养主：（恶魔确使我遭受苦楚与疼痛了。）","translator":"王静斋"},{"sura":38,"aya":42,"text":"（我说：）（你以脚跺地吧！这是凉爽用作沐浴的，和饮料。）","translator":"王静斋"},{"sura":38,"aya":43,"text":"我以自己的慈惠并为提醒有才智的人，把他的眷属与和他们相等的一倍赏给他了。","translator":"王静斋"},{"sura":38,"aya":44,"text":"（我说：）（你手握一把草，用它殴打，不要违誓。我实见他是忍耐的。这仆人太好了，他实是一个归向的。）","translator":"王静斋"},{"sura":38,"aya":45,"text":"你当记念我的众仆——有力量与见解的易卜拉欣、易司哈格、叶而孤白。","translator":"王静斋"},{"sura":38,"aya":46,"text":"我因为特别的（意志）——宣布后世，选择他们了。","translator":"王静斋"},{"sura":38,"aya":47,"text":"他们在我御前实属优秀被选的。","translator":"王静斋"},{"sura":38,"aya":48,"text":"你当记念易司马衣、业赛尔、与宰洛克福里。每一个是属于优秀的。","translator":"王静斋"},{"sura":38,"aya":49,"text":"这是一种记念。敬慎的人必获优美的归处，","translator":"王静斋"},{"sura":38,"aya":50,"text":"久居的天园，各门为他们开放着。","translator":"王静斋"},{"sura":38,"aya":51,"text":"他们靠在那里，寻求多的鲜果与饮料。","translator":"王静斋"},{"sura":38,"aya":52,"text":"在他们跟前有些不妄视的同年女子。","translator":"王静斋"},{"sura":38,"aya":53,"text":"这是为审判日所约许你们的。","translator":"王静斋"},{"sura":38,"aya":54,"text":"这确是我无尽的赐与。","translator":"王静斋"},{"sura":38,"aya":55,"text":"就是这样。违法的人必获至恶的归所——火狱。","translator":"王静斋"},{"sura":38,"aya":56,"text":"他们将要进它，栖处太恶了：","translator":"王静斋"},{"sura":38,"aya":57,"text":"这是滚水与脓汁——叫他们尝试它吧！","translator":"王静斋"},{"sura":38,"aya":58,"text":"并有另外和它一样的种种。","translator":"王静斋"},{"sura":38,"aya":59,"text":"这是追随你们同入的一伙。他们得不到宽阔！他们是必定进火狱的。","translator":"王静斋"},{"sura":38,"aya":60,"text":"他们说：不然，你们不获宽阔!你们为我们把它送到前面了。居处太恶了！","translator":"王静斋"},{"sura":38,"aya":61,"text":"他们说：（我们的养主啊！那为我们把这个送在前面的人，你就把火狱中加倍的刑罚增给他吧！）","translator":"王静斋"},{"sura":38,"aya":62,"text":"他们说：（从前我们把他们列为恶徒一类的人，（现下）怎么看不见呢?","translator":"王静斋"},{"sura":38,"aya":63,"text":"我们曾把他们当作可戏的吗?不然，众目未正视他们吗?）","translator":"王静斋"},{"sura":38,"aya":64,"text":"这确是真的。火狱的人互起争辩。","translator":"王静斋"},{"sura":38,"aya":65,"text":"你说：（我只是一个传警告的，除去独一权威的安拉以外，再无有主。","translator":"王静斋"},{"sura":38,"aya":66,"text":"调养天地万有的主，优胜多恕的主。）","translator":"王静斋"},{"sura":38,"aya":67,"text":"你说：（这是一个重大的消息。","translator":"王静斋"},{"sura":38,"aya":68,"text":"你们是远避它的。","translator":"王静斋"},{"sura":38,"aya":69,"text":"当最高的天使辩论的时候，我原不知道他们。","translator":"王静斋"},{"sura":38,"aya":70,"text":"我惟奉命作一个公开传警告的。）","translator":"王静斋"},{"sura":38,"aya":71,"text":"昔时，你的养主对众天使说：（我将从泥上造化人类。","translator":"王静斋"},{"sura":38,"aya":72,"text":"我把他造成完整的，并把我的「鲁哈」吹在他里时候，你们就向他倒身下拜。","translator":"王静斋"},{"sura":38,"aya":73,"text":"于是众天使均一同下拜了，","translator":"王静斋"},{"sura":38,"aya":74,"text":"以卜里厮在外。他高傲了，他本属于逆徒。","translator":"王静斋"},{"sura":38,"aya":75,"text":"主说：（以卜里厮啊！什么事阻止你拜我亲手所造的呢? 你高傲了吗?不然，你原属于高尚的呢?）","translator":"王静斋"},{"sura":38,"aya":76,"text":"他说：（我是优越他的，你从火上造化了我，你从泥上造化了他。）","translator":"王静斋"},{"sura":38,"aya":77,"text":"主说：（你从这里出去吧！你是定被驱远的。","translator":"王静斋"},{"sura":38,"aya":78,"text":"你必遭我驱远直到还报日。）","translator":"王静斋"},{"sura":38,"aya":79,"text":"以卜里厮说：（我的养主啊！你姑容我，直到复活他们的那一天吧！）","translator":"王静斋"},{"sura":38,"aya":80,"text":"主说：（你是定受姑容，","translator":"王静斋"},{"sura":38,"aya":81,"text":"直到一定时日的。）","translator":"王静斋"},{"sura":38,"aya":82,"text":"以卜里厮说：（指着你的荣耀为誓：","translator":"王静斋"},{"sura":38,"aya":83,"text":"除去你的清白仆人以外，我必诱他们一同迷误。","translator":"王静斋"},{"sura":38,"aya":84,"text":"主说：（指着真的为誓——我惟说真的——","translator":"王静斋"},{"sura":38,"aya":85,"text":"我将必以你和他们之中追随你的人一同充满火狱。）","translator":"王静斋"},{"sura":38,"aya":86,"text":"你说：我不以此向你们讨工资；我不属于勉强造作的。","translator":"王静斋"},{"sura":38,"aya":87,"text":"它只是公布给普世界的。","translator":"王静斋"},{"sura":38,"aya":88,"text":"你们在一向之后，必就知道它所表白的了。）","translator":"王静斋"},{"sura":39,"aya":1,"text":"降经是由优胜明哲的安拉上。","translator":"王静斋"},{"sura":39,"aya":2,"text":"我确据实把经典降给你了；你当拜安拉，对他竭诚顺服着。","translator":"王静斋"},{"sura":39,"aya":3,"text":"须知真挚的顺服惟有对安拉。一般撇开他取爱友的人（说：）我们敬拜他们，只求介绍我们接近安拉，切实地接近。安拉将对他们所争议的从中判断。安拉绝不引导说谎忘恩的人。","translator":"王静斋"},{"sura":39,"aya":4,"text":"设若安拉要立儿子了，他必由他所造的以内随意选择。赞主清净！他是独一权威的安拉。","translator":"王静斋"},{"sura":39,"aya":5,"text":"他因着真理造化了天地。他折夜于昼，折昼于夜；他降服日月。每一个行到一定的时期。须知他是优胜的，多恕的。","translator":"王静斋"},{"sura":39,"aya":6,"text":"他自一人上造化了你们；他且自那一人上造化其妻，并为你们由家畜上造化八只。他造你们于母腹中，在三重黑暗里，一造复又一造。这是安拉——调养你们的主。惟他掌权。除他以外再无有主。你们转变到哪里?","translator":"王静斋"},{"sura":39,"aya":7,"text":"若是你们忘恩了，安拉一定是无求你们的。他不喜其众仆的忘恩。若是你们感念了，他喜你们的感念。负重担的人不负别个人的重担。而后你们惟回到自己的养主。他把你们所作过的宣告给你们。","translator":"王静斋"},{"sura":39,"aya":8,"text":"他深知胸中所怀的。人类遭难的时候，他就呼求养土，归向他着。而后真主把自己的恩惠赐给他的时候，他就忘记从前所向真主呼求的；他为安拉安置同等的，而致诱人失迷他的路道。","translator":"王静斋"},{"sura":39,"aya":9,"text":"你说：（你乘着自己的不信快乐少时吧！你必定是居火狱的，）（逆徒们是优上的?）或是那叩头，立站，惧怕后世仰望养主的慈惠着，在夜间顺命作功的人呢?","translator":"王静斋"},{"sura":39,"aya":10,"text":"你说：（有知识的人和无知识的人相等吗?）惟独有才识的人受劝。","translator":"王静斋"},{"sura":39,"aya":11,"text":"你呼我那归信的众仆们说：（你们当畏惧养主：在今世为善的人可获幸福，安拉的地是宽阔的，惟有忍耐的人无算地完全得到自己的报酬。）","translator":"王静斋"},{"sura":39,"aya":12,"text":"你说：（我确奉命拜安拉，对他竭诚顺服着。我且奉命为众穆思林的首领。）","translator":"王静斋"},{"sura":39,"aya":13,"text":"你说：（我若违犯养主了，我确骇怕大日的刑罚。 ）","translator":"王静斋"},{"sura":39,"aya":14,"text":"你说：（我惟拜安拉，对他竭诚我的顺服着。","translator":"王静斋"},{"sura":39,"aya":15,"text":"任你舍去真主随便去拜吧！）你说：（伤折的人是一般在复生日自伤己身与眷属的。须知这就是显著的伤折。","translator":"王静斋"},{"sura":39,"aya":16,"text":"在他们上面有一层一层的火，在他们下面也有一层一层的（火）这是安拉以它惊吓自己的众仆。）我的众仆啊！你们应当惧我。","translator":"王静斋"},{"sura":39,"aya":17,"text":"忌拜大欧特，归向安拉的人可获佳音。","translator":"王静斋"},{"sura":39,"aya":18,"text":"你报告我那听到言语，面从其至善者的众仆！他们是得到安拉引领的，他们就是有才识的。","translator":"王静斋"},{"sura":39,"aya":19,"text":"罪刑的话已对他决定的人，可（像不是这样的人）吗?你岂能解救那居火狱的人呢?","translator":"王静斋"},{"sura":39,"aya":20,"text":"只是那般敬畏养主的人可享造成的楼上楼，诸河流于其下。这是安拉许下的，安拉不爽约。","translator":"王静斋"},{"sura":39,"aya":21,"text":"你岂未见。安拉自上空降水，而使它流入地泉，再用它发现色色不一的禾谷；随后，那田禾枯干，你看它变为黄色的，他更使它化为碎屑。此中对于一般有才识的人确含有一种教诫。","translator":"王静斋"},{"sura":39,"aya":22,"text":"安拉因为伊斯兰展其胸，他就守其养主光辉的人，可（像其非这样的人）吗?可叹一般心硬不记念安拉的人。他们是在显著的迷误中。","translator":"王静斋"},{"sura":39,"aya":23,"text":"安拉降下极好的话，就是那相似重言的经典，畏惧养主的人因它皮战。他们的皮与心柔软地趋向记念安拉。这是安拉的南针；他用它指导自所意欲……的人。安拉造为迷误的人，无一引导他的。","translator":"王静斋"},{"sura":39,"aya":24,"text":"在复生日，那以面部遮挡烈刑的人，可（像安然无恐的人）吗? 有的向背义的人说：（你们尝试自所干过的吧！）","translator":"王静斋"},{"sura":39,"aya":25,"text":"他们以前的众人曾经不信了，而后刑罚突然到来，","translator":"王静斋"},{"sura":39,"aya":26,"text":"安拉教他们在今世尝辱刑。 一定后世的刑罚是至大的。设若他们知道了，——。","translator":"王静斋"},{"sura":39,"aya":27,"text":"我在这古兰中确为众人发布各项譬喻，好教他们受劝。","translator":"王静斋"},{"sura":39,"aya":28,"text":"无歪曲的阿拉伯文的古兰，只为他们敬慎。","translator":"王静斋"},{"sura":39,"aya":29,"text":"安拉发布一个譬喻：一个人有些复杂的伙伴；又一个人只服从一个人，这两个人的情形相等吗? 赞颂安拉： 不然，他们的多半不知道。","translator":"王静斋"},{"sura":39,"aya":30,"text":"你是一定将死的；他们也是一定将死的。","translator":"王静斋"},{"sura":39,"aya":31,"text":"而后你们在复生日，在养主的御前互起争辩。","translator":"王静斋"},{"sura":39,"aya":32,"text":"往安拉上说谎话，并在得到真教的时侯，不肯信它，谁是比这类人更是背义的。火狱里没有不信的人的居处吗?","translator":"王静斋"},{"sura":39,"aya":33,"text":"发动正言且信以为实的人，均是敬慎的。","translator":"王静斋"},{"sura":39,"aya":34,"text":"他们在养主御前获得自所想望的。这是报偿一般为善的人；","translator":"王静斋"},{"sura":39,"aya":35,"text":"终至安拉给他们遮蔽那所作的最丑恶者，并依他们所作的最优者报以善赏。","translator":"王静斋"},{"sura":39,"aya":36,"text":"安拉岂未有使自己的仆人满足吗? 他们以真主以外的来恫吓你。安拉造为迷误的人，无一引导他的。","translator":"王静斋"},{"sura":39,"aya":37,"text":"安拉引导的人，无一诱他迷误的。安拉岂非优胜的，掌报应的吗?","translator":"王静斋"},{"sura":39,"aya":38,"text":"你若问他们：（谁造化天地了。）他们一定说：（安拉。）你说：（你们曾见超过安拉所呼求的吗?若是安拉要我遭难了，它们能释去患难吗?或是他对我要慈惠了，它们能止其慈惠吗?） 你说：安拉使我满足了。仰赖的人们惟独仰赖他）","translator":"王静斋"},{"sura":39,"aya":39,"text":"你说：我的族人哪！你们尽力去做吧！我是一定要做的。","translator":"王静斋"},{"sura":39,"aya":40,"text":"不久你们就知道什么人得到侮辱的刑罚，遭受长久的刑罚。","translator":"王静斋"},{"sura":39,"aya":41,"text":"我因着真理为世人把经典降给你。得到引领的人，于他自身有利；迷误的人惟害自身迷误。你不是能保护他们的。","translator":"王静斋"},{"sura":39,"aya":42,"text":"安拉收取将死者的灵魂，与那在睡卧中未死者。（的灵魂；）而后他拘留已判定必死的那一个，而放还别的、直到一定的时期。此中对于有参悟的民众确含有种种表征。","translator":"王静斋"},{"sura":39,"aya":43,"text":"不然，他们撇开安拉取告赦的吗? 你说：（设若它们不掌一事，也不了解，可以吗?）","translator":"王静斋"},{"sura":39,"aya":44,"text":"你说：（告赦统归安拉执掌。他掌天地的权柄，而后你们均被归于他。）","translator":"王静斋"},{"sura":39,"aya":45,"text":"一旦有人记念安拉独一了，那般不信后世的人心就紧缩起来。一旦有人记念他以外的那一切了，他们立刻表示欣悦。","translator":"王静斋"},{"sura":39,"aya":46,"text":"你说：（安拉！造化天地的，知道目不能见的与显见的主啊！你本在自己众仆中对于他们所争议的施以判断。）","translator":"王静斋"},{"sura":39,"aya":47,"text":"假设地上所有的一切，并另外加上一倍，是属背义的人执掌了，他们一定在复生日用它罚赎恶刑，他们未曾料及的，由安拉上发现了，","translator":"王静斋"},{"sura":39,"aya":48,"text":"他们所作的种种罪恶明显了；他们被自所嘲弄的包围了。","translator":"王静斋"},{"sura":39,"aya":49,"text":"人类遇难的时候，他就呼求我；而后我赐给他恩典了，他就说：（那只是我以知识得到的。）不然，那是一种磨难；但是他们大多数人不知道。","translator":"王静斋"},{"sura":39,"aya":50,"text":"他们以前的众人确说过这话。他们所赚下的。未给他们避免。","translator":"王静斋"},{"sura":39,"aya":51,"text":"他们遭受自所作过的罪恶。背义的这般人，即将遭受所作的罪恶。他们不是能作抵抗的。","translator":"王静斋"},{"sura":39,"aya":52,"text":"他们岂不知道安拉为其意欲——的人伸缩给养吗?此中对于归信的民众确含有种种表征。","translator":"王静斋"},{"sura":39,"aya":53,"text":"你呼我的一般对自身妄为的仆人，说吧！你们对于安拉的慈惠不要绝望。安拉一定饶恕统通的罪恶。他确是多恕的，特慈的。","translator":"王静斋"},{"sura":39,"aya":54,"text":"你们当归向养主，你们当在未遇到刑罚，且不获相助以前顺服他：","translator":"王静斋"},{"sura":39,"aya":55,"text":"你们在尚不知觉着，刑罚突至以前，追从那由养主上降给你们的最善者：","translator":"王静斋"},{"sura":39,"aya":56,"text":"免得人说：（可惜我对安拉方面怠慢了，我实是嘲弄的；）","translator":"王静斋"},{"sura":39,"aya":57,"text":"或是说：（设若安拉引导我了，我必归为敬慎的；）","translator":"王静斋"},{"sura":39,"aya":58,"text":"或是在亲见刑罚的时候说：（宁愿我有一次转回而归入为善的。）","translator":"王静斋"},{"sura":39,"aya":59,"text":"是的，我的表征达至你了。但是你未信它，你高傲了，你本是逆徒一类的。","translator":"王静斋"},{"sura":39,"aya":60,"text":"当复生日，你看见一般曾往安拉上说谎的人是面黑的。火狱里岂无有高傲人所住的地方吗?","translator":"王静斋"},{"sura":39,"aya":61,"text":"安拉解救敬畏的人，以其获救的原由。他们不遭患难，也不忧愁。","translator":"王静斋"},{"sura":39,"aya":62,"text":"安拉是造化万事的，他是监管万事的。","translator":"王静斋"},{"sura":39,"aya":63,"text":"惟他掌管天地的锁钥。不信安拉表征的人，均是伤折的。","translator":"王静斋"},{"sura":39,"aya":64,"text":"你呼一般愚人说，你们命我拜安拉以外的吗?","translator":"王静斋"},{"sura":39,"aya":65,"text":"我确已告谕你，和你以前的众人了。如果你信匹偶，你的善举必就归为失效， 你必归于伤折的人了。","translator":"王静斋"},{"sura":39,"aya":66,"text":"不然，你当拜安拉，你当归为感念的。","translator":"王静斋"},{"sura":39,"aya":67,"text":"他们估计安拉，未曾适当地估计他。在复生日，地统在他的掌握。诸天是被他的右手卷起的。真主清净，他玄高于他们所拟的匹偶！","translator":"王静斋"},{"sura":39,"aya":68,"text":"吹起了号筒，而后除去安拉所意欲——的人以外，天地间所有的人晕死过去。而后另吹一次，他们立时起来观看。","translator":"王静斋"},{"sura":39,"aya":69,"text":"地藉其养主的光辉照耀了。设置文卷，召来一般圣人与见证者。有的在他们中间秉公判断，并不亏损。","translator":"王静斋"},{"sura":39,"aya":70,"text":"每一个人完全得到自所作过的。真主至知他们的作为。","translator":"王静斋"},{"sura":39,"aya":71,"text":"不信的人一群一群地被驱至火狱。待至来到它的时候，其门始在开放着。管火狱的天使对他们说：（你们同类的使者岂未来对你们诵养主的表征，并对你们警告遇见今日吗?）他们说：（是的；）但是关于罪刑的话在一切不信的人上决定了。","translator":"王静斋"},{"sura":39,"aya":72,"text":"有的说：（你们进火狱的门，永居在那里吧！）高傲人的居所太恶了。","translator":"王静斋"},{"sura":39,"aya":73,"text":"畏惧养主的人一群一群地被促至天园。待至他们来到的时候，其门已在开放着。管天园的天使对他们说：（祝你们平安：你们快乐了，你们进到天园永居吧！）","translator":"王静斋"},{"sura":39,"aya":74,"text":"他们说：（赞颂真主；他对我们实现他的所约，他教我们继承此地。我们居住天园里自所意欲的地方。）工作者的报酬太好了。","translator":"王静斋"},{"sura":39,"aya":75,"text":"你看众天使是环绕（阿勒世）的四周，以赞其养主称颂清净的。有的在他们中间秉公判断；有的诵：（赞颂安拉——调养众世界的主。）","translator":"王静斋"},{"sura":40,"aya":1,"text":"（哈、密默。）","translator":"王静斋"},{"sura":40,"aya":2,"text":"降经是由优胜深知的，","translator":"王静斋"},{"sura":40,"aya":3,"text":"恕罪的，允忏悔的，掌烈刑的，宽恩的安拉上。除他以外再无有主，归惟归于他。","translator":"王静斋"},{"sura":40,"aya":4,"text":"对于安拉的表征起争论，只是一般不信的人。你莫被他们的往返在各城里所欺，","translator":"王静斋"},{"sura":40,"aya":5,"text":"努海的族人和他们以后的几伙人，先他们不信服了。每一伙人意欲捉捕他们的使者。他们用假的来争论，为的是用它破坏真的。而后我惩治他们了。我的惩治如何?","translator":"王静斋"},{"sura":40,"aya":6,"text":"你的养主对逆徒们的话：（他们是居火狱的，）如此决定了。","translator":"王静斋"},{"sura":40,"aya":7,"text":"负荷（阿勒世）及其四周的众天使，以赞其养主称颂清净。他们归信他，并为众归信的求饶， (说：）我们的养主啊！你的慈惠和知识充满了万物。你饶恕那般悔罪并追随你的道路的人吧：你解救他们，免受火狱的刑罚吧！","translator":"王静斋"},{"sura":40,"aya":8,"text":"养主啊！你教他们和他们列祖与妻子中的义人，进你所许的长久天园吧！的确，你实是优胜的，明哲的。","translator":"王静斋"},{"sura":40,"aya":9,"text":"你解救他们，免受种种罪刑吧！当那一日，得你庇佑不受罪刑的人，确蒙你慈悯了，这就是大成功。","translator":"王静斋"},{"sura":40,"aya":10,"text":"逆徒们确得到呼喊：（安拉的怒恨大过你们恨自己。那时候 有人唤你们归信，但是你们不信）","translator":"王静斋"},{"sura":40,"aya":11,"text":"他们说：（养主啊！你教我们死两次了，你教我们生两次了，我们承认自己的罪了，可有一个出路吗?）","translator":"王静斋"},{"sura":40,"aya":12,"text":"这是由于有人呼求独一的安拉时候你们不信；若是有人给他举匹偶了，你们就信。判断是属伟大玄高的安拉执掌。","translator":"王静斋"},{"sura":40,"aya":13,"text":"他那真主把自己的表征现给你们；他为你们从上空降下给养。惟有归向的人受劝。","translator":"王静斋"},{"sura":40,"aya":14,"text":"你们当呼求安拉，为他竭诚顺服着；纵令逆徒们憎恶。","translator":"王静斋"},{"sura":40,"aya":15,"text":"（他）是品级高的，掌「阿勒世」的。他把属于自己命令的鲁哈投给他意欲……的仆人，只为警告以亲见的日期，","translator":"王静斋"},{"sura":40,"aya":16,"text":"就是他们出现的那一日。他们无一事能瞒过安拉。（今日，权柄归谁管理?）（独一权威的安拉管理。）","translator":"王静斋"},{"sura":40,"aya":17,"text":"今日，还报每一个人以其所作的。今日，无有不公。安拉实是审算神速的。","translator":"王静斋"},{"sura":40,"aya":18,"text":"你把复生日警告他们吧！那时候，心达近咽喉，在拥塞着。背义的人无一挚友，也无一个受服从的告赦的。","translator":"王静斋"},{"sura":40,"aya":19,"text":"他知道眼的欺骗，和胸里所怀的。","translator":"王静斋"},{"sura":40,"aya":20,"text":"安拉秉公判断。他们撇开他所呼求的那一切，均不能判断一事。安拉实是能听的，能见的。","translator":"王静斋"},{"sura":40,"aya":21,"text":"他们岂未在地上游行，而观察众前人的结果如何吗?那般人原是以力量和地上的踪迹 比他们雄厚的；而后他们因为罪恶遭安拉的惩罚了，无一个抵抗安拉庇护他们的。","translator":"王静斋"},{"sura":40,"aya":22,"text":"这是由于列使示给他们种种明证，但是他们不信，所以安拉惩罚他们了。他实是大力的，掌烈刑的。","translator":"王静斋"},{"sura":40,"aya":23,"text":"我确把母撒持我的异迹和明证。","translator":"王静斋"},{"sura":40,"aya":24,"text":"遣至匪喇傲、哈曼、葛伦了。但是他们说：（他）是一个谎言的术士。","translator":"王静斋"},{"sura":40,"aya":25,"text":"及其从我御前把真的现给他们了，他们就说：（要你们杀毙那般随他归信者的男儿，留下他们的女儿，）逆徒们的诡计不外是在失效中。","translator":"王静斋"},{"sura":40,"aya":26,"text":"匪喇傲说：（你们容我杀毙母撒，让他呼求自己的养主。我实怕他改换你们的宗教，或是他在此地发现罪恶。）","translator":"王静斋"},{"sura":40,"aya":27,"text":"母撒说：（我于一切不信算日的恶徒上祈我的养主，也是你们的养主护佑。）","translator":"王静斋"},{"sura":40,"aya":28,"text":"匪喇傲的眷属中一个暗中归信的穆民人说：你们杀人，只因着他说：「我的养主是安拉」吗?他确把养主的种种明证拿给你们了。如果他是谎言的，他的撒谎只害他个人；他若是实言的，你们将遭他对你们许下的一部分。安拉绝不引导妄为撒谎的人。","translator":"王静斋"},{"sura":40,"aya":29,"text":"我的族人哪，今日，你们在地上强梁着掌权。若是安拉的刑罚来到了，谁能相助我们?）匪喇傲说：（我只把我见到的现给你们，我惟指示你们正道。","translator":"王静斋"},{"sura":40,"aya":30,"text":"那一归信的人说：我的族人哪！我实为你们骇怕像群众的那一天；","translator":"王静斋"},{"sura":40,"aya":31,"text":"若努海的族人，阿代，赛母待，以及他们以后众人的情况；安拉不欲迫害众仆。","translator":"王静斋"},{"sura":40,"aya":32,"text":"我的族人哪！我实为你们骇怕相呼唤的那一天，","translator":"王静斋"},{"sura":40,"aya":33,"text":"就是你们退转的那一天。你们无一个抵抗安拉的（刑罚）作护佑的。安拉造为迷误的人，无一引导他的。","translator":"王静斋"},{"sura":40,"aya":34,"text":"从前郁素福确对你们现出种种表征，然而你们对他所现出的，时常是在疑惑中。待他死去的时候，你们就说：（安拉在他以后绝不遣一个使者，）安拉就是这样地使那妄为疑惑的入迷误。","translator":"王静斋"},{"sura":40,"aya":35,"text":"那般人无有得到的明证着，因为安拉的表征互起争论。他在安拉御前并在穆民跟前极其可怒了，安拉就是这样地封闭每一个高傲强霸的人心。","translator":"王静斋"},{"sura":40,"aya":36,"text":"匪喇傲呼哈曼说：（你为我建筑一座高楼，或许我要达到些个路径，","translator":"王静斋"},{"sura":40,"aya":37,"text":"就是天上的路径，我照见母撒的士气我实揣度他是撒谎的。）有的为匪喇傲如此着装饰他的恶行，并阻止他得正道。匪喇傲的计谋确是在消灭中。","translator":"王静斋"},{"sura":40,"aya":38,"text":"那一归信的人说：（我的族人哪！你们追随我吧！我把你们引至正路。","translator":"王静斋"},{"sura":40,"aya":39,"text":"我的族人哪！这今生仅是些微的享受。后世确是久居的笫宅。","translator":"王静斋"},{"sura":40,"aya":40,"text":"作恶的人惟遭受和它一样的报应。作善举的男女穆民均将进天园；他们在那里无算地蒙受给养。","translator":"王静斋"},{"sura":40,"aya":41,"text":"我的族人哪！我是怎的了?我唤你们于得救，你们唤我去到火狱。","translator":"王静斋"},{"sura":40,"aya":42,"text":"你们唤我不信安拉，并把我不知道的举为他的匹偶；我唤你们趋向优胜的，多恕的主。","translator":"王静斋"},{"sura":40,"aya":43,"text":"无疑你们所往它上唤导我的，在今世后世不获允纳。我们将必归到安拉。妄为的人均是居火狱的。","translator":"王静斋"},{"sura":40,"aya":44,"text":"你们即将想起我向你们所说过的。我把自己的事情付托给安拉。安拉实是看得见众仆的。）","translator":"王静斋"},{"sura":40,"aya":45,"text":"而后安拉解救未受那般人所计划的恶谋。匪喇傲的党徒被烈刑环绕了。","translator":"王静斋"},{"sura":40,"aya":46,"text":"他们朝夕被现到火上，在复生成立的那一天， (主说：）你们教匪喇傲的党徒进最烈的刑罚吧！","translator":"王静斋"},{"sura":40,"aya":47,"text":"那时候，他们在火狱里彼此起争论。无能为的人，对高傲的人说：（我们确曾追随你们了，你们能替我们避去一部分火吗?）","translator":"王静斋"},{"sura":40,"aya":48,"text":"高傲的人说：（我们统是在火中。）安拉必在众仆之间判明。","translator":"王静斋"},{"sura":40,"aya":49,"text":"火里的人对管火狱的天使们说：（你们呼求自己的养主！他给我们减轻一日的刑罚。）","translator":"王静斋"},{"sura":40,"aya":50,"text":"它们说：（你们的列使未把种种明证。现给你们吗?）他们说：是的。）它们说：（你们呼求吧！）逆徒们的呼求只是在失效中。","translator":"王静斋"},{"sura":40,"aya":51,"text":"我必在今生，并在证人们站起的那一日，","translator":"王静斋"},{"sura":40,"aya":52,"text":"相助我的列使和一般归信的人。当那一日，托词无济于背义的人；他们应受谴责；他们应遭后世的恶刑。","translator":"王静斋"},{"sura":40,"aya":53,"text":"我确把标准赐给母撒了，我教以斯拉衣来的子孙继承经典，","translator":"王静斋"},{"sura":40,"aya":54,"text":"藉以引导并劝戒有才识的人。","translator":"王静斋"},{"sura":40,"aya":55,"text":"你当忍耐！安拉的约是切实的。你为自己的罪求恕吧，你在夕朝以赞你养主称述清净吧！","translator":"王静斋"},{"sura":40,"aya":56,"text":"一般无有得到的明证，对安拉的表征起辩论者，他们胸怀的不外是难以达到的一种狂傲。你当求安拉护佑。真主实是能听的，能见的。","translator":"王静斋"},{"sura":40,"aya":57,"text":"造化天地大过造化人类，但是世人的多半不知道。","translator":"王静斋"},{"sura":40,"aya":58,"text":"瞎子和有眼的不一样。归信并作善举的人和作恶的人也不一样。你们绝少受劝。","translator":"王静斋"},{"sura":40,"aya":59,"text":"的确，复生日无疑是该来的。但是世人的多半不肯置信。","translator":"王静斋"},{"sura":40,"aya":60,"text":"你们的养主说：（你们呼求我吧，我就应承你们。对于拜我上高傲的人，即将卑鄙着进火狱。）","translator":"王静斋"},{"sura":40,"aya":61,"text":"安拉，那真主为你们造夜，好教你们在夜里安歇；并造化光明的白昼。安拉实是有恩于世人的，但是世人的多半不知感念。","translator":"王静斋"},{"sura":40,"aya":62,"text":"这是安拉——调养你们的主，造化万物的主。除他以外再无有主。你们转变到哪里?","translator":"王静斋"},{"sura":40,"aya":63,"text":"不承认安拉表征的人，就是这样地被转变。","translator":"王静斋"},{"sura":40,"aya":64,"text":"安拉，那真主为你们造地为居所，造天为覆盖。他给你们成起形来，且使你们的外形为美观的；更赐给你们各样美品。这是安拉——调养你们的主。安拉——调养众世界的主太尊大了！","translator":"王静斋"},{"sura":40,"aya":65,"text":"他是永生的；除他以外，无一受拜的。你们当要呼求他！为他竭诚顺服着。赞颂安拉——调养众世界的主。","translator":"王静斋"},{"sura":40,"aya":66,"text":"你说：（当我养主种种明证达到的时候，我确被禁止拜你们撇开安拉所拜的那一切。我奉命顺服调养众世界的主。）","translator":"王静斋"},{"sura":40,"aya":67,"text":"他那真主，造你们于土上，再（造）于一点精上，又（造）于一块血上。随后，他现出你们为婴儿着。而后(他留下你们，）为的是你们达到壮年，而后你们成为老者。你们其中有先死的；并使你们达到一定的时期，冀使你们了解。","translator":"王静斋"},{"sura":40,"aya":68,"text":"他是使生使死的主。他判定一事的时候，只对它说：（有！）它遂就有了。","translator":"王静斋"},{"sura":40,"aya":69,"text":"你岂未见一般对安拉的表征起辩论的人们怎样地转变？","translator":"王静斋"},{"sura":40,"aya":70,"text":"他们不信经典，且不信我为它遣使的事情。不久他们就知道。","translator":"王静斋"},{"sura":40,"aya":71,"text":"那时候，他们的脖子带有枷链，","translator":"王静斋"},{"sura":40,"aya":72,"text":"被拉在滚水中，复被燃烧在火狱里。","translator":"王静斋"},{"sura":40,"aya":73,"text":"然后有的对他们说：（你们撇开安拉而自信的匹偶是在哪里?）","translator":"王静斋"},{"sura":40,"aya":74,"text":"他们说：（它们已离开我们不在了。不然，从前我们未拜一物。）安拉就是这样地造逆徒们迷误。","translator":"王静斋"},{"sura":40,"aya":75,"text":"这是由于你们在地上背义着狂喜和夸张。","translator":"王静斋"},{"sura":40,"aya":76,"text":"你们进火狱的门，永居在那里吧！众高傲者的居所太恶了，","translator":"王静斋"},{"sura":40,"aya":77,"text":"你忍耐吧！安拉的约是切实的。如果我把自所许给他们的一部分显给你，…… 或是取你的命去，他们惟被归于我，","translator":"王静斋"},{"sura":40,"aya":78,"text":"我确遣你以前的列使了他们之中有我述说给你的；他们之中有我未曾述说给你的。使者非奉安拉的命令，则不应现出一项异迹。当安拉的命令到来的时候，便就秉公判定。一般虚浮的人在那时陕，归为伤折的了。","translator":"王静斋"},{"sura":40,"aya":79,"text":"安拉，那真主为你们造化了家畜，好教你们骑它吃它；","translator":"王静斋"},{"sura":40,"aya":80,"text":"并自它上获些利益；更为你们骑它达到胸中所需求的。你们乘此牲畜和船只。","translator":"王静斋"},{"sura":40,"aya":81,"text":"他把自己的表征现给你们，你们不承认安拉的哪项表征?","translator":"王静斋"},{"sura":40,"aya":82,"text":"他们岂未在地上游行，而观察他们以前众人的结果如何?那般人原是比他们众多的，且是在力量和在地面的遗迹上（比他们）雄厚的，但是它们所作的未能代为避免。","translator":"王静斋"},{"sura":40,"aya":83,"text":"列使对他们现出种种明证的时候，他们以自己所具的知识狂喜，他们被自所嘲笑的包围了。","translator":"王静斋"},{"sura":40,"aya":84,"text":"及其亲见我的刑罚时候。他们就说：（我们归信独一的安拉；我们不承认曾给他所举的匹偶。）","translator":"王静斋"},{"sura":40,"aya":85,"text":"当他们亲见我的刑罚时候，他们的归信无济了。这是安拉对其众仆过去的惯例。众不信的人，在那时候归为伤折的了。","translator":"王静斋"},{"sura":41,"aya":1,"text":"哈、密默。","translator":"王静斋"},{"sura":41,"aya":2,"text":"降自普慈特慈的主，","translator":"王静斋"},{"sura":41,"aya":3,"text":"阿拉伯文的古兰经，它的文词为着有知识的民众受了分析，","translator":"王静斋"},{"sura":41,"aya":4,"text":"在报喜信，传警告着。他们的多半避而不闻。","translator":"王静斋"},{"sura":41,"aya":5,"text":"他们说：（我们的心于你所唤导的是在被遮中。我们重听，我们彼此间有一个幔帐。你去工作吧！我们是一定要工作的。）","translator":"王静斋"},{"sura":41,"aya":6,"text":"你说：（我只是和你们一样的人，我所得到的启示是：你们的主是独一受拜的，你们要一直的倾向他，求他饶恕。","translator":"王静斋"},{"sura":41,"aya":7,"text":"不施天课且不信后世的拜偶像的人有祸了。","translator":"王静斋"},{"sura":41,"aya":8,"text":"归信并作善举的人，可获无间断的报酬。）","translator":"王静斋"},{"sura":41,"aya":9,"text":"你说：（你们不信那在两日里造地的主，你们为他拟同等的吗?）这是调养众世界的主。","translator":"王静斋"},{"sura":41,"aya":10,"text":"他在地上造化了高山，并在那里降下福庆，更在完全的四天内为着一般需求的人规定了它的给养。","translator":"王静斋"},{"sura":41,"aya":11,"text":"他又倾向烟雾的天，他对天地说：（你俩顺服或委屈着来吧！）天地说：（我们顺服着来了。）","translator":"王静斋"},{"sura":41,"aya":12,"text":"他就在两日内造齐了七天。他在每一天上发布关于它的命令。我以星辰装饰了至近的天，并加以保护。这是优胜深知的主的定夺。","translator":"王静斋"},{"sura":41,"aya":13,"text":"如果他们远避了，你就说：（我已把烈刑，就像啊代、赛母待的烈刑，警告你们了。）","translator":"王静斋"},{"sura":41,"aya":14,"text":"那时候，列使自他们前后到来，说：（你们莫要拜安拉以外的）他们就说。）假若我们的养主意欲——了，他必降下天使。我们不信你们的使命。）","translator":"王静斋"},{"sura":41,"aya":15,"text":"阿代，瞥在地上背义着高傲，说：（谁是比我们力量坚强的，）他们岂未看见那造化他们的安拉，就是比他们力量坚强的吗?他们不承认我的表征；","translator":"王静斋"},{"sura":41,"aya":16,"text":"而后我在不幸的几日内向他们发出暴风；为的是教他们在今世尝试辱刑。后世的刑罚实是至侮辱的，他们不复得到相助。","translator":"王静斋"},{"sura":41,"aya":17,"text":"我曾引导赛母待了：但是他们在正道上选择了盲昧，所以他们由于自所作过的遭受辱刑的惩治了，","translator":"王静斋"},{"sura":41,"aya":18,"text":"我解救了众归信并且敬畏的人。","translator":"王静斋"},{"sura":41,"aya":19,"text":"当那一日，安拉的仇敌集至火狱，均被排列起来。","translator":"王静斋"},{"sura":41,"aya":20,"text":"待至他们来到的时候，他们的耳、眼、皮。作不利于他们的见证。","translator":"王静斋"},{"sura":41,"aya":21,"text":"他们对自己的皮说：（你们为什么作不利于我们的见证呢?）他们说：（那教万物发言的安拉令我们发言了。他在前次造化你们了，你们将被归返于他。","translator":"王静斋"},{"sura":41,"aya":22,"text":"你们的遮掩 原不是为着自己的耳、眼、皮，作不利于你们的见证，而是你们揣度安拉不知道你们所作的许多。","translator":"王静斋"},{"sura":41,"aya":23,"text":"这是你们对于养主所想象的那种揣度。他使你们堕落，而致你们归为伤折的。 ）","translator":"王静斋"},{"sura":41,"aya":24,"text":"如果他们忍耐了，火就是他们的居处。若是他们求赦了，他们不获赦免。","translator":"王静斋"},{"sura":41,"aya":25,"text":"我为他们安置了一般同伴，且为他们装饰了以前以后的。（关于罪刑的）话对于他们，连同以前过去的镇呢和人类已竟决定了。他们原是伤折的人。","translator":"王静斋"},{"sura":41,"aya":26,"text":"不信的人说：（你们不要听这古兰，你们于中胡乱吵杂！希望你们获胜。）","translator":"王静斋"},{"sura":41,"aya":27,"text":"我必教不信的人尝烈刑，我必依他们的作为把最丑恶的还报给他们。","translator":"王静斋"},{"sura":41,"aya":28,"text":"这火狱是安拉的仇敌的报应，他们在那里获得永居的第宅。因着他们不承认我的表征而施行惩罚。","translator":"王静斋"},{"sura":41,"aya":29,"text":"不信的人说：（我们的养主啊！你把诱我们迷误的那两般…… 镇呢和人类现给我们，我们将其置在脚下，而使其归为最卑下的。）","translator":"王静斋"},{"sura":41,"aya":30,"text":"的确，一般声称我们的养主是安拉而且坚定的人，天使降临他们，（说：）你们莫骇怕，莫忧愁，你们因着所许给的天园喜乐吧！","translator":"王静斋"},{"sura":41,"aya":31,"text":"我们是在今生并在后世爱喜你们的。你们在那里享受心所爱欲的；你们在那里享受自所想望的。","translator":"王静斋"},{"sura":41,"aya":32,"text":"出自多恕特慈的主的路筵。","translator":"王静斋"},{"sura":41,"aya":33,"text":"什么人是比唤导至安拉并作善举，且称：我是顺服者一类的人，尤为言语最佳的?","translator":"王静斋"},{"sura":41,"aya":34,"text":"善恶不相等。你用最善的行为报答（罪恶）吧！若然，你和他彼此间有仇的那个人，突然好似一个挚友。","translator":"王静斋"},{"sura":41,"aya":35,"text":"接受它的人只是忍耐的；接受它的人，只是有大福分的。","translator":"王静斋"},{"sura":41,"aya":36,"text":"若是出于恶魔的诱惑鼓动你了，你就以安拉求护吧！他实是能见的，深知的。","translator":"王静斋"},{"sura":41,"aya":37,"text":"夜昼日月，是属真主的表征。你们莫拜日月，你们当拜那造化这一切的安拉， 如果你们只给他作奴的时候。","translator":"王静斋"},{"sura":41,"aya":38,"text":"若是他们高傲了，在你养主御前的那一切，夜昼赞主清净，并不厌烦。","translator":"王静斋"},{"sura":41,"aya":39,"text":"你看见地是枯干的，我把水降在它上的时候，它就发动，生长起来，这也属于他的表征。那使地再生的主，一定是复活死者的。他实是全能于万事的。","translator":"王静斋"},{"sura":41,"aya":40,"text":"对我的表征偏颇的人；他们瞒不过我，被投在火狱的人是至优的?或是那在复生日安然来到的人呢? 你们任便去作吧！他看得见你们所作的。","translator":"王静斋"},{"sura":41,"aya":41,"text":"当得到教戒时候不肯归信的人，……它实是高贵的经典。","translator":"王静斋"},{"sura":41,"aya":42,"text":"前后概无虚妄。降自明哲受赞的主。","translator":"王静斋"},{"sura":41,"aya":43,"text":"对你所说的，不外是对你以前列使所说的。你的养主实是掌饶恕的，掌痛刑的。","translator":"王静斋"},{"sura":41,"aya":44,"text":"设若我降它为外国语的古兰了，他们必说：（何不分清它的文词?岂可外国语的而归阿拉伯人呢?）你说：它是引导并医治众穆民的。不信的人重听，是他们盲然不解的。这般人是从远处受人呼喊的。","translator":"王静斋"},{"sura":41,"aya":45,"text":"我确把经典降给母撒，但是有人对它起争议了。设非你养主的话在先，一定就在他们中间判定了。他们确在对它不安的疑惑中。","translator":"王静斋"},{"sura":41,"aya":46,"text":"作善举的人则利他自身。为恶的人则害自身。你的养主绝不是迫害众仆的。","translator":"王静斋"},{"sura":41,"aya":47,"text":"知复生日惟归到主上。所出自茎衣的一切果实、女子怀妊、产生，无一不是依他知道的。 当那一日，真主晓谕他们：（我的诸匹偶哪里去了?）他们说：（我们禀告于你了，我们绝无一见证的。）","translator":"王静斋"},{"sura":41,"aya":48,"text":"他们从前所呼求的不在了。他们确信自己无一逃脱。","translator":"王静斋"},{"sura":41,"aya":49,"text":"人类不厌于求福。他若遭祸了，遂就绝望，不再希冀。","translator":"王静斋"},{"sura":41,"aya":50,"text":"我若教他在遭难以后尝我的慈惠了，他必定说：（这是我应该得到的。我不以为复生日将要成立。设我得以回到养主了，我必在他御前蒙得好处。（我必对于不信的人解明他们所作过的；我必教他们尝重刑。","translator":"王静斋"},{"sura":41,"aya":51,"text":"人要蒙我施恩的时候，他就扭转而避在一旁。一旦遭患，就有多的祈求，","translator":"王静斋"},{"sura":41,"aya":52,"text":"你说：（你们看出来了吗?如果那是从安拉上来的，你们且不信它，什么人是比这处在遥远反抗的人更是迷误的。）","translator":"王静斋"},{"sura":41,"aya":53,"text":"我即将把我在各方面，并在本地方的种种表征现给他们，终至他们明白那是真的。你的养主是见证万事的，这还不使他们满足吗?","translator":"王静斋"},{"sura":41,"aya":54,"text":"须知他们对于看见养主上是在疑惑中，须知真主是尽悉万事的。","translator":"王静斋"},{"sura":42,"aya":1,"text":"（哈、密默。","translator":"王静斋"},{"sura":42,"aya":2,"text":"二尼、斯尼、格甫。）","translator":"王静斋"},{"sura":42,"aya":3,"text":"优胜明哲的安拉就是这样的告谕你，和你以前的众人。","translator":"王静斋"},{"sura":42,"aya":4,"text":"在天在地的惟他执掌。他是尊大玄高的，","translator":"王静斋"},{"sura":42,"aya":5,"text":"诸天几乎要从上面崩裂。众天使以赞其养主称颂清净，并为地上所有的人求恕。须知安拉实是多恕的，特慈的。","translator":"王静斋"},{"sura":42,"aya":6,"text":"一般撇开真主结友的人，安拉是监察他们的。你不是经管他们的。","translator":"王静斋"},{"sura":42,"aya":7,"text":"我就这样地把阿拉伯文的古兰降给你，好教你警告都会及其四周的人。你警告以无疑的集合日：一伙在天园，一伙在火狱。","translator":"王静斋"},{"sura":42,"aya":8,"text":"设若安拉意欲……了，他必教他们成为一伙；但是他教自所意欲……的人入到他的慈惠中。在背义的人们无有一爱护的，也无一相助的。","translator":"王静斋"},{"sura":42,"aya":9,"text":"不然，他们撇开他结一般爱友吗?安拉就是爱护的。他复活死的；他是全能于万事的。","translator":"王静斋"},{"sura":42,"aya":10,"text":"你们所争议的无论什么事情，则归安拉判明。这是安拉，调养我的主。我惟仰赖他。我惟归向他。","translator":"王静斋"},{"sura":42,"aya":11,"text":"造化天地的主。他为你们就着同类的人造化匹偶，并就着牲畜造化匹偶。他教你们繁殖在它里。无一物和他类似。他是能听的，能看的。","translator":"王静斋"},{"sura":42,"aya":12,"text":"天地的锁钥惟他执掌。他为其意欲……的人伸缩给养，他实是深知万事的。","translator":"王静斋"},{"sura":42,"aya":13,"text":"主为你们规定那用它命令努海的宗教；并规定所告谕你的，和用它命令易卜拉欣、母撒、尔撒的；那就是你们当支持宗教莫分派系。你所用以唤导多神教徒的，在他们感觉困难。安拉向自己选其意欲——的人；向自己导其归附的人。","translator":"王静斋"},{"sura":42,"aya":14,"text":"他们分派系，仅在得到知识以后，由于自间的嫉忌。设非你养主先发出（姑容他们）直到限定时期的话，一定就在他们中间判决了。一般在他们以后承受经典的人，确切是在不安的疑惑中，","translator":"王静斋"},{"sura":42,"aya":15,"text":"你就向此唤导吧！你要如命守正，不可随从他们的私见。你说：（我信安拉所降的经典。我奉命在你们中间行公道。安拉是调养我们的主，也是调养你们的主。我们有我们的作为，你们有你们的作为。我们彼此间无争论。安拉将在我们中间集合。归惟归于他。）","translator":"王静斋"},{"sura":42,"aya":16,"text":"一般在接受以后，因着安拉起争辩的人，他们的争辩在你养主认为是虚伪的。他们遭受怒恨，并遭烈刑。","translator":"王静斋"},{"sura":42,"aya":17,"text":"安拉是那为真理降下经典和天秤的主；你能知道什么?恐怕复生日是临近的。","translator":"王静斋"},{"sura":42,"aya":18,"text":"不信它的人要求立时实现它。归信的人们是因它恐惧的；他们知道那是确实的。须知对于复生日相争辩的人，确是在遥远的迷误中。","translator":"王静斋"},{"sura":42,"aya":19,"text":"安拉是慈惠其众仆的；他赐其意欲的人给养。他是大力的，优胜的。","translator":"王静斋"},{"sura":42,"aya":20,"text":"讨后世禾田的人，我就因为他增其禾田；要今世禾田的人，我就此赐给他，其人在后世并无有福分。","translator":"王静斋"},{"sura":42,"aya":21,"text":"不然，他们有些伙伴，代为规定未得安拉允许的宗教吗? 若不是明白说过， 一定就在他们中间判定了。背义的人应遭痛刑。","translator":"王静斋"},{"sura":42,"aya":22,"text":"（在复生日，）你看见背义的人是因着自己的作为骇惧的。那是将要临到他们的。归信并作善举的人，是在天园的花厅里；他们在养主御前享受自所意欲的。","translator":"王静斋"},{"sura":42,"aya":23,"text":"这就是大恩。这是安拉给他那归信并作善举的众仆报喜信的。你说：（我不向你们讨报酬，惟盼爱喜亲族。）为善的人，我就增给他好处。安拉是多恕的，颁赏的。","translator":"王静斋"},{"sura":42,"aya":24,"text":"不然，他们说：（他往安拉上说谎了吗?）若是安拉意欲…了，他就封闭你的心。安拉消灭假的，而用自己的话奠定真的。他是深知胸中所怀的。","translator":"王静斋"},{"sura":42,"aya":25,"text":"他那真主允其众仆的悔罪，放赦种种罪恶。","translator":"王静斋"},{"sura":42,"aya":26,"text":"他知道你们的作为；他应承众归信并作善举的人；他增给他们特恩。众逆徒应遭烈刑。","translator":"王静斋"},{"sura":42,"aya":27,"text":"设若安拉为他的众仆扩张给养了，他们必在地上违法，但是他本着预定，降其所意欲…的。他实是尽知能见其众仆的。","translator":"王静斋"},{"sura":42,"aya":28,"text":"他那真主，在众人绝望以后降下大雨，并展开自己的慈惠。他是爱护的，受赞的。","translator":"王静斋"},{"sura":42,"aya":29,"text":"造化天地和散播其中的动物，是属他的表征。在他意欲…的时候，他是能于集合他们的。","translator":"王静斋"},{"sura":42,"aya":30,"text":"你们所遭的患难，是由亲手造成的，他赦免许多的。","translator":"王静斋"},{"sura":42,"aya":31,"text":"你们不是能在地上作抵抗的。你们撇开安拉无一爱护的，也无一相助的。","translator":"王静斋"},{"sura":42,"aya":32,"text":"海中像大山似的行船，是属他的表征。","translator":"王静斋"},{"sura":42,"aya":33,"text":"他若意欲……了，他就使风静止，那些船只遂就停泊在海面上。这内中对于大凡忍耐感念的人，确含有种种表征；","translator":"王静斋"},{"sura":42,"aya":34,"text":"或是他因着他们所作的消灭那些船只。他赦免许多的。","translator":"王静斋"},{"sura":42,"aya":35,"text":"（为的是惩罚他们；）并为那对我的表征起争辩的人知道！自无逃脱的地方。","translator":"王静斋"},{"sura":42,"aya":36,"text":"所赐给你们的无论何物，那是今生的享受。在安拉御前的，是于归信并仰赖其养主的人至好至久的，","translator":"王静斋"},{"sura":42,"aya":37,"text":"也于一般远离大罪和丑恶的人；他们忿怒的时候便就恕过；","translator":"王静斋"},{"sura":42,"aya":38,"text":"也于那般应承养主，并立拜功，他们的事情是彼此商议；并就着我所赐给的作使用的人。","translator":"王静斋"},{"sura":42,"aya":39,"text":"也于那般遭到迫害的时候，施行报复的人。","translator":"王静斋"},{"sura":42,"aya":40,"text":"恶的报应是和它一样的恶。恕过与解和的人，他的报酬由安拉担负。真主实不喜背义的人。","translator":"王静斋"},{"sura":42,"aya":41,"text":"受亏后施行报复的，对这般人无一途径。","translator":"王静斋"},{"sura":42,"aya":42,"text":"途径 惟对于一般迫害众人，并在地上背义着违法的人。这一般应遭痛刑。","translator":"王静斋"},{"sura":42,"aya":43,"text":"忍耐与恕过的人，这确是意志坚决的事情。","translator":"王静斋"},{"sura":42,"aya":44,"text":"经安拉造成迷误的人，撇开真主，无一爱护他的。你看背义的人们，在亲见刑罚的时候，说：（可有一归回的路子吗?）","translator":"王静斋"},{"sura":42,"aya":45,"text":"你看见他们骇惧着被现到火狱上；他们在微贱着，以半睁的眼观看。众归信的人说：（伤折的人是那在复生日伤及自身与眷属的。）须知背义的人必在长久的刑罚中。","translator":"王静斋"},{"sura":42,"aya":46,"text":"他们得不到一切爱友超过安拉来相助他们。经安拉造成迷误的人，在他得不到一个途径。","translator":"王静斋"},{"sura":42,"aya":47,"text":"你们在无转回的那一日来自安拉以前应承养主吧！当那时候，你们不获一个隐身的地方，也不得否认。","translator":"王静斋"},{"sura":42,"aya":48,"text":"如果他们扭转了，我原未差你保护他们；在你应负的只是宣达而已。我若教人尝试我的慈惠了，他就藉它狂喜。他们若因亲手前送的遭患难了，人类一定是忘恩的。","translator":"王静斋"},{"sura":42,"aya":49,"text":"天地的权柄惟安拉执掌。他随便造化，他把女儿赏其意欲……的人；他把男儿赏其意欲……的人；","translator":"王静斋"},{"sura":42,"aya":50,"text":"或把男儿女儿一并赏给他们；并造其意欲……的人为不能生育的。真主实是深知的，全能的。","translator":"王静斋"},{"sura":42,"aya":51,"text":"人不宜于安拉对他交谈，除非借着感悟或由帐后，或他遣使，而使者奉他的命令告谕他所意欲……的人。他实是明哲的，玄高的。","translator":"王静斋"},{"sura":42,"aya":52,"text":"我就这样地把出我命令的（鲁哈）告谕你；你原不知道什么是经典，什么是信仰?我只是置它作光辉。我以它引导自所意欲……的仆人。你必导至正道，","translator":"王静斋"},{"sura":42,"aya":53,"text":"那掌在天的在地的安拉之道。须知诸事惟归到安拉上。","translator":"王静斋"},{"sura":43,"aya":1,"text":"（哈、密默。）","translator":"王静斋"},{"sura":43,"aya":2,"text":"指着表明的经典为誓：","translator":"王静斋"},{"sura":43,"aya":3,"text":"我把这经作为阿拉伯文的古兰，以便你们了解。","translator":"王静斋"},{"sura":43,"aya":4,"text":"的确，它是载在经母的；在我御前高尚的玄奥的。","translator":"王静斋"},{"sura":43,"aya":5,"text":"我以你们是一伙妄为的人而对你们停止教戒，切实地停止吗?","translator":"王静斋"},{"sura":43,"aya":6,"text":"我在前代世人里差过许多圣人。","translator":"王静斋"},{"sura":43,"aya":7,"text":"每逢有一个圣人达至他们，他们就嘲笑他。","translator":"王静斋"},{"sura":43,"aya":8,"text":"我曾灭绝比他们在力量上坚强的。众前人的情形过去了，","translator":"王静斋"},{"sura":43,"aya":9,"text":"你若问他们：（谁造化了天地?）他们必说： 优胜的深知的主造化它们了。）","translator":"王静斋"},{"sura":43,"aya":10,"text":"那真主为你们以地作铺垫，并为你们在地上发现些个道路，以便你们能于达到。","translator":"王静斋"},{"sura":43,"aya":11,"text":"那真主依着预定自天空上降下水来，而用它活死城。将要这样地现出你们。","translator":"王静斋"},{"sura":43,"aya":12,"text":"那个主，他造化了各种匹偶：他为你们造化你们所乘的船只和家畜，","translator":"王静斋"},{"sura":43,"aya":13,"text":"以便你们稳定在它的上面，且为你们当稳定在它上的时候，记念养主的恩典， 说：（赞颂那真主；他为我们降服这个，我们本不是能于管制它的；","translator":"王静斋"},{"sura":43,"aya":14,"text":"我们将必归返养主。）","translator":"王静斋"},{"sura":43,"aya":15,"text":"他们给他安排一部分仆人。人类实是明明忘恩的。","translator":"王静斋"},{"sura":43,"aya":16,"text":"不然，他于自所造化的上取女儿，他把男儿让给你们吗?","translator":"王静斋"},{"sura":43,"aya":17,"text":"他们任凭哪一个，若遇人以那为着慈主举相似的，给他报喜信了，他们的面容遂就变黑了；他是极愤懑的。","translator":"王静斋"},{"sura":43,"aya":18,"text":"生成在装饰里，于分辩上不能阐明的人，可（称为主的女儿）吗?","translator":"王静斋"},{"sura":43,"aya":19,"text":"他们以属于慈主仆辈的天使作女儿，他们亲临造化它们吗? 他们的见证将被记录，而向他们提出质问。","translator":"王静斋"},{"sura":43,"aya":20,"text":"他们说：（设若慈主意欲——了，我们就不拜它们。）他们本不知道那个。他们不过是揣度。","translator":"王静斋"},{"sura":43,"aya":21,"text":"不然，我把以前的经典赐给他们，他们把握它了吗?","translator":"王静斋"},{"sura":43,"aya":22,"text":"不然，他们说：（我们曾见自己的列祖是守一种宗教，我们实是追随他们得正道的。）","translator":"王静斋"},{"sura":43,"aya":23,"text":"就是这样 在你以前每逢我往一城市差去一个传警告的，它的富户就说：（我们确见自己的列祖守一种宗教，我们是在他们后面追随的。）","translator":"王静斋"},{"sura":43,"aya":24,"text":"他说：（假使我把比你们曾见自己列祖所守的，尤能作引导的现给你们，可以吗?）他们说：（我们实不信你们的使命。）","translator":"王静斋"},{"sura":43,"aya":25,"text":"而后我惩罚他们了。看哪！不信的人们结果如何?","translator":"王静斋"},{"sura":43,"aya":26,"text":"昔时，易卜拉欣对他父亲和他的族人说：（我实是对你们所拜的那个无干的；","translator":"王静斋"},{"sura":43,"aya":27,"text":"只有那造化我的主，他即将引导我。）","translator":"王静斋"},{"sura":43,"aya":28,"text":"他把它作为留在自己子孙间的一句话，以便他们转回。","translator":"王静斋"},{"sura":43,"aya":29,"text":"不然，我教这般人和他们的列祖安乐，","translator":"王静斋"},{"sura":43,"aya":30,"text":"直到真理和一倡明的使者达至他们。当真理达到的时候，他们就说：（这是邪术，我们绝不信它。）","translator":"王静斋"},{"sura":43,"aya":31,"text":"他们说：（为什么不把这古兰降给两城市的一个伟人呢?）","translator":"王静斋"},{"sura":43,"aya":32,"text":"他们分配你养主的慈惠吗?我本在今生于中分布他们的给养，我教他们的一部分在阶级上高过一部分，而致他们的一部分以一部分为可受支配的。你养主的慈惠强似他们所积聚的。","translator":"王静斋"},{"sura":43,"aya":33,"text":"若不是世人归为一伙了，我必给不信慈主的人的居室，安置银的屋顶，和登它上升的梯子；","translator":"王静斋"},{"sura":43,"aya":34,"text":"并为他们的居室安（银）门与靠床和金饰。","translator":"王静斋"},{"sura":43,"aya":35,"text":"统不外是今生的享受。后世在你养主认为归属一般敬慎的人。","translator":"王静斋"},{"sura":43,"aya":36,"text":"忽于记念慈主的人，我必为他委定一个恶魔与他结伴。","translator":"王静斋"},{"sura":43,"aya":37,"text":"它们必阻止他们入正路。他们自以为是得正道的了。","translator":"王静斋"},{"sura":43,"aya":38,"text":"待至那人来至我的时候，他就说：（哎！宁愿在我和你两中间有东方和西方的距离。这同伴太恶了。）","translator":"王静斋"},{"sura":43,"aya":39,"text":"只因你们曾经背义，今日，（那种愿望）绝不利于你们；你们当然是在刑罚里共同的。","translator":"王静斋"},{"sura":43,"aya":40,"text":"你能使聋子听到，或能引导瞎子，和那处在显著的迷误之中的人吗?","translator":"王静斋"},{"sura":43,"aya":41,"text":"我若取去你的时候，我必惩治他们。","translator":"王静斋"},{"sura":43,"aya":42,"text":"我若把听许给他们的现给你，我是能于作到的。","translator":"王静斋"},{"sura":43,"aya":43,"text":"你保持所告谕你的吧！你实是在正道上。","translator":"王静斋"},{"sura":43,"aya":44,"text":"那 是劝化你和你族人的。不久你们就受质问。","translator":"王静斋"},{"sura":43,"aya":45,"text":"你问我先你所遣的列使；我们可撇开慈主安置其它受拜的一切主吗?","translator":"王静斋"},{"sura":43,"aya":46,"text":"我曾把母撒藉我的异迹遣至匪喇傲和他的众伟人；他说：（我确是调养众世界的主差来的。）","translator":"王静斋"},{"sura":43,"aya":47,"text":"及母撒把我的异迹现给他们了，他们立时就嘲笑它。","translator":"王静斋"},{"sura":43,"aya":48,"text":"我所现给他们的异迹，全是比先前和它同样的还要伟大。我用刑罚惩治他们，冀使他们转回。","translator":"王静斋"},{"sura":43,"aya":49,"text":"他们说：（术士！你以养主对你所约的，为我们呼求他吧！我们是必定归正道的。）","translator":"王静斋"},{"sura":43,"aya":50,"text":"及我给他们释去刑罚了，他们立时破约。","translator":"王静斋"},{"sura":43,"aya":51,"text":"匪喇傲在他民众里晓谕，说：（我的民众啊！密速尔国岂不是我的吗?这些河道流在我的下面，你们怎不观察呢?","translator":"王静斋"},{"sura":43,"aya":52,"text":"我不比这个语言几至不清的贱人优越吗?","translator":"王静斋"},{"sura":43,"aya":53,"text":"为何不投给他金钏，或有天使随他相继同来。）","translator":"王静斋"},{"sura":43,"aya":54,"text":"他诱其族人失去理智了，所以他们服从他。他们实是一般恶民。","translator":"王静斋"},{"sura":43,"aya":55,"text":"当他们惹我震怒的时候，我就惩罚他们，我把他们一同淹没。","translator":"王静斋"},{"sura":43,"aya":56,"text":"我使他们作为后人的领袖与榜样。","translator":"王静斋"},{"sura":43,"aya":57,"text":"有人举马尔焉的子作比喻的时候，你的族人就立时大声喊起，","translator":"王静斋"},{"sura":43,"aya":58,"text":"说（我们的主是较优的?或是他呢?）他们对你举出这个比喻来，只是因为争辩。不然，他们是一伙好辩的。","translator":"王静斋"},{"sura":43,"aya":59,"text":"他只是蒙我施恩的仆人；我使他作为以斯拉衣来子孙的一个奇迹。","translator":"王静斋"},{"sura":43,"aya":60,"text":"设若我意欲——了，我必以天使代你们在地上继替。","translator":"王静斋"},{"sura":43,"aya":61,"text":"他 实是复生日的一个预兆。你们不要对它怀疑。你们当追随我，这是正路。","translator":"王静斋"},{"sura":43,"aya":62,"text":"莫容恶魔阻止你们。他实是你们的明敌。","translator":"王静斋"},{"sura":43,"aya":63,"text":"当尔撒发现各种异迹的时候，他就说：（我现给你们智慧了；我为你们解明你们所争议的一部分。你们当畏惧安拉，当顺从我。","translator":"王静斋"},{"sura":43,"aya":64,"text":"安拉实是我的养主，也是你们的养主，你们应当拜他，这是正路。）","translator":"王静斋"},{"sura":43,"aya":65,"text":"而后，他们其中几伙人发生争议了。可叹背义的人们遭受痛日的刑罚。","translator":"王静斋"},{"sura":43,"aya":66,"text":"他们只等侯复生日突然来到，其时均在不觉着。","translator":"王静斋"},{"sura":43,"aya":67,"text":"是日，一切朋友，除敬慎的人以外，一部分是一部分的仇敌。","translator":"王静斋"},{"sura":43,"aya":68,"text":"我的一般信我表征并且服从的仆人哪！今日，在你们无惧无忧了。","translator":"王静斋"},{"sura":43,"aya":69,"text":"你们和自己的妻室愉快着进天园吧！","translator":"王静斋"},{"sura":43,"aya":70,"text":".","translator":"王静斋"},{"sura":43,"aya":71,"text":"有的给他们轮斟以金盆和无柄的大盏。（他们）在那里享受心所想望，目觉愉快的。你们永居在那里。","translator":"王静斋"},{"sura":43,"aya":72,"text":"这是你们以自己的作为得到继承的天园。","translator":"王静斋"},{"sura":43,"aya":73,"text":"你们在那里享食多的鲜果。","translator":"王静斋"},{"sura":43,"aya":74,"text":"为恶的人永在火狱的刑罚中。","translator":"王静斋"},{"sura":43,"aya":75,"text":"不给他们轻松。他们是在那里绝望的。","translator":"王静斋"},{"sura":43,"aya":76,"text":"我未亏负他们，他们只是自亏的。","translator":"王静斋"},{"sura":43,"aya":77,"text":"他们呼喊：（玛利克呀！祈你的养主解决我们吧！）说：（你们一定是久留的。）","translator":"王静斋"},{"sura":43,"aya":78,"text":"我把真的告诉你们了，但是你们的多半憎恶真的。","translator":"王静斋"},{"sura":43,"aya":79,"text":"不然，他们决断一事了吗? 我是一定坚决的。","translator":"王静斋"},{"sura":43,"aya":80,"text":"不然，他们猜度我听不到他们的秘密和耳语吗?是的，我的列使在他们跟前作记录。","translator":"王静斋"},{"sura":43,"aya":81,"text":"你说：（若是慈主有儿子了，我是首先敬拜的。","translator":"王静斋"},{"sura":43,"aya":82,"text":"赞调养天地，调养“阿勒世”的主清净，无染他们所称述的。）","translator":"王静斋"},{"sura":43,"aya":83,"text":"你任他们诙谐打趣，待其遇到所许给他们的那一日吧！","translator":"王静斋"},{"sura":43,"aya":84,"text":"他是在天的主，在地的主。他是明哲的，深知的。","translator":"王静斋"},{"sura":43,"aya":85,"text":"那掌天地万有的权柄的主太尊大了，惟他知道复生日，你们惟被归于他。","translator":"王静斋"},{"sura":43,"aya":86,"text":"他们舍去他所拜的那一切，不掌告赦。除非那知道着见证真理的人。","translator":"王静斋"},{"sura":43,"aya":87,"text":"你若问他们：（谁造化他们了，）他们必说：（ 安拉。）他们转变到哪里? （惟真主知道）","translator":"王静斋"},{"sura":43,"aya":88,"text":"他说：（我的养主啊！这般是一伙不信的人）","translator":"王静斋"},{"sura":43,"aya":89,"text":"你宽容他们吧：你当说：和平不久他们就知道。","translator":"王静斋"},{"sura":44,"aya":1,"text":"（哈、密默。 ）","translator":"王静斋"},{"sura":44,"aya":2,"text":"试思表明的经典；","translator":"王静斋"},{"sura":44,"aya":3,"text":"我确在吉庆的夜里降下了它；我确是传警告的；","translator":"王静斋"},{"sura":44,"aya":4,"text":"就在那夜里分别一切固定的事情。","translator":"王静斋"},{"sura":44,"aya":5,"text":"出自我御前的命令。我实是遣使的。","translator":"王静斋"},{"sura":44,"aya":6,"text":"出你养主的慈惠。他实是能听的，深知的。","translator":"王静斋"},{"sura":44,"aya":7,"text":"调养天地万有的主。如果你们是确信的了，……","translator":"王静斋"},{"sura":44,"aya":8,"text":"除他以外再无有主。他使生，使死，（他）是调养你们的主，也是调养你们前代列祖的主。","translator":"王静斋"},{"sura":44,"aya":9,"text":"他们实是在疑惑中嬉戏着。","translator":"王静斋"},{"sura":44,"aya":10,"text":"你等侯天空发现显明的烟气，","translator":"王静斋"},{"sura":44,"aya":11,"text":"遮覆众人的那一日吧！（这是痛刑。","translator":"王静斋"},{"sura":44,"aya":12,"text":"我们的养主啊！你给我们释去刑罚吧！我们是一定归信的。）他们哪能受劝呢?","translator":"王静斋"},{"sura":44,"aya":13,"text":"明明的使者达至他们，","translator":"王静斋"},{"sura":44,"aya":14,"text":"他们且复远避他，说：（他）是一个受过教导的疯子。","translator":"王静斋"},{"sura":44,"aya":15,"text":"我必略微释去刑罚。你们将必复反。","translator":"王静斋"},{"sura":44,"aya":16,"text":"在我捕捉绝大捕捉的那一日，我实是施行惩罚的。","translator":"王静斋"},{"sura":44,"aya":17,"text":"在他们以前，我曾试证匪喇傲的民众。一位高贵的使者，达至他们说：","translator":"王静斋"},{"sura":44,"aya":18,"text":"（你们把安拉的众仆交给我吧！我确是一个对你们忠实的使者；","translator":"王静斋"},{"sura":44,"aya":19,"text":"你们不要对安拉高傲，我把显著的明证现给你们了。","translator":"王静斋"},{"sura":44,"aya":20,"text":"我求我的养主，也是你们的养主护佑，莫遭石击！","translator":"王静斋"},{"sura":44,"aya":21,"text":"如果你们不信我了，你们就远离我。","translator":"王静斋"},{"sura":44,"aya":22,"text":"（而后他呼求养主，（说：）（这般人是一伙子恶民。）","translator":"王静斋"},{"sura":44,"aya":23,"text":"你偕我的仆人夜行吧!你们必定遭追赶。","translator":"王静斋"},{"sura":44,"aya":24,"text":"你任海静止着； 他们必定是被淹的军兵。","translator":"王静斋"},{"sura":44,"aya":25,"text":"他们遗下了多的花园，源泉，","translator":"王静斋"},{"sura":44,"aya":26,"text":"他们遗下了多的花园，源泉，","translator":"王静斋"},{"sura":44,"aya":27,"text":"与他们所享受的安逸的生活。","translator":"王静斋"},{"sura":44,"aya":28,"text":"就是这样。我教另一伙人继承它了。","translator":"王静斋"},{"sura":44,"aya":29,"text":"天地不哀泣他们；他们不获姑容。","translator":"王静斋"},{"sura":44,"aya":30,"text":"我确教以斯拉衣来的子孙脱离匪喇傲的辱刑了；","translator":"王静斋"},{"sura":44,"aya":31,"text":"他实是高傲妄为的。","translator":"王静斋"},{"sura":44,"aya":32,"text":"我在世人上依着知识选择了他们。","translator":"王静斋"},{"sura":44,"aya":33,"text":"我把内含明明试证的异迹赐给他们了。","translator":"王静斋"},{"sura":44,"aya":34,"text":"这般人说：","translator":"王静斋"},{"sura":44,"aya":35,"text":"死亡就是初次的死亡，我们绝非复起的。","translator":"王静斋"},{"sura":44,"aya":36,"text":"如果你们是实言的了，要你们拿来我们的列祖吧。）","translator":"王静斋"},{"sura":44,"aya":37,"text":"他们是至优的吗?不然，是顿白尔的族人，及其以前的人呢?我曾灭绝他们了，他们本是为恶的人。","translator":"王静斋"},{"sura":44,"aya":38,"text":"我未戏耍着造化天地万有。","translator":"王静斋"},{"sura":44,"aya":39,"text":"我只因为真理造化了天地；但是他们的多半不知道。","translator":"王静斋"},{"sura":44,"aya":40,"text":"的确，分明的日子是他们一个共同的时期。","translator":"王静斋"},{"sura":44,"aya":41,"text":"当那一日，除去蒙受安拉慈惠的人以外，亲朋不能代亲朋避免一事；他们不获相助。","translator":"王静斋"},{"sura":44,"aya":42,"text":"他实是优胜的，特慈的。","translator":"王静斋"},{"sura":44,"aya":43,"text":"凿骨末树","translator":"王静斋"},{"sura":44,"aya":44,"text":"确是罪犯的食物，","translator":"王静斋"},{"sura":44,"aya":45,"text":"像是熔化的铜；在腹内沸腾，","translator":"王静斋"},{"sura":44,"aya":46,"text":"就像滚水的沸腾着。","translator":"王静斋"},{"sura":44,"aya":47,"text":"你们捉住他！把他扯在火狱的中央！","translator":"王静斋"},{"sura":44,"aya":48,"text":"再往他头上倾滚水的刑罚。","translator":"王静斋"},{"sura":44,"aya":49,"text":"你尝试吧！你确是伟大的，尊贵的。","translator":"王静斋"},{"sura":44,"aya":50,"text":"这是你们曾对它怀疑的，","translator":"王静斋"},{"sura":44,"aya":51,"text":"敬畏的人们是在平安的地方；","translator":"王静斋"},{"sura":44,"aya":52,"text":"在花园与源泉。","translator":"王静斋"},{"sura":44,"aya":53,"text":"他们穿着细绫和粗缎，彼此相向着。","translator":"王静斋"},{"sura":44,"aya":54,"text":"就是这样。我以宽目面白的女子给他们作婚配。","translator":"王静斋"},{"sura":44,"aya":55,"text":"他们在那里安然着寻求一切鲜果。","translator":"王静斋"},{"sura":44,"aya":56,"text":"他们在那里，除去头一次死亡以外，不再尝一死。真主庇佑他们脱开火狱的刑罚，","translator":"王静斋"},{"sura":44,"aya":57,"text":"由于出你养主的特恩。这就是大成功。","translator":"王静斋"},{"sura":44,"aya":58,"text":"我用你的乡音把它作为容易的，好教他们受劝。","translator":"王静斋"},{"sura":44,"aya":59,"text":"你等待吧，他们是定要等待的。","translator":"王静斋"},{"sura":45,"aya":1,"text":"「哈、密默。」","translator":"王静斋"},{"sura":45,"aya":2,"text":"降经是由优胜明哲的安拉上。","translator":"王静斋"},{"sura":45,"aya":3,"text":"天地间确含有对众穆民的种种表征。","translator":"王静斋"},{"sura":45,"aya":4,"text":"造化你们和他所散播的动物里，对于坚信的民众含有种种表征。","translator":"王静斋"},{"sura":45,"aya":5,"text":"夜昼的不同，与安拉降自上空而用它活地于既死以后的给养，与转变风，内中对于有理解的民众确含有种种显迹。","translator":"王静斋"},{"sura":45,"aya":6,"text":"这是安拉的表征。我把它据实述说给你。他们信安拉和他的表征以外的什么言语呢?","translator":"王静斋"},{"sura":45,"aya":7,"text":"一切说谎干罪的人有祸了；","translator":"王静斋"},{"sura":45,"aya":8,"text":"那人听到安拉的表征有人诵给他，他依然高傲；好像他未曾听见，你报告给他痛刑吧！","translator":"王静斋"},{"sura":45,"aya":9,"text":"他知道我的些微表征的时候，就以它为可戏的。这般人应遭辱刑。","translator":"王静斋"},{"sura":45,"aya":10,"text":"在他们前面有火狱；他们所赚下的未能代为避免一事、他们撇开安拉置作可爱的也未能够。他们应遭巨刑。","translator":"王静斋"},{"sura":45,"aya":11,"text":"这是一种指导；不信其养主的表征的人，应遭痛苦的烈刑。","translator":"王静斋"},{"sura":45,"aya":12,"text":"安拉，那真主为你们降服了海，以便船只奉他的命令在那里航行；并为你们讨他的特恩，冀使你们感念。","translator":"王静斋"},{"sura":45,"aya":13,"text":"他为你们降服了天地间所出自他的一切。此中对于有参悟的民众确含有种种表征。","translator":"王静斋"},{"sura":45,"aya":14,"text":"你说给众归信的人，饶恕那不盼望安拉的时日的人们；为的是真主还报一伙人，以其所作过的。","translator":"王静斋"},{"sura":45,"aya":15,"text":"作善举的人利他自身；作恶的人害他自身；而后你们惟被归到养主上。","translator":"王静斋"},{"sura":45,"aya":16,"text":"我确把经典、裁判、圣职，赐给以斯拉衣来的子孙了；我把一部分美品赐给他们，我使他们优越世人了。","translator":"王静斋"},{"sura":45,"aya":17,"text":"我且把关于这事。种种的明证赐给他们。他们发生争议，惟在得到知识以后，由于自间的嫉忌。你的养主将在复生日对于他们所争议的从中判断。","translator":"王静斋"},{"sura":45,"aya":18,"text":"而后我把你委在这事情的明路上。你当追随它，不得追从一般无知识人的私见。","translator":"王静斋"},{"sura":45,"aya":19,"text":"他们绝不能代你避免安拉的一事。背义的人们其一部分是一部分的爱友。安拉爱护敬慎的人。","translator":"王静斋"},{"sura":45,"aya":20,"text":"这是对世人的明证，并引导与慈惠坚信的民众。","translator":"王静斋"},{"sura":45,"aya":21,"text":"不然，作恶的人自以为我把他们当若归信并行善举的人，他们的生死相等吗? 他们判断的太恶了。","translator":"王静斋"},{"sura":45,"aya":22,"text":"安拉因为真理造化了天地；并对每一个人依其所作的施以还报，并不亏损。","translator":"王静斋"},{"sura":45,"aya":23,"text":"你可看见以自己的私见当主宰的人吗? 安拉明知着造他迷误了，并封闭他的耳和心，更在他的眼上造成一种幔帐。谁在安拉以后引导他，你们怎不受劝呢?","translator":"王静斋"},{"sura":45,"aya":24,"text":"他们说：（生存只是我们今世的生存；我们有死有生。消灭我们的只是光阴。 ）他们本不实知道这事。他们不过是揣度。","translator":"王静斋"},{"sura":45,"aya":25,"text":"有人向他们诵我显明的表征时候，他们的争论只是说：（如果你们的所言属实，你们就拿来我们的列祖。）","translator":"王静斋"},{"sura":45,"aya":26,"text":"你说：（安拉使你们生，又使你们死，又把你们集至无可疑的复生日。但是世人的多半不知道。","translator":"王静斋"},{"sura":45,"aya":27,"text":"天地的权柄惟安拉执掌。在复生日成立的那一天，是日，一般虚浮的人伤折了。","translator":"王静斋"},{"sura":45,"aya":28,"text":"你看见每一个人是跪着的。每一个人被唤至自己的文卷。（我说：）今日一本你们的作为施以还报。","translator":"王静斋"},{"sura":45,"aya":29,"text":"（我说：）这是我写定的。它据实言说给你们，我曾写定你们所作的。","translator":"王静斋"},{"sura":45,"aya":30,"text":"归信并作善举的人，养主将教他们入在他的慈惠中。这是显著的成功。","translator":"王静斋"},{"sura":45,"aya":31,"text":"不信的人，（我对他们说：我的表征岂未有人诵给你们吗?但是你们高傲，归为罪民了）","translator":"王静斋"},{"sura":45,"aya":32,"text":"每逢有人说：（安拉的约是确实的，复生日是无可疑惑的，）你们就说：（我们不知道什么是复生日，我们不过是略有揣度；我们绝不深信。）","translator":"王静斋"},{"sura":45,"aya":33,"text":"他们所作的种种罪恶现露给他们了。他们被自所嘲笑的包围了。","translator":"王静斋"},{"sura":45,"aya":34,"text":"有的说：（今日，我忘却你们，就如你们忘却了看见今日；你们的居所是火狱，无有相助你们的。","translator":"王静斋"},{"sura":45,"aya":35,"text":"这是由于你们以安拉的表征当作可戏的。你们受今世生活的欺骗了。）今日，不容他们出离火狱，也不允他们求喜悦。","translator":"王静斋"},{"sura":45,"aya":36,"text":"赞颂安拉——调养天的主，调养地的主，调养众世界的土。","translator":"王静斋"},{"sura":45,"aya":37,"text":"天地间的尊大惟他所有。他是优胜的，明哲的。","translator":"王静斋"},{"sura":46,"aya":1,"text":"（哈、密默。）","translator":"王静斋"},{"sura":46,"aya":2,"text":"降经是由优胜明哲的安拉上。","translator":"王静斋"},{"sura":46,"aya":3,"text":"我只因为真理和一定的期限造化了天地万有。不信的人是远避所警告他们的。","translator":"王静斋"},{"sura":46,"aya":4,"text":"你说！（你们曾见自己撇开安拉所呼求的吗?你们现给我。它们从地上造化的是些什么？）不然，它们参与（造）天了吗?如果你们所言属实，你们就把这以前的经典或遗下的学识现给我。","translator":"王静斋"},{"sura":46,"aya":5,"text":"撇开安拉呼求那直到复生日不能允许他，且茫于一般的呼求者，谁比这类人更是不义的?","translator":"王静斋"},{"sura":46,"aya":6,"text":"当集合众人的时候，它们是他们的仇敌；更不承认他们的敬拜。","translator":"王静斋"},{"sura":46,"aya":7,"text":"有人向他们诵我的显明的表征时候，不信的人在真理达到之际，就指着它说：（这只是显明的邪术。）","translator":"王静斋"},{"sura":46,"aya":8,"text":"不然，他们就说：（他造作它了。）你说：（若是我造作它了，你们不能抵抗安拉管我一事，他是至知你们对它所深究的。真主在我和你们中间作见证，已可满足了。他是多恕的，特慈的。）","translator":"王静斋"},{"sura":46,"aya":9,"text":"你说：（我不是列使中一个特殊的。我不知道：将怎样对待我和你们。我只追从自己所得到的启示；我只是一个公开传警告的。）","translator":"王静斋"},{"sura":46,"aya":10,"text":"你说：（你们告诉我：它若出自安拉御前，你们不信它，以斯拉衣来子孙中一个证人见证和它一样的， 他已然归信，你们高傲了。安拉绝不引导逆民。","translator":"王静斋"},{"sura":46,"aya":11,"text":"逆徒们指着归信的人说：（设若那是很好的了，他们不能争先我们得到它。）他们未能以它得正道的时候，就说：（这是早年的谎言。）","translator":"王静斋"},{"sura":46,"aya":12,"text":"在它以前原有母撒的经典，在作领导和慈惠着。这是证实的阿拉伯乡音的经典，好教它警告一般背义的人，并给一般为善的人报喜信。","translator":"王静斋"},{"sura":46,"aya":13,"text":"的确，声称：调养我们的主是安拉并且守正的人，在他们无惧无忧。","translator":"王静斋"},{"sura":46,"aya":14,"text":"这般人是永居天园的，因着他们所作的施以报酬。","translator":"王静斋"},{"sura":46,"aya":15,"text":"我嘱告人类对他父母行孝。他的母坚苦着孕他，坚苦着产他。怀他和他的离乳是三十个月。待他已达壮年，至四十岁的时候，他就说：（我的养主啊！你感悟我：谢你对我并对我父母的恩典，且作你所喜悦的善功；你为我规正我的子孙，我确向你悔罪了；我确是顺服的。）","translator":"王静斋"},{"sura":46,"aya":16,"text":"这般人是蒙我允纳他们所作的最好的，并放过他们种种罪恶，列在天园人以内的。这是所许给他们的实约。","translator":"王静斋"},{"sura":46,"aya":17,"text":"有人对自己父母说：（噫嘻你两个：在我以前的若干代确已过去了，你俩尚恫吓我将被现出吗?）他俩求安拉解救着（说：）（你该遭祸了！你归信了吧！安拉的约是确实的。）他说：（这不过是前人的谎话。）","translator":"王静斋"},{"sura":46,"aya":18,"text":"这般人是话已对他们决定的了，列在他们以前过去的群众，镇呢和人类以内的。他们均是伤折的。","translator":"王静斋"},{"sura":46,"aya":19,"text":"每一个人依其所作的获得种种阶级。真主把他们所作的完全赏给他们，并不亏损。","translator":"王静斋"},{"sura":46,"aya":20,"text":"当那一日，逆徒们被现到火狱上；有的说：（你们在生前已然取得自己的美事了，你们已竟借着它享受了。今日，还报你们辱刑，只因你们在地上背义着高傲，并由于你们作恶。）","translator":"王静斋"},{"sura":46,"aya":21,"text":"你当记念阿代的兄弟。那时候，他在哀哈格夫警告自己的族人： 那时候，一般作警告的人在他的前后过去了（你们莫拜安拉以外的；我实为你们骇怕大日的刑罚。）","translator":"王静斋"},{"sura":46,"aya":22,"text":"他们说：（你来阻止我们拜自己的主吗?如果你的所言属实，你就把所约定的现给我们。）","translator":"王静斋"},{"sura":46,"aya":23,"text":"他说：（知道惟在安拉。我向你们宣达使命，但是我看你们是无知的一伙人。）","translator":"王静斋"},{"sura":46,"aya":24,"text":"当他们见它是向山谷前进的云的时候，他们就说：（这是向我们落雨的云。）不然，那是你们要求立时实现的，就是含有烈刑的风；","translator":"王静斋"},{"sura":46,"aya":25,"text":"那风奉其养主的命令，毁灭所有的物类。次晨，他们仅止看见自己的居处。我就这样地还报一般为恶的民众。","translator":"王静斋"},{"sura":46,"aya":26,"text":"我确教他们住在未曾教你们住过的地方；我为他们造化了耳、眼、心，但是他们的耳、眼、心未曾代为避免一事。那时候，他们不承认安拉的表征。他们被自所嘲笑的包围了。","translator":"王静斋"},{"sura":46,"aya":27,"text":"我确曾灭绝你们四周的城池；我重复种种表征，好教他们转回。","translator":"王静斋"},{"sura":46,"aya":28,"text":"他们舍去安拉，为着接近而当作主宰的那一切，为什么不相助他们呢?不然，那一切离他们不在了。这是他们的谎言，是他们自行造作的。","translator":"王静斋"},{"sura":46,"aya":29,"text":"那时候，我使一伙子镇呢往你那里去听古兰。当他们集结到他跟前的时候，他们就说：（要你们缄默，）及至终了的时候，他们就回到自己的族人，传警告着，","translator":"王静斋"},{"sura":46,"aya":30,"text":"他们呼自己的族人说：（我们确听得在母撒以后一部被降的经典，证实它以前的，导至真理和正路。","translator":"王静斋"},{"sura":46,"aya":31,"text":"我们的族人哪，你们当应允安拉的唤导者，你们当归信他，他就饶恕你们的罪恶；他就解救你们脱开痛刑。","translator":"王静斋"},{"sura":46,"aya":32,"text":"凡是不 应允安拉的唤导者，他不是能在地上作抵抗的。在他无有真主以外的爱护者。这般人是在显著的迷误中。","translator":"王静斋"},{"sura":46,"aya":33,"text":"他们岂未看见那造化天地，未因着造化它们疲乏的安拉，是能于复活死者的吗?是的，他实是全能于万事的。","translator":"王静斋"},{"sura":46,"aya":34,"text":"当不信的人被现至火狱的时候，（有的就说：）这不是真的吗?他们说：（指我们的养主为誓：是的。）他说：（你们由于不信尝试刑罚吧！）","translator":"王静斋"},{"sura":46,"aya":35,"text":"你当忍耐，就像意志坚决的列使忍耐！你不要为他们要求立时实现。好像是他们在亲见所约定的那一天，仅迟留白昼的一时。（这）是一种宣告。遭灭绝的只有一般恶民。","translator":"王静斋"},{"sura":47,"aya":1,"text":"不信并且阻碍安拉之道的人，真主已消灭他们的功修了。","translator":"王静斋"},{"sura":47,"aya":2,"text":"归信并行义举且信降给穆罕默德的人——那本是出自养主的真理。真主给他们遮蔽罪恶，并规正他们的情况。","translator":"王静斋"},{"sura":47,"aya":3,"text":"这是由于不信的人追随假的，归信的人追随出自养主的真理。安拉就是这样地为世人发表他们的情形。","translator":"王静斋"},{"sura":47,"aya":4,"text":"你们遇见逆徒的时候，就打脖子；待至你们对他们鏖战的时候，要绑紧了绳索。 嗣后，或开恩，或取赎，直到战争卸下它的重负为止。就是这样。设若安拉意欲……了，他必惩创他们。但是（他以此命令你们，）为的是以一部分试证一部分。被杀在安拉之道的人，真主绝不消灭他们的功修；","translator":"王静斋"},{"sura":47,"aya":5,"text":"他将引导他们，规正他们的情况；","translator":"王静斋"},{"sura":47,"aya":6,"text":"并教他们进天园，他已把它介绍给他们了。","translator":"王静斋"},{"sura":47,"aya":7,"text":"众归信的人哪，若是你们相助安拉了，他就相助你们，并稳定你们的脚步。","translator":"王静斋"},{"sura":47,"aya":8,"text":"可叹不信的人们遭祸了，真主已使他们的功修归为无效。","translator":"王静斋"},{"sura":47,"aya":9,"text":"那是因他们憎恶安拉所降的，所以他消灭他们的功修。","translator":"王静斋"},{"sura":47,"aya":10,"text":"他们岂未在地上游行，而观察他们以前众人的结果如何吗?安拉毁灭他们了。逆徒们应遭像这样的。","translator":"王静斋"},{"sura":47,"aya":11,"text":"这由于安拉确是相助众穆民的，并由于逆徒们自无有爱护的。","translator":"王静斋"},{"sura":47,"aya":12,"text":"安拉将令归信并行义举的人进入诸河流于其下的天园。逆徒们亨受，他们吃，就像是牲畜们一样的吃。火狱就是他们的居处。","translator":"王静斋"},{"sura":47,"aya":13,"text":"许多城池，它是在实力上比那逐出你的城池更有实力。我灭绝他们了，无有相助他们的。","translator":"王静斋"},{"sura":47,"aya":14,"text":"守其养主的明证的人，可像那恶行为受了粉饰而随从自己私见的人吗?","translator":"王静斋"},{"sura":47,"aya":15,"text":"许给敬慎之人天园的情形……。内有常久不浊的水河，滋味不变的乳河，在饮者感觉味美的酒河，和清澈的蜜河。他们在那里亨受各项果实，并蒙其养主的饶恕。（这般人）可像那永居火狱被饮给滚水而割断肠子的人吗?","translator":"王静斋"},{"sura":47,"aya":16,"text":"他们内中有向你侧耳的；待至从你跟前出去的时候，他们就向一般得到知识的人说：（他适才说的是什么?）这般人，安拉已封闭他们的心。他们追从自己的私见了。","translator":"王静斋"},{"sura":47,"aya":17,"text":"得正道的人，真主越发引导他们，并赐给他们虔恪。","translator":"王静斋"},{"sura":47,"aya":18,"text":"不信的人们，只等侯复生日突然来到。它的一切预兆确已降临了。当它来到的时候，他们哪能受劝?","translator":"王静斋"},{"sura":47,"aya":19,"text":"你当知道安拉以外再无有主；你当为自己的罪并为众男女穆民求恕。安拉知道你们往返的地方，和你们归来的地方。","translator":"王静斋"},{"sura":47,"aya":20,"text":"众归信的人说：（为何不降一苏赖?）及至降下肯定的一章，提到格斗的时候，你看那心中有病的人看你，如同因为怕死而昏晕过去的人观看。他们快要遭祸了，","translator":"王静斋"},{"sura":47,"aya":21,"text":"惟有服从和光明的言语。在事情决定的时候，设若他们对安拉忠实了，那实是于他们最好的了。","translator":"王静斋"},{"sura":47,"aya":22,"text":"若是你们不肯了，恐怕你们就要在地上造恶，并与血亲断绝往来吧！","translator":"王静斋"},{"sura":47,"aya":23,"text":"这般人是遭安拉驱远，致他们耳聋眼瞎的。","translator":"王静斋"},{"sura":47,"aya":24,"text":"他们怎不参悟古兰呢?不然，是一般心上有锁子吗?","translator":"王静斋"},{"sura":47,"aya":25,"text":"在明白正道以后退转的人，恶魔给他们一种幻想了。真主姑容他们。","translator":"王静斋"},{"sura":47,"aya":26,"text":"这是由于他们对那般憎恶安拉所降者的人说：（我们即将在一部分事情上顺从你们，）安拉知道他们的密语，","translator":"王静斋"},{"sura":47,"aya":27,"text":"在天使打他们的面目和脊背着取其性命的时候，是个什么情形?","translator":"王静斋"},{"sura":47,"aya":28,"text":"这是由于他们追从那惹安拉恼恨的事情，并憎恶真主的喜爱，所以他消灭他们的功修了。","translator":"王静斋"},{"sura":47,"aya":29,"text":"不然，心中有病的人，自以为安拉绝不暴露他们的衔恨吗?","translator":"王静斋"},{"sura":47,"aya":30,"text":"设若我意欲——了，我就把他们现给你，你必以形迹认识他们。你必在语言之间认出他们来。安拉知道你们的功修。","translator":"王静斋"},{"sura":47,"aya":31,"text":"我将试证你们终至晓得。你们其中出战的，忍耐的。我且试证你们的种种行动。","translator":"王静斋"},{"sura":47,"aya":32,"text":"的确，一般不信并阻碍安拉之道，且在明白正道以后反抗使者的人，他们绝不能妨害安拉一事。真主即将消灭他们的功修。","translator":"王静斋"},{"sura":47,"aya":33,"text":"众归信的人那！你们顺从安拉吧！你们顺从使者吧！不要破坏自己的功修。","translator":"王静斋"},{"sura":47,"aya":34,"text":"的确，不信并阻碍安拉之道，终至不信着死去的人，安拉绝不饶恕他们。","translator":"王静斋"},{"sura":47,"aya":35,"text":"你们不要示弱，不要倡和，你们本是至获胜利的。安拉是随同你们的，他绝不消灭你们的功修。","translator":"王静斋"},{"sura":47,"aya":36,"text":"今生不外是玩乐和荒嬉。若是你们归信了，敬慎了，主就赐给你们报酬，他不向你们求财。","translator":"王静斋"},{"sura":47,"aya":37,"text":"若是他向你们求财，且向你们勉强要求，你们就吝啬起来，那就暴露你们的衔恨了。","translator":"王静斋"},{"sura":47,"aya":38,"text":"咳！你们这般人被唤至使用在安拉之道上；但是你们之中有的人吝啬。吝啬的人惟害其自身吝啬。安拉是无求的，你们是祈求的。若是你们不肯了，他就改换除过你们的一伙人，他们不复和你们一样。","translator":"王静斋"},{"sura":48,"aya":1,"text":"我确已为你开放，显明地开放，","translator":"王静斋"},{"sura":48,"aya":2,"text":"终至安拉恕你以前以后的罪，全满他对你的恩典，导你于正路。","translator":"王静斋"},{"sura":48,"aya":3,"text":"安拉相助你，","translator":"王静斋"},{"sura":48,"aya":4,"text":"有力地相助他那真主：把安静降在众穆民心中，以便他们于归信上增加归信。 天地的军兵是安拉执掌。安拉是深知的，明哲的。","translator":"王静斋"},{"sura":48,"aya":5,"text":"（真主赐以安静，）为的是使男女穆民进入诸河流于其下的天园，永居在那里；并为给他们遮蔽种种罪恶，那在安拉认为一大成功；","translator":"王静斋"},{"sura":48,"aya":6,"text":"并为惩治一般男女伪信士，和男女信匹偶的，…… 一般对安拉揣度恶地揣度的。他们应遭恶患！安拉怒恨他们，驱逐他们，并为他们预备火狱了。那归所太恶了！","translator":"王静斋"},{"sura":48,"aya":7,"text":"天地的军兵惟安拉执掌。安拉是优胜的，明哲的。","translator":"王静斋"},{"sura":48,"aya":8,"text":"我确差你作见证，报喜信，传警告着。","translator":"王静斋"},{"sura":48,"aya":9,"text":"（穆民大众啊！）为的是你们信安拉和他的使者，你们相助他，尊崇他。你们在朝夕颂他清净。","translator":"王静斋"},{"sura":48,"aya":10,"text":"的确，一般对你矢诚的人，即是对安拉矢诚。安拉的手是在他们的手上。破约的人，那只是害他自身破约。全其对安拉所约的人，主即将赐给他巨赏。","translator":"王静斋"},{"sura":48,"aya":11,"text":"一般居郊野的落后者，就要对你说：（我们的财产眷属贻误我们了；你为我们求恕吧！）他们口说心里无有的。你说：（若是安拉意欲伤你们，或欲利于你们，谁为你们管安拉的一事?","translator":"王静斋"},{"sura":48,"aya":12,"text":"不然，安拉尽知你们的作为。不然，你们揣度使者和众穆民永不能转回家乡了。有的在你们心中装饰这事了；你们揣度，恶地揣度了。你们原是一伙沦丧的。）","translator":"王静斋"},{"sura":48,"aya":13,"text":"未信安拉和他的使者的人，我已为这般不信的人预备烈火了。","translator":"王静斋"},{"sura":48,"aya":14,"text":"天地的权柄惟安拉执掌。他恕其意欲……的人，惩其意欲……的人。安拉是多恕的，特慈的。","translator":"王静斋"},{"sura":48,"aya":15,"text":"这般落后的人，当你们往取战利品的时候，就要说：（你们容我们同去吧！）他们欲变更安拉的话。你说：（你们绝不能追随我们；从前安拉就这样地说过了。 ）他们就要说：（不然，你们嫉忌我们。）不然，他们只是稍微的解悟。","translator":"王静斋"},{"sura":48,"aya":16,"text":"你对一般居郊野落后的人说：（你们即将被召至一伙强壮勇武的人。你们对他们格斗，或是他们归依伊斯兰。如果你们服从了， 安拉就赐给你们优赏。若是你们不肯，就像从前不肯了，他就惩你们以痛刑。）","translator":"王静斋"},{"sura":48,"aya":17,"text":"在瞎子无罪，在瘸子无罪，在病人无罪。顺从安拉和他的使者的人，真主将教他进入诸河流于其下的天园。不肯的人，真主将惩他以痛刑。","translator":"王静斋"},{"sura":48,"aya":18,"text":"安拉确实爱喜众穆民了。那时候，他们在树下向你矢诚。他知道他们心怀的。而后，他降给他们安静了；他把不远的攻克。","translator":"王静斋"},{"sura":48,"aya":19,"text":"以及他们得到的许多战利品赏给他们了。安拉是优胜的，明哲的。","translator":"王静斋"},{"sura":48,"aya":20,"text":"安拉许给你们得到的很多战利品，他把这个立时赐给你们。他替你们制止众人的手。好教它作为对于众穆民的表征，他指示你们正路，","translator":"王静斋"},{"sura":48,"aya":21,"text":"更有你们所不能（获得）的另外一项，安拉确知道它。安拉是全能于万事的。","translator":"王静斋"},{"sura":48,"aya":22,"text":"设若众不信的人对你们格杀了，他们一定掉过背去；他们再得不到一个爱护的，相助的。","translator":"王静斋"},{"sura":48,"aya":23,"text":"就像安拉的已竟过去的惯例，你于安拉的惯例上绝不见得有变更。","translator":"王静斋"},{"sura":48,"aya":24,"text":"他那真主，在教你们获胜利以后，在墨克的腹地，为你们制止他们的手，并为他们制止你们的手。安拉看得见你们的作为。","translator":"王静斋"},{"sura":48,"aya":25,"text":"他们这般人，自己不信，并阻止你们入禁寺，且阻止祭物，被拘不能达到它的处所，有一般男女穆民，你们不知道他们。若不是（恐怕）你们误踏他们，而致遭其憎恶了……。终至安拉使自所意欲……的人入在他的慈惠中。设若他们分居了，我必用痛刑惩治他们其中一般逆徒。","translator":"王静斋"},{"sura":48,"aya":26,"text":"那时候，逆徒们心怀骄狂，愚昧的骄狂，而后安拉把沉静降在自己的使者和穆民上了。他使他们坚守虔恪的话。 他们是至相宜它的，堪当它的。安拉是深知万事的。","translator":"王静斋"},{"sura":48,"aya":27,"text":"安拉确以正义向他的使者实现梦象了；（那就是）如果安拉意欲……了，你们就剃头，剪短（头发）安然无恐着入禁寺。他知道你们不知道的。主在这以前已决定相近的攻克。","translator":"王静斋"},{"sura":48,"aya":28,"text":"他是因着引导和真教遣他的使者，为的是使它超出么教以上的主。安拉作见证已可满足了。","translator":"王静斋"},{"sura":48,"aya":29,"text":"穆罕默德是安拉的使者。追随他的人是对一般逆徒严厉的，在自己之间慈祥的。你看他们鞠躬叩头，在讨安拉的特恩和喜悦着。他们面上的情状是属叩头的踪迹。这是他们载在讨喇忒上的情形。他们载在引支勒上的情形，好像是禾谷发出枝条。而助长它，而成粗壮的立在它的枝干上。那禾田使得农人们喜爱，（作此比喻，）为的是借着他们激动逆徒的怒恨。安拉对他们的一般归信并作善举的人，许给饶恕和重赏了。","translator":"王静斋"},{"sura":49,"aya":1,"text":"众归信的人哪！你们不得超过安拉并他的使者自作主张。你们当畏惧安拉！安拉实是能听的，深知的。","translator":"王静斋"},{"sura":49,"aya":2,"text":"众归信的人哪！你们不要使自己的声音高过圣人的声音；你们不要对他高声说话，就像你们彼此一个对一个的高声，免得你们的善功于不知觉之中归为失效。","translator":"王静斋"},{"sura":49,"aya":3,"text":"的确，那在安拉的使者面前低声的人，均是安拉因为（表露）虔恪试证其心的。他们可获饶恕和大赏。","translator":"王静斋"},{"sura":49,"aya":4,"text":"的确，从内室后面呼唤你的人，他们的多半不甚了解。","translator":"王静斋"},{"sura":49,"aya":5,"text":"设若他们忍耐，待你出至他们了，那确是于他们至好的了。安拉是多恕的，特慈的。","translator":"王静斋"},{"sura":49,"aya":6,"text":"众归信的人哪！若有恶人向你们报告一项消息；你们当加以考察，免得你们误害一伙人，而致追悔自所作过的。","translator":"王静斋"},{"sura":49,"aya":7,"text":"你们当要知道自己间有安拉的使者。设若他在许多事情上顺从你们，一定你们就要受苦了。安拉只是教你们爱喜「信仰，」并以它装饰你们的心，且教你们憎恶不信、作恶、违命。这般人，就是借着安拉的特慈和恩典得正道的。","translator":"王静斋"},{"sura":49,"aya":8,"text":"安拉是深知的，明哲的。","translator":"王静斋"},{"sura":49,"aya":9,"text":"若有两伙穆民相格斗了，你们就应从中调解。如果一方面压迫另一方面了，你们就对那施行压迫的一方面武力从事，待至他归到安拉的命令。如果他归反了，你们就从中秉公调解，并应主持公道。","translator":"王静斋"},{"sura":49,"aya":10,"text":"安拉实爱喜主持公道的人。惟有众穆民是弟兄，你们要在自己兄弟之间解和；你们当畏惧安拉，冀使你们蒙慈惠。","translator":"王静斋"},{"sura":49,"aya":11,"text":"众归信的人哪，莫容一伙男子嘲弄一伙男子；恐怕这一般是优越他们的。也莫容一伙女子嘲弄一伙女子；恐怕这一般比她们优越。你们不要毁谤同类。你们不要相呼以浑号。在归信以后丑名太坏了！未曾悔罪的人，均是背义的。","translator":"王静斋"},{"sura":49,"aya":12,"text":"众归信的人哪！你们要避免多揣；一部分猜疑确是罪恶。你们不要搜查；你们不要背后一个谈论一个。你们有人爱吃他死弟兄的肉吗? 但是你们憎恶它。 你们当畏惧安拉。安拉实是允承悔罪的，特慈的。","translator":"王静斋"},{"sura":49,"aya":13,"text":"众人哪！我从一男一女上造化了你们，我把你们分成各族各支，好教你们彼此相识。的确，你们在安拉御前至高贵的，是你们至虔敬的。安拉实是深知的，尽知的。","translator":"王静斋"},{"sura":49,"aya":14,"text":"居郊野的人说：（我们归信了。）你说：（你们未归信，你们只说：我们顺服了。）信仰尚未入到你们心里。若是你们顺服安拉和他的使者了，他不给你们减少些微功修，安拉实是多恕的，特慈的。","translator":"王静斋"},{"sura":49,"aya":15,"text":"众穆民只是那般归信安拉和他的使者，不复怀疑，且用财命在安拉之道上奋斗的。这般人就是实言的。","translator":"王静斋"},{"sura":49,"aya":16,"text":"你说!（你们把自己的教门告知安拉。安拉知道在天的在地的，安拉是深知万事的。 ）","translator":"王静斋"},{"sura":49,"aya":17,"text":"他们以归信伊斯兰向你示惠，你说！你们不要以自己的归顺向我示惠。安拉应以指示（信仰）向你们示惠。如果你们的所言属实了，……。","translator":"王静斋"},{"sura":49,"aya":18,"text":"安拉实知道天地间目不能见的。安拉看得见你们的作为。","translator":"王静斋"},{"sura":50,"aya":1,"text":"（戛甫。）指着光荣的古来为誓；古来氏的逆徒不信穆罕默德。","translator":"王静斋"},{"sura":50,"aya":2,"text":"不然！他们奇异出自同类的一个传警告的来至他们吗?众不信的人说：（这是一种奇事。","translator":"王静斋"},{"sura":50,"aya":3,"text":"我们已死而化为土的时候，可以吗? 这是遥远的归返。）","translator":"王静斋"},{"sura":50,"aya":4,"text":"我确知道地从他们上减去的。在我御前有被庇的记录。","translator":"王静斋"},{"sura":50,"aya":5,"text":"不然，他们得到真理的时候，已然不信它了。他们处在不定的事务中。","translator":"王静斋"},{"sura":50,"aya":6,"text":"他们岂未观察上面的天。我怎样地创建它，装饰它。在它无有些须罅缝。","translator":"王静斋"},{"sura":50,"aya":7,"text":"我伸展了地，我把定山置在它上，我在那里生长各类美丽的，","translator":"王静斋"},{"sura":50,"aya":8,"text":"冀以引领与劝化一切归向的仆人。","translator":"王静斋"},{"sura":50,"aya":9,"text":"我由上空降下多福的雨；我用它生长园林，与被收割的子粒，","translator":"王静斋"},{"sura":50,"aya":10,"text":"与有迭迭果实的高杆枣树以作众仆的给养。","translator":"王静斋"},{"sura":50,"aya":11,"text":"我以它活既死的城池出观就是这样。","translator":"王静斋"},{"sura":50,"aya":12,"text":"努海的族人、滥斯人、赛母待、","translator":"王静斋"},{"sura":50,"aya":13,"text":"阿代、匪喇傲鲁脱的弟兄、","translator":"王静斋"},{"sura":50,"aya":14,"text":"哀凯人、顿白尔的族人，在他们以前不信服了。统不信服列使。而后我的约实现了。","translator":"王静斋"},{"sura":50,"aya":15,"text":"我因前一次的造化疲乏了吗?不然，他们是在疑惑重新造化中。","translator":"王静斋"},{"sura":50,"aya":16,"text":"我确造化人类，我知道他的心所暗唆的。我比静脉更接近他。","translator":"王静斋"},{"sura":50,"aya":17,"text":"当两个记录的 坐在右边和左边记录的时候，","translator":"王静斋"},{"sura":50,"aya":18,"text":"那人每吐一语，在他跟前就有一个备妥的巡察者。","translator":"王静斋"},{"sura":50,"aya":19,"text":"死的痛苦确实临到了。这是你素所躲避的。","translator":"王静斋"},{"sura":50,"aya":20,"text":"鸣起了号筒、这是预许的日子。","translator":"王静斋"},{"sura":50,"aya":21,"text":"每一个人有一个催促的，见证的，随他到来。","translator":"王静斋"},{"sura":50,"aya":22,"text":"确实你原未注意这事。我给你揭起幔帐了。 今日，你的眼是锐利的。","translator":"王静斋"},{"sura":50,"aya":23,"text":"他的同伴说：这是在我跟前备妥的。","translator":"王静斋"},{"sura":50,"aya":24,"text":"你俩往火狱里投一切顽强的，阻善的，","translator":"王静斋"},{"sura":50,"aya":25,"text":"恶霸的，怀疑的，忘恩者，","translator":"王静斋"},{"sura":50,"aya":26,"text":"那人并同安拉设定另一受拜的。你俩投他于烈刑中。","translator":"王静斋"},{"sura":50,"aya":27,"text":"他的同伴说：（我们的养主啊，我未诱他过分；但是他本在遥远的迷误中。）","translator":"王静斋"},{"sura":50,"aya":28,"text":"主说：（你们不要在我御前起辩论；我已先向你们提出惊吓了。","translator":"王静斋"},{"sura":50,"aya":29,"text":"出我御前的话不受变更。我绝不是迫害众仆的。）","translator":"王静斋"},{"sura":50,"aya":30,"text":"当那一日，我对火狱说：（你容满了吗?）火状说：（还有增加吗？）","translator":"王静斋"},{"sura":50,"aya":31,"text":"天园接近敬慎的人们，就是一切归向谨守的人不远了——","translator":"王静斋"},{"sura":50,"aya":32,"text":"（我说：）这是所许给你们的；","translator":"王静斋"},{"sura":50,"aya":33,"text":"也就是那未见而骇怕慈主，并秉着归向的心到来的人。","translator":"王静斋"},{"sura":50,"aya":34,"text":"（我说：）你们平安着进天园吧！这是永久的日子。","translator":"王静斋"},{"sura":50,"aya":35,"text":"他们在那里享受自所愿望的。在我御前尚有增加。","translator":"王静斋"},{"sura":50,"aya":36,"text":"我在他们以前灭绝了若干代；它们比这般人 实力雄厚，它们穿行在各城池， 可有一个逃避吗?","translator":"王静斋"},{"sura":50,"aya":37,"text":"此中对于那有心或亲临侧耳的人含有一种教戒。","translator":"王静斋"},{"sura":50,"aya":38,"text":"我确在六日间造化了天地万有，并未遭到疲乏。","translator":"王静斋"},{"sura":50,"aya":39,"text":"你忍受他们所说的吧！你当在日升之前，日落之前，以赞你养主称颂清净。","translator":"王静斋"},{"sura":50,"aya":40,"text":"你当在夜间并在拜后颂主清净，","translator":"王静斋"},{"sura":50,"aya":41,"text":"你侧耳静听吧！在晓谕的从近处晓谕的那一日，","translator":"王静斋"},{"sura":50,"aya":42,"text":"就是他们确实听到一声鸣的那一日；这是出现的日子。","translator":"王静斋"},{"sura":50,"aya":43,"text":"的确，我使生使死。归惟归于我——","translator":"王静斋"},{"sura":50,"aya":44,"text":"也就是地从他们上崩裂的那一日，匆忙着。这是在我上容易的集合。","translator":"王静斋"},{"sura":50,"aya":45,"text":"我知道他们所说的。你不是强制他们的。你用古兰劝化那怕我惊吓的人吧！","translator":"王静斋"},{"sura":51,"aya":1,"text":"试思撒播撤播的；","translator":"王静斋"},{"sura":51,"aya":2,"text":"负重的；","translator":"王静斋"},{"sura":51,"aya":3,"text":"易驶的；","translator":"王静斋"},{"sura":51,"aya":4,"text":"分配事情的；","translator":"王静斋"},{"sura":51,"aya":5,"text":"所许给你们的一定确实，","translator":"王静斋"},{"sura":51,"aya":6,"text":"还报一定要有。","translator":"王静斋"},{"sura":51,"aya":7,"text":"试思有轨道的天；","translator":"王静斋"},{"sura":51,"aya":8,"text":"你们确是在不同的言语中。","translator":"王静斋"},{"sura":51,"aya":9,"text":"自那个上移转该受移转的人。","translator":"王静斋"},{"sura":51,"aya":10,"text":"一般在茫昧中昏愤的谎言者应遭谴责，","translator":"王静斋"},{"sura":51,"aya":11,"text":"一般在茫昧中昏愤的谎言者应遭谴责，","translator":"王静斋"},{"sura":51,"aya":12,"text":"他们问：（报日是在何时?）","translator":"王静斋"},{"sura":51,"aya":13,"text":"当他们在火上受磨难的那一日，","translator":"王静斋"},{"sura":51,"aya":14,"text":"（我说：）你们尝试刑罚吧！这是你们曾经要求速现的。","translator":"王静斋"},{"sura":51,"aya":15,"text":"敬慎的人必在花园和源泉里，","translator":"王静斋"},{"sura":51,"aya":16,"text":"接受他们养主所赐的着。从前他们实是为善的人。","translator":"王静斋"},{"sura":51,"aya":17,"text":"他们在夜间少眠，","translator":"王静斋"},{"sura":51,"aya":18,"text":"他们在黎明时祈恕。","translator":"王静斋"},{"sura":51,"aya":19,"text":"他们的资财中有为祈求和不肯祈求的人分定的。","translator":"王静斋"},{"sura":51,"aya":20,"text":"地和你们自身上对于坚信的人们含有种种表征。","translator":"王静斋"},{"sura":51,"aya":21,"text":"你们怎不观察呢?","translator":"王静斋"},{"sura":51,"aya":22,"text":"天上有你们的给养。和所许给你们的。","translator":"王静斋"},{"sura":51,"aya":23,"text":"指着调养天地的主为誓：那一定是确实的；就跟你们的说话一样。","translator":"王静斋"},{"sura":51,"aya":24,"text":"易卜拉欣的贵客的消息达至你了吗?","translator":"王静斋"},{"sura":51,"aya":25,"text":"那时候，他们来至他，（向他）道安，他也道安！非常的一伙。","translator":"王静斋"},{"sura":51,"aya":26,"text":"他就转回自己家去，拿来肥牛犊；","translator":"王静斋"},{"sura":51,"aya":27,"text":"把它摆在他们跟前。他说：（你们为什么不吃?）","translator":"王静斋"},{"sura":51,"aya":28,"text":"他对他们心怀一种骇怕。他们说：（你不要骇怕。）他们以一个有知识的童子给他报喜信。","translator":"王静斋"},{"sura":51,"aya":29,"text":"他的妻呼喊着奔来，自批其颊着，说：（我是一个未曾产生过的老妪。）","translator":"王静斋"},{"sura":51,"aya":30,"text":"他们说：（你的养主就是这样地说。他实是明哲的，深知的。）","translator":"王静斋"},{"sura":51,"aya":31,"text":"易卜拉欣说：（众使者！你们是什么事情?）","translator":"王静斋"},{"sura":51,"aya":32,"text":"他们说：（我们被差至一伙罪民；","translator":"王静斋"},{"sura":51,"aya":33,"text":"只为向他们驱泥石。","translator":"王静斋"},{"sura":51,"aya":34,"text":"在你养主御前已经因为一般妄为的人盖过印子的。","translator":"王静斋"},{"sura":51,"aya":35,"text":"我们放出那地方的穆民，","translator":"王静斋"},{"sura":51,"aya":36,"text":"而致在那里不见一处非穆思林的住户。","translator":"王静斋"},{"sura":51,"aya":37,"text":"我们在那里为着一般骇怕痛刑的人遗下了踪迹。）","translator":"王静斋"},{"sura":51,"aya":38,"text":"并在母撒中；那时候，我借着显著的明证把它遣至匪喇傲。","translator":"王静斋"},{"sura":51,"aya":39,"text":"但是他依自己的势力拒绝了。且说：（母撒）是个行邪术的，或是有疯病的。","translator":"王静斋"},{"sura":51,"aya":40,"text":"而后我惩治他和他的军兵，我把他们弃在深海中，他是该受谴责的。","translator":"王静斋"},{"sura":51,"aya":41,"text":"并在阿代中；那时候，我向他们驱无益的风。","translator":"王静斋"},{"sura":51,"aya":42,"text":"那风所经过的不论什么东西，无一不被弃若枯物。","translator":"王静斋"},{"sura":51,"aya":43,"text":"并在赛母待中；那时候，有人对他们说：（你们享受直到一个时期吧！）","translator":"王静斋"},{"sura":51,"aya":44,"text":"而后，他们违背养主的命令，他们眼看着遭遇一声呜。","translator":"王静斋"},{"sura":51,"aya":45,"text":"他们未能站起；未能相互自助。","translator":"王静斋"},{"sura":51,"aya":46,"text":"并有从前努海的族人，他们实是恶民。","translator":"王静斋"},{"sura":51,"aya":47,"text":"天，我用力量建筑它了，我实是有能力的。","translator":"王静斋"},{"sura":51,"aya":48,"text":"地，我铺展它了。","translator":"王静斋"},{"sura":51,"aya":49,"text":"铺展者，太好了！我就着每一物类造化两性，冀使你们受教。","translator":"王静斋"},{"sura":51,"aya":50,"text":"你们逃至安拉吧！我实是由主上（差来）公开警告你们的。","translator":"王静斋"},{"sura":51,"aya":51,"text":"你们不要并同安拉设定另一个受拜的。我实是由主上（差来）公开警告你们的。","translator":"王静斋"},{"sura":51,"aya":52,"text":"就是这样，每有一使者抵达先前的众人，他们就说：（[他]是个行邪术的，或是疯子。）","translator":"王静斋"},{"sura":51,"aya":53,"text":"他们以这个相嘱告了吗?不然，他们是一伙犯罪的人。","translator":"王静斋"},{"sura":51,"aya":54,"text":"你回避他们吧！你绝不是该受谴责的。","translator":"王静斋"},{"sura":51,"aya":55,"text":"你当劝化！劝化确利于众穆民。","translator":"王静斋"},{"sura":51,"aya":56,"text":"我造化镇呢与人类只是终至给我为奴。","translator":"王静斋"},{"sura":51,"aya":57,"text":"我不向他们讨给养，我不要他们款待我，","translator":"王静斋"},{"sura":51,"aya":58,"text":"安拉实是赐予给养的，有力的，坚强的。","translator":"王静斋"},{"sura":51,"aya":59,"text":"背义的人们遭受一分罪刑，就像他们同伴的一分罪刑。他们不要向我忙讨。","translator":"王静斋"},{"sura":51,"aya":60,"text":"逆徒在惊吓过他们的那一天遭祸了！","translator":"王静斋"},{"sura":52,"aya":1,"text":"试思土尔；","translator":"王静斋"},{"sura":52,"aya":2,"text":"及展开的纸上","translator":"王静斋"},{"sura":52,"aya":3,"text":"写定的法令，","translator":"王静斋"},{"sura":52,"aya":4,"text":"兴盛的殿宇；","translator":"王静斋"},{"sura":52,"aya":5,"text":"高耸的屋顶，","translator":"王静斋"},{"sura":52,"aya":6,"text":"与满溢的海，","translator":"王静斋"},{"sura":52,"aya":7,"text":"的确，你养主的罪刑一定是应有的，","translator":"王静斋"},{"sura":52,"aya":8,"text":"无有抵抗它的。","translator":"王静斋"},{"sura":52,"aya":9,"text":"在天动","translator":"王静斋"},{"sura":52,"aya":10,"text":"山摇的那一日，","translator":"王静斋"},{"sura":52,"aya":11,"text":"是日一般嬉戏在妄谈中的不信者遭祸了：","translator":"王静斋"},{"sura":52,"aya":12,"text":".","translator":"王静斋"},{"sura":52,"aya":13,"text":"在向火狱的火上拖他们一拖的那一日，","translator":"王静斋"},{"sura":52,"aya":14,"text":"这是你们曾经不信的火狱。","translator":"王静斋"},{"sura":52,"aya":15,"text":"这是邪术吗? 或是你们看不见呢?","translator":"王静斋"},{"sura":52,"aya":16,"text":"你们进它吧！无论你们忍与不忍，是于你们一样的。只依你们所作的施以还报。","translator":"王静斋"},{"sura":52,"aya":17,"text":"的确，敬慎的人是在园林与恩惠中，","translator":"王静斋"},{"sura":52,"aya":18,"text":"借着养主所赐的享受福祉。养主庇护他们不遭火狱的罪刑，","translator":"王静斋"},{"sura":52,"aya":19,"text":"有的对他们说：（你们借着那以往所作的畅然着吃饮吧！）","translator":"王静斋"},{"sura":52,"aya":20,"text":"他们靠在排列的床上。我以目宽面白的女子给他们作配偶。","translator":"王静斋"},{"sura":52,"aya":21,"text":"那归信并他们的子孙随着归信的人，我就教他们的子孙联合他们；我不减去他们的些微善功。每一个人是依自己的作为被质的。","translator":"王静斋"},{"sura":52,"aya":22,"text":"我以他们所想望的鲜果与肉食增添给他们。","translator":"王静斋"},{"sura":52,"aya":23,"text":"他们在那里轮斟无妄谈无罪恶的酒杯。","translator":"王静斋"},{"sura":52,"aya":24,"text":"有些童子往还至他们，像是隐伏的珍珠。","translator":"王静斋"},{"sura":52,"aya":25,"text":"他们彼此相向互问，","translator":"王静斋"},{"sura":52,"aya":26,"text":"说：（从前我们在自己家里却是畏惧的，","translator":"王静斋"},{"sura":52,"aya":27,"text":"而后安拉对我们施恩了，他庇佑我们脱去热毒风的刑罚。","translator":"王静斋"},{"sura":52,"aya":28,"text":"从前我们曾呼求他了，他实是施恩的，特慈的。）","translator":"王静斋"},{"sura":52,"aya":29,"text":"你当劝化！你不是借着养主的恩典作占卜的，也不是疯狂的。","translator":"王静斋"},{"sura":52,"aya":30,"text":"不然，他们说：（他是诗人，我们等侯他一死）吗?","translator":"王静斋"},{"sura":52,"aya":31,"text":"你说：（你们等侯吧！我实是随你们等侯的）不然，他们的理智以此命令他们吗?","translator":"王静斋"},{"sura":52,"aya":32,"text":"不然，他们是一伙叛民。","translator":"王静斋"},{"sura":52,"aya":33,"text":"或是他们说：（他造作了它）吗?不然，乃是他们不信。","translator":"王静斋"},{"sura":52,"aya":34,"text":"如果他们的所言属实了，就任他们现出和它相似的话来，","translator":"王静斋"},{"sura":52,"aya":35,"text":"不然，他们是无物造成的吗?或是他们是自造的呢?","translator":"王静斋"},{"sura":52,"aya":36,"text":"不然，他们造化天地了吗?实是他们不坚信。","translator":"王静斋"},{"sura":52,"aya":37,"text":"不然，，他们掌你养主的慈库吗：不然，他们是强胜的吗?","translator":"王静斋"},{"sura":52,"aya":38,"text":"或是他们有梯子，藉它闻听吗?让他们（听得的人）举出显著的明证来：","translator":"王静斋"},{"sura":52,"aya":39,"text":"不然，他有女儿，你们有男儿吗?","translator":"王静斋"},{"sura":52,"aya":40,"text":"不然，你向他们讨工资。他们是由担负上受重压的吗?","translator":"王静斋"},{"sura":52,"aya":41,"text":"不然，他们知目不能见的而自己书写吗?","translator":"王静斋"},{"sura":52,"aya":42,"text":"不然，他们欲施诡计吗? 不信的人均是被诡计所祸的。","translator":"王静斋"},{"sura":52,"aya":43,"text":"不然，他们有安拉以外的主吗?安拉清净，不染他们所举的匹偶：","translator":"王静斋"},{"sura":52,"aya":44,"text":"他们若看见一块天落下了，就说：（这是累起的云）","translator":"王静斋"},{"sura":52,"aya":45,"text":"你放任他们，直到他们遇见晕倒的那一天，","translator":"王静斋"},{"sura":52,"aya":46,"text":"当那一日，他们的诡计不能代为避免一事，且无有相助他们的。","translator":"王静斋"},{"sura":52,"aya":47,"text":"的确，背义的人们遭此以外的刑罚。但是他们的多半不知道。","translator":"王静斋"},{"sura":52,"aya":48,"text":"你安心守你养主的判断吧！你确在我的相助之下。 你站起的时候，就当以赞你养主称颂清净！","translator":"王静斋"},{"sura":52,"aya":49,"text":"你在夜半并在群星落下以后赞他清净吧！","translator":"王静斋"},{"sura":53,"aya":1,"text":"试思星辰当其落下时；","translator":"王静斋"},{"sura":53,"aya":2,"text":"你们的同人未迷路，他也未错误。","translator":"王静斋"},{"sura":53,"aya":3,"text":"他不私自发言。","translator":"王静斋"},{"sura":53,"aya":4,"text":"这只是启示他的命令。","translator":"王静斋"},{"sura":53,"aya":5,"text":"强健的，见解充足的（天使）教给他了。","translator":"王静斋"},{"sura":53,"aya":6,"text":".","translator":"王静斋"},{"sura":53,"aya":7,"text":"那天使在最高的境界端立着。","translator":"王静斋"},{"sura":53,"aya":8,"text":"随后他接近而下来；","translator":"王静斋"},{"sura":53,"aya":9,"text":"它是两弓的距离，或是更近的。","translator":"王静斋"},{"sura":53,"aya":10,"text":"他把那自所告谕的告谕主的仆人了。","translator":"王静斋"},{"sura":53,"aya":11,"text":"心对于所见的未曾造伪。","translator":"王静斋"},{"sura":53,"aya":12,"text":"你们对他所看见的向他辩论吗？","translator":"王静斋"},{"sura":53,"aya":13,"text":"他另一次在西得赖摸台哈一旁看见他了。","translator":"王静斋"},{"sura":53,"aya":14,"text":".","translator":"王静斋"},{"sura":53,"aya":15,"text":"在那地方有居住的园林。","translator":"王静斋"},{"sura":53,"aya":16,"text":"遮覆的 遮覆西得赖的时候，","translator":"王静斋"},{"sura":53,"aya":17,"text":"目未斜视，也未过当。","translator":"王静斋"},{"sura":53,"aya":18,"text":"他确看见养主的绝大象征了。","translator":"王静斋"},{"sura":53,"aya":19,"text":"你们曾见俩台、运札","translator":"王静斋"},{"sura":53,"aya":20,"text":"和末后第三埋哪台了吗?","translator":"王静斋"},{"sura":53,"aya":21,"text":"你们有男儿，惟他有女儿吗?","translator":"王静斋"},{"sura":53,"aya":22,"text":"果然是这样，那却是一种不公平的分配。","translator":"王静斋"},{"sura":53,"aya":23,"text":"这不过是你们和你们列祖所呼的名称，安拉并未对此降一种明证。他们不过是追随揣度，与私心所偏的。出自养主的指导确已达至他们了。","translator":"王静斋"},{"sura":53,"aya":24,"text":"人可得到自所希望的吗?","translator":"王静斋"},{"sura":53,"aya":25,"text":"后世与今世惟属安拉执掌。","translator":"王静斋"},{"sura":53,"aya":26,"text":"天上许多天使的告赦，不能避免一事；除非是安拉意欲（放赦）并他爱喜的人，在经过他的许可以后。","translator":"王静斋"},{"sura":53,"aya":27,"text":"的确，不信后世的人，像称女儿似的称道天使。","translator":"王静斋"},{"sura":53,"aya":28,"text":"他们本不知道这个。只不过是追随揣度；揣度绝不能替代一些真的。","translator":"王静斋"},{"sura":53,"aya":29,"text":"你远避那拒绝我的教戒，只讨今生的人。","translator":"王静斋"},{"sura":53,"aya":30,"text":"这是他们知识的极处——你养主是至知那错过其道的；他是至知得正道的。","translator":"王静斋"},{"sura":53,"aya":31,"text":"天地间所有的惟安拉执掌。终而他对恶人依其所作的施以还报。他以善赏报偿一般为善的。","translator":"王静斋"},{"sura":53,"aya":32,"text":"那般人远离小罪以外种种大罪与丑恶。你养主是宽恕的。他从地上造化你们的时候，你们是在母腹中的胎儿时候，他是深知你们的。你们不要自称清净。他至知敬慎的人。","translator":"王静斋"},{"sura":53,"aya":33,"text":"你曾看见那引退，给出微须的，留下余剩的人吗?","translator":"王静斋"},{"sura":53,"aya":34,"text":".","translator":"王静斋"},{"sura":53,"aya":35,"text":"在他知道目不能见的而亲见了吗?","translator":"王静斋"},{"sura":53,"aya":36,"text":"不然岂未向他告诉母撒","translator":"王静斋"},{"sura":53,"aya":37,"text":"和那全约的易卜拉欣的册籍中所记的吗?","translator":"王静斋"},{"sura":53,"aya":38,"text":"负重担的人不负别人的重担，","translator":"王静斋"},{"sura":53,"aya":39,"text":"人类惟获自所奔驰的；","translator":"王静斋"},{"sura":53,"aya":40,"text":"他的奔驰，不久就要见到。","translator":"王静斋"},{"sura":53,"aya":41,"text":"而后报他以充分的报酬；","translator":"王静斋"},{"sura":53,"aya":42,"text":"归惟归到你的养主。","translator":"王静斋"},{"sura":53,"aya":43,"text":"他致笑致哭。","translator":"王静斋"},{"sura":53,"aya":44,"text":"他使死使生。","translator":"王静斋"},{"sura":53,"aya":45,"text":"他造化两性——男女","translator":"王静斋"},{"sura":53,"aya":46,"text":"于外溢的一点精水。","translator":"王静斋"},{"sura":53,"aya":47,"text":"另一次造化当然在他。","translator":"王静斋"},{"sura":53,"aya":48,"text":"他致富，且使满足。","translator":"王静斋"},{"sura":53,"aya":49,"text":"他是调养天狼星的主，","translator":"王静斋"},{"sura":53,"aya":50,"text":"他灭绝了前辈的阿代，","translator":"王静斋"},{"sura":53,"aya":51,"text":"赛母待，并未留下；","translator":"王静斋"},{"sura":53,"aya":52,"text":"又有从前努海的族人，他们确系至背义至悖谬的，","translator":"王静斋"},{"sura":53,"aya":53,"text":"他陷落了被颠覆的。","translator":"王静斋"},{"sura":53,"aya":54,"text":"遮覆的已经遮覆它了。","translator":"王静斋"},{"sura":53,"aya":55,"text":"你对于养主的哪样恩典怀疑呢?","translator":"王静斋"},{"sura":53,"aya":56,"text":"这是前代警告的人一类的警告者。","translator":"王静斋"},{"sura":53,"aya":57,"text":"复生日将近了。","translator":"王静斋"},{"sura":53,"aya":58,"text":"除去安拉无一揭穿它的。","translator":"王静斋"},{"sura":53,"aya":59,"text":"你们对于这话奇异，","translator":"王静斋"},{"sura":53,"aya":60,"text":"笑而不哭，","translator":"王静斋"},{"sura":53,"aya":61,"text":"均在嬉戏着吗?","translator":"王静斋"},{"sura":53,"aya":62,"text":"你们当要叩拜安拉！","translator":"王静斋"},{"sura":54,"aya":1,"text":"复生日切近了，月分两开了，","translator":"王静斋"},{"sura":54,"aya":2,"text":"他们若看见一项异迹，便就退避，且说是一种有力的邪术。","translator":"王静斋"},{"sura":54,"aya":3,"text":"他们不信实，而追随自己的私见。凡事是有决定的。","translator":"王静斋"},{"sura":54,"aya":4,"text":"含有禁忌的种种消息，就是充分的智能，确已达至他们了，","translator":"王静斋"},{"sura":54,"aya":5,"text":"但是各项警告无济于事。你远避他们吧！","translator":"王静斋"},{"sura":54,"aya":6,"text":"在唤导者唤至可憎的事情的那一日，","translator":"王静斋"},{"sura":54,"aya":7,"text":"他们目现卑贱着从墓穴里出来，恰像他们是散漫的蝗虫，","translator":"王静斋"},{"sura":54,"aya":8,"text":"急奔至唤导者。不信的人说：（这是艰苦的日子。）","translator":"王静斋"},{"sura":54,"aya":9,"text":"在他们以前努海的族人不信服了，他们不信我的仆人，说是一个疯子，他曾遭受呵责了。","translator":"王静斋"},{"sura":54,"aya":10,"text":"随后，他呼其养主：（我确是失败的了。求你施以报复吧！）","translator":"王静斋"},{"sura":54,"aya":11,"text":"而后我以多倾的水开放了天门；","translator":"王静斋"},{"sura":54,"aya":12,"text":"我使地涌出源泉；而后水依着预定的事情接触了。","translator":"王静斋"},{"sura":54,"aya":13,"text":"我教他乘上有板钉的船，","translator":"王静斋"},{"sura":54,"aya":14,"text":"那船借着我的护佑行驶。这是报复那不信他的人。","translator":"王静斋"},{"sura":54,"aya":15,"text":"我确遗下这表征了。可有一个受劝的人吗?","translator":"王静斋"},{"sura":54,"aya":16,"text":"我的惩罚与警告如何?","translator":"王静斋"},{"sura":54,"aya":17,"text":"我确因为教戒使古兰为容易的。可有一个受劝的人吗？","translator":"王静斋"},{"sura":54,"aya":18,"text":"阿代，不曾信服了，我的刑罚与警告如何?","translator":"王静斋"},{"sura":54,"aya":19,"text":"我曾在极端不幸的日子， 往他们上驱暴风了，","translator":"王静斋"},{"sura":54,"aya":20,"text":"那风拔去众人，直像是根绝的枣树身。","translator":"王静斋"},{"sura":54,"aya":21,"text":"我的刑罚与警告如何?","translator":"王静斋"},{"sura":54,"aya":22,"text":"我确因为教戒使古兰为容易的。可有一个受劝的人吗?","translator":"王静斋"},{"sura":54,"aya":23,"text":"赛母待不信服警告的人了；","translator":"王静斋"},{"sura":54,"aya":24,"text":"他们说：（我们能肯追随同类的一个人吗?果然是那样，我们定是在迷误与疯狂中。","translator":"王静斋"},{"sura":54,"aya":25,"text":"可于我们之中把（教戒）投给他吗?","translator":"王静斋"},{"sura":54,"aya":26,"text":"不然，他是一个撒谎自矜的。）明日，他们就知道谁是撒谎自矜的。","translator":"王静斋"},{"sura":54,"aya":27,"text":"我确撒放母驼。所因试证他们。你等侯他们吧?你当忍耐。","translator":"王静斋"},{"sura":54,"aya":28,"text":"你告诉他们：（水是在他们之间分配过的。每一分水是该被亲临的。）","translator":"王静斋"},{"sura":54,"aya":29,"text":"但是，他们唤来自己的同人，他就伸手损害了。","translator":"王静斋"},{"sura":54,"aya":30,"text":"我的刑罚与警告如何?","translator":"王静斋"},{"sura":54,"aya":31,"text":"我确向他们发出一声鸣，他们遂就化若造栏人的枯木。","translator":"王静斋"},{"sura":54,"aya":32,"text":"我因为教戒使古兰为容易的，可有一个受劝的人吗?","translator":"王静斋"},{"sura":54,"aya":33,"text":"鲁脱的族人不信服警告的人了。","translator":"王静斋"},{"sura":54,"aya":34,"text":"我确向他们驱砂石，鲁脱的眷属在外；我在黎明时解救他们了。","translator":"王静斋"},{"sura":54,"aya":35,"text":"出我御前的恩典。我就是这样地善赏感念的人。","translator":"王静斋"},{"sura":54,"aya":36,"text":"他把我的刑罚警告他们了，然而他们对警告的人起争辩。","translator":"王静斋"},{"sura":54,"aya":37,"text":"他们向他诱骗他的宾客。而后我涂抹他们的眼了，（说：）你们尝我的刑罚与警告吧！","translator":"王静斋"},{"sura":54,"aya":38,"text":"一定的刑罚在早晨降至他们了。","translator":"王静斋"},{"sura":54,"aya":39,"text":"你们尝我的刑罚与警告吧：","translator":"王静斋"},{"sura":54,"aya":40,"text":"我确因为劝戒使古兰为容易的，可有一个受劝的人吗?","translator":"王静斋"},{"sura":54,"aya":41,"text":"警告的人确曾来至匪喇傲的党徒了。","translator":"王静斋"},{"sura":54,"aya":42,"text":"他们不信服我全部的异迹。而后我惩治他们，乃是优胜大能的主的惩治。","translator":"王静斋"},{"sura":54,"aya":43,"text":"你们的逆徒 强过这伙人吗?或是古籍上载定你们获得赦免呢?","translator":"王静斋"},{"sura":54,"aya":44,"text":"不然，他们自称：我们是报复的一伙吗?","translator":"王静斋"},{"sura":54,"aya":45,"text":"这伙人即将败退，转过背去，","translator":"王静斋"},{"sura":54,"aya":46,"text":"不然，复生日是对他们约定的时候。复生是患难极大的最苦的。","translator":"王静斋"},{"sura":54,"aya":47,"text":"为恶的人们确是在迷误与狂妄中。","translator":"王静斋"},{"sura":54,"aya":48,"text":"当他们颠倒着被扯在火中的时候，（我说：）你们尝试火狱的接触吧！","translator":"王静斋"},{"sura":54,"aya":49,"text":"凡事确是依着前定造成的。","translator":"王静斋"},{"sura":54,"aya":50,"text":"我的命令，不过是一句话，如同一转瞬。","translator":"王静斋"},{"sura":54,"aya":51,"text":"我确灭绝你们的同伙了；可有一个受劝的人吗?","translator":"王静斋"},{"sura":54,"aya":52,"text":"他们所行的一切事，载在文卷中。","translator":"王静斋"},{"sura":54,"aya":53,"text":"一切小的大的，均被写定。","translator":"王静斋"},{"sura":54,"aya":54,"text":"敬慎的人们确是在园林与河下；","translator":"王静斋"},{"sura":54,"aya":55,"text":"是在可欣的坐处，在大能的掌权的真主御前。","translator":"王静斋"},{"sura":55,"aya":1,"text":"慈主","translator":"王静斋"},{"sura":55,"aya":2,"text":"教授了古兰。","translator":"王静斋"},{"sura":55,"aya":3,"text":"他造化了人类，","translator":"王静斋"},{"sura":55,"aya":4,"text":"教授他语言。","translator":"王静斋"},{"sura":55,"aya":5,"text":"日月依循计算。","translator":"王静斋"},{"sura":55,"aya":6,"text":"无干与有干的田苗皆顺命。","translator":"王静斋"},{"sura":55,"aya":7,"text":"他升起了天，他设置了天秤，","translator":"王静斋"},{"sura":55,"aya":8,"text":"为的是你们于天秤上不至违法。","translator":"王静斋"},{"sura":55,"aya":9,"text":"你们当秉公设置称衡。你们不要减少天秤。","translator":"王静斋"},{"sura":55,"aya":10,"text":"他为世人铺展了地。","translator":"王静斋"},{"sura":55,"aya":11,"text":"地上有鲜果子，与有茎衣的枣实，","translator":"王静斋"},{"sura":55,"aya":12,"text":"有叶的谷类，与香草。","translator":"王静斋"},{"sura":55,"aya":13,"text":"你们两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":14,"text":"他由像瓦器的干泥上造化了人类，","translator":"王静斋"},{"sura":55,"aya":15,"text":"他从无烟的纯火上造化了迦呢。","translator":"王静斋"},{"sura":55,"aya":16,"text":"你们不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":17,"text":"调养两东的主，调养两西的主。","translator":"王静斋"},{"sura":55,"aya":18,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":19,"text":"他任凭两海彼此交会。","translator":"王静斋"},{"sura":55,"aya":20,"text":"一个阻隔介于其间，不相侵犯。","translator":"王静斋"},{"sura":55,"aya":21,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":22,"text":"珍珠珊瑚出自两海。","translator":"王静斋"},{"sura":55,"aya":23,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":24,"text":"海中像大山似的张帆的船只惟他执掌。","translator":"王静斋"},{"sura":55,"aya":25,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":26,"text":"地上所有的均将朽坏。","translator":"王静斋"},{"sura":55,"aya":27,"text":"你养主有庄严与尊威的本然长存。","translator":"王静斋"},{"sura":55,"aya":28,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":29,"text":"天地间所有的全都求他，每日他是处在事务中。","translator":"王静斋"},{"sura":55,"aya":30,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":31,"text":"两大群众啊！我即将向你们清算。","translator":"王静斋"},{"sura":55,"aya":32,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":33,"text":"镇呢与人类大众啊！如果你们能逃出天地的境界了，你们就逃出去！你们非借着一种力量则不能逃出。","translator":"王静斋"},{"sura":55,"aya":34,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":35,"text":"火焰与烟倾在你们上，你们不能抵抗。","translator":"王静斋"},{"sura":55,"aya":36,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":37,"text":"在天崩遂就变如红皮似的蔷薇的时候，……。","translator":"王静斋"},{"sura":55,"aya":38,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":39,"text":"当那一日，不问人类与边怩的罪恶。","translator":"王静斋"},{"sura":55,"aya":40,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":41,"text":"可借着形迹认识一切为恶的人，而被抓住额发与双足。","translator":"王静斋"},{"sura":55,"aya":42,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":43,"text":"这是为恶的人们所不信的火狱。","translator":"王静斋"},{"sura":55,"aya":44,"text":"他们往来在那火与极热的滚水间。","translator":"王静斋"},{"sura":55,"aya":45,"text":"你两伙不言养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":46,"text":"骇怕站在养主御前的人可获两园。","translator":"王静斋"},{"sura":55,"aya":47,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":48,"text":"两座稠密的园林。","translator":"王静斋"},{"sura":55,"aya":49,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":50,"text":"两园里有两道流泉。","translator":"王静斋"},{"sura":55,"aya":51,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":52,"text":"那两园里每种鲜果有两类。","translator":"王静斋"},{"sura":55,"aya":53,"text":"你两伙不信服养土的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":54,"text":"他们靠在衬以粗缎的铺垫上。两园的果实是垂近的。","translator":"王静斋"},{"sura":55,"aya":55,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":56,"text":"宫殿里有一般不妄视的女子；在他们以前无有人类与边呢和她们接触。","translator":"王静斋"},{"sura":55,"aya":57,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":58,"text":"她们好像是红玉与珊瑚。","translator":"王静斋"},{"sura":55,"aya":59,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":60,"text":"善的报酬能不是善吗?","translator":"王静斋"},{"sura":55,"aya":61,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":62,"text":"二园以外尚有二园。","translator":"王静斋"},{"sura":55,"aya":63,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":64,"text":"两座绿茵茵的园林。","translator":"王静斋"},{"sura":55,"aya":65,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":66,"text":"两园里有汹涌的二泉。","translator":"王静斋"},{"sura":55,"aya":67,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":68,"text":"园中有些美丽上选的女子。","translator":"王静斋"},{"sura":55,"aya":69,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":70,"text":"二园中有鲜果子、枣树、石榴。","translator":"王静斋"},{"sura":55,"aya":71,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":72,"text":"一般隐居帐幕面白的女子。","translator":"王静斋"},{"sura":55,"aya":73,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":74,"text":"他们以前无有人类与迦呢和她们接触。","translator":"王静斋"},{"sura":55,"aya":75,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":76,"text":"他们倚靠在绿垫褥与精美的大毡上。","translator":"王静斋"},{"sura":55,"aya":77,"text":"你两伙不信服养主的哪样恩典?","translator":"王静斋"},{"sura":55,"aya":78,"text":"你的有庄严与尊威的养主之名太玄高了！","translator":"王静斋"},{"sura":56,"aya":1,"text":"当难免的事情 发现的时候，","translator":"王静斋"},{"sura":56,"aya":2,"text":"在它遇到之际、无有不信的。","translator":"王静斋"},{"sura":56,"aya":3,"text":"降低的，抬高的，","translator":"王静斋"},{"sura":56,"aya":4,"text":"在地震动，","translator":"王静斋"},{"sura":56,"aya":5,"text":"群山粉碎，","translator":"王静斋"},{"sura":56,"aya":6,"text":"而成飞扬的尘土的时候，……。","translator":"王静斋"},{"sura":56,"aya":7,"text":"你们分为三级；右边的人，","translator":"王静斋"},{"sura":56,"aya":8,"text":"右边人是什么?左边的人，","translator":"王静斋"},{"sura":56,"aya":9,"text":"左边人是什么?","translator":"王静斋"},{"sura":56,"aya":10,"text":"争先的人们是争先的。","translator":"王静斋"},{"sura":56,"aya":11,"text":"这般人是接近的，","translator":"王静斋"},{"sura":56,"aya":12,"text":"居乐园的。","translator":"王静斋"},{"sura":56,"aya":13,"text":"大部分前人。","translator":"王静斋"},{"sura":56,"aya":14,"text":"少数的后人；","translator":"王静斋"},{"sura":56,"aya":15,"text":"是在被编的床榻上。","translator":"王静斋"},{"sura":56,"aya":16,"text":"彼此相向着靠在那里。","translator":"王静斋"},{"sura":56,"aya":17,"text":"常少的童男持着盏觞，与满盛清酒的杯，往返至他们。","translator":"王静斋"},{"sura":56,"aya":18,"text":".","translator":"王静斋"},{"sura":56,"aya":19,"text":"他们不因那酒感觉头痛，也不昏晕。","translator":"王静斋"},{"sura":56,"aya":20,"text":"更有他们所选的鲜果，","translator":"王静斋"},{"sura":56,"aya":21,"text":"和他们想望的禽肉。","translator":"王静斋"},{"sura":56,"aya":22,"text":"更有宽目面白的女子，","translator":"王静斋"},{"sura":56,"aya":23,"text":"如同是隐伏的珠子。","translator":"王静斋"},{"sura":56,"aya":24,"text":"依他们所作的施以报酬。","translator":"王静斋"},{"sura":56,"aya":25,"text":"他们在那里听不到妄言与罪恶；","translator":"王静斋"},{"sura":56,"aya":26,"text":"又有说（平安，平安！）","translator":"王静斋"},{"sura":56,"aya":27,"text":"右边的人，右边人是什么?","translator":"王静斋"},{"sura":56,"aya":28,"text":"是在无刺的「席代雷」","translator":"王静斋"},{"sura":56,"aya":29,"text":"紧密的香蕉，","translator":"王静斋"},{"sura":56,"aya":30,"text":"常时的阴影，","translator":"王静斋"},{"sura":56,"aya":31,"text":"倾流的水，","translator":"王静斋"},{"sura":56,"aya":32,"text":"并有许多无尽无阻的鲜果，","translator":"王静斋"},{"sura":56,"aya":33,"text":".","translator":"王静斋"},{"sura":56,"aya":34,"text":"与高贵的铺垫。","translator":"王静斋"},{"sura":56,"aya":35,"text":"我确为了右边的人创造她们，切实地创造了。并把她们造成年令相等可爱的处女。","translator":"王静斋"},{"sura":56,"aya":36,"text":".","translator":"王静斋"},{"sura":56,"aya":37,"text":".","translator":"王静斋"},{"sura":56,"aya":38,"text":".","translator":"王静斋"},{"sura":56,"aya":39,"text":"是大部分前人，","translator":"王静斋"},{"sura":56,"aya":40,"text":"也是大部分后人。","translator":"王静斋"},{"sura":56,"aya":41,"text":"左边的人；左边人是什么?","translator":"王静斋"},{"sura":56,"aya":42,"text":"是在毒风滚水，","translator":"王静斋"},{"sura":56,"aya":43,"text":"与不凉不爽的黑烟影下。","translator":"王静斋"},{"sura":56,"aya":44,"text":".","translator":"王静斋"},{"sura":56,"aya":45,"text":"的确，在那以前他们是享受的；","translator":"王静斋"},{"sura":56,"aya":46,"text":"他们常干大罪，","translator":"王静斋"},{"sura":56,"aya":47,"text":"且说我们死去，已化为土与骨的时候，定被复活吗?","translator":"王静斋"},{"sura":56,"aya":48,"text":"我们的前代列祖也是这样吗?）","translator":"王静斋"},{"sura":56,"aya":49,"text":"你说：（前人后人均被集至一定的日期。）","translator":"王静斋"},{"sura":56,"aya":50,"text":".","translator":"王静斋"},{"sura":56,"aya":51,"text":"一般迷误不信的人哪！","translator":"王静斋"},{"sura":56,"aya":52,"text":"而后你们是必就着「凿骨未」树吃，","translator":"王静斋"},{"sura":56,"aya":53,"text":"而用它满腹的。","translator":"王静斋"},{"sura":56,"aya":54,"text":"又在那上饮滚水，","translator":"王静斋"},{"sura":56,"aya":55,"text":"而饮如渴驼饮水的。","translator":"王静斋"},{"sura":56,"aya":56,"text":"这就是他们在还报日的路筵。","translator":"王静斋"},{"sura":56,"aya":57,"text":"我造化了你们，你们为何不信?","translator":"王静斋"},{"sura":56,"aya":58,"text":"你们曾见自所倾溢的吗?","translator":"王静斋"},{"sura":56,"aya":59,"text":"可是你们造化它?或是我造化的呢?","translator":"王静斋"},{"sura":56,"aya":60,"text":"我在你们之中预定了死亡。","translator":"王静斋"},{"sura":56,"aya":61,"text":"我不是不能改换和你们一样的，并造你们于自所不知的之中。","translator":"王静斋"},{"sura":56,"aya":62,"text":"你们确知道初次的造化了。你们怎不受劝呢?","translator":"王静斋"},{"sura":56,"aya":63,"text":"你们曾见自所耕种的吗?","translator":"王静斋"},{"sura":56,"aya":64,"text":"你们耕种它?或是我耕种的呢?","translator":"王静斋"},{"sura":56,"aya":65,"text":"设我意欲——了，我就把它化为枯禾，你们感觉奇异，","translator":"王静斋"},{"sura":56,"aya":66,"text":"（说：）我们实是蒙受损失的了。","translator":"王静斋"},{"sura":56,"aya":67,"text":"不然，我们是无分的了。","translator":"王静斋"},{"sura":56,"aya":68,"text":"你们曾见自所饮用的水吗?","translator":"王静斋"},{"sura":56,"aya":69,"text":"你们由云中降下它吗?或是我降的呢?","translator":"王静斋"},{"sura":56,"aya":70,"text":"设我意欲——了，我就把它变为咸的。你们怎不感念呢?","translator":"王静斋"},{"sura":56,"aya":71,"text":"你们曾见自所钻取的火吗?","translator":"王静斋"},{"sura":56,"aya":72,"text":"你们造化了它的树，或是我造的呢?我造化了它，","translator":"王静斋"},{"sura":56,"aya":73,"text":"只为提醒，并利于一般旅客。","translator":"王静斋"},{"sura":56,"aya":74,"text":"你以尊大养主的尊名称颂清净吧！","translator":"王静斋"},{"sura":56,"aya":75,"text":"我指着群星的没落为誓！","translator":"王静斋"},{"sura":56,"aya":76,"text":"这确是一个重大的盟誓，假若你们知道。","translator":"王静斋"},{"sura":56,"aya":77,"text":"那确是在秘籍中高贵的古兰","translator":"王静斋"},{"sura":56,"aya":78,"text":".","translator":"王静斋"},{"sura":56,"aya":79,"text":"惟有一般清洁的接触它。","translator":"王静斋"},{"sura":56,"aya":80,"text":"降自调养众世界底真主的。","translator":"王静斋"},{"sura":56,"aya":81,"text":"你们轻慢这话吗?","translator":"王静斋"},{"sura":56,"aya":82,"text":"你们以不信置作（感念）自己的给养吗?","translator":"王静斋"},{"sura":56,"aya":83,"text":"在[鲁哈]达至食嗓的时候，为什么不呢?","translator":"王静斋"},{"sura":56,"aya":84,"text":"那时候，你们亲眼得见；","translator":"王静斋"},{"sura":56,"aya":85,"text":"我是比你们还接近他的，但是你们看不见。","translator":"王静斋"},{"sura":56,"aya":86,"text":"若是你们不至受还报，","translator":"王静斋"},{"sura":56,"aya":87,"text":"你们的所言如果属实，你们为什么不令它退转。","translator":"王静斋"},{"sura":56,"aya":88,"text":"那人若是接近的了，","translator":"王静斋"},{"sura":56,"aya":89,"text":"可获欣悦，鲜花与乐园。","translator":"王静斋"},{"sura":56,"aya":90,"text":"若果他是右边人了，","translator":"王静斋"},{"sura":56,"aya":91,"text":"右边的人就向你祝安。","translator":"王静斋"},{"sura":56,"aya":92,"text":"若果他是迷误不信的了，","translator":"王静斋"},{"sura":56,"aya":93,"text":"就以滚水作路筵，","translator":"王静斋"},{"sura":56,"aya":94,"text":"并进入火狱。","translator":"王静斋"},{"sura":56,"aya":95,"text":"这是确切实在的。","translator":"王静斋"},{"sura":56,"aya":96,"text":"你当以你尊大养主的名字称颂清净！","translator":"王静斋"},{"sura":57,"aya":1,"text":"天地间的一切颂安拉清净。他是优胜的，明哲的。","translator":"王静斋"},{"sura":57,"aya":2,"text":"天地的权柄惟他执掌，他使生使死，他是能于万事的。","translator":"王静斋"},{"sura":57,"aya":3,"text":"他是太初的，至终的，显的，隐的。","translator":"王静斋"},{"sura":57,"aya":4,"text":"他是深知万事的。他是在六日造化了天地而后操纵[阿勒世]的。他知道入地的，出地的，与降自天的，升上天的。你们处在无论哪里他是和你们同在的。安拉看见你们所作的。","translator":"王静斋"},{"sura":57,"aya":5,"text":"天地的权柄惟他执掌。凡事惟被归到安拉上。","translator":"王静斋"},{"sura":57,"aya":6,"text":"他纳夜于昼，纳昼于夜。他深知胸中所怀的。","translator":"王静斋"},{"sura":57,"aya":7,"text":"你们当归信安拉和他的使者。你们当就着他教你们所继承的作使用！你们之中归信并且使用的人，他们可获大赏。","translator":"王静斋"},{"sura":57,"aya":8,"text":"你们为什么不信安拉?使者正在唤你们归信养主着。真主确已取得你们的约了。如果你们是归信的，……。","translator":"王静斋"},{"sura":57,"aya":9,"text":"他那真主，把显著的明证降给自己的仆人，为的是使你们由黑暗里出至光明。安拉确是怜悯特慈你们的。","translator":"王静斋"},{"sura":57,"aya":10,"text":"你们为什么不施用在安拉的道上。天地的遗业惟安拉执掌。你们之中在克复之前使用，而且格杀的人，并不相等。这般人在阶级上，比那在此以后使用而且格杀的人们更伟大。安拉把上赏！许给每一个人了。安拉尽知你们的作为。","translator":"王静斋"},{"sura":57,"aya":11,"text":"什么人贷予安拉，好自贷予，而得他加倍赐给并获得佳赏?","translator":"王静斋"},{"sura":57,"aya":12,"text":"在你看见众男女穆民，其光射于前面与右面的那一日，（我说：）今日你们的喜言是诸河流于其下的天园，永居在那里，这是大成功。","translator":"王静斋"},{"sura":57,"aya":13,"text":"当那一日，男女伪信士对众穆民说：（你们看顾我们，我们就借你们的光！）有的说：（你们转回后面，寻光去吧！）既而在他们之间打上一堵有门的墙。墙内是慈惠，墙外是刑罚。","translator":"王静斋"},{"sura":57,"aya":14,"text":"伪信士们呼喊众穆民：（我们原未和你们同处吗?）他们说：（是的，但是你们自祸己身了，你们等待了，你们怀疑了。你们曾被妄想所欺，直到安拉的命令已至。欺骗的借着安拉欺骗你们了。","translator":"王静斋"},{"sura":57,"aya":15,"text":"今日，不接受你们的，也不接受众逆徒的赎资。你们的归处是火狱。那是于你们最相宜的。归处太恶了！）","translator":"王静斋"},{"sura":57,"aya":16,"text":"归信的人，其心因着记念安拉及他所降的真理而敬畏，这在他们尚非其时吗? 他们不要像那从前一般蒙赐经典的人，时间长久而他们心硬了。他们许多的人是作恶的。","translator":"王静斋"},{"sura":57,"aya":17,"text":"你们当知安拉活地于既死以后，我为你们解明种种表征，好教你们了解。","translator":"王静斋"},{"sura":57,"aya":18,"text":"的确，男女施济者，他们贷予安拉好自贷予；将加倍地赐给他们，他们可获佳赏。","translator":"王静斋"},{"sura":57,"aya":19,"text":"归信安拉和他的列使的人，均是诚实的。在他们养主御前作见证的。他们获得自己的报酬与光辉。隐昧并否信我表征的人，均是居火狱的。","translator":"王静斋"},{"sura":57,"aya":20,"text":"你们当知道今生不过是玩乐，荒嬉，与装饰，彼此间的夸耀，对于资财子女上竞多。其情好像大雨的情形，它的田苗使得农家欣喜，而后枯干了，你看它变成黄的，终而化为碎屑。在后世有烈刑，并有安拉的饶恕与喜爱。今生不过是欺人的家私。","translator":"王静斋"},{"sura":57,"aya":21,"text":"你们当奔向养主的饶恕，与那宽如天地之宽的天园。那为归信安拉和他使者的人已经备妥了。这是安拉的特恩。他赐给自所意欲——的人。安拉是有大恩的。","translator":"王静斋"},{"sura":57,"aya":22,"text":"地上和你们自身上所遭的患难，在我造它以前，无一不在记录着。这是在安拉上容易的。","translator":"王静斋"},{"sura":57,"aya":23,"text":"（我告诉这话，）以免你们对于所失去的有所忧虑；并以他赐给你们的表现狂喜。安拉不爱一切操妄自矜的。","translator":"王静斋"},{"sura":57,"aya":24,"text":"那些人们自己吝啬，且命人吝啬。退转的人，安拉确是无求的，受赞的。","translator":"王静斋"},{"sura":57,"aya":25,"text":"我曾以种种明证差遣我的列使，我随同他们降下经典与天秤，以便世人执行公道。我曾发现了铁，内中含有暴力，并有对世人的种种利益；（为利于他们）并为安拉知道，那暗中相助他，和他列使的人，安拉实是大力的，优胜的。","translator":"王静斋"},{"sura":57,"aya":26,"text":"我确差去了努海与易卜拉欣；我把圣职，与经典置在他俩的子孙中。而后，他们之间有守正道的。他们许多人是作恶的。","translator":"王静斋"},{"sura":57,"aya":27,"text":"我在他们以后续遣我的列使。我续遣了马尔焉的子尔撒，我赐给他引支勒，我在一般追随他的人心里输入怜悯与慈爱。他们创行出家。我原未对他们规定这事。不过是因讨安拉的喜；但是他们守它，并未切实地守它。而后，我赐给他们其中有信心的人报酬。他们的许多人是作恶的。","translator":"王静斋"},{"sura":57,"aya":28,"text":"众归信的人哪！你们当畏惧安拉，并信他的使者。果然，他就赐给你们两份慈惠，并赐给你们光亮，藉它步行；更要饶恕你们。安拉是多恕的，特慈的。","translator":"王静斋"},{"sura":57,"aya":29,"text":"（真主告诉你们这事）为的是有经的人知道他们不能于安拉的些微特恩；并知道特恩是在安拉的手里。他把它赐给自所意欲……的人，安拉是有大恩的。","translator":"王静斋"},{"sura":58,"aya":1,"text":"安拉确已听取因着本夫对你起辩论，并向安拉诉苦的那一女子的说话了。安拉本听得你俩的答辩。安拉实是能听的，能见的。","translator":"王静斋"},{"sura":58,"aya":2,"text":"你们之中把妻比作母背的人，她们并不是他们的母；他们的母只是那生育他们的人。他们确说出不可理解的话与谎言。安拉实是放赦的，多恕的。","translator":"王静斋"},{"sura":58,"aya":3,"text":"比妻为母背，复又收回所说的，其人应在彼此相触以前解放一仆。这是用它劝阻你们。安拉尽知你们的作为。","translator":"王静斋"},{"sura":58,"aya":4,"text":"得不到的人，自应在彼此相触以前接连着封两个月的斋。不能作到的人，就款待六十个贫人。这是好教你们信安拉和他的使者。这是安拉的律例，不信的人应遭痛刑。","translator":"王静斋"},{"sura":58,"aya":5,"text":"抵抗安拉和他的使者的人，他们受挫折。就像以前的人们受挫折。我确降下了显明的表征。不信的人应遭辱刑。","translator":"王静斋"},{"sura":58,"aya":6,"text":"当那一日，安拉把他们一同复起，而宣告他们所作过的。安拉数算它，他们亡心却它了——安拉是见证万事的…","translator":"王静斋"},{"sura":58,"aya":7,"text":"你岂未看见安拉知道在天在地的吗?有三个人秘密接谈，主就是他们的第四者；有五个人，他便是他们的第六者。无论比这较少较多的，他们处在不论哪里，主是与他们同在的。而后当复生日，他对他们宣告他们所作过的。安拉实是深知万事的。","translator":"王静斋"},{"sura":58,"aya":8,"text":"你未看见吗?有些人本已禁止他们秘密接谈，而后他们复蹈禁忌。他们在罪恶、仇视、与违犯使者上、秘密接谈。他们来至你的时候，就用安拉未曾用那庆祝过你的来庆祝你。他们心里说：（安拉为什么不因我们所说的，罪刑我们呢?）火狱使他们满足了，他们将要进它，归处太恶了，","translator":"王静斋"},{"sura":58,"aya":9,"text":"众归信的人哪！你们彼此密谈的时候，不要以罪恶、仇视、与违犯使者，秘密接谈；你们要以好义与虔敬相密谈。你们当敬畏安拉——将被归返于他的那个主。","translator":"王静斋"},{"sura":58,"aya":10,"text":"密谈惟出自恶魔。为的是教归信的人们忧愁。除非奉到安拉的命令，他绝不能妨碍他们一事。当令众穆民惟独仰赖安拉。","translator":"王静斋"},{"sura":58,"aya":11,"text":"众归信的人哪！有人在席面上对你们说：（闪开，）的时候一你们就应闪开！（若然）安拉就为你们展开。有人说：你们起席的时候，你们就起席，（若然）安拉就升高你们其中一般归信的人，与蒙赐知识的人种种品级。安拉尽知你们所作的。","translator":"王静斋"},{"sura":58,"aya":12,"text":"众归信的人哪！你们对使者密谈的时候，要在密谈以前，先作施济。这是于你们至好的，至清洁的。你们若得不到了，安拉实是多恕的，特慈的。","translator":"王静斋"},{"sura":58,"aya":13,"text":"你们骇怕在密谈以前先作施济吗? 在你们未肯实作，安拉已允你们悔罪的时候，你们就当立拜功，施天课；并当顺从安拉和他的使者。安拉尽知你们的作为。","translator":"王静斋"},{"sura":58,"aya":14,"text":"你岂未见遭受安拉怒恨的那一伙人吗? 他们不属你们一类，也不属他们一类，他们明知着发伪誓。","translator":"王静斋"},{"sura":58,"aya":15,"text":"安拉已为他们预备烈刑。他们所作过的实在太恶了！","translator":"王静斋"},{"sura":58,"aya":16,"text":"他们以矢誓当作遮牌。他们阻碍安拉之道，应遭辱刑。","translator":"王静斋"},{"sura":58,"aya":17,"text":"他们的资财子女不能代其避免安拉的一事。这般人是永居火狱的。","translator":"王静斋"},{"sura":58,"aya":18,"text":"在安拉把他们一同复起的那一日，他们对主发誓，就如对你们发誓。他们自以为处在一事上。须知他们确是说谎的人。","translator":"王静斋"},{"sura":58,"aya":19,"text":"恶魔压制他们了。它教他们忘却记念安拉。这般人是恶魔一伙的。须知恶魔的同伙就是伤折的，","translator":"王静斋"},{"sura":58,"aya":20,"text":"的确，抵抗安拉和他的使者的人，均在最卑贱的人一列。","translator":"王静斋"},{"sura":58,"aya":21,"text":"安拉判定：我和我的列使必定获胜。安拉实是大力的。优胜的。","translator":"王静斋"},{"sura":58,"aya":22,"text":"你得不到归信安拉与末日的一伙人，结交那抵抗安拉与他的使者的人；纵令那般人是他们的父、或子、或弟兄、或近族。这般人，真主已把正信写在他们心中；并以自己的「鲁哈」扶助他们。他将教他们进入诸河流于其下的天园，永居在那里。安拉喜他们，他们也喜他。这般人是安拉一伙的。须知安拉一伙的人，就是得到所欲的。","translator":"王静斋"},{"sura":59,"aya":1,"text":"在天的在地的，颂安拉清净。他是优胜的，明哲的。","translator":"王静斋"},{"sura":59,"aya":2,"text":"他那真主，在初次聚集的时候，驱逐有经人中的不信者出离自己的住宅。 你们不以为他们出去，他们尝以为自己的堡垒能抗安拉而加护卫的。 而后，安拉自他们意想不到的地方到达了。他把恐惧投在他们心中，他们以自己的手，并以众穆民的手捣毁自己的住室。明鉴的人哪！你们应当有所儆诫。","translator":"王静斋"},{"sura":59,"aya":3,"text":"若不是安拉对他们决定放逐了，他必在今世惩治他们，他们在后世应遭火刑。","translator":"王静斋"},{"sura":59,"aya":4,"text":"这是由于他们反抗安拉和他的使者。反抗安拉的人，安拉一定是有烈刑的。","translator":"王静斋"},{"sura":59,"aya":5,"text":"你们所伐的或是任它生长的枣树，乃奉安拉的命令。为的是侮辱作恶的人，","translator":"王静斋"},{"sura":59,"aya":6,"text":"他们的所有，曾经安拉归给他的使者的，你们原未向那上催马催驼。但是安拉使自己的列使压倒自所意欲——的人。安拉是全能于万事的。","translator":"王静斋"},{"sura":59,"aya":7,"text":"各城人的所有，曾经安拉归给自己使者的，可归安拉，归使者，归近戚、孤儿、贫人、旅客，以免它周流在你们的富人之间，使者给你们的，你们就接受它；他禁止过的，你们就停止。你们当畏惧安拉。安拉实是有烈刑的——","translator":"王静斋"},{"sura":59,"aya":8,"text":"应给一般迁居的贫人，就是被逐出离住宅与财产的人。他们讨安拉的特恩与喜爱，他们相助安拉和他的使者。这般人均是诚实的。","translator":"王静斋"},{"sura":59,"aya":9,"text":"并给一般先于他们住在都会，（实行）归信的人。他们爱喜那向他们迁来的人。 他们心中对于所赠给它们的，不感觉需求。纵然他们自身奇穷，而情愿克己让人。凡是得到庇佑，心不悭吝的人，均是得意的。","translator":"王静斋"},{"sura":59,"aya":10,"text":"在他们以后到达的人说：（养主啊！你饶恕我们，并饶恕那先我们得到信仰的弟兄。你莫把怀恨众穆民置在我们心中。养主啊！你实是怜悯的，特慈的。","translator":"王静斋"},{"sura":59,"aya":11,"text":"你岂未看见一般伪信士对有经人中背逆的弟兄们说：（如果你们被人逐出了，我们必随你们出去，且为着你们永不顺从任何一个人。若是有人对你们格斗了，我们一定协助你们。）安拉见证它们一定是谎言的。","translator":"王静斋"},{"sura":59,"aya":12,"text":"如果他们被人逐出了，它们不随同他们出去。若是有人与他们格斗了，它们也不予以协助。如果它们协助了，便就掉过背去，不复得到相助。","translator":"王静斋"},{"sura":59,"aya":13,"text":"你们在它们心中实比安拉更是可怕的。这是由于它们为不解悟的一伙人。","translator":"王静斋"},{"sura":59,"aya":14,"text":"它们除去（潜伏）在有堡垒的城乡或由墙后以外，则不一同对你们格杀，它们彼此间的斗争是激烈的。你揣度它们是团结的，它们的心本是涣散的。这是由于它们为不了解的一伙人。","translator":"王静斋"},{"sura":59,"aya":15,"text":"有如新近在它们以前那般人的情形。它们尝到自己事情的恶果了，它们应遭痛刑。","translator":"王静斋"},{"sura":59,"aya":16,"text":"犹如恶魔的情形；先时他语人作背逆的事。及至那人作下背逆事了，他就说：（我实是与你无干的；我实骇怕安拉——调养众世界的主。）","translator":"王静斋"},{"sura":59,"aya":17,"text":"结果，是彼此永居火狱中。这是还报背义的人。","translator":"王静斋"},{"sura":59,"aya":18,"text":"众归信的人哪！你们当畏惧安拉！人当参看那为着明天往前送的。你们当畏惧安拉！安拉实尽知你们的作为。","translator":"王静斋"},{"sura":59,"aya":19,"text":"你们不要类似一般忘记安拉，而被忘其自身的人们。这般人均是作恶的。","translator":"王静斋"},{"sura":59,"aya":20,"text":"火狱的人和天园的人不一样。天园的人均是得到所欲的。","translator":"王静斋"},{"sura":59,"aya":21,"text":"设若我把这古兰降给山。你必看见它是惧怕的；是因惧怕安拉崩碎的。这些情形，我对世人表明它，好教他们参悟。","translator":"王静斋"},{"sura":59,"aya":22,"text":"他是安拉，那除他以外再无有主的。知道目不能见的，与显见的；他是普慈的，特慈的主。","translator":"王静斋"},{"sura":59,"aya":23,"text":"他是安拉，除他以外再无有主。掌权的，清洁的，和平的，赐安宁的，维护的，优胜的，无敌的，至尊的。颂安拉清净，不染他们所拟的匹偶。","translator":"王静斋"},{"sura":59,"aya":24,"text":"他是安拉，造化的，创始的，赐形的。惟他具有至优的尊名。天地间所有的惟颂他清净：他是优胜的，明哲的。","translator":"王静斋"},{"sura":60,"aya":1,"text":"众归信的人哪！你们不要以我的仇敌，也是你们的仇敌当作友人，至以情感向他们投递（消息）。他们本不信你们得到的真理。他们逐出使者和你们，只因你们归信安拉；调养你们的主。如果你们因为在我的道上奋斗，并因为讨我的喜爱出去了，（则不得与他们结友。）你们因着感情向他们暗通（消息。）我是深知你们所隐所显的。你们其中行这事的人，他确失迷正路了。","translator":"王静斋"},{"sura":60,"aya":2,"text":"他们若遇到你们了，便就是你们的仇敌，而伸开手、舌损害你们。他们盼望你们不信。","translator":"王静斋"},{"sura":60,"aya":3,"text":"在复生日你们的血亲与子女绝不利于你们。真主将在你们中间判断。安拉看得见你们的作为。","translator":"王静斋"},{"sura":60,"aya":4,"text":"你们于易卜拉欣和随从他的人可获一种好的楷模。那时候，他们对自己的族人说：（我们是于你们并于你们撇开安拉所拜者无干的。我们不承认你们。仇恨永远发现在我们彼此之间，直到你们归信独一的安拉为止。）除非易卜拉欣对他父说：（我即将为你求恕——我不能为你掌管安拉的一事。）养主啊！我们惟仰赖你，惟归向你，惟返回你。","translator":"王静斋"},{"sura":60,"aya":5,"text":"养主啊！你勿使逆徒磨难我们，你饶恕我们吧！养主啊！你是优胜的，明哲的。","translator":"王静斋"},{"sura":60,"aya":6,"text":"的确，你们就是盼望安拉与末日的人，可于他们上获一种好的楷模。背转的人，安拉实是无求的，受赞的。","translator":"王静斋"},{"sura":60,"aya":7,"text":"安拉将要在你们和你们所仇视的那般人彼此间发生感情。安拉是全能的，安拉是多恕的，特慈的。","translator":"王静斋"},{"sura":60,"aya":8,"text":"一般未曾因为教门对你们格斗，也未把你们逐出住宅的人，安拉不禁止你们优遇他们，与公平对待。安拉实喜爱主持公道的人。","translator":"王静斋"},{"sura":60,"aya":9,"text":"安拉只禁止你们结交一般因为教门对你们格斗，把你们逐出住宅，并为驱逐你们相互臂助的人。凡对他们发生友爱的人，均是背义的。","translator":"王静斋"},{"sura":60,"aya":10,"text":"众归信的人哪！一般穆民女子迁来至你们的时候，你们要考查她们，安拉至知她们的信仰。如果你们知道她们有真的信仰了，你们就不可让她们返回逆徒去。她们于他们不合法，他们也于她们不合法。你们当付给他们聘仪！你们付过她们聘仪的时候，不妨与她们结婚。你们不要保持逆妇的婚约。你们索自所使用的。让他们也索回自所使用的。这是安拉的判断。他在你们中间判断。安拉是深知的，明哲的。","translator":"王静斋"},{"sura":60,"aya":11,"text":"若是你们的一个妻室失掉，投归逆徒，而后你们施行报复了，你们就照付给失妻的人所费用的。你们当畏惧自所归信的安拉。","translator":"王静斋"},{"sura":60,"aya":12,"text":"圣人哪！一旦穆民女子来对你矢诚：（不给安拉举一物为匹偶，不偷窃，不行奸，不残杀自己的子女，不以私生子假冒本夫的骨血，对于好事上不违悖你，）你就该对她们矢诚，并为她们祈安拉饶恕。安拉实是多恕的，特慈的。","translator":"王静斋"},{"sura":60,"aya":13,"text":"众归信的人哪！你们不要和那遭安拉怒恼的一伙人发生友爱。那般人对于后世上绝望，就如住墓穴的逆徒们绝望了。","translator":"王静斋"},{"sura":61,"aya":1,"text":"在天的在地的颂安拉清净，他是优胜的，明哲的。","translator":"王静斋"},{"sura":61,"aya":2,"text":"众归信的人哪！你们为什么说自己不肯作的。","translator":"王静斋"},{"sura":61,"aya":3,"text":"你们说自己不作的，这事在安拉视为极其可怒。","translator":"王静斋"},{"sura":61,"aya":4,"text":"安拉实爱喜像被砌的墙似的列班着在他的道上格斗的人。","translator":"王静斋"},{"sura":61,"aya":5,"text":"昔时，母撒对自己的族人说：（我的族人哪！你们确知道我是安拉向你们差来的，你们为什么扰害我呢?）当他们偏斜的时候，安拉就使他们的心偏斜了。安拉不引导恶民。","translator":"王静斋"},{"sura":61,"aya":6,"text":"昔时，马尔焉的子尔撤说：（以斯拉衣来的子孙哪！我确是安拉向你们差来的；证实我以前的讨喇忒，并以在我以后来的名为阿罕默德的使者报喜信着。）及至他 把种种明证给他们的时候，他们就说：（这是显明的邪术。）","translator":"王静斋"},{"sura":61,"aya":7,"text":"遇人唤至伊斯兰而往安拉上造作谎言，谁是比这类人更为背义的。安拉不引导逆民。","translator":"王静斋"},{"sura":61,"aya":8,"text":"他们意欲用口熄灭安拉的光。安拉是完成自己光辉的，纵令逆徒们憎恶。","translator":"王静斋"},{"sura":61,"aya":9,"text":"他是用南针与正教遣其使者，为的是使它超越各教的，纵令信偶像的人们憎恶。","translator":"王静斋"},{"sura":61,"aya":10,"text":"众归信的人哪！我指示一种能救你们脱离痛刑的贸易，好吗?","translator":"王静斋"},{"sura":61,"aya":11,"text":"（那贸易）是你们信安拉和他的使者；你们用财产生命在安拉之道上奋斗。若是你们晓得了，这是于你们最好的。","translator":"王静斋"},{"sura":61,"aya":12,"text":"他饶恕你们的罪，并教你们进入诸河流于其下的天园，与永居的天园中清洁的居处，这是一大成功。","translator":"王静斋"},{"sura":61,"aya":13,"text":"尚有你们所爱的别一项（恩惠）安拉的相助，切近的胜利。你当给众穆民报喜信。","translator":"王静斋"},{"sura":61,"aya":14,"text":"众归信的人哪！你们应当是相助安拉的，就像马尔焉的子尔撒对门徒们说： （谁是倾向安拉相助我的?）门徒们说：（我们是相助安拉的。）而后，以斯拉衣来的子孙一伙归信， 一伙不信。我援助归信的人，对付他们的仇敌，他们终归是获胜的。","translator":"王静斋"},{"sura":62,"aya":1,"text":"在天的，在地的，颂掌权的、圣洁的、优胜的、明哲的——安拉清净。","translator":"王静斋"},{"sura":62,"aya":2,"text":"他是往一般失教的人里，派去他们一类的使者的。那使者向他们诵他的表征，洁净他们，并教给他们经典与智慧。他们在这以前确是在显著的迷误中。","translator":"王静斋"},{"sura":62,"aya":3,"text":"并有他们之中另一伙尚未联合的。他是优胜的，明哲的。","translator":"王静斋"},{"sura":62,"aya":4,"text":"这是安拉的特恩，他把它赐给自所意欲——的人。安拉是有大恩的。","translator":"王静斋"},{"sura":62,"aya":5,"text":"曾被责成担负讨喇忒而未担负的那般人的情形，如同是驴负册籍的情形。不信安拉表征的那伙人的情形太恶了。安拉不引导逆民。","translator":"王静斋"},{"sura":62,"aya":6,"text":"你说：（奉犹太教的人们哪！若是你们以为自己超过世人是安拉所爱的了，如果你们所言属实，你们就盼望死去。）","translator":"王静斋"},{"sura":62,"aya":7,"text":"（但是）他们由于亲手前送的永不盼望。安拉深知背义的人们。","translator":"王静斋"},{"sura":62,"aya":8,"text":"你说：（的确，你们（意欲）逃避的死亡，是必须遇到你们的；而后你们被返至知目不能见与显见的主；他向你们宣告你们所作过的。）","translator":"王静斋"},{"sura":62,"aya":9,"text":"众归信的人哪！当聚礼日，有人呼唤礼拜的时候，你们就当忙去记念安拉，丢开买卖。如果你们晓得了，这是于你们最好的。","translator":"王静斋"},{"sura":62,"aya":10,"text":"及至拜功完毕的时候，你们就散布在地上，寻求安拉的特恩。你们要多记念安拉，以便达到所欲。","translator":"王静斋"},{"sura":62,"aya":11,"text":"他们看见贸易，或（听到）荒嬉的时候，就向那里纷纷奔去；他们弃下你站立着。你说，（在安拉那里的，强过了荒嬉与贸易。安拉是至上的供给者。）","translator":"王静斋"},{"sura":63,"aya":1,"text":"伪信士们来至你的时侯，他们就说：（我们见证你确是安拉的使者）——安拉本知道你确是他的使者——安拉见证伪信士确是谎言的。","translator":"王静斋"},{"sura":63,"aya":2,"text":"他们以发誓当作遮牌，且阻碍安拉之道。他们所作的实在太恶了！","translator":"王静斋"},{"sura":63,"aya":3,"text":"这是由于他们归信，而又不信；所以他们的心被封闭，而不解悟。","translator":"王静斋"},{"sura":63,"aya":4,"text":"你看见他们的时候，他们的体态使你爱慕。若是他们说话了，你听他们的谈话。 他们好像支墙的木料。他们疑为每次呼喊是对于他们。他们是仇敌，你当防备他们。安拉驱远他们了。他们转变到哪里?","translator":"王静斋"},{"sura":63,"aya":5,"text":"每逢有人说：（你们来呀！若然，安拉的使者为你门求恕，）他们就掉头而去。你看他们高傲着退转。","translator":"王静斋"},{"sura":63,"aya":6,"text":"无论你为他们求恕也罢，不求恕也罢，安拉绝不饶恕他们；安拉绝不引导恶民。","translator":"王静斋"},{"sura":63,"aya":7,"text":"他们说：（你们不要施舍给安拉的使者左右的人，迨至它们散去。）天地的宝库惟安拉执掌。但是伪信士们不解悟。","translator":"王静斋"},{"sura":63,"aya":8,"text":"他们说：（如果我们回到默底纳了，高尚的人，必由那里逐出卑贱的人。）荣耀是属安拉的，和他使者的，与众穆民的。但是伪信士们不知道。","translator":"王静斋"},{"sura":63,"aya":9,"text":"众归信的人哪！你们不要被资财子女所误，而致不顾及记念安拉。凡行这事的人，均是伤折的。","translator":"王静斋"},{"sura":63,"aya":10,"text":"你们当就着我所赐给的作费用，你们任何一个人要得使费我所供给的，莫待死亡来到而说：（我的养主啊！为什么你不教我延至切近的时期，我去作施济，归为清廉的。）","translator":"王静斋"},{"sura":63,"aya":11,"text":"人的死期巳至时，安拉绝不姑容他。安拉尽知你们的作为。","translator":"王静斋"},{"sura":64,"aya":1,"text":"在天的，在地的，颂安拉清净。惟他掌权惟他受赞。他是全能于万事的。","translator":"王静斋"},{"sura":64,"aya":2,"text":"他是造化你们的；而后你们之中有不信的，有归信的。安拉看得见你们的作为。","translator":"王静斋"},{"sura":64,"aya":3,"text":"他因真理造化了天地。他给你们成形，使你们的形态美观。归惟归于他。","translator":"王静斋"},{"sura":64,"aya":4,"text":"他知道天地间所有的，他知道你们所隐的所显的。安拉深知胸中所怀的。","translator":"王静斋"},{"sura":64,"aya":5,"text":"先前不信而后尝试自己的结果，遭受痛刑的那般人的消息，未曾达至你们吗?","translator":"王静斋"},{"sura":64,"aya":6,"text":"这是由于列使把种种异迹现给他们，但是他们说：「人类可能引导我们吗?」他们不信，他们远避了。安拉原无所求，安拉是无求的，受赞的。","translator":"王静斋"},{"sura":64,"aya":7,"text":"不信的人们揣度绝不复活他们。你说：「是的，指着我的养主为誓：将必复活你们，而后向你们宣告你们所作过的。这是在安拉上容易的。」","translator":"王静斋"},{"sura":64,"aya":8,"text":"你们当信安拉和他的使者；并信我颁降的光辉。安拉尽知你们的作为。","translator":"王静斋"},{"sura":64,"aya":9,"text":"真主在集合之日，聚起你们的那一天，那就是互有伤折的日子。信安拉并作善举的人，主就给他遮蔽种种罪恶；并教他进入诸河流于其下的天园，永久居在那里。这是一大成功。","translator":"王静斋"},{"sura":64,"aya":10,"text":"隐昧且不信我表征的人，是永居火狱的，归处太恶了！","translator":"王静斋"},{"sura":64,"aya":11,"text":"患难到来，无一不依循安拉的判定。信安拉的人他就指导他的心。安拉是深知万事的。","translator":"王静斋"},{"sura":64,"aya":12,"text":"你们当顺从安拉，当顺从使者，如果你们不肯了，我的使者只担负公然的宣达，","translator":"王静斋"},{"sura":64,"aya":13,"text":"安拉，除他以外再无有主。当令众穆民惟仰赖安拉！","translator":"王静斋"},{"sura":64,"aya":14,"text":"众归信的人哪！你们的妻子之中有你们的仇敌。你们当防备他们。如果你们放过、宽容、饶恕了，安拉是多恕的，特慈的。","translator":"王静斋"},{"sura":64,"aya":15,"text":"你们的资财子女，就是一种试证。安拉自据有大赏。","translator":"王静斋"},{"sura":64,"aya":16,"text":"你们当尽力地畏惧安拉。你们应当听从，顺服，并为自身使费最优的。内心被庇不染吝啬的，均是得到所欲的。","translator":"王静斋"},{"sura":64,"aya":17,"text":"若是你们贷予安拉，好自贷予了，他就把它加倍地赐给你们，饶恕你们。安拉是颁赏的，大度的。","translator":"王静斋"},{"sura":64,"aya":18,"text":"知道目不能见与显见的，优胜的，明哲的。","translator":"王静斋"},{"sura":65,"aya":1,"text":"圣人哪！你们意欲对妇离婚的时候，要在规定的期限以内离异。 你们要计数规定的期限。你们当畏惧安拉——你们的养主。你们不要教她们出离自己的住室。她们也莫自出，除非是她们发现显明的丑恶时候。这是安拉的定例。越过安拉定例的人，他确自亏己身了。你不知道；也许安拉要在这以后发现一事。","translator":"王静斋"},{"sura":65,"aya":2,"text":"她们达到规定的期限时候，你们要循规留下。或循规离异。你们约出同族中两个公正人作见证，你们当为安拉执行见证。这是用它劝化归信安拉与末日的人。畏惧安拉的人，他就为他发现一个出路，","translator":"王静斋"},{"sura":65,"aya":3,"text":"并由他意想不到的地方赐与他。仰赖安拉的人，他就是使他满足的，安拉是必能成全自己事情的。安拉确为着每项事情规定了限度。","translator":"王静斋"},{"sura":65,"aya":4,"text":"不再希望见月经的女子，如果你们怀疑了，她们的期限是三个月。无月经的，也是这样。妊妇的期限是分娩。畏惧安拉的人，他就使他的事情容易。","translator":"王静斋"},{"sura":65,"aya":5,"text":"这是安拉的命令。他把它降给你们。畏惧安拉的人他就给他遮蔽种种罪恶，增大他的善赏。","translator":"王静斋"},{"sura":65,"aya":6,"text":"你们量力教她们住在自所居住的地方。你们不要为着迫害她们而予以不便。 她们若是有妊的，当给她们费用，待至她们分娩为止。如果她们为你们哺乳了，你们就当给她们酬资。你们彼此间要好自商议。如果你们互相苛酷了，即将由别的妇人乳他。","translator":"王静斋"},{"sura":65,"aya":7,"text":"使有能力的人，本着自己的能力去费用。生活困窘的人，可任他就着安拉所赐的去费用。安拉惟责人担负其所赐给他的。安拉即将在困难以后发现容易。","translator":"王静斋"},{"sura":65,"aya":8,"text":"许多违背养主和他列使命令的城池，我对它严算；我惩它以丑刑，","translator":"王静斋"},{"sura":65,"aya":9,"text":"它尝试自己事情的恶报了。它底事情的结果是伤折。","translator":"王静斋"},{"sura":65,"aya":10,"text":"安拉已为他们预备烈刑。有才识的众穆民哪：你们当畏惧安拉。安拉确向你们降下了教戒——","translator":"王静斋"},{"sura":65,"aya":11,"text":"一个使者 把安拉底显明的表征诵给你们，为的是教归信并作善举的人从黑暗里出至光明。归信安拉并作善举的人，真主将教他进入诸河流于其下的天园，永久居在那里。安拉已为他备好了给养。","translator":"王静斋"},{"sura":65,"aya":12,"text":"安拉造化七天，与和它们相似的地。命令降在它们其间；好教你们知道安拉是能于万事的，并知道安拉确在知识上环绕万物。","translator":"王静斋"},{"sura":66,"aya":1,"text":"圣人哪！为什么你禁忌安拉为你列为合法的，在讨你妻门的喜欢着。安拉是多恕的，特慈的。","translator":"王静斋"},{"sura":66,"aya":2,"text":"安拉已为你们规定解除誓约。安拉是相助你们的。他是深知的，明哲的。","translator":"王静斋"},{"sura":66,"aya":3,"text":"那时候，圣人和他的一部分妻室暗中叙话。及至她宣露那话，安拉把它现给他的时候，他就发布一部分，隐讳一部分。及至他把那话宣露给她的时候，她就说：（谁把这话告诉你了?）他说：（深知尽知的主告诉我了。）","translator":"王静斋"},{"sura":66,"aya":4,"text":"若是你俩向安拉悔罪了，（那是很好的，）因为你俩的心偏了。若是你俩互助地妨害他了，安拉一定是相助他的。哲布来衣勒和清廉的穆民们，与众天使，是在这以后相助 (他）的。","translator":"王静斋"},{"sura":66,"aya":5,"text":"如果他休掉你们了，他的养主，不久就给他改换优越你们的妻室；那就是顺服的、归信的、听命的、忏悔的、功修的、斋戒的、再醮妇与处女。","translator":"王静斋"},{"sura":66,"aya":6,"text":"众归信的人哪！你们当庇护自身与家眷远离火狱。那火狱的燃料是人与石。在那上有些严厉雄健的天使，他们不背安拉的命令，而奉行所吩咐他们的。","translator":"王静斋"},{"sura":66,"aya":7,"text":"众不信的人哪！今日，你们不要托辞。惟依着你们所作的施以还报！","translator":"王静斋"},{"sura":66,"aya":8,"text":"众归信的人哪！你们当向安拉忏悔，真挚地忏悔。你们的养主行将给你们遮蔽罪恶，教你们进那诸河流于其下的天园。当那一日；安拉不侮辱圣人，与随他归信的人。他们的光射在前面与右面，说：（养主啊！你为我们全满我们的光，你饶恕我们吧！你实是全能于万事的。）","translator":"王静斋"},{"sura":66,"aya":9,"text":"圣人哪！你当对不信者与伪信士们奋斗，你当严厉地对待他们。他们的归处是火狱。居所太恶了。","translator":"王静斋"},{"sura":66,"aya":10,"text":"安拉因为众不信者发了一个比喻。努海的妇人与鲁脱的妇人；他俩原是在我仆人中的两个义仆手下。但是她俩对他二人背信了，他二人未给她俩避免安拉的一事。有的说：（你俩追随进的人一同进火狱吧！）","translator":"王静斋"},{"sura":66,"aya":11,"text":"安拉因为众归信的人发了一个比喻。匪喇傲的妇人；那时候她说：（养主啊！求你在天园里为我建筑一处接近你的住室。求你解救我脱离匪喇傲与他的行为。求你解救我脱离了逆民。）","translator":"王静斋"},{"sura":66,"aya":12,"text":"更有尔母兰的女儿——马尔焉，那一操守的女子。我借着自己的（鲁哈）吹在她里，她信其养主的话，和他的经典。她本是顺命一类的。","translator":"王静斋"},{"sura":67,"aya":1,"text":"那掌权的主太尊大了；他是全能于万事的。","translator":"王静斋"},{"sura":67,"aya":2,"text":"那真主，造化了死与生；只为试证你们的哪一个在作为上是至好的。他是优胜的，多恕的。","translator":"王静斋"},{"sura":67,"aya":3,"text":"那真主造化了七重天。你在慈主的造化上看不出一些参差来。你再看，你可看出些微罅缝吗?","translator":"王静斋"},{"sura":67,"aya":4,"text":"你再连次地回视；目就卑贱疲倦着归转。","translator":"王静斋"},{"sura":67,"aya":5,"text":"我确以明灯装饰了至近的天。我用它作为驱逐众恶魔的。我为它们备下焰刑了。","translator":"王静斋"},{"sura":67,"aya":6,"text":"不信养主的人，遭火狱的罪刑。归所太恶了！","translator":"王静斋"},{"sura":67,"aya":7,"text":"他们被投在火狱的时候，就听得它的驴声；火在沸腾着，","translator":"王静斋"},{"sura":67,"aya":8,"text":"几至因怒而崩裂，每逢往那里投进一伙，管火狱的 就问他们：（未有传警告的人达至你们吗?）","translator":"王静斋"},{"sura":67,"aya":9,"text":"他们说：（是的，警告的人确巳达至我们了，我们未曾信服；）我们说：（安拉来降下一事，你们只是在大迷中。）","translator":"王静斋"},{"sura":67,"aya":10,"text":"他们又说：（设若我们听从或是解悟了，我们未尝是在火狱的人一流。","translator":"王静斋"},{"sura":67,"aya":11,"text":"于是，他们承认自己的罪恶了。火狱的人离远了！","translator":"王静斋"},{"sura":67,"aya":12,"text":"一般尚未眼见而畏惧养主的人，必获饶恕与巨赏。","translator":"王静斋"},{"sura":67,"aya":13,"text":"任你们低言密语，或高声说话。真主实是深知胸中所怀的。","translator":"王静斋"},{"sura":67,"aya":14,"text":"造物主岂能不知?他是妙知的，尽知的。","translator":"王静斋"},{"sura":67,"aya":15,"text":"他那真主为你们造地为易御的，你们步行在它的各边界；你们就着他的供给取食吧！复起惟独归向他。","translator":"王静斋"},{"sura":67,"aya":16,"text":"你们不怕在天的主以地陷下你们，地立刻震动吗?","translator":"王静斋"},{"sura":67,"aya":17,"text":"或是你们不怕在天的主向你们驱砂石吗?你们即将知道我的警告如何?","translator":"王静斋"},{"sura":67,"aya":18,"text":"他们以前的众人确曾不信服了。我的惩罚如何?","translator":"王静斋"},{"sura":67,"aya":19,"text":"他们岂未看见在上面展缩羽翼的飞鸟吗?只有慈主保持他们。主确是看得见万事的。","translator":"王静斋"},{"sura":67,"aya":20,"text":"谁是超过慈主相助你们的军兵? 逆徒们只是在受骗中。","translator":"王静斋"},{"sura":67,"aya":21,"text":"他若停止给养了，谁是那予你们给养的?不然，他们曾深在主傲慢与逃遁中。","translator":"王静斋"},{"sura":67,"aya":22,"text":"那一步一跌的人是至得正道的?或是那在正路上端然行走的人呢?","translator":"王静斋"},{"sura":67,"aya":23,"text":"你说：（他那真主造化了你们，并为你们安置了耳、眼、心，你们绝少感念。）","translator":"王静斋"},{"sura":67,"aya":24,"text":"你说：（他那真主把你们散播在地上，你们将被集至他）","translator":"王静斋"},{"sura":67,"aya":25,"text":"他们说：（这约是在何时呢? 如果你们的所言属实了，……。）","translator":"王静斋"},{"sura":67,"aya":26,"text":"你说：（惟有安拉知道。我只是一个公开传警告的。）","translator":"王静斋"},{"sura":67,"aya":27,"text":"当他们见它切近的时候，逆徒们的面目已竟变黑了。有的说：（这是你们曾经寻求的。）","translator":"王静斋"},{"sura":67,"aya":28,"text":"你说：（你们看出来了吗?如果安拉灭绝我和追随我的人，或他慈惠我们，谁能解救不信的人脱离痛刑。）","translator":"王静斋"},{"sura":67,"aya":29,"text":"你说：（他是慈主；我们归信他了。我们仰赖他了。你们即将知道谁是在显著的迷误中。）","translator":"王静斋"},{"sura":67,"aya":30,"text":"你说：（你们看出来了吗?若是你们的水下渗了，谁把清流水赐给你们?）","translator":"王静斋"},{"sura":68,"aya":1,"text":"（奴尼，）试思「阁兰」与他们所写的；","translator":"王静斋"},{"sura":68,"aya":2,"text":"你绝不是藉你养主底恩典疯狂的。","translator":"王静斋"},{"sura":68,"aya":3,"text":"你必获无间断的巨赏。","translator":"王静斋"},{"sura":68,"aya":4,"text":"你确在伟大的性格上。","translator":"王静斋"},{"sura":68,"aya":5,"text":"你即将看见，他们也将看见：","translator":"王静斋"},{"sura":68,"aya":6,"text":"你们的哪一个是疯子。","translator":"王静斋"},{"sura":68,"aya":7,"text":"你的养主是至知那错过其道的，他是至知得正道的。","translator":"王静斋"},{"sura":68,"aya":8,"text":"你不要顺从一般不信服的。","translator":"王静斋"},{"sura":68,"aya":9,"text":"他们希望你缓和，他们也缓和。","translator":"王静斋"},{"sura":68,"aya":10,"text":"一切好发誓的，","translator":"王静斋"},{"sura":68,"aya":11,"text":"卑鄙的，谤人的，奔走谗言的，","translator":"王静斋"},{"sura":68,"aya":12,"text":"阻碍善事的，过当的，作恶的，","translator":"王静斋"},{"sura":68,"aya":13,"text":"暴虐的，而且是私生的，","translator":"王静斋"},{"sura":68,"aya":14,"text":"你莫因他有资财与子女而去服从他。","translator":"王静斋"},{"sura":68,"aya":15,"text":"有人向他诵我的表征时候，他就说是前人的故事。","translator":"王静斋"},{"sura":68,"aya":16,"text":"我即将在鼻子上给他烙痕迹。","translator":"王静斋"},{"sura":68,"aya":17,"text":"我确试证他们，就像试证一般园主。那时候，他们发誓：（必定在早晨收割它。）","translator":"王静斋"},{"sura":68,"aya":18,"text":"他们并未说；（若是安拉意欲……的时候。）","translator":"王静斋"},{"sura":68,"aya":19,"text":"他们正在睡眠的时候，那园林遭你养主的灾害，","translator":"王静斋"},{"sura":68,"aya":20,"text":"旋就变若被割的。","translator":"王静斋"},{"sura":68,"aya":21,"text":"次晨，他们相互呼喊！","translator":"王静斋"},{"sura":68,"aya":22,"text":"你们若欲收割，赶快去到自己的禾田。","translator":"王静斋"},{"sura":68,"aya":23,"text":"于是他们前往，彼此低声说道：","translator":"王静斋"},{"sura":68,"aya":24,"text":"（今日，你们莫容一个贫人进到园林。）","translator":"王静斋"},{"sura":68,"aya":25,"text":"他们早晨赶到，本是能于制止的。","translator":"王静斋"},{"sura":68,"aya":26,"text":"及至看见那园林了，他们就说：（我们实在错误了，","translator":"王静斋"},{"sura":68,"aya":27,"text":"不然，我们是五分的了。）","translator":"王静斋"},{"sura":68,"aya":28,"text":"他们一个最公正的人说：（我岂未对你们说：你们为何不称颂清净?）","translator":"王静斋"},{"sura":68,"aya":29,"text":"他们说：（颂我们的养主清净！我们实是自亏的了。）","translator":"王静斋"},{"sura":68,"aya":30,"text":"他们彼此对面，互相责备，","translator":"王静斋"},{"sura":68,"aya":31,"text":"说：（哎我们悲苦啊！我们实是违法的人了。","translator":"王静斋"},{"sura":68,"aya":32,"text":"我们的养主或许给我们改换比它更好的，我们是一定仰望养主的。）","translator":"王静斋"},{"sura":68,"aya":33,"text":"罪刑就是这样，后世的罪刑一定是较大的。设若他们知道了，……。","translator":"王静斋"},{"sura":68,"aya":34,"text":"敬慎的人，必在养主御前获乐园。","translator":"王静斋"},{"sura":68,"aya":35,"text":"我把顺服的人置若为恶的人吗?","translator":"王静斋"},{"sura":68,"aya":36,"text":"你们怎的了，你们如何判断的?","translator":"王静斋"},{"sura":68,"aya":37,"text":"不然，你们具有自读的经典，","translator":"王静斋"},{"sura":68,"aya":38,"text":"而于中获得自所选择的吗?","translator":"王静斋"},{"sura":68,"aya":39,"text":"不然，你们必获自所判断的，这是我应负到复生日确定的约吗?","translator":"王静斋"},{"sura":68,"aya":40,"text":"你问他们：（他们的哪一个承担这事?）","translator":"王静斋"},{"sura":68,"aya":41,"text":"不然，他们有些伙伴吗?如果他们的所言属实，就令他们举出自己的伙伴来。","translator":"王静斋"},{"sura":68,"aya":42,"text":"在事态严重，均被唤至叩头的那一日，他们不得能够；","translator":"王静斋"},{"sura":68,"aya":43,"text":"其目骇惧，遭受侮辱着。当他们健康的时候，确有的唤他们叩头了。","translator":"王静斋"},{"sura":68,"aya":44,"text":"你任我对待那不信这话的人。我即将于他们不知道的地位，使他们渐渐地坠落。","translator":"王静斋"},{"sura":68,"aya":45,"text":"我姑容他们，我的谋划实是牢固的。","translator":"王静斋"},{"sura":68,"aya":46,"text":"不然，你向他们求工资，他们是对于例外担当上负重的吗?","translator":"王静斋"},{"sura":68,"aya":47,"text":"不然，他们得到目不能见的，而自行记录吗?","translator":"王静斋"},{"sura":68,"aya":48,"text":"你要安心等侯养主的判断。 你不要像鱼人似的。那时候，他忧闷着呼吁。","translator":"王静斋"},{"sura":68,"aya":49,"text":"设非养主的恩典达至他了，他就遭受谴责着被弃在不毛之地。","translator":"王静斋"},{"sura":68,"aya":50,"text":"而后养主选择他了，他就教他作了义人。","translator":"王静斋"},{"sura":68,"aya":51,"text":"逆徒们听到教戒的时候，几至用目使你滑到。他们说：（这人实是一个疯子。 ）","translator":"王静斋"},{"sura":68,"aya":52,"text":"（其实）它只是劝戒普世界人的。","translator":"王静斋"},{"sura":69,"aya":1,"text":"真实的，","translator":"王静斋"},{"sura":69,"aya":2,"text":"真实的是什么?","translator":"王静斋"},{"sura":69,"aya":3,"text":"你从何知道什么是真实的?","translator":"王静斋"},{"sura":69,"aya":4,"text":"赛母待与阿代不信敲击的了。","translator":"王静斋"},{"sura":69,"aya":5,"text":"赛母待曾被暴声灭绝了。","translator":"王静斋"},{"sura":69,"aya":6,"text":"说到阿代，他们曾被非常的暴风灭绝了。","translator":"王静斋"},{"sura":69,"aya":7,"text":"主用那暴风制服他们，接连的七夜八昼。你见众人倒在那里，好像是空洞的枣树身。","translator":"王静斋"},{"sura":69,"aya":8,"text":"你看他们有一个存在的吗?","translator":"王静斋"},{"sura":69,"aya":9,"text":"匪喇傲和他以前的人，以及被颠覆的，发现罪恶了，","translator":"王静斋"},{"sura":69,"aya":10,"text":"他们违背养主的使者。于是主惩治他们，猛烈地惩治。","translator":"王静斋"},{"sura":69,"aya":11,"text":"的确，在水泛滥的时候，我教你们乘上行舟，","translator":"王静斋"},{"sura":69,"aya":12,"text":"为的是把它作为提醒你们的。并为能记的耳记下它。","translator":"王静斋"},{"sura":69,"aya":13,"text":"当吹起了号筒一吹，","translator":"王静斋"},{"sura":69,"aya":14,"text":"地与群山悬起，彼此一度相撞的时候，","translator":"王静斋"},{"sura":69,"aya":15,"text":"在那一日，不可免的实现，","translator":"王静斋"},{"sura":69,"aya":16,"text":"天已崩裂了。当那一日，天是塌落的。","translator":"王静斋"},{"sura":69,"aya":17,"text":"天使守其各方面。是日，有八个天使在他们上面荷你养主的（阿勒世。）","translator":"王静斋"},{"sura":69,"aya":18,"text":"当那一日，你们统被现出；你们无一事瞒得过。","translator":"王静斋"},{"sura":69,"aya":19,"text":"文卷被交到右手的人，他就说： （你们来诵我的文卷吧！","translator":"王静斋"},{"sura":69,"aya":20,"text":"我曾深信必遇到对我清算。）","translator":"王静斋"},{"sura":69,"aya":21,"text":"而后他是在愉快的生活中","translator":"王静斋"},{"sura":69,"aya":22,"text":"居住高园的。","translator":"王静斋"},{"sura":69,"aya":23,"text":"那园的果子是相近的。","translator":"王静斋"},{"sura":69,"aya":24,"text":"（我说：）任你们以往日送到前面的畅然着吃饮吧！","translator":"王静斋"},{"sura":69,"aya":25,"text":"文卷被交给左手的人，他就说：（哎！宁愿我未接到自己的文卷！","translator":"王静斋"},{"sura":69,"aya":26,"text":"我不知道怎么对我算的?","translator":"王静斋"},{"sura":69,"aya":27,"text":"哎！宁愿一死是解决的。","translator":"王静斋"},{"sura":69,"aya":28,"text":"我的财产未利于我。","translator":"王静斋"},{"sura":69,"aya":29,"text":"我的权势离我消失了。（当时有的说）","translator":"王静斋"},{"sura":69,"aya":30,"text":"你们抓住他，给他戴上枷，","translator":"王静斋"},{"sura":69,"aya":31,"text":"而后令他入火狱！","translator":"王静斋"},{"sura":69,"aya":32,"text":"你们再把他扯进其长度是七十尺的链子里。","translator":"王静斋"},{"sura":69,"aya":33,"text":"的确，他原不信尊大的安拉；","translator":"王静斋"},{"sura":69,"aya":34,"text":"他也不催促款待贫人。","translator":"王静斋"},{"sura":69,"aya":35,"text":"今日，在那里无有他的契友。","translator":"王静斋"},{"sura":69,"aya":36,"text":"除脓水以外，也无有食物。","translator":"王静斋"},{"sura":69,"aya":37,"text":"吃脓水的，只是一般作罪的人。","translator":"王静斋"},{"sura":69,"aya":38,"text":"指着你们看得见的，","translator":"王静斋"},{"sura":69,"aya":39,"text":"与看不见的为誓：","translator":"王静斋"},{"sura":69,"aya":40,"text":"的确，它是一个贵使者的言语；","translator":"王静斋"},{"sura":69,"aya":41,"text":"它绝不是诗人的话，你们绝少信从；","translator":"王静斋"},{"sura":69,"aya":42,"text":"也不是卜者的话，","translator":"王静斋"},{"sura":69,"aya":43,"text":"你们绝少纳劝降自调养众世界的主。","translator":"王静斋"},{"sura":69,"aya":44,"text":"他若往我上造作一部分话了，","translator":"王静斋"},{"sura":69,"aya":45,"text":"我必抓住他的右手，","translator":"王静斋"},{"sura":69,"aya":46,"text":"且必断他的动脉；","translator":"王静斋"},{"sura":69,"aya":47,"text":"你们无一人代他制止。","translator":"王静斋"},{"sura":69,"aya":48,"text":"的确，它是劝戒敬慎者的。","translator":"王静斋"},{"sura":69,"aya":49,"text":"我实知道你们之中有不信服的。","translator":"王静斋"},{"sura":69,"aya":50,"text":"它确是使逆徒们懊丧的。","translator":"王静斋"},{"sura":69,"aya":51,"text":"它是确切真实的。","translator":"王静斋"},{"sura":69,"aya":52,"text":"你借着养主的尊名称颂清净吧！","translator":"王静斋"},{"sura":70,"aya":1,"text":"一个祈求者祈求落到众逆徒上的刑罚；","translator":"王静斋"},{"sura":70,"aya":2,"text":"那刑罚无有一个对于掌阶梯的安拉作抵抗的。","translator":"王静斋"},{"sura":70,"aya":3,"text":".","translator":"王静斋"},{"sura":70,"aya":4,"text":"众天使与[鲁哈]在它的限度是五万年的那一日升高于他。","translator":"王静斋"},{"sura":70,"aya":5,"text":"你要好自忍耐。","translator":"王静斋"},{"sura":70,"aya":6,"text":"他们看它是遥远的；","translator":"王静斋"},{"sura":70,"aya":7,"text":"我看它是切近的。","translator":"王静斋"},{"sura":70,"aya":8,"text":"在那一日，天化若熔铜；","translator":"王静斋"},{"sura":70,"aya":9,"text":"群山像是羊绒。","translator":"王静斋"},{"sura":70,"aya":10,"text":"近戚不问及近戚；教他们彼此相亲。","translator":"王静斋"},{"sura":70,"aya":11,"text":"为恶的人希望用自己的子女，","translator":"王静斋"},{"sura":70,"aya":12,"text":"妻室，弟兄，","translator":"王静斋"},{"sura":70,"aya":13,"text":"与同处居住的族人，","translator":"王静斋"},{"sura":70,"aya":14,"text":"以及地上所有的一切，赎那一日的刑罚，而致使他得救。","translator":"王静斋"},{"sura":70,"aya":15,"text":"不可能。","translator":"王静斋"},{"sura":70,"aya":16,"text":"它实是拔头皮的火陷。","translator":"王静斋"},{"sura":70,"aya":17,"text":"它唤那退转与聚藏的人。","translator":"王静斋"},{"sura":70,"aya":18,"text":".","translator":"王静斋"},{"sura":70,"aya":19,"text":"人类确是天生好敛的。","translator":"王静斋"},{"sura":70,"aya":20,"text":"遇难的时候便就暴躁；","translator":"王静斋"},{"sura":70,"aya":21,"text":"享福的时候，便就吝啬。","translator":"王静斋"},{"sura":70,"aya":22,"text":"除非一般作礼拜的，","translator":"王静斋"},{"sura":70,"aya":23,"text":"就是那时常作礼拜的；","translator":"王静斋"},{"sura":70,"aya":24,"text":"其财产中有为乞求和不肯乞求者规定的义务；","translator":"王静斋"},{"sura":70,"aya":25,"text":".","translator":"王静斋"},{"sura":70,"aya":26,"text":"也是信报日的，","translator":"王静斋"},{"sura":70,"aya":27,"text":"畏惧养主底刑罚的。","translator":"王静斋"},{"sura":70,"aya":28,"text":"的确，他们养主底刑罚，是不可安然的。","translator":"王静斋"},{"sura":70,"aya":29,"text":"也是一般掩护羞体的；","translator":"王静斋"},{"sura":70,"aya":30,"text":"除非对自己妻室或手辖的；他们是不遭责备的。","translator":"王静斋"},{"sura":70,"aya":31,"text":"寻这以外的人，那就是过为的。","translator":"王静斋"},{"sura":70,"aya":32,"text":"又有一般注意寄托物与诺言的；","translator":"王静斋"},{"sura":70,"aya":33,"text":"和一般支持见证的，","translator":"王静斋"},{"sura":70,"aya":34,"text":"谨守拜功的。","translator":"王静斋"},{"sura":70,"aya":35,"text":"这些人们是在天园里获荣耀的。","translator":"王静斋"},{"sura":70,"aya":36,"text":"逆徒们为什么奔驰在你的四周，","translator":"王静斋"},{"sura":70,"aya":37,"text":"集结在右左?","translator":"王静斋"},{"sura":70,"aya":38,"text":"他们每一个人盼望进乐园吗?","translator":"王静斋"},{"sura":70,"aya":39,"text":"不可，我由他们知道的上造化了他们。","translator":"王静斋"},{"sura":70,"aya":40,"text":"指着调养东西各方的主为誓。","translator":"王静斋"},{"sura":70,"aya":41,"text":"我是一定能于改换比他们优越的，我不是失败的。","translator":"王静斋"},{"sura":70,"aya":42,"text":"你任他们深入，玩耍，迨其遇见所许给他们的那一日吧！","translator":"王静斋"},{"sura":70,"aya":43,"text":"当那一日，他们忙从墓穴里出来，好像是奔赴到偶像。","translator":"王静斋"},{"sura":70,"aya":44,"text":"其目畏惧，遭受侮辱着。这就是曾所预许他们的那一天。","translator":"王静斋"},{"sura":71,"aya":1,"text":"的确，我曾把努海遣至他的族人，（说：）你在痛刑未到以前，警告自己的族人吧！","translator":"王静斋"},{"sura":71,"aya":2,"text":"他说：（我的族人哪！我实是一个公开警告你们的；","translator":"王静斋"},{"sura":71,"aya":3,"text":"你们当拜安拉，当畏惧他，并当顺从我；","translator":"王静斋"},{"sura":71,"aya":4,"text":"他就饶恕你们的罪，并姑容你们，直到一定的时期。的确，安拉预定的时期一旦来到，那就不复延迟。设若你们知道了，……。","translator":"王静斋"},{"sura":71,"aya":5,"text":"他说：（我的养主啊！我夜昼教导自己的族人了；","translator":"王静斋"},{"sura":71,"aya":6,"text":"但是我的教导只增加他们逃遁。","translator":"王静斋"},{"sura":71,"aya":7,"text":"每逢我教导他们，为的是你饶恕他们，他们就置指在耳，用衣遮蒙。他们时常反抗，高傲，顽强。","translator":"王静斋"},{"sura":71,"aya":8,"text":"我高声教导它们。","translator":"王静斋"},{"sura":71,"aya":9,"text":"我对他们明讲，我对他们暗说，","translator":"王静斋"},{"sura":71,"aya":10,"text":"切实地暗说。我说：（你们祈养主的饶恕吧！他确是多恕的。","translator":"王静斋"},{"sura":71,"aya":11,"text":"他降给你们连绵的雨；","translator":"王静斋"},{"sura":71,"aya":12,"text":"他助你们以资财与子女；他赐给你们天园；他为你们发现了诸河。","translator":"王静斋"},{"sura":71,"aya":13,"text":"你们为什么不盼望安拉的尊敬。","translator":"王静斋"},{"sura":71,"aya":14,"text":"他确造你们为不同的阶段。","translator":"王静斋"},{"sura":71,"aya":15,"text":"你们岂未看见安拉怎样地造化了七重天，","translator":"王静斋"},{"sura":71,"aya":16,"text":"于中造月为光，造日为灯。","translator":"王静斋"},{"sura":71,"aya":17,"text":"安拉使你们生长于地，切使生长。","translator":"王静斋"},{"sura":71,"aya":18,"text":"随又使你们归地，并使你们复出。","translator":"王静斋"},{"sura":71,"aya":19,"text":"安拉为你们以地作铺垫，","translator":"王静斋"},{"sura":71,"aya":20,"text":"以便你们在地上行走宽阔的道路。","translator":"王静斋"},{"sura":71,"aya":21,"text":"努海说：（我的养主啊！他们实违悖我了；他们追随那只被资财子女增加伤折的人了。","translator":"王静斋"},{"sura":71,"aya":22,"text":"他们施计谋，很大地计谋。）","translator":"王静斋"},{"sura":71,"aya":23,"text":"他们说：（你们不要放弃自己受拜的；也莫放弃「万德、」「素哇娥、」「业欧赛、」「业欧益、」「奈斯尔。」）","translator":"王静斋"},{"sura":71,"aya":24,"text":"他们确诱多的人迷误了。","translator":"王静斋"},{"sura":71,"aya":25,"text":"（主啊）你对于背义的人仅止增加迷误吧！……他们因着种种过错被淹，旋就进火狱了，他们抛开安拉得不到助己的……；","translator":"王静斋"},{"sura":71,"aya":26,"text":"努海说：（我的养主啊！你不要把一家逆徒遗在地上。","translator":"王静斋"},{"sura":71,"aya":27,"text":"的确，你若遗下了，他们就诱你的众仆迷误，他们竟自产生作恶的逆徒。","translator":"王静斋"},{"sura":71,"aya":28,"text":"我的养主啊！求你饶恕我和我父母，和那归信着进我房子的人；并饶恕众男女穆民：你对于背义的人仅止增加毁灭吧！）","translator":"王静斋"},{"sura":72,"aya":1,"text":"你说：我曾得到默示：一伙子镇呢侧耳，且说：我们闻到一导至正路的可奇的古兰，","translator":"王静斋"},{"sura":72,"aya":2,"text":"我们已然信它了。我们绝不举哪一个为养主作匹偶。","translator":"王静斋"},{"sura":72,"aya":3,"text":"又我们养主的尊威很玄高了；他未曾立一妻一子。","translator":"王静斋"},{"sura":72,"aya":4,"text":"又，我们的无才者往安拉上说谎话了。","translator":"王静斋"},{"sura":72,"aya":5,"text":"又，我们揣度，人类与镇呢绝不往安拉上说谎话。","translator":"王静斋"},{"sura":72,"aya":6,"text":"又，一般人类男子向一般镇呢男子求护佑。他们益增它们骄傲了。","translator":"王静斋"},{"sura":72,"aya":7,"text":"又，他们揣度，就像你们揣度。安拉绝不复活任何一个。","translator":"王静斋"},{"sura":72,"aya":8,"text":"又，我们摸天，而得见它是以坚强的守卫，与流星充满的。","translator":"王静斋"},{"sura":72,"aya":9,"text":"又，我们在那里为着闻听坐在各地方。而今，凡是侧耳的，他就遭遇一个为他隐伏的流星。","translator":"王静斋"},{"sura":72,"aya":10,"text":"又，我们不知道可对于地上的人要祸呀?还是养主对他们要福呢?","translator":"王静斋"},{"sura":72,"aya":11,"text":"又，我们之中有清廉的，有不及那个的，我们是不同的派系。","translator":"王静斋"},{"sura":72,"aya":12,"text":"又，我们深知，在地上不能抵抗安拉，也不能以逃跑抵抗他。","translator":"王静斋"},{"sura":72,"aya":13,"text":"又我们闻到引领的时候。我们归信他了，归信养主的人。不畏减少与亏损。又我们之中有顺从的，","translator":"王静斋"},{"sura":72,"aya":14,"text":"有作罪的。那顺从的人，均倾向得正道。","translator":"王静斋"},{"sura":72,"aya":15,"text":"作罪的人，均是火狱的柴薪。","translator":"王静斋"},{"sura":72,"aya":16,"text":"又设若他们端守正道了，我就教他们饮多的水，","translator":"王静斋"},{"sura":72,"aya":17,"text":"而以此试证他们。凡是违背养主教戒的人，他就教它入酷刑。","translator":"王静斋"},{"sura":72,"aya":18,"text":"又一切礼拜的所在，是安拉的执掌。你们不要并同安拉呼求任何一个。","translator":"王静斋"},{"sura":72,"aya":19,"text":"又当安拉的仆人站起拜主的时候，他们几至贴附他了。","translator":"王静斋"},{"sura":72,"aya":20,"text":"你说：（我惟拜我的养主，不单任何一个给他为伙伴。）","translator":"王静斋"},{"sura":72,"aya":21,"text":"你说：（我不能为你们掌祸福）；","translator":"王静斋"},{"sura":72,"aya":22,"text":"你说：（无有一个人向安拉上解救我，我撇开他绝得不到一个庇所）","translator":"王静斋"},{"sura":72,"aya":23,"text":"只有出自安拉的宣达和他的敕命。违犯安拉和他的使者的人，必遭火狱的火，永久居在其中。","translator":"王静斋"},{"sura":72,"aya":24,"text":"待至看见所惊吓的那个时候，他们即刻知道谁是在辅佐上薄弱的，在数目上至少的?","translator":"王静斋"},{"sura":72,"aya":25,"text":"你说：（我不知所惊吓你们的是临近的吗?或是养主为它规定远期呢?","translator":"王静斋"},{"sura":72,"aya":26,"text":"知道目不能见的主，他不容哪一个人知道他的隐秘；","translator":"王静斋"},{"sura":72,"aya":27,"text":"除非是他喜爱的使者。的确，他教巡察的行在它的前后；","translator":"王静斋"},{"sura":72,"aya":28,"text":"只为它知道他们已宣达养主的敕命了。他环绕他们跟前的了，他计万物的数目了。）","translator":"王静斋"},{"sura":73,"aya":1,"text":"哎裹大衣的人哪！","translator":"王静斋"},{"sura":73,"aya":2,"text":"你当在夜晚立站，只余少的半夜。","translator":"王静斋"},{"sura":73,"aya":3,"text":"或是你减去它些微的，","translator":"王静斋"},{"sura":73,"aya":4,"text":"或是你再往上增加。 你要消停着朗诵古兰，消停着朗诵。","translator":"王静斋"},{"sura":73,"aya":5,"text":"我即将把严重的话。投给你——","translator":"王静斋"},{"sura":73,"aya":6,"text":"的确，夜间起来，是最获心间的；是发言至为纯正的。","translator":"王静斋"},{"sura":73,"aya":7,"text":"你在白昼确有常时间的奔走。","translator":"王静斋"},{"sura":73,"aya":8,"text":"你当记念养主之名，你当屏除一切，而倾向于他，切实地屏除。","translator":"王静斋"},{"sura":73,"aya":9,"text":"调养东方西方的主。除他以外再无有主，你就应该把他当为可靠的。","translator":"王静斋"},{"sura":73,"aya":10,"text":"你当忍受他们所说的。你要远离他们，善自远离。","translator":"王静斋"},{"sura":73,"aya":11,"text":"你任我对付一般享受的不信者。你要略微姑容他们；","translator":"王静斋"},{"sura":73,"aya":12,"text":"在我御前确有桎梏与烈火；","translator":"王静斋"},{"sura":73,"aya":13,"text":"并有鲠噎的食物，与痛刑。","translator":"王静斋"},{"sura":73,"aya":14,"text":"这在地与群山震动，群山化成流沙的那一日。","translator":"王静斋"},{"sura":73,"aya":15,"text":"的确，我向你们遣去一位使者见证你们，就如向匪喇傲遣去一位使者；","translator":"王静斋"},{"sura":73,"aya":16,"text":"但是，匪刺傲违背这使者了。于是我惩罚他，严厉地惩罚。","translator":"王静斋"},{"sura":73,"aya":17,"text":"若是你们不信了，你们怎能脱开化儿童为老者，","translator":"王静斋"},{"sura":73,"aya":18,"text":"天且崩裂的那一日呢？主的约。是该实现的。","translator":"王静斋"},{"sura":73,"aya":19,"text":"这是教戒。可任人随便取一倾向养主之道。","translator":"王静斋"},{"sura":73,"aya":20,"text":"你的养主确知道你和随从你的一伙人，站立少过一夜的三分之二；与它的平半；与它的三分之一。安拉规定夜昼。他知道你们不能计数它；所以他给你们减少痛苦了。你们诵容易读的古兰吧！他知道你们之中即将有患病的，和另一伙在地上讨安拉底特恩着旅行的，又一伙在安拉之道上格杀的，你们诵其中易读的吧！你们当举拜功， 施天课，并贷予安拉，好自贷予。你们为自身预先作下的善事，将在安拉那里得见它是至好的，且是善赏最大的。你们当求安拉饶恕，安拉实是多恕的，特慈的。","translator":"王静斋"},{"sura":74,"aya":1,"text":"着大衣的人哪！","translator":"王静斋"},{"sura":74,"aya":2,"text":"你起来传警告，","translator":"王静斋"},{"sura":74,"aya":3,"text":"你当颂养主伟大，","translator":"王静斋"},{"sura":74,"aya":4,"text":"你洁净自己的衣服。","translator":"王静斋"},{"sura":74,"aya":5,"text":"你要离远污秽！","translator":"王静斋"},{"sura":74,"aya":6,"text":"你不要希望厚报着施恩。","translator":"王静斋"},{"sura":74,"aya":7,"text":"你要为着你的养主忍耐。","translator":"王静斋"},{"sura":74,"aya":8,"text":"当吹起了号筒时候，","translator":"王静斋"},{"sura":74,"aya":9,"text":"是日，那是一个困难的日子；","translator":"王静斋"},{"sura":74,"aya":10,"text":"是在逆徒上不容易的。","translator":"王静斋"},{"sura":74,"aya":11,"text":"你任我对付那被我造为独身的人。","translator":"王静斋"},{"sura":74,"aya":12,"text":"我赐给他宽裕的资财，","translator":"王静斋"},{"sura":74,"aya":13,"text":"与满堂的子女，","translator":"王静斋"},{"sura":74,"aya":14,"text":"并为他扩张、切使扩张。","translator":"王静斋"},{"sura":74,"aya":15,"text":"他更希图我增加。","translator":"王静斋"},{"sura":74,"aya":16,"text":"不能，那人确不服我的表征。","translator":"王静斋"},{"sura":74,"aya":17,"text":"我即将强他受苦。","translator":"王静斋"},{"sura":74,"aya":18,"text":"的确，他参想，他裁夺。","translator":"王静斋"},{"sura":74,"aya":19,"text":"他随被驱远了。","translator":"王静斋"},{"sura":74,"aya":20,"text":"他怎样裁夺。他更被驱远了。他怎样裁夺。","translator":"王静斋"},{"sura":74,"aya":21,"text":"而后他观察。","translator":"王静斋"},{"sura":74,"aya":22,"text":"他皱面蹙额。","translator":"王静斋"},{"sura":74,"aya":23,"text":"他且掉背，高傲。","translator":"王静斋"},{"sura":74,"aya":24,"text":"说：（这不过是递嬗的邪术。","translator":"王静斋"},{"sura":74,"aya":25,"text":"这不外是人的话。）","translator":"王静斋"},{"sura":74,"aya":26,"text":"我即将教他进「赛盖来。」","translator":"王静斋"},{"sura":74,"aya":27,"text":"你怎能知道「赛盖来」是甚么?","translator":"王静斋"},{"sura":74,"aya":28,"text":"即不遗落，也不放弃。","translator":"王静斋"},{"sura":74,"aya":29,"text":"烧黑了外皮的。","translator":"王静斋"},{"sura":74,"aya":30,"text":"在那上有十九个天使。","translator":"王静斋"},{"sura":74,"aya":31,"text":"我惟安置天使掌火狱。我只是以他们的数目作为磨难逆徒的。为的是蒙赐经典的人坚信不疑，归信的人益增归信；有经的人和众穆民不至疑惑。更为心中有病的人并众逆徒说：（安拉欲用这譬喻作甚么?）安拉就这样地造其意欲——的人迷误；导其意欲……的人得道，惟他知你养主的军兵。那只是对人类的一种教戒。","translator":"王静斋"},{"sura":74,"aya":32,"text":"何尝不是。","translator":"王静斋"},{"sura":74,"aya":33,"text":"试思月与夜晚，当其继至时；","translator":"王静斋"},{"sura":74,"aya":34,"text":"试思清晨当其发光时；","translator":"王静斋"},{"sura":74,"aya":35,"text":"这 实是一大患难；","translator":"王静斋"},{"sura":74,"aya":36,"text":"是对世人………，","translator":"王静斋"},{"sura":74,"aya":37,"text":"你们之中其欲争先或退后的人作警告的。","translator":"王静斋"},{"sura":74,"aya":38,"text":"每一个有性命的，是依自己的作为被质的；","translator":"王静斋"},{"sura":74,"aya":39,"text":"除非是右边人。","translator":"王静斋"},{"sura":74,"aya":40,"text":"居住在天园中。","translator":"王静斋"},{"sura":74,"aya":41,"text":"对一般为恶的人相询问。","translator":"王静斋"},{"sura":74,"aya":42,"text":"你们何致于进入「赛盖来」呢?","translator":"王静斋"},{"sura":74,"aya":43,"text":"他们说：我们未作礼拜，","translator":"王静斋"},{"sura":74,"aya":44,"text":"未款待贫人；","translator":"王静斋"},{"sura":74,"aya":45,"text":"我们和一般诙谐的人一同诙谐了。","translator":"王静斋"},{"sura":74,"aya":46,"text":"我们至死不信报应日。","translator":"王静斋"},{"sura":74,"aya":47,"text":".","translator":"王静斋"},{"sura":74,"aya":48,"text":"告赦者的告赦不利于他们。","translator":"王静斋"},{"sura":74,"aya":49,"text":"他们为什么避忌教戒呢?","translator":"王静斋"},{"sura":74,"aya":50,"text":"好像他们是脱离狮子的逃驴。","translator":"王静斋"},{"sura":74,"aya":51,"text":".","translator":"王静斋"},{"sura":74,"aya":52,"text":"不然，他们每一个人欲蒙赐一部展开的圣典。","translator":"王静斋"},{"sura":74,"aya":53,"text":"不能，实是他们不怕后世。","translator":"王静斋"},{"sura":74,"aya":54,"text":"不可，它实在是一种教戒。","translator":"王静斋"},{"sura":74,"aya":55,"text":"意欲（受教）的人，","translator":"王静斋"},{"sura":74,"aya":56,"text":"便受它教戒；除非是安拉意欲……的时候，他们不会纳劝。他是堪掌敬慎的主，堪掌饶恕的主。","translator":"王静斋"},{"sura":75,"aya":1,"text":"我指着复生日为誓；","translator":"王静斋"},{"sura":75,"aya":2,"text":"我指着自责的心为誓；","translator":"王静斋"},{"sura":75,"aya":3,"text":"人类岂可以为我不能聚起他的骨骸?","translator":"王静斋"},{"sura":75,"aya":4,"text":"是的，（我）能于齐整他的手指。","translator":"王静斋"},{"sura":75,"aya":5,"text":"不然，人类欲先作恶吗?","translator":"王静斋"},{"sura":75,"aya":6,"text":"他问：复生日是在什么时候呢？","translator":"王静斋"},{"sura":75,"aya":7,"text":"当目膛、","translator":"王静斋"},{"sura":75,"aya":8,"text":"月暗，","translator":"王静斋"},{"sura":75,"aya":9,"text":"日月相并的时候，","translator":"王静斋"},{"sura":75,"aya":10,"text":"是日，人类说：（逃到哪里?）","translator":"王静斋"},{"sura":75,"aya":11,"text":"不可无一庇护的所在。","translator":"王静斋"},{"sura":75,"aya":12,"text":"是日，只向养主求一安定的地方。","translator":"王静斋"},{"sura":75,"aya":13,"text":"是日，向人类宣告他所提前落后的。","translator":"王静斋"},{"sura":75,"aya":14,"text":"不然，人类是证明他自身的，","translator":"王静斋"},{"sura":75,"aya":15,"text":"纵令他提出自己的辩词。","translator":"王静斋"},{"sura":75,"aya":16,"text":"你不要为着赶忙得到而因它动舌。","translator":"王静斋"},{"sura":75,"aya":17,"text":"集它诵它确实在我。","translator":"王静斋"},{"sura":75,"aya":18,"text":"我诵它的时候，你就追随他的诵读吧！","translator":"王静斋"},{"sura":75,"aya":19,"text":"而后解明它确实在我。","translator":"王静斋"},{"sura":75,"aya":20,"text":"何尝不然，你们实爱现世，","translator":"王静斋"},{"sura":75,"aya":21,"text":"放弃后世。","translator":"王静斋"},{"sura":75,"aya":22,"text":"是日，一般面目是鲜艳的，","translator":"王静斋"},{"sura":75,"aya":23,"text":"观看养主的。","translator":"王静斋"},{"sura":75,"aya":24,"text":"是日。一般面目是酸暗的；","translator":"王静斋"},{"sura":75,"aya":25,"text":"他深知要对他施行一大不幸。","translator":"王静斋"},{"sura":75,"aya":26,"text":"不同往常。性命达到了锁骨，","translator":"王静斋"},{"sura":75,"aya":27,"text":"有的说：（谁施行符咒?）","translator":"王静斋"},{"sura":75,"aya":28,"text":"他且深知这是要离开的；","translator":"王静斋"},{"sura":75,"aya":29,"text":"胫与胫贴。","translator":"王静斋"},{"sura":75,"aya":30,"text":"是日，惟独驱至养主。","translator":"王静斋"},{"sura":75,"aya":31,"text":"他不信服。他不礼拜。","translator":"王静斋"},{"sura":75,"aya":32,"text":"他只是不信，退转。","translator":"王静斋"},{"sura":75,"aya":33,"text":"他且复摇摆着去到自己家里。","translator":"王静斋"},{"sura":75,"aya":34,"text":"你快遭不幸，快遭不幸了；","translator":"王静斋"},{"sura":75,"aya":35,"text":"再说你快遭不幸，快遭不幸了，","translator":"王静斋"},{"sura":75,"aya":36,"text":"人类自以为任他流荡吗?","translator":"王静斋"},{"sura":75,"aya":37,"text":"他岂不是被倾的一点精水，","translator":"王静斋"},{"sura":75,"aya":38,"text":"而后一块血，再经真主造化，而使齐全，","translator":"王静斋"},{"sura":75,"aya":39,"text":"真主且由那上造化了两性——男女！","translator":"王静斋"},{"sura":75,"aya":40,"text":"这岂不是能于复活死者的吗?","translator":"王静斋"},{"sura":76,"aya":1,"text":"一度光阴未曾经过人类吗? （彼时）他不是一项可提到的事情。","translator":"王静斋"},{"sura":76,"aya":2,"text":"我确由搀混的一点精上造化了人类。我试证他，而后我使他能听能见。","translator":"王静斋"},{"sura":76,"aya":3,"text":"我确指示他正道。或是感念的，或是忘恩的。","translator":"王静斋"},{"sura":76,"aya":4,"text":"我确为逆徒们预备了铁链、项圈、与烈火。","translator":"王静斋"},{"sura":76,"aya":5,"text":"一般好人，必饮自它那调和物是“克夫尔”的酒杯，","translator":"王静斋"},{"sura":76,"aya":6,"text":"就是一种泉源；安拉的众仆自那上饮水，他们使它涌流，切使涌流。","translator":"王静斋"},{"sura":76,"aya":7,"text":"他们全满许下的愿，骇怕其恶四扬的那一日。","translator":"王静斋"},{"sura":76,"aya":8,"text":"他们以自爱的食物款待贫人、孤儿、俘虏。","translator":"王静斋"},{"sura":76,"aya":9,"text":"（说：）我们为着讨安拉的喜款待你们，并不向你们索报酬与感谢。","translator":"王静斋"},{"sura":76,"aya":10,"text":"我们实是骇惧出自养主极端酸脸的那一日。","translator":"王静斋"},{"sura":76,"aya":11,"text":"安拉于那日的祸患上庇佑他们。并使他们看见鲜艳与欣喜；","translator":"王静斋"},{"sura":76,"aya":12,"text":"他因着他们的忍耐，把园林与绫锦赏给他们。","translator":"王静斋"},{"sura":76,"aya":13,"text":"他们在那里靠在床上，看不见太阳与严寒。","translator":"王静斋"},{"sura":76,"aya":14,"text":"园林的阴影接近他们。它的果子容易摘取。","translator":"王静斋"},{"sura":76,"aya":15,"text":"给他们轮转银器与玻璃杯。","translator":"王静斋"},{"sura":76,"aya":16,"text":"就是他们估计，切实估计的银玻璃。","translator":"王静斋"},{"sura":76,"aya":17,"text":"他们在那里得饮以调和物是姜汁子的盏。","translator":"王静斋"},{"sura":76,"aya":18,"text":"在那里一个名为（色洛赛逼来）的泉源。","translator":"王静斋"},{"sura":76,"aya":19,"text":"日久不变容颜的幼童往来至他们。你瞧见他们的时候，便以为他们是断了线的珍珠。","translator":"王静斋"},{"sura":76,"aya":20,"text":"你在那里看的时候，你就看见福祉与大国了。","translator":"王静斋"},{"sura":76,"aya":21,"text":"他们上身着有绿色的精致的与粗糙的绸缎衣服，并被饰以银镯。养主教他们饮清洁的饮料。","translator":"王静斋"},{"sura":76,"aya":22,"text":"这 是对你们的报偿；你们的努力已获悦纳了。","translator":"王静斋"},{"sura":76,"aya":23,"text":"我降给你古兰，切实地颁降。","translator":"王静斋"},{"sura":76,"aya":24,"text":"你当安心奉行养主的判断。你不要顺从他们之中，作罪的或忘恩的人。","translator":"王静斋"},{"sura":76,"aya":25,"text":"你当朝夕记念你养主之名。","translator":"王静斋"},{"sura":76,"aya":26,"text":"你当在半夜里为他叩头；你当在长夜里颂他清净。","translator":"王静斋"},{"sura":76,"aya":27,"text":"的确，这般人爱现世，弃重日于后面。","translator":"王静斋"},{"sura":76,"aya":28,"text":"我造化了他们；我坚固了他们的关节。我意欲……的时候，我就改换类似他们的，切实地改换。","translator":"王静斋"},{"sura":76,"aya":29,"text":"这确是一种教戒。","translator":"王静斋"},{"sura":76,"aya":30,"text":"谁愿意……谁就向其养主取一途径。除非安拉意欲……的时候，你们无所意欲。安拉确是深知的，明哲的。","translator":"王静斋"},{"sura":76,"aya":31,"text":"他教自所意欲……的人入在自己的慈惠中。背义的人，真主已为他们备下痛刑了。","translator":"王静斋"},{"sura":77,"aya":1,"text":"试思一般继续被差的，","translator":"王静斋"},{"sura":77,"aya":2,"text":"行动神速神速的，","translator":"王静斋"},{"sura":77,"aya":3,"text":"播扬播扬的，","translator":"王静斋"},{"sura":77,"aya":4,"text":"分别分别的，","translator":"王静斋"},{"sura":77,"aya":5,"text":"因为（杜绝）托词或为着警告而投教戒的；","translator":"王静斋"},{"sura":77,"aya":6,"text":".","translator":"王静斋"},{"sura":77,"aya":7,"text":"所对你们约定的，必将实现。","translator":"王静斋"},{"sura":77,"aya":8,"text":"当列星消灭的时候，","translator":"王静斋"},{"sura":77,"aya":9,"text":"天已崩裂的时候，","translator":"王静斋"},{"sura":77,"aya":10,"text":"群山粉碎的时候，","translator":"王静斋"},{"sura":77,"aya":11,"text":"列使集合的时侯，……。","translator":"王静斋"},{"sura":77,"aya":12,"text":"当那一天迟延它？","translator":"王静斋"},{"sura":77,"aya":13,"text":"为分明的日子。","translator":"王静斋"},{"sura":77,"aya":14,"text":"你怎能知道哪是分明的日子?","translator":"王静斋"},{"sura":77,"aya":15,"text":"不信的人们在那时候遭祸了，","translator":"王静斋"},{"sura":77,"aya":16,"text":"我岂未灭绝前代","translator":"王静斋"},{"sura":77,"aya":17,"text":"而今后人追随他们吗?","translator":"王静斋"},{"sura":77,"aya":18,"text":"我就这样地对待一般为恶的人。","translator":"王静斋"},{"sura":77,"aya":19,"text":"不信的人在那日遭祸了：","translator":"王静斋"},{"sura":77,"aya":20,"text":"我未曾把你们造自微贱的水，而把它置在坚固的定处，","translator":"王静斋"},{"sura":77,"aya":21,"text":".","translator":"王静斋"},{"sura":77,"aya":22,"text":"直到限定的时期吗?","translator":"王静斋"},{"sura":77,"aya":23,"text":"我曾规定了；规定者太好了，","translator":"王静斋"},{"sura":77,"aya":24,"text":"不信的人在那日遭祸了！","translator":"王静斋"},{"sura":77,"aya":25,"text":"我岂未把地作成收揽活的与死的，","translator":"王静斋"},{"sura":77,"aya":26,"text":".","translator":"王静斋"},{"sura":77,"aya":27,"text":"并在那里造化高大的定山，更教你们饮甘水吗?","translator":"王静斋"},{"sura":77,"aya":28,"text":"不信的人在那日遭祸了！","translator":"王静斋"},{"sura":77,"aya":29,"text":"你们去到曾所不信的吧！","translator":"王静斋"},{"sura":77,"aya":30,"text":"你们去到那不遮阴不避焰火的三叉影下吧！","translator":"王静斋"},{"sura":77,"aya":31,"text":".","translator":"王静斋"},{"sura":77,"aya":32,"text":"它射出宫殿般的火花，","translator":"王静斋"},{"sura":77,"aya":33,"text":"好像是黄毛骆驼。","translator":"王静斋"},{"sura":77,"aya":34,"text":"不信的人在那日遭祸了！","translator":"王静斋"},{"sura":77,"aya":35,"text":"这是他们不能发言，","translator":"王静斋"},{"sura":77,"aya":36,"text":"且不允他们托辞的那一日。","translator":"王静斋"},{"sura":77,"aya":37,"text":"不信的人在那日遭祸了！","translator":"王静斋"},{"sura":77,"aya":38,"text":"这是分明的日子。我集合你们与众前人。","translator":"王静斋"},{"sura":77,"aya":39,"text":"如果你们有诡计，你们就对我施诡计吧！","translator":"王静斋"},{"sura":77,"aya":40,"text":"不信的人在那日遭祸了！","translator":"王静斋"},{"sura":77,"aya":41,"text":"的确，敬慎的人们，是在阴影与泉源，","translator":"王静斋"},{"sura":77,"aya":42,"text":"和他们所想望的鲜果之下。","translator":"王静斋"},{"sura":77,"aya":43,"text":"（我说：）你们以自所作过的畅然着吃饮吧！","translator":"王静斋"},{"sura":77,"aya":44,"text":"我就这样地善赏一般为善的人。","translator":"王静斋"},{"sura":77,"aya":45,"text":"不信的人在那日遭祸了！","translator":"王静斋"},{"sura":77,"aya":46,"text":"你们吃吧，你们略微享受吧！你们实是为恶的人。","translator":"王静斋"},{"sura":77,"aya":47,"text":"不信的人在那日遭祸了！","translator":"王静斋"},{"sura":77,"aya":48,"text":"有人语他们鞠躬的时候，他们不鞠躬。","translator":"王静斋"},{"sura":77,"aya":49,"text":"不信的人在那日遭祸了！","translator":"王静斋"},{"sura":77,"aya":50,"text":"他们信它以后的什么话呢？","translator":"王静斋"},{"sura":78,"aya":1,"text":"他们对于甚么上互相询问?","translator":"王静斋"},{"sura":78,"aya":2,"text":"对于他们所因它起争议的重大消息上。","translator":"王静斋"},{"sura":78,"aya":3,"text":".","translator":"王静斋"},{"sura":78,"aya":4,"text":"不可，他们即将知道。","translator":"王静斋"},{"sura":78,"aya":5,"text":"尤其不可，他们即将知道。","translator":"王静斋"},{"sura":78,"aya":6,"text":"我岂未造地为铺垫，","translator":"王静斋"},{"sura":78,"aya":7,"text":"造山为钉子吗?","translator":"王静斋"},{"sura":78,"aya":8,"text":"我造你们为两性，","translator":"王静斋"},{"sura":78,"aya":9,"text":"我规定你们的睡眠为休息。","translator":"王静斋"},{"sura":78,"aya":10,"text":"我造夜为衣服。","translator":"王静斋"},{"sura":78,"aya":11,"text":"造白昼为谋生的时间。","translator":"王静斋"},{"sura":78,"aya":12,"text":"我在你们上面建造坚固的七八天。","translator":"王静斋"},{"sura":78,"aya":13,"text":"我造化了明灯。","translator":"王静斋"},{"sura":78,"aya":14,"text":"我由云中降下倾流的水，","translator":"王静斋"},{"sura":78,"aya":15,"text":"为的是用它发生谷粒，植物，","translator":"王静斋"},{"sura":78,"aya":16,"text":"与繁茂的园林。","translator":"王静斋"},{"sura":78,"aya":17,"text":"的确分明的日子，有一定的时间。","translator":"王静斋"},{"sura":78,"aya":18,"text":"当吹起了号筒的那一日，你们一伙一伙地到來。","translator":"王静斋"},{"sura":78,"aya":19,"text":"天被开启而发现了诸门。","translator":"王静斋"},{"sura":78,"aya":20,"text":"群山移动而成映影。","translator":"王静斋"},{"sura":78,"aya":21,"text":"火狱确是一般违法者的要道。——归返的地方。","translator":"王静斋"},{"sura":78,"aya":22,"text":".","translator":"王静斋"},{"sura":78,"aya":23,"text":"他们在那里迟留好久的时期。","translator":"王静斋"},{"sura":78,"aya":24,"text":"他们在那里尝不到凉爽与饮料，","translator":"王静斋"},{"sura":78,"aya":25,"text":"只有滚水与脓水；","translator":"王静斋"},{"sura":78,"aya":26,"text":"一种相当的报应。","translator":"王静斋"},{"sura":78,"aya":27,"text":"的确，他们原未骇惧到审算。","translator":"王静斋"},{"sura":78,"aya":28,"text":"他们不信我的表征，切实地不信。","translator":"王静斋"},{"sura":78,"aya":29,"text":"万事惟经我计数它，写定的。","translator":"王静斋"},{"sura":78,"aya":30,"text":"你们尝试吧：我只是增给你们罪刑。","translator":"王静斋"},{"sura":78,"aya":31,"text":"敬慎的人们必获所得的——","translator":"王静斋"},{"sura":78,"aya":32,"text":"花园与葡萄（园）。","translator":"王静斋"},{"sura":78,"aya":33,"text":"一般年令相等隆乳的青年，","translator":"王静斋"},{"sura":78,"aya":34,"text":"与满溢的杯。","translator":"王静斋"},{"sura":78,"aya":35,"text":"他们在那里听不到妄言与谎话。","translator":"王静斋"},{"sura":78,"aya":36,"text":"这是出自调养你的主，就是调养天地万有慈主的善赏——充足的赐与。","translator":"王静斋"},{"sura":78,"aya":37,"text":"他们不敢对他交谈。","translator":"王静斋"},{"sura":78,"aya":38,"text":"当（鲁哈）和众天使列班着站起的那一天，他们不能发言，除非得到慈主许可，并说正言的人。","translator":"王静斋"},{"sura":78,"aya":39,"text":"这是必有的一天，人可随便取一达到养主的归所。","translator":"王静斋"},{"sura":78,"aya":40,"text":"我警告你们以相近的刑罚。当那一日，人就看见他的双手曾经前送的。不信的人说：（哎！宁愿我化为土吧！）","translator":"王静斋"},{"sura":79,"aya":1,"text":"试思用力取得的；","translator":"王静斋"},{"sura":79,"aya":2,"text":"温和收取的；","translator":"王静斋"},{"sura":79,"aya":3,"text":"游动游动的；","translator":"王静斋"},{"sura":79,"aya":4,"text":"争先争先的；","translator":"王静斋"},{"sura":79,"aya":5,"text":"治理事情的；","translator":"王静斋"},{"sura":79,"aya":6,"text":"在震动的发生震动，","translator":"王静斋"},{"sura":79,"aya":7,"text":"后来的继它而至的日子，","translator":"王静斋"},{"sura":79,"aya":8,"text":"是日，众心恐怖，","translator":"王静斋"},{"sura":79,"aya":9,"text":"其目微贱。","translator":"王静斋"},{"sura":79,"aya":10,"text":"他们说：（我们可能归到原来的情形吗?","translator":"王静斋"},{"sura":79,"aya":11,"text":"我们既成朽骨的时候，可以吗?","translator":"王静斋"},{"sura":79,"aya":12,"text":"他们说：（果然那样，这是一种沦落的归返。）","translator":"王静斋"},{"sura":79,"aya":13,"text":"它，只是一次暴声，","translator":"王静斋"},{"sura":79,"aya":14,"text":"忽然他们就在平地上。","translator":"王静斋"},{"sura":79,"aya":15,"text":"母撤的故事未曾达至你吗?","translator":"王静斋"},{"sura":79,"aya":16,"text":"那时候，他的养主曾在洁谷，图哇晓谕他：","translator":"王静斋"},{"sura":79,"aya":17,"text":"（你去到匪喇傲！他实在违犯了。","translator":"王静斋"},{"sura":79,"aya":18,"text":"你说，你欲洁净吗?","translator":"王静斋"},{"sura":79,"aya":19,"text":"我导你于养主，你遂惧怕，）","translator":"王静斋"},{"sura":79,"aya":20,"text":"而后他把绝大的异迹现给他了。","translator":"王静斋"},{"sura":79,"aya":21,"text":"但是他不信，他背逆。","translator":"王静斋"},{"sura":79,"aya":22,"text":"他且匆忘退转，","translator":"王静斋"},{"sura":79,"aya":23,"text":"而集合，大呼说：","translator":"王静斋"},{"sura":79,"aya":24,"text":"（我是你们至高的养主。）","translator":"王静斋"},{"sura":79,"aya":25,"text":"而后，安拉惩他以后世与今世的刑罚。","translator":"王静斋"},{"sura":79,"aya":26,"text":"的确，内中对于惧怕的人，含有一种儆诫。","translator":"王静斋"},{"sura":79,"aya":27,"text":"你们是在造化上坚固的吗?抑或是天呢? 他建造了它，","translator":"王静斋"},{"sura":79,"aya":28,"text":"他起高了它的华盖而使它整齐。","translator":"王静斋"},{"sura":79,"aya":29,"text":"他遮覆了黑夜，而现出日光。","translator":"王静斋"},{"sura":79,"aya":30,"text":"此后他又铺展了地，","translator":"王静斋"},{"sura":79,"aya":31,"text":"而就中发生它的水草。","translator":"王静斋"},{"sura":79,"aya":32,"text":"他且稳定了群山，","translator":"王静斋"},{"sura":79,"aya":33,"text":"以利于你们，和你们的家畜。","translator":"王静斋"},{"sura":79,"aya":34,"text":"当极大的患难到来的时候，","translator":"王静斋"},{"sura":79,"aya":35,"text":"就是人类想起自所奔波的，","translator":"王静斋"},{"sura":79,"aya":36,"text":"火狱被现给见者的那一日，","translator":"王静斋"},{"sura":79,"aya":37,"text":"说到违法","translator":"王静斋"},{"sura":79,"aya":38,"text":"并选择今生的人，","translator":"王静斋"},{"sura":79,"aya":39,"text":"火狱就是居处；","translator":"王静斋"},{"sura":79,"aya":40,"text":"那骇怕站在养主御前，节制偏私的人，","translator":"王静斋"},{"sura":79,"aya":41,"text":"天园就是居处。","translator":"王静斋"},{"sura":79,"aya":42,"text":"他们对于复生上问你：它在何时确定?","translator":"王静斋"},{"sura":79,"aya":43,"text":"你从哪里宣布它呢?","translator":"王静斋"},{"sura":79,"aya":44,"text":"它的结局惟归到你的养主。","translator":"王静斋"},{"sura":79,"aya":45,"text":"你只是警告那惧怕它的人。","translator":"王静斋"},{"sura":79,"aya":46,"text":"在他们亲见它的那一日，好像他们仅迟留一夕或其一朝。","translator":"王静斋"},{"sura":80,"aya":1,"text":"在盲人到来的时侯，他蹙额，扭面了。","translator":"王静斋"},{"sura":80,"aya":2,"text":".","translator":"王静斋"},{"sura":80,"aya":3,"text":"你从何知道：或许他清洁，","translator":"王静斋"},{"sura":80,"aya":4,"text":"或他纳劝而教戒于他有益。","translator":"王静斋"},{"sura":80,"aya":5,"text":"说到富有的人，","translator":"王静斋"},{"sura":80,"aya":6,"text":"你独亲近他。","translator":"王静斋"},{"sura":80,"aya":7,"text":"他不清洁于你无害。","translator":"王静斋"},{"sura":80,"aya":8,"text":"惧怕着忙来至你的人，","translator":"王静斋"},{"sura":80,"aya":9,"text":".","translator":"王静斋"},{"sura":80,"aya":10,"text":"你顾不得他。","translator":"王静斋"},{"sura":80,"aya":11,"text":"不可，（这 ）实是一种教戒。","translator":"王静斋"},{"sura":80,"aya":12,"text":"可任人随便记它。","translator":"王静斋"},{"sura":80,"aya":13,"text":"载在清洁高贵的圣典中；","translator":"王静斋"},{"sura":80,"aya":14,"text":".","translator":"王静斋"},{"sura":80,"aya":15,"text":"操在顺命高尚的作记录的天使手里。","translator":"王静斋"},{"sura":80,"aya":16,"text":".","translator":"王静斋"},{"sura":80,"aya":17,"text":"人类遭受驱远了：他怎么不信呢?","translator":"王静斋"},{"sura":80,"aya":18,"text":"主由哪一物上造化了他?","translator":"王静斋"},{"sura":80,"aya":19,"text":"由一点精上，土造化了他，且给他规定。","translator":"王静斋"},{"sura":80,"aya":20,"text":"主且赐他容易通过道路。","translator":"王静斋"},{"sura":80,"aya":21,"text":"主又教他死，而令他入墓穴，","translator":"王静斋"},{"sura":80,"aya":22,"text":"而后真主意欲……的时候，就使他复活。","translator":"王静斋"},{"sura":80,"aya":23,"text":"不可，他未曾实行主所命令的。","translator":"王静斋"},{"sura":80,"aya":24,"text":"任人类观察自己的食物；","translator":"王静斋"},{"sura":80,"aya":25,"text":"我倾下了水，切实倾下：","translator":"王静斋"},{"sura":80,"aya":26,"text":"我且剖开了地，切实剖开。","translator":"王静斋"},{"sura":80,"aya":27,"text":"我在它里生长出子粒、","translator":"王静斋"},{"sura":80,"aya":28,"text":"葡萄、苜蓿、","translator":"王静斋"},{"sura":80,"aya":29,"text":"橄榄、枣树、","translator":"王静斋"},{"sura":80,"aya":30,"text":"繁茂的花园、","translator":"王静斋"},{"sura":80,"aya":31,"text":"鲜果、牧草；","translator":"王静斋"},{"sura":80,"aya":32,"text":"以利于你们，并利于你们的家畜。","translator":"王静斋"},{"sura":80,"aya":33,"text":"在暴声发出的时候，","translator":"王静斋"},{"sura":80,"aya":34,"text":"就是人类逃开自己的弟兄、","translator":"王静斋"},{"sura":80,"aya":35,"text":"母亲、父亲、","translator":"王静斋"},{"sura":80,"aya":36,"text":"妻室、子女的那一日；","translator":"王静斋"},{"sura":80,"aya":37,"text":"是日，每一个人有他那缠身的事情。","translator":"王静斋"},{"sura":80,"aya":38,"text":"是日，一般面目是光润的，","translator":"王静斋"},{"sura":80,"aya":39,"text":"愉快的，欣喜的。","translator":"王静斋"},{"sura":80,"aya":40,"text":"是日，一般面目染有灰尘，","translator":"王静斋"},{"sura":80,"aya":41,"text":"黑暗所遮。","translator":"王静斋"},{"sura":80,"aya":42,"text":"这般人就是作恶的逆徒。","translator":"王静斋"},{"sura":81,"aya":1,"text":"在太阳折起的时候，","translator":"王静斋"},{"sura":81,"aya":2,"text":"在群星没落的时候，","translator":"王静斋"},{"sura":81,"aya":3,"text":"在山狱行动的时候，","translator":"王静斋"},{"sura":81,"aya":4,"text":"在怀孕十个月的母驼游荡的时候，","translator":"王静斋"},{"sura":81,"aya":5,"text":"在野兽被集的时候。","translator":"王静斋"},{"sura":81,"aya":6,"text":"在各海澎湃的时候，","translator":"王静斋"},{"sura":81,"aya":7,"text":"在性命配搭的时候，","translator":"王静斋"},{"sura":81,"aya":8,"text":"活埋的幼女遇问：","translator":"王静斋"},{"sura":81,"aya":9,"text":"在为着哪样罪恶被杀的时候，","translator":"王静斋"},{"sura":81,"aya":10,"text":"在册籍展开的时候，","translator":"王静斋"},{"sura":81,"aya":11,"text":"在天被剥落的时候，","translator":"王静斋"},{"sura":81,"aya":12,"text":"在火狱燃着的时候，","translator":"王静斋"},{"sura":81,"aya":13,"text":"在天园接近的时候，","translator":"王静斋"},{"sura":81,"aya":14,"text":"人就知道自所现出的了。","translator":"王静斋"},{"sura":81,"aya":15,"text":"我指着隐伏不显的行星为誓；","translator":"王静斋"},{"sura":81,"aya":16,"text":".","translator":"王静斋"},{"sura":81,"aya":17,"text":"指着夜，在其入到黑暗的时候为誓；","translator":"王静斋"},{"sura":81,"aya":18,"text":"并指着清晨在其吐气的时候为誓，","translator":"王静斋"},{"sura":81,"aya":19,"text":"那确是贵使者的言语。","translator":"王静斋"},{"sura":81,"aya":20,"text":"(那使者)是有力量的，在掌[阿勒世]的主御前有地位的，","translator":"王静斋"},{"sura":81,"aya":21,"text":"在那里受服从的，忠诚的。","translator":"王静斋"},{"sura":81,"aya":22,"text":"你们的同人绝不是疯子。","translator":"王静斋"},{"sura":81,"aya":23,"text":"他确在明朗的天际看见它了。","translator":"王静斋"},{"sura":81,"aya":24,"text":"他不是对于目所不见者吝而不宣的。","translator":"王静斋"},{"sura":81,"aya":25,"text":"那不是被逐的恶魔偕说的。","translator":"王静斋"},{"sura":81,"aya":26,"text":"你们去到哪里?","translator":"王静斋"},{"sura":81,"aya":27,"text":"它不外是教戒普世界的人，","translator":"王静斋"},{"sura":81,"aya":28,"text":"就是你们之中意欲正直的人。","translator":"王静斋"},{"sura":81,"aya":29,"text":"你们非至安拉——调养众世界的主意欲——的时候，则无所意欲。","translator":"王静斋"},{"sura":82,"aya":1,"text":"在天已分裂的时候，","translator":"王静斋"},{"sura":82,"aya":2,"text":"在群星纷落的时候，","translator":"王静斋"},{"sura":82,"aya":3,"text":"在各海泛滥的时候，","translator":"王静斋"},{"sura":82,"aya":4,"text":"在墓穴翻覆的时候，","translator":"王静斋"},{"sura":82,"aya":5,"text":"人就知道自所送于前落于后的了。","translator":"王静斋"},{"sura":82,"aya":6,"text":"人类啊！甚么事情藉你仁慈的养主欺骗你了?","translator":"王静斋"},{"sura":82,"aya":7,"text":"那造化了你，且使你健全相称的主，","translator":"王静斋"},{"sura":82,"aya":8,"text":"他任便把你配合在哪样形态里了?","translator":"王静斋"},{"sura":82,"aya":9,"text":"不可，","translator":"王静斋"},{"sura":82,"aya":10,"text":"不然你们不信还报。","translator":"王静斋"},{"sura":82,"aya":11,"text":"确有看守你们的一般作记录的高尚的天使，","translator":"王静斋"},{"sura":82,"aya":12,"text":"他们知道你们所行的。","translator":"王静斋"},{"sura":82,"aya":13,"text":"好人必在乐园中，","translator":"王静斋"},{"sura":82,"aya":14,"text":"恶人必在火狱中。","translator":"王静斋"},{"sura":82,"aya":15,"text":"他们将在报日进入它。","translator":"王静斋"},{"sura":82,"aya":16,"text":"他们绝非离它不在的。","translator":"王静斋"},{"sura":82,"aya":17,"text":"你从何知道甚么是报日?","translator":"王静斋"},{"sura":82,"aya":18,"text":"再说你从何知道甚么是报日?","translator":"王静斋"},{"sura":82,"aya":19,"text":"就是人不能管人一事的日子。是日，命令惟归安拉执掌。","translator":"王静斋"},{"sura":83,"aya":1,"text":"称量不公的人遭祸了，","translator":"王静斋"},{"sura":83,"aya":2,"text":"那般人量人家的时候便就求全。","translator":"王静斋"},{"sura":83,"aya":3,"text":"量给他们或称给他们的时候，就要减少。","translator":"王静斋"},{"sura":83,"aya":4,"text":"这般人岂不知道将在重大的日子，","translator":"王静斋"},{"sura":83,"aya":5,"text":".","translator":"王静斋"},{"sura":83,"aya":6,"text":"就是众人站在调养众世界主御前的那一天必得复起吗?","translator":"王静斋"},{"sura":83,"aya":7,"text":"不可，恶人的文卷必在“信禁”中——","translator":"王静斋"},{"sura":83,"aya":8,"text":"你从何知道：「信禁」是甚么；","translator":"王静斋"},{"sura":83,"aya":9,"text":"书明的文卷。","translator":"王静斋"},{"sura":83,"aya":10,"text":"是日那般否认报日的不信者遭祸了！","translator":"王静斋"},{"sura":83,"aya":11,"text":".","translator":"王静斋"},{"sura":83,"aya":12,"text":"除去一切越范作罪的人以外，没有不信服它的。","translator":"王静斋"},{"sura":83,"aya":13,"text":"有人向他读我的表征时候，他就说是前人的故事。","translator":"王静斋"},{"sura":83,"aya":14,"text":"不可，不然，他们所作的，遮住他们的心了。","translator":"王静斋"},{"sura":83,"aya":15,"text":"不可，他们在那日实是离去养主被遮，","translator":"王静斋"},{"sura":83,"aya":16,"text":"且必进火狱的。","translator":"王静斋"},{"sura":83,"aya":17,"text":"而后，有的个对他们（说：）这是你们曾经不信的。","translator":"王静斋"},{"sura":83,"aya":18,"text":"确实，善人的文卷必在（恩霖阴）中——","translator":"王静斋"},{"sura":83,"aya":19,"text":"你从何知道，「恩霖阴」是甚么——","translator":"王静斋"},{"sura":83,"aya":20,"text":"书明的文卷。","translator":"王静斋"},{"sura":83,"aya":21,"text":"一般接近的，守护它。","translator":"王静斋"},{"sura":83,"aya":22,"text":"善人必在福乐中。","translator":"王静斋"},{"sura":83,"aya":23,"text":"他们坐在床上四顾。","translator":"王静斋"},{"sura":83,"aya":24,"text":"你在他们的面上瞧出享受的光润。","translator":"王静斋"},{"sura":83,"aya":25,"text":"教他们就着被封的清酒痛饮。","translator":"王静斋"},{"sura":83,"aya":26,"text":"那酒的封口是麝香。令贪恋的人贪恋这个吧，","translator":"王静斋"},{"sura":83,"aya":27,"text":"它的搀杂物，","translator":"王静斋"},{"sura":83,"aya":28,"text":"是（特斯呢目，）就是一般接近的，饮它的那个泉源。","translator":"王静斋"},{"sura":83,"aya":29,"text":"的确，为恶的人曾嘲笑众归信的人。","translator":"王静斋"},{"sura":83,"aya":30,"text":"它们经过他们的时候，就彼此以目示意。","translator":"王静斋"},{"sura":83,"aya":31,"text":"他们返回自己家中的时候，就狂喜着转回。","translator":"王静斋"},{"sura":83,"aya":32,"text":"他们看见他们的时候，就说：（这般人实是迷误的。 ）","translator":"王静斋"},{"sura":83,"aya":33,"text":"本未差他们来保护他们。","translator":"王静斋"},{"sura":83,"aya":34,"text":"今日之下，众归信的人嘲笑众逆徒。","translator":"王静斋"},{"sura":83,"aya":35,"text":"他们坐在床上四顾。","translator":"王静斋"},{"sura":83,"aya":36,"text":"能不对于众逆徒还报其所作的吗?","translator":"王静斋"},{"sura":84,"aya":1,"text":"试思天已破裂，","translator":"王静斋"},{"sura":84,"aya":2,"text":"听命于养主——应当听命——的时候；","translator":"王静斋"},{"sura":84,"aya":3,"text":"在地被展开，","translator":"王静斋"},{"sura":84,"aya":4,"text":"抛出内中所有而致空洞，","translator":"王静斋"},{"sura":84,"aya":5,"text":"听命于养主——应当听命——的时候。","translator":"王静斋"},{"sura":84,"aya":6,"text":"人类啊：你是该当努力倾向养主，而后看见他的。","translator":"王静斋"},{"sura":84,"aya":7,"text":"说到文卷被交到右手的人，","translator":"王静斋"},{"sura":84,"aya":8,"text":"不久就对他清算，容易地清算。","translator":"王静斋"},{"sura":84,"aya":9,"text":"他快乐着返回家去。","translator":"王静斋"},{"sura":84,"aya":10,"text":"说到从背后接受文卷的人，","translator":"王静斋"},{"sura":84,"aya":11,"text":"不久他就呼喊（遭祸，）","translator":"王静斋"},{"sura":84,"aya":12,"text":"进到火狱。","translator":"王静斋"},{"sura":84,"aya":13,"text":"那人在自己家里原是快乐的。","translator":"王静斋"},{"sura":84,"aya":14,"text":"他揣度绝不能转来。","translator":"王静斋"},{"sura":84,"aya":15,"text":"是的，养主实是看见他的。","translator":"王静斋"},{"sura":84,"aya":16,"text":"我指着日落时的红气为誓；","translator":"王静斋"},{"sura":84,"aya":17,"text":"指着夜及其所包罗的为誓；","translator":"王静斋"},{"sura":84,"aya":18,"text":"指着月当其圆满的时候为誓；","translator":"王静斋"},{"sura":84,"aya":19,"text":"你们必须达到一重之后的一重上。","translator":"王静斋"},{"sura":84,"aya":20,"text":"他们为什么不信服?","translator":"王静斋"},{"sura":84,"aya":21,"text":"有人向他们诵读古兰的时候，他们不肯叩头?","translator":"王静斋"},{"sura":84,"aya":22,"text":"不然，逆徒们不信服。","translator":"王静斋"},{"sura":84,"aya":23,"text":"安拉至知他们所匿的。","translator":"王静斋"},{"sura":84,"aya":24,"text":"你以痛刑报告他们吧！","translator":"王静斋"},{"sura":84,"aya":25,"text":"只有归信并作善举的人，可获无间断的善赏。","translator":"王静斋"},{"sura":85,"aya":1,"text":"试思有宿宫的天，","translator":"王静斋"},{"sura":85,"aya":2,"text":"与预许的日子，","translator":"王静斋"},{"sura":85,"aya":3,"text":"与见证的被见证的，","translator":"王静斋"},{"sura":85,"aya":4,"text":"有燃料的火坑的人遭谴责了。","translator":"王静斋"},{"sura":85,"aya":5,"text":".","translator":"王静斋"},{"sura":85,"aya":6,"text":"那时候，他们坐在那里，","translator":"王静斋"},{"sura":85,"aya":7,"text":"监视对于众穆民所施行的。","translator":"王静斋"},{"sura":85,"aya":8,"text":"他们毁谤他们，只因他们信安拉，——优胜的，受赞的主，","translator":"王静斋"},{"sura":85,"aya":9,"text":"那掌天地的权柄的主。安拉实是照见万事的。","translator":"王静斋"},{"sura":85,"aya":10,"text":"一般磨难男女穆民，后来未曾悔罪的人，应遭火狱的刑罚，焚化的刑罚。","translator":"王静斋"},{"sura":85,"aya":11,"text":"归信并作善举的人，必获那诸河流于其下的天园。这是大成功?","translator":"王静斋"},{"sura":85,"aya":12,"text":"你养主的权势实是一定坚强的。","translator":"王静斋"},{"sura":85,"aya":13,"text":"他本肇造而又复造。","translator":"王静斋"},{"sura":85,"aya":14,"text":"他是多恕的，喜爱的。","translator":"王静斋"},{"sura":85,"aya":15,"text":"掌[阿勒世]的，尊大的，","translator":"王静斋"},{"sura":85,"aya":16,"text":"为所欲为的。","translator":"王静斋"},{"sura":85,"aya":17,"text":"众兵——匪喇傲与赛母待的故事可曾达至你了吗?","translator":"王静斋"},{"sura":85,"aya":18,"text":".","translator":"王静斋"},{"sura":85,"aya":19,"text":"逆徒们实是在不信中。","translator":"王静斋"},{"sura":85,"aya":20,"text":"安拉是环绕他们四周的。","translator":"王静斋"},{"sura":85,"aya":21,"text":"那实是记在。","translator":"王静斋"},{"sura":85,"aya":22,"text":"被庇的牌上，光荣的古兰。","translator":"王静斋"},{"sura":86,"aya":1,"text":"试思天与夜来者——","translator":"王静斋"},{"sura":86,"aya":2,"text":"你从何知道夜来者是甚么?","translator":"王静斋"},{"sura":86,"aya":3,"text":"贯穿的明星——","translator":"王静斋"},{"sura":86,"aya":4,"text":"每一个人皆有一个看守他的。","translator":"王静斋"},{"sura":86,"aya":5,"text":"人类当参看他由什么上受造?","translator":"王静斋"},{"sura":86,"aya":6,"text":"他由倾出来的一点精水上被造了。","translator":"王静斋"},{"sura":86,"aya":7,"text":"那点精水出自脊骨与胸骨。","translator":"王静斋"},{"sura":86,"aya":8,"text":"真主实是能于恢复他的。","translator":"王静斋"},{"sura":86,"aya":9,"text":"当种种机密暴露的那一日，","translator":"王静斋"},{"sura":86,"aya":10,"text":"在他无有力量，也无有一个相助的。","translator":"王静斋"},{"sura":86,"aya":11,"text":"试思有雨的天","translator":"王静斋"},{"sura":86,"aya":12,"text":"和有植物的地。","translator":"王静斋"},{"sura":86,"aya":13,"text":"它实是分明的言语。","translator":"王静斋"},{"sura":86,"aya":14,"text":"它绝不是戏言。","translator":"王静斋"},{"sura":86,"aya":15,"text":"的确，他们施诡计，","translator":"王静斋"},{"sura":86,"aya":16,"text":"我报复诡计，报复诡计。","translator":"王静斋"},{"sura":86,"aya":17,"text":"你要姑容不信的人。你要放任他们片刻。","translator":"王静斋"},{"sura":87,"aya":1,"text":"你当颂你养主至高的尊名清净！","translator":"王静斋"},{"sura":87,"aya":2,"text":"那造化而使齐整的主，","translator":"王静斋"},{"sura":87,"aya":3,"text":"那预定而加指导的主，","translator":"王静斋"},{"sura":87,"aya":4,"text":"那发现青草，","translator":"王静斋"},{"sura":87,"aya":5,"text":"而使它化为黑枯碎屑的主。","translator":"王静斋"},{"sura":87,"aya":6,"text":"我即将诵给你，而后你不忘；","translator":"王静斋"},{"sura":87,"aya":7,"text":"除非安拉意欲——的。他实知道明显的，暗昧的。","translator":"王静斋"},{"sura":87,"aya":8,"text":"我使你容易得到福祉。","translator":"王静斋"},{"sura":87,"aya":9,"text":"你当劝化如果劝化有济了；","translator":"王静斋"},{"sura":87,"aya":10,"text":"惧怕的人即将纳劝。","translator":"王静斋"},{"sura":87,"aya":11,"text":"那进极大的火狱最厄运的人远离它。","translator":"王静斋"},{"sura":87,"aya":12,"text":".","translator":"王静斋"},{"sura":87,"aya":13,"text":"那人在火狱里不死不生。","translator":"王静斋"},{"sura":87,"aya":14,"text":"那清洁并记念养主之名而作礼拜的人确已得意了。","translator":"王静斋"},{"sura":87,"aya":15,"text":".","translator":"王静斋"},{"sura":87,"aya":16,"text":"不然，你们选择今世的生存。","translator":"王静斋"},{"sura":87,"aya":17,"text":"后世是最好的，至久的。","translator":"王静斋"},{"sura":87,"aya":18,"text":"这确是记在前代的圣典，","translator":"王静斋"},{"sura":87,"aya":19,"text":"即易卜拉欣与母撒底圣典中的。","translator":"王静斋"},{"sura":88,"aya":1,"text":"遮覆的事件达至你了吗?","translator":"王静斋"},{"sura":88,"aya":2,"text":"是日， 一般面目是颓丧的，","translator":"王静斋"},{"sura":88,"aya":3,"text":"是劳苦的，困顿的。","translator":"王静斋"},{"sura":88,"aya":4,"text":"他将进炎热的火，","translator":"王静斋"},{"sura":88,"aya":5,"text":"教他饮自极热的泉。","translator":"王静斋"},{"sura":88,"aya":6,"text":"他们除去不加胖不充饥的荆棘以外，别无食品，","translator":"王静斋"},{"sura":88,"aya":7,"text":".","translator":"王静斋"},{"sura":88,"aya":8,"text":"是日，一般面目 是鲜艳的，","translator":"王静斋"},{"sura":88,"aya":9,"text":"欣喜个人底努力的，","translator":"王静斋"},{"sura":88,"aya":10,"text":"居高园的。","translator":"王静斋"},{"sura":88,"aya":11,"text":"你在那里闻不到妄言。","translator":"王静斋"},{"sura":88,"aya":12,"text":"在那里有活泉，","translator":"王静斋"},{"sura":88,"aya":13,"text":"有高榻，","translator":"王静斋"},{"sura":88,"aya":14,"text":"有陈列的酒杯，","translator":"王静斋"},{"sura":88,"aya":15,"text":"有排列的垫褥，","translator":"王静斋"},{"sura":88,"aya":16,"text":"与伸展的大毯。","translator":"王静斋"},{"sura":88,"aya":17,"text":"他们岂不看驼，它如何的被造?","translator":"王静斋"},{"sura":88,"aya":18,"text":"岂不看天，它如何的高耸?","translator":"王静斋"},{"sura":88,"aya":19,"text":"岂不看山，它如何的矗立?","translator":"王静斋"},{"sura":88,"aya":20,"text":"岂不看地，它如何的铺平?","translator":"王静斋"},{"sura":88,"aya":21,"text":"你当劝化，你只是一个劝化的。","translator":"王静斋"},{"sura":88,"aya":22,"text":"你不是管制他们的。","translator":"王静斋"},{"sura":88,"aya":23,"text":"但是，那拒绝并且不信的人，","translator":"王静斋"},{"sura":88,"aya":24,"text":"安拉将惩他以巨刑。","translator":"王静斋"},{"sura":88,"aya":25,"text":"他们必定归返于我。","translator":"王静斋"},{"sura":88,"aya":26,"text":"我且必对他们清算。","translator":"王静斋"},{"sura":89,"aya":1,"text":"指着破晓，","translator":"王静斋"},{"sura":89,"aya":2,"text":"十夜，","translator":"王静斋"},{"sura":89,"aya":3,"text":"双的，单的，","translator":"王静斋"},{"sura":89,"aya":4,"text":"与夜，当其已逝的时候，","translator":"王静斋"},{"sura":89,"aya":5,"text":"为誓，……内中可供有才智的人指以为誓吗?","translator":"王静斋"},{"sura":89,"aya":6,"text":"你岂未看见你的养主如何对付阿代，","translator":"王静斋"},{"sura":89,"aya":7,"text":"就是在各城里从未造化过同样的那体态魁梧的伊銮，","translator":"王静斋"},{"sura":89,"aya":8,"text":".","translator":"王静斋"},{"sura":89,"aya":9,"text":"与在山谷里凿石的赛母待，","translator":"王静斋"},{"sura":89,"aya":10,"text":"与在各城里造恶且多方破坏的那有木撅的匪喇傲。","translator":"王静斋"},{"sura":89,"aya":11,"text":".","translator":"王静斋"},{"sura":89,"aya":12,"text":".","translator":"王静斋"},{"sura":89,"aya":13,"text":"你的养主已把一种刑罚倾诸他们了。","translator":"王静斋"},{"sura":89,"aya":14,"text":"你的养主确是在要路口上。","translator":"王静斋"},{"sura":89,"aya":15,"text":"说到人类，每逢养主试证他，提高他，慈惠他了，他就说：（我养主提高我了。）","translator":"王静斋"},{"sura":89,"aya":16,"text":"每逢真主试证他，使他的生计窄狭了，他就说：（我的养主轻慢我了。）","translator":"王静斋"},{"sura":89,"aya":17,"text":"不可，你们实不敬重孤儿，","translator":"王静斋"},{"sura":89,"aya":18,"text":"不催促款待贫人，","translator":"王静斋"},{"sura":89,"aya":19,"text":"你们吞没遗产很多地吞没。","translator":"王静斋"},{"sura":89,"aya":20,"text":"你们爱财，多多地爱喜。","translator":"王静斋"},{"sura":89,"aya":21,"text":"不可，在地一块一块的粉碎，","translator":"王静斋"},{"sura":89,"aya":22,"text":"你的养主与一列一列的天使来到，","translator":"王静斋"},{"sura":89,"aya":23,"text":"并在是日现出火狱的时候，是日，人就想起。他哪能获劝呢?","translator":"王静斋"},{"sura":89,"aya":24,"text":"他说：（哎！宁愿我为着自己的永生差到前面。）","translator":"王静斋"},{"sura":89,"aya":25,"text":"是日，任何人的惩治不像他的惩治；","translator":"王静斋"},{"sura":89,"aya":26,"text":"任何人的束缚不像他的束缚。","translator":"王静斋"},{"sura":89,"aya":27,"text":"安静的性灵啊！","translator":"王静斋"},{"sura":89,"aya":28,"text":"你满意且获喜爱着归到养主吧！","translator":"王静斋"},{"sura":89,"aya":29,"text":"你入到我的众仆一流吧！","translator":"王静斋"},{"sura":89,"aya":30,"text":"你进我的园林吧！","translator":"王静斋"},{"sura":90,"aya":1,"text":"我指着这城池，","translator":"王静斋"},{"sura":90,"aya":2,"text":"你是将下到这城的——","translator":"王静斋"},{"sura":90,"aya":3,"text":"并指着生者，及其所生者为誓：","translator":"王静斋"},{"sura":90,"aya":4,"text":"我确造人类于困苦中。","translator":"王静斋"},{"sura":90,"aya":5,"text":"他以为无一个能够对付他的吗?","translator":"王静斋"},{"sura":90,"aya":6,"text":"他说：（我损失大宗财物了。）","translator":"王静斋"},{"sura":90,"aya":7,"text":"他自以为无一个看见他的吗?","translator":"王静斋"},{"sura":90,"aya":8,"text":"我岂未给他造化两眼、","translator":"王静斋"},{"sura":90,"aya":9,"text":"一舌、二唇、","translator":"王静斋"},{"sura":90,"aya":10,"text":"并指示他两条道路吗?","translator":"王静斋"},{"sura":90,"aya":11,"text":"他不冒入山路。","translator":"王静斋"},{"sura":90,"aya":12,"text":"你何从知道山路是什么?","translator":"王静斋"},{"sura":90,"aya":13,"text":"就是解放奴仆；","translator":"王静斋"},{"sura":90,"aya":14,"text":"或在饥馑的日子款待","translator":"王静斋"},{"sura":90,"aya":15,"text":"近族的孤儿，","translator":"王静斋"},{"sura":90,"aya":16,"text":"或卧土的贫人。","translator":"王静斋"},{"sura":90,"aya":17,"text":"他且属于归信并以忍耐相嘱，以怜爱相嘱的人，","translator":"王静斋"},{"sura":90,"aya":18,"text":"这一般是幸运的。","translator":"王静斋"},{"sura":90,"aya":19,"text":"不信我表征的人，均是厄运的。","translator":"王静斋"},{"sura":90,"aya":20,"text":"他们应遭封闭的火狱。","translator":"王静斋"},{"sura":91,"aya":1,"text":"试思太阳及其光辉！","translator":"王静斋"},{"sura":91,"aya":2,"text":"试思月亮当它追随它的时候！","translator":"王静斋"},{"sura":91,"aya":3,"text":"试思白昼当它现出它的时候！","translator":"王静斋"},{"sura":91,"aya":4,"text":"试思夜晚当它遮覆它的时候！","translator":"王静斋"},{"sura":91,"aya":5,"text":"试思天与创造它的主！","translator":"王静斋"},{"sura":91,"aya":6,"text":"试思陆地与铺平它的主！","translator":"王静斋"},{"sura":91,"aya":7,"text":"试思人类与那调整它！","translator":"王静斋"},{"sura":91,"aya":8,"text":"并把罪恶与敬慎昭示它的主！","translator":"王静斋"},{"sura":91,"aya":9,"text":"洁净自身的人确已得意了！","translator":"王静斋"},{"sura":91,"aya":10,"text":"污染自身的人确已失望了！","translator":"王静斋"},{"sura":91,"aya":11,"text":"赛母待当它的最厄运的人崛起的时候！","translator":"王静斋"},{"sura":91,"aya":12,"text":"由于悖谬不曾信服了！","translator":"王静斋"},{"sura":91,"aya":13,"text":"安拉的使者。对他们说：（莫犯）安拉的母驼和它的饮料。","translator":"王静斋"},{"sura":91,"aya":14,"text":"但是他们不信而屠宰它了。 而后，养主因他们的罪恶予以毁灭，而一概地损伤了。","translator":"王静斋"},{"sura":91,"aya":15,"text":"真主并不畏它的将来。","translator":"王静斋"},{"sura":92,"aya":1,"text":"试思夜晚当它遮覆时，","translator":"王静斋"},{"sura":92,"aya":2,"text":"试思白昼当其发光时，","translator":"王静斋"},{"sura":92,"aya":3,"text":"试思那造化男女的主，","translator":"王静斋"},{"sura":92,"aya":4,"text":"你们的努力是不同的。","translator":"王静斋"},{"sura":92,"aya":5,"text":"说到施舍，表示敬慎，","translator":"王静斋"},{"sura":92,"aya":6,"text":"并确信至好的人，","translator":"王静斋"},{"sura":92,"aya":7,"text":"我即将使他容易得到福祉。","translator":"王静斋"},{"sura":92,"aya":8,"text":"说到吝啬、无求、","translator":"王静斋"},{"sura":92,"aya":9,"text":"不信至好的人，","translator":"王静斋"},{"sura":92,"aya":10,"text":"我即将使他容易得到苦难。","translator":"王静斋"},{"sura":92,"aya":11,"text":"当他堕落的时候，他的资财于他无济。","translator":"王静斋"},{"sura":92,"aya":12,"text":"引导一定在我。","translator":"王静斋"},{"sura":92,"aya":13,"text":"惟我确掌后世与今世。","translator":"王静斋"},{"sura":92,"aya":14,"text":"我以发焰的火警告你们，","translator":"王静斋"},{"sura":92,"aya":15,"text":".","translator":"王静斋"},{"sura":92,"aya":16,"text":"进它的只是那不信，并且退转的至厄运的人。","translator":"王静斋"},{"sura":92,"aya":17,"text":".","translator":"王静斋"},{"sura":92,"aya":18,"text":"那清洁着给出自己资财的最敬慎者，即将远离它。","translator":"王静斋"},{"sura":92,"aya":19,"text":"他本不欠一个人应报的恩情，","translator":"王静斋"},{"sura":92,"aya":20,"text":"只不过讨其最高养主的喜悦。","translator":"王静斋"},{"sura":92,"aya":21,"text":"一定不久的他就满意。","translator":"王静斋"},{"sura":93,"aya":1,"text":"试思早晨；","translator":"王静斋"},{"sura":93,"aya":2,"text":"与夜当其遮黑时，","translator":"王静斋"},{"sura":93,"aya":3,"text":"你养主未弃掉你，他并未怒恼。","translator":"王静斋"},{"sura":93,"aya":4,"text":"一定后世于你强过了今世。","translator":"王静斋"},{"sura":93,"aya":5,"text":"一定不久的你的养主赐给你，然后你喜乐。","translator":"王静斋"},{"sura":93,"aya":6,"text":"他未见你是孤儿然后予以安插，","translator":"王静斋"},{"sura":93,"aya":7,"text":"见你是迷路的，然后加以指导，","translator":"王静斋"},{"sura":93,"aya":8,"text":"见你是贫穷的，然后使富吗?","translator":"王静斋"},{"sura":93,"aya":9,"text":"说到孤儿上，你不要压迫；","translator":"王静斋"},{"sura":93,"aya":10,"text":"说到乞者上，你不要呵叱；","translator":"王静斋"},{"sura":93,"aya":11,"text":"你当宣扬你养主的恩典。","translator":"王静斋"},{"sura":94,"aya":1,"text":"我未为你展开你的胸吗?","translator":"王静斋"},{"sura":94,"aya":2,"text":"我给你卸下","translator":"王静斋"},{"sura":94,"aya":3,"text":"那压你脊背擦擦响的重担。","translator":"王静斋"},{"sura":94,"aya":4,"text":"我为你提高你的声誉。","translator":"王静斋"},{"sura":94,"aya":5,"text":"的确，随同困难有容易；","translator":"王静斋"},{"sura":94,"aya":6,"text":"的确，随同困难有容易。","translator":"王静斋"},{"sura":94,"aya":7,"text":"你得闲的时候，应当受苦！","translator":"王静斋"},{"sura":94,"aya":8,"text":"并当哀怜自己的养主！","translator":"王静斋"},{"sura":95,"aya":1,"text":"试思无花果，橄榄，","translator":"王静斋"},{"sura":95,"aya":2,"text":"西乃山，","translator":"王静斋"},{"sura":95,"aya":3,"text":"与这平安的城市。","translator":"王静斋"},{"sura":95,"aya":4,"text":"我确造化人类于端正极美之中；","translator":"王静斋"},{"sura":95,"aya":5,"text":"而后把他归到一般低的至低的。","translator":"王静斋"},{"sura":95,"aya":6,"text":"惟有一切归信并作善举的人，他们可获无间断的善赏。","translator":"王静斋"},{"sura":95,"aya":7,"text":"在这以后什么事情，使你不信还报?","translator":"王静斋"},{"sura":95,"aya":8,"text":"安拉岂不是公断的至公断的吗?","translator":"王静斋"},{"sura":96,"aya":1,"text":"你奉那造物的养主的名诵读吧！","translator":"王静斋"},{"sura":96,"aya":2,"text":"他曾自凝血上造化了人类。","translator":"王静斋"},{"sura":96,"aya":3,"text":"你诵读吧！你养主是至高贵的。","translator":"王静斋"},{"sura":96,"aya":4,"text":"那教以用笔的主，","translator":"王静斋"},{"sura":96,"aya":5,"text":"他教授人类原不知道的。","translator":"王静斋"},{"sura":96,"aya":6,"text":"确实人类当看见自己富有的时候，必定犯罪。","translator":"王静斋"},{"sura":96,"aya":7,"text":".","translator":"王静斋"},{"sura":96,"aya":8,"text":"归必归你养主。","translator":"王静斋"},{"sura":96,"aya":9,"text":"你曾看见当仆人礼拜的时候，加以阻止的人吗?","translator":"王静斋"},{"sura":96,"aya":10,"text":"你曾看见当仆人礼拜的时候，加以阻止的人吗?","translator":"王静斋"},{"sura":96,"aya":11,"text":"你曾看见吗?","translator":"王静斋"},{"sura":96,"aya":12,"text":"如果他守正道或命以公义……。","translator":"王静斋"},{"sura":96,"aya":13,"text":"你曾看见吗?如果他不信，他退转了……。","translator":"王静斋"},{"sura":96,"aya":14,"text":"他岂未知安拉看得见吗?","translator":"王静斋"},{"sura":96,"aya":15,"text":"不可，如果他未停止了， 我必扯住额发，","translator":"王静斋"},{"sura":96,"aya":16,"text":"作恶撒谎的额发。","translator":"王静斋"},{"sura":96,"aya":17,"text":"令他呼自己的议席吧！","translator":"王静斋"},{"sura":96,"aya":18,"text":"我即将唤到一般掌刑的天使。","translator":"王静斋"},{"sura":96,"aya":19,"text":"不可，你莫顺从他。你当叩头！你当接近！","translator":"王静斋"},{"sura":97,"aya":1,"text":"的确，我在[盖德雷]夜内降下它了。","translator":"王静斋"},{"sura":97,"aya":2,"text":"你从何知道[盖德雷]夜是什么?","translator":"王静斋"},{"sura":97,"aya":3,"text":"「盖德雷」的一夜优越千个月。","translator":"王静斋"},{"sura":97,"aya":4,"text":"众天使与（鲁哈）在那夜里奉他们养主的命令，为着各项事情下临。","translator":"王静斋"},{"sura":97,"aya":5,"text":"那一夜是平安的，直到东方亮。","translator":"王静斋"},{"sura":98,"aya":1,"text":"不信的人；有经人和一般拜偶像的不离开，直至明证……，","translator":"王静斋"},{"sura":98,"aya":2,"text":"安拉的一个使者来读那载有正确条规的纯洁圣典为止。","translator":"王静斋"},{"sura":98,"aya":3,"text":".","translator":"王静斋"},{"sura":98,"aya":4,"text":"有经人分派系，只是在明证达到以后。","translator":"王静斋"},{"sura":98,"aya":5,"text":"他们原奉命惟拜安拉，对他竭诚顺服，守正不偏着；并举拜功，施天课。这是正确的宗教。","translator":"王静斋"},{"sura":98,"aya":6,"text":"的确，不信的人……有经人和一般拜偶像的，永居在火狱的火中。这一般就是群生中最恶劣的。","translator":"王静斋"},{"sura":98,"aya":7,"text":"的确，一般归信并作善举的人是群生中最善良的。","translator":"王静斋"},{"sura":98,"aya":8,"text":"他们在养主御前的善赏，是那诸河流于其下常住的天园；永久居在那里。安拉爱喜他们，他们也爱喜他。这是属于惧怕养主之人的。","translator":"王静斋"},{"sura":99,"aya":1,"text":"在地已震动，（极）其震动，","translator":"王静斋"},{"sura":99,"aya":2,"text":"地已现出其重负，","translator":"王静斋"},{"sura":99,"aya":3,"text":"人类说：（它是怎的）时候，","translator":"王静斋"},{"sura":99,"aya":4,"text":"当那一日，地因你养主命令它，","translator":"王静斋"},{"sura":99,"aya":5,"text":"而历述它的各种消息。","translator":"王静斋"},{"sura":99,"aya":6,"text":"当那一日，众人等等不一着转来；只为教他们亲见自己所作的。","translator":"王静斋"},{"sura":99,"aya":7,"text":"作尘星之重的善事者，将要看见它；","translator":"王静斋"},{"sura":99,"aya":8,"text":"作尘星之重的恶事者。将要看见它。","translator":"王静斋"},{"sura":100,"aya":1,"text":"试思疾驰喘息的，","translator":"王静斋"},{"sura":100,"aya":2,"text":"蹄触火花的，","translator":"王静斋"},{"sura":100,"aya":3,"text":"黎明劫掠，","translator":"王静斋"},{"sura":100,"aya":4,"text":"而激起尘上，","translator":"王静斋"},{"sura":100,"aya":5,"text":"立时冲入群众之间的！","translator":"王静斋"},{"sura":100,"aya":6,"text":"人类实是对他养主忘恩的；","translator":"王静斋"},{"sura":100,"aya":7,"text":"他自身实是见证这事的；","translator":"王静斋"},{"sura":100,"aya":8,"text":"他是极端爱财的。","translator":"王静斋"},{"sura":100,"aya":9,"text":"他岂不知当复起墓中人，","translator":"王静斋"},{"sura":100,"aya":10,"text":"显出胸中所怀的时候……。","translator":"王静斋"},{"sura":100,"aya":11,"text":"他们养主在那一日，必定是尽知他们的。","translator":"王静斋"},{"sura":101,"aya":1,"text":"[嘎雷阿，]","translator":"王静斋"},{"sura":101,"aya":2,"text":"[嘎雷阿]是什么?","translator":"王静斋"},{"sura":101,"aya":3,"text":"你何以知道[嘎雷阿]是什么?","translator":"王静斋"},{"sura":101,"aya":4,"text":"当那一日，人类像是四散的飞蛾；","translator":"王静斋"},{"sura":101,"aya":5,"text":"群山如同弹碎的羊毛。","translator":"王静斋"},{"sura":101,"aya":6,"text":"天秤重的人，","translator":"王静斋"},{"sura":101,"aya":7,"text":"他是在快乐的生活中。","translator":"王静斋"},{"sura":101,"aya":8,"text":"天秤轻的人，","translator":"王静斋"},{"sura":101,"aya":9,"text":"他的归处是[哈维业。]","translator":"王静斋"},{"sura":101,"aya":10,"text":"你从何知道它 是什么?","translator":"王静斋"},{"sura":101,"aya":11,"text":"那就是暴烈的火。","translator":"王静斋"},{"sura":102,"aya":1,"text":"竞多贻误你们，","translator":"王静斋"},{"sura":102,"aya":2,"text":"竟至探望坟墓了。","translator":"王静斋"},{"sura":102,"aya":3,"text":"不可，不久你们就知道。","translator":"王静斋"},{"sura":102,"aya":4,"text":"尤其不可，不久你们就知道。","translator":"王静斋"},{"sura":102,"aya":5,"text":"确实，设若你们知道，确切知道了，","translator":"王静斋"},{"sura":102,"aya":6,"text":"你们将必看见火狱。","translator":"王静斋"},{"sura":102,"aya":7,"text":"然后，你们必定亲眼看见它。","translator":"王静斋"},{"sura":102,"aya":8,"text":"然后，在那时候，必对于快乐上质问你们。","translator":"王静斋"},{"sura":103,"aya":1,"text":"试思[阿素雷重]","translator":"王静斋"},{"sura":103,"aya":2,"text":"的确，人类是在伤折中；","translator":"王静斋"},{"sura":103,"aya":3,"text":"除去一般归信并作善举，且以真理相嘱，并以忍耐相嘱的人。","translator":"王静斋"},{"sura":104,"aya":1,"text":"一切多谤好贬的人有祸了；","translator":"王静斋"},{"sura":104,"aya":2,"text":"那人积下资财，而频数它，","translator":"王静斋"},{"sura":104,"aya":3,"text":"他自以为他的资财能使他常存。","translator":"王静斋"},{"sura":104,"aya":4,"text":"不可，他将被弃在[候代麦]里。","translator":"王静斋"},{"sura":104,"aya":5,"text":"你从何知道[候代麦]是什么?","translator":"王静斋"},{"sura":104,"aya":6,"text":"就是安拉所燃着的火，","translator":"王静斋"},{"sura":104,"aya":7,"text":"冲上心去的。","translator":"王静斋"},{"sura":104,"aya":8,"text":"那火确是在他们上面的穹窿，","translator":"王静斋"},{"sura":104,"aya":9,"text":"在长大的圆柱中。","translator":"王静斋"},{"sura":105,"aya":1,"text":"你岂未看见养主怎样地对付象军?","translator":"王静斋"},{"sura":105,"aya":2,"text":"他岂未把他们的诡计置在无效中，","translator":"王静斋"},{"sura":105,"aya":3,"text":"并遣去一群一群的飞鸟，","translator":"王静斋"},{"sura":105,"aya":4,"text":"以泥石射击他们，","translator":"王静斋"},{"sura":105,"aya":5,"text":"而使他们化若被吃的禾叶吗?","translator":"王静斋"},{"sura":106,"aya":1,"text":"只因古来氏结合……，","translator":"王静斋"},{"sura":106,"aya":2,"text":"他们结合在冬夏旅行，","translator":"王静斋"},{"sura":106,"aya":3,"text":"令他们敬拜调养此殿的主吧！","translator":"王静斋"},{"sura":106,"aya":4,"text":"那真主因为饥饿赐给他们食；并因为骇怕赐给他们安宁。","translator":"王静斋"},{"sura":107,"aya":1,"text":"你曾看见那不信还报的人吗?","translator":"王静斋"},{"sura":107,"aya":2,"text":"这是那驱逐孤儿","translator":"王静斋"},{"sura":107,"aya":3,"text":"不催促款待贫人的。","translator":"王静斋"},{"sura":107,"aya":4,"text":"那般礼拜的人有祸了","translator":"王静斋"},{"sura":107,"aya":5,"text":"他们疏忽自己的拜功；","translator":"王静斋"},{"sura":107,"aya":6,"text":"他们沽名，","translator":"王静斋"},{"sura":107,"aya":7,"text":"并拒借需求品。","translator":"王静斋"},{"sura":108,"aya":1,"text":"我确赐给你[考塞雷]了。","translator":"王静斋"},{"sura":108,"aya":2,"text":"你当为你的养主礼拜。你当献祭。","translator":"王静斋"},{"sura":108,"aya":3,"text":"的确，怒恨你的人就是无后的。","translator":"王静斋"},{"sura":109,"aya":1,"text":"你说：众不信的人哪！","translator":"王静斋"},{"sura":109,"aya":2,"text":"我不拜你们拜的。","translator":"王静斋"},{"sura":109,"aya":3,"text":"你们是不拜我拜的。","translator":"王静斋"},{"sura":109,"aya":4,"text":"我是不拜你们拜的，","translator":"王静斋"},{"sura":109,"aya":5,"text":"你们是不拜我拜的。","translator":"王静斋"},{"sura":109,"aya":6,"text":"你们有你们的教门，我有我的教门。","translator":"王静斋"},{"sura":110,"aya":1,"text":"安拉的相助与胜利既至，","translator":"王静斋"},{"sura":110,"aya":2,"text":"你看见众人一伙一伙地入到安拉底教门的时候，","translator":"王静斋"},{"sura":110,"aya":3,"text":"你就以赞你的养主称颂清净吧！你求他饶恕吧！他实是允承忏悔的。","translator":"王静斋"},{"sura":111,"aya":1,"text":"厄不勒害部的两手消灭了，他已消灭了。","translator":"王静斋"},{"sura":111,"aya":2,"text":"他的资财和他所赚下的未能代他避免。","translator":"王静斋"},{"sura":111,"aya":3,"text":"他即将进入发焰的火。","translator":"王静斋"},{"sura":111,"aya":4,"text":"并有他的妇人，负荆棘着，","translator":"王静斋"},{"sura":111,"aya":5,"text":"项上有枣皮绳子。","translator":"王静斋"},{"sura":112,"aya":1,"text":"你说：他，安拉是独一的。","translator":"王静斋"},{"sura":112,"aya":2,"text":"安拉是无求的。","translator":"王静斋"},{"sura":112,"aya":3,"text":"他未产，未被产。","translator":"王静斋"},{"sura":112,"aya":4,"text":"无一是他底对等的。","translator":"王静斋"},{"sura":113,"aya":1,"text":"你说：我借着掌理黎明的主，","translator":"王静斋"},{"sura":113,"aya":2,"text":"于他所造的恶上，","translator":"王静斋"},{"sura":113,"aya":3,"text":"并于黑夜当它弥漫时候的恶上，","translator":"王静斋"},{"sura":113,"aya":4,"text":"并于吹缬妇的恶上，","translator":"王静斋"},{"sura":113,"aya":5,"text":"并于嫉忌的人，当其嫉忌时候的恶上，祈求护佑。","translator":"王静斋"},{"sura":114,"aya":1,"text":"你说：我借着调养人类的主，","translator":"王静斋"},{"sura":114,"aya":2,"text":"执掌人类的主，","translator":"王静斋"},{"sura":114,"aya":3,"text":"人类敬拜的主，","translator":"王静斋"},{"sura":114,"aya":4,"text":"于暗唆的隐伏的，","translator":"王静斋"},{"sura":114,"aya":5,"text":"就是那暗唆于人的胸间的——","translator":"王静斋"},{"sura":114,"aya":6,"text":"镇呢与人类的恶上祈求护佑。","translator":"王静斋"}];